				// GT1.8, guarantorDatetimeOfBirth
				if (seg['GT1.8']['GT1.8.1'].toString() != "") { objGT1[index].guarantorDatetimeOfBirth = seg['GT1.8']['GT1.8.1']; }

				// GT1.9, guarantorSex
				if (seg['GT1.9']['GT1.9.1'].toString() != "") { objGT1[index].guarantorSex = seg['GT1.9']['GT1.9.1']; }

				// GT1.10, guarantorType
				if (seg['GT1.10']['GT1.10.1'].toString() != "") { objGT1[index].guarantorType = seg['GT1.10']['GT1.10.1']; }

				// GT1.11, guarantorRelationship
				if (seg['GT1.11']['GT1.11.1'].toString() != "") { objGT1[index].guarantorRelationship = seg['GT1.11']['GT1.11.1']; }

				// GT1.12, guarantorSSN
				if (seg['GT1.12']['GT1.12.1'].toString() != "") { objGT1[index].guarantorSSN = seg['GT1.12']['GT1.12.1']; }

