				// GT1.47, contactReason
				objGT1[index].contactReason = {};
				if (seg['GT1.47']['GT1.47.1'].toString() != "") { objGT1[index].contactReason.id = seg['GT1.47']['GT1.47.1']; }
				if (seg['GT1.47']['GT1.47.2'].toString() != "") { objGT1[index].contactReason.text = seg['GT1.47']['GT1.47.2']; }
				if (seg['GT1.47']['GT1.47.3'].toString() != "") { objGT1[index].contactReason.codingSystem = seg['GT1.47']['GT1.47.3']; }
				if (seg['GT1.47']['GT1.47.4'].toString() != "") { objGT1[index].contactReason.altId = seg['GT1.47']['GT1.47.4']; }
				if (seg['GT1.47']['GT1.47.5'].toString() != "") { objGT1[index].contactReason.altText = seg['GT1.47']['GT1.47.5']; }
				if (seg['GT1.47']['GT1.47.6'].toString() != "") { objGT1[index].contactReason.altCodingSystem = seg['GT1.47']['GT1.47.6']; }
				if (Object.keys(objGT1[index].contactReason).length == 0) {
					delete objGT1[index].contactReason;
				}

				// GT1.48, contactRelationship
				if (seg['GT1.48']['GT1.48.1'].toString() != "") { objGT1[index].contactRelationship = seg['GT1.48']['GT1.48.1']; }

				// GT1.49, jobTitle
				if (seg['GT1.49']['GT1.49.1'].toString() != "") { objGT1[index].jobTitle = seg['GT1.49']['GT1.49.1']; }

				// GT1.50, jobCodeClass
				objGT1[index].jobCodeClass = {};
				if (seg['GT1.50']['GT1.50.1'].toString() != "") { objGT1[index].jobCodeClass.id = seg['GT1.50']['GT1.50.1']; }
				if (seg['GT1.50']['GT1.50.1'].toString() != "") { objGT1[index].jobCodeClass.id = seg['GT1.50']['GT1.50.1']; }
				if (Object.keys(objGT1[index].jobCodeClass).length == 0) {
					delete objGT1[index].jobCodeClass;
				}

				// GT1.51, guarantorEmployersOrganization
				objGT1[index].guarantorEmployersOrganization = [];
				for each (field in seg['GT1.51']) {
					var block = {};
					if (field['GT1.51.1'].toString() != "") { block.organizationName = field['GT1.51.1']; }
					if (field['GT1.51.2'].toString() != "") { block.organizationNameTypeCode = field['GT1.51.2']; }
					if (field['GT1.51.3'].toString() != "") { block.idNumber = field['GT1.51.3']; }
					if (field['GT1.51.4'].toString() != "") { block.identifierCheckDigit = field['GT1.51.4']; }
					if (field['GT1.51.5'].toString() != "") { block.codeIdentifyingCheckDigitScheme = field['GT1.51.5']; }
					block.assigningAuthority = {};
					if (field['GT1.51.6']['GT1.51.6.1'].toString() != "") { block.assigningAuthority.namespaceId = field['GT1.51.6']['GT1.51.6.1']; }
					if (field['GT1.51.6']['GT1.51.6.2'].toString() != "") { block.assigningAuthority.universalId = field['GT1.51.6']['GT1.51.6.2']; }
					if (field['GT1.51.6']['GT1.51.6.3'].toString() != "") { block.assigningAuthority.universalIdType = field['GT1.51.6']['GT1.51.6.3']; }
					if (Object.keys(block.assigningAuthority).length == 0) {
						if (field['GT1.51.6'].toString() != "") { block.assigningAuthority.namespaceId = field['GT1.51.6']; }
						else delete block.assigningAuthority;
					}
					if (field['GT1.51.7'].toString() != "") { block.idTypeCode = field['GT1.51.7']; }
					block.assigningFacility = {};
					if (field['GT1.51.8']['GT1.51.8.1'].toString() != "") { block.assigningFacility.namespaceId = field['GT1.51.8']['GT1.51.8.1']; }
					if (field['GT1.51.8']['GT1.51.8.2'].toString() != "") { block.assigningFacility.universalId = field['GT1.51.8']['GT1.51.8.2']; }
					if (field['GT1.51.8']['GT1.51.8.3'].toString() != "") { block.assigningFacility.universalIdType = field['GT1.51.8']['GT1.51.8.3']; }
					if (Object.keys(block.assigningFacility).length == 0) {
						if (field['GT1.51.8'].toString() != "") { block.assigningFacility.namespaceId = field['GT1.51.8']; }
						else delete block.assigningFacility;
					}
					if (Object.keys(block).length > 0) {
						objGT1[index].guarantorEmployersOrganization.push(block);
					}
				}
				if (objGT1[index].guarantorEmployersOrganization.length == 0) {
					delete objGT1[index].guarantorEmployersOrganization;
				}
				// GT1.52, handicap
				if (seg['GT1.52']['GT1.52.1'].toString() != "") { objGT1[index].handicap = seg['GT1.52']['GT1.52.1']; }

				// GT1.53, jobStatus
				if (seg['GT1.53']['GT1.53.1'].toString() != "") { objGT1[index].jobStatus = seg['GT1.53']['GT1.53.1']; }

				// GT1.54, guarantorFinancialClass
				objGT1[index].guarantorFinancialClass = {};
				if (seg['GT1.54']['GT1.54.1'].toString() != "") { objGT1[index].guarantorFinancialClass.financialClass = seg['GT1.54']['GT1.54.1']; }
				if (seg['GT1.54']['GT1.54.1'].toString() != "") { objGT1[index].guarantorFinancialClass.effectiveDate = seg['GT1.54']['GT1.54.1']; }
				if (Object.keys(objGT1[index].guarantorFinancialClass).length == 0) {
					delete objGT1[index].guarantorFinancialClass;
				}

				// GT1.55, guarantorRace
				if (seg['GT1.55']['GT1.55.1'].toString() != "") { objGT1[index].guarantorRace = seg['GT1.55']['GT1.55.1']; }

				// clean up GT1 and increment index
				if (Object.keys(objGT1[index]).length == 0) {
					delete objGT1[index];
				} else {
					index++;
				}
			}
		} // end if 	

		return objGT1;

	} // end if 
}

/**
	getHL7MessageFromXML(msg)- Desc: This function retrieves the HL7v2 content from a SOAP-WS Service response
	from the ns1:return XML element value.

	@param {String} msg - provide the SOAP-WS XML responses containing HL7 message
	@return {String} - return param msg returns the HL7 string
*/
function getHL7MessageFromXML(msg) {

	var scriptName= 'getHL7MessageFromXML';
	var debug= 1;

	if(debug>0) { logger.error('DEBUG::'+scriptName+' BEFORE msg: '+msg); }
	msg= msg.*::['Body'].*::['submitMessageResponse'].*::['return'].toString();
	channelMap.put('hl7ack',msg);

	msg = new XML(SerializerFactory.getSerializer('HL7V2').toXML(msg));
	if(debug>0) { logger.error('DEBUG::'+scriptName+' AFTER XML to HL7 Serializer msg: '+msg); }

	return msg;
}

/**
	getIN1_v23(msg)

	- Desc: This function retrieves HL7v2 HAPI format elements and values from IN1 segment stored in
	object for reference

	@param {String} msg - this contains the raw message (string)
	@return {object} objIN1 - this contains the IN1 segment elements composed into a single objIN1
		object.
*/
function getIN1_v23(msg) {

	// define variables
	if (typeof(msg['IN1'].toString()) !== undefined) {

		// IN1
		var objIN1= new Object();

		if (msg['IN1'].length() > 0) {
			objIN1 = [];
			var index = 0;
			for each (seg in msg.IN1) {
				objIN1[index] = {};

				// IN1.1, setId
				if (seg['IN1.1']['IN1.1.1'].toString() != "") { objIN1[index].setId = seg['IN1.1']['IN1.1.1']; }

				// IN1.2, planId
				objIN1[index].planId = {};
				if (seg['IN1.2']['IN1.2.1'].toString() != "") { objIN1[index].planId.id = seg['IN1.2']['IN1.2.1']; }
				if (seg['IN1.2']['IN1.2.2'].toString() != "") { objIN1[index].planId.text = seg['IN1.2']['IN1.2.2']; }
				if (seg['IN1.2']['IN1.2.3'].toString() != "") { objIN1[index].planId.codingSystem = seg['IN1.2']['IN1.2.3']; }
				if (seg['IN1.2']['IN1.2.4'].toString() != "") { objIN1[index].planId.altId = seg['IN1.2']['IN1.2.4']; }
				if (seg['IN1.2']['IN1.2.5'].toString() != "") { objIN1[index].planId.altText = seg['IN1.2']['IN1.2.5']; }
				if (seg['IN1.2']['IN1.2.6'].toString() != "") { objIN1[index].planId.altCodingSystem = seg['IN1.2']['IN1.2.6']; }
				if (Object.keys(objIN1[index].planId).length == 0) {
					delete objIN1[index].planId;
				}

				// IN1.3, companyId
				objIN1[index].companyId = [];
				for each (field in seg['IN1.3']) {
					var block = {};
					if (field['IN1.3.1'].toString() != "") { block.id = field['IN1.3.1']; }
					if (field['IN1.3.2'].toString() != "") { block.identifierCheckDigit = field['IN1.3.2']; }
					if (field['IN1.3.3'].toString() != "") { block.codeIdentifyingCheckDigitScheme = field['IN1.3.3']; }
					block.assigningAuthority = {};
					if (field['IN1.3.4']['IN1.3.4.1'].toString() != "") { block.assigningAuthority.namespaceId = field['IN1.3.4']['IN1.3.4.1']; }
					if (field['IN1.3.4']['IN1.3.4.2'].toString() != "") { block.assigningAuthority.universalId = field['IN1.3.4']['IN1.3.4.2']; }
					if (field['IN1.3.4']['IN1.3.4.3'].toString() != "") { block.assigningAuthority.universalIdType = field['IN1.3.4']['IN1.3.4.3']; }
					if (Object.keys(block.assigningAuthority).length == 0) {
						if (field['IN1.3.4'].toString() != "") { block.assigningAuthority.namespaceId = field['IN1.3.4']; }
						else delete block.assigningAuthority;
					}
					if (field['IN1.3.5'].toString() != "") { block.idTypeCode = field['IN1.3.5']; }
					block.assigningFacility = {};
					if (field['IN1.3.6']['IN1.3.6.1'].toString() != "") { block.assigningFacility.namespaceId = field['IN1.3.6']['IN1.3.6.1']; }
					if (field['IN1.3.6']['IN1.3.6.2'].toString() != "") { block.assigningFacility.universalId = field['IN1.3.6']['IN1.3.6.2']; }
					if (field['IN1.3.6']['IN1.3.6.3'].toString() != "") { block.assigningFacility.universalIdType = field['IN1.3.6']['IN1.3.6.3']; }
					if (Object.keys(block.assigningFacility).length == 0) {
						if (field['IN1.3.6'].toString() != "") { block.assigningFacility.namespaceId = field['IN1.3.6']; }
						else delete block.assigningFacility;
					}
					if (Object.keys(block).length > 0) {
						objIN1[index].companyId.push(block);
					}
				}
				if (objIN1[index].companyId.length == 0) {
					delete objIN1[index].companyId;
				}

				// IN1.4, companyName
				objIN1[index].companyName = [];
				for each (field in seg['IN1.4']) {
					var block = {};
					if (field['IN1.4.1'].toString() != "") { block.organizationName = field['IN1.4.1']; }
					if (field['IN1.4.2'].toString() != "") { block.organizationNameTypeCode = field['IN1.4.2']; }
					if (field['IN1.4.3'].toString() != "") { block.idNumber = field['IN1.4.3']; }
					if (field['IN1.4.4'].toString() != "") { block.identifierCheckDigit = field['IN1.4.4']; }
					if (field['IN1.4.5'].toString() != "") { block.codeIdentifyingCheckDigitScheme = field['IN1.4.5']; }
					block.assigningAuthority = {};
					if (field['IN1.4.6']['IN1.4.6.1'].toString() != "") { block.assigningAuthority.namespaceId = field['IN1.4.6']['IN1.4.6.1']; }
					if (field['IN1.4.6']['IN1.4.6.2'].toString() != "") { block.assigningAuthority.universalId = field['IN1.4.6']['IN1.4.6.2']; }
					if (field['IN1.4.6']['IN1.4.6.3'].toString() != "") { block.assigningAuthority.universalIdType = field['IN1.4.6']['IN1.4.6.3']; }
					if (Object.keys(block.assigningAuthority).length == 0) {
						if (field['IN1.4.6'].toString() != "") { block.assigningAuthority.namespaceId = field['IN1.4.6']; }
						else delete block.assigningAuthority;
					}
					if (field['IN1.4.7'].toString() != "") { block.idTypeCode = field['IN1.4.7']; }
					block.assigningFacility = {};
					if (field['IN1.4.8']['IN1.4.8.1'].toString() != "") { block.assigningFacility.namespaceId = field['IN1.4.8']['IN1.4.8.1']; }
					if (field['IN1.4.8']['IN1.4.8.2'].toString() != "") { block.assigningFacility.universalId = field['IN1.4.8']['IN1.4.8.2']; }
					if (field['IN1.4.8']['IN1.4.8.3'].toString() != "") { block.assigningFacility.universalIdType = field['IN1.4.8']['IN1.4.8.3']; }
					if (Object.keys(block.assigningFacility).length == 0) {
						if (field['IN1.4.8'].toString() != "") { block.assigningFacility.namespaceId = field['IN1.4.8']; }
						else delete block.assigningFacility;
					}
					if (Object.keys(block).length > 0) {
						objIN1[index].companyName.push(block);
					}
				}
				if (objIN1[index].companyName.length == 0) {
					delete objIN1[index].companyName;
				}

				// IN1.5, companyAddress
				objIN1[index].companyAddress = [];
				for each (field in seg['IN1.5']) {
					var block = {};
					if (field['IN1.5.1'].toString() != "") { block.streetAddress = field['IN1.5.1']; }
					if (field['IN1.5.2'].toString() != "") { block.otherDesignation = field['IN1.5.2']; }
					if (field['IN1.5.3'].toString() != "") { block.city = field['IN1.5.3']; }
					if (field['IN1.5.4'].toString() != "") { block.stateOrProvince = field['IN1.5.4']; }
					if (field['IN1.5.5'].toString() != "") { block.zip = field['IN1.5.5']; }
					if (field['IN1.5.6'].toString() != "") { block.country = field['IN1.5.6']; }
					if (field['IN1.5.7'].toString() != "") { block.addressType = field['IN1.5.7']; }
					if (field['IN1.5.8'].toString() != "") { block.otherGeographicDesignation = field['IN1.5.8']; }
					if (field['IN1.5.9'].toString() != "") { block.countyOrParishCode = field['IN1.5.9']; }
