				}

				// IN1.8, groupNumber
				if (seg['IN1.8']['IN1.8.1'].toString() != "") { objIN1[index].groupNumber = seg['IN1.8']['IN1.8.1']; }

				// IN1.9, groupName
				objIN1[index].groupName = [];
				for each (field in seg['IN1.9']) {
					var block = {};
					if (field['IN1.9.1'].toString() != "") { block.organizationName = field['IN1.9.1']; }
