				// PV1.49, totalPayments
				if (seg['PV1.49']['PV1.49.1'].toString() != "") { pv1[index].totalPayments = seg['PV1.49']['PV1.49.1']; }

				// PV1.50, alternateVisitID
				pv1[index].alternateVisitID = {};
				if (seg['PV1.50']['PV1.50.1'].toString() != "") { pv1[index].alternateVisitID.id = seg['PV1.50']['PV1.50.1']; }
				if (seg['PV1.50']['PV1.50.2'].toString() != "") { pv1[index].alternateVisitID.identifierCheckDigit = seg['PV1.50']['PV1.50.2']; }
				if (seg['PV1.50']['PV1.50.3'].toString() != "") { pv1[index].alternateVisitID.codeIdentifyingCheckDigitScheme = seg['PV1.50']['PV1.50.3']; }
				pv1[index].alternateVisitID.assigningAuthority = {};
				if (seg['PV1.50']['PV1.50.4']['PV1.50.4.1'].toString() != "") { pv1[index].alternateVisitID.assigningAuthority.namespaceId = seg['PV1.50']['PV1.50.4']['PV1.50.4.1']; }
				if (seg['PV1.50']['PV1.50.4']['PV1.50.4.2'].toString() != "") { pv1[index].alternateVisitID.assigningAuthority.universalId = seg['PV1.50']['PV1.50.4']['PV1.50.4.2']; }
				if (seg['PV1.50']['PV1.50.4']['PV1.50.4.3'].toString() != "") { pv1[index].alternateVisitID.assigningAuthority.universalIdType = seg['PV1.50']['PV1.50.4']['PV1.50.4.3']; }
				if (Object.keys(pv1[index].alternateVisitID.assigningAuthority).length == 0) {
					if (seg['PV1.50']['PV1.50.4'].toString() != "") { pv1[index].alternateVisitID.assigningAuthority.namespaceId = seg['PV1.50']['PV1.50.4']; }
					else delete pv1[index].alternateVisitID.assigningAuthority;
				}
				if (seg['PV1.50']['PV1.50.5'].toString() != "") { pv1[index].alternateVisitID.idTypeCode = seg['PV1.50']['PV1.50.5']; }
				pv1[index].alternateVisitID.assigningFacility = {};
				if (seg['PV1.50']['PV1.50.6']['PV1.50.6.1'].toString() != "") { pv1[index].alternateVisitID.assigningFacility.namespaceId = seg['PV1.50']['PV1.50.6']['PV1.50.6.1']; }
				if (seg['PV1.50']['PV1.50.6']['PV1.50.6.2'].toString() != "") { pv1[index].alternateVisitID.assigningFacility.universalId = seg['PV1.50']['PV1.50.6']['PV1.50.6.2']; }
				if (seg['PV1.50']['PV1.50.6']['PV1.50.6.3'].toString() != "") { pv1[index].alternateVisitID.assigningFacility.universalIdType = seg['PV1.50']['PV1.50.6']['PV1.50.6.3']; }
				if (Object.keys(pv1[index].alternateVisitID.assigningFacility).length == 0) {
					if (seg['PV1.50']['PV1.50.6'].toString() != "") { pv1[index].alternateVisitID.assigningFacility.namespaceId = seg['PV1.50']['PV1.50.6']; }
					else delete pv1[index].alternateVisitID.assigningFacility;
				}
				if (Object.keys(pv1[index].alternateVisitID).length == 0) {
					delete pv1[index].alternateVisitID;
				}

				// PV1.51, visitIndicator
				if (seg['PV1.51']['PV1.51.1'].toString() != "") { pv1[index].visitIndicator = seg['PV1.51']['PV1.51.1']; }

				// PV1.52, otherHealthcareProvider
				pv1[index].otherHealthcareProvider = {};
				if (seg['PV1.52']['PV1.52.1'].toString() != "") { pv1[index].otherHealthcareProvider.id = seg['PV1.52']['PV1.52.1']; }
				if (seg['PV1.52']['PV1.52.2'].toString() != "") { pv1[index].otherHealthcareProvider.lastName = seg['PV1.52']['PV1.52.2']; }
				if (seg['PV1.52']['PV1.52.3'].toString() != "") { pv1[index].otherHealthcareProvider.firstName = seg['PV1.52']['PV1.52.3']; }
				if (seg['PV1.52']['PV1.52.4'].toString() != "") { pv1[index].otherHealthcareProvider.middleInitOrName = seg['PV1.52']['PV1.52.4']; }
				if (seg['PV1.52']['PV1.52.5'].toString() != "") { pv1[index].otherHealthcareProvider.suffix = seg['PV1.52']['PV1.52.5']; }
				if (seg['PV1.52']['PV1.52.6'].toString() != "") { pv1[index].otherHealthcareProvider.prefix = seg['PV1.52']['PV1.52.6']; }
				if (seg['PV1.52']['PV1.52.7'].toString() != "") { pv1[index].otherHealthcareProvider.degree = seg['PV1.52']['PV1.52.7']; }
				if (seg['PV1.52']['PV1.52.8'].toString() != "") { pv1[index].otherHealthcareProvider.sourceTable = seg['PV1.52']['PV1.52.8']; }
				pv1[index].otherHealthcareProvider.assigningAuthority = {};
				if (seg['PV1.52']['PV1.52.9']['PV1.52.9.1'].toString() != "") { pv1[index].otherHealthcareProvider.assigningAuthority.id = seg['PV1.52']['PV1.52.9']['PV1.52.9.1']; }
				if (seg['PV1.52']['PV1.52.9']['PV1.52.9.2'].toString() != "") { pv1[index].otherHealthcareProvider.assigningAuthority.universalId = seg['PV1.52']['PV1.52.9']['PV1.52.9.2']; }
				if (seg['PV1.52']['PV1.52.9']['PV1.52.9.3'].toString() != "") { pv1[index].otherHealthcareProvider.assigningAuthority.universalIdType = seg['PV1.52']['PV1.52.9']['PV1.52.9.3']; }
				if (Object.keys(pv1[index].otherHealthcareProvider.assigningAuthority).length == 0) {
					if (seg['PV1.52']['PV1.52.9'].toString() != "") { pv1[index].otherHealthcareProvider.assigningAuthority.id = seg['PV1.52']['PV1.52.9']; }
					else delete pv1[index].otherHealthcareProvider.assigningAuthority;
				}
				if (seg['PV1.52']['PV1.52.10'].toString() != "") { pv1[index].otherHealthcareProvider.nameTypeCode = seg['PV1.52']['PV1.52.10']; }
				if (seg['PV1.52']['PV1.52.11'].toString() != "") { pv1[index].otherHealthcareProvider.identifierCheckDigit = seg['PV1.52']['PV1.52.11']; }
				if (seg['PV1.52']['PV1.52.12'].toString() != "") { pv1[index].otherHealthcareProvider.codeIdentifyingCheckDigitScheme = seg['PV1.52']['PV1.52.12']; }
				if (seg['PV1.52']['PV1.52.13'].toString() != "") { pv1[index].otherHealthcareProvider.identifierTypeCode = seg['PV1.52']['PV1.52.13']; }
				pv1[index].otherHealthcareProvider.assigningFacility = {};
				if (seg['PV1.52']['PV1.52.14']['PV1.52.14.1'].toString() != "") { pv1[index].otherHealthcareProvider.assigningFacility.id = seg['PV1.52']['PV1.52.14']['PV1.52.14.1']; }
				if (seg['PV1.52']['PV1.52.14']['PV1.52.14.2'].toString() != "") { pv1[index].otherHealthcareProvider.assigningFacility.universalId = seg['PV1.52']['PV1.52.14']['PV1.52.14.2']; }
				if (seg['PV1.52']['PV1.52.14']['PV1.52.14.3'].toString() != "") { pv1[index].otherHealthcareProvider.assigningFacility.universalIdType = seg['PV1.52']['PV1.52.14']['PV1.52.14.3']; }
				if (Object.keys(pv1[index].otherHealthcareProvider.assigningFacility).length == 0) {
					if (seg['PV1.52']['PV1.52.14'].toString() != "") { pv1[index].otherHealthcareProvider.assigningFacility.id = seg['PV1.52']['PV1.52.14']; }
					else delete pv1[index].otherHealthcareProvider.assigningFacility;
				}
				if (Object.keys(pv1[index].otherHealthcareProvider).length == 0) {
					delete pv1[index].otherHealthcareProvider;
				}
				*/

				index++;

				/*
				if(debug> 0) {
					logger.error(scriptName+'::DEBUG seg["+index+"]: '+seg.toString());
					logger.error(scriptName+'::DEBUG pv1["+index+"]: '+pv1.toString());
				}
				*/

			}
		}

		logger.error(scriptName+'::DEBUG objPV1: '+JSON.stringify(objPV1));		
		return objPV1;
	}
}

/**
	getSCH_v23(msg)

	- Desc: This function retrieves HL7v2 HAPI format elements and values from SCH segment stored in
	object for reference

	@param {String} msg - this contains the raw message (string)
	@return {object} objSCH - this contains the SCH segment elements composed into a single objSCH object.
*/
function getSCH_v23(msg) {

	// define variables
	if (typeof(msg['SCH'].toString()) !== undefined) {

		// SCH	
		var objSCH= new Object();

		if (msg['SCH'].length() > 0) {
			var index = 0;
			for each (seg in msg.SCH) {

				// SCH.1, placerAppointmentId
				if(typeof(seg['SCH.1']) !== 'undefined') {
					var objPlacerAppointmentId= new Object();
					if (seg['SCH.1']['SCH.1.1'].toString() != "") { objPlacerAppointmentId.id= seg['SCH.1']['SCH.1.1'].toString(); }
					if (seg['SCH.1']['SCH.1.2'].toString() != "") { objPlacerAppointmentId.namespaceId= seg['SCH.1']['SCH.1.2'].toString(); }
					if (seg['SCH.1']['SCH.1.3'].toString() != "") { objPlacerAppointmentId.universalId= seg['SCH.1']['SCH.1.3'].toString(); }
					if (seg['SCH.1']['SCH.1.4'].toString() != "") { objPlacerAppointmentId.universalIdType= seg['SCH.1']['SCH.1.4'].toString(); }

					objSCH.objPlacerAppointmentId= objPlacerAppointmentId;
				}

				// SCH.2, fillerAppointmentId
				if(typeof(seg['SCH.2']) !== 'undefined') {
					var objFillerAppointmentId= new Object();
					if (seg['SCH.2']['SCH.2.1'].toString() != "") { objFillerAppointmentId.id= seg['SCH.2']['SCH.2.1'].toString(); }
					if (seg['SCH.2']['SCH.2.2'].toString() != "") { objFillerAppointmentId.namespaceId= seg['SCH.2']['SCH.2.2'].toString(); }
					if (seg['SCH.2']['SCH.2.3'].toString() != "") { objFillerAppointmentId.universalId= seg['SCH.2']['SCH.2.3'].toString(); }
					if (seg['SCH.2']['SCH.2.4'].toString() != "") { objFillerAppointmentId.universalIdType= seg['SCH.2']['SCH.2.4'].toString(); }

					objSCH.fillerAppointmentId= objFillerAppointmentId;
				}

				// SCH.3, occurrenceNumber
				if (seg['SCH.3']['SCH.3.1'].toString() != "") { objSCH.occurrenceNumber = seg['SCH.3']['SCH.3.1'].toString(); }

				// SCH.4, placerGroupNumber
				if(typeof(seg['SCH.4']) !== 'undefined') {
					var objPlacerGroupNumber= new Object();
					if (seg['SCH.4']['SCH.4.1'].toString() != "") { objPlacerGroupNumber.id= seg['SCH.4']['SCH.4.1'].toString(); }
					if (seg['SCH.4']['SCH.4.2'].toString() != "") { objPlacerGroupNumber.namespaceId= seg['SCH.4']['SCH.4.2'].toString(); }
					if (seg['SCH.4']['SCH.4.3'].toString() != "") { objPlacerGroupNumber.universalId= seg['SCH.4']['SCH.4.3'].toString(); }
					if (seg['SCH.4']['SCH.4.4'].toString() != "") { objPlacerGroupNumber.universalIdType= seg['SCH.4']['SCH.4.4'].toString(); }

					objSCH.placerGroupNumber= objPlacerGroupNumber;
				}

				// SCH.5, scheduleId
				if(typeof(seg['SCH.5']) !== 'undefined') {
					var objScheduleId= new Object();
					if (seg['SCH.5']['SCH.5.1'].toString() != "") { objScheduleId.id= seg['SCH.5']['SCH.5.1'].toString(); }
					if (seg['SCH.5']['SCH.5.2'].toString() != "") { objScheduleId.text= seg['SCH.5']['SCH.5.2'].toString(); }
					if (seg['SCH.5']['SCH.5.3'].toString() != "") { objScheduleId.codeSystem= seg['SCH.5']['SCH.5.3'].toString(); }
					if (seg['SCH.5']['SCH.5.4'].toString() != "") { objScheduleId.alternateId= seg['SCH.5']['SCH.5.4'].toString(); }
					if (seg['SCH.5']['SCH.5.5'].toString() != "") { objScheduleId.alternateText= seg['SCH.5']['SCH.5.5'].toString(); }
					if (seg['SCH.5']['SCH.5.6'].toString() != "") { objScheduleId.altCodeSystem= seg['SCH.5']['SCH.5.6'].toString(); }

					objSCH.scheduleId= objScheduleId;
				}

				// SCH.6, eventReason
				if(typeof(seg['SCH.6']) !== 'undefined') {
					var objEventReason= new Object();
					if (seg['SCH.6']['SCH.6.1'].toString() != "") { objEventReason.id= seg['SCH.6']['SCH.6.1'].toString(); }
					if (seg['SCH.6']['SCH.6.2'].toString() != "") { objEventReason.text= seg['SCH.6']['SCH.6.2'].toString(); }
					if (seg['SCH.6']['SCH.6.3'].toString() != "") { objEventReason.codeSystem= seg['SCH.6']['SCH.6.3'].toString(); }
					if (seg['SCH.6']['SCH.6.4'].toString() != "") { objEventReason.alternateId= seg['SCH.6']['SCH.6.4'].toString(); }
					if (seg['SCH.6']['SCH.6.5'].toString() != "") { objEventReason.alternateText= seg['SCH.6']['SCH.6.5'].toString(); }
					if (seg['SCH.6']['SCH.6.6'].toString() != "") { objEventReason.altCodeSystem= seg['SCH.6']['SCH.6.6'].toString(); }

					objSCH.eventReason= objEventReason;
				}

				// SCH.7, appointmentReason
				if(typeof(seg['SCH.7']) !== 'undefined') {
					var objAppointmentReason= new Object();
					if (seg['SCH.7']['SCH.7.1'].toString() != "") { objAppointmentReason.id= seg['SCH.7']['SCH.7.1'].toString(); }
					if (seg['SCH.7']['SCH.7.2'].toString() != "") { objAppointmentReason.text= seg['SCH.7']['SCH.7.2'].toString(); }
					if (seg['SCH.7']['SCH.7.3'].toString() != "") { objAppointmentReason.codeSystem= seg['SCH.7']['SCH.7.3'].toString(); }
					if (seg['SCH.7']['SCH.7.4'].toString() != "") { objAppointmentReason.alternateId= seg['SCH.7']['SCH.7.4'].toString(); }
					if (seg['SCH.7']['SCH.7.5'].toString() != "") { objAppointmentReason.alternateText= seg['SCH.7']['SCH.7.5'].toString(); }
					if (seg['SCH.7']['SCH.7.6'].toString() != "") { objAppointmentReason.altCodeSystem= seg['SCH.7']['SCH.7.6'].toString(); }

					objSCH.appointmentReason= objAppointmentReason;
				}

				// SCH.8, appointmentType
				if(typeof(seg['SCH.8']) !== 'undefined') {
					var x= new Object();
					if (seg['SCH.8']['SCH.8.1'].toString() != "") { objAppointmentType.id= seg['SCH.8']['SCH.8.1'].toString(); }
					if (seg['SCH.8']['SCH.8.2'].toString() != "") { objAppointmentType.text= seg['SCH.8']['SCH.8.2'].toString(); }
					if (seg['SCH.8']['SCH.8.3'].toString() != "") { objAppointmentType.codeSystem= seg['SCH.8']['SCH.8.3'].toString(); }
					if (seg['SCH.8']['SCH.8.4'].toString() != "") { objAppointmentType.alternateId= seg['SCH.8']['SCH.8.4'].toString(); }
					if (seg['SCH.8']['SCH.8.5'].toString() != "") { objAppointmentType.alternateText= seg['SCH.8']['SCH.8.5'].toString(); }
					if (seg['SCH.8']['SCH.8.6'].toString() != "") { objAppointmentType.altCodeSystem= seg['SCH.8']['SCH.8.6'].toString(); }

					objSCH.appointmentType= objAppointmentType;
				}

				// SCH.9, appointmentDuration
				if (seg['SCH.9']['SCH.9.1'].toString() != "") { objSCH.appointmentDuration= seg['SCH.9']['SCH.9.1'].toString(); }

				// SCH.10, appointmentDurationUnits
				if(typeof(seg['SCH.10']) !== 'undefined') {
					var objAppointmentDurationUnits= new Object();
					if (seg['SCH.10']['SCH.10.1'].toString() != "") { objAppointmentDurationUnits.id= seg['SCH.10']['SCH.10.1'].toString(); }
					if (seg['SCH.10']['SCH.10.2'].toString() != "") { objAppointmentDurationUnits.text= seg['SCH.10']['SCH.10.2'].toString(); }
					if (seg['SCH.10']['SCH.10.3'].toString() != "") { objAppointmentDurationUnits.codeSystem= seg['SCH.10']['SCH.10.3'].toString(); }
					if (seg['SCH.10']['SCH.10.4'].toString() != "") { objAppointmentDurationUnits.alternateId= seg['SCH.10']['SCH.10.4'].toString(); }
					if (seg['SCH.10']['SCH.10.5'].toString() != "") { objAppointmentDurationUnits.alternateText= seg['SCH.10']['SCH.10.5'].toString(); }
					if (seg['SCH.10']['SCH.10.6'].toString() != "") { objAppointmentDurationUnits.altCodeSystem= seg['SCH.10']['SCH.10.6'].toString(); }

					objSCH.appointmentDurationUnits= objAppointmentDurationUnits;
				}

				// SCH.11, appointmentTimingQuantity
				if(typeof(seg['SCH.11']) !== 'undefined') {

					var objAppointmentTimingQuantity= new Object();

					// SCH.11.1, quantity
					if(typeof(seg['SCH.11']['SCH.11.1']) !== 'undefined') {
						var objAppointmentTimingQuantityCE= new Object();
						if (seg['SCH.11']['SCH.11.1']['SCH.11.1.1'].toString() != "") { objAppointmentTimingQuantityCE.quantity= seg['SCH.11']['SCH.11.1']['SCH.11.1.1'].toString(); }
						if (seg['SCH.11']['SCH.11.1']['SCH.11.1.2'].toString() != "") { objAppointmentTimingQuantityCE.units= seg['SCH.11']['SCH.11.1']['SCH.11.1.2'].toString(); }
						objAppointmentTimingQuantity.quantity= objAppointmentTimingQuantityCE;
					}

					// SCH.11.2, interval
					if(typeof(seg['SCH.11']['SCH.11.2']) !== 'undefined') {
						var objAppointmentTimingQuantityInterval= new Object();
						if (seg['SCH.11']['SCH.11.2']['SCH.11.2.1'].toString() != "") { objAppointmentTimingQuantityInterval.pattern= seg['SCH.11']['SCH.11.2']['SCH.11.2.1'].toString(); }
						if (seg['SCH.11']['SCH.11.2']['SCH.11.2.2'].toString() != "") { objAppointmentTimingQuantityInterval.interval= seg['SCH.11']['SCH.11.2']['SCH.11.2.2'].toString(); }
						objAppointmentTimingQuantity.interval= objAppointmentTimingQuantityInterval;
					}

					// SCH.11.3, duration
					if (seg['SCH.11']['SCH.11.3'].toString() != '') { objAppointmentTimingQuantity.duration= seg['SCH.11']['SCH.11.3'].toString(); }

					// SCH.11.4, start date/time
					if (seg['SCH.11']['SCH.11.4'].toString() != '') { objAppointmentTimingQuantity.startDateTime= seg['SCH.11']['SCH.11.4'].toString(); }

					// SCH.11.5, stop date/time
					if (seg['SCH.11']['SCH.11.5'].toString() != '') { objAppointmentTimingQuantity.stopDateTime= seg['SCH.11']['SCH.11.5'].toString(); }

					objSCH.appointmentTimingQuantity= objAppointmentTimingQuantity;
				}	

				// SCH.12, placer contact person
				if(typeof(seg['SCH.12']) !== 'undefined') {
					var objPlacerContactPerson= new Object();
					if (seg['SCH.12']['SCH.12.1'].toString() != "") { objPlacerContactPerson.id= seg['SCH.12']['SCH.12.1'].toString(); }
					if (seg['SCH.12']['SCH.12.2'].toString() != "") { objPlacerContactPerson.familyName= seg['SCH.12']['SCH.12.2'].toString(); }
					if (seg['SCH.12']['SCH.12.3'].toString() != "") { objPlacerContactPerson.givenName= seg['SCH.12']['SCH.12.3'].toString(); }
					if (seg['SCH.12']['SCH.12.4'].toString() != "") { objPlacerContactPerson.middleName= seg['SCH.12']['SCH.12.4'].toString(); }
					if (seg['SCH.12']['SCH.12.5'].toString() != "") { objPlacerContactPerson.suffix= seg['SCH.12']['SCH.12.5'].toString(); }
					if (seg['SCH.12']['SCH.12.6'].toString() != "") { objPlacerContactPerson.prefix= seg['SCH.12']['SCH.12.6'].toString(); }
					if (seg['SCH.12']['SCH.12.7'].toString() != "") { objPlacerContactPerson.degree= seg['SCH.12']['SCH.12.7'].toString(); }
					if (seg['SCH.12']['SCH.12.8'].toString() != "") { objPlacerContactPerson.sourceTable= seg['SCH.12']['SCH.12.8'].toString(); }
					if (seg['SCH.12']['SCH.12.9'].toString() != "") { objPlacerContactPerson.assigningAuthority= seg['SCH.12']['SCH.12.9'].toString(); }

					objSCH.placerContractPerson= objPlacerContactPerson;
				}

				// SCH.13, placer contact phone number
				if(typeof(seg['SCH.13']) !== 'undefined') {
					var objPlacerContactPhoneNumber= new Object();
					if (seg['SCH.13']['SCH.13.1'].toString() != "") { objPlacerContactPhoneNumber.telephoneNumber= seg['SCH.13']['SCH.13.1'].toString(); }
					if (seg['SCH.13']['SCH.13.2'].toString() != "") { objPlacerContactPhoneNumber.useCode= seg['SCH.13']['SCH.13.2'].toString(); }
					if (seg['SCH.13']['SCH.13.3'].toString() != "") { objPlacerContactPhoneNumber.equipmentType= seg['SCH.13']['SCH.13.3'].toString(); }
					if (seg['SCH.13']['SCH.13.4'].toString() != "") { objPlacerContactPhoneNumber.emailAddress= seg['SCH.13']['SCH.13.4'].toString(); }
					if (seg['SCH.13']['SCH.13.5'].toString() != "") { objPlacerContactPhoneNumber.countryCode= seg['SCH.13']['SCH.13.5'].toString(); }
					if (seg['SCH.13']['SCH.13.6'].toString() != "") { objPlacerContactPhoneNumber.areaCode= seg['SCH.13']['SCH.13.6'].toString(); }
					if (seg['SCH.13']['SCH.13.6'].toString() != "") { objPlacerContactPhoneNumber.phoneNumber= seg['SCH.13']['SCH.13.7'].toString(); }

					objSCH.placerContactPhoneNumber= objPlacerContactPhoneNumber;
				}

				// SCH.14, placer contact address
				if(typeof(seg['SCH.14']) !== 'undefined') {
					var objPlacerContactAddress= new Object();
					if (seg['SCH.14']['SCH.14.1'].toString() != "") { objPlacerContactAddress.streetAddress= seg['SCH.14']['SCH.14.1'].toString(); }
					if (seg['SCH.14']['SCH.14.2'].toString() != "") { objPlacerContactAddress.otherDesignation= seg['SCH.14']['SCH.14.2'].toString(); }
					if (seg['SCH.14']['SCH.14.3'].toString() != "") { objPlacerContactAddress.city= seg['SCH.14']['SCH.14.3'].toString(); }
					if (seg['SCH.14']['SCH.14.4'].toString() != "") { objPlacerContactAddress.state= seg['SCH.14']['SCH.14.4'].toString(); }
					if (seg['SCH.14']['SCH.14.5'].toString() != "") { objPlacerContactAddress.postalCode= seg['SCH.14']['SCH.14.5'].toString(); }
					if (seg['SCH.14']['SCH.14.6'].toString() != "") { objPlacerContactAddress.country= seg['SCH.14']['SCH.14.6'].toString(); }
					if (seg['SCH.14']['SCH.14.7'].toString() != "") { objPlacerContactAddress.addressType= seg['SCH.14']['SCH.14.7'].toString(); }

					objSCH.placerContactAddress= objPlacerContactAddress;
				}

				// SCH.15, placer contact location
				if(typeof(seg['SCH.15']) !== 'undefined') {
					var objPlacerContactLocation= new Object();
					if (seg['SCH.15']['SCH.15.1'].toString() != "") { objPlacerContactLocation.pointOfCare= seg['SCH.15']['SCH.15.1'].toString(); }
					if (seg['SCH.15']['SCH.15.2'].toString() != "") { objPlacerContactLocation.room= seg['SCH.15']['SCH.15.2'].toString(); }
					if (seg['SCH.15']['SCH.15.3'].toString() != "") { objPlacerContactLocation.bed= seg['SCH.15']['SCH.15.3'].toString(); }
					if (seg['SCH.15']['SCH.15.4'].toString() != "") { objPlacerContactLocation.facility= seg['SCH.15']['SCH.15.4'].toString(); }
					if (seg['SCH.15']['SCH.15.5'].toString() != "") { objPlacerContactLocation.locationStatus= seg['SCH.15']['SCH.15.5'].toString(); }

					objSCH.placerContactLocation= objPlacerContactLocation;
				}

				// SCH.16, filler contact person
				if(typeof(seg['SCH.16']) !== 'undefined') {
					var objFillerContactPerson= new Object();

					if (seg['SCH.16']['SCH.16.1'].toString() != "") { objFillerContactPerson.id= seg['SCH.16']['SCH.16.1'].toString(); }
					if (seg['SCH.16']['SCH.16.2'].toString() != "") { objFillerContactPerson.familyName= seg['SCH.16']['SCH.16.2'].toString(); }
					if (seg['SCH.16']['SCH.16.3'].toString() != "") { objFillerContactPerson.givenName= seg['SCH.16']['SCH.16.3'].toString(); }
					if (seg['SCH.16']['SCH.16.4'].toString() != "") { objFillerContactPerson.middleName= seg['SCH.16']['SCH.16.4'].toString(); }
					if (seg['SCH.16']['SCH.16.5'].toString() != "") { objFillerContactPerson.suffix= seg['SCH.16']['SCH.16.5'].toString(); }
					if (seg['SCH.16']['SCH.16.6'].toString() != "") { objFillerContactPerson.prefix= seg['SCH.16']['SCH.16.6'].toString(); }
					if (seg['SCH.16']['SCH.16.7'].toString() != "") { objFillerContactPerson.degree= seg['SCH.16']['SCH.16.7'].toString(); }
					if (seg['SCH.16']['SCH.16.8'].toString() != "") { objFillerContactPerson.sourceTable= seg['SCH.16']['SCH.16.8'].toString(); }
					if (seg['SCH.16']['SCH.16.9'].toString() != "") { objFillerContactPerson.assigningAuthority= seg['SCH.16']['SCH.16.9'].toString(); }

					objSCH.fillerContractPerson= objFillerContactPerson;
				}

				// SCH.17, filler contact phone number
				if(typeof(seg['SCH.17']) !== 'undefined') {
					var objFillerContactPhoneNumber= new Object();
					if (seg['SCH.17']['SCH.17.1'].toString() != "") { objFillerContactPhoneNumber.telephoneNumber= seg['SCH.17']['SCH.17.1'].toString(); }
