					if (seg['SCH.17']['SCH.17.5'].toString() != "") { objFillerContactPhoneNumber.countryCode= seg['SCH.17']['SCH.17.5'].toString(); }
					if (seg['SCH.17']['SCH.17.6'].toString() != "") { objFillerContactPhoneNumber.areaCode= seg['SCH.17']['SCH.17.6'].toString(); }
					if (seg['SCH.17']['SCH.17.6'].toString() != "") { objFillerContactPhoneNumber.phoneNumber= seg['SCH.17']['SCH.17.7'].toString(); }

					objSCH.fillerContactPhoneNumber= objFillerContactPhoneNumber;
				}

				// SCH.18, placer contact address
				if(typeof(seg['SCH.18']) !== 'undefined') {
					var objFillerContactAddress= new Object();
					if (seg['SCH.18']['SCH.18.1'].toString() != "") { objFillerContactAddress.streetAddress= seg['SCH.18']['SCH.18.1'].toString(); }
					if (seg['SCH.18']['SCH.18.2'].toString() != "") { objFillerContactAddress.otherDesignation= seg['SCH.18']['SCH.18.2'].toString(); }
					if (seg['SCH.18']['SCH.18.3'].toString() != "") { objFillerContactAddress.city= seg['SCH.18']['SCH.18.3'].toString(); }
					if (seg['SCH.18']['SCH.18.4'].toString() != "") { objFillerContactAddress.state= seg['SCH.18']['SCH.18.4'].toString(); }
					if (seg['SCH.18']['SCH.18.5'].toString() != "") { objFillerContactAddress.postalCode= seg['SCH.18']['SCH.18.5'].toString(); }
					if (seg['SCH.18']['SCH.18.6'].toString() != "") { objFillerContactAddress.country= seg['SCH.18']['SCH.18.6'].toString(); }
					if (seg['SCH.18']['SCH.18.7'].toString() != "") { objFillerContactAddress.addressType= seg['SCH.18']['SCH.18.7'].toString(); }

					objSCH.fillerContactAddress= objFillerContactAddress;
				}

				// SCH.19, placer contact location
				if(typeof(seg['SCH.19']) !== 'undefined') {
					var objFillerContactLocation= new Object();
					if (seg['SCH.19']['SCH.19.1'].toString() != "") { objFillerContactLocation.pointOfCare= seg['SCH.19']['SCH.19.1'].toString(); }
					if (seg['SCH.19']['SCH.19.2'].toString() != "") { objFillerContactLocation.room= seg['SCH.19']['SCH.19.2'].toString(); }
					if (seg['SCH.19']['SCH.19.3'].toString() != "") { objFillerContactLocation.bed= seg['SCH.19']['SCH.19.3'].toString(); }
					if (seg['SCH.19']['SCH.19.4'].toString() != "") { objFillerContactLocation.facility= seg['SCH.19']['SCH.19.4'].toString(); }
					if (seg['SCH.19']['SCH.19.5'].toString() != "") { objFillerContactLocation.locationStatus= seg['SCH.19']['SCH.19.5'].toString(); }

					objSCH.placerContactLocation= objFillerContactLocation;
				}

				// SCH.20, entered by person
				if(typeof(seg['SCH.20']) !== 'undefined') {
					var objEnteredByPerson= new Object();

					if (seg['SCH.20']['SCH.20.1'].toString() != "") { objEnteredByPerson.id= seg['SCH.20']['SCH.20.1'].toString(); }
