					if (seg['SCH.20']['SCH.20.4'].toString() != "") { objEnteredByPerson.middleName= seg['SCH.20']['SCH.20.4'].toString(); }
					if (seg['SCH.20']['SCH.20.5'].toString() != "") { objEnteredByPerson.suffix= seg['SCH.20']['SCH.20.5'].toString(); }
					if (seg['SCH.20']['SCH.20.6'].toString() != "") { objEnteredByPerson.prefix= seg['SCH.20']['SCH.20.6'].toString(); }
					if (seg['SCH.20']['SCH.20.7'].toString() != "") { objEnteredByPerson.degree= seg['SCH.20']['SCH.20.7'].toString(); }
					if (seg['SCH.20']['SCH.20.8'].toString() != "") { objEnteredByPerson.sourceTable= seg['SCH.20']['SCH.20.8'].toString(); }
					if (seg['SCH.20']['SCH.20.9'].toString() != "") { objEnteredByPerson.assigningAuthority= seg['SCH.20']['SCH.20.9'].toString(); }

					objSCH.fillerContractPerson= objEnteredByPerson;
				}

				// SCH.21, entered by phone number
				if(typeof(seg['SCH.21']) !== 'undefined') {
					var objEnteredByPhoneNumber= new Object();
					if (seg['SCH.21']['SCH.21.1'].toString() != "") { objEnteredByPhoneNumber.telephoneNumber= seg['SCH.21']['SCH.21.1'].toString(); }
