				// SCH.22, entered by location
				if(typeof(seg['SCH.22']) !== 'undefined') {
					var objEnteredByLocation= new Object();
					if (seg['SCH.22']['SCH.22.1'].toString() != "") { objEnteredByLocation.streetAddress= seg['SCH.22']['SCH.22.1'].toString(); }
