					objSCH.placerContactAddress= objEnteredByLocation;
				}

				// SCH.23, parent placer appointment id
				if(typeof(seg['SCH.23']) !== 'undefined') {
					var objParentPlacerAppointmentId= new Object();
					if (seg['SCH.23']['SCH.23.1'].toString() != "") { objParentPlacerAppointmentId.id= seg['SCH.23']['SCH.23.1'].toString(); }
					if (seg['SCH.23']['SCH.23.2'].toString() != "") { objParentPlacerAppointmentId.namespaceId= seg['SCH.23']['SCH.23.2'].toString(); }
					if (seg['SCH.23']['SCH.23.3'].toString() != "") { objParentPlacerAppointmentId.universalId= seg['SCH.23']['SCH.23.3'].toString(); }
					if (seg['SCH.23']['SCH.23.4'].toString() != "") { objParentPlacerAppointmentId.universalIdType= seg['SCH.23']['SCH.23.4'].toString(); }

					objSCH.parentPlacerAppointmentId= objParentPlacerAppointmentId;
				}

				// SCH.24, parent filler appointment id
				if(typeof(seg['SCH.24']) !== 'undefined') {
					var objParentFillerAppointmentId= new Object();
					if (seg['SCH.24']['SCH.24.1'].toString() != "") { objParentFillerAppointmentId.id= seg['SCH.24']['SCH.24.1'].toString(); }
					if (seg['SCH.24']['SCH.24.2'].toString() != "") { objParentFillerAppointmentId.namespaceId= seg['SCH.24']['SCH.24.2'].toString(); }
					if (seg['SCH.24']['SCH.24.3'].toString() != "") { objParentFillerAppointmentId.universalId= seg['SCH.24']['SCH.24.3'].toString(); }
					if (seg['SCH.24']['SCH.24.4'].toString() != "") { objParentFillerAppointmentId.universalIdType= seg['SCH.24']['SCH.24.4'].toString(); }

					objSCH.parentFillerAppointmentId= objParentFillerAppointmentId;
				}

				// SCH.25, filler status code
				if(typeof(seg['SCH.25']) !== 'undefined') {
					var objFillerStatusCode= new Object();
					if (seg['SCH.25']['SCH.25.1'].toString() != "") { objFillerStatusCode.id= seg['SCH.25']['SCH.25.1'].toString(); }
					if (seg['SCH.25']['SCH.25.2'].toString() != "") { objFillerStatusCode.text= seg['SCH.25']['SCH.25.2'].toString(); }
					if (seg['SCH.25']['SCH.25.3'].toString() != "") { objFillerStatusCode.codeSystem= seg['SCH.25']['SCH.25.3'].toString(); }
					if (seg['SCH.25']['SCH.25.4'].toString() != "") { objFillerStatusCode.alternateId= seg['SCH.25']['SCH.25.4'].toString(); }
					if (seg['SCH.25']['SCH.25.5'].toString() != "") { objFillerStatusCode.alternateText= seg['SCH.25']['SCH.25.5'].toString(); }
					if (seg['SCH.25']['SCH.25.6'].toString() != "") { objFillerStatusCode.altCodeSystem= seg['SCH.25']['SCH.25.6'].toString(); }

					objSCH.fillerStatusCode= objFillerStatusCode;
				}

				/*
				// clean up SCH and increment index
				if (Object.keys(objSCH[index]).length == 0) {
					delete objSCH[index];
				} else {
					index++;
				}
				*/

			}
		}

		return objSCH;
	}
}

/**
	To verify if one string (source) contains another string (target).

	@param {String} arg1 - target string to lookup in the source string
	@return {String} return true if the source string contains the target string
*/
String.prototype.contains = function(arg1) {

	return this.indexOf(arg1) > -1;

};

/**
	To verify if the given string contains HTTP status code.

	@param - no parameter
	@return {String} return true if the string contains the HTTP status code, false otherwise
*/

function isHttpCode2xx(arg1) {
	var code2xx = ['200','201','202','203','204','205','206','207','208'];	
	var length = code2xx.length;

	while(length--) {
   		if (arg1.indexOf(code2xx[length]) > -1) {
       		return true;
   		}   		
	}
	return false;
}

function isHttpCode4xx(arg1) {
	var code4xx = ['400','401','402','403','404','405','406','407','408','409','410','411','412','413','414','415','416','417','429','431','451'];	
	var length = code4xx.length;

	while(length--) {
   		if (arg1.indexOf(code4xx[length]) > -1) {
       		return true;
   		}   		
	}
	return false;
}

function isHttpCode5xx(arg1) {
	var code5xx = ['500','501','502','503','504','505','506','510','511'];	
	var length = code5xx.length;

	while(length--) {
   		if (arg1.indexOf(code5xx[length]) > -1) {
       		return true;
   		}   		
	}
	return false;
}

