/**
	convertMSHtoJSON(segment)

	- Desc: This function retrieves HL7v2 HAPI format elements and values from MSH segment converts to AristaMd customized JSON object for reference

	@param {String} segment - this contains the raw segment (string)
	@return {object} objMSH - this contains the MSH segment elements composed into a single objMSH
		object.
*/
function convertMSHtoJSON(segment) {

	var scriptName= 'convertMSHtoJSON';
	var debug= 0;
	if (debug>0) { logger.error(scriptName+'::DEBUG segment: '+segment.toString()); }

	// define variables
	if (typeof(segment.toString()) !== undefined) {

		// MSH
		// var objMSH= new Object();
		var meta = {};

		// MSH.9.1 Message Type -> DataModel
		if (msg['MSH']['MSH.9']['MSH.9.1'].toString() != "") { 
			var columnName= "DataModel";
			if (msg['MSH']['MSH.9']['MSH.9.1'].toString() == "SIU") { 
				meta[columnName] = "Scheduling"; 
			} else if (msg['MSH']['MSH.9']['MSH.9.1'].toString() == "ORU") {
				meta[columnName]= "Order Result";
			} else if (msg['MSH']['MSH.9']['MSH.9.1'].toString() == "OMG") {
				meta[columnName]= "New Order";
			} // end if 
		} // end if 

		// MSH.9.2 Event Type -> EventType
		if (msg['MSH']['MSH.9']['MSH.9.2'].toString() != "") { 
			var columnName= "EventType";
			if (msg['MSH']['MSH.9']['MSH.9.2'].toString() == "S12") { 
				meta[columnName] = "New"; 
			} else if (msg['MSH']['MSH.9']['MSH.9.2'].toString() == "S14") {
				meta[columnName]= "Modification";
			} else if (msg['MSH']['MSH.9']['MSH.9.2'].toString() == "S15") {
				meta[columnName]= "Cancel";
			} // end if 
		} // end if

		// MSH.7.1 Date/Time of Message -> EventDateTime
		if (msg['MSH']['MSH.7']['MSH.7.1'].toString() != "") { 
			var columnName= "EventDateTime";
			if(msg['MSH']['MSH.7']['MSH.7.1'].toString().indexOf("T")>-1) {
				var columnValue = msg['MSH']['MSH.7']['MSH.7.1'].toString().substr(0,19).replace(/[^0-9]/g, "");
			} else {
				var columnValue = msg['MSH']['MSH.7']['MSH.7.1'].toString();
			}
			meta[columnName] = columnValue;
			if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+columnValue); }
		} // end if 

		// MSH.11.1 ProcessedId -> Test
		if (msg['MSH']['MSH.11']['MSH.11.1'].toString() != "") { 
			var columnName= "Test";
			if(msg['MSH']['MSH.11']['MSH.11.1'].toString()== 'T') {
				var columnValue = "true";
			} else {
				var columnValue = "false";
			}
			meta[columnName] = columnValue;
			if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+columnValue); }
		} // end if 

		// MSH.3.1 SendingApplication -> Source.Name
		if (msg['MSH']['MSH.3']['MSH.3.1'].toString() != "") { 
			var source_name_obj= {};
			var columnName= "Name";
			var columnValue= msg['MSH']['MSH.3']['MSH.3.1'].toString();

			source_name_obj[columnName]= columnValue;
			var parentColumnName= "Source";		
			meta[parentColumnName] = source_name_obj;

			if(debug>0) { logger.info(scriptName+"::DEBUG meta["+parentColumnName+"] value: "+JSON.stringify(source_name_obj)); }
		} // end if 

		// MSH.5.1 ReceivingApplication -> Destination.Name
		if (msg['MSH']['MSH.5']['MSH.5.1'].toString() != "") { 
			var destination_name_obj= {};
			var columnName= "Name";
			var columnValue= msg['MSH']['MSH.5']['MSH.5.1'].toString();

			destination_name_obj[columnName]= columnValue;
			var parentColumnName= "Destination";		
			meta[parentColumnName] = destination_name_obj;

			if(debug>0) { logger.info(scriptName+"::DEBUG meta["+parentColumnName+"] value: "+JSON.stringify(destination_name_obj)); }
		} // end if 

		// MSH.10.1 MessageControlID -> Message.ID
		if (msg['MSH']['MSH.10']['MSH.10.1'].toString() != "") { 
			var messageControlId_obj= {};
			var columnName= "ID";
			var columnValue= msg['MSH']['MSH.10']['MSH.10.1'].toString();

			messageControlId_obj[columnName]= columnValue;
			var parentColumnName= "Message";		
			meta[parentColumnName] = messageControlId_obj;

			if(debug>0) { logger.info(scriptName+"::DEBUG meta["+parentColumnName+"] value: "+JSON.stringify(messageControlId_obj)); }
		} // end if 

		jsonMeta= JSON.stringify(meta); // convert to JSON
		if(debug>0) { logger.info(scriptName+"::DEBUG meta: "+JSON.stringify(jsonMeta)); }

		/*
		// objMSH.4, sendingFacility
		if (msg['MSH']['MSH.4']['MSH.4.1'].toString() != "") { objMSH.sendingFacility= msg['MSH']['MSH.4']['MSH.4.1'].toString(); }

		// objMSH.5, receivingApplication
		if (msg['MSH']['MSH.5']['MSH.5.1'].toString() != "") { objMSH.receivingApplication = msg['MSH']['MSH.5']['MSH.5.1'].toString(); }

		// objMSH.6, receivingFacility
		if (msg['MSH']['MSH.6']['MSH.6.1'].toString() != "") { objMSH.receivingFacility = msg['MSH']['MSH.6']['MSH.6.1'].toString(); }

		// objMSH.7, dateTimeOfMessage
		if (msg['MSH']['MSH.7']['MSH.7.1'].toString() != "") { objMSH.messageDateTime = msg['MSH']['MSH.7']['MSH.7.1'].toString(); }

		// objMSH.8, security
		if (msg['MSH']['MSH.8']['MSH.8.1'].toString() != "") { objMSH.security = msg['MSH']['MSH.8']['MSH.8.1'].toString(); }

		// objMSH.9, messageType
		if (msg['MSH']['MSH.9'].toString() != "") {
			objMSH.messageType = msg['MSH']['MSH.9']['MSH.9.1'].toString();
			objMSH.eventType   = msg['MSH']['MSH.9']['MSH.9.2'].toString();
			// logger.error(scriptName+'::DEBUG objMSH_messageType: '+objMSH.messageType+' objMSH_eventType: '+objMSH.eventType);
		}

		// objMSH.10, messageControlID
		if (msg['MSH']['MSH.10']['MSH.10.1'].toString() != "") { 
			objMSH.messageControlID = msg['MSH']['MSH.10']['MSH.10.1'].toString(); 
		}

		// objMSH.11, processingID
		if (msg['MSH']['MSH.11']['MSH.11.1'].toString() != "") {
			objMSH.processingID = msg['MSH']['MSH.11']['MSH.11.1'].toString(); 
		}

		// objMSH.12, versionID
		if (msg['MSH']['MSH.12']['MSH.12.1'].toString() != "") { objMSH.hl7version = msg['MSH']['MSH.12']['MSH.12.1'].toString(); }

		// objMSH.13, sequenceNumber
		if (msg['MSH']['MSH.13']['MSH.13.1'].toString() != "") { objMSH.sequenceNumber = msg['MSH']['MSH.13']['MSH.13.1'].toString(); }

		// objMSH.14, continuationPointer
		if (msg['MSH']['MSH.14']['MSH.14.1'].toString() != "") { objMSH.continuationPointer = msg['MSH']['MSH.14']['MSH.14.1'].toString(); }

		// objMSH.15, acceptAcknowledgmentType
		if (msg['MSH']['MSH.15']['MSH.15.1'].toString() != "") { objMSH.acceptAcknowledgmentType = msg['MSH']['MSH.15']['MSH.15.1'].toString(); }

		// objMSH.16, applicationAcknowledgmentType
		if (msg['MSH']['MSH.16']['MSH.16.1'].toString() != "") { objMSH.applicationAcknowledgmentType = msg['MSH']['MSH.16']['MSH.16.1'].toString(); }

		// objMSH.17, countryCode
		if (msg['MSH']['MSH.17']['MSH.17.1'].toString() != "") { objMSH.countryCode = msg['MSH']['MSH.17']['MSH.17.1'].toString(); }

		// objMSH.18, characterSet
		if (msg['MSH']['MSH.18']['MSH.18.1'].toString() != "") { objMSH.characterSet= msg['MSH']['MSH.18']['MSH.18.1'].toString(); }

		// objMSH.19, principalLanguageOfMessage
		if (msg['MSH']['MSH.19']['MSH.19.1'].toString() != "") { objMSH.principalLanguageOfMessage = msg['MSH']['MSH.19']['MSH.19.1'].toString(); }

		if (debug>0) { 
			logger.error(scriptName+'::DEBUG objMSH: '+JSON.stringify(objMSH)); 
		}
		*/
		channelMap.put('MSH_json',jsonMeta);
		return jsonMeta;
	}
}

/**
	convertPIDtoJSON(segment)

	- Desc: This function retrieves HL7v2 HAPI format elements and values from PID segment converts to AristaMd customized JSON object for reference

	@param {String} segment - this contains the raw segment (string)
	@return {object} objMSH - this contains the PID segment elements composed into a single objMSH
		object.
*/
function convertPIDtoJSON(segment) {

	var scriptName= 'convertPIDtoJSON';
	var debug= 0;
	// if (debug>0) { 
		logger.error(scriptName+'::DEBUG segment: '+segment.toString()); 
	// }

	// define variables
	if (typeof(segment.toString()) !== undefined) {

		// MSH
		// var objMSH= new Object();
		var patientHeader= {};

		var patient = {};

		// declare PatientIdentifiers array
		var patientIdentifiers= [];

		// declare PatientIdentifier object
		var patientIdentifier= {};
		debug= 1;
		if(typeof(msg['PID']['PID.3']) !== 'undefined') {
			for each (element in msg['PID']['PID.3']) {
				logger.info('PID.3 element: '+element);
				// PID.3.1 Patient Identifier List -> Patient Identifier
				if(typeof(element['PID.3.1']) !== 'undefined') {
					var columnName= "ID";
					if(element['PID.3.1'].toString() != "") { 
						patientIdentifier[columnName] = element['PID.3.1'].toString();
					} else {
						patientIdentifier[columnName] = null;
					} // end if
					if(debug>0) { logger.info(scriptName+"::DEBUG patientIdentifier["+columnName+"] typeof: "+typeof(patientIdentifier[columnName])+" value: "+patientIdentifier[columnName]); }
				} // end if 

				// PID.3.5 Patient Identifier Type Code -> IDType
				if (typeof(element['PID.3.5']) !== 'undefined') {
					var columnName= "IDType";
					if(element['PID.3.5'].toString() != "") { 
						patientIdentifier[columnName] = element['PID.3.5'].toString();
					} else {
						patientIdentifier[columnName] = null;	
					} // end if
					if(debug>0) { logger.info(scriptName+"::DEBUG patientIdentifier["+columnName+"] typeof: "+typeof(patientIdentifier[columnName])+" value: "+patientIdentifier[columnName]); }
				} // end if

				if(typeof(patientIdentifier) !== 'undefined') {
					patientIdentifiers.push(patientIdentifier);
					if(debug>0) { logger.info(scriptName+"::DEBUG patientIdentifiers typeof: "+typeof(patientIdentifiers)+" value: "+patientIdentifiers+" string: "+patientIdentifiers.toString()); }
				}

			}

			// Add Identifiers Object to Patient Object
			var columnName= "Identifiers";
			patient[columnName]= patientIdentifiers;

			if(debug>0) { logger.info(scriptName+"::DEBUG patient["+columnName+"] typeof: "+typeof(patient[columnName])+" value: "+patient[columnName]+" stringify: "+JSON.stringify(patient[columnName])); }
		}


		// declare Demographics 
		var patientDemographics= {};
		debug= 0;
		// PID.5.2 FirstName -> Patient Demographics FirstName 
		if (typeof(msg['PID']['PID.5']['PID.5.2']) !== 'undefined') {
			var columnName= "FirstName";
			if(msg['PID']['PID.5']['PID.5.2'].toString() != "") {
				patientDemographics[columnName] = msg['PID']['PID.5']['PID.5.2'].toString();
			} else {
				patientDemographics[columnName] = null;		
			} // end if
			if(debug>0) { logger.info(scriptName+"::DEBUG patientDemographics["+columnName+"] value: "+patientDemographics[columnName]); }
		} // end if

		// PID.5.1 LastName -> Patient Demographics LastName 
		if (typeof(msg['PID']['PID.5']['PID.5.1']) !== 'undefined') {
			var columnName= "LastName";
			if(msg['PID']['PID.5']['PID.5.1'].toString() != "") {
				patientDemographics[columnName] = msg['PID']['PID.5']['PID.5.1'].toString();
			} else {
				patientDemographics[columnName] = null;		
			} // end if
			if(debug>0) { logger.info(scriptName+"::DEBUG patientDemographics["+columnName+"] value: "+patientDemographics[columnName]); }
		} // end if

		// PID.5.3 MiddleName -> Patient Demographics MiddleName 
		if (typeof(msg['PID']['PID.5']['PID.5.3']) !== 'undefined') {
			var columnName= "MiddleName";
			if(msg['PID']['PID.5']['PID.5.3'].toString() != "") {
				patientDemographics[columnName] = msg['PID']['PID.5']['PID.5.3'].toString();
			} else {
				patientDemographics[columnName] = null;		
			} // end if
			if(debug>0) { logger.info(scriptName+"::DEBUG patientDemographics["+columnName+"] value: "+patientDemographics[columnName]); }
		} // end if

		// PID.7.1 DateOfBirth -> Patient Demographics DateOfBirth 
		if (typeof(msg['PID']['PID.7']['PID.7.1']) !== 'undefined') {
			var columnName= "DOB";
			if(msg['PID']['PID.7']['PID.7.1'].toString() != "") {
				patientDemographics[columnName] = msg['PID']['PID.7']['PID.7.1'].toString();
			} else {
				patientDemographics[columnName] = null;		
			} // end if
			if(debug>0) { logger.info(scriptName+"::DEBUG patientDemographics["+columnName+"] value: "+patientDemographics[columnName]); }
		}

		// PID.8 Administrative Sex -> Patient Demographics Sex
		if (typeof(msg['PID']['PID.8']['PID.8.1']) !== 'undefined') {
			var columnName= "Sex";
			if(msg['PID']['PID.8']['PID.8.1'].toString() != "") {
				patientDemographics[columnName] = msg['PID']['PID.8']['PID.8.1'].toString();
			} else {
				patientDemographics[columnName] = null;		
			} // end if
			if(debug>0) { logger.info(scriptName+"::DEBUG patientDemographics["+columnName+"] value: "+patientDemographics[columnName]); }
		} // end if

		// PID.10.1 Race Identifier -> Patient Demographics Race
		if (typeof(msg['PID']['PID.10']['PID.10.1']) !== 'undefined') {
			var columnName= "Race";
			if(msg['PID']['PID.10']['PID.10.1'].toString() != "") {
				patientDemographics[columnName] = msg['PID']['PID.10']['PID.10.1'].toString();
			} else {
				patientDemographics[columnName] = null;		
			} // end if
			if(debug>0) { logger.info(scriptName+"::DEBUG patientDemographics["+columnName+"] value: "+patientDemographics[columnName]); }
		} // end if 

		// PID.16.1 Martial Status Identifier -> Patient Demographics MaritalStatus
		if (typeof(msg['PID']['PID.16']['PID.16.1']) !== 'undefined') {
			var columnName= "MaritalStatus";
			if(msg['PID']['PID.16']['PID.16.1'].toString() != "") {
				patientDemographics[columnName] = msg['PID']['PID.16']['PID.16.1'].toString();
			} else {
				patientDemographics[columnName] = null;		
			} // end if
			if(debug>0) { logger.info(scriptName+"::DEBUG patientDemographics["+columnName+"] value: "+patientDemographics[columnName]); }
		} // end if

		// PID.30 Patient Death Indicator -> Patient Demographics IsDeceased
		if (typeof(msg['PID']['PID.30']) !== 'undefined') {
			var columnName= "IsDeceased";
			if(msg['PID']['PID.30'].toString() != "") {
				patientDemographics[columnName] = msg['PID']['PID.30'].toString();
			} else {
				patientDemographics[columnName] = null;		
			} // end if
			if(debug>0) { logger.info(scriptName+"::DEBUG patientDemographics["+columnName+"] value: "+patientDemographics[columnName]); }
		} // end if 

		// PID.29.1 Patient Death Datetime -> Patient Demographics DeathDateTime
		if (typeof(msg['PID']['PID.29']['PID.29.1']) !== 'undefined') {
			var columnName= "DeathDateTime";
			if(msg['PID']['PID.29']['PID.29.1'].toString() != "") {
				patientDemographics[columnName] = msg['PID']['PID.29']['PID.29.1'].toString();
			} else {
				patientDemographics[columnName] = null;		
			} // end if
			if(debug>0) { logger.info(scriptName+"::DEBUG patientDemographics["+columnName+"] value: "+patientDemographics[columnName]); }
		} // end if 

		// declare phoneNumbers 
		var phoneNumbers= {};

		// declare phoneNumber
		var phoneNumber= {};

		// PID.13.1 Phone Number - Home -> Patient Demographics PhoneNumber Home
		if (typeof(msg['PID']['PID.13']['PID.13.1']) !== 'undefined') {
			var columnName= "Home";
			if(msg['PID']['PID.13']['PID.13.1'].toString() != "") {
				phoneNumber[columnName] = msg['PID']['PID.13']['PID.13.1'].toString();
			} else {
				phoneNumber[columnName] = null;		
			} // end if
			if(debug>0) { logger.info(scriptName+"::DEBUG phoneNumber["+columnName+"] value: "+phoneNumber[columnName]); }
			Object.assign(phoneNumbers, phoneNumber);
		} // end if 

		// PID.14.1 Phone Number - Business -> Patient Demographics PhoneNumber Office
		if (typeof(msg['PID']['PID.14']['PID.14.1']) !== 'undefined') {
			var columnName= "Office";
			if(msg['PID']['PID.14']['PID.14.1'].toString() != "") {
				phoneNumber[columnName] = msg['PID']['PID.14']['PID.14.1'].toString();
			} else {
				phoneNumber[columnName] = null;		
			} // end if
			if(debug>0) { logger.info(scriptName+"::DEBUG phoneNumber["+columnName+"] value: "+phoneNumber[columnName]); }
			Object.assign(phoneNumbers, phoneNumber);
		} // end if

		if(typeof(phoneNumbers) !== 'undefined') {
			var columnName= "PhoneNumber";
			patientDemographics[columnName]= phoneNumbers;
			if(debug>0) { logger.info(scriptName+"::DEBUG patientDemographics["+columnName+"] value: "+patientDemographics[columnName]); }
		} // end if 

		// PID.13.4 Email Address -> Patient Demographics EmailAddresses
		if (typeof(msg['PID']['PID.13']['PID.13.4']) !== 'undefined') {
			var columnName= "EmailAddresses";
			if(msg['PID']['PID.13']['PID.13.4'].toString() != "") {
				patientDemographics[columnName] = msg['PID']['PID.13']['PID.13.4'].toString();
			} else {
				patientDemographics[columnName] = null;		
			} // end if
			if(debug>0) { logger.info(scriptName+"::DEBUG patientDemographics["+columnName+"] value: "+patientDemographics[columnName]); }
		} // end if 

		// Add Demographics Object to Patient Object
		var columnName= "Demographics";
		patient[columnName]= patientDemographics;
		if(debug>0) { logger.info(scriptName+"::DEBUG patient["+columnName+"] value: "+patient[columnName]); }

		var columnName= 'Patient';
		patientHeader[columnName]= jsonMeta;

		// jsonMeta= JSON.stringify(patientHeader); // convert to JSON
		if(debug>0) { logger.info(scriptName+"::DEBUG patientHeader: "+patientHeader); }

		channelMap.put('PID_json',patientHeader);
		return jsonMeta;
	}
}

/**
	checkElementIfUndefined (element, debug)

	- Function to check provided Element if Undefined or length less than 1 character return null.

	@param {element} - XML object provided (e.g. msg['ORC']['ORC.7']['ORC.7.1']
	@return {String} return XML object or null object reference
*/
function checkElementIfUndefined (element, debug) {
	var scriptName= 'checkHL7ElementIfUndefined';

	if(debug>0) { logger.info(scriptName+"::DEBUG element: "+element+" typeof: "+typeof(element)+" string: "+element.toString()+" length: "+element.toString().length); }

	if(element == 'undefined') {
		return null;
	} else {
		if(element.toString().length<1) {
			return null;
		} else {
			return element;
		}
	}
}

/**
	Converts standard HL7 v2.x date format to human-readable ISO date format
*/

/*
	Author: IntegrateIT.us LLC
	toISODate: Returns an ISO-formatted date string with specified timezone offset

	Arguments
	---------
		Required
		--------
			hl7DateString:		An HL7-formatted date string (ex: "yyyyMMddHHmmss"

		Optional
		--------
			timezome:			A timezone identifier string corresponding to one of the java.util.TimeZone values (ex: "US/Central")
*/

function toISODate( hl7DateString, timezone ) {
	var isoDate;
	var dateString;
	var hl7Formatter;
	var isoFormatter = new Packages.java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ssZ");

	if (timezone) {
		isoFormatter.setTimeZone(Packages.java.util.TimeZone.getTimeZone(timezone));
	}

	if ( hl7DateString != null && !isNaN(hl7DateString.substring(0,13)) && hl7DateString.length > 14 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmmssZ");
	}
	else if ( hl7DateString != null && !isNaN(hl7DateString) && hl7DateString > 0 && hl7DateString.length == 14 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmmss");
	} 
	else if ( hl7DateString != null && !isNaN(hl7DateString) && hl7DateString > 0 && hl7DateString.length == 12 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmm");
	} 
	else if ( hl7DateString != null && !isNaN(hl7DateString) && hl7DateString > 0 && hl7DateString.length == 8 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMdd");
	} 
	else dateString = hl7DateString;

	try {
		if (hl7Formatter) {
			isoDate = hl7Formatter.parse(hl7DateString);
			isoDate = isoFormatter.format(isoDate);
			dateString = new String(isoDate);
		}
	} catch (err) {
		dateString = hl7DateString;
	} finally {
		return dateString;
	}
}

/**
	Converts ISO date format to HL7 v2 format - toHL7Date( isoDateString, timezone, debug )
*/

/*
	Author: IntegrateIT.us LLC
	toHL7Date: Returns an HL7-formatted date string with specified timezone offset

	Arguments
	---------
		Required
		--------
			isoDateString:		An ISO-formatted date string (ex: "yyyy-MM-ddTHH:mm:ssZ"

		Optional
		--------
			timezome:			A timezone identifier string corresponding to one of the java.util.TimeZone values (ex: "US/Central")
*/

function toHL7Date( isoDateString, timezone, debug ) 
{
	var scriptName= "toHL7Date";
	var hl7Date;
	var dateString;
	var isoFormatter;
	var hl7Formatter;

	// 2020-11-07T00:01:22+00:00
	var strTimeZone= "GMT-8:00";
	if(debug>0) { logger.info(scriptName+"::DEBUG:BEGIN isoDateString: "+isoDateString+", timezone: "+timezone+", strTimeZone: "+strTimeZone); }

	// ISO Date Format: yyyy-MM-dd'T'HH:mm:ss'Z'
	if ( isoDateString != null && isoDateString.length <= 25 && isoDateString.length >= 22) {
		isoFormatter = new Packages.java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ssZ");
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmmssZ");
		if (timezone) {
			hl7Formatter.setTimeZone(Packages.java.util.TimeZone.getTimeZone(strTimeZone));
		}
		if(debug>0) { logger.info(scriptName+"::DEBUG:UPDATE isoDateString: "+isoDateString+", isoFormatter: "+isoFormatter.toString()+", hl7Formatter: "+hl7Formatter.toString()+", timezone: "+timezone+", strTimeZone: "+strTimeZone); }
	}
	// ISO Date Format: yyyy-MM-dd HH:mm:ss
	else if ( isoDateString != null && isoDateString.length == 19 ) {
		isoFormatter = new Packages.java.text.SimpleDateFormat("yyyy-MM-dd HH:mm:ss");
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmmss");
	}
	// ISO Date Format: yyyy-MM-dd HH:mm
	else if ( isoDateString != null && isoDateString.length == 16 ) {
		isoFormatter = new Packages.java.text.SimpleDateFormat("yyyy-MM-dd HH:mm");
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmm");
	} 
	// ISO Date Format: yyyy-MM-dd
	else if ( isoDateString != null && isoDateString.length == 10 ) {
		isoFormatter = new Packages.java.text.SimpleDateFormat("yyyy-MM-dd");
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMdd");	
	}
	// None of the above, return original date string.
	else dateString = isoDateString;
	if(debug>0) { logger.info(scriptName+"::DEBUG:END isoDateString: "+isoDateString+", isoFormatter: "+isoFormatter.toString()+", hl7Formatter: "+hl7Formatter.toString()+", timezone: "+timezone+", strTimeZone: "+strTimeZone); }

	try {
		if (isoFormatter) {
			hl7Date = isoFormatter.parse(isoDateString);
			hl7Date = hl7Formatter.format(hl7Date);
			dateString = new String(hl7Date);
		}
	} catch (err) {
		dateString = isoDateString;
	} finally {
		return dateString;
	}
}

/**

	Convert MM-DD-YY HH:MM format to HL7 format

	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function convertMDYtoHL7DateTime(eventDateTime, debug) {

	var scriptName= 'convertMDYtoHL7DateTime';
	if(typeof(debug) == 'undefined') { var debug= 0; }

	var lstEventDateTime= eventDateTime.toUpperCase().split(" ");
	var tEventDateTime= "".concat(lstEventDateTime[0]).concat(" ").concat(lstEventDateTime[1]);

	var nEventDate= tEventDateTime.slice(6,8).concat("20").concat(tEventDateTime.slice(0,2)).concat(tEventDateTime.slice(3,5));
	var nEventTime= tEventDateTime.split(" ")[1];

	if(nEventTime.toString().indexOf("PM")>-1) {
		if(debug>0) { logger.info(scriptName+"::DEBUG: eventTime contains PM: " + nEventTime.toString().indexOf("PM")); }
		if(nEventTime.toString().length<7) {
			if(debug>0) { logger.info(scriptName+"::DEBUG: eventTime length<7 chars: " + nEventTime.toString().length); } 
			var addLeadingZero= 12;
			var tHours= nEventTime.split(":")[0];
			var tMinutes= nEventTime.split(":")[1].replace(/[^0-9]/g, "");
			var tHours= parseInt(addLeadingZero) + parseInt(tHours);
			var tEventTime= tHours.toString().concat(tMinutes.toString()).concat("00");
			if(debug>0) { logger.info(scriptName+"::DEBUG: new EventTime: " + tEventTime.toString()); } 
		} else { 
			var tEventTime= nEventTime.toString().replace(/[^0-9]/g, "") + "00"; 
		} 
	} else {
		var tEventTime= "0" + nEventTime.toString().replace(/[^0-9]/g, "") + "00"; 
	}
	var nEventDateTime= nEventDate.toString().concat(tEventTime.toString());
	if(debug>0) { logger.info(scriptName+"::DEBUG datetime: "+nEventDateTime); }

	return nEventDateTime;

}

/** currentDate_yyyyMMddhhmmss

  	- Desc: This function returns current DateTime only YYYYMMDDHHMMSS format

	@return {string} datetime - this string object contains the current DateTime only YYYYMMDDHHMMSS format
*/
function currentDate_yyyyMMddhhmmss() {
	var today = new Date();
	// set Date with leading zeros if needed
	var tMonth= today.getMonth()+1;
	if(tMonth.toString().length<2) tMonth= '0'+tMonth;
	var tDay= today.getDate()
	if(tDay.toString().length<2) tDay= '0'+tDay;
	var date = today.getFullYear()+''+tMonth+''+tDay;

	// set Time with leading zeros if needed
	var tHour= today.getHours();
	if(tHour.toString().length<2) tHour= '0'+tHour;
	var tMinute= today.getMinutes();
	if(tMinute.toString().length<2) tMinute= '0'+tMinute;
	var tSecond= today.getSeconds();
	if(tSecond.toString().length<2) tSecond= '0'+tSecond;

	var time = tHour+''+tMinute+''+tSecond;
	var dateTime = date+''+time;

	return dateTime;
}

/**
	getChannelId(nameOfChannel) function
	DESC: This function takes the string nameOfChannel Object as a parameter and returns the string channelId

	PARAMETERS:
	@param	{string} - pass the string object of the channelName
	@return 	{string} - return the channelId

	MODIFIED:	

 	USAGE:								
	E.G 
	var arrElementObjects= setXMLElementsArrayWithDescendants(xmlElement, debug);

*/

function getChannelId(nameOfChannel){ 
    if (nameOfChannel === undefined || nameOfChannel.length == 0) return ""; 

    var listIDs = ChannelUtil.getChannelIds(); 
    if (listIDs.isEmpty()) return ""; 

    var nameFromList; 
    for (var i = 0; i < listIDs.size();i++){ 
        nameFromList = ChannelUtil.getChannelName(listIDs.get(i)); 
        if(nameFromList == nameOfChannel) return listIDs.get(i); 
    } 
    return ""; 
}

/**
	setHL7v2ACK(msg) - DEVELOPMENT

	- Desc: This function set and returns HL7v2 HAPI Acknowledgement (ACK) message based on evaluating conditions

	@param 
	sourceMsg {string} - source message
	responseCode {string} - Mirth Connect processing responseCode
	responseMsg {string} - response MSA message
	responseStatus {string} - Mirth Connect processing responseStatus

	@return {XML} - this returns the HL7 HAPI Ack message
*/
function setHL7v2ACK(sourceMsg,responseCode,responseMsg,responseStatus) {

	var scriptName= 'getHL7v2ACK';
	var debug= 0;
	if (debug>0) { logger.error(scriptName+'::DEBUG msg.MSH: '+JSON.stringify(msg['MSH'].toString())); }

	importPackage(com.mirth.connect.model);
	// responseStatus is an optional parameter
	if (!responseStatus)
		responseStatus = {'AA':Response.Status.SUCCESS,'AR':Response.Status.FILTERED,'AE':Response.Status.FAILURE}[responseCode] || Response.Status.UNKNOWN;
	var ack = <HL7Message/>;

	ack.MSH['MSH.1'] = sourceMsg.MSH['MSH.1'].toString();
	ack.MSH['MSH.2'] = sourceMsg.MSH['MSH.2'].toString();
	ack.MSH['MSH.3'] = sourceMsg.MSH['MSH.5'].copy();
	ack.MSH['MSH.4'] = sourceMsg.MSH['MSH.6'].copy();
	ack.MSH['MSH.5'] = sourceMsg.MSH['MSH.3'].copy();
	ack.MSH['MSH.6'] = sourceMsg.MSH['MSH.4'].copy();
	ack.MSH['MSH.7']['MSH.7.1'] = DateUtil.getCurrentDate('yyyyMMddHHmmss');
	ack.MSH['MSH.9']['MSH.9.1'] = sourceMsg.MSH['MSH.9']['MSH.9.1'].toString();
	ack.MSH['MSH.9']['MSH.9.2'] = sourceMsg.MSH['MSH.9']['MSH.9.2'].toString();
	ack.MSH['MSH.9']['MSH.9.3'] = 'ACK';
	ack.MSH['MSH.10'] = sourceMsg.MSH['MSH.10'].copy();
	ack.MSH['MSH.11'] = sourceMsg.MSH['MSH.11'].copy();
	ack.MSH['MSH.12'] = sourceMsg.MSH['MSH.12'].copy();
	ack.MSA['MSA.1']['MSA.1.1'] = responseCode;
	ack.MSA['MSA.2']['MSA.2.1'] = sourceMsg.MSH['MSH.10']['MSH.10.1'].toString();
	ack.MSA['MSA.3']['MSA.3.1'] = responseMsg;

	responseMap.put('ACK',new Response(responseStatus,SerializerFactory.getHL7Serializer().fromXML(ack)));

}

/**
	Converts JSON Null Object values to empty string
*/

/*
	Author: IntegrateIT.us LLC
	toHL7Date: Returns an empty string

	Arguments
	---------
		Required
		--------
			JSON element:		A JSON element (e.g. "MiddleName":null )

*/

function jsonNulltoEmptyString( strElement, jsonElement, debug ) 
{

	var scriptName= "jsonNulltoEmptyString";
	if(debug>0) {
		logger.info(scriptName+"::DEBUG jsonElement IN key:"+strElement+" value: "+jsonElement); 
		logger.info(scriptName+"::DEBUG typeof jsonElement IN: "+typeof(jsonElement));
	}
	// try {
		if (typeof(jsonElement) !== 'undefined') {
			if (typeof(jsonElement) !== 'string') {
				if (typeof(jsonElement) == 'object') {
					var jsonElement= "";
				} 
			} else {
				if (jsonElement.toString()== null) {
					var jsonElement= jsonElement.replace(null,"");
				}
			}	
		} else {
			var jsonElement= "";
		}
	/*
	} catch (err) {
		return jsonElement
	} finally {
		return newJsonElement;
	}
	*/
	if(debug>0) {
		logger.info(scriptName+"::DEBUG jsonElement OUT key:"+strElement+" value: "+jsonElement); 
		logger.info(scriptName+"::DEBUG typeof jsonElement OUT: "+typeof(jsonElement));
		logger.info("");
	}
	return jsonElement;
}

/**
	This function will produce a random date based on a provided date and number of days argument.

	@param {String} start - enter the starting date ('YYYY', 'MM', 'DD') (e.g. '2021', '09', '13')
	@param {String} days -  number of days (positive or negative) (e.g. 365, 3000, -365)
	@return {String} return description
*/
function random_date_after_date(start, days) {

	// randomDateAfterDate(new Date(2021, 4, 5), -365) 
	var scriptName= 'random_date_after_date';
	var debug= 1;

	// if(debug>0) {
		logger.error('DEBUG::'+scriptName+' start= '+start+' number of days: '+days);
	// }

	var strRandom= Math.random()*days*10000;
	var strStringRandom= strRandom.toString();
	var strRandom= strStringRandom.substring(0,4);

	var newDate= new Date(start.getTime() + strRandom);
	// Fri Jun 26 2020 08:59:35 GMT-0600 (GMT-06:00)
	var strDate= newDate.getFullYear() + "" + addLeadingZeros(newDate.getMonth() + 1) + "" + addLeadingZeros(newDate.getDate());

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' random date generated: '+strDate);
	}

	return strDate;
}

function addLeadingZeros(n) {

  if (n <= 9) {
    return "0" + n;
  }

  return n;
}

/**
	This function will produce a random number based on the number of characters provided into the numOfChars argument.

	@param {String} numOfChars - enter the number of random characters to be returned
	@return {String} return description
*/
function random_generator(numOfChars) {
	var scriptName= 'random_generator';
	var debug= 0;

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' evaluate number of characters: '+numOfChars);
	}

	var strNumOfChars= ''+numOfChars+'';
	var cntNumOfChars= strNumOfChars.padEnd(numOfChars,0);
	var strRandom= (Math.random()*cntNumOfChars).toFixed();

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' random number generated: '+strRandom);
	}

	return strRandom;
}

/**

	sendHttpsPostClientCert(state, url, certificatePath, certificatePassword) - Returns custom HTTP response

	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} state - arg1 description
	@param {String} url - arg2 description
	@param {String} certificatePath - arg3 description
	@param {String} certificatePasswordte - arg4 description
	@return {String} return description
*/
function sendHttpsPostClientCert(state, url, certificatePath, certificatePassword) {
	// write code here.

	var scriptName= "sendHttpsPostClientCert";
	var messageBody = messageObject.getEncodedData();
	var httpsSender = new Packages.com.xti.util.HTTPSSender(certificatePath, certificatePassword);

	// Set all the necessary parameters...
	httpsSender.setURL(url);
	httpsSender.setMessageBody(messageBody);

	logger.info(scriptName+"::DEBUG Sending message to " + url + " - " + messageBody);

	// Send the message to the URL...
	try {
		httpsSender.send();
		var retString = httpsSender.getReturnValue();
		retString = retString.substr(0, retString.indexOf("MSA")) + "\r" + retString.substr(retString.indexOf("MSA"));
		var responseMessage = new Packages.com.mirth.connect.model.converters.SerializerFactory.getHL7Serializer().toXML(retString);
		channelMap.put('responseMessage',responseMessage);

		if (responseMessage['MSA']['MSA.1']['MSA.1.1'].toString() == 'AA') {
			logger.info(scriptName+"::DEBUG HTTPS send success. Return value= " + retString);
		} else {
			logger.info(scriptName+"::DEBUG HTTPS send failure. Return value= " + retString);
			throw("HTTPS send failure. Return value=" + retString);
		}
	} catch (e) {
		logger.info(scriptName+"::DEBUG Error message= " + e.message);
		throw(e.message);
	}
}

/**
	set24Hrs - Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function set24Hrs(d){

  if (d.slice(-2) === "PM"){
      var hrs = parseInt(d.slice(-7,-5))
      var mins = d.slice(-4,-2)
      hrs = hrs + 12
      var dd = d.slice(0,9) + " " + hrs + ":" + mins;
      return dd;
  } else if(d.slice(-2) === "AM"){
      return (d.slice(0, 16));
  } else {
      throw ("UNRECOGNIZED_FORMAT","set24Hrs() Received Unrecognized Formatted String");
  }
}

/**  getAddress(address) - Desc: This function receives CCD/CCDA msg and returns from Address Object

   	@param {String} msg - this contains the raw message (string)
	@return {object} objAddress - this contains the patientRole elements composed into a single objPatientRole object.

	History
	2022-07-28 JER - Modify the streetAddressLine if multiple streetAddressLine element objects
	2021-09-20 JER - Modified to incorporate evaluation of multiple patientRole Id(s) in the case there are multiple patient identifiers
	2021-09-20 JER - Modified to patient.name incorporate evaluation of multiple patient name child elements in the case there are multiple patient identifiers
*/
function getAddress(address) {

	// define variables
	var debug= 0;
	var scriptName= 'getAddress';
	var arrAddresses= new Array();

	if(debug>0) { logger.error(scriptName+' msg= '+msg); }
	if (typeof(address) !== 'undefined') {

		if(debug>0) { logger.error('DEBUG::'+scriptName+' address.length= '+address.length()+' address= '+address); }			

		if(address.length()>1) {
			// for each addr element
			var j= 0;
			for(var i= 0;i<1;i++) {

				var objAddress= new Object();
				objAddress.id= j;
				if(address[i]['@use']) {
					objAddress.use= address[i]['@use'].toString();
				} // end if address element @use property is not null

				if(debug>0) { logger.error('DEBUG::'+scriptName+' address['+i+'].children()= '+address[i].children()); }

				// 2022-07-28 JER - Modify the streetAddressLine if multiple streetAddressLine element objects
				var strStreetAddressLine= ''; var lenStreetAddressLine= address[i].streetAddressLine.length();
				for(var j= 0;j<address[i].streetAddressLine.length();j++) {
					if(lenStreetAddressLine>1) {
						strStreetAddressLine+= address[i].streetAddressLine[j].toString()+' ';
					} else {
						strStreetAddressLine=  address[i].streetAddressLine[j].toString();
					} // end if 
				} // end for 
				objAddress.streetAddressLine= strStreetAddressLine;

				/*
				if(address[i].streetAddressLine.toString().length>0) {
					objAddress.streetAddressLine=	address[i].streetAddressLine.toString();
				} else {
					objAddress.streetAddressLine=	""; 
				} // end if streetAddressLine
				*/	

					if(address[i].county.toString().length>0) {
						objAddress.county=			address[i].county.toString()
					} else {
						objAddress.county=	""; 
					} // end if county element

					if(address[i].city.toString().length>0) {
						objAddress.city=			address[i].city.toString()
					} else {
						objAddress.city=	""; 
					} // end if city element

					if(address[i].state.toString().length>0) {
						objAddress.state=			address[i].state.toString()
					} else {
						objAddress.state= "";
					} // end if state element

					if(address[i].postalCode.toString().length>0) {
						objAddress.postalCode=		address[i].postalCode.toString()
					} else {
						objAddress.postalCode= "";
					} // end if postalCode element

					if(address[i].useablePeriod) {
						if(address[i].useablePeriod.children().length()>0) {
							for each(child in address[i].useablePeriod.children()) {
								if(child.localName()== 'low') {
									objAddress.beginDate=	child['@low'].toString();
								}
								if(child.localName()== 'high') {
									objAddress.endDate=		child['@high'].toString();
								}
							} // end for each
						} // end if 
					} // end if useablePeriod element
					arrAddresses.push(objAddress);
				//} // for each child element
				if(debug>0) { logger.error('DEBUG::'+scriptName+'function arrAddress: '+JSON.stringify(arrAddresses)); }
				j++;
			} // for each address element 
		} else {
			if(debug>0) { logger.error('DEBUG::'+scriptName+' address.children()= '+JSON.stringify(address.children())); }
			var objAddress= new Object();
			objAddress.id= i;
			if(address['@use']) {
				objAddress.use= address['@use'].toString();
			} // end if address element @use property is not null

			if(debug>0) { logger.error('DEBUG::'+scriptName+' address.children()= '+address.children()); }

				// for each(child in address.children()) {

					if(address.streetAddressLine.toString().length>0) {
						objAddress.streetAddressLine=	address.streetAddressLine.toString();
					} else {
						objAddress.streetAddressLine=	""; 
					} // end if streetAddressLine

					if(address.county.toString().length>0) {
						objAddress.county=			address.county.toString()
					} else {
						objAddress.county=	""; 
					} // end if county element

					if(address.city.toString().length>0) {
						objAddress.city=			address.city.toString()
					} else {
						objAddress.city=	""; 
					} // end if city element

					if(address.state.toString().length>0) {
						objAddress.state=			address.state.toString()
					} else {
						objAddress.state= "";
					} // end if state element

					if(address.postalCode.toString().length>0) {
						objAddress.postalCode=		address.postalCode.toString()
					} else {
						objAddress.postalCode= "";
					} // end if postalCode element

					if(address.useablePeriod) {
						if(address.useablePeriod.children().length()>0) {
							for each(child in address.useablePeriod.children()) {
								if(child.localName()== 'low') {
									objAddress.beginDate=	child['@low'].toString();
								}
								if(child.localName()== 'high') {
									objAddress.endDate=		child['@high'].toString();
								}
							} // end for each
						} // end if 
					} // end if useablePeriod element
					arrAddresses.push(objAddress);
				// } // for each child element

				if(debug>0) { logger.error('DEBUG::'+scriptName+'function arrAddress: '+JSON.stringify(arrAddresses)); }

			arrAddresses= objAddress;
			if(debug>0) { logger.error('DEBUG::'+scriptName+'function arrAddress: '+JSON.stringify(arrAddresses)); }
		} // end if number of addr elements

		channelMap.put('arrAddresses',JSON.stringify(arrAddresses));
		return arrAddresses;
	}
}

/** getAssignedAuthor(msg)

  	- Desc: This function receives CCD assignedAuthor XML Object and returns JavaScript object
  	- Modified 
  	2018-02-24 04:18 CT JER - Modified the msg['assignedAuthor']['representedOrganization'].toString() evaluation !== undefined to length>0

   	@param {XML} msg - this contains the raw message (string)
	@return {object} getAssignedAuthor - contains getAssignedAuthor JS object and related properties.
*/

function getAssignedAuthor(msg) {

	var debug= 0;
	var objAssignedAuthor= new Object;

	if(debug>0) { 
		logger.error('getAssignedAuthor msg: '+msg);
		logger.error('getAssignedAuthor msg[assignedAuthor]: '+JSON.stringify(msg['assignedAuthor'])+
		' typeof: '+msg['assignedAuthor']+
		' length: '+msg['assignedAuthor'].toString().length); 
	} // end if 

	if(typeof msg['assignedAuthor'] !== 'undefined') {
		if(msg['assignedAuthor'].children().length()>0) {

			// <assignedAuthor classCode="ASSIGNED">
			// set assignedAuthor classCode
			if(typeof msg['assignedAuthor']['@classCode'] !== 'undefined') {
				if(debug>0) { logger.error('msg[assignedAuthor][@classCode]: '+msg['assignedAuthor']['@classCode'].toString()); }
				objAssignedAuthor.classCode= msg['assignedAuthor']['@classCode'].toString();
			} // end if

			// <id assigningAuthorityName="INLP_FIN" extension="INLP" root="2.16.840.1.113883.3.2139.200"/>
			// set assignedAuthor Id
			if(typeof msg['assignedAuthor']['id'] !== 'undefined') {
				// set assignedAuthor Id assigningAuthorityName
				if(typeof msg['assignedAuthor']['id']['@assigningAuthorityName'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedAuthor][id][@assigningAuthorityName]: '+msg['assignedAuthor']['id']['@assigningAuthorityName'].toString()); }
					objAssignedAuthor.assigningAuthorityName= msg['assignedAuthor']['id']['@assigningAuthorityName'].toString();
				} // end if
				// set assignedAuthor Id root
				if(typeof msg['assignedAuthor']['id']['@root'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedAuthor][id][@root]: '+msg['assignedAuthor']['id']['@root'].toString()); }
					objAssignedAuthor.root= msg['assignedAuthor']['id']['@root'].toString();
				} // end if
				// set assignedAuthor Id extension
				if(typeof msg['assignedAuthor']['id']['@extension'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedAuthor][id][@extension]: '+msg['assignedAuthor']['id']['@extension'].toString()); }
					objAssignedAuthor.extension= msg['assignedAuthor']['id']['@extension'].toString();
				} // end if
			} // end if

			// set assignedAuthor Code
			if(typeof msg['assignedAuthor']['code'] !== 'undefined') {
				// set assignedAuthor code code
				if(typeof msg['assignedAuthor']['code']['@code'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedAuthor][code][@code]: '+msg['assignedAuthor']['code']['@code'].toString()); }
					objAssignedAuthor.code= msg['assignedAuthor']['code']['@code'].toString();
				} // end if
				// set assignedAuthor code codeSystemOid
				if(typeof msg['assignedAuthor']['code']['@codeSystem'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedAuthor][code][@codeSystem]: '+msg['assignedAuthor']['code']['@codeSystem'].toString()); }
					objAssignedAuthor.codeSystem= msg['assignedAuthor']['code']['@codeSystem'].toString();
				} // end if
				// set assignedAuthor code codeSystemName
				if(typeof msg['assignedAuthor']['code']['@codeSystemName'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedAuthor][code][@codeSystemName]: '+msg['assignedAuthor']['code']['@codeSystemName'].toString()); }
					objAssignedAuthor.codeSystemName= msg['assignedAuthor']['code']['@codeSystemName'].toString();
				} // end if
				// set assignedAuthor code displayName
				if(typeof msg['assignedAuthor']['code']['@displayName'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedAuthor][code][@displayName]: '+msg['assignedAuthor']['code']['@displayName'].toString()); }
					objAssignedAuthor.codeDisplayName= msg['assignedAuthor']['code']['@displayName'].toString();
				} // end if
			} // end if


			// set assignedAuthor assignedAuthoringDevice
			/* <assignedAuthoringDevice><manufacturerModelName>Cerner Corporation</manufacturerModelName><softwareName>Millennium Clinical Document Generator</softwareName></assignedAuthoringDevice> */
			if(typeof msg['assignedAuthor']['assignedAuthoringDevice'] !== 'undefined') {
				// get manufacturer
				if(typeof msg['assignedAuthor']['assignedAuthoringDevice']['manufacturerModelName'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedAuthor][assignedAuthoringDevice][manufacturerModelName]: '+msg['assignedAuthor']['assignedAuthoringDevice']['manufacturerModelName'].toString()); }
					objAssignedAuthor.assignedAuthorizingDevice_manufacturer= msg['assignedAuthor']['assignedAuthoringDevice']['manufacturerModelName'].toString();
				} // end if 
				// get softwareName
				if(typeof msg['assignedAuthor']['assignedAuthoringDevice']['softwareName'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedAuthor][assignedAuthoringDevice][softwareName]: '+msg['assignedAuthor']['assignedAuthoringDevice']['softwareName'].toString()); }
					objAssignedAuthor.assignedAuthorizingDevice_softwareName= msg['assignedAuthor']['assignedAuthoringDevice']['softwareName'].toString();
				} // end if 
			} // end if 

			// set assignedAuthor representedOrganization
			/*  <representedOrganization classCode="ORG" determinerCode="INSTANCE"><id assigningAuthorityName="INLP_FIN" extension="INLP" root="2.16.840.1.113883.3.2139.200"/><id root="2.16.840.1.113883.3.2139"/><name>La Porte Hospital</name>
			*/

			if(debug>0) { 
				logger.error('msg[assignedAuthor][representedOrganization]: '+msg['assignedAuthor']['representedOrganization']+
					' typeof: '+typeof msg['assignedAuthor']['representedOrganization']+
					' toString: '+msg['assignedAuthor']['representedOrganization'].toString()+
					' length: '+msg['assignedAuthor']['representedOrganization'].toString().length); 
			}

			if(msg['assignedAuthor']['representedOrganization'].toString().length>0) {
				if(typeof msg['assignedAuthor']['representedOrganization']['id'] !== 'undefined') {
					// set representedOrganization Id assigningAuthorityName
					if(typeof msg['assignedAuthor']['representedOrganization']['id']['@assigningAuthorityName'] !== 'undefined') {
						if(debug>0) { logger.error('msg[assignedAuthor][representedOrganization][id][@assigningAuthorityName]: '+msg['assignedAuthor']['representedOrganization']['id']['@assigningAuthorityName'].toString()); }
						objAssignedAuthor.representedOrganization_assigningAuthorityName= msg['assignedAuthor']['representedOrganization']['id']['@assigningAuthorityName'].toString();
					} // end if
					// set representedOrganization Id extension
					if(typeof msg['assignedAuthor']['representedOrganization']['id'][0]['@extension'] !== 'undefined') {
						if(debug>0) { logger.error('msg[assignedAuthor][representedOrganization][id][@extension]: '+msg['assignedAuthor']['representedOrganization']['id'][0]['@extension'].toString()); }
						objAssignedAuthor.representedOrganization_extension= msg['assignedAuthor']['representedOrganization']['id'][0]['@extension'].toString();
					} // end if
					// set representedOrganization Id root
					if(typeof msg['assignedAuthor']['representedOrganization']['id'][0]['@root'] !== 'undefined') {
						if(debug>0) { logger.error('msg[assignedAuthor][representedOrganization][id][@root]: '+msg['assignedAuthor']['representedOrganization']['id'][0]['@root'].toString()); }
						objAssignedAuthor.representedOrganization_root= msg['assignedAuthor']['representedOrganization']['id'][0]['@root'].toString();
					} // end if
				} // end if 
			} // end if

			// check for assignedAuthor representedOrganization name child element
			if(typeof msg['assignedAuthor']['representedOrganization']['name'] !== 'undefined') {
				if(msg['assignedAuthor']['representedOrganization']['name'].toString().length>0) {
					if(debug>0) { logger.error('msg[assignedAuthor][representedOrganization][name]: '+msg['assignedAuthor']['representedOrganization']['name'].toString()); }
					objAssignedAuthor.representedOrganizationName= msg['assignedAuthor']['representedOrganization']['name'].toString();	// Walgreens Drug Store 12562
				} else if (msg['assignedAuthor']['assignedPerson']['name'].toString().length>0) {
					if(debug>0) { logger.error('msg[assignedAuthor][assignedPerson][name]: '+msg['assignedAuthor']['assignedPerson']['name'].toString()); }
					objAssignedAuthor.representedOrganizationName= msg['assignedAuthor']['assignedPerson']['name'].toString();	// Walgreens Drug Store 12562
				} // end if
			} // end if 
		} // end if
	} // end if 
	return objAssignedAuthor;
}

/** setAssignedAuthorAddress(msg)

  	- Desc: This setAssignedAuthorAddress function receives message and returns objAssignedAuthorAddress toXMLString
  	- Modified:
  	2019-11-20 22:20 CST - Under development

   	@param {msg} - this contains the Javascript message document 
	@return {XML} objAssignedAuthorAddress - contains objAssignedAuthorAddress in XML Object string.

	Reference: https://gazelle.ihe.net/XDStarClient/rimihe-templates/rimihe-Author_XDSDocumentEntry.html

*/

function getAssignedAuthorAddress(msg) {

	var debug= 0;
	var arrParticipant= new Array();
	var strFunctionName= 'getAssignedAuthorAddress';

	if(debug>0) {
		logger.error('DEBUG::'+strFunctionName+' msg[author][assignedAuthor]: '+msg['author']['assignedAuthor'].toXMLString());
		logger.error('DEBUG::'+strFunctionName+' msg[author][assignedAuthor][addr]: '+msg['author']['assignedAuthor']['addr'].toXMLString());
	} // end if 

	var objAssignedAuthorAddress= new Object;
	if(msg.author.assignedAuthor.children().length()>0) {

		if(debug>0) { 
			logger.error('DEBUG::'+strFunctionName+' AssignedAuthor.children().length: '+msg.author.assignedAuthor.children().length()+' children names: '+msg.author.assignedAuthor.children());
		} // end if 
		if((msg['author']['assignedAuthor']['addr'] !== 'undefined')&&(msg['author']['assignedAuthor']['addr'].toString().length>0)) {

			for each (child in msg.author.assignedAuthor.children()) {

				if(debug>0) { 
					logger.error('DEBUG::'+strFunctionName+' child.fullName: '+child.name()+' child.text: '+child.name+' child.localName(): '+child.localName());
				} // end if 
				if(child.localName()== 'addr') {
					if((child.toString() !== 'undefined')&&(child.toString().length>0)) {
						if(debug>0) { 
							logger.error('DEBUG::'+strFunctionName+' addr.toXMLString: '+child.toXMLString());
						} // end if 
						objAssignedAuthorAddress.addr= child.toXMLString();
					} // end if 
				} // end if 
			} // end for each
		} // end if
		channelMap.put('objAssignedAuthorAddress',objAssignedAuthorAddress);
	} // end if

} // end proc

/** getAssignedEntity(msg)

  	- Desc: This function receives CCD assignedEntity XML Object and returns JavaScript object
  	- Modified 	
  	2020-01-29 19:25 CT JER - Modified to comment out the debug statement to echo the toXMLString msg[assignedEntity][assignedPerson][name][given]
	2018-04-22 16:43 CT JER - Modified to include the storing of the id.root element.attribute for reference purposes
	2018-04-15 23:42 CT JER - Modified to include additional if no value or sizeable value handling (length()>1, length= 1 and the else) for givenName element value.
	2018-02-16 17:48 CT JER - Added the assignedPerson Name suffix and the representedOrganization Name

   	@param {XML} msg - this contains the raw message (string)
	@return {object} objAssignedEntity - contains objAssignedEntity JS object and related properties.
*/

function getAssignedEntity(msg) {

	var debug= 0;
	var objAssignedEntity= new Object;
	default xml namespace = 'urn:hl7-org:v3';
	// <given xmlns="urn:hl7-org:v3">Julius</given><given xmlns="urn:hl7-org:v3">R</given>

	if(typeof msg['assignedEntity'] !== 'undefined') {
		if(msg['assignedEntity'].children().length()>0) {

			// set assignedEntity classCode
			if(typeof msg['assignedEntity']['@classCode'] !== 'undefined') {
				if(debug>0) { logger.error('msg[assignedEntity][@classCode]: '+msg['assignedEntity']['@classCode'].toString()); }
				objAssignedEntity.classCode= msg['assignedEntity']['@classCode'].toString();
			} // end if

			// set assignedEntity Id
			if(typeof msg['assignedEntity']['id'][0] !== 'undefined') {
				// set assignedEntity Id assigningAuthorityName
				if(typeof msg['assignedEntity']['id'][0]['@assigningAuthorityName'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedEntity][id][0][@assigningAuthorityName]: '+msg['assignedEntity']['id'][0]['@assigningAuthorityName'].toString()); }
					objAssignedEntity.assigningAuthorityName= msg['assignedEntity']['id'][0]['@assigningAuthorityName'].toString();
				} // end if
				// set assignedEntity Id root
				if(typeof msg['assignedEntity']['id'][0]['@root'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedEntity][id][0][@root]: '+msg['assignedEntity']['id'][0]['@root'].toString()); }
					objAssignedEntity.root= msg['assignedEntity']['id'][0]['@root'].toString();
				} // end if
				// set assignedEntity Id extension
				if(typeof msg['assignedEntity']['id'][0]['@extension'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedEntity][id][0][@extension]: '+msg['assignedEntity']['id'][0]['@extension'].toString()); }
					objAssignedEntity.extension= msg['assignedEntity']['id'][0]['@extension'].toString();
				} // end if
			} // end if

			// set assignedEntity Code
			if(typeof msg['assignedEntity']['code'] !== 'undefined') {
				// set assignedEntity code code
				if(typeof msg['assignedEntity']['code']['@code'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedEntity][code][@code]: '+msg['assignedEntity']['code']['@code'].toString()); }
					objAssignedEntity.code= msg['assignedEntity']['code']['@code'].toString();
				} // end if
				// set assignedEntity code codeSystemOid
				if(typeof msg['assignedEntity']['code']['@codeSystem'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedEntity][code][@codeSystem]: '+msg['assignedEntity']['code']['@codeSystem'].toString()); }
					objAssignedEntity.codeSystem= msg['assignedEntity']['code']['@codeSystem'].toString();
				} // end if
				// set assignedEntity code codeSystemName
				if(typeof msg['assignedEntity']['code']['@codeSystemName'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedEntity][code][@codeSystemName]: '+msg['assignedEntity']['code']['@codeSystemName'].toString()); }
					objAssignedEntity.codeSystemName= msg['assignedEntity']['code']['@codeSystemName'].toString();
				} // end if
				// set assignedEntity code displayName
				if(typeof msg['assignedEntity']['code']['@displayName'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedEntity][code][@displayName]: '+msg['assignedEntity']['code']['@displayName'].toString()); }
					objAssignedEntity.codeDisplayName= msg['assignedEntity']['code']['@displayName'].toString();
				} // end if
			} // end if


			// set assignedEntity assignedPerson name
			if(typeof msg['assignedEntity']['assignedPerson'] !== 'undefined') {
				if(debug>0) { 
					logger.error('toXMLString msg[assignedEntity][assignedPerson][name][given]: '+msg['assignedEntity']['assignedPerson']['name']['given'].toXMLString()+
					'msg[assignedEntity][assignedPerson][name][given]: '+msg['assignedEntity']['assignedPerson']['name']['given'].toString()+
					' typeof: '+typeof msg['assignedEntity']['assignedPerson']['name']['given']+
					' length: '+msg['assignedEntity']['assignedPerson']['name']['given'].length()
					);
				}
				if(typeof msg['assignedEntity']['assignedPerson']['name'] !== 'undefined') {
					// get assignedEntity Given name	
					if((typeof msg['assignedEntity']['assignedPerson']['name']['given'] !== 'undefined')||(msg['assignedEntity']['assignedPerson']['name']['given'].toString().length>0)) {	
						if(debug>0) { 
							logger.error('msg[assignedEntity][assignedPerson][name][given]: '+msg['assignedEntity']['assignedPerson']['name']['given'][0].toString()); 
						} // end if 
						if(msg['assignedEntity']['assignedPerson']['name']['given'].length()>1) {
							objAssignedEntity.givenName= msg['assignedEntity']['assignedPerson']['name']['given'][0].toString();
						} else if(msg['assignedEntity']['assignedPerson']['name']['given'].length()== 1) {
							objAssignedEntity.givenName= msg['assignedEntity']['assignedPerson']['name']['given'].toString();
						} else {
							objAssignedEntity.givenName= '';
						} // end if 
					} // end if 
					// get assignedEntity Family name	
					if((typeof msg['assignedEntity']['assignedPerson']['name']['family'] !== 'undefined')||(msg['assignedEntity']['assignedPerson']['name']['family'].length>0)) {	
						if(debug>0) { logger.error('msg[assignedEntity][assignedPerson][name][family]: '+msg['assignedEntity']['assignedPerson']['name']['family'].toString().replace(/xmlns=\"(.*?)\"/g, '')); }
						objAssignedEntity.familyName= msg['assignedEntity']['assignedPerson']['name']['family'].toString().replace(/xmlns=\"(.*?)\"/g, '');
					} // end if
					// get assignedEntity Suffix
					if(debug>0) { logger.error('getAssignedEntity msg[assignedEntity][assignedPerson][name][suffix]: '+msg['assignedEntity']['assignedPerson']['name']['suffix']+
						' typeof: '+typeof msg['assignedEntity']['assignedPerson']['name']['suffix']+
						' length: '+msg['assignedEntity']['assignedPerson']['name']['suffix'].toString().length);
					} // end if logger.error
					if((typeof msg['assignedEntity']['assignedPerson']['name']['suffix'] !== 'undefined')||(msg['assignedEntity']['assignedPerson']['name']['suffix'].toString().length>0)) {	
						if(debug>0) { logger.error('msg[assignedEntity][assignedPerson][name][suffix]: '+msg['assignedEntity']['assignedPerson']['name']['suffix'].toString()); }
						objAssignedEntity.suffix= msg['assignedEntity']['assignedPerson']['name']['suffix'].toString();
					} // end if 
				} // end if 
			} // end if 

			// set assignedEntity representedOrganization
			if(typeof msg['assignedEntity']['representedOrganization'] !== 'undefined') {
				// check for assignedEntity representedOrganization name child element
				if(typeof msg['assignedEntity']['representedOrganization']['name'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedEntity][representedOrganization][name]: '+msg['assignedEntity']['representedOrganization']['name'].toString()); }
					objAssignedEntity.representedOrganizationName= msg['assignedEntity']['representedOrganization']['name'].toString();	// Walgreens Drug Store 12562
				} // end if

				if(typeof msg['assignedEntity']['representedOrganization']['id'] !== 'undefined') {
					if(typeof msg['assignedEntity']['representedOrganization']['id']['root'] !== 'undefined') {
						if(debug>0) { logger.error('msg[assignedEntity][representedOrganization][id][root]: '+msg['assignedEntity']['representedOrganization']['id']['root'].toString()); }
						objAssignedEntity.representedOrganizationIdRoot= msg['assignedEntity']['representedOrganization']['id']['root'].toString();	// 12562
					} // end if 
				} // end if
			} // end if
		} // end if
	} // end if 

	if(objAssignedEntity.toString().length>0) {
		channelMap.put('assignedEntity',objAssignedEntity);
	}

	return objAssignedEntity;
}

/** getAuthorPerson(object)

  	- Desc: This function receives Javascript Object and returns AuthorPerson string
  	- Modified:
	2018-02-18 10:12 CT JER - Modified to accommodate the changes to the objAssignedEntity
  	2018-02-16 10:40 CT JER - Removed the objAuthor instances reference and left the references specific to JS Rhino XPath
  	2018-02-15 16:01 CT JER - Modified included additional typeof and for each object

   	@param {object} objAuthor - this contains the Javascript Object (object)
	@return {string} strAuthorPerson - contains authorPerson string containing the required HL7.Composite.XCN datatype and related properties.

	[2018-02-18 10:11:47,629]  ERROR (transformer:?): objAuthor= ({assigningAuthorityName:"INLP_FIN", extension:"3100004118", root:"2.16.840.1.113883.3.2139.200", startTime:"20180208041500", stopTime:"20180208075000", dispositionCode:"", dispositionCodeSystemOid:"", dispositionCodeSystemName:"", dispositionDisplayName:"Home or Self Care", encounterParticipant_code:"ATND", assignedEntity_root:"2.16.840.1.113883.3.2139.300", assignedEntity_extension:"8284", assignedEntity_assigningAuthorityName:"LPS_ORG_DOC", assignedEntity_code:"", assignedEntity_codeSystem:"", assignedEntity_codeSystemName:"", assignedEntity_codeDisplayName:"", assignedEntity_givenName:"Robert", assignedEntity_familyName:"Riggs"})

*/

function getAuthorPerson(objAuthor) {

	var debug= 0;
	var cntObject= 0;
	var strAuthorPerson= '';
	if(debug>0) { 
		logger.error("getAuthorPerson objAuthor= "+objAuthor.toSource()); 
		// logger.error("objAuthor JSON= "+JSON.stringify(objAuthor, null, 4));
	}

	if(typeof objAuthor !== 'undefined') {	

		// replaced the assignedId with assignedEntity_extension
		if(typeof objAuthor.assignedEntity_extension !== 'undefined') {
			if(objAuthor.assignedEntity_extension.toString().length>0) {
				if(debug>0) { logger.error('objAuthor.assignedEntity_extension: '+objAuthor.assignedEntity_extension.toString()); }
				strAuthorPerson += objAuthor.assignedEntity_extension.toString();
			} // end if 
		} // end if 


		// replaced the assignedPersonLastName with assignedEntity_familyName
		if(typeof objAuthor.assignedEntity_familyName !== 'undefined') {
			if(debug>0) { logger.error('objAuthor.assignedEntity_familyName: '+objAuthor.assignedEntity_familyName.toString()); }
			if(objAuthor.assignedEntity_familyName.toString().length>0) {
				strAuthorPerson += "^"+objAuthor.assignedEntity_familyName.toString();
			} else {
				strAuthorPerson += "^";
			} // end if 
		} // end if 
		// replaced the assignedPersonFirstName with assignedEntity_givenName
		if(typeof objAuthor.assignedEntity_givenName !== 'undefined') {
			// replace(/xmlns=\"(.*?)\"/g, '') to remove namespaces
			// objAuthor.assignedEntity_givenName.toString().replace(/xmlns=\"(.*?)\"/g, '')); }
			if(debug>0) { 
				logger.error('objAuthor.assignedEntity_givenName: '+objAuthor.assignedEntity_givenName.toString());
			} // end if 
			if(objAuthor.assignedEntity_givenName.toString().length>0) {
				// strAuthorPerson += "^"+objAuthor.assignedEntity_givenName.toString().replace(/xmlns=\"(.*?)\"/g, '');
				strAuthorPerson += "^"+objAuthor.assignedEntity_givenName;
			} else {
				strAuthorPerson += "^";
			} // end if 
		} // end if


		// replace the assignedPersonSuffix with assignedEntity_suffix
		if(typeof objAuthor.assignedEntity_suffix !== 'undefined') {
			if(debug>0) { logger.error('objAuthor.assignedEntity_suffix: '+objAuthor.assignedEntity_suffix.toString()); }
			if(objAuthor.assignedEntity_suffix.toString().length>0) {
				strAuthorPerson += "^^"+objAuthor.assignedEntity_suffix.toString();
			} else {
				strAuthorPerson += "^^";
			} // end if 
		} // end if 
		// replace the assignedRoot with assignedEntity_root
		if(typeof objAuthor.assignedEntity_root !== 'undefined') {
			if(debug>0) { logger.error('objAuthor.assignedEntity_root: '+objAuthor.assignedEntity_root.toString()); }
			if (objAuthor.assignedEntity_root.toString().length>0) {
				strAuthorPerson += "^^^^^&amp;"+objAuthor.assignedEntity_root.toString()+"&amp;ISO";
			} else {
				strAuthorPerson += "^^^^^";
			} // end if
		} // end if
	} // end if 

	if(debug>0) { logger.error("getAuthorPerson.strAuthorPerson: "+strAuthorPerson); }
	return strAuthorPerson;
}

/** getAuthors(msg)

	- Desc: This function receives CCD msg and returns an array of Author objects 
	- Modified
	2018-02-18 13:38 CT JER - Modified to accommodate the changes to call the objAssignedAuthor function
	2018-02-10 11:32 CT JER - Verified per the HL7 Rim model 1..* assignedAuthors elements (1..* more authors) list objects persist to arrAuthors.
	2018-02-08 03:58 CT JER - Modified for additional handling verify typeof !== 'undefined' and two conditionals to set authorInstitution property

	@param {String} msg - Raw msg
	@return {Array} return array of Author objects
*/
function getAuthors(msg) {

	var debug= 0;
	// uncertain if more than one assignedAuthor per ClinicalDocument
	if(debug>0) { logger.error('getAuthor of msg[author]: '+msg['author']); }

	var objAuthor= new Object;			
	var objAssignedAuthor= getAssignedAuthor(msg['author']);

	if(typeof objAssignedAuthor !== 'undefined') {

		// get assigningAuthorityName
		if((typeof objAssignedAuthor.assigningAuthorityName !== 'undefined')&&(objAssignedAuthor.assigningAuthorityName.toString().length>0)) {
			if(debug>0) { logger.error('msg[author][assignedAuthor][id][@assigningAuthorityName]: '+objAssignedAuthor.assigningAuthorityName.toString()); }
			objAuthor.IdAssigningAuthorityName= objAssignedAuthor.assigningAuthorityName.toString();
		} // end if
		// get root
		if((typeof objAssignedAuthor.root !== 'undefined')&&(objAssignedAuthor.root.toString().length>0)) {
			if(debug>0) { logger.error('msg[author][assignedAuthor][id][@root]: '+objAssignedAuthor.root.toString()); }
			objAuthor.Idroot= objAssignedAuthor.root.toString();
		} // end if
		// get extension
		if((typeof objAssignedAuthor.extension !== 'undefined')&&(objAssignedAuthor.extension.toString().length>0)) {
			if(debug>0) { logger.error('msg[author][assignedAuthor][id][@extension]: '+objAssignedAuthor.extension.toString()); }
			objAuthor.Idextension= objAssignedAuthor.extension.toString();
		} // end if			

		// get assignedAuthorizingDevice manufacturer -> set manufacturer
		if((typeof objAssignedAuthor.assignedAuthorizingDevice_manufacturer !== 'undefined')&&(objAssignedAuthor.assignedAuthorizingDevice_manufacturer.toString().length>0)) {
			if(debug>0) { logger.error('msg[author][assignedAuthor][assignedAuthoringDevice][manufacturerModelName]: '+objAssignedAuthor.assignedAuthorizingDevice_manufacturer.toString()); }
			objAuthor.authorizingDevice= objAssignedAuthor.assignedAuthorizingDevice_manufacturer.toString();
		} // end if			

		// get assignedAuthorizingDevice softwareName -> set softwareName
		if((typeof objAssignedAuthor.assignedAuthorizingDevice_softwareName !== 'undefined')&&(objAssignedAuthor.assignedAuthorizingDevice_softwareName.toString().length>0)) {
			if(debug>0) { logger.error('msg[author][assignedAuthor][assignedAuthoringDevice][softwareName]: '+objAssignedAuthor.assignedAuthorizingDevice_softwareName.toString()); }
			objAuthor.softwareName= objAssignedAuthor.assignedAuthorizingDevice_softwareName.toString();
		} // end if	

		// get objAssignedAuthor representedOrganization_assigningAuthorityName -> set assigningAuthorityName
		if((typeof objAssignedAuthor.representedOrganization_assigningAuthorityName !== 'undefined')&&(objAssignedAuthor.representedOrganization_assigningAuthorityName.toString().length>0)) {
			if(debug>0) { logger.error('msg[author][assignedAuthor][representedOrganization][id][assigingAuthorityName]: '+objAssignedAuthor.representedOrganization_assigningAuthorityName.toString()); }
			objAuthor.representedOrganization_assigingAuthorityName= objAssignedAuthor.representedOrganization_assigningAuthorityName.toString();
		} // end if				

		// get objAssignedAuthor representedOrganization_extension -> set extension
		if((typeof objAssignedAuthor.representedOrganization_extension !== 'undefined')&&(objAssignedAuthor.representedOrganization_extension.toString().length>0)) {
			if(debug>0) { logger.error('msg[author][assignedAuthor][representedOrganization][id][extension]: '+objAssignedAuthor.representedOrganization_extension.toString()); }
			objAuthor.representedOrganization_extension= objAssignedAuthor.representedOrganization_extension.toString();
		} // end if	

		// get objAssignedAuthor representedOrganization_root -> set root
		if((typeof objAssignedAuthor.representedOrganization_root !== 'undefined')&&(objAssignedAuthor.representedOrganization_root.toString().length>0)) {
			if(debug>0) { logger.error('msg[author][assignedAuthor][representedOrganization][id][root]: '+objAssignedAuthor.representedOrganization_root.toString()); }
			objAuthor.representedOrganization_root= objAssignedAuthor.representedOrganization_root.toString();
		} // end if

		// get objAssignedAuthor.representedOrganizationName -> set representedOrganizationName
		if((typeof objAssignedAuthor.representedOrganizationName !== 'undefined')&&(objAssignedAuthor.representedOrganizationName.toString().length>0)) {
			if(debug>0) { logger.error('msg[author][assignedAuthor][representedOrganization][name]: '+objAssignedAuthor.representedOrganizationName.toString()); }
			objAuthor.authorInstitution= objAssignedAuthor.representedOrganizationName.toString();
		} // end if
	} // end if 

	return objAuthor;
}

/** getCCDASectionMap(code)

  	- Desc: This function receives the templateId code and returns the mapped CCDA Section titlename

   	@param {String} msg - this contains the raw message (string)
	@return {object} objPatientRole - this contains the patientRole elements composed into a single objPatientRole object.

	History
*/
function getCCDASectionMap(code) {

	var debug= 0;
	var scriptName= 'getCCDASectionMap';

	if(code== "2.16.840.1.113883.10.20.22.2.22") return 'Encounter Details';
	if(code== "2.16.840.1.113883.10.20.22.2.6")  return 'Allergies';
	if(code== "2.16.840.1.113883.10.20.22.2.1")  return 'Medications';
	if(code== "2.16.840.1.113883.10.20.22.2.5")  return 'Active Problems';
	if(code== "2.16.840.1.113883.10.20.22.2.2")  return 'Immunizations';
	if(code== "2.16.840.1.113883.10.20.22.2.17") return 'Social History';
	if(code== "2.16.840.1.113883.10.20.22.2.4")  return 'Vital Signs';
	if(code== "1.3.6.1.4.1.19376.1.5.3.1.3.4")   return 'Progress Notes';
	if(code== "2.16.840.1.113883.10.20.22.2.65") return 'Miscellaneous Notes';
	if(code== "2.16.840.1.113883.10.20.22.2.10") return 'Plan of Treatment'; 
	if(code== "2.16.840.1.113883.10.20.22.2.7")  return 'Procedures';
	if(code== "2.16.840.1.113883.10.20.22.2.3")  return 'Results';
	if(code== "2.16.840.1.113883.10.20.22.2.8")  return 'Visit Diagnoses';
	if(code== "2.16.840.1.113883.10.20.22.2.18") return 'Insurance';
}

/** getClinicalDocumentInfo(msg)

  	- Desc: This function receives CCD msg and returns clinicalDocumentInfo object
  	- Modified:
 	2018-02-10 11:20 CT JER - Modified to include capturing the SetId and versionNumber attributes

   	@param {String} msg - this contains the raw message (string)
	@return {object} objClinDocInfo - this contains a clinical document info element specific values composed into a single objClinDocInfo object.
	History 
	2022-08-23 JER - Added channelMap to capture the CCDA Document Header Information
	2021-09-20 JER - Modified to incorporate logger.error prior to returning function value
*/
function getClinicalDocumentInfo(msg) {
	var scriptName= 'getClinicalDocumentInfo';
	var debug= 0;
	if(debug>0) { logger.error('DEBUG::'+scriptName+' function BEGIN msg: '+msg); }

		var objClinDocInfo= new Object();

		objClinDocInfo.extension=				msg['id']['@extension'].toString();
		objClinDocInfo.root=					msg['id']['@root'].toString();	
		objClinDocInfo.assigningAuthorityName=		msg['id']['@assigningAuthorityName'].toString();
		objClinDocInfo.code=					msg['code']['@code'].toString();
		objClinDocInfo.codeSystemOid=				msg['code']['@codeSystem'].toString();
		objClinDocInfo.codeSystemName=			msg['code']['@codeSystemName'].toString();
		objClinDocInfo.displayName=				msg['code']['@displayName'].toString();
		objClinDocInfo.title=					msg['title'].toString();
		objClinDocInfo.creationTime=				msg['effectiveTime']['@value'].toString().substring(0,14);
		objClinDocInfo.confCode=					msg['confidentialityCode']['@code'].toString();
		objClinDocInfo.confCodeSystemOid=			msg['confidentialityCode']['@codeSystem'].toString();
		objClinDocInfo.languageCode=				msg['languageCode']['@code'].toString();
		objClinDocInfo.setIdExtension=			msg['setId']['@extension'].toString();
		objClinDocInfo.setIdRoot=				msg['setId']['@root'].toString();	
		objClinDocInfo.setIdAssigningAuthorityName=	msg['setId']['@assigningAuthorityName'].toString();
		objClinDocInfo.documentVersion=			msg['versionNumber']['@value'].toString();

		// 2022-08-23 JER - Added channelMap to capture the CCDA Document Header Information
		if(debug>0) { logger.error('DEBUG::'+scriptName+' function objClinDocInfo: '+JSON.stringify(objClinDocInfo)); }
		channelMap.put('ClinDocInfo',JSON.stringify(objClinDocInfo));

		return objClinDocInfo;

}

/** getCustodian(msg)

	- Desc: This function receives CCD msg and returns an array of custodian objects 
	- Modified
	2018-02-11 17:49 CT JER - Newly created to store the custodian objects

	@param {String} msg - Raw msg
	@return {Array} return array of custodian objects
*/
function getCustodian(msg) {

	var debug= 0;
	var objCounter= 1;
	var arrCustodian= new Array();

	// uncertain if more than one assignedCustodians per ClinicalDocument
	for each(custodian in msg['custodian'][0]) {
		var objCustodian= new Object;
		objCustodian.id= objCounter;
		if(custodian.children().length()>0) {
			if(debug>0) { logger.error('custodian children()= '+custodian.children()); }
			for each(child in custodian.children()) {
				// each assignedCustodian
				if(child.localName()== 'assignedCustodian') {
					// <id assigningAuthorityName="HMS HOSPITAL NUMBER" extension="134" root="2.16.840.1.113883.3.1579.834848495152.134.500"/>
					if(typeof child.representedCustodianOrganization.id !== 'undefined') {
						objCustodian.representedOrganization_AssignAuthorityName=	child.representedCustodianOrganization.id.@assigningAuthorityName.toString();
						objCustodian.representedOrganization_Extension=  			child.representedCustodianOrganization.id.@extension.toString();
						objCustodian.representedOrganization_Root=  				child.representedCustodianOrganization.id.@root.toString();
					} // end if
					// e.g <name>HILL REGIONAL HOSPITAL</name>
					if(typeof child.representedOrganziation.name !== 'undefined') {
						objCustodian.representedOrganization=					child.representedCustodianOrganization.name.toString();
					}
					// <telecom use="WP" value="tel:(254)580-8500"/>
					if(typeof child.representedOrganziation.telecom !== 'undefined') {
						objCustodian.representedOrganization_telecom=			child.representedCustodianOrganization.telecom.@value.toString();
					}
					// <addr use="WP"><streetAddressLine>101 CIRCLE DRIVE</streetAddressLine><city>HILLSBORO</city><state>TX</state><postalCode>766450000</postalCode></addr>
					if(typeof child.representedOrganziation.addr !== 'undefined') {
						objCustodian.representedOrganization_streetAddress=				child.representedCustodianOrganization.addr.streetAddressLine.toString();
						objCustodian.representedOrganization_city=						child.representedCustodianOrganization.addr.city.toString();
						objCustodian.representedOrganization_state=						child.representedCustodianOrganization.addr.state.toString();
						objCustodian.representedOrganization_postalCode=					child.representedCustodianOrganization.addr.postalCode.toString();
					} // end if
				}
			} // end for each
		} // end if 
		objCounter++;
	} // end for each
	arrCustodian.push(objCustodian);

	return arrCustodian;
}

/** getEncounter(msg)

  	- Desc: This function receives CCD msg and returns Encounter object
  	- Modified
  	2018-02-18 20:23 CT JER - Modified to include the addition of healthcareFacility Location Name - use for HealthcareFacilityTypeCode displayName
  	2018-02-16 17:33 CT JER - Added association to the getAssignedEntity to retrieve the assignedEntity related elements and attributes
  	2018-02-16 15:55 CT JER - Added handling for DischargeDisposition elements and attributes
  	2018-02-11 17:34 CT JER - Modified startTime and stopTime by applying the substring(0,14) function to remove the datetime offset
  	2018-02-10 11:27 CT JER - Corrected the setting of the objEncounter extension, root, startTime and stopTime


   	@param {String} msg - this contains the raw message (string)
	@return {object} objEncounter - contains objEncounter object and related properties.
*/

function getEncounter(msg) {

var debug= 0;

	if(typeof msg['componentOf']['encompassingEncounter'] !== 'undefined') {
		if(msg['componentOf']['encompassingEncounter'].children().length()>0) {
			var objEncounter= new Object;

			// <id assigningAuthorityName="HMS PATIENT ACCOUNT NUMBER" extension="3480487" root="2.16.840.1.113883.3.1579.834848495152.134.300"/>
			if(typeof msg['componentOf']['encompassingEncounter']['id'] !== 'undefined') {
				if(typeof msg['componentOf']['encompassingEncounter']['id']['@assigningAuthorityName'] !== 'undefined') {
					if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][id][@assigningAuthorityName]: '+msg['componentOf']['encompassingEncounter']['id']['@assigningAuthorityName'].toString()); }
					objEncounter.assigningAuthorityName= msg['componentOf']['encompassingEncounter']['id']['@assigningAuthorityName'].toString();
				} // end if
				if(typeof msg['componentOf']['encompassingEncounter']['id']['@extension'] !== 'undefined') {
					if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][id][@extension]: '+msg['componentOf']['encompassingEncounter']['id']['@extension'].toString()); }
					objEncounter.extension= msg['componentOf']['encompassingEncounter']['id']['@extension'].toString();
				} // end if
				if(typeof msg['componentOf']['encompassingEncounter']['id']['@root'] !== 'undefined') {
					if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][id][@root]: '+msg['componentOf']['encompassingEncounter']['id']['@root'].toString()); }
					objEncounter.root= msg['componentOf']['encompassingEncounter']['id']['@root'].toString();
				} // end if
			} // end if

			// <effectiveTime><low value="20180203075200-0600"/><high value="20180203075200-0600"/></effectiveTime>
			if(typeof msg['componentOf']['encompassingEncounter']['effectiveTime'] !== 'undefined') {
				if(typeof msg['componentOf']['encompassingEncounter']['effectiveTime']['low']['@value'] !== 'undefined') {
					if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][effectiveTime][low][@value]: '+msg['componentOf']['encompassingEncounter']['effectiveTime']['low']['@value'].toString()); }
					objEncounter.startTime= msg['componentOf']['encompassingEncounter']['effectiveTime']['low']['@value'].toString().substring(0,14);
				} // end if
				if(typeof msg['componentOf']['encompassingEncounter']['effectiveTime']['high']['@value'] !== 'undefined') {
					if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][effectiveTime][high][@value]: '+msg['componentOf']['encompassingEncounter']['effectiveTime']['high']['@value'].toString()); }
					objEncounter.stopTime= msg['componentOf']['encompassingEncounter']['effectiveTime']['high']['@value'].toString().substring(0,14);
				} // end if
			} // end if

			//  <dischargeDispositionCode code="1" codeSystem="2.16.840.1.113883.12.112" codeSystemName="HL7 Discharge Disposition" displayName="DISCHARGED HOME/SELF CARE"/>
			if(typeof msg['componentOf']['encompassingEncounter']['dischargeDispositionCode'] !== 'undefined') {
				if(typeof msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@code'] !== 'undefined') {
					// get DischargeDisposition Code
					if(typeof msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@code'] !== 'undefined') {						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][dischargeDispositionCode][@code]: '+msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@code'].toString()); }
						objEncounter.dispositionCode= msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@code'].toString();
					} // end if
					// get DischargeDisposition CodeSystemOid
					if(typeof msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@codeSystem'] !== 'undefined') {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][dischargeDispositionCode][@codeSystem]: '+msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@codeSystem'].toString()); }
						objEncounter.dispositionCodeSystemOid= msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@codeSystem'].toString();
					} // end if
					// get DischargeDisposition CodeSystemName
					if(typeof msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@codeSystemName'] !== 'undefined') {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][dischargeDispositionCode][@codeSystemName]: '+msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@codeSystemName'].toString()); }						
						objEncounter.dispositionCodeSystemName= msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@codeSystemName'].toString();
					} // end if
					// get DischargeDisposition DisplayName
					if(typeof msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@displayName'] !== 'undefined') {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][dischargeDispositionCode][@displayName]: '+msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@displayName'].toString()); }
						objEncounter.dispositionDisplayName= msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@displayName'].toString();
					} // end if
				} // end if 
				// get DischargeDisposition originalText
				if(typeof msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['originalText'] !== 'undefined') {
					if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][dischargeDispositionCode][originalText]: '+msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['originalText'].toString()); }
					objEncounter.dispositionDisplayName= msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['originalText'].toString();
				} // end if 
			} // end if 	

			// get encounterParticipant assignedEntity
			if(typeof msg['componentOf']['encompassingEncounter']['encounterParticipant'] !== 'undefined') {

				if(typeof msg['componentOf']['encompassingEncounter']['encounterParticipant']['@typeCode'] !== 'undefined') {
					if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][encounterParticipant][@typeCode]: '+msg['componentOf']['encompassingEncounter']['encounterParticipant']['@typeCode'].toString()); }
					objEncounter.encounterParticipant_code= msg['componentOf']['encompassingEncounter']['encounterParticipant']['@typeCode'].toString();
				} // end if

				// call getAssignedEntity
				var objAssignedEntity= getAssignedEntity(msg['componentOf']['encompassingEncounter']['encounterParticipant']);
				if(objAssignedEntity !== 'undefined') {

					// get assignedEntity Root
					if(objAssignedEntity.root != null) {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][encounterParticipant][assignedEntity][id][@root]: '+objAssignedEntity.root.toString()); }
						objEncounter.assignedEntity_root= objAssignedEntity.root.toString();
					} // end if
					// get assignedEntity Extension
					if(objAssignedEntity.extension != null) {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][encounterParticipant][assignedEntity][id][@extension]: '+objAssignedEntity.extension.toString()); }
						objEncounter.assignedEntity_extension= objAssignedEntity.extension.toString();
					} // end if
					// get assignedEntity AssigningAuthorityName
					if(objAssignedEntity.assigningAuthorityName != null) {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][encounterParticipant][assignedEntity][id][@assigningAuthorityName]: '+objAssignedEntity.assigningAuthorityName.toString()); }
						objEncounter.assignedEntity_assigningAuthorityName= objAssignedEntity.assigningAuthorityName.toString();
					} // end if

					// get assignedEntity code
					if(objAssignedEntity.code != null) {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][encounterParticipant][assignedEntity][code][@code]: '+objAssignedEntity.code.toString()); }
						objEncounter.assignedEntity_code= objAssignedEntity.code.toString();
					} // end if
					// get assignedEntity codeSystem
					if(objAssignedEntity.codeSystem != null) {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][encounterParticipant][assignedEntity][code][@codeSystem]: '+objAssignedEntity.codeSystem.toString()); }
						objEncounter.assignedEntity_codeSystem= objAssignedEntity.codeSystem.toString();
					} // end if
					// get assignedEntity codeSystemName
					if(objAssignedEntity.codeSystemName != null) {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][encounterParticipant][assignedEntity][code][@codeSystemName]: '+objAssignedEntity.codeSystemName.toString()); }
						objEncounter.assignedEntity_codeSystemName= objAssignedEntity.codeSystemName.toString();
					} // end if
					// get assignedEntity displayName
					if(objAssignedEntity.codeDisplayName != null) {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][encounterParticipant][assignedEntity][code][@displayName]: '+objAssignedEntity.codeDisplayName.toString()); }
						objEncounter.assignedEntity_codeDisplayName= objAssignedEntity.codeDisplayName.toString();
					} // end if

					// get assignedEntity Author GivenName
					if(objAssignedEntity.givenName != null) {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][encounterParticipant][assignedEntity][assignedPerson][name][@given]: '+objAssignedEntity.givenName.toString()); }
						objEncounter.assignedEntity_givenName= objAssignedEntity.givenName.toString();
					} // end if
					// get assignedEntity Author FamilyName
					if(objAssignedEntity.familyName != null) {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][encounterParticipant][assignedEntity][assignedPerson][name][@family]: '+objAssignedEntity.familyName.toString()); }
						objEncounter.assignedEntity_familyName= objAssignedEntity.familyName.toString();
					} // end if					
				} // end if 
			} // end if 

			// get HealthcareFacilityTypeCode
			if(typeof msg['componentOf']['encompassingEncounter']['location']['healthCareFacility'] !== 'undefined') {

				// set healthcareFacility classCode
				if(typeof msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['@classCode'] !== 'undefined') {
					if((typeof msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['@classCode'] != null)&&(msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['@classCode'].toString().length>0)) {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][location][healthcareFacility][@classCode].toString(): '+msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['@classCode'].toString()); }
						objEncounter.healthcareFacility_classCode= msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['@classCode'].toString();
					} // end if 
				} // end if

				// set healthcareFacility Location 
				if(typeof msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['location'] !== 'undefined') {

					// set healthcareFacility Location classCode
					if((typeof msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['location']['@classCode'] != null)&&(msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['location']['@classCode'].toString().length>0)) {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][location][healthcareFacility][location][@classCode].toString(): '+msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['location']['@classCode'].toString()); }
						objEncounter.healthcareFacilityLocation_classCode= msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['location']['@classCode'].toString();
					} // end if 

					// set healthcareFacility Location Name - use for HealthcareFacilityTypeCode displayName
					if(typeof msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['location']['name'] !== 'undefined') {
						if((typeof msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['location']['name'] != null)&&(msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['location']['name'].toString().length>0)) {
							if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][location][healthcareFacility][location][name].toString(): '+msg.componentOf.encompassingEncounter.location.healthcareFacility.location.name.toString()); }
							objEncounter.healthcareFacilityLocation_name= msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['location']['name'].toString();
							channelMap.put(healthcareFacilityDisplayName,objEncounter.healthcareFacilityLocation_name);
						} // end if 
					} // end if
				} // end if 
			} // end if 
		} // end if 
	} // end if

	return objEncounter;
}

/**  getEncounterDetails(object) - Desc: This function evaluates the Encounter Details Section of the CCDA to create arrObservations array of objObservations objects 

   	@param {String} msg - this contains the raw message (string)
	@return {object} objPatientRole - this contains the patientRole elements composed into a single objPatientRole object.

	History
	2021-09-20 JER - Modified to incorporate evaluation of multiple patientRole Id(s) in the case there are multiple patient identifiers
	2021-09-20 JER - Modified to patient.name incorporate evaluation of multiple patient name child elements in the case there are multiple patient identifiers
*/
function getEncounterDetails(section) {

	// define variables
	var debug= 0;
	var scriptName= 'getEncounterDetails';
	var arrObservations= [];

	// if(debug>0) { 
		logger.error('DEBUG::'+scriptName+' typeof(section)= '+typeof(section)+' section= '+section); 
	// }

	logger.error('DEBUG::'+scriptName+' A'+'\n');

	if (typeof(section['entry']['encounter']['entryRelationship']['act'] !== 'undefined')) {

		logger.error('DEBUG::'+scriptName+' B act.children()= '+section['entry']['encounter']['entryRelationship']['act'].children()+'\n');

		var cnt= 0;
		for(child in section['entry']['encounter']['entryRelationship']['act'].children()) {

			logger.error('DEBUG::'+scriptName+' C'+'\n');

			if(child.localName()== 'entryRelationship') {
				if (child['@typeCode'].toString() == 'SUBJ') {

					logger.error('DEBUG::'+scriptName+' D'+'\n');
					var objObservation= new Object();
					if(debug>0) logger.error('DEBUG::'+scriptName+': entryRelationship.act= '+entryRelationship['@typeCode'].toString()+' of entryRelationship= '+entryRelationship);
					objObservation.id= cnt;
					objObservation.typeCode= 'SUBJ';

					cnt++;
					logger.error('DEBUG::'+scriptName+': objObservation= '+objObservation);
					arrObservations.push(objObservation);
				}
			}
		}
	}

	channelMap.put('arrObservations',arrObservations);
	return arrObservations;
}

/**  getEthnicGroupCode(ethnicity) - Desc: This function receives CCD/CCDA msg and returns from getEthnicGroupCode Object

   	@param {String} msg - this contains the raw message (string)
	@return {object} objEthnicity - this contains the elements composed into a single objEthnicity object.

	History
	2022-08-21 JER - Handling of nullFlavor property with UNK value
	2022-08-17 JER - Modifying arrEthnicity from array to objEthnicity as a singular object
	2021-09-20 JER - leveraging the getTelecom for getEthnicGroupCode script
*/
function getEthnicGroupCode(ethnicity) {

	// define variables
	var debug= 0;
	var scriptName= 'getEthnicGroupCode';
	// var arrEthnicity= new Array();

	if(debug>0) { logger.error(scriptName+' ethnicity= '+ethnicity); }
	if (typeof(ethnicity) !== 'undefined') {

		if(debug>0) { 
			logger.error('DEBUG::'+scriptName+' object.length= '+ethnicity.length+' value= '+ethnicity);
		}

		var j= 0;
		for(var i= 0;i<ethnicity.length();i++) {

			if(debug>0) { 
				logger.error('DEBUG::'+scriptName+' typeof(ethnicity['+i+'][@code])= '+typeof(ethnicity[i]['@code'])+' ethnicity[@code]= '+ethnicity[i]['@code']);
			}

			var objEthnicity= new Object();
			// 2022-08-19 JER - Added evaluation of code if undefined or has toString gt zero (0) 
			if((typeof(ethnicity[i]['@code'])!== 'undefined')&&(ethnicity[i]['@code'].toString().length>0)) {

				if(debug> 0) { logger.error('DEBUG::'+scriptName+' ethnicity['+i+'][@code]: '+ethnicity[i]['@code'].toString()); }

				objEthnicity.id= j;
				objEthnicity.code= ethnicity[i]['@code'].toString();
				objEthnicity.codeSystem= ethnicity[i]['@codeSystem'].toString();
				objEthnicity.codeSystemName= ethnicity[i]['@codeSystemName'].toString();
				objEthnicity.displayName= ethnicity[i]['@displayName'].toString();

			} else {
				logger.error('B');
				if(debug> 0) { logger.error('DEBUG::'+scriptName+' ethnicity['+i+'][@code]: '+ethnicity[i]['@code'].toString()); }

				objEthnicity.id= j;
				objEthnicity.code= 'LA4489-6';
				objEthnicity.codeSystem= '1.3.6.1.4.1.12009.10.1.1817';
				objEthnicity.codeSystemName= 'LOINC UCUM Codes for Electronic Messaging'
				objEthnicity.displayName= 'Unknown'

				if(debug>0) {
					logger.error('DEBUG::'+scriptName+' function objEthnicity: '+JSON.stringify(objEthnicity));
				}
			} // end if 

			if(debug>0) { 
				logger.error('DEBUG::'+scriptName+' function objEthnicity: '+JSON.stringify(objEthnicity)); 
			}
			j++;
		} // for each address element 

		//2022-08-17 JER - Modifying arrEthnicity from array to objEthnicity as a singular object
		// channelMap.put('arrEthnicity',JSON.stringify(arrEthnicity));
		// return arrEthnicity;
		if(objEthnicity) {
			return objEthnicity;
		} // end if 
	} else {
		return;
	} // end if 
}

/**  getLanguageCommunication(language) - Desc: This function receives CCD/CCDA msg and returns from getLanguageCommunication Object

   	@param {String} msg - this contains the raw message (string)
	@return {object} objLanguage - this contains the elements composed into a single objLanguage object.

	History
	2022-08-17 JER - Modifying arrLanguage from array to objLanguage as a singular object
	2021-09-20 JER - leveraging the getTelecom for getLanguageCommunication script
*/
function getLanguageCommunication(language) {

	// define variables
	var debug= 0;
	var scriptName= 'getLanguageCommunication';
	// var arrLanguage= new Array();

	if(debug>0) { logger.error(scriptName+' language= '+language); }
	if (typeof(language) !== 'undefined') {

		if(debug>0) { logger.error('DEBUG::'+scriptName+' object.length= '+language.length()+' value= '+language);	}			

		if(language['languageCode'].length()>0) {
			// for each language element
			var j= 0;
			for(var i= 0;i<language['languageCode'].length();i++) {

				var objLanguage= new Object();
				// objLanguage.id= j;
				if(language['languageCode'][i]['@code'].toString().length>0) {

					if(debug> 0) { logger.error('DEBUG::'+scriptName+' language[languageCode]['+i+'][@code]: '+language['languageCode'][i]['@code'].toString()); }

					objLanguage.code= language['languageCode'][i]['@code'].toString();
					objLanguage.codeSystem= language['languageCode'][i]['@codeSystem'].toString();
					objLanguage.codeSystemName= language['languageCode'][i]['@codeSystemName'].toString();
					objLanguage.displayName= language['languageCode'][i]['@displayName'].toString();
				} // end if 

				// 2022-08-17 JER - Modifying arrLanguage from array to objLanguage as a singular object
				// arrLanguage.push(objLanguage);

				//} // for each child element
				if(debug>0) { logger.error('DEBUG::'+scriptName+' function objLanguage: '+JSON.stringify(objLanguage)); }
				j++;
			} // for each address element 
		} // end if 

		// 2022-08-17 JER - Modifying arrLanguage from array to objLanguage as a singular object
		// channelMap.put('arrLanguage',JSON.stringify(arrLanguage));
		// return arrLanguage;
		return objLanguage;
	} else {
		return;
	} // end if 
}

/** getParticipant(msg)

  	- Desc: This function receives CCD msg and returns Participant objects
  	- Modified
	2019-11-21 CT JER - Copy of the getPerformer function

   	@param {String} msg - this contains the raw message (string)
	@return {array} arrParticipant - contains arrParticipant array contains objParticipant objects and related properties.
*/

function getParticipant(msg) {

	var debug= 0;
	var arrParticipant= new Array();

	if(debug>0) {
		logger.error('DEBUG::'+strFunctionName+' msg[component][structuredBody][component][section][entry]..*[participant]: '+msg['component']['structuredBody']['component']['section']['entry']..*['participant'].toXMLString()); 
		logger.error('DEBUG::'+strFunctionName+' msg[component][structuredBody][component][section][entry]..*[participant][@addr]: '+msg['component']['structuredBody']['component']['section']['entry']..*['participant']['@addr'].toXMLString()); 
	} // end if 

	for each (participant in msg[component][structuredBody][component][section][entry]..*[participant]) {

		if((participant !== 'undefined')&&(participant.toString().length()>0)) {
			var strPartCount= 0;
			if(debug>0) { logger.error('participant['+strPartCount+']: '+participant); }

			var objParticipant= new Object;
			if(participant.children().length()>0) {

					if(debug>0) { logger.error('Participant.children().length: '+participant.children().length()+' children names: '+participant.children()); }
					// str = str.replace(/xmlns=\"(.*?)\"/g, '');

					if(participant['addr'] != null) {
						// check for functionCode element and it's attributes
						if(typeof participant['addr'] !== 'undefined') {
							// check for functionCode code attribute
							if(participant['addr'].toString().length>0) {
								// get Participant Address
								if(debug>0) { logger.error('participant[addr].toString(): '+participant['addr'].toXMLString()); }
								objParticipant.addr= participant['addr'].toString();				// PP
							} // end if 
						} // end if 

					} // end if 

				} // end if
			//} // end if 
			arrParticipant.push(objParticipant);
			strPartCount++;
		} // end for each
	} // end if
	channelMap.put('objParticipant',arrParticipant);
	return arrParticipant;
}

/** getPatientRole(msg)

  	- Desc: This function receives CCD msg and returns from patientRole Object

   	@param {String} msg - this contains the raw message (string)
	@return {object} objPatientRole - this contains the patientRole elements composed into a single objPatientRole object.

	History
	2022-08-16 JER - Removed this code from the getPatientRole and add to the channel transformer separate these values from the patientRole object
	2022-08-14 JER - Modified/Add the patient/raceCode, patient/ethnicGroupCode and patient/languageCommunication coded element
	2022-07-28 JER - Modified to evaluate multiple name.given elements with the first as firstName and secondary elements as middleNames
	2021-09-20 JER - Modified to incorporate evaluation of multiple patientRole Id(s) in the case there are multiple patient identifiers
	2021-09-20 JER - Modified to patient.name incorporate evaluation of multiple patient name child elements in the case there are multiple patient identifiers
*/
function getPatientRole(msg) {

	// define variables
	var debug= 0;
	var scriptName= 'getPatientRole';
	var arrPatientIds= new Array();
	default xml namespace = new Namespace('urn:hl7-org:v3');

	if(debug>0) { logger.error(scriptName+' msg= '+msg); }
	if (typeof(msg['recordTarget']['patientRole'].toString()) !== 'undefined') {

		var objPatientRole= new Object();

		// 2021-09-20 JER - Modified to incorporate evaluation of multiple patientRole Id(s) in the case there are multiple patient identifiers
		if (typeof(msg['recordTarget']['patientRole']['id']) !== 'undefined') {

			if(msg['recordTarget']['patientRole']['id'].length()>0) {
				if(msg['recordTarget']['patientRole']['id'].length()> 1) {
					for each(child in msg['recordTarget']['patientRole']['id']) {

						var objPatientId= new Object();
						objPatientId.patientId=						child['@extension'].toString();
						objPatientId.assigningAuthorityOid=			child['@root'].toString();
						objPatientId.assigningAuthorityName= 			child['@assigningAuthorityName'].toString();
						arrPatientIds.push(objPatientId);
						if(debug>0) { logger.error('DEBUG::'+scriptName+' function evaluate recordTarget.patientRole.Id objPatientId: '+JSON.stringify(objPatientId)); }

					} // end for each 
					objPatientRole.patientIds=		arrPatientIds;
				} else {
					objPatientRole.patientId=					msg['recordTarget']['patientRole']['id']['@extension'].toString();
					objPatientRole.assigningAuthorityOid=			msg['recordTarget']['patientRole']['id']['@root'].toString();
					objPatientRole.assigningAuthorityName= 			msg['recordTarget']['patientRole']['id']['@assigningAuthorityName'].toString();;
				}
			} // end if 
		} // end if 

		objPatientRole.address= getAddress(msg['recordTarget']['patientRole']['addr'])

		// 2021-09-20 JER - Modified to patient.name incorporate evaluation of multiple patient name child elements in the case there are multiple patient identifiers
		for(var i= 0;i<msg['recordTarget']['patientRole']['patient']['name'].length();i++) {

			if (typeof(msg['recordTarget']['patientRole']['patient']['name'][i]) !== 'undefined') {
				if(msg['recordTarget']['patientRole']['patient']['name'][i]['@use'] == 'L') {

					if(debug>0) { 
						logger.error('recordTarget.patientRole.patient.name= '+JSON.stringify(msg['recordTarget']['patientRole']['patient']['name'][0].toString())); 
					}
					// 2022-07-28 JER - Modified to evaluate multiple name.given elements with the first as firstName and secondary elements as middleNames
					objPatientRole.lastName=	msg['recordTarget']['patientRole']['patient']['name'][i]['family'].toString();
					for(var j= 0;j<msg['recordTarget']['patientRole']['patient']['name'][i]['given'].length();j++) {
						if(j==0) {
							objPatientRole.firstName=	msg['recordTarget']['patientRole']['patient']['name'][i]['given'][j].toString();
						} else {
							objPatientRole.middleName=	msg['recordTarget']['patientRole']['patient']['name'][i]['given'][j].toString();
						}
					} // end for 
				} // end if 
			} // end if

		} // end for

		objPatientRole.gender=			msg['recordTarget']['patientRole']['patient']['administrativeGenderCode']['@code'].toString();
		objPatientRole.dateOfBirth=		msg['recordTarget']['patientRole']['patient']['birthTime']['@value'].toString();
		// objPID.dateOfBirth=			convertDate(msg['PID']['PID.7']['PID.7.1'].text().toString(),"MM-dd-yyyy");
		if(debug>0) { logger.error('DEBUG::'+scriptName+' phoneNumber= '+msg['recordTarget']['patientRole']['telecom']); }
		objPatientRole.telecom=			getTelecom(msg['recordTarget']['patientRole']['telecom']);
		// objPatientRole.phoneNumber=	msg['recordTarget']['patientRole']['telecom']['@value'].toString();

		/* 2022-08-16 JER - Removed this code from the getPatientRole and add to the channel transformer separate these values from the patientRole object */
		/*
		// 2022-08-14 JER - Modified/Add the patient/raceCode coded element
		if(msg['recordTarget']['patientRole']['patient']['raceCode']) {
			objPatientRole.race= 		getRaceCode(msg['recordTarget']['patientRole']['patient']['raceCode']);
		} // end if

		// 2022-08-14 JER - Modified/Add the patient/ethnicGroupCode coded element
		if(msg['recordTarget']['patientRole']['patient']['ethnicGroupCode']) {
			objPatientRole.ethnicity= 	getEthnicGroupCode(msg['recordTarget']['patientRole']['patient']['ethnicGroupCode']);
		} // end if 

		// 2022-08-14 JER - Modified/Add the patient/languageCommunication coded element
		if(msg['recordTarget']['patientRole']['patient']['languageCommunication']) {
			objPatientRole.language= 	getLanguageCommunication(msg['recordTarget']['patientRole']['patient']['languageCommunication']);
		} // end if 
		*/

		if(debug>0) { logger.error('DEBUG::'+scriptName+'function F objPatientRole: '+JSON.stringify(objPatientRole)); }
		channelMap.put('objPatientRole',objPatientRole);
		return objPatientRole;
	}
}

/** getPerformer(msg)

  	- Desc: This function receives CCD msg and returns Performer objects
  	- Modified
	2018-02-16 18:21 CT JER - Modified call the getAssignedEntity function to reference the serviceEvent\assignedEntity child elements and attributes instead of using XPath assignments - reusability of the getAssignedEntities
	2018-02-16 10:40 CT JER - Corrected typo error still referencing performerChild object no longer in reference/deprecated usage of performer['functionCode']['@displayName'] XPath reference
	2018-02-15 08:37 CT JER - Modified changed from usage of children(), child and attribute XML ECMAScript E4X calls to specific XMLPath calls
  	2018-02-14 17:19 CT JER - Modified to provide additional handling typeof != 'undefined' on the functionCode element and child elements and their attributes
	2018-02-07 11:44 CT JER - Modified for additional handling verify typeof !== 'undefined'

   	@param {String} msg - this contains the raw message (string)
	@return {array} arrPerformer - contains arrPerformer array contains objPerformer objects and related properties.
*/

function getPerformer(msg) {

	var debug= 0;
	var arrPerformer= new Array();

	if(msg['documentationOf']['serviceEvent']['performer'] !== 'undefined') {
		var strPerfCount= 0;
		for each (performer in msg['documentationOf']['serviceEvent']['performer'][0]) {
			if(debug>0) { logger.error('performer['+strPerfCount+']: '+performer); }
			//if(typeof performer !== 'undefined') {
				var objPerformer= new Object;
				if(performer.children().length()>0) {

					if(debug>0) { logger.error('Performer.children().length: '+performer.children().length()+' children names: '+performer.children()); }
					// str = str.replace(/xmlns=\"(.*?)\"/g, '');

					if(performer['functionCode']['@code'] != null) {
						// check for functionCode element and it's attributes
						if(typeof performer['functionCode']['@code'] !== 'undefined') {
							// check for functionCode code attribute
							if(performer['functionCode']['@code'].toString().length>0) {
								// get Provider Role
								if(debug>0) { logger.error('performer[functionCode][@code].toString(): '+performer['functionCode']['@code'].toString()); }
								objPerformer.functionCode= performer['functionCode']['@code'].toString();				// PP
							} // end if 
						} // end if 
						// check for functionCode displayName attribute
						if(typeof performer['functionCode']['@displayName'] !=null) {
							// check for functionCode displayName attribute
							if(performer['functionCode']['@displayName'].toString().length>0) {
								// get Provider Role
								if(debug>0) { logger.error('performer[functionCode][@displayName].toString(): '+performer['functionCode']['@displayName'].toString()); }
								objPerformer.functionDisplayNmae= performer['functionCode']['@displayName'].toString();						// PP
							} // end if 
						} // end if 
						if(typeof performer['functionCode']['@codeSystem'] !=null) {
							// check for functionCode codeSystem attribute
							if(performer['functionCode']['@codeSystem'].toString().length>0) {
								if(debug>0) { logger.error('performer[functionCode][@codeSystem].toString(): '+performer['functionCode']['@codeSystem'].toString()); }
								objPerformer.functionCodeSystem= performer['functionCode']['@codeSystem'].toString(); 		// 2.16.840.1.113883.12.443
							} // end if 
						} // end if
					} // end if

					channelMap.put('Performer',objPerformer);

					var objAssignedEntity= getAssignedEntity(performer);
					if(objAssignedEntity !== 'undefined') {

						// check for assignedEntity id assigningAuthorityName 
						if(typeof objAssignedEntity.assigningAuthorityName !== 'undefined') {
							if(objAssignedEntity.assigningAuthorityName.toString().length>0) {
								if(debug>0) { logger.error('performer[assignedEntity][id][@assigningAuthorityName].toString(): '+objAssignedEntity.assigningAuthorityName.toString()); }
								objPerformer.assignedEntity_assigningAuthorityName= objAssignedEntity.assigningAuthorityName.toString(); 		// 2.16.840.1.113883.12.443
							} // end if							
						} // end if 

						// check for assignedEntity id root 
						if(typeof objAssignedEntity.root !== 'undefined') {
							if(objAssignedEntity.root.toString().length>0) {
								if(debug>0) { logger.error('performer[assignedEntity][id][@root].toString(): '+objAssignedEntity.root.toString()); }
								objPerformer.assignedEntity_root= objAssignedEntity.root.toString(); 		// 2.16.840.1.113883.12.443
							} // end if							
						} // end if 

						// check for assignedEntity id extension 
						if(typeof objAssignedEntity.extension !== 'undefined') {
							if(objAssignedEntity.extension.toString().length>0) {
								if(debug>0) { logger.error('performer[assignedEntity][id][@extension].toString(): '+objAssignedEntity.extension.toString()); }
								objPerformer.assignedEntity_extension= objAssignedEntity.extension.toString(); 		// 2.16.840.1.113883.12.443
							} // end if							
						} // end if 


						// <code code="208D00000X" codeSystem="2.16.840.1.113883.6.101" codeSystemName="NUCC Health Care Provider Taxonomy" displayName="General Practice"/>
						// check for assignedEntity code element
						if(typeof objAssignedEntity.code !== 'undefined') {
							if(objAssignedEntity.code.toString().length>0) {
								if(debug>0) { logger.error('performer[assignedEntity][code][@code].toString(): '+objAssignedEntity.code.toString()); }
								objPerformer.assignedEntity_code= objAssignedEntity.code.toString(); 				// 208D00000X
							} // end if							
						} // end if 

						// check for assignedEntity codeSystem element
						if(typeof objAssignedEntity.codeSystem !== 'undefined') {
							if(objAssignedEntity.codeSystem.toString().length>0) {
								if(debug>0) { logger.error('performer[assignedEntity][code][@codeSystem].toString(): '+objAssignedEntity.codeSystem.toString()); }
								objPerformer.assignedEntity_codeSystem= objAssignedEntity.codeSystem.toString(); 			// 2.16.840.1.113883.6.101
							} // end if							
						} // end if 

						// check for assignedEntity codeSystemName element
						if(typeof objAssignedEntity.codeSystemName !== 'undefined') {
							if(objAssignedEntity.codeSystemName.toString().length>0) {
								if(debug>0) { logger.error('performer[assignedEntity][code][@codeSystemName].toString(): '+objAssignedEntity.codeSystemName.toString()); }
								objPerformer.assignedEntity_codeSystemName= objAssignedEntity.codeSystemName.toString(); 		// NUCC Health Care Provider Taxonomy
							} // end if							
						} // end if 

						// check for assignedEntity displayName element
						if(typeof objAssignedEntity.displayName !== 'undefined') {
							if(objAssignedEntity.displayName.toString().length>0) {
								if(debug>0) { logger.error('performer[assignedEntity][code][@displayName].toString(): '+objAssignedEntity.displayName.toString()); }
								objPerformer.assignedEntity_displayName= objAssignedEntity.codeSystemName.toString(); 		// NUCC Health Care Provider Taxonomy
							} // end if							
						} // end if 						


						// check for assignedEntity assignedPerson Given name
						if(typeof objAssignedEntity.givenName !== 'undefined') {
							if(objAssignedEntity.givenName.toString().length>0) {
								if(debug>0) { logger.error('performer[assignedEntity][assignedPerson][name][given].toString(): '+objAssignedEntity.givenName.toString()); }
								objPerformer.assignedEntity_givenName= objAssignedEntity.givenName.toString(); 		// NUCC Health Care Provider Taxonomy
							} // end if							
						} // end if

						// check for assignedEntity assignedPerson Family name
						if(typeof objAssignedEntity.familyName !== 'undefined') {
							if(objAssignedEntity.familyName.toString().length>0) {
								if(debug>0) { logger.error('performer[assignedEntity][assignedPerson][name][family].toString(): '+objAssignedEntity.familyName.toString()); }
								objPerformer.assignedEntity_familyName= objAssignedEntity.familyName.toString(); 		// NUCC Health Care Provider Taxonomy
							} // end if							
						} // end if

						if(debug>0) { logger.error('getPerformer objAssignedEntity.suffix: '+objAssignedEntity.suffix+
							' typeof: '+typeof objAssignedEntity.suffix); }

						// check for assignedEntity assignedPerson Suffix
						if(typeof objAssignedEntity.suffix !== 'undefined') {
							if(objAssignedEntity.suffix.toString().length>0) {
								if(debug>0) { logger.error('performer[assignedEntity][assignedPerson][name][suffix].toString(): '+objAssignedEntity.suffix.toString()); }
								objPerformer.assignedEntity_suffix= objAssignedEntity.suffix.toString(); 		// NUCC Health Care Provider Taxonomy
							} // end if							
						} // end if


						// check for assignedEntity representedOrganization name
						if(typeof objAssignedEntity.representedOrganizationName !== 'undefined') {
							if(objAssignedEntity.representedOrganizationName.toString().length>0) {
								if(debug>0) { logger.error('performer[assignedEntity][representedOrganization][name].toString(): '+objAssignedEntity.representedOrganizationName.toString()); }
								objPerformer.assignedEntity_representedOrganization= objAssignedEntity.representedOrganizationName.toString(); 		// Walgreens Drug Store 12562
							} // end if							
						} // end if

					} // end if 

				} // end if
			//} // end if 

			if(objPerformer.toString().length>0) {
				channelMap.put('objPerformer',objPerformer);
			}

			arrPerformer.push(objPerformer);
			strPerfCount++;
		} // end for each
	} // end if

	if(arrPerformer.toString().length>0) {
		channelMap.put('arrPerformer',arrPerformer);
	}

	return arrPerformer;
}

/**  getRace(race) - Desc: This function receives CCD/CCDA msg and returns from RaceCode Object

   	@param {String} msg - this contains the raw message (string)
	@return {object} objRace - this contains the elements composed into a single objRace object.

	History
	2022-08-21 JER - Handling of nullFlavor property with UNK value
	2022-08-19 JER - Added evaluation of code if undefined or has toString gt zero (0)
	2022-08-19 JER - perform finalize review to confirm objRace has populated with value
	2022-08-17 JER - Modifying arrRace from array to objRace as a singular object
	2021-09-20 JER - leveraging the getTelecom for getRaceCode script
*/
function getRaceCode(race) {

	// define variables
	var debug= 0;
	var scriptName= 'getRaceCode';
	// 2022-08-17 JER - Modifying arrRace from array to objRace as a singular object
	// var arrRace= new Array();

	if(debug>0) { logger.error(scriptName+' race= '+race); }
	if (typeof(race) !== 'undefined') {

		if(debug>0) { 
			logger.error('DEBUG::'+scriptName+' typeof(race)= '+typeof(race)+' object.length= '+race.length()+' value= '+race);	
		}			


		// if(race.length()>0) {
			// for each race element
			var j= 0;
			for(var i= 0;i<race.length();i++) {

			if(debug>0) { 
				logger.error('DEBUG::'+scriptName+' typeof(race['+i+'][@code])= '+typeof(race[i]['@code'])+' race[@code]= '+race[i]['@code']);
				logger.error('DEBUG::'+scriptName+' PART 1: typeof(race['+i+'][@code])= '+typeof(race[i]['@code']));
				logger.error('DEBUG::'+scriptName+' PART 2: race['+i+'][@code].toString().length= '+race[i]['@code'].toString().length);
				logger.error('DEBUG::'+scriptName+' typeof(race['+i+'][@code])= '+typeof(race[i]['@code'])+' race['+i+'][@code].toString().length= '+race[i]['@code'].toString().length+' EVALUATION= '+((typeof(race[i]['@code']) !== 'undefined')&&(race[i]['@code'].toString().length>0)));
			}

				var objRace= new Object();
				// 2022-08-19 JER - Added evaluation of code if undefined or has toString gt zero (0) 
				if((typeof(race[i]['@code'])!== 'undefined')&&(race[i]['@code'].toString().length>0)) {
					// objRace.id= j;
					//if(race[i]['@code'].toString().length>0) {

						if(debug> 0) { logger.error('DEBUG::'+scriptName+' race['+i+'][@code]: '+race[i]['@code'].toString()); }

						objRace.code= race[i]['@code'].toString();
						objRace.codeSystem= race[i]['@codeSystem'].toString();
						objRace.codeSystemName= race[i]['@codeSystemName'].toString();
						objRace.displayName= race[i]['@displayName'].toString();
					//} // end if 

					if(debug>0) { 
						logger.error('DEBUG::'+scriptName+' function objRace: '+JSON.stringify(objRace)); 
					}
				} else {
					var objRace= new Object();
					// objRace.id= j;

					objRace.code= 'LA4489-6';
					objRace.codeSystem= '1.3.6.1.4.1.12009.10.1.1817';
					objRace.codeSystemName= 'LOINC UCUM Codes for Electronic Messaging'
					objRace.displayName= 'Unknown'

					if(debug>0) { 
						logger.error('DEBUG::'+scriptName+' function objRace: '+JSON.stringify(objRace)); 
					}
				} // end if 
				j++;
			} // for each address element 
		// } // end if 

		// 2022-08-17 JER - Modifying arrRace from array to objRace as a singular object
		// channelMap.put('arrRace',JSON.stringify(arrRace));
		// return arrRace;
		// 2022-08-19 JER - perform finalize review to confirm objRace has populated with value
		if(objRace) {
			return objRace;
		} // end if 
	} else {
		return;
	} // end if 
}

/** getServiceEvent(msg)

  	- Desc: This function receives CCD msg and returns Service Event objects
  	- Modified:
	2018-11-12 JER - Modified additional debugging for function
  	2018-02-14 JER - Modified to capture the serviceEvent ClassCode and DisplayName attribute values
  	2018-02-12 JER - multiple documentationOf (1..M) with (1..1) serviceEvents

   	@param {String} msg - this contains the raw message (string)
	@return {array} arrServiceEvents - this array contains list of objServiceEvent objects composed into the arrServiceEvents
*/
function getServiceEvent(msg) {

	// define variables
	var scriptName= 'getServiceEvent';
	var arrServiceEvent= new Array();
	var tSrvcCount= 1;
	var debug= 0;
	if(debug>0) { logger.error('Function: '+scriptName+' msg[documentationOf]= '+msg['documentationOf'].toXMLString()); }

	if(msg['documentationOf'] !== 'undefined') {
		for each(documentationOf in msg['documentationOf']) {
			if(documentationOf.serviceEvent !== 'undefined') {
				for each(serviceEvent in documentationOf.serviceEvent) {
					var objServiceEvent= new Object();
					objServiceEvent.id= 	tSrvcCount;
					if(typeof serviceEvent.@classCode !== 'undefined') {
						if(serviceEvent.@classCode.length()>0) {
							objServiceEvent.classCode=	serviceEvent.@classCode.toString();
						} // end if 
					} // end if 
					if(serviceEvent.children().length()>0) {
						if(typeof serviceEvent.id.@extension !== 'undefined') {
							objServiceEvent.extension=	serviceEvent.id.@extension.toString();
						} else {
							objServiceEvent.extension=    '';
						} // end if
						if(typeof serviceEvent.id.@root !== 'undefined') {
							objServiceEvent.root=		serviceEvent.id.@root.toString();
						} else {
							objServiceEvent.root=		'';
						} // end if
						if(typeof serviceEvent.code.@displayName !== 'undefined') {
							objServiceEvent.displayName=	serviceEvent.code.@displayName.toString();
						} else {
							objServiceEvent.displayName=    '';
						} // end if
						if(typeof serviceEvent.effectiveTime.low.@value !== 'undefined') {
							objServiceEvent.startTime=	serviceEvent.effectiveTime.low.@value.toString().substring(0,14);
						} else {
							objServiceEvent.startTime=	'';
						} // end if
						if(typeof serviceEvent.effectiveTime.high.@value !== 'undefined') {
							objServiceEvent.stopTime=	serviceEvent.effectiveTime.high.@value.toString().substring(0,14);
						} else {
							objServiceEvent.stopTime=	'';
						} // end if
					} // end if 
					arrServiceEvent.push(objServiceEvent);
				} // end for each	
			} // end if 
		} // end for each
	} // end if 

	if(debug>0) { logger.error('Function: '+scriptName+' arrServiceEvent= '+JSON.stringify(arrServiceEvent, null, 4)); }

	return arrServiceEvent;
}

/**  getTelecom(telecom) - Desc: This function receives CCD/CCDA msg and returns from Telecom Object

   	@param {String} msg - this contains the raw message (string)
	@return {object} objAddress - this contains the elements composed into a single objTelecom object.

	History
	2021-09-20 JER - Modified to incorporate evaluation of multiple patientRole Id(s) in the case there are multiple patient identifiers
	2021-09-20 JER - Modified to patient.name incorporate evaluation of multiple patient name child elements in the case there are multiple patient identifiers
*/
function getTelecom(telecom) {

	// define variables
	var debug= 0;
	var scriptName= 'getTelecom';
	var arrTelecom= new Array();

	if(debug>0) { logger.error(scriptName+' telecom= '+telecom); }
	if (typeof(telecom) !== 'undefined') {

		if(debug>0) { logger.error('DEBUG::'+scriptName+' object.length= '+telecom.length()+' value= '+telecom);	}			

		if(telecom.length()>0) {
			// for each addr element
			var j= 0;
			for(var i= 0;i<telecom.length();i++) {

				var objTelecom= new Object();
				objTelecom.id= j;
				if(telecom[i]['@value'].toString().length> 0) {
					if(telecom[i]['@value'].toString().indexOf(':')>-1) {

						if(debug> 0) { logger.error('DEBUG::'+scriptName+' telecom['+i+'][@value]: '+telecom[i]['@value'].toString()); }

						var lstTelecom=	telecom[i]['@value'].toString().split(':');
						var varName= lstTelecom[0];

						if(debug>0) { logger.error('DEBUG::'+scriptName+' varName: '+varName); }

						switch (varName) {
							case "tel": 	objTelecom.type= varName; break;
							case "fax": 	objTelecom.type= varName; break;
							case "mailto": 	objTelecom.type= varName; break;
							default: 		objTelecom.type= varName; break;
						} // end switch 
						// remove the + character from the value
						objTelecom.value= lstTelecom[1].replace('+','');
					} else {
						objTelecom.value= telecom[i]['@value'].toString().replace('+','');
					} // end if 
				} // end if 

				arrTelecom.push(objTelecom);

				//} // for each child element
				if(debug>0) { logger.error('DEBUG::'+scriptName+'function arrTelecom: '+JSON.stringify(arrTelecom)); }
				j++;
			} // for each address element 
		} // end if 

		channelMap.put('arrTelecom',JSON.stringify(arrTelecom));
		return arrTelecom;
	} else {
		return;
	} // end if 
}

/** getXdsAuthorPerson

	- Desc: This function receives Author Object and returns a CCD authorPerson XML
	- Modified:
	2018-02-25 12:35 PM CST - Perform additional checks on the supplied object typeof and null checks

	@param {object} objAuthor - the objAuthor object passed input argument
	@return {XML} return stringXML containing the newly devised XML containing the authorPerson

*/
function getXdsAuthorPerson(objAuthor) {

	// 0^Allscripts^System^^^^^^&amp;1.3.6.1.4.1.22812.4.10065404.0.3.3
	var authorPerson= '';
	var debug= 0;

	if(debug>0) { logger.error('getXdsAuthorPerson Input objAuthor: '+objAuthor); }

	// Author Id Extension
	if((typeof objAuthor.extension !== 'undefined')&&(objAuthor.extension != null)) {
		if(objAuthor.extension.toString().length>0) {
			if(debug>0) { logger.error('objAuthor.extension: '+objAuthor.extension.toString()); }
			authorPerson += objAuthor.extension.toString()+'^';
		} else {
			authorPerson += '^';
		} // end if
	} else {
		authorPerson += '^';
	} // end if 

	// Author LastName
	if((typeof objAuthor.idExtension !== 'undefined')&&(objAuthor.idExtension != null)) {
		if(objAuthor.idExtension.toString().length>0) {
			authorPerson += objAuthor.idExtension.toString()+'^';
		} else {
			authorPerson += '^';
		} // end if
	} else {
		authorPerson += '^';
	} // end if 

	// Author FirstName
	if((typeof objAuthor.idExtension !== 'undefined')&&(objAuthor.idExtension != null)) {
		if(objAuthor.idExtension.toString().length>0) {
			authorPerson += objAuthor.idExtension.toString()+'^';
		} else {
			authorPerson += '^';
		} // end if
	} else {
		authorPerson += '^';
	} // end if 


	// Author Id Root - objAssignedAuthor.root
	if((typeof objAuthor.root !== 'undefined')&&(objAuthor.root != null)) {
		if(objAuthor.root.toString().length>0) {
			if(debug>0) { logger.error('objAuthor.root: '+objAuthor.root.toString()); }
			authorPerson += '^^^^^'+objAuthor.root.toString();
		} else {
			authorPerson += '^^^^^';
		} // end if
	} else {
		authorPerson += '^^^^^'; 
	} // end if

	if(debug) { logger.error('getXdsAuthorPerson authorPerson: '+authorPerson); }
	channelMap.put('authorPerson',authorPerson);	

	return authorPerson;
}

