/** getXdsSourcePatientInfo(object)

  	- Desc: This function receives patientRole JavaScript Object and returns a XML SourcePatientInfo JavaScript object

	@param {object} objPatientRole - the objPatientRole object passed input argument
	@return {object} return object containing the newly devised string for XML containing the sourcePatientInfo
*/
// @return {XML} return stringXML containing the newly devised XML containing the sourcePatientInfo
function getXdsSourcePatientInfo(objPatientRole) {

	var objSourcePatientInfo= new Object;
	var rim = new Namespace('urn:oasis:names:tc:ebxml-regrep:xsd:rim:3.0');
	var stringXML= new XML('<ValueList/>');

	var pid_3=  "PID-3|"+objPatientRole.patientId+"^^^"+objPatientRole.assigningAuthorityName+"&amp;"+objPatientRole.assigningAuthorityOid+"&amp;ISO"
	var stringXML= stringXML.appendChild('<Value>'+pid_3+'</Value>');
	objSourcePatientInfo.pid3= pid_3;

	var pid_5=  "PID-5|"+objPatientRole.lastName+"^"+objPatientRole.firstName+"^^^^"
	var stringXML= stringXML.appendChild('<Value>'+pid_5+'</Value>');
	objSourcePatientInfo.pid5= pid_5;

	var pid_7=  "PID-7|"+objPatientRole.dateOfBirth
	var stringXML= stringXML.appendChild('<Value>'+pid_7+'</Value>');
	objSourcePatientInfo.pid7= pid_7;

	var pid_8=  "PID-8|"+objPatientRole.gender
	var stringXML= stringXML.appendChild('<Value>'+pid_8+'</Value>');
	objSourcePatientInfo.pid8= pid_8;

	var pid_11= "PID-11|"+objPatientRole.streetAddress+"^^"+objPatientRole.city+"^"+objPatientRole.state+"^"+objPatientRole.postalCode+"^"+objPatientRole.country
	var stringXML= stringXML.appendChild('<Value>'+pid_11+'</Value>');
	objSourcePatientInfo.pid11= pid_11;

	var pid_13= "PID-13|"+objPatientRole.phoneNumber
	var stringXML= stringXML.appendChild('<Value>'+pid_13+'</Value>');
	objSourcePatientInfo.pid13= pid_13;

	return objSourcePatientInfo;
}

/** setXdsMetadataAuthor(object)

  	- Desc: This setXdsAuthor function receives Javascript Array of Objects and author XML string
  	- Modified:
  	2018-02-26 19:20 CST - Under development
	2018-02-25 12:40PM CST - Replacement of the Call getPerformer, getAuthor and usage of objEncounter transformation function.

   	@param {object} arrPerformer - this contains the Javascript Object (object) - could pass in 
	@return {XML} authorXML - contains authorXML XML Object string to build out the Template : XDSDocumentEntry.author (urn:uuid:93606bcf-9494-43ec-9b4e-a7748d1a838d) XML scheme.

	Reference: https://gazelle.ihe.net/XDStarClient/rimihe-templates/rimihe-Author_XDSDocumentEntry.html

*/

function setXdsMetadataAuthor(arrAuthors) {

	var debug= 1;
	var i= 0;

	if(debug>0) { logger.error('setXdsAuthor arrAuthors: '+JSON.stringify(arrAuthors)); } 

	if(typeof arrAuthor !== 'undefined') {
		if(arrAuthor.length>1) {
			for each(objAuthor in arrAuthors) {
				if(debug>0) { logger.error('objAuthor['+i+']: '+JSON.stringify(objAuthor)+
					' typeof: '+typeof objAuthor+
					' length: '+objAuthor.length());
				} // end if 

				var authorXML = new XML('<rim:Classification id="${uuid}" classificationScheme="urn:uuid:93606bcf-9494-43ec-9b4e-a7748d1a838d" classifiedObject="${extrinsicObjectUUID}"></rim:Classification>');

				// need to confirm the objAuthor contains the representedOrganization
				// create Author Person - using either objAuthor.assignedEntity_familyName OR objEncounter.assignedEntity_familyName call the getAuthorPerson function
				if((typeof objAuthor.assignedEntity_familyName !== 'undefined')&&(objAuthor.assignedEntity_familyName.toString() != null)) { 
					var xmlAuthor= new XML('<rim:Slot name="authorPerson"><rim:ValueList><rim:Value></rim:Value></rim:ValueList></rim:Slot>');
					xmlAuthor['rim:Slot']['rim:ValueList']['rim:Value']= getAuthorPerson(objAuthor);
				} else {
					var xmlAuthor= new XML('<rim:Slot name="authorPerson"><rim:ValueList><rim:Value></rim:Value></rim:ValueList></rim:Slot>');
				} // end if 

				// create Author Institution - objAuthor.authorInstitution
				if((typeof objAuthor.authorInstitution !== 'undefined')&&(objAuthor.authorInstitution.toString() != null)) {
					var xmlAuthor= new XML('<rim:Slot name="authorInstitution"><rim:ValueList><rim:Value></rim:Value></rim:ValueList></rim:Slot>');
					xmlAuthor['rim:Slot']['rim:ValueList']['rim:Value']= objAuthor.authorInstitution.toString();
				} else {
					var xmlAuthor= new XML('<rim:Slot name="authorInstitution"><rim:ValueList><rim:Value></rim:Value></rim:ValueList></rim:Slot>');
				} // end if

				// create Author Role 
				// objPerformers.functionDisplayName
				if((typeof objAuthor.functionDisplayName !== 'undefined')&&(objAuthor.functionDisplayName.toString() != null)) {
					if(objAuthor.functionDisplayName.toString().length>0) {
						var xmlAuthor= new XML('<rim:Slot name="authorRole"><rim:ValueList><rim:Value></rim:Value></rim:ValueList></rim:Slot>');
						xmlAuthor['rim:Slot']['rim:ValueList']['rim:Value']= objAuthor.functionDisplayName.toString();
					} // end if 
					// objEncounter.encounterParticipant_code
				} else if((typeof objAuthor.encounterParticipant_code !== 'undefined')&&(objAuthor.encounterParticipant_code.toString() != null)) {
					if(objAuthor.encounterParticipant_code.toString().length>0) {
						var xmlAuthor= new XML('<rim:Slot name="authorRole"><rim:ValueList><rim:Value></rim:Value></rim:ValueList></rim:Slot>');
						xmlAuthor['rim:Slot']['rim:ValueList']['rim:Value']= objAuthor.functionDisplayName.toString();
					} // end if 
				// objEncounter.assignedEntity_codeDisplayName
				} else if((typeof objAuthor.assignedEntity_codeDisplayName !== 'undefined')&&(objAuthor.assignedEntity_codeDisplayName.toString() != null)) {
					if(objAuthor.assignedEntity_codeDisplayName.toString().length>0) {
						var xmlAuthor= new XML('<rim:Slot name="authorRole"><rim:ValueList><rim:Value></rim:Value></rim:ValueList></rim:Slot>');
						xmlAuthor['rim:Slot']['rim:ValueList']['rim:Value']= objAuthor.assignedEntity_codeDisplayName.toString();
					} // end if 
				} else {
					var xmlAuthor= new XML('<rim:Slot name="authorRole"><rim:ValueList><rim:Value>Medical Doctor</rim:Value></rim:ValueList></rim:Slot>');
				} // end if 

				// create Author Specialty
				// objPerformers.assignedDisplayName
				if((typeof objAuthor.assignedDisplayName !== 'undefined')&&(objAuthor.assignedDisplayName.toString() != null)) {
					if(objAuthor.assignedDisplayName.toString().length>0) {
						var xmlAuthor= new XML('<rim:Slot name="authorSpecialty"><rim:ValueList><rim:Value></rim:Value></rim:ValueList></rim:Slot>');
						xmlAuthor['rim:Slot']['rim:ValueList']['rim:Value']= objAuthor.assignedDisplayName.toString();
					} // end if 
				// objEncounter.assignedEntity_codeDisplayName
				} else if((typeof objAuthor.assignedEntity_codeDisplayName !== 'undefined')&&(objAuthor.assignedEntity_codeDisplayName.toString() != null)) {
					if(objAuthor.assignedEntity_codeDisplayName.toString().length>0) {
						var xmlAuthor= new XML('<rim:Slot name="authorSpecialty"><rim:ValueList><rim:Value></rim:Value></rim:ValueList></rim:Slot>');
						xmlAuthor['rim:Slot']['rim:ValueList']['rim:Value']= objAuthor.assignedEntity_codeDisplayName.toString();
					} // end if 
				} else {
					var xmlAuthor= new XML('<rim:Slot name="authorSpecialty"><rim:ValueList><rim:Value>General Medicine</rim:Value></rim:ValueList></rim:Slot>');
				} // end if 
			} // end for each
		} // end if 
	} else {		
		var classificationXML=     ['rim:Classification']['@id']="1234"; 
		classificationXML+=        ['rim:Classification']['@ClassificationScheme']= "urn:uuid:93606bcf-9494-43ec-9b4e-a7748d1a838d";
		classificationXML+=        ['rim:Classification']['@classifiedObject']= "";
		var authorXML = new XML(classificationXML);

		var authorPersonXML=    	new XML('<rim:Slot name="authorPerson"></rim:Slot>');
		authorPersonXML+= 		new XML('<rim:ValueList></rim:ValueList>');
		authorPersonXML+=	 	new XML('<rim:Value></rim:Value>');
		authorXML+= authorPersonXML; 

		var authorInstitutionXML=    new XML('<rim:Slot name="authorInstitution"></rim:Slot>');
		authorInstitutionXML+= 		new XML('<rim:ValueList></rim:ValueList>');
		authorInstitutionXML+=		new XML('<rim:Value></rim:Value>');
		authorXML+= authorInstitutionXML;

		var authorRoleXML=  new XML('<rim:Slot name="authorRole"></rim:Slot>');
		authorRoleXML+= 	new XML('<rim:ValueList></rim:ValueList>');
		authorRoleXML+=	new XML('<rim:Value></rim:Value>');
		authorXML+= autorRoleXML;

		var authorSpecialtyXML =		new XML('<rim:Slot name="authorSpecialty"></rim:Slot>');
		authorSpecialtyXML+= new XML('<rim:ValueList></rim:ValueList>');
		authorSpecialtyXML+= new XML('<rim:Value></rim:Value>');
		authorXML+= authorSpecialtyXML;

	} // end if 	

	return authorXML;
} // end function

/** createHL7FromObject

  	- Desc: This function receives an Object and returns formed HL7 in XML format
  	- Modified
	2020-03-04 JER - Created

Message Structure

MSH|^~\&|||||||<Event>||<Processing ID>|<Version>|
PID|||<Patient Identifiers>||<Patient Name>||<Date of Birth>|<Sex>||||||||||<PatientVisit ID>|<SSN>|
PV1|||<Patient Location>||||||||||||||||<Patient Visit ID>|
TXA||<Report Type>||<Activity Date/Time>|<Primary Provider ID>|<Origination
Date/Time>|<Transcription Date/Time>|||<Assigned Document Authenticator ID>||<Document ID>|||||<Document Completion Status>||<Document Availability Status>|
OBX||<Value Type>|||<Observation Value>|||<Abnormal Flag>|


Data Element Details
Required
	PID-3: Patient Identifiers
	TXA-2: Report Type
	TXA-4: Activity Date/Time
	TXA-5: Primary Activity Provider ID
 	TXA-12: Document ID - Unique ID that globally identifies a document
	OBX-2: Value Type - TX or FT for transcriptions. RP for reference pointers
	OBX-5: Observation Value - Body of the document


   	@param {String} msg - this contains a RAW message (i.e. msg)
	@return {Object} objMsgContent - this object contains objectification of the RAW msg string
*/
function createHL7FromObject (msgObject, debug) {

	var debug= 0;  // 0= OFF, 1= ON
	var currDateTime= getCurrentDateTime();	// only supports YYYYMMDDHHMMSS
	var transDescription= 'createHL7FromObject';
	var objMsgContent= msgObject;

	// set or modify these values as needed
	var setID = '1';
	var msgCode= 'MDM';
	var eventType= 'T02';
	channelMap.put('msgType',msgCode+'^'+eventType);
	channelMap.put('currentUUID',UUIDGenerator.getUUID());
	channelMap.put('messageId',$('currentUUID'));

	var sendingApplication= 'ABC-APPLICATION';
	var receivingApplication= 'EPIC'
	var messageProcessingId= 'P';  // reference HL7 Table 0103 WHERE P= Production, T= Test, D= Debugging, N= Non-Production, V= Valdation
	var hl7version= '2.3';
	var patientClass= 'I';  		// reference HL7 Table 0004 https://www.hl7.org/fhir/v2/0004/index.html
	//
	tmp['MSH']['MSH.3']['MSH.3.1']= sendingApplication;
	tmp['MSH']['MSH.4']['MSH.4.1']= channelMap.get('SendingFacility');
	tmp['MSH']['MSH.5']['MSH.5.1']= receivingApplication;
	tmp['MSH']['MSH.6']['MSH.6.1']= channelMap.get('ReceivingFacility');
	tmp['MSH']['MSH.7']['MSH.7.1']= currDateTime;
	tmp['MSH']['MSH.8']['MSH.8.1']= '';
	tmp['MSH']['MSH.9']['MSH.9.1']= msgCode;
	tmp['MSH']['MSH.9']['MSH.9.2']= eventType;
	tmp['MSH']['MSH.10']['MSH.10.1']= $('currentUUID');
	tmp['MSH']['MSH.11']['MSH.11.1']= messageProcessingId;
	tmp['MSH']['MSH.12']['MSH.12.1']= hl7version;

	tmp['PID']['PID.1']['PID.1.1'] = setID;
	tmp['PID']['PID.3']['PID.3.1']= objMsgContent.PatId;
	tmp['PID']['PID.5']['PID.5.1']= objMsgContent.PatientLastNm;
	tmp['PID']['PID.5']['PID.5.2']= objMsgContent.PatientFirstNm;
	tmp['PID']['PID.5']['PID.5.3']= objMsgContent.PatientMiddleNm;
	tmp['PID']['PID.7']['PID.7.1']= objMsgContent.PatientDOB;
	tmp['PID']['PID.8']['PID.8.1']= objMsgContent.PatientGender.toString().substring(0, 1);
	tmp['PID']['PID.18']['PID.18.1']= objMsgContent.EncounterId;
	tmp['PID']['PID.19']['PID.19.1']= objMsgContent.PatientSSN;

	tmp['PV1']['PV1.1']['PV1.1.1'] = setID;
	tmp['PV1']['PV1.19']['PV1.19.1'] = objMsgContent.EncounterId;

	tmp['TXA']['TXA.1']['TXA.1.1'] = setID;
	tmp['TXA']['TXA.2']['TXA.2.1'] = objMsgContent.ReportType;
	tmp['TXA']['TXA.4']['TXA.4.1'] = objMsgContent.Activity_DTTM;
	tmp['TXA']['TXA.5']['TXA.5.1'] = objMsgContent.ProvId;
	tmp['TXA']['TXA.4']['TXA.6.1'] = objMsgContent.OriginiationDTTM;
	tmp['TXA']['TXA.4']['TXA.7.1'] = objMsgContent.TranscribedDTTM;
	tmp['TXA']['TXA.12']['TXA.12.1'] = objMsgContent.DocumentId;
	tmp['TXA']['TXA.12']['TXA.17.1'] = objMsgContent.DOCStatus.toString().substring(0, 1);

	tmp['OBX']['OBX.1']['OBX.1.1'] = setID;
	tmp['OBX']['OBX.2']['OBX.2.1'] = objMsgContent.ValueType; 
	tmp['OBX']['OBX.5']['OBX.5.1'] = objMsgContent.DocumentBodyRTF;

	if (debug>0) { logger.error(transDescription+' tmp: '+tmp); }	
	// var message = SerializerFactory.getSerializer('HL7V2').fromXML(tmp);

	return tmp;
}

/** getCurrentDateTime

  	- Desc: This function returns current DateTime only YYYYMMDDHHMMSS format

	@return {string} datetime - this string object contains the current DateTime only YYYYMMDDHHMMSS format
*/
function getCurrentDateTime() {
	var today = new Date();
	// set Date with leading zeros if needed
	var tMonth= today.getMonth()+1;
	if(tMonth.toString().length<2) tMonth= '0'+tMonth;
	var tDay= today.getDate()
	if(tDay.toString().length<2) tDay= '0'+tDay;
	var date = today.getFullYear()+''+tMonth+''+tDay;

	// set Time with leading zeros if needed
	var tHour= today.getHours();
	if(tHour.toString().length<2) tHour= '0'+tHour;
	var tMinute= today.getMinutes();
	if(tMinute.toString().length<2) tMinute= '0'+tMinute;
	var tSecond= today.getSeconds();
	if(tSecond.toString().length<2) tSecond= '0'+tSecond;

	var time = tHour+''+tMinute+''+tSecond;
	var dateTime = date+''+time;

	return dateTime;
}

/** occurrences

	-Desc: This function that count occurrences of a substring in a string.

 	@param {String} string               The string
 	@param {String} subString            The sub string to search for
 	@param {Boolean} [allowOverlapping]  Optional. (Default:false)

 	@author Vitim.us https://gist.github.com/victornpb/7736865
 	@see Unit Test https://jsfiddle.net/Victornpb/5axuh96u/
 	@see http://stackoverflow.com/questions/4009756/how-to-count-string-occurrence-in-string/7924240#7924240
 */
function occurrences(string, subString, allowOverlapping) {

    string += "";
    subString += "";
    if (subString.length <= 0) return (string.length + 1);

    var n = 0,
        pos = 0,
        step = allowOverlapping ? 1 : subString.length;

    while (true) {
        pos = string.indexOf(subString, pos);
        if (pos >= 0) {
            ++n;
            pos += step;
        } else break;
    }
    return n;
}

/** parseCSVtoObject

  	- Desc: This function receives a RAW msg string and returns string objectified

   	@param {String} msg - this contains a RAW message (i.e. msg)
	@return {Object} objMsgContents - this object contains objectification of the RAW msg string
*/
function parseCSVtoObject (msg, debug) {

	// var debug=1; // 0= OFF, 1= channelMap, 2= logger.error
	var funcDescription= 'parseCSVtoObject';
	if(debug>0) { logger.error('DEBUG::'+funcDescription+' msg: '+msg); }

	// create obj Message Contents
	var objMsgContents= new Object(); 

// CHS FACILITY NAME
	if(msg['row']['column1'].toString().length>0) {
		objMsgContents.FacName= msg['row']['column1'].toString();
	} else {
		objMsgContents.FacName= '';
	}
	channelMap.put('FacName',objMsgContents.FacName);	
// CHS REGION
	if(msg['row']['column2'].toString().length>0) {
		objMsgContents.ChsRegion= msg['row']['column2'].toString();
	} else {
		objMsgContents.ChsRegion= 'X';
	}
	channelMap.put('ChsRegion',objMsgContents.ChsRegion);
// CHS EHR SYSTEM	
	if(msg['row']['column3'].toString().length>0) {
		objMsgContents.adtEHR= msg['row']['column3'].toString();
	} else {
		objMsgContents.adtEHR= '';
	}
	channelMap.put('adtEHR',objMsgContents.adtEHR);	
// SENDING FACILITY	
	if(msg['row']['column4'].toString().length>0) {
		objMsgContents.SendingFacility= msg['row']['column4'].toString()
	} else {
		objMsgContents.SendingFacility= '';
	}
// RECV FACILITY	
	if(msg['row']['column5'].toString().length>0) {
		objMsgContents.ReceivingFacility= msg['row']['column5'].toString();
	} else {
		objMsgContents.ReceivingFacility= '';
	}
	channelMap.put('ReceivingFacility',objMsgContents.ReceivingFacility);
// PAT MRN	
	if(msg['row']['column6'].toString().length>0) {
		objMsgContents.PatientIdentifier= msg['row']['column6'].toString();
	} else {
		objMsgContents.PatientIdentifier= '';
	}
	channelMap.put('PatientId',objMsgContents.PatientIdentifier);		
// PAT ACCT #	
	if(msg['row']['column7'].toString().length>0) {
		objMsgContents.PatientAcct= msg['row']['column7'].toString();
	} else {
		objMsgContents.PatientAcct= '';
	}
	channelMap.put('PatientAcct',objMsgContents.PatientAcct);	
// PAT LAST NAME	
	if(msg['row']['column8'].toString().length>0) {
		objMsgContents.PatientLastName= msg['row']['column8'].toString();
	} else {
		objMsgContents.PatientLastName= '';
	}
	channelMap.put('PatientLastName',objMsgContents.PatientLastName);
// PAT ADDRESS FIRST NAME	
	if(msg['row']['column9'].toString().length>0) {
		objMsgContents.PatientFirstName= msg['row']['column9'].toString();
	} else {
		objMsgContents.PatientFirstName= '';
	}
	channelMap.put('PatientFirstName',objMsgContents.PatientFirstName);
// PAT ADDRESS DOB	
	if(msg['row']['column10'].toString().length>0) {
		objMsgContents.PatientDOB= msg['row']['column10'].toString();
	} else {
		objMsgContents.PatientDOB= '19000101';
	}
// PAT GENDER	
	if(msg['row']['column11'].toString().length>0) {
		objMsgContents.PatientGender= msg['row']['column11'].toString();
	} else {
		objMsgContents.PatientGender= '';
	}
// PAT PHONE	
	if(msg['row']['column12'].toString().length>0) {
		objMsgContents.PatientPhoneNumber= msg['row']['column12'].toString();
	} else {
		objMsgContents.PatientPhoneNumber= '';
	}
// PAT ADDRESS STREET	
	if(msg['row']['column13'].toString().length>0) {
		objMsgContents.PatientAddressStreet= msg['row']['column13'].toString();
	} else {
		objMsgContents.PatientAddressStreet= '';
	}
	channelMap.put('PatientAddressStreet',objMsgContents.PatientAddressStreet);
// PAT ADDRESS CITY	
	if(msg['row']['column14'].toString().length>0) {
		objMsgContents.PatientAddressCity= msg['row']['column14'].toString();
	} else {
		objMsgContents.PatientAddressCity= '';
	}
	channelMap.put('PatientAddressCity',objMsgContents.PatientAddressCity);
// PAT ADDRESS ST	
	if(msg['row']['column15'].toString().length>0) {
		objMsgContents.PatientAddressState= msg['row']['column15'].toString();
	} else {
		objMsgContents.PatientAddressState= '';
	}
	channelMap.put('PatientAddressState',objMsgContents.PatientAddressState);	
// PAT ADDRESS ZIP
	if(msg['row']['column16'].toString().length>0) {
		objMsgContents.PatientAddressZip= msg['row']['column16'].toString();
	} else {
		objMsgContents.PatientAddressZip= '';
	}
	channelMap.put('PatientAddressZip',objMsgContents.PatientAddressZip);	

	if(debug>0) { logger.error('DEBUG::'+funcDescription+' object: '+JSON.stringify(objMsgContents)); }
	return objMsgContents;
} // end function

/** parseHL7MDMtoObject

  	- Desc: This function receives a RAW HL7v2 MDM message string and returns string objectified
  	- Modified
	2020-03-03 JER - Modified to iterate through the instances of the row XML object into JS Object
	2020-03-27 JER - parse DOB to provide YYYYMMDD format

   	@param {String} msg - this contains a RAW message (i.e. msg)
	@return {Object} objMsgContent - this object contains objectification of the RAW msg string
*/
function parseHL7MDMtoObject (msg, debug) {

	// var debug=1; // 0= OFF, 1= channelMap, 2= logger.error
	var funcDescription= 'parseHL7MDMtoObject';
	var intCount= 0;
	var chkNumeric= false;
	if(debug>0) { logger.error('DEBUG::'+funcDescription+' msg: '+msg); } 

	var arrORUMessageSegments= new Array();

	for each(element in msg.children()) {
		if (element.name()== 'MSH') {
			var msgSegment= new Object();
			msgSegment.SendingApplication= 		element['MSH.3']['MSH.3.1'].toString();
			msgSegment.SendingFacility= 			element['MSH.4']['MSH.4.1'].toString();
			msgSegment.ReceivingApplication=		element['MSH.5']['MSH.5.1'].toString();
			msgSegment.ReceivingFacility=			element['MSH.6']['MSH.6.1'].toString();
			msgSegment.MessageDateTime=			element['MSH.7']['MSH.7.1'].toString();
			msgSegment.MessagesType=				element['MSH.9']['MSH.9.1'].toString();
			msgSegment.MessageEventType=			element['MSH.9']['MSH.9.2'].toString();
			msgSegment.MessageControlId=			element['MSH.10']['MSH.10.1'].toString();
			arrORUMessageSegments.push(msgSegment);
		} else if (element.name()== 'PID') {
			var msgSegment= new Object();
			msgSegment.PatientId= 				element['PID.3']['PID.3.1'].toString();
			msgSegment.LastName=				element['PID.5']['PID.5.1'].toString();
			msgSegment.FirstName=				element['PID.5']['PID.5.2'].toString();
			msgSegment.MiddleName=				element['PID.5']['PID.5.2'].toString();
			msgSegment.DateOfBirth=				element['PID.7']['PID.7.1'].toString();
			msgSegment.Gender=					element['PID.8']['PID.8.1'].toString();
			msgSegment.AccountNumber=			element['PID.18']['PID.18.1'].toString();
			msgSegment.SSN=					element['PID.19']['PID.19.1'].toString();
			arrORUMessageSegments.push(msgSegment);
		} else if (element.name()== 'PV1') {
			msgSegment.VisitNumber=				element['PV1.19']['PV1.19.1'].toString();
			arrORUMessageSegments.push(msgSegment);
		} else if (element.name()== 'TXA') {
			var documentType=					element['TXA.2']['TXA.2.1'].toString();
			msgSegment.DocumentType=				documentType;
			var activityDateTime=				element['TXA.4']['TXA.4.2'].toString().replace(/[^0-9a-zA-Z]/g,'');
			msgSegment.ActivityDateTime=			activityDateTime.substring(0,8);
			msgSegment.OriginationDateTime=		activityDateTime.substring(0,8);
			msgSegment.TranscriptionDateTime=		activityDateTime.substring(0,8);
			msgSegment.ProviderCode=				element['TXA.5']['TXA.5.1'].toString();
			msgSegment.DocumentNumberId=			element['TXA.12']['TXA.12.1'].toString();
			msgSegment.DocumentNamespaceId=		element['TXA.12']['TXA.12.2'].toString();
			arrORUMessageSegments.push(msgSegment);
		} else if (element.name()== 'OBX') {
			msgSegment.ValueType=				documentType;
			msgSegment.ObservationId=			element['OBX.2']['OBX.2.1'].toString();
			arrORUMessageSegments.push(msgSegment);
		}
	}

	if(debug>0) { logger.error('arrORUMessageSegments.length: '+arrORUMessageSegments.length); }
	channelMap.put('arrORUMessageSegments',arrORUMessageSegments);
	return arrORUMessageSegments;
}

/** parseRTFtoObject

  	- Desc: This function receives a RAW msg string and returns string objectified
  	- Modified
	2020-03-03 JER - Modified to iterate through the instances of the row XML object into JS Object
	2020-03-27 JER - parse DOB to provide YYYYMMDD format

   	@param {String} msg - this contains a RAW message (i.e. msg)
	@return {Object} objMsgContent - this object contains objectification of the RAW msg string
*/
function parseRTFtoObject (msg, debug) {

	// var debug=1; // 0= OFF, 1= channelMap, 2= logger.error
	var funcDescription= 'parseRTFtoObject';
	var intCount= 0;
	var chkNumeric= false;
	if(debug>0) { logger.error('DEBUG::'+funcDescription+' msg: '+msg); } 

	// create Message objects
	var arrMsgContents= new Array();

	for each (row in msg['row']) {
		if (row.children().length()>1) {
			if(debug>0) { 
				logger.error('row['+intCount+'].children.length: '+row.children().length()+' row children names: '+row.children());
			}

			for each (child in row.children()) {

				if (child.localName().toString() == 'Patient_ID') {
					chkNumeric= isNumeric(child.toString());
					if(debug>0) {
						logger.error(child.localName().toString()+'.isNumeric:'+chkNumeric);
					}
					if(isNumeric(child.toString())== true) {
						// create each Message object
						var objMsgContent= new Object();

						// Patient ID
						if(child.toString().length>0) {
							objMsgContent.PatId= child.toString();
						} else {
							objMsgContent.PatId= '';
						}
						//channelMap.put('PatId',objMsgContent.PatId);
					}
				}

				if(chkNumeric==  true) {
					// ReportType
					if(child.localName().toString() == 'ReportType') {
						if(child.toString().length>0) {
							objMsgContent.ReportType= child.toString();
						} else {
							objMsgContent.ReportType= 'X';
						}	
						//channelMap.put('ReportType',objMsgContent.ReportType);
					// Activity_DTTM	
					} else if(child.localName().toString() == 'Activity_DTTM') {
						if(child.toString().length>0) {
							objMsgContent.ActivityDT= child.toString();
						} else {
							objMsgContent.ActivityDT= '';
						}	
						//channelMap.put('ActivityDT',objMsgContent.ActivityDT);
					// Prov_ID	
					} else if(child.localName().toString() == 'Prov_ID') {
						if(child.toString().length>0) {
							objMsgContent.ProvId= child.toString();
						} else {
							objMsgContent.ProvId= '';
						}	
						//channelMap.put('ProvId',objMsgContent.ProvId);
					// Prov_Last_Nm
					} else if(child.localName().toString() == 'Prov_Last_Nm') {
						if(child.toString().length>0) {
							objMsgContent.ProvLastNm= child.toString();
						} else {
							objMsgContent.ProvLastNm= '';
						}	
						// channelMap.put('ProvLastNm',objMsgContent.ProvLastNm);
					// Prov_First_Nm
					} else if(child.localName().toString() == 'Prov_First_Nm') {
						if(child.toString().length>0) {
							objMsgContent.ProvFirstNm= child.toString();
						} else {
							objMsgContent.ProvFirstNm= '';
						}	
						// channelMap.put('ProvFirstNm',objMsgContent.ProvFirstNm);
					// Prov_Middle_Nm
					} else if(child.localName().toString() == 'Prov_Middle_Nm') {
						if(child.toString().length>0) {
							objMsgContent.ProvMiddleNm= child.toString();
						} else {
							objMsgContent.ProvMiddleNm= '';
						}	
						// channelMap.put('ProvMiddleNm',objMsgContent.ProvFirstNm);
					// DocumentID
					} else if(child.localName().toString() == 'DocumentID') {
						if(child.toString().length>0) {
							objMsgContent.DocumentId= child.toString();
						} else {
							objMsgContent.DocumentId= '';
						}	
						// channelMap.put('DocumentId',objMsgContent.DocumentId);
					// Value_Type
					} else if(child.localName().toString() == 'Value_Type') {
						if(child.toString().length>0) {
							objMsgContent.ValueType= child.toString();
						} else {
							objMsgContent.ValueType= '';
						}	
						// channelMap.put('ValueType',objMsgContent.ValueType);							
					// Patient_Last_Nm
					} else if(child.localName().toString() == 'Patient_Last_Nm') {
						if(child.toString().length>0) {
							objMsgContent.PatientLastNm= child.toString();
						} else {
							objMsgContent.PatientLastNm= '';
						}	
						// channelMap.put('PatientLastNm',objMsgContent.PatientLastNm);
					// Patient_First_Nm
					} else if(child.localName().toString() == 'Patient_First_Nm') {
						if(child.toString().length>0) {
							objMsgContent.PatientFirstNm= child.toString();
						} else {
							objMsgContent.PatientFirstNm= '';
						}	
						// channelMap.put('PatientFirstNm',objMsgContent.PatientFirstNm);
					// Patient_Middle_Nm
					} else if(child.localName().toString() == 'Patient_Middle_Nm') {
						if(child.toString().length>0) {
							objMsgContent.PatientMiddleNm= child.toString();
						} else {
							objMsgContent.PatientMiddleNm= '';
						}	
						// channelMap.put('PatientMiddleNm',objMsgContent.PatientMiddleNm);
					// Patient_DOB
					} else if(child.localName().toString() == 'Patient_DOB') {
						if(child.toString().length>0) {

							// 2020-03-27 JER - parse DOB to provide YYYYMMDD format
							var strPatientDOB= 	child.toString().replace(/-/g,'').replace(/:/g,'').replace(' ','');
							var newPatientDOB= 	strPatientDOB.substring(0,8);
							if(debug>0) { logger.error('DEBUG::'+funcDescription+' strPatientDOB: '+strPatientDOB+' newPatientDOB: '+newPatientDOB); } 
							objMsgContent.PatientDOB= newPatientDOB;
							// objMsgContent.PatientDOB= child.toString();
						} else {
							objMsgContent.PatientDOB= '';
						}	
						// channelMap.put('PatientDOB',objMsgContent.PatientDOB);
					// Patient_Gender
					} else if(child.localName().toString() == 'Patient_Gender') {
						if(child.toString().length>0) {
							objMsgContent.PatientGender= child.toString();
						} else {
							objMsgContent.PatientGender= '';
						}	
						// channelMap.put('PatientGender',objMsgContent.PatientGender);
					// Encounter_ID
					} else if(child.localName().toString() == 'Encounter_ID') {
						if(child.toString().length>0) {
							objMsgContent.EncounterID= child.toString();
						} else {
							objMsgContent.EncounterID= '';
						}	
						// channelMap.put('EncounterID',objMsgContent.EncounterID);
					// Patient_SSN
					} else if(child.localName().toString() == 'Patient_SSN') {
						if(child.toString().length>0) {
							objMsgContent.PatientSSN= child.toString();
						} else {
							objMsgContent.PatientSSN= '';
						}	
						// channelMap.put('PatientSSN',objMsgContent.PatientSSN);
					// DOCStatus
					} else if(child.localName().toString() == 'DOCStatus') {
						if(child.toString().length>0) {
							objMsgContent.DOCStatus= child.toString();
						} else {
							objMsgContent.DOCStatus= '';
						}	
						// channelMap.put('DOCStatus',objMsgContent.DOCStatus);	
					// OriginiationDTTM
					} else if(child.localName().toString() == 'OriginiationDTTM') {
						if(child.toString().length>0) {
							objMsgContent.OriginiationDTTM= child.toString();
						} else {
							objMsgContent.OriginiationDTTM= '';
						}	
						// channelMap.put('OriginiationDTTM',objMsgContent.OriginiationDTTM);
					// TranscribedDTTM
					} else if(child.localName().toString() == 'TranscribedDTTM') {
						if(child.toString().length>0) {
							objMsgContent.TranscribedDTTM= child.toString();
						} else {
							objMsgContent.TranscribedDTTM= '';
						}	
						// channelMap.put('TranscribedDTTM',objMsgContent.TranscribedDTTM);	
					// Document_Body_RTF
					} else if(child.localName().toString() == 'Document_Body_RTF') {
						if(child.toString().length>0) {
							objMsgContent.DocumentBodyRTF= child.toString();
						} else {
							objMsgContent.DocumentBodyRTF= '';
						}	
						// channelMap.put('DocumentBodyRTF',objMsgContent.DocumentBodyRTF);		
					}

				}
			}

			if(debug>0) { logger.error('DEBUG::'+funcDescription+' object: '+JSON.stringify(objMsgContent)); }
			// store objMsgContent in arrMsgContents
			if(chkNumeric==  true) arrMsgContents.push(objMsgContent);
		}
		intCount++;
		var chkNumeric= false;
	}
	if(debug>0) { logger.error('arrMsgContents.length: '+arrMsgContents.length); }
	channelMap.put('arrMsgContents',arrMsgContents);
	return arrMsgContents;
} // end function

function isNumeric(num){
  return !isNaN(num)
}

/**
	Function (createRootElement) : It will develope root element dynamically based on the incoming message. The EHR is expected
	to send this root element to the middleware and it can never be empty or null

	@param {String} rootElementTag - Header element of the message
	@return {XML element} - <PRPA_IN201309UV02 xmlns:xsi="http://www.w3.org/2001/XMLSchema-instance" xmlns="urn:hl7-org:v3" ITSVersion="XML_1.0">
*/
function createRootElement(rootElementTag) {

	var rootElement = new XML('<'+rootElementTag+'></'+rootElementTag+'>');
	rootElement.addNamespace(new Namespace('xsi', 'http://www.w3.org/2001/XMLSchema-instance'));
	rootElement['@xsi:schemaLocation']='urn:hl7-org:v3 ../../schema/HL7V3/NE2008/multicacheschemas/PRPA_IN201305UV02.xsd';
	rootElement['@ITSVersion']='XML_1.0';

	return rootElement;
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function createMessageAcceptAckCode(ackCode) {
	var 	accept_ack_code 	   = new XML('<acceptAckCode></acceptAckCode>');
	accept_ack_code['@code'] = ackCode;
	return accept_ack_code;
}

/**
	Function (createMessageCreationTime) : It  will develope the XML Element <creationTime value="20070810140900"/>, assuming the EHR
	Will always provide this value. (Empty or NULL scenario is hypothetical)

	@param {String} creationTime - The time at which the query is triggered, the EHR is expected to send this time
	in the format of YYYYMMddHHmmss
	@return {XML Element}  -  <creationTime value="20070810140900"/>
*/
function createMessageCreationTime(creationTime) {
    var message_created_date = new XML('<creationTime></creationTime>');
    message_created_date['@value'] = creationTime;
    return message_created_date;
}

/**
	Function (createMessageInteractionRoot) : It will develope <interactionId root="" extension=""/>, Assuming the EHR will always provide
	interactionId root got from HIE to middleware

	@param {String} oid - OID obtained from the HIE by EHR system
	@param {String} extension - The queryType paramter in JSON
	@return {XML Element} - <interactionId root="" extension=""/>
*/
function createMessageInteractionRoot(oid,extension) {
	var 	message_interaction_id = new XML('<interactionId></interactionId>');
	message_interaction_id['@root'] 	  = oid;
	message_interaction_id['@extension'] = extension;
	return message_interaction_id;
}

/**
	Function (createMessageProcessingCode) : It will develope <processingCode code="P"/> the values are
	P: production envionment
	T: testing environment

	@param No Parameter - This function Takes by default only 2 values either 
	P or E and it will be set in global Script Area

	@return {XML Element} - <processingCode code="P"/>
*/
function createMessageProcessingCode(processingCode) {
	var 	message_processing_code 	   = new XML('<processingCode></processingCode>');
	message_processing_code['@code'] = processingCode;
	return message_processing_code;
}

/**
	Function (createMessageProcessingModeCode) : It will develope <processingModeCode code="T"/> The middleware assumes that this data will always come
	and Empty or Null scenario is merely hypothetical

	@param {String} modeCode - The EHR is expected to send 4 different values here it can be T, I, A and R
	@return {XML Element} - <processingModeCode code="T"/>
*/
function createMessageProcessingModeCode(modeCode) {
	var 	message_processing_mode_code 		= new XML('<processingModeCode></processingModeCode>');
	message_processing_mode_code['@code'] 	= modeCode;
	return message_processing_mode_code;
}

/**
	Function (createMessageReceiver) : It will develope <receiver typeCode="RCV"> <device classCode="DEV"
	determinerCode="INSTANCE"> <id root="1.2.840.114350.1.13.999.234"/> </device> </receiver> The ID
	will be always provided by the EHR. Although the telecom parameter will be optional

	@param {String} receiverOid - This OID will be provided by EHR who obtained from HIE
	@param {Any} telecom - 
	@return {XML Element} - <receiver typeCode="RCV"> <device classCode="DEV"
		determinerCode="INSTANCE"> <id root="1.2.840.114350.1.13.999.234"/> </device> </receiver>
*/
// BEGIN: RECEIVER DEVICE/APPLICATION INFORMATION
function createMessageReceiver(receiverOid,telecomUse,telecom) {
  // <receiver typeCode="RCV">
	var receiver_information = new XML('<receiver></receiver>');
	receiver_information['@typeCode'] = "RCV";
  // <device classCode="DEV" determinerCode="INSTANCE">
	var device = new XML('<device></device>');
	device['@classCode'] = "DEV";
	device['@determinerCode'] = "INSTANCE";
  // <id root="1.2.840.114350.1.13.999.234"/>
	var receiver_root_id = new XML('<id></id>');
	receiver_root_id['@root'] = receiverOid;
	device.appendChild(receiver_root_id);
	//logger.debug(telecom)
	if(telecom!=''){
  // <telecom value="http://servicelocation/PDQuery"/>
	var receiver_telecom_value = new XML('<telecom></telecom>');
	receiver_telecom_value['@value'] = telecom;
	if(telecomUse!=''){
	receiver_telecom_value['@use'] = telecomUse;
	}
	device.appendChild(receiver_telecom_value);
	}
	receiver_information.appendChild(device);
  // APPEND: receiver information with header tag
	return receiver_information;
}

/**
	Function (createMessageRoot) : It will develope <id root="OID" extension="ext_value"/> (or) <id root = "GUID">

	@param {String} oidOrGuid - This parameter cannot be null, it will receive either OID or GUID value
	@param {String} extension - If this parameter is null then only id root with GUID will be generated
	@return {XML Element} -  <id root="OID" extension="ext_value"/> (or) <id root = "GUID">

*/
function createMessageRoot(oidOrGuid, ext_id) {
    var message_root_id = new XML('<id></id>');
    message_root_id['@root'] = oidOrGuid;
    // IF: the extension is provided then create extension attribute else not needed.
    if (ext_id != null) {
        message_root_id['@extension'] = ext_id;
    }

    return message_root_id;
}

/**
	Function (createMessageSender) : It will develope the complete sender information comprising of sender device OID
	and the OID provided to the clinic which uses the EMR 

	@param {String} deviceOid - This is the baby oid provided by the EHR upon registering with HL7
	@param {String} communityOid - OID used by the clinic or hospital which uses the same  EHR (optional)
	@return {XML Element} - complete sender information
*/

// BEGIN: SENDER APPLICATION/ DEVICE INFORMATION
function createMessageSender(deviceOid,senderName,communityOid,telecom,telecomUse) {
	// <sender typeCode="SND">
	var sender_information = new XML('<sender></sender>');
	sender_information['@typeCode'] = "SND";
	// <device classCode="DEV" determinerCode="INSTANCE">
	var device = new XML('<device></device>');
	device['@classCode'] = "DEV";
	device['@determinerCode'] = "INSTANCE";
	// <id root="1.2.840.114350.1.13.999.567"/>
	var sender_root_id = new XML('<id></id>');
	sender_root_id['@root']= deviceOid;
	device.appendChild(sender_root_id);
	// communityOid: Is optional field, if it does not come then representedOrganzation 
	// should not be printed
	if(communityOid!=''){
	// <asAgent classCode="AGNT">
	var asAgent = new XML('<asAgent></asAgent>');
	asAgent['@classCode'] = 'AGNT';
	// <representedOrganization classCode="ORG" determinerCode="INSTANCE">
	var representedOrganization = new XML('<representedOrganization></representedOrganization>');
	representedOrganization['@classCode'] = 'ORG';
	representedOrganization['@determinerCode'] = 'INSTANCE';
	// <id root="1.2.3" />
	var sender_home_community_id = new XML('<id></id>'); 
	sender_home_community_id['@root'] = communityOid;
	representedOrganization.appendChild(sender_home_community_id);
	if(senderName!=''){
	var sender_name = new XML('<name>'+senderName+'</name>');
	representedOrganization.appendChild(sender_name);
	}

	if(telecom!=''){
	var sender_telecom_value = new XML('<telecom></telecom>');
	sender_telecom_value['@value'] = telecom;
	if(telecomUse!=''){
	sender_telecom_value['@use'] = telecomUse;
	}
	representedOrganization.appendChild(sender_telecom_value);
	}

	asAgent.appendChild(representedOrganization);
	device.appendChild(asAgent);
	}

	sender_information.appendChild(device);
	// APPEND: receiver information with header tag
	return sender_information;
}

/**
	Function (returnDestinationChannelId) : Based on the incoming query type in the JSON from EHR, the function will route the data
	to respective channel ID

	@param {String} query_type - query_type (Message types based on ITI profiles for IHE)
	@return {String} returns ChannelID of deployed channels
*/
function returnDestinationChannelId(query_type) {

    var hie_query_method_iti_47 = ['PRPA_IN201305UV02', 'PRPA_IN201307UV02'];
    var hie_query_method_iti_45 = ['PRPA_IN201309UV02'];
    var hie_query_method_iti_9  = ['pixv2_iti_9'];
    var hie_query_method_iti_8  = ['pixv2_iti_8'];
    var hie_query_method_iti_47_cont = ['QUQI_IN000003UV01'];
    var channelID = '';
	    if (hie_query_method_iti_47.indexOf(query_type.toString()) >= 0) {
	        channelID = ChannelUtil.getDeployedChannelId('TRN_EHR_JSON_TO_ITI-47_PRPA_IN201305UV02');
	    } else if (hie_query_method_iti_45.indexOf(query_type.toString()) >= 0) {
	        channelID = ChannelUtil.getDeployedChannelId('TRN_EHR_JSON_TO_ITI-45_PRPA_IN201309UV02');
	    } else if (hie_query_method_iti_9.indexOf(query_type.toString()) >= 0) {
	        channelID = ChannelUtil.getDeployedChannelId('TRN_EHR_JSON_TO_ITI9-PIX_QUERY');
	    } else if (hie_query_method_iti_8.indexOf(query_type.toString()) >= 0) {
	        channelID = ChannelUtil.getDeployedChannelId('TRN_EHR_JSON_TO_ITI-8_PATIENT_ID_FEED');
	    }else if(hie_query_method_iti_47_cont.indexOf(query_type.toString())>=0){
	    	   channelID = ChannelUtil.getDeployedChannelId('TRN_EHR_JSON_TO_ITI-47_QUQI_IN000003UV01');
	    }
    return channelID;
}

/**
	Encounter - Process HTTP2xx Codes from JSON to HL7v2 - Encounter/Location Resource

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function encounterProcessHTTP2xxCodes(resp, pid) {

	if (resp.contains('200')) {

		var objEncounter= JSON.parse(new java.lang.String(FileUtil.decode(msg['Body'].toString()), "UTF-8"));
		var count= objEncounter.total;
		pv1['PV1.1']['PV1.2.1']= "N";
		if(count > 0) {
			// Checking only the first entry for that patient
			for (var i= 0;i <= objEncounter.total; i++) {

				if (keyExists(objEncounter.entry[i].resource.resourceType) && objEncounter.entry[i].resource.resourceType == "Encounter") {
						populateEncounter(pv1, objEncounter, i);
				} // end if 

				if (keyExists(objEncounter.entry[i].resource.resourceType) && objEncounter.entry[i].resource.resourceType == "Location") {
						populateLocation(pv1, objEncounter, i);
				} // end if 

			} // end for

		} // end if 

	} // end if - 200 OK

} // end if

/**
	Patient - Process HTTP2xx Codes from JSON to HL7v2 - Patient Resource
	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function processHTTP2xxCodes(resp, pid) {

	if (resp.contains('200')) {

		var objPatient= JSON.parse(new java.lang.String(FileUtil.decode(msg['Body'].toString()), "UTF-8"));
		var count= objPatient.total;
		if(count > 0) {
			// Checking only the first entry for that patient
			if (keyExists(objPatient.entry[0].resource.name)) {
				pid['PID.5']['PID.5.1']= extract(objPatient.entry[0].resource.name[0].family[0]);
				pid['PID.5']['PID.5.2']= extract(objPatient.entry[0].resource.name[0].given[0]);

				if (objPatient.entry[0].resource.name[0].given.length > 1) {
					pid['PID.5']['PID.5.3']= extract(objPatient.entry[0].resource.name[0].given);
				} // end if 

				pid['PID.7']['PID.7.1']= convertToHL7v2Date(extract(objPatient.entry[0].resource.birthDate));
				pid['PID.8']['PID.8.1']= convertToHL7v2Table(extract(objPatient.entry[0].resource.gender), "0001");

				if (keyExists(objPatient.entry[0].resource.telecom)) {
					pid['PID.13']['PID.13.1']= extract(objPatient.entry[0].resource.telecom[0].value);
				} // end if 

				if (keyExists(objPatient.entry[0].resource.address)) {
					pid['PID.11']['PID.11.1']= extract(objPatient.entry[0].resource.address[0].line);
					pid['PID.11']['PID.11.3']= extract(objPatient.entry[0].resource.address[0].city);
					pid['PID.11']['PID.11.4']= extract(objPatient.entry[0].resource.address[0].state);
					pid['PID.11']['PID.11.5']= extract(objPatient.entry[0].resource.address[0].postalCode);
					pid['PID.11']['PID.11.6']= extract(objPatient.entry[0].resource.address[0].country);

					if (keyExists(objPatient.entry[0].resource.address[0].type)) {
						pid['PID.11']['PID.11.7']= convertToHL7v2Table(extract(objPatient.entry[0].resource.address[0].type),"0190");
					} // end if 

				} // end if 

			} // end if 

		} // end if 

	} // end if - 200 OK

} // end if

/**
	Patient - Process HTTP4xx Codes from JSON to HL7v2 - Error 400

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function patientProcessHTTP4xxCodes(resp, pid) {

	if (resp.contains('400')) {

		var objError= JSON.parse(new java.lang.String(FileUtil.decode(msg['Body'].toString()), "UTF-8"));
		if (keyExists(objError.issue)) { 
			logger.error(extract(objError.issue[0].diagnostics));
		} // end if 
		pid['PID.5']['PID.5.7']= 'U';

	} // end if - 400 Error

} // end if

/**
	Patient - Process HTTP5xx Codes from JSON to HL7v2 - 500 Error

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function patientProcessHTTP5xxCodes(resp, pid) {

	if (resp.contains('500')) {

		var objError= JSON.parse(new java.lang.String(FileUtil.decode(msg['Body'].toString()), "UTF-8"));
		if (keyExists(objError.issue)) { 
			logger.error(extract(objError.issue[0].diagnostics));
		} // end if 
		pid['PID.5']['PID.5.7']= 'U';

	} // end if - 500 Error

} // end if

/** function(vendor,version,token)

  	- Desc: This function receives a code system code (string) and returns confidentiality array contains both the code and displayName

   	@param {String} code - this contains a code system code (string)
	@return {array} confidentiality - this array contains the Confidentiality Code and DisplayName (key,value) pair in the confidentialityCode array.
*/

var fhirObj = function(){

  var scriptName= 'fhirObj function';

  this.init = function(vendor,version,token){
  	var vendors = globalMap.get("FhirVendors");
  	var vendorObj = vendors[vendor];
  	if(vendorObj.name == 'Cerner'){
  		vendorObj.uname = token;
  	}
	var fhir = Packages.com.fhir.client.FhirFactory();
     if(version == '')
     	version = "DSTU2";

     versionURL = version.toLowerCase()+'_url';
     vendorObj.url = vendorObj[versionURL];
     this.vendorObj = fhir.initFhir(version, vendorObj.url, vendorObj.uname, vendorObj.pwd);

     if(debug>0) { logger.info(scriptName+"::DEBUG fhir version:"+version.toString()+" vendorObj.url: "+(vendorObj.url).toString()+" vendorObj.uname: "+(vendorObj.uname).toString()+" vendorObj.pwd: "+(vendorObj.pwd).toString()); }

     return this.vendorObj;
   };   
}

