			if(debug>0) { 
				logger.error('DEBUG::'+scriptName+' dbConn.'+operation+'('+sqlStatement+')'); 
			}
			var resultOperation= dbConn.executeCachedQuery(sqlStatement);
			var resultSize= resultOperation.size();

