/**
	Creates and executes database statements

	@param {Boolean} recreate - If true, the connection will always be closed and created anew.
	@return {DatabaseConnection} The database connection object.

	History:
	2022-08-24 JER - Modified moving the dbConn and the conditional (while or if-then) logic into the same parent if-then code block
	2022-08-23 JER - Modified the name from executeOperationMSSQL to executeSQLStatement
	2022-08-03 JER - Addedd the IF conditional to evaluate either the pre-existing query_verify_code or executing a different SQL stored procedure or SQL statement
	2022-07-31 JER - Modified executeOperationMSSQL function to evaluate the code_values.code_value and code_values.code_value_description fields
*/
// executeOperation('executeCachedQuery','EXEC 'query_verify_code('419199007')')
// function executeOperation(operation, expression, parameters) {
function executeSQLStatement(operation, sqlStatement, resultColumns) {
	var scriptName= 'executeSQLStatement';
	var dbConn = getMSSQLDatabaseConnection('true');
	var attempts = 0;
	var debug= 0;
	// package org.apache.commons.lang3.math.NumberUtils;
	var maxAttempts = org.apache.commons.lang3.math.NumberUtils.toInt($('sqlDBMaxRetries') + '', 0) + 1;

	//while (attempts < maxAttempts) {
	//	attempts++;

		// try {

			if(debug>0) { 
				logger.error('DEBUG::'+scriptName+' dbConn operation: '+operation+' sqlStatement: '+sqlStatement)
			}

			if(operation== 'SELECT') {
				var resultOperation= dbConn.executeCachedQuery(sqlStatement);
				var resultSize= resultOperation.size();

				if(debug>0) { logger.error('DEBUG::'+scriptName+' dbConn.resultOperation size: '+resultSize+' results: '+resultOperation); }

				while (resultOperation.next()) {
					// if resultColumns contains a string of column headers
					if((typeof(resultColumns)!== 'undefined')&&(resultColumns.toString().length>0)) {
						var i=0;
						var objResultOperation= new Object();
						objResultOperation['id']= i;
						for each(column in resultColumns) {
							objResultOperation[column]= resultOperation.getString(i);
							i++;
						} // end for each

						var returnResult= objResultOperation;
						if(debug>0) { 
							logger.error('DEBUG::'+scriptName+' executeCachedQuery specific COLUMNS SELECT returnResult: '+returnResult);
						} // end if
					// no resultColumns either undefined or no column headers string length using SELECT TOP 1 * ...
					} else {
						var returnResult= resultOperation.getString(1);
						if(debug>0) { 
							logger.error('DEBUG::'+scriptName+' executeCachedQuery SELECT returnResult: '+returnResult);
						} // end if
					} // end if 
				} // end while

			} else {
				var resultOperation= dbConn.executeUpdate(sqlStatement);
				if(debug>0) { logger.error('DEBUG::'+scriptName+' dbConn.resultOperation result: '+resultOperation); }

				if(resultOperation) {
					if(debug>0) { 
						logger.error('DEBUG::'+scriptName+' **** SQL SUCCESS ****: RESULT=' + resultOperation.toString());
					} // end if 
				} else {
					if(debug>0) { 
						logger.error('DEBUG::'+scriptName+' **** SQL FAILED  ****: RESULT=' + resultOperation.toString());
					} // end if 
				} // end if
				var returnResult= resultOperation;
				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' executeUpdate on INSERT returnResult: '+returnResult);
				} // end if

			} // end if 

		/*
		} finally {
			if (dbConn) {
				dbConn.close;
			}
		}
		*/

		/*
			if (attempts >= maxAttempts) {
				var errorMessage = 'Unable to execute statement.\n';
				errorMessage += 'Expression: ' + expression + '\n';
				if (parameters) {
					errorMessage += 'Parameters: ' + java.util.Arrays.asList(parameters).toString();
				}
				logger.error(errorMessage, e.javaException);
				throw e;
			}

			java.lang.Thread.sleep(1000);
		}
	} */

	if (dbConn) { dbConn.close; }
	return returnResult;
}

/**
	Creates and instantiates database connection from the global channel map, creating a new one if necessary.

	@param {Boolean} recreate - If true, the connection will always be closed and created anew.
	@return {DatabaseConnection} The database connection object.

	History:
	2022-07-31 JER - Modified executeOperationMSSQL function to evaluate the code_values.code_value and code_values.code_value_description fields
*/
function getMSSQLDatabaseConnection(recreate) {
	var scriptName= 'getMSSQLDatabaseConnection';
	var dbConn = $gc('dbConn');
	var debug= 0;

	if (!dbConn || recreate) {
		if (dbConn) {
			try {
				dbConn.close(); 	
			} catch (e) {}
		}

		if(debug>0) { logger.error('DEBUG::'+scriptName+' fmiMSSQLDriverString= '+$('fmiMSSQLDriverString')+' URL= '+$('fmiMSSQLUrl')+' userId= '+$('fmiMSSQLUserId')+' password= '+$('fmiMSSQLPassword')); }
		dbConn = DatabaseConnectionFactory.createDatabaseConnection($('fmiMSSQLDriverString'), $('fmiMSSQLUrl'), $('fmiMSSQLUserId'), $('fmiMSSQLPassword'));
		$gc('dbConn', dbConn);
	}

	return dbConn;
}

