/**
	Desc: This function retrieves HL7v2 HAPI formatted Date/Time Range (DR) data element values converted into JSON object for reference

	@param {String} element- this contains the raw HL7 element (string) (e.g. 19741225000000&20201001000000)
	@return {object} objPeriod- this contains the HL7 Date/Time Range (DR) element into a single objPeriod JSON object.
	(e.g. 
		"period": {
			"start": "1974-12-25",
			"end": "2020-10-01"
		}
	)
**/
function convertDateTimeRangeToPeriod(element) {

	var scriptName= 'convertDateTimeRangeToPeriod';
	var debug= 0;
	if (debug>0) { logger.error(scriptName+'::DEBUG element: '+element.toString()); }

	// define variables
	if (typeof(element.toString()) !== undefined) {

		var objPeriod= new Object();
		var period = {};

		// evaluate List
		var lstDateTimeRange= element.toString().toUpperCase().split("&");

		// DateTimeRange object contains two elements: Range Start Date/Time and Range End Date/Time
		for(i= 0;i<lstDateTimeRange.length;i++) {
			var subElement= lstDateTimeRange[i].toString();

			//  PID-11.12.1 Range Start Date/Time (TS)
			if(i== 0) {
				if(subElement.toString().length> 0) {
					var columnName= "start";
					period[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG period["+columnName+"] value: "+columnValue); }
				} // end if 
			} // end if 
			//  PID-11.12.2 Range End Date/Time (TS)
			if(i== 0) {
				if(subElement.toString().length> 0) {
					var columnName= "end";
					period[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG period["+columnName+"] value: "+columnValue); }
				} // end if 
			} // end if 
		} // end for 

		objPeriod= JSON.stringify(period); // convert to JSON
		if(debug>0) { 
			logger.error(scriptName+"::DEBUG period: "+JSON.stringify(period));
			logger.error(scriptName+'::DEBUG objPeriod: '+objPeriod); 
		}

		return objPeriod;
	} // end if 
} // end function

/**
	Desc: This function converts HL7v2 HAPI formatted Extended Address (XAD) data element to FHIR Address object

	@param {String} element- this contains the raw HL7 element (string) (e.g. 12345 WEST MAIN STREET^^CARMEL^IN^46032)
	@return {object} objAddress- this contains the HL7 Address (XAD) element into a single objAddress JSON object.
	(e.g. 
	{ "address" : [{
		"use" : "home",
		"type" : "both",
		"text" : "534 Erewhon St PeasantVille, Rainbow, Vic  3999",
		"line" : ["534 Erewhon St"],
		"city" : "PleasantVille",
		"district" : "Rainbow",
		"state" : "Vic",
		"postalCode" : "3999",
		"period" : {
			"start" : "1974-12-25",
			"stop" : "2020-03-07"
		}
	  },
	  "address" : [{
		"use" : "home",
		"type" : "both",
		"text" : "1123 Rockland Ave PeasantVille, Rainbow, Vic  3999",
		"line" : ["1123 Rockland Ave"],
		"city" : "PleasantVille",
		"district" : "Rainbow",
		"state" : "Vic",
		"postalCode" : "3999",
		"period" : {
			"start" : "1974-12-25"
		}
	  }]
	})
**/
function convertExtendedAddressToAddress(element) {

	var scriptName= 'convertExtendedAddressToAddress';
	var debug= 0;
	if (debug>0) { logger.error(scriptName+'::DEBUG element: '+element.toString()); }

	// define variables
	if (typeof(element.toString()) !== undefined) {

		var objAddress= new Object();
		var address = {};

		// evaluate List for Entity Identifier
		var lstExtendedAddress= element.toString().toUpperCase().split("^");

		// Entity Identity Identifier -> @DataModel
		for(i= 0;i<lstExtendedAddress.length;i++) {
			var subElement= lstExtendedAddress[i].toString();

			//  PID-11.1 Street Address (SAD) -> address.line
			if(i== 0) {
				if(subElement.toString().length> 0) {
					var columnName= "line";
					address[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG address["+columnName+"] value: "+columnValue); }
				}
			}
			// PID-11.2 Other Designation (ST) -> address.use
			if(i== 1) {
				if(subElement.toString().length> 0) {
					var columnName= "use";
					address[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG address["+columnName+"] value: "+columnValue); }
				}
			}
			// PID-11.3 City (ST) -> address.city
			if(i== 2) {
				if(subElement.toString().length> 0) {
					var columnName= "city";
					address[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG address["+columnName+"] value: "+columnValue); }
				}
			}
			// PID-11.4 State or Province (ST) -> address.state
			if(i== 3) {
				if(subElement.toString().length> 0) {
					var columnName= "state";
					address[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG address["+columnName+"] value: "+columnValue); }

				}
			}
			// PID-11.5 Zip or Postal Code (ST) -> address.postalCode
			if(i== 4) {
				if(subElement.toString().length> 0) {
					var columnName= "postalCode";
					address[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG address["+columnName+"] value: "+columnValue); }

				}
			}
			// PID-11.6 Country (ID) - address.country
			if(i== 5) {
				if(subElement.toString().length> 0) {
					var columnName= "country";
					address[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG address["+columnName+"] value: "+columnValue); }

				}
			}
			// PID-11.7 Address type (ID) -> address.type
			if(i== 6) {
				if(subElement.toString().length> 0) {
					var columnName= "type";
					address[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG address["+columnName+"] value: "+columnValue); }

				}
			}
			/*
			// PID-11.8 Other Geographic Designation (ST)
			if(i== 7) {
				if(subElement.toString().length> 0) {
					var columnName= "OtherGeographicDesignation";
					address[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG address["+columnName+"] value: "+columnValue); }

				}
			}
			*/			
			// PID-11.9 County/Parish Code (IS) -> address.district
			if(i== 8) {
				if(subElement.toString().length> 0) {
					var columnName= "district";
					address[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG address["+columnName+"] value: "+columnValue); }

				}
			}
			/*
			// PID-11.10 Census Tract (IS)
			if(i== 9) {
				if(subElement.toString().length> 0) {
					var columnName= "CensusTract";
					address[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG address["+columnName+"] value: "+columnValue); }

				}
			}
			// PID-11.11 Address Representation Code (IS)
			if(i== 10) {
				if(subElement.toString().length> 0) {
					var columnName= "AddressRepresentationCode";
					address[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG address["+columnName+"] value: "+columnValue); }

				}
			}
			*/
			// PID-11.12 Address Validity Range (DR) -> address.period.start or address.period.end
			if(i== 11) {
				if(subElement.toString().length> 0) {
					var columnName= "period";

					// call the convertDateTimeRangeToPeriod function
					var columnValue= convertDateTimeRangeToPeriod(subElement);
					address[columnName]= columnValue;

					if(debug>0) { logger.info(scriptName+"::DEBUG address["+columnName+"] value: "+columnValue); }
				}
			}
		} // end if 

		objAddress= JSON.stringify(address); // convert to JSON
		if(debug>0) { 
			logger.error(scriptName+"::DEBUG address: "+JSON.stringify(address));
			logger.error(scriptName+'::DEBUG objAddress: '+objAddress); 
		}

		return objAddress;
	}
}

/**
	Desc: This function retrieves HL7v2 HAPI formatted Extended Address (XAD) data element values converted into JSON object for reference

	@param {String} element- this contains the raw HL7 element (string) (e.g. 12345 WEST MAIN STREET^^CARMEL^IN^46032)
	@return {object} objAddress- this contains the HL7 Address (XAD) element into a single objAddress JSON object.
	(e.g. 
	{ Address {
		"StreetAddress"="12345 WEST MAIN STREET",
		"OtherDesignation"="RX",
		"City"="CARMEL",
		"StateOrProvince"="IN",
		"ZipOrPostalCode"="46032",
		"Country"="",
		"AddressType"="",
		"OtherGeographicDesignation"="",
		"CountyParishCode"="",
		"CensusTract"="",
		"AddressRepresentationCode"="",
		"AddressValidityRange"=""
	})
**/
function convertAddressToJSON(element) {

	var scriptName= 'convertAddressToJSON';
	var debug= 0;
	if (debug>0) { logger.error(scriptName+'::DEBUG element: '+element.toString()); }

	// define variables
	if (typeof(element.toString()) !== undefined) {

		var objAddress= new Object();
		var meta = {};

		// evaluate List for Entity Identifier
		var lstExtendedAddress= element.toString().toUpperCase().split("^");

		// Entity Identity Identifier -> @DataModel
		for(i= 0;i<lstExtendedAddress.length;i++) {
			var subElement= lstExtendedAddress[i].toString();

			//  PID-11.1 Street Address (SAD)
			if(i== 0) {
				if(subElement.toString().length> 0) {
					var columnName= "StreetAddress";
					meta[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+columnValue); }
				}
			}
			// PID-11.2 Other Designation (ST)
			if(i== 1) {
				if(subElement.toString().length> 0) {
					var columnName= "OtherDesignation";
					meta[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+columnValue); }
				}
			}
			// PID-11.3 City (ST)
			if(i== 2) {
				if(subElement.toString().length> 0) {
					var columnName= "City";
					meta[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+columnValue); }
				}
			}
			// PID-11.4 State or Province (ST)	
			if(i== 3) {
				if(subElement.toString().length> 0) {
					var columnName= "StateOrProvince";
					meta[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+columnValue); }

				}
			}
			// PID-11.5 Zip or Postal Code (ST)
			if(i== 4) {
				if(subElement.toString().length> 0) {
					var columnName= "ZipOrPostalCode";
					meta[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+columnValue); }

				}
			}
			// PID-11.6 Country (ID)
			if(i== 5) {
				if(subElement.toString().length> 0) {
					var columnName= "Country";
					meta[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+columnValue); }

				}
			}
			// PID-11.7 Address type (ID)
			if(i== 6) {
				if(subElement.toString().length> 0) {
					var columnName= "AddressType";
					meta[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+columnValue); }

				}
			}
			// PID-11.8 Other Geographic Designation (ST)
			if(i== 7) {
				if(subElement.toString().length> 0) {
					var columnName= "OtherGeographicDesignation";
					meta[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+columnValue); }

				}
			} 
			// PID-11.9 County/Parish Code (IS)
			if(i== 8) {
				if(subElement.toString().length> 0) {
					var columnName= "CountyParishCode";
					meta[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+columnValue); }

				}
			}
			// PID-11.10 Census Tract (IS)
			if(i== 9) {
				if(subElement.toString().length> 0) {
					var columnName= "CensusTract";
					meta[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+columnValue); }

				}
			}
			// PID-11.11 Address Representation Code (IS)
			if(i== 10) {
				if(subElement.toString().length> 0) {
					var columnName= "AddressRepresentationCode";
					meta[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+columnValue); }

				}
			}
			// PID-11.12 Address Validity Range (DR)
			if(i== 11) {
				if(subElement.toString().length> 0) {
					var columnName= "AddressValidityRange";

					// call the convertDateTimeRangeToJSON function
					var columnValue= convertDateTimeRangeToJSON(subElement);
					meta[columnName]= columnValue;

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+columnValue); }
				}
			}
		} // end if 

		objAddress= JSON.stringify(meta); // convert to JSON
		if(debug>0) { 
			logger.error(scriptName+"::DEBUG meta: "+JSON.stringify(meta));
			logger.error(scriptName+'::DEBUG objAddress: '+objAddress); 
		}

		return objAddress;
	}
}

/**
	Desc: This function retrieves HL7v2 HAPI formatted Coded Element (CE) to JSON data element values converted into JSON object for reference

	@param {String} element- this contains the raw HL7 element (string) (e.g. F-11380^CREATININE^I9^2148-5^CREATININE^LN)
	elementName - this contains the label or element name to be added to the JSON object
	@return {object} objCodedElement - this contains the HL7  Coded Element (CE) element into a single objExtendedTelecommunicationNumber JSON object.
	(e.g. 
		{ "Order Control Code Reason" {
			"Identifier"="F-11380",
			"Text"="QAM",
			"NameOfCodingSystem"="X3",
			"AlternateIdentifier"="",
			"AlternateText"="",
			"NameOfAlternateCodingSystem"=""
		})
	History:
	2022-08-26 JER - Substitute space character with no character
*/
function convertCodedElementToJSON(element) {

	var scriptName= 'convertCodedElementToJSON';
	var debug= 0;
	if (debug>0) { 
		logger.error(scriptName+'::DEBUG element: '+element.toString()); 
	}

	// define variables
	if (typeof(element.toString()) !== undefined) {

		var objCodedElement= new Object();
		var meta = {};

		var i= 0;

		// Person Location -> @DataModel
		for each (child in element.children()) {

			if(i== 0) {
				if(child.toString().length> 0) {
					var columnName= "Identifier";
					meta[columnName]= child.toString();

					// 2022-08-26 JER - Substitute space character with no character
					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString().replace(/\s/,'')); }
				}
			}
			if(i== 1) {
				if(child.toString().length> 0) {
					var columnName= "Text";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 2) {
				if(child.toString().length> 0) {
					var columnName= "NameOfCodingSystem";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 3) {
				if(child.toString().length> 0) {
					var columnName= "AlternateIdentifier";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString().replace(/\s/,'')); }
				}
			}
			if(i== 4) {
				if(child.toString().length> 0) {
					var columnName= "AlternateText";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 5) {
				if(child.toString().length> 0) {
					var columnName= "NameOfAlternateCodingSystem";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			i++;
		} // end if  

		objCodedElement= meta; // convert to JSON
		if(debug>0) { 
			logger.error(scriptName+"::DEBUG meta: "+JSON.stringify(meta));
			logger.error(scriptName+'::DEBUG objIdentifier: '+objCodedElement); 
		}

		return objCodedElement;
	}
}

/**
	Desc: This function retrieves HL7v2 HAPI formatted Coded With Exceptions (CWE) to JSON data element values converted into JSON object for reference

	@param {String} element- this contains the raw HL7 element (string) (e.g. F-11380^CREATININE^I9^2148-5^CREATININE^LN)
	elementName - this contains the label or element name to be added to the JSON object
	@return {object} objCodedWithExceptions - this contains the HL7  Coded With Exceptions (CWE) element into a single objCodedWithExceptions JSON object.
	(e.g. 
		{ "Repeat Pattern Code" {
			"Identifier"="F-11380",
			"Text"="QAM",
			"NameOfCodingSystem"="X3",
			"AlternateIdentifier"="",
			"AlternateText"="",
			"NameOfAlternateCodingSystem"="",
			"CodingSystemVersionId"="",
			"AlternateCodingSystemVersionId"="",
			"OriginalText"=""
		})

		"repeatPattern" : {
      		"RepeatPatternCode" : {
        			"Identifier" : "1D  "
      		},
      		"CalendarAlignment" : "1"
    		},
	Components: <identifier (ST)> ^ <text (ST)> ^ <name of coding system (IS)> ^ <alternate identifier (ST)> ^ <alternate text (ST)> ^ <name of alternate coding system (IS)> ^ <coding system version ID (ST)> ^ alternate coding system version ID (ST)> ^ <original text (ST)>
	History:
	2022-08-26 JER - Modify to remove all space characters from an element value using replace(/\s+/g,'')
*/
function convertCodedWithExceptionsToJSON(element) {

	var scriptName= 'convertCodedWithExceptionsToJSON';
	var debug= 0;
	if (debug>0) { logger.error(scriptName+'::DEBUG element: '+element.toString()); }

	// define variables
	if (typeof(element.toString()) !== undefined) {

		var objCodedWithExceptions= new Object();
		var meta = {};

		var i= 0;

		// Person Location -> @DataModel
		for each (child in element.children()) {

			if(i== 0) {
				if(child.toString().length> 0) {
					var columnName= "Identifier";
					// 2022-08-26 JER - Modify to remove all space characters from an element value using replace(/\s+/g,'')
					meta[columnName]= child.toString().replace(/\s+/g,'');

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 1) {
				if(child.toString().length> 0) {
					var columnName= "Text";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 2) {
				if(child.toString().length> 0) {
					var columnName= "NameOfCodingSystem";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 3) {
				if(child.toString().length> 0) {
					var columnName= "AlternateIdentifier";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 4) {
				if(child.toString().length> 0) {
					var columnName= "AlternateText";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 5) {
				if(child.toString().length> 0) {
					var columnName= "NameOfAlternateCodingSystem";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 6) {
				if(child.toString().length> 0) {
					var columnName= "CodingSystemVersionId";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 7) {
				if(child.toString().length> 0) {
					var columnName= "AlternateCodingSystemVersionId";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 8) {
				if(child.toString().length> 0) {
					var columnName= "OriginalText";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			i++;
		} // end if  

		objCodedWithExceptions= meta; // convert to JSON
		if(debug>0) { 
			logger.error(scriptName+"::DEBUG meta: "+JSON.stringify(meta));
			logger.error(scriptName+'::DEBUG objIdentifier: '+objCodedWithExceptions); 
		}

		return objCodedWithExceptions;
	}
}

/**
	Desc: This function retrieves HL7v2 HAPI formatted Composite Quantity Timing (CQT) to JSON data element values converted into JSON object for reference

	@param {String} element- this contains the raw HL7 element (string) (e.g. 1^QAM^X3)
	elementName - this contains the label or element name to be added to the JSON object
	@return {object} objCompositeQuantityTiming - this contains the HL7 Extended Composite Number and Name for Persons element into a single objCompositeQuantityTiming JSON object.
	(e.g. 
		{ "Quantity/Timing" {
			"Quantity"="1",
			"Interval"="QAM",
			"Duration"="X3"
		})
	@history
	2023-02-01 JER - Modified from using JSON.stringify to NOT using the JSON.stringify on the objCompositeQuantityTiming object	
	2022-12-27 JER - Modified from calling convertCompositeQuantityWithUnitsToJSON(orc['ORC.7']) to child
*/
function convertCompositeQuantityTimingToJSON(element) {

	var scriptName= 'convertCompositeQuantityTimingToJSON';
	var debug= 0;
