					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 5) {
				if(child.toString().length> 0) {
					var columnName= "Priority";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 6) {
				if(child.toString().length> 0) {
					var columnName= "Condition";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 7) {
				if(child.toString().length> 0) {
					var columnName= "Text";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 7) {
				if(child.toString().length> 0) {
					var columnName= "Conjunction";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 8) {
				if(child.toString().length> 0) {
					var columnName= "OrderingSequence";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 9) {
				if(child.toString().length> 0) {
					var columnName= "OccurrenceDuration";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 10) {
				if(child.toString().length> 0) {
					var columnName= "TotalOccurrences";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			i++;
		} // end if  

		// 2023-02-01 JER - Modified from using JSON.stringify to NOT using the JSON.stringify on the objCompositeQuantityTiming object
		// objCompositeQuantityTiming= JSON.stringify(meta); // convert to JSON
		objCompositeQuantityTiming= meta; // convert to JSON
		if(debug>0) { 
