		} // end if  

		objExtendedCompositeNameAndIdentificationNumberForOrganizations= meta; // convert to JSON
		if(debug>0) { 
			logger.error(scriptName+"::DEBUG meta: "+JSON.stringify(objExtendedCompositeNameAndIdentificationNumberForOrganizations));
			logger.error(scriptName+'::DEBUG objIdentifier: '+objExtendedCompositeNameAndIdentificationNumberForOrganizations); 
		}

		return objExtendedCompositeNameAndIdentificationNumberForOrganizations;
	}
}

/**	
	Desc: This function retrieves HL7v2 HAPI formatted convert Extended Person Name (XPN) to JSON data element values converted into JSON object for reference

	@param {String} element- this contains the raw HL7 element (string) (e.g. QS1^QS1^QS1^^^^^^QS1)
	elementName - this contains the label or element name to be added to the JSON object
	@return {object} objExtendedPersonName - this contains the HL7 Extended Person Name element into a single objExtendedPersonName JSON object.
	(e.g. 
		{ "ExtendedPersonName" {
			"FamilyName"="", (FN)
			"GivenName"="", (ST)
			"SecondAndFurtherGivenNamesOrInitials"="" (ST)
			"Suffix"="", (ST)
			"Prefix"="", (ST)
			"Degree"="", (IS)
			"NameTypeCode"="", (ID)
			"NameRepresentationCode"="", (ID)
			"NameContext"="", (CE)
			"NameValidityRange"="", (DR)
			"NameAssemblyOrder"="", (ID)
			"EffectiveDate"="", (TS)
			"ExpirationDate"="", (TS)
			"ProfessionalSuffix"="" (ST)
		})
**/
function convertExtendedPersonNameToJSON(element) {

	var scriptName= 'convertExtendedPersonNameToJSON';
	var debug= 0;
	if (debug>0) { logger.error(scriptName+'::DEBUG element: '+element.toString()); }

	// define variables
	if (typeof(element.toString()) !== undefined) {

		var objExtendedPersonName= new Object();
		var meta = {};
		var i= 0;

		// Entity Identity Identifier -> @DataModel
		for each (child in element.children()) {

			/* FamilyName (FN) */
			if(i== 0) {
				if(child.toString().length> 0) {
					var columnName= "FamilyName";
					meta[columnName]= child.toString();
					// meta[columnName]= convertCodedWithExceptionsToJSON(child);

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			/* GivenName (ST) */
			if(i== 1) {
				if(child.toString().length> 0) {
					var columnName= "GivenName";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			/* SecondAndFurtherGivenNamesOrInitials (ST) */
			if(i== 2) {
				if(child.toString().length> 0) {
					var columnName= "SecondAndFurtherGivenNamesOrInitials";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			/* Suffix (ST) */
			if(i== 3) {
				if(child.toString().length> 0) {
					var columnName= "Suffix";
					meta[columnName]= child.toString();
					// meta[columnName]= convertHierarchicDesignatorToJSON(child);

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			/* Prefix (ST) */
			if(i== 4) {
				if(child.toString().length> 0) {
					var columnName= "Prefix";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			/* Degree (IS) */
			if(i== 5) {
				if(child.toString().length> 0) {
					var columnName= "Degree";
					meta[columnName]= child.toString();
					// meta[columnName]= convertHierarchicDesignatorToJSON(child);

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			/* Name Type Code (ID) */
			if(i== 6) {
				if(child.toString().length> 0) {
					var columnName= "NameTypeCode";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			/* Name Representation Code (ID) */
			if(i== 7) {
				if(child.toString().length> 0) {
					var columnName= "NameRepresentationCode";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			/* Name Context (CE)*/
			if(i== 8) {
				if(child.toString().length> 0) {
					var columnName= "NameContext";
					// meta[columnName]= child.toString();
					meta[columnName]= convertCodedElementToJSON(child);

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			/* Name Validity Range (DR) */
			if(i== 9) {
				if(child.toString().length> 0) {
					var columnName= "NameValidityRange";
					meta[columnName]= child.toString();
					// meta[columnName]= convertCodedWithExceptionsToJSON(child);

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			/* Name Assembly Order (ID) */
			if(i== 10) {
				if(child.toString().length> 0) {
					var columnName= "NameAssemblyOrder";
					meta[columnName]= child.toString();
					// meta[columnName]= convertCodedWithExceptionsToJSON(child);

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			/* EffectiveDate (DT) */
			if(i== 11) {
				if(child.toString().length> 0) {
					var columnName= "EffectiveDate";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			/* ExpirationDate (DT) */
			if(i== 12) {
				if(child.toString().length> 0) {
					var columnName= "ExpirationDate";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			/* Professional Suffix (ST) */
			if(i== 12) {
				if(child.toString().length> 0) {
					var columnName= "ProfessionalSuffix";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			i++;
		} // end if  

		objExtendedPersonName= meta; // convert to JSON
		if(debug>0) { 
			logger.error(scriptName+"::DEBUG meta: "+JSON.stringify(objExtendedPersonName));
			logger.error(scriptName+'::DEBUG objIdentifier: '+objExtendedPersonName); 
		}

		return objExtendedPersonName;
	}
}

/**	
	Desc: This function retrieves HL7v2 HAPI formatted convert Extended Telecommunication Number (XTN) to JSON data element values converted into JSON object for reference

	@param {String} element- this contains the raw HL7 element (string) (e.g. QS1^QS1^QS1^^^^^^QS1)
	elementName - this contains the label or element name to be added to the JSON object
	@return {object} objExtendedTelecommunicationNumber - this contains the HL7 Extended Telecommunication Number (XTN) element into a single objExtendedTelecommunicationNumber JSON object.
	(e.g. 
		{ "ExtendedTelecommunicationNumber" {
			"TelephoneNumber="", (ST)
			"TelecommunicationUseCode"="", (ID)
			"TelecommunicationEquipmentType"="" (ID)
			"EmailAddress"="", (ST)
			"CountryCode"="", (NM)
			"AreaCityCode"="", (NM)
			"LocalNumber"="", (NM)
			"Extension"="", (NM)
			"AnyText"="", (ST)
			"ExtensionPrefix"="", (ST)
			"SpeedDialCode"="", (ST)
			"UnformattedTelephoneNumber"="" (ST)
		})
**/
function convertExtendedTelecommunicationNumberToJSON(element) {

	var scriptName= 'convertExtendedTelecommunicationNumberToJSON';
	var debug= 0;
	if (debug>0) { logger.error(scriptName+'::DEBUG element: '+element.toString()); }

	// define variables
	if (typeof(element.toString()) !== undefined) {

		var objExtendedTelecommunicationNumber= new Object();
		var meta = {};
		var i= 0;

		// Entity Identity Identifier -> @DataModel
		for each (child in element.children()) {

			/* TelephoneNumber (ST) */
			if(i== 0) {
				if(child.toString().length> 0) {
					var columnName= "TelephoneNumber";
					meta[columnName]= child.toString();
					// meta[columnName]= convertCodedWithExceptionsToJSON(child);

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			/* TelecommunicationUseCode (ID) */
			if(i== 1) {
				if(child.toString().length> 0) {
					var columnName= "TelecommunicationUseCode";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			/* TelecommunicationEquipmentType (ID) */
			if(i== 2) {
				if(child.toString().length> 0) {
					var columnName= "TelecommunicationEquipmentType";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			/* EmailAddress (ST) */
			if(i== 3) {
				if(child.toString().length> 0) {
					var columnName= "EmailAddress";
					meta[columnName]= child.toString();
					// meta[columnName]= convertHierarchicDesignatorToJSON(child);

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			/* CountryCode (NM) */
			if(i== 4) {
				if(child.toString().length> 0) {
					var columnName= "CountryCode";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			/* AreaCityCode (NM) */
			if(i== 5) {
				if(child.toString().length> 0) {
					var columnName= "AreaCityCode";
					meta[columnName]= child.toString();
					// meta[columnName]= convertHierarchicDesignatorToJSON(child);

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			/* LocalNumber (NM) */
			if(i== 6) {
				if(child.toString().length> 0) {
					var columnName= "LocalNumber";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			/* Extension (NM) */
			if(i== 7) {
				if(child.toString().length> 0) {
					var columnName= "Extension";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			/* AnyText (ST)*/
			if(i== 8) {
				if(child.toString().length> 0) {
					var columnName= "AnyText";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			/* ExtensionPrefix (ST) */
