			logger.error(scriptName+"::DEBUG meta: "+JSON.stringify(objExtendedTelecommunicationNumber));
			logger.error(scriptName+'::DEBUG objExtendedTelecommunicationNumber: '+objExtendedTelecommunicationNumber); 
