/**
	Desc: This function retrieves HL7v2 HAPI formatted Specimen Source (SPS) data element values converted into JSON object for reference

	@param {String} element- this contains the raw HL7 element (string) for Specimen Source SPS element
	@return {object} objSpecimenSource JSON - this contains the HL7 Specimen Source SPS element into a single objSpecimenSource JSON object.
	@created:  2022-12-30 JER
	(e.g. 
		{ "SpecimenSource" {
			"SpecimenSourceNameorCode"="", (CWE)
			"Additives"="", (CWE)
			"SpecimenCollectionMethod"="", (TX)
			"BodySize"="", (CWE)
			"SiteModifier"="", (CWE)
			"CollectionMethodModifierCode"="", (CWE)
			"SpecimenRole"="" (CWE)
		})
	Components: <SpecimenSourceNameorCode (CWE)> & <Additives (CWE)> & <SpecimenCollectionMethod (TX)> & <BodySize (CWE)> & <SiteModifier (CWE)> & <CollectionMethodModifierCode (CWE)> & <SpecimenRole (CWE)>

*/
function convertSpecimenSourceToJSON(element) {

	var scriptName= 'convertSpecimenSourceToJSON';
	var debug= 0;
	if (debug>0) { logger.error(scriptName+'::DEBUG element: '+element.toString()); }

	// define variables
	if (typeof(element.toString()) !== undefined) {

		var objSpecimenSource= new Object();
		var meta = {};

		var i= 0;

		// Specimen Source  -> @DataModel
		for each (child in element.children()) {

			if(i== 0) {
				// Specimen Source Code or Name (CWE)
				if(child.toString().length> 0) {
					var columnName= "SpecimenSourceCode";
					// call convertCodedWithExceptionsToJSON
					meta[columnName]= convertCodedWithExceptionsToJSON(child.toString());

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+convertCodedWithExceptionsToJSON(child.toString())); }
				}
			}
			if(i== 1) {
				// Additives (CWE)
				if(child.toString().length> 0) {
					var columnName= "Additives";
					// call convertCodedWithExceptionsToJSON
					meta[columnName]= convertCodedWithExceptionsToJSON(child.toString());

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+convertCodedWithExceptionsToJSON(child.toString())); }
				}
			}	
			if(i== 2) {
				// Specimen Collection Method (TX)
				if(child.toString().length> 0) {
					var columnName= "SpecimenCollectionMethod";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 3) {
				if(child.toString().length> 0) {
					// Body Site (CWE)
					var columnName= "BodySite";
					// call convertCodedWithExceptionsToJSON
					meta[columnName]= convertCodedWithExceptionsToJSON(child.toString());

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+convertCodedWithExceptionsToJSON(child.toString())); }

				}
			}
			if(i== 4) {
				if(child.toString().length> 0) {
					// Site Modifier (CWE)
					var columnName= "SiteModifier";
					// call convertCodedWithExceptionsToJSON
					meta[columnName]= convertCodedWithExceptionsToJSON(child.toString());

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+convertCodedWithExceptionsToJSON(child.toString())); }

				}
			}
			if(i== 5) {
				if(child.toString().length> 0) {
					// Collection Method Modifier Code (CWE)
					var columnName= "CollectionMethodModifierCode";
					// call convertCodedWithExceptionsToJSON
					meta[columnName]= convertCodedWithExceptionsToJSON(child.toString());

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+convertCodedWithExceptionsToJSON(child.toString())); }

				}
			}
			if(i== 6) {
				if(child.toString().length> 0) {
					// Specimen Role (CWE)
					var columnName= "SpecimenRole";
					// call convertCodedWithExceptionsToJSON
					meta[columnName]= convertCodedWithExceptionsToJSON(child.toString());

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+convertCodedWithExceptionsToJSON(child.toString())); }

				}
			}
			i++;
		} // end if  

		objSpecimenSource= meta; // convert to JSON
		if(debug>0) { 
			logger.error(scriptName+"::DEBUG meta: "+JSON.stringify(meta));
			logger.error(scriptName+'::DEBUG objIdentifier: '+objSpecimenSource); 
		}

		return objSpecimenSource;
	}
}

/**
	Desc: This function retrieves HL7v2 HAPI formatted Street Address (SAD) data element values converted into JSON object for reference

	@param {String} element- this contains the raw HL7 element (string) (e.g. 348 W Duarte road&Duarte&348)
	@return {object} objAddress- this contains the HL7 Street Address (SAD) element into a single objAddress JSON object.
	(e.g. 
	{ Address {
		"Street or Mailing Address"="348 W Duarte road",
		"Street Name"="Duarte",
		"Dwelling Number"="348"
	})
**/
function convertStreetAddressToJSON(element) {

	var scriptName= 'convertStreetAddressToJSON';
	var debug= 0;
	if (debug>0) { logger.error(scriptName+'::DEBUG element: '+element.toString()); }

	// define variables
	if (typeof(element.toString()) !== undefined) {

		var objStreetAddress= new Object();
		var meta = {};

		// evaluate List
		var lstStreetAddress= element.toString().toUpperCase().split("^");

		// Entity Identity Identifier -> @DataModel
		for(i= 0;i<lstStreetAddress.length;i++) {
			var subElement= lstStreetAddress[i].toString();

			//  PID-11.1.1 Street Or Mailing Address (ST)
			if(i== 0) {
				if(subElement.toString().length> 0) {
					var columnName= "StreetOrMailingAddress";
					meta[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+columnValue); }
				}
			}

			//  PID-11.1.2 Street Name (ST)
			if(i== 0) {
				if(subElement.toString().length> 0) {
					var columnName= "StreetName";
					meta[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+columnValue); }
				}
			}

			//  PID-11.1.3 Dwelling Number (ST)
			if(i== 0) {
				if(subElement.toString().length> 0) {
					var columnName= "DwellingNumber";
					meta[columnName]= subElement.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+columnValue); }
				}
			}

		} // end for 

		objStreetAddress= JSON.stringify(meta); // convert to JSON
		if(debug>0) { 
			logger.error(scriptName+"::DEBUG meta: "+JSON.stringify(meta));
			logger.error(scriptName+'::DEBUG objStreetAddress: '+objStreetAddress); 
		} // end if 

		return objStreetAddress;
	} // end if 

} // end function

/**
	Desc: This function retrieves HL7v2 HAPI formatted Timestamp (TS) to JSON data element values converted into JSON object for reference

	@param {String} element- this contains the raw HL7 Timestamp (TS) element (string) (e.g. 20140410094429^20140410214429.3+000)
	elementName - this contains the label or element name to be added to the JSON object
	@return {object} objTimestamp - this contains the HL7 Timestamp (TS) with percision into a single objTimestamp JSON object.
	(e.g. 
		{ "Date/Time of Transaction" {
			"Time"="20140410094429",
			"Degree of Precision"="20140410214429.3+000"
		})
*/
function convertTimestampToJSON(element) {

	var scriptName= 'convertTimestampToJSON';
	var debug= 0;
	if (debug>0) { logger.error(scriptName+'::DEBUG element: '+element.toString()); }

	// define variables
	if (typeof(element.toString()) !== undefined) {

		var objTimestamp= new Object();
		var meta = {};

		var i= 0;

		// Timestamp with Degree of Precision -> @DataModel
		for each (child in element.children()) {

			if(i== 0) {
				if(child.toString().length> 0) {
					var columnName= "Time";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 1) {
				if(child.toString().length> 0) {
					var columnName= "Degree of Precision";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			i++;
		} // end if  

		objTimestamp= meta; // convert to JSON
		if(debug>0) { 
			logger.error(scriptName+"::DEBUG meta: "+JSON.stringify(meta));
			logger.error(scriptName+'::DEBUG objIdentifier: '+objTimestamp);
		}

		return objTimestamp;
	}
}

/**
	Desc: This function retrieves HL7v2 HAPI formatted Timing Quantity (TQ) to JSON data element values converted into JSON object for reference

	@param {String} element- this contains the raw HL7 element (string) (e.g. F-11380^CREATININE^I9^2148-5^CREATININE^LN)
	elementName - this contains the label or element name to be added to the JSON object
	@return {object} objTimingQuantity - this contains the HL7 Timing Quantity (TQ) element into a single objTimingQuantity JSON object.
	(e.g. 
		{ "QuantityTiming" {
			"Quantity"="F-11380",
			"Interval"="QAM",
			"Duration"="X3",
			"StartDateTime"="",
			"EndDateTime"="",
			"Priority"="",
			"Condition"="",
			"Text"="",
			"Conjunction"="",
			"OrderSequencing"="",
			"OccurrenceDuration"="",
			"TotalOccurrences"=""
		})
	Components: <Quantity (CQ)> ^ <Interval (RI)> ^ <Duration (ST)> ^ <Start Date/Time (TS)> ^ <End Date/Time (TS)> ^ <Priority (ST)> ^ <Condition (ST)> ^ <Text (TX)> ^ <Conjunction (ID)> ^ <Order Sequencing (OSD)> ^ <Occurrence Duration (CE)> ^ <Total Occurrences (NM)>
**/
function convertTimingQuantityToJSON(element) {

	var scriptName= 'convertTimingQuantityToJSON';
	var debug= 0;
	if (debug>0) { logger.error(scriptName+'::DEBUG element: '+element.toString()); }

	// define variables
	if (typeof(element.toString()) !== undefined) {

		var objTimingQuantity= new Object();
		var meta = {};

		var i= 0;

		// Person Location -> @DataModel
		for each (child in element.children()) {

			if(i== 0) {
				if(child.toString().length> 0) {
					var columnName= "Quantity";
					// meta[columnName]= child.toString();
					meta[columnName]= convertCompositeQuantityWithUnitsToJSON(child);

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 1) {
				if(child.toString().length> 0) {
					var columnName= "Interval";
					// meta[columnName]= child.toString();
					meta[columnName]= convertRepeatIntervalToJSON(child);

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 2) {
				if(child.toString().length> 0) {
					var columnName= "Duration";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 3) {
				if(child.toString().length> 0) {
					var columnName= "StartDateTime";
					// meta[columnName]= child.toString();
					meta[columnName]= convertTimestampToJSON(child);

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 4) {
				if(child.toString().length> 0) {
					var columnName= "EndDateTime";
					//meta[columnName]= child.toString();
					meta[columnName]= convertTimestampToJSON(child);

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 5) {
				if(child.toString().length> 0) {
					var columnName= "Priority";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 6) {
				if(child.toString().length> 0) {
					var columnName= "Condition";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 7) {
				if(child.toString().length> 0) {
					var columnName= "Text";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 8) {
				if(child.toString().length> 0) {
					var columnName= "Conjunction";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 9) {
				if(child.toString().length> 0) {
					var columnName= "OrderSequencing";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 10) {
				if(child.toString().length> 0) {
					var columnName= "OccurrenceDuration";
					//meta[columnName]= child.toString();
					meta[columnName]= convertCodedElementToJSON(child);

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			if(i== 11) {
				if(child.toString().length> 0) {
					var columnName= "TotalOccurrences";
					meta[columnName]= child.toString();

					if(debug>0) { logger.info(scriptName+"::DEBUG meta["+columnName+"] value: "+child.toString()); }
				}
			}
			i++;
		} // end if  

		objTimingQuantity= meta; // convert to JSON
		if(debug>0) { 
			logger.error(scriptName+"::DEBUG meta: "+JSON.stringify(meta));
			logger.error(scriptName+'::DEBUG objIdentifier: '+objTimingQuantity); 
		}

		return objTimingQuantity;
	}
}

/**
	Desc:  this JS code template is to parse HL7v2 message PID segment the PID.3 patient identifier element and return a Javascript Array object contain PID.3.1, PID.3.4 and PID.3.5

	Filename: hl7_get_patient_identifier
	@param {String} pid object - This will contains the XML representation of the HL7v2 PID segment desired PID identifier element for the patient identifier (e.g. PID-2, PID-3, PID-4)
	@return {String} return - Return an array of patient identifier object containing the patient identifier (PID-3.1), patient identifier domain (PID-3.4), and patient identifier assigning authority (PID-3.5)
	E.G.   
		<PID.3><PID.3.1>1112223233</PID.3.1><PID.3.2></PID.3.2><PID.3.3></PID.3.3><PID.3.4>BJC</PID.3.4><PID.3.5>2.16.840.35553.312.331.1112223233</PID.3.5></PID.3>
		<PID.3><PID.3.1>99823204</PID.3.1><PID.3.2></PID.3.2><PID.3.3></PID.3.3><PID.3.4>EPIC</PID.3.4><PID.3.5>2.16.840.13663.23.17</PID.3.5></PID.3>
	History:

*/
function hl7_get_patient_identifier(pid) {

	var scriptName= 'hl7_get_patient_identifier'
	var debug= 0;
	var arrPatientIdentifiers= new Array();

	if(debug>0) { logger.error('DEBUG::'+scriptName+' pid typeof: '+typeof(pid)+' length: '+pid.length()+' pid identifier: '+pid); } 

	for(var i= 0;i<pid.length();i++) {

		var objPatientIdentifier= new Object();
		objPatientIdentifier.patientIdentifier= pid[i]['PID.3.1'].toString();
		objPatientIdentifier.patientIdentifierDomain= pid[i]['PID.3.4'].toString();
		objPatientIdentifier.identifierTypeCode= pid[i]['PID.3.5'].toString();

		if(debug>0) { 
			logger.error('DEBUG::'+scriptName+' pid['+i+'] typeof: '+typeof(pid[i])+' length: '+pid[i].length+' pid identifier: '+pid[i]); 
			logger.error('DEBUG::'+scriptName+' pid['+i+'] patientIdentifier: '+objPatientIdentifier.patientIdentifier); 
			logger.error('DEBUG::'+scriptName+' pid['+i+'] patientIdentifierDomain: '+objPatientIdentifier.patientIdentifierDomain); 
			logger.error('DEBUG::'+scriptName+' pid['+i+'] identifierTypeCode: '+objPatientIdentifier.identifierTypeCode); 			
		} 

		arrPatientIdentifiers.push(objPatientIdentifier);
	} // end for
	if(debug>0) { logger.error('DEBUG::'+scriptName+' return arrPatientIdentifiers: '+JSON.stringify(arrPatientIdentifiers)); }

	return arrPatientIdentifiers;
}

/**
	Desc:  this JS code template is to parse HL7v2 message DG1 segment and return a Javascript object

	Filename: json_dg1_diagnosis.js
	@param {String} orc - This will contains the XML representation of the HL7v2 DG1 egment
	@return {String} return - Returning the Javascript object representation of the conveted HL7v2 DG1 segment
	History:
	2023-09-09 JER - Created mirroring the json_patient_allergy code template as DG1 has similiar limited content provided from QS1 Pharmacy system as AL1 segment
*/
function json_dg1_diagnosis(dg1) {

	var scriptName= 'json_dg1_diagnosis';
	var debug= 0;

	var objDiagnosis= {};

	// DG1.1 - set Id
	if(dg1['DG1.1'].toString().length>0) objDiagnosis.setId= dg1['DG1.1']['DG1.1.1'].toString();

	// DG1.2 - Diagnosis Coding Method
	if(dg1['DG1.2'].toString().length>0) objDiagnosis.diagnosisCodingMethod= dg1['DG1.2']['DG1.2.1'].toString();

	// DG1.3 - Diagnosis Code (identifier(ST)^text(ST)^name of codeSystem(ID)^alternate Id(ST)^alternate text(ST)^name of alternate codeSystem(ID) )
	if(dg1['DG1.3'].toString().length>0) objDiagnosis.diagnosisCode= convertCodedElementToJSON(dg1['DG1.3']);

	// DG1.4 - Diagnosis Description (ST)
	if(dg1['DG1.4'].toString().length>0) objDiagnosis.diagnosisDescription= dg1['DG1.4']['DG1.4.1'].toString();

	// DG1.5 - Diagnosis Date/Time (TS)
	if(dg1['DG1.5'].toString().length>0) objDiagnosis.diagnosisDateTime= dg1['DG1.5']['DG1.5.1'].toString();

	// DG1.6 - Diagnosis Type (IS)
	if(dg1['DG1.6'].toString().length>0) objDiagnosis.diagnosisType= dg1['DG1.6'].toString();

	// DG1.7 - Major Diagnosis Category (identifier(ST)^text(ST)^name of codeSystem(ID)^alternate Id(ST)^alternate text(ST)^name of alternate codeSystem(ID) )
	if(dg1['DG1.7'].toString().length>0) objDiagnosis.majorDiagnosisCategory= convertCodedElementToJSON(dg1['DG1.7']);

	// DG1.8 - Diagnosis Related Group (identifier(ST)^text(ST)^name of codeSystem(ID)^alternate Id(ST)^alternate text(ST)^name of alternate codeSystem(ID) )
	if(dg1['DG1.8'].toString().length>0) objDiagnosis.diagnosisRelatedGroup= convertCodedElementToJSON(dg1['DG1.8']);

	// DG1.9 - DRG Approval Indicator (ID)
	if(dg1['DG1.9'].toString().length>0) objDiagnosis.drgApprovalIndicator= dg1['DG1.9'].toString();

	// DG1.10 - DRG Grouper Review Code (IS)
	if(dg1['DG1.10'].toString().length>0) objDiagnosis.drgGrouperReviewCode= dg1['DG1.10'].toString();

	if(debug>0) {
		logger.error(scriptName+':DEBUG:: objDiagnosis= '+objDiagnosis);
		logger.error(scriptName+':DEBUG:: stringify(objDiagnosis)= '+JSON.stringify(objDiagnosis));
	}

	channelMap.put('Diagnosis',JSON.stringify(objDiagnosis));
	return objDiagnosis;

}

/**
	Desc:  this JS code template is to parse HL7v2 message IN1 segment and return a Javascript object

	Filename: json_in1_insurance.js
	@param {String} orc - This will contains the XML representation of the HL7v2 IN1 egment
	@return {String} return - Returning the Javascript object representation of the conveted HL7v2 IN1 segment
	History:
	2023-09-09 JER - Created mirroring the json_dg1_diagnosis code template as IN1 has similiar limited content provided from QS1 Pharmacy system as IN1 segment
*/
function json_in1_insurance(in1) {

	var scriptName= 'json_in1_insurance';
	var debug= 0;

	var objInsurance= {};

	// IN1.1 - set Id (SI)
	if(in1['IN1.1'].toString().length>0) objInsurance.setId= in1['IN1.1']['IN1.1.1'].toString();

	// IN1.2 - Insurance Plan Id (CE)
	if(in1['IN1.2'].toString().length>0) objInsurance.insurancePlanId= convertCodedElementToJSON(in1['IN1.2']);

	// IN1.3 - Insurance Company Id (CX)
	if(in1['IN1.3'].toString().length>0) objInsurance.insuranceCompanyId= convertExtendedCompositeIDWithCheckDigitToJSON(in1['IN1.3']);

	// IN1.4 - Insurance Company Name (XCN)
	if(in1['IN1.4'].toString().length>0) objInsurance.insuranceCompanyName= convertExtendedCompositeIDNumberAndNameToJSON(in1['IN1.4']);

	// IN1.5 - Insurance Company Address (XAD)
	if(in1['IN1.5'].toString().length>0) objInsurance.insuranceCompanyAddress= convertExtendedAddressToJSON(in1['IN1.5']);

	// IN1.6 - Insurance Co Contact Person (XPN)
	if(in1['IN1.6'].toString().length>0) objInsurance.insuranceCoContactPerson= convertExtendedPersonNameToJSON(in1['IN1.6']);

	// IN1.7 - Insurance Co Phone Number (XTN)
	if(in1['IN1.7'].toString().length>0) objInsurance.insuranceCoPhoneNumber= convertExtendedTelecommunicationNumberToJSON(in1['IN1.7']);

	// IN1.8 - Group Number (ST)
	if(in1['IN1.8'].toString().length>0) objInsurance.groupNumber= in1['IN1.8']['IN1.8.1'].toString();

	// IN1.9 - Group Name (XCN)
	if(in1['IN1.9'].toString().length>0) objInsurance.groupName= convertExtendedCompositeIDNumberAndNameToJSON(in1['IN1.9']);

	// IN1.10 - Insured Group Employee ID (CX)
	if(in1['IN1.10'].toString().length>0) objInsurance.insuredGroupEmployeeId= convertExtendedCompositeIDWithCheckDigitToJSON(in1['IN1.10']);

	// IN1.11 - Insured Group Employee Name (XON)
	if(in1['IN1.11'].toString().length>0) objInsurance.insuredGroupEmployeeId= convertExtendedCompositeNameAndIdentificationNumberForOrganizationsToJSON(in1['IN1.11']);

	// IN1.12 - Plan Effective Date (DT)
	if(in1['IN1.12'].toString().length>0) objInsurance.planEffectiveDate= in1['IN1.12']['IN1.12.1'].toString();

	// IN1.13 - Plan Expiration Date (DT)
	if(in1['IN1.13'].toString().length>0) objInsurance.planExpirationDate= in1['IN1.13']['IN1.13.1'].toString();

	// IN1.17 - Insured Relationship to Patient (CE)
	if(in1['IN1.17'].toString().length>0) objInsurance.insuredRelationshipToPatient= in1['IN1.17']['IN1.17.1'].toString();

	// IN1.20 - Assignment of Benefits (IS)
	if(in1['IN1.20'].toString().length>0) objInsurance.assignmentOfBenefits= in1['IN1.20']['IN1.20.1'].toString();

	// IN1.22 - Coordination of Benefit Priority (ST)
	if(in1['IN1.22'].toString().length>0) objInsurance.benefitPriority= in1['IN1.22']['IN1.22.1'].toString();

	// IN1.36 - Policy Number (ST)
	if(in1['IN1.22'].toString().length>0) objInsurance.policyNumber= in1['IN1.36']['IN1.36.1'].toString();

	if(debug>0) {
		logger.error(scriptName+':DEBUG:: objInsurance= '+objInsurance);
		logger.error(scriptName+':DEBUG:: stringify(objInsurance)= '+JSON.stringify(objInsurance));
	}

	channelMap.put('Diagnosis',JSON.stringify(objInsurance));
	return objInsurance;

}

/**
	Desc:  this JS code template is to parse HL7v2 MRG segment and return a Javascript object

	@param {String} mrg - This will contains the XML representation of the HL7v2 MRG segment

	@return {String} return - Returning the Javascript object representation of the conveted HL7v2 MRG segment
	History:
	2022-02-28 jer - Modified to include the MRG-7.1 lastName and MRG-7.2 firstName properties to merge object

*/
function json_mrg_patient(mrg) {

	var scriptName= 'json_mrg_patient';
	/*
	logger.error(scriptName+':DEBUG:: mrg= '+mrg);
	*/
	var mergePatient= {};

	for each(mrg1 in mrg['MRG.1']) {

		var pidNum = mrg1['MRG.1.1'].toString();
		var entity = mrg1['MRG.1.4'].toString();
		// 2022-02-22 jer - verify if PID.3.5 exists if not is undefined
		if(mrg1['MRG.1.5'].toString().length>0) {
			var IDCode = mrg1['MRG.1.5'].toString();
		}

		//logger.error(scriptName+':DEBUG:: typeof(IDCode)= '+typeof(IDCode));

		// 2022-02-22 jer - verify if undefined or defined with value of MRN
		if((typeof(IDCode)== 'undefined') || (IDCode.indexOf('MRN')>-1)) {
			if(entity != null) {
				mergePatient.mrn= pidNum;
				mergePatient.mrnDomain= entity;
			}
		}
		else if (IDCode.indexOf("MR")>-1) {
			if(entity != null) {
				mergePatient.mpi= pidNum;
				mergePatient.mpiDomain= entity;
			}
		}
	}

	// 2022-02-28 jer - Modified to include the MRG-7.1 lastName and MRG-7.2 firstName properties to merge object
	mergePatient.firstName= 	mrg['MRG.7']['MRG.7.2'].toString();
	mergePatient.middleName= mrg['MRG.7']['MRG.7.3'].toString();
	mergePatient.lastName= 	mrg['MRG.7']['MRG.7.1'].toString();

	// logger.error(scriptName+':DEBUG:: mergePatient= '+JSON.stringify(mergePatient));
	return mergePatient;

}

/**
	Desc:  this JS code template is to parse HL7v2 MSH header segment and return a Javascript object

	@param {String} msh - This will contains the XML representation of the HL7v2 MSH header segment
	@param {String} patient - This will contains the XML representation of the Javascript patient object

	@return {String} return - Returning the Javascript object representation of the conveted HL7v2 MSH segment
	History:
**/
function json_msh_header(msh) {

	var scriptName= 'json_msh_header';
	var debug= 0;
	if(debug>0) { logger.error(scriptName+':DEBUG:: msh= '+msh); }

	var header= {};

	header.sendingApplication= 		msh['MSH.3']['MSH.3.1'].toString();
	header.sendingFacility= 			msh['MSH.4']['MSH.4.1'].toString();
	header.receivingApplication= 		msh['MSH.5']['MSH.5.1'].toString();
	header.receivingFacility= 		msh['MSH.6']['MSH.6.1'].toString();
	header.messageDateTime=			msh['MSH.7']['MSH.7.1'].toString();
	header.messageType= 			msh['MSH.9']['MSH.9.1'].toString();
	header.eventType= 				msh['MSH.9']['MSH.9.2'].toString();
	header.messageControlId=			msh['MSH.10']['MSH.10.1'].toString();
	header.processingId=			msh['MSH.11']['MSH.11.1'].toString();
	header.versionId=				msh['MSH.12']['MSH.12.1'].toString();

	if(debug>0) { logger.error(scriptName+':DEBUG:: header= '+JSON.stringify(header)); }
	return header;

}

/**
	Desc:  this JS code template is to parse HL7v2 message OBR segment and return a Javascript object

	Filename: json_obr_observation_request.js
	@param {String} obr - This will contains the XML representation of the HL7v2 OBR segment
	@return {String} return - Returning the Javascript object representation of the conveted HL7v2 OBR segment
	History:
	2023-01-04 JER - Modified to evaluate the OBR.2.1 field to determine the type of order as order_type

*/
function json_obr_observation_request(obr) {

	var scriptName= 'json_obr_observation_request';
	var debug= 0;
	/*
	logger.error(scriptName+':DEBUG:: obr= '+obr);;
	*/
	var objObservationRequest= {};

	// OBR.1 - set Id (ID)
	// if(obr['OBR.1'].toString().length>0) objObservationRequest.setId= convertTimingQuantityToJSON(obr['OBX.1']);
	if(obr['OBR.1'].toString().length>0) objObservationRequest.setId= obr['OBR.1']['OBR.1.1'].toString();

	// OBR.2 - Placer Order Number (EI)
	// if(obr['OBR.2'].toString().length>0) objObservationRequest.placerOrderNumber= convertCodedElementToJSON(obr['OBR.2']);
	if(obr['OBR.2'].toString().length>0) objObservationRequest.placerOrderNumber= convertEntityIdentifierToJSON(obr['OBR.2']);

	// 2023-01-04 JER - Modified to evaluate the OBR.2.1 field to determine the type of order as order_type
	if(obr['OBR.2']['OBR.2.1'].toString()== 'RAD') {
		objObservationRequest.orderType= 'Radiology';
	} else if(obr['OBR.2']['OBR.2.1'].toString()== 'LAB') {
		objObservationRequest.orderType= 'Laboratory'	
	} else if(obr['OBR.2']['OBR.2.1'].toString()== 'LAB-SPM') {
		objObservationRequest.orderType= 'Laboratory-Specimen';
	} else {
		objObservationRequest.orderType= 'Common Order';
	}

	// OBR.3 - Filler Order Number (EI)
	// CE - Observation Identifier (identifier^text^name of codeSystem^alternate Id^alternate text^name of alternate codeSystem)
	if(obr['OBR.3'].toString().length>0) objObservationRequest.fillerOrderNumber= convertEntityIdentifierToJSON(obr['OBR.3']);

	// OBR.4 - Universal Order Service Identifier (CE)
	if(obr['OBR.4'].toString().length>0) objObservationRequest.orderServiceIdentifier= convertCodedElementToJSON(obr['OBR.4']);

	// OBR.5 - Priority (ID)
	if(obr['OBR.5'].toString().length>0) objObservationRequest.priorityIdentifier= obr['OBR.5']['OBR.5.1'].toString();

	// OBR.6 - Requested DateTime (TS)
	if(obr['OBR.6'].toString().length>0) objObservationRequest.requestedDateTime= convertTimestampToJSON(obr['OBR.6']);

	// OBR.7 - Observed DateTime (TS)
	if(obr['OBR.7'].toString().length>0) objObservationRequest.observedDateTime= convertTimestampToJSON(obr['OBR.7']);

	// OBR.8 - Observed End DateTime (TS)
	if(obr['OBR.8'].toString().length>0) objObservationRequest.observedEndDateTime= convertTimestampToJSON(obr['OBR.8']);

	// OBR.9 - Collection Volume (CQ)
	if(obr['OBR.9'].toString().length>0) objObservationRequest.collectionVolume= convertCompositeQuantityWithUnitsToJSON(obr['OBR.9']);

	// OBR.10 - Collector Identifier (XCN)
	if(obr['OBR.10'].toString().length>0) objObservationRequest.collectorIdentifier= convertExtendedCompositeIDNumberAndNameToJSON(obr['OBR.10']);

	// OBR.11 - Specimen Action Code (ID)
	if(obr['OBR.11'].toString().length>0) objObservationRequest.specimenActionCode= obr['OBR.11']['OBR.11.1'].toString();

	// OBR.12 - Danger Code (CE)
	if(obr['OBR.12'].toString().length>0) objObservationRequest.dangerCode= convertCodedElementToJSON(obr['OBR.12']);

	// OBR.13 - Relevant Clinical Information (ST)
	if(obr['OBR.13'].toString().length>0) objObservationRequest.relevantClinicalInformation= obr['OBR.13']['OBR.13.1'].toString();

	// OBR.14 - Specimen Received DateTime (TS)
	if(obr['OBR.14'].toString().length>0) objObservationRequest.specimenReceivedDateTime= convertTimestampToJSON(obr['OBR.14']);

	// OBR.15 - Specimen Source (SPS)
	if(obr['OBR.15'].toString().length>0) objObservationRequest.producersId= convertSpecimenSourceToJSON(obr['OBR.15']);

	// convertExtendedCompositeIDNumberAndNameToJSON
	// OBR.16 - Ordering Provider (XCN)
	if(obr['OBR.16'].toString().length>0) objObservationRequest.orderingProvider= convertExtendedCompositeIDNumberAndNameToJSON(obr['OBR.16']);

	// convertExtendedTelecommunicationNumberToJSON
	// OBR.17 - Order Callback Phone Number (XTN)
	if(obr['OBR.17'].toString().length>0) objObservationRequest.orderingProvider= convertExtendedTelecommunicationNumberToJSON(obr['OBR.17']);

	// OBR.18 - Placer Field 1 (ST)
	if(obr['OBR.18'].toString().length>0) objObservationRequest.placerField1= obr['OBR.18']['OBR.18.1'].toString();

	// OBR.19 - Placer Field 2 (ST)
	if(obr['OBR.19'].toString().length>0) objObservationRequest.placerField2= obr['OBR.19']['OBR.19.1'].toString();

	// OBR.20 - Filler Field 1 (ST)
	if(obr['OBR.20'].toString().length>0) objObservationRequest.fillerField1= obr['OBR.20']['OBR.20.1'].toString();

	// OBR.21 - Filler Field 2 (ST)
	if(obr['OBR.21'].toString().length>0) objObservationRequest.fillerField2= obr['OBR.21']['OBR.21.1'].toString();

	// OBR.22 - Results Report Status Change DateTime (TS)
	if(obr['OBR.22'].toString().length>0) objObservationRequest.resultsReportStatusChange= convertTimestampToJSON(obr['OBR.22']);

	// OBR.25 - Result Status (ID)
	if(obr['OBR.25'].toString().length>0) objObservationRequest.resultStatus= obr['OBR.25']['OBR.25.1'].toString();

	// OBR.27 - Quantity/Timing (TQ)
	// convertQuantityTimingToJSON
	if(obr['OBR.27'].toString().length>0) objObservationRequest.quantityTiming= convertQuantityTimingToJSON(obr['OBR.27']);

	if(debug>0) {
		logger.error(scriptName+':DEBUG:: objObservationRequest= '+objObservationRequest);
		logger.error(scriptName+':DEBUG:: observationRequest= '+JSON.stringify(objObservationRequest));
	}

	channelMap.put('observationRequest',JSON.stringify(objObservationRequest));
	return objObservationRequest;

}

/**
	Desc:  this JS code template is to parse HL7v2 message OBX segment and return a Javascript object

	Filename: json_obx_observation.js
	@param {String} obx - This will contains the XML representation of the HL7v2 OBX segment
	@return {String} return - Returning the Javascript object representation of the conveted HL7v2 OBX segment
	Created: 2024-01-15 JER
	History:
	2024-01-15 JER - Created modeled after json_patient_allergy function
*/
function json_obx_observation(obx) {

	var scriptName= 'json_obx_observation';
	var debug= 0;
	var objObservation= {};

	// OBX.1 - set Id
	if(obx['OBX.1'].toString().length>0) objObservation.setId= obx['OBX.1']['OBX.1.1'].toString();

	// OBX.2 - Value Type
	if(obx['OBX.2'].toString().length>0) objObservation.valueType= obx['OBX.2']['OBX.2.1'].toString();

	// OBX.3 - Observation Identifier CE (identifier^text^name of codeSystem^alternate Id^alternate text^name of alternate codeSystem)
	if(obx['OBX.3'].toString().length>0) objObservation.observationIdentifier= convertCodedElementToJSON(obx['OBX.3']);

	// OBX.4 - Observation Substitution ID (ST)
	if(obx['OBX.4'].toString().length>0) objObservation.substitutionID= obx['OBX.4']['OBX.4.1'];

	// OBX.5 - Observation Value (ST)
	if(obx['OBX.5'].toString().length>0) objObservation.observationValue= obx['OBX.5']['OBX.5.1'];

	// OBX.6 - Observation Units CE (identifier^text^name of codeSystem^alternate Id^alternate text^name of alternate codeSystem)
	if(obx['OBX.6'].toString().length>0) objObservation.units= convertCodedElementToJSON(obx['OBX.6']);

	// OBX.11 - Observation Result Status
	if(obx['OBX.11'].toString().length>0) objObservation.resultStatus= obx['OBX.11']['OBX.11.1'];

	// OBX.14 - Date/Time of Observation
	if(obx['OBX.14'].toString().length>0) objObservation.resultStatus= obx['OBX.14']['OBX.14.1'];

	if(debug>0) {
		logger.error(scriptName+':DEBUG:: objObservation= '+objObservation);
		logger.error(scriptName+':DEBUG:: observation= '+JSON.stringify(objObservation));
	}

	channelMap.put('Observation',JSON.stringify(objObservation));
	return objObservation;

}

/**
	Desc:  this JS code template is to parse HL7v2 message OBX segment and return a Javascript object

	Filename: json_obx_observation_result.js
	@param {String} orc - This will contains the XML representation of the HL7v2 OBX segment
	@return {String} return - Returning the Javascript object representation of the conveted HL7v2 OBX segment
	History:
	2022-12-27 JER - Modified function name from json_obx_observation to json_obx_observation_result
*/
function json_obx_observation_result(obx) {

	var scriptName= 'json_obx_observation_result';
	var debug= 0;
	/*
	logger.error(scriptName+':DEBUG:: obx= '+obx);;
	*/
	var objObservation= {};

	// OBX.1 - set Id
	// if(obx['OBX.1'].toString().length>0) objObservation.setId= convertTimingQuantityToJSON(obx['OBX.1']);
	if(obx['OBX.1'].toString().length>0) objObservation.setId= obx['OBX.1']['OBX.1.1'].toString();

	// OBX.2 - Value Type
	// if(obx['OBX.2'].toString().length>0) objObservation.valueType= convertCodedElementToJSON(obx['OBX.2']);
	if(obx['OBX.2'].toString().length>0) objObservation.valueType= obx['OBX.2']['OBX.2.1'].toString();

	// OBX.3 - Observation Identifier (identifier^text^name of codeSystem^alternate Id^alternate text^name of alternate codeSystem)
	if(obx['OBX.3'].toString().length>0) objObservation.observationIdentifier= convertCodedElementToJSON(obx['OBX.3']);

	// OBX.4 - Observation Sub-Id
	if(obx['OBX.4'].toString().length>0) objObservation.observationSubId= obx['OBX.4']['OBX.4.1'].toString();

	// OBX.5 - Observation Value
	if(obx['OBX.5'].toString().length>0) objObservation.observationValue= obx['OBX.5']['OBX.5.1'].toString();

	// OBX.6 - Units
	if(obx['OBX.6'].toString().length>0) objObservation.units= convertCodedElementToJSON(obx['OBX.6']);

	// OBX.7 - References Range
	if(obx['OBX.7'].toString().length>0) objObservation.referencesRange= obx['OBX.7']['OBX.7.1'].toString();

	// OBX.8 - Abnormal Flags
	if(obx['OBX.8'].toString().length>0) objObservation.deliverToLocation= obx['OBX.8']['OBX.8.1'].toString();

	// OBX.9 - Probability
	if(obx['OBX.9'].toString().length>0) objObservation.probability= obx['OBX.9']['OBX.9.1'].toString();

	// OBX.10 - Nature Of Abnormal Test
	if(obx['OBX.10'].toString().length>0) objObservation.natureOfAbnormalTest= obx['OBX.10']['OBX.10.1'].toString();

	// OBX.11 - Observation Result Status
	if(obx['OBX.11'].toString().length>0) objObservation.observationResultStatus= obx['OBX.11']['OBX.11.1'].toString();

	// OBX.12 - Effective Date of Reference Range
	if(obx['OBX.12'].toString().length>0) objObservation.effectiveDateReferenceRange= convertTimestampToJSON(obx['OBX.12']);

	// OBX.13 - User Defined Access Checks
	if(obx['OBX.13'].toString().length>0) objObservation.userDefinedAccessChecks= obx['OBX.13']['OBX.13.1'].toString();

	// OBX.14 - Observation Date/Time
	if(obx['OBX.14'].toString().length>0) objObservation.observationDateTime= convertTimestampToJSON(obx['OBX.14']);

	// OBX.15 - Producers Id (CE)
	if(obx['OBX.15'].toString().length>0) objObservation.producersId= convertCodedElementToJSON(obx['OBX.15']);

	if(debug>0) {
		logger.error(scriptName+':DEBUG:: objObservation= '+objObservation);
		logger.error(scriptName+':DEBUG:: commonOrder= '+JSON.stringify(objObservation));
	}

	// channelMap.put('PharmacyEncodedOrder',JSON.stringify(objObservation));
	return objObservation;

}

/**
	Desc:  this JS code template is to parse HL7v2 ORC segment and return a Javascript object

	@param {String} orc - This will contains the XML representation of the HL7v2 ORC segment
	@return {String} return - Returning the Javascript object representation of the conveted HL7v2 ORC segment
	History:
	2023-02-01 jer - Modified from calling convertCompositeQuantityWithUnitsToJSON to convertCompositeQuantityTimingToJSON() function
	2023-01-20 JER - Modified to evaluate the provided order control codes to align with a description value
	2023-01-02 JER - Added enterersLocation orc[ORC.13]
	2022-12-31 jer - Modified the declaration of orderStatus from orc['ORC.5'] to orc['ORC.5']['ORC.5.1']
	2022-04-07 jer - Created from json_pv1_visit(pv1) method to populate visit centric content
*/
function json_orc_common_order(orc) {

	var scriptName= 'json_orc_common_order';
	var debug= 0;
	/*
	logger.error(scriptName+':DEBUG:: orc= '+orc);;
	*/
	var objCommonOrder= {};

	/* 2023-01-20 JER - Modified to evaluate the provided order control codes to align with a description value */
	if(orc['ORC.1'].toString().length>0) {
		var orderControl= orc['ORC.1']['ORC.1.1'].toString();

		switch (orderControl) {
  			case 'CA':
    				var strOrderControl= 'CANCELLED';
    				break;
  			case 'DC':
  				var strOrderControl= 'DISCONTINUED';
  				break;
  			case 'DR':
    				var strOrderControl= 'DISCONTINUED';
  				break;
  			case 'NW':
    				var strOrderControl= 'NEW';
  				break;
  			case 'OC':
    				var strOrderControl= 'CANCELLED';
  				break;
  			case 'OD':
    				var strOrderControl= 'DISCONTINUED';
  				break;
  			case 'RF':
    				var strOrderControl= 'REFILL';
  				break;
  			case 'RO':
    				var strOrderControl= 'REPLACEMENT';
  				break;
  			case 'XO':
    				var strOrderControl= 'MODIFIED';
  				break;
  			default:
    				var strOrderControl= 'MODIFIED';
    				break;
		}
		objCommonOrder.orderControl= strOrderControl;
	} else {
		objCommonOrder.orderControl= 'MODIFIED';
	}
	if(orc['ORC.2'].toString().length>0) objCommonOrder.placerOrderNumber= convertEntityIdentifierToJSON(orc['ORC.2']);
	if(orc['ORC.3'].toString().length>0) objCommonOrder.fillerOrderNumber= convertEntityIdentifierToJSON(orc['ORC.3']);
	if(orc['ORC.4'].toString().length>0) objCommonOrder.placerGroupNumber= convertEntityIdentifierToJSON(orc['ORC.4']);
	// 2022-12-31 jer - Modified the declaration of orderStatus from orc['ORC.5'] to orc['ORC.5']['ORC.5.1']
	if(orc['ORC.5'].toString().length>0) objCommonOrder.orderStatus= orc['ORC.5']['ORC.5.1'].toString();
	if(orc['ORC.6'].toString().length>0) objCommonOrder.responseFlag= orc['ORC.6'].toString();

	// 2023-02-01 jer - Modified from calling convertCompositeQuantityWithUnitsToJSON to convertCompositeQuantityTimingToJSON() function
	// 2022-04-07 jer - Added convertCompositeQuantityWithUnitsToJSON code template
	// if(orc['ORC.7'].toString().length>0) objCommonOrder.quantityTiming= convertCompositeQuantityWithUnitsToJSON(orc['ORC.7']);
	if(orc['ORC.7'].toString().length>0) objCommonOrder.quantityTiming=  convertCompositeQuantityTimingToJSON(orc['ORC.7']);

	if(orc['ORC.8'].toString().length>0) {
		var objParentOrder= {};
		for(i= 0;i<orc['ORC.8'].children().length;i++) {
			if (i== 0) {
				var placerAssignedIdentifier= convertEntityIdentifierToJSON(orc['ORC.8']['ORC.8.1']);
				objParentOrder.placedAssignedIdentifier= placerAssignedIdentifier;	
			}
			if (i== 1) {
				var fillerAssignedIdentifier= convertEntityIdentifierToJSON(orc['ORC.8']['ORC.8.2']);
				objParentOrder.fillerAssignedIdentifier= fillerAssignedIdentifier;	
			}
		}
		objCommonOrder.parentOrder= JSON.stringify(objParentOrder);
	}
	if(orc['ORC.9'].toString().length>0)  objCommonOrder.dateTimeOfTransaction= convertTimestampToJSON(orc['ORC.9']);
	if(orc['ORC.10'].toString().length>0) objCommonOrder.enteredBy= convertExtendedCompositeIDNumberAndNameToJSON(orc['ORC.10']);
	if(orc['ORC.11'].toString().length>0) objCommonOrder.verifiedBy= convertExtendedCompositeIDNumberAndNameToJSON(orc['ORC.11']);
	if(orc['ORC.12'].toString().length>0) objCommonOrder.orderingProvider= convertExtendedCompositeIDNumberAndNameToJSON(orc['ORC.12']);
	// 2023-01-02 JER - Added enterersLocation orc[ORC.13]
	if(orc['ORC.13'].toString().length>0) objCommonOrder.enterersLocation= convertPersonLocationToJSON(orc['ORC.13']);
	if(orc['ORC.15'].toString().length>0) objCommonOrder.orderedEffectiveDateTime= convertTimestampToJSON(orc['ORC.15']);

	if(debug>0) {
		logger.error(scriptName+':DEBUG:: objCommonOrder= '+objCommonOrder);
		logger.error(scriptName+':DEBUG:: commonOrder= '+JSON.stringify(objCommonOrder));
	}

	channelMap.put('commonOrder',JSON.stringify(objCommonOrder));
	return objCommonOrder;

}

/**
	Desc:  this JS code template is to parse HL7v2 message AL1 segment and return a Javascript object

	Filename: json_patient_allergy.js
	@param {String} orc - This will contains the XML representation of the HL7v2 AL1 segment
	@return {String} return - Returning the Javascript object representation of the conveted HL7v2 AL1 segment
	History:

*/
function json_patient_allergy(al1) {

	var scriptName= 'json_patient_allergy';
	var debug= 0;
	/*
	logger.error(scriptName+':DEBUG:: al1= '+al1);;
	*/
	var objAllergy= {};

	// AL1.1 - set Id
	if(al1['AL1.1'].toString().length>0) objAllergy.setId= al1['AL1.1']['AL1.1.1'].toString();

	// AL1.2 - Type Code
	// if(al1['AL1.2'].toString().length>0) objAllergy.valueType= convertCodedElementToJSON(al1['AL1.2']);
	if(al1['AL1.2'].toString().length>0) objAllergy.typeCode= convertCodedElementToJSON(al1['AL1.2']);

	// AL1.3 - Allergen Code (identifier^text^name of codeSystem^alternate Id^alternate text^name of alternate codeSystem)
	if(al1['AL1.3'].toString().length>0) objAllergy.allergyCode= convertCodedElementToJSON(al1['AL1.3']);

	// AL1.4 - Severity Code
	if(al1['AL1.4'].toString().length>0) objAllergy.severityCode= convertCodedElementToJSON(al1['AL1.4']);

	// AL1.5 - Reaction Code
	var arrReactionCode= new Array();
	for each(reaction in al1['AL1.5']) {
		arrReactionCode.push(reaction['AL1.5.1'].toString());
	} // end for each
	if(al1['AL1.5'].toString().length>0) objAllergy.reactionCode= arrReactionCode;

	// AL1.6 - Identification Date
	if(al1['AL1.6'].toString().length>0) objAllergy.identificationDate= al1['AL1.6']['AL1.6.1'].toString();


	if(debug>0) {
		logger.error(scriptName+':DEBUG:: objAllergy= '+objAllergy);
		logger.error(scriptName+':DEBUG:: commonOrder= '+JSON.stringify(objAllergy));
	}

	channelMap.put('Allergy',JSON.stringify(objAllergy));
	return objAllergy;

}

/**
	Desc:  this JS code template is to handle Patient Gender parsing PID.8

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function json_pid_gender(pid, patient) {

	var scriptName= 'json_pid_gender';
	var gender= pid['PID.8']['PID.8.1'].toString().toUpperCase();

//	logger.error(scriptName+':DEBUG:: pidGender= '+pid+' patient= '+patient);

	if (gender == 'F' || gender == 'FEMALE'){
		patient.gender= 'Female';
	}

	else if (gender == 'M' || gender == 'MALE'){
		patient.gender= 'Male';
	}

	else if (gender == 'O' || gender == 'OTHER'){
		patient.gender= 'Other';
	}

	else if (gender == 'U' || gender == 'UNKNOWN'){
		patient.gender= 'Unknown';
	}
	else{
		patient.gender= pid['PID.8']['PID.8.1'].toString();
	}

//	logger.error(scriptName+':DEBUG:: patient= '+JSON.stringify(patient));
	return patient;

}

/**
	Desc:  this JS code template is to parse HL7v2 PID segment and return a Javascript object

	@param {String} pid - This will contains the XML representation of the HL7v2 PID segment

	@return {String} return - Returning the Javascript object representation of the conveted HL7v2 PID segment
	History:
	2023-09-21 JER - Added code to evaluate patientIdentifier for either PID-3 in addition to PID-4 value
	2023-09-10 JER - Added phone numbers (home and business) PID.13 and PID.14 with other updates references objPatient
	2022-08-25 JER - Modified from mrn and mrnDomain to identifier and assigningAuthorityName
	2022-05-13 JER - per requested customer provides unique Patient Id in PID.4
	2022-02-25 JER - Modified to include the call json_pv1_patient(pv1, patient) method to populate visit-centric content
*/
function json_pid_patient(pid) {

	var scriptName= 'json_pid_patient';
	var debug= 0;

	var objPatient= {};

	if(debug>0) {
		logger.debug('DEBUG::'+scriptName+' pid[PID.4] typeof: '+typeof(pid['PID.4'])+' PID.4 value: '+pid['PID.4'].toString()+' PID.4.1 value: '+pid['PID.4']['PID.4.1'].toString());
		logger.debug('DEBUG::'+scriptName+' pid[PID.3] typeof: '+typeof(pid['PID.3'])+' PID.3 value: '+pid['PID.3'].toString()+' PID.3.1 value: '+pid['PID.3']['PID.3.1'].toString());
	} // end if 

	if(pid['PID.4']['PID.4.1'].toString().length>0) {
		// 2022-05-13 JER - per requested customer provides unique Patient Id in PID.4
		for each(pid4 in pid['PID.4']) {

			var pidNum = pid4['PID.4.1'].toString();
			var entity = pid4['PID.4.4'].toString();
			// 2022-02-22 jer - verify if PID.3.5 exists if not is undefined
			if(pid4['PID.4.5'].toString().length>0) {
				var IDCode = pid4['PID.4.5'].toString();
			} // end if 

			// 2022-02-22 jer - verify if undefined or defined with value of MRN
			if((typeof(IDCode)== 'undefined') || (IDCode.indexOf('MRN')>-1)) {
				if(entity != null) {
					// 2022-08-25 JER - Modified from mrn and mrnDomain to identifier and assigningAuthorityName
					objPatient.identifier= pidNum;
					objPatient.assigningAuthorityName= entity;
					channelMap.put('identifier',pidNum); 
					channelMap.put('assigningAuthorityName',entity);
				}
			} else if (IDCode.indexOf("MR")>-1) {
				if(entity != null) {
					// 2022-08-25 JER - Modified from mpi and mpiDomain to mpiIdentifier and globalAssigningAuthorityName
					objPatient.mpiIdentifier= pidNum;
					objPatient.globalAssigningAuthorityName= entity;
					channelMap.put('mpiIdentifier',pidNum); 
					channelMap.put('globalAssigningAuthorityName',entity);
				}
			} else {
				if(entity != null) {
					// 2022-08-25 JER - Modified from mpi and mpiDomain to mpiIdentifier and globalAssigningAuthorityName
					objPatient.mpiIdentifier= pidNum;
					objPatient.globalAssigningAuthorityName= entity;
					channelMap.put('mpiIdentifier',pidNum); 
					channelMap.put('globalAssigningAuthorityName',entity);
				} // end if 
			} // end if 
		} // end for 
	// 2023-09-21 JER - Added code to evaluate patientIdentifier for either PID-3 in addition to PID-4 value
	} else if(pid['PID.3']['PID.3.1'].toString().length>0) {

		// 2022-05-13 JER - per requested customer provides unique Patient Id in PID.4
		for each(pid3 in pid['PID.3']) {

			var pidNum = pid3['PID.3.1'].toString();
			var entity = pid3['PID.3.4'].toString();
			// 2022-02-22 jer - verify if PID.3.5 exists if not is undefined
			if(pid3['PID.3.5'].toString().length>0) {
				var IDCode = pid3['PID.3.5'].toString();
			} // end if 

			//logger.error(scriptName+':DEBUG:: typeof(IDCode)= '+typeof(IDCode));

			// 2022-02-22 jer - verify if undefined or defined with value of MRN
			if((typeof(IDCode)== 'undefined') || (IDCode.indexOf('MRN')>-1)) {
				if(entity != null) {
					// 2022-08-25 JER - Modified from mrn and mrnDomain to identifier and assigningAuthorityName
					objPatient.identifier= pidNum;
					objPatient.assigningAuthorityName= entity;
					channelMap.put('identifier',pidNum); 
					channelMap.put('assigningAuthorityName',entity);
				}
			} else if (IDCode.indexOf("MR")>-1) {
				if(entity != null) {
					// 2022-08-25 JER - Modified from mpi and mpiDomain to mpiIdentifier and globalAssigningAuthorityName
					objPatient.mpiIdentifier= pidNum;
					objPatient.globalAssigningAuthorityName= entity;
					channelMap.put('mpiIdentifier',pidNum); 
					channelMap.put('globalAssigningAuthorityName',entity);
				}
			} else {
				if(entity != null) {
					// 2022-08-25 JER - Modified from mpi and mpiDomain to mpiIdentifier and globalAssigningAuthorityName
					objPatient.mpiIdentifier= pidNum;
					objPatient.globalAssigningAuthorityName= entity;
					channelMap.put('mpiIdentifier',pidNum); 
					channelMap.put('globalAssigningAuthorityName',entity);
				} // end if 
			} // end if 
		} // end for 
	} // end if 

	objPatient.encounterNumber= pid['PID.18']['PID.18.1'].toString();
	objPatient.firstName= pid['PID.5']['PID.5.2'].toString();
	if(pid['PID.5']['PID.5.3'].toString().length>0) objPatient.middleName=  pid['PID.5']['PID.5.3'].toString();
	objPatient.lastName=  pid['PID.5']['PID.5.1'].toString();
	objPatient.dob= pid['PID.7']['PID.7.1'].toString();
	objPatient.gender= pid['PID.8']['PID.8.1'].toString();

	// 2022-04-07 jer - added convertExtendedAddressToJSON method for Patient Address
	/* Patient Address PID.11 (XAD) */
	if(pid['PID.11'].toString().length>0) objPatient.patientAddress= convertExtendedAddressToJSON(pid['PID.11']);

	// 2023-09-10 JER - Added phone numbers (home and business) PID.13 and PID.14
	/* Phone Number - Home (XTN) PID.13 */
	if(pid['PID.13'].toString().length>0) objPatient.phoneNumberHome= convertExtendedTelecommunicationNumberToJSON(pid['PID.13']);

	/* Phone Number - Business (XTN) PID.14 */
	if(pid['PID.14'].toString().length>0) objPatient.phoneNumberBusiness= convertExtendedTelecommunicationNumberToJSON(pid['PID.14']);

	/* Patient Account Number (CX) PID.18 */
	if(pid['PID.18'].toString().length>0) objPatient.patientAccountNumber= pid['PID.18']['PID.18.1'].toString();
	// 2023-10-09 JER - Added the patient Ethnicity using the convertCodedElementToJSON using PID-22
	if(pid['PID.22'].toString().length>0) objPatient.ethnicity= convertCodedElementToJSON(pid['PID.22'])
	/* Patient Death Date and Time PID.29 */
	if(pid['PID.29'].toString().length>0) objPatient.deathDateTime= pid['PID.29']['PID.29.1'].toString();
	/* Patient Death Indicator PID.30 */
	if(pid['PID.30'].toString().length>0) objPatient.deathIndicator= pid['PID.30']['PID.30.1'].toString();

	if(debug>0) {
		logger.error(scriptName+':DEBUG:: objPatient= '+objPatient);
		logger.error(scriptName+':DEBUG:: stringify(objPatient)= '+JSON.stringify(objPatient));
	}

	channelMap.put('Patient',JSON.stringify(objPatient));
	return objPatient;

}

/**
	Desc:  this JS code template is to parse HL7v2 PID segment and return a Javascript object 
	File:  json_pid_patient_alliant.js
	Clone of: json_pid_patient

	@param {String} pid - This will contains the XML representation of the HL7v2 PID segment
	@return {String} return - Returning the Javascript object representation of the conveted HL7v2 PID segment

	History:
	2024-01-15 JER - Added the handling of the SoftWriters Patient Identifier into alternateIdentifier property
	2023-11-29 JER - Modified to include the handling of the AlliantPatientIdConfig (supported values: PID-3,PID-4,PID-18) to determine the patient identifier
	2023-10-09 JER - Added the patient Ethnicity using the convertCodedElementToJSON using PID-22
	2023-09-21 JER - Added code to evaluate patientIdentifier for either PID-3 in addition to PID-4 value
	2023-09-10 JER - Added phone numbers (home and business) PID.13 and PID.14 with other updates references objPatient
	2022-08-25 JER - Modified from mrn and mrnDomain to identifier and assigningAuthorityName
	2022-05-13 JER - per requested customer provides unique Patient Id in PID.4
	2022-02-25 JER - Modified to include the call json_pv1_patient(pv1, patient) method to populate visit-centric content
*/
function json_pid_patient_alliant(pid, locationPatientId) {

	var scriptName= 'json_pid_patient_alliant';
	var debug= 0;
	var objPatient= {};
	if(channelMap.get('PatientID_SW')) alternateIdentifier= channelMap.get('PatientID_SW');

	// if locationPatientId is not defined or has no string value - set to default
	if( (typeof(locationPatientId)== undefined) || (locationPatientId.toString().length<1) ) {
		var locationPatientId= 'default';
	} // end if 

	if(debug>0) {
		logger.debug('DEBUG::'+channelName+':'+scriptName+' locationPatientId: '+locationPatientId);
		logger.debug('DEBUG::'+channelName+':'+scriptName+' pid[PID.4] typeof: '+typeof(pid['PID.4'])+' PID.4 value: '+pid['PID.4'].toString()+' PID.4.1 value: '+pid['PID.4']['PID.4.1'].toString());
		logger.debug('DEBUG::'+channelName+':'+scriptName+' pid[PID.3] typeof: '+typeof(pid['PID.3'])+' PID.3 value: '+pid['PID.3'].toString()+' PID.3.1 value: '+pid['PID.3']['PID.3.1'].toString());
	} // end if 

	// 2023-11-29 JER - Modified to include the handling of the AlliantPatientIdConfig (supported values: PID-3,PID-4,PID-18) to determine the patient identifier
	if(locationPatientId.toString().indexOf('PID')>-1) {

		var lstLocationPatientId= locationPatientId.toString().split(',');
		for each(eachLocation in lstLocationPatientId) {

			// if the AlliantPatientId ConfigMap key value pair contains PID-3
			if(eachLocation.toString().indexOf('PID-3')>-1) {

				// 2024-01-15 JER - Added to evaluate if typeof PID.3 has a value
				if(typeof(pid['PID.3']['PID.3.1'])!== 'undefined') { 

					// 2023-09-21 JER - Added code to evaluate patientIdentifier for either PID-3 in addition to PID-4 value
					if(pid['PID.3']['PID.3.1'].toString().length>0) {
						// 2022-05-13 JER - per requested customer provides unique Patient Id in PID.4
						for each(pid3 in pid['PID.3']) {

							var pidNum = pid3['PID.3.1'].toString();
							var entity = pid3['PID.3.4'].toString();
							// 2022-02-22 jer - verify if PID.3.5 exists if not is undefined
							if(pid3['PID.3.5'].toString().length>0) {
								var IDCode = pid3['PID.3.5'].toString();
							} // end if 

							//logger.error(scriptName+':DEBUG:: typeof(IDCode)= '+typeof(IDCode));

							// 2022-02-22 jer - verify if undefined or defined with value of MRN
							if((typeof(IDCode)== 'undefined') || (IDCode.indexOf('MRN')>-1)) {

								if(entity != null) {
									// 2022-08-25 JER - Modified from mrn and mrnDomain to identifier and assigningAuthorityName
									objPatient.identifier= pidNum;
									objPatient.assigningAuthorityName= entity;
									channelMap.put('identifier',pidNum); 
									channelMap.put('assigningAuthorityName',entity);
								} // end if 
							} else if (IDCode.indexOf("MR")>-1) {
								if(entity != null) {
									// 2022-08-25 JER - Modified from mpi and mpiDomain to mpiIdentifier and globalAssigningAuthorityName
									objPatient.mpiIdentifier= pidNum;
									objPatient.globalAssigningAuthorityName= entity;
									channelMap.put('mpiIdentifier',pidNum); 
									channelMap.put('globalAssigningAuthorityName',entity);
								} // end if 
							} else {
								if(entity != null) {
									// 2022-08-25 JER - Modified from mpi and mpiDomain to mpiIdentifier and globalAssigningAuthorityName
									objPatient.mpiIdentifier= pidNum;
									objPatient.globalAssigningAuthorityName= entity;
									channelMap.put('mpiIdentifier',pidNum); 
									channelMap.put('globalAssigningAuthorityName',entity);
								} // end if 
							} // end if 

						} // end for

					} // end if 

				} // end if 

			// if the AlliantPatientIdConfig ConfigMap key value pair contains PID-4
			} else if(eachLocation.toString().indexOf('PID-4')>-1) {

				// 2024-01-15 JER - Added to evaluate if typeof PID.4 has a value
				if(typeof(pid['PID.4']['PID.4.1'])!== 'undefined') { 

					if(pid['PID.4']['PID.4.1'].toString().length>0) {
						// 2022-05-13 JER - per requested customer provides unique Patient Id in PID.4
						for each(pid4 in pid['PID.4']) {

							var pidNum = pid4['PID.4.1'].toString();
							var entity = pid4['PID.4.4'].toString();

							// 2022-02-22 jer - verify if PID.4.5 exists if not is undefined
							if(pid4['PID.4.5'].toString().length>0) {
								var IDCode = pid4['PID.4.5'].toString();
							} // end if 

							// 2022-02-22 jer - verify if undefined or defined with value of MRN
							if((typeof(IDCode)== 'undefined') || (IDCode.indexOf('MRN')>-1)) {
								if(entity != null) {
									// 2022-08-25 JER - Modified from mrn and mrnDomain to identifier and assigningAuthorityName
									objPatient.identifier= pidNum;
									objPatient.assigningAuthorityName= entity;
									channelMap.put('identifier',pidNum); 
									channelMap.put('assigningAuthorityName',entity);
								} // end if 

							} else if (IDCode.indexOf("MR")>-1) {
								if(entity != null) {
									// 2022-08-25 JER - Modified from mpi and mpiDomain to mpiIdentifier and globalAssigningAuthorityName
									objPatient.mpiIdentifier= pidNum;
									objPatient.globalAssigningAuthorityName= entity;
									channelMap.put('mpiIdentifier',pidNum); 
									channelMap.put('globalAssigningAuthorityName',entity);
								} // end if 

							} else {
								if(entity != null) {
									// 2022-08-25 JER - Modified from mpi and mpiDomain to mpiIdentifier and globalAssigningAuthorityName
									objPatient.mpiIdentifier= pidNum;
									objPatient.globalAssigningAuthorityName= entity;
									channelMap.put('mpiIdentifier',pidNum); 
									channelMap.put('globalAssigningAuthorityName',entity);
								} // end if 
							} // end if 
						} // end for
					} // end if 
				} // end if 
			// if the AlliantPatientIdConfig ConfigMap key value pair contains PID-18
			} else if(eachLocation.toString().indexOf('PID-18')>-1) {

				if(pid['PID.18']['PID.18.1'].toString().length>0) {

					// if PID-18.1 exists
					if(pid['PID.18']['PID.18.1']) {
						var pidNum = pid['PID.18']['PID.18.1'].toString();
					} 
					// if PID-18.4 exists
					if(pid['PID.18']['PID.18.4']) {
						var entity= pid['PID.18']['PID.18.1'].toString();
					} else {
						var entity= '';
					} // end if 
					// if PID-18.5 exists
					if(pid['PID.18']['PID.18.5']) {
						var IDCode = pid['PID.18']['PID.18.5'].toString();
					} else {
						var IDCode = '';
					} // end if 

					// if pidNum exists 
					if(pidNum) {
						objPatient.identifier= pidNum;
						objPatient.assigningAuthorityName= entity;
						channelMap.put('identifier',pidNum); 
						channelMap.put('assigningAuthorityName',entity);
					} // end if 

				} // end if 
			} // end if 
		} // end for each

	// 2023-11-29 JER - Modified for non locationPatientId 
	} else {

		// 2023-09-21 JER - Added code to evaluate patientIdentifier for either PID-3 in addition to PID-4 value
		if(pid['PID.3']['PID.3.1'].toString().length>0) {

			// 2022-05-13 JER - per requested customer provides unique Patient Id in PID.4
			for each(pid3 in pid['PID.3']) {

				var pidNum = pid3['PID.3.1'].toString();
				var entity = pid3['PID.3.4'].toString();
				// 2022-02-22 jer - verify if PID.3.5 exists if not is undefined
				if(pid3['PID.3.5'].toString().length>0) {
					var IDCode = pid3['PID.3.5'].toString();
				} // end if 

				//logger.error(scriptName+':DEBUG:: typeof(IDCode)= '+typeof(IDCode));

				// 2022-02-22 jer - verify if undefined or defined with value of MRN
				if((typeof(IDCode)== 'undefined') || (IDCode.indexOf('MRN')>-1)) {
					if(entity != null) {
						// 2022-08-25 JER - Modified from mrn and mrnDomain to identifier and assigningAuthorityName
						objPatient.identifier= pidNum;
						objPatient.assigningAuthorityName= entity;
						channelMap.put('identifier',pidNum); 
						channelMap.put('assigningAuthorityName',entity);
					} // end if 

				} else if (IDCode.indexOf("MR")>-1) {
					if(entity != null) {
						// 2022-08-25 JER - Modified from mpi and mpiDomain to mpiIdentifier and globalAssigningAuthorityName
						objPatient.mpiIdentifier= pidNum;
						objPatient.globalAssigningAuthorityName= entity;
						channelMap.put('mpiIdentifier',pidNum); 
						channelMap.put('globalAssigningAuthorityName',entity);
					} // end if 

				} else {
					if(entity != null) {
						// 2022-08-25 JER - Modified from mpi and mpiDomain to mpiIdentifier and globalAssigningAuthorityName
						objPatient.mpiIdentifier= pidNum;
						objPatient.globalAssigningAuthorityName= entity;
						channelMap.put('mpiIdentifier',pidNum); 
						channelMap.put('globalAssigningAuthorityName',entity);
					} // end if 
				} // end if 

			} // end for

		// PID-4
		} else if(pid['PID.4']['PID.4.1'].toString().length>0) {

			// 2022-05-13 JER - per requested customer provides unique Patient Id in PID.4
			for each(pid4 in pid['PID.4']) {

				var pidNum = pid4['PID.4.1'].toString();
				var entity = pid4['PID.4.4'].toString();

				// 2022-02-22 jer - verify if PID.4.5 exists if not is undefined
				if(pid4['PID.4.5'].toString().length>0) {
					var IDCode = pid4['PID.4.5'].toString();
				} // end if 

				// 2022-02-22 jer - verify if undefined or defined with value of MRN
				if((typeof(IDCode)== 'undefined') || (IDCode.indexOf('MRN')>-1)) {
					if(entity != null) {
						// 2022-08-25 JER - Modified from mrn and mrnDomain to identifier and assigningAuthorityName
						objPatient.identifier= pidNum;
						objPatient.assigningAuthorityName= entity;
						channelMap.put('identifier',pidNum); 
						channelMap.put('assigningAuthorityName',entity);
					} // end if 

				} else if (IDCode.indexOf("MR")>-1) {
					if(entity != null) {
						// 2022-08-25 JER - Modified from mpi and mpiDomain to mpiIdentifier and globalAssigningAuthorityName
						objPatient.mpiIdentifier= pidNum;
						objPatient.globalAssigningAuthorityName= entity;
						channelMap.put('mpiIdentifier',pidNum); 
						channelMap.put('globalAssigningAuthorityName',entity);
					} // end if 

				} else {
					if(entity != null) {
						// 2022-08-25 JER - Modified from mpi and mpiDomain to mpiIdentifier and globalAssigningAuthorityNameobalAssigningAuthorityName
						objPatient.mpiIdentifier= pidNum;
						objPatient.globalAssigningAuthorityName= entity;
						channelMap.put('mpiIdentifier',pidNum); 
						channelMap.put('globalAssigningAuthorityName',entity);
					} // end if 
				} // end if 
			} // end for

		// PID-18	
		} else if(pid['PID.18']['PID.18.1'].toString().length>0) {

			// if PID-18.1 exists
			if(pid['PID.18']['PID.18.1']) {
				var pidNum = pid['PID.18']['PID.18.1'].toString();
			} 
			// if PID-18.4 exists
			if(pid['PID.18']['PID.18.4']) {
				var entity= pid['PID.18']['PID.18.1'].toString();
			} else {
				var entity= '';
			} // end if 
			// if PID-18.5 exists
			if(pid['PID.18']['PID.18.5']) {
				var IDCode = pid['PID.18']['PID.18.5'].toString();
			} else {
				var IDCode = '';
			} // end if 

			// if pidNum exists 
			if(pidNum) {
				objPatient.identifier= pidNum;
				objPatient.assigningAuthorityName= entity;
				channelMap.put('identifier',pidNum); 
				channelMap.put('assigningAuthorityName',entity);
			} // end if

		} // end if

	} // end if 

	// 2024-01-15 JER - Added the handling of the SoftWriters Patient Identifier into alternateIdentifier property
	if(alternateIdentifier) objPatient.alternateIdentifier= alternateIdentifier;
	objPatient.encounterNumber= pid['PID.18']['PID.18.1'].toString();
	objPatient.firstName= pid['PID.5']['PID.5.2'].toString();
	if(pid['PID.5']['PID.5.3'].toString().length>0) objPatient.middleName=  pid['PID.5']['PID.5.3'].toString();
	objPatient.lastName=  pid['PID.5']['PID.5.1'].toString();
	objPatient.dob= pid['PID.7']['PID.7.1'].toString();
	objPatient.gender= pid['PID.8']['PID.8.1'].toString();
	// 2022-04-07 jer - added convertExtendedAddressToJSON method for Patient Address
	/* Patient Address PID.11 (XAD) */
	if(pid['PID.11'].toString().length>0) objPatient.patientAddress= convertExtendedAddressToJSON(pid['PID.11']);

	// 2023-09-10 JER - Added phone numbers (home and business) PID.13 and PID.14
	/* Phone Number - Home (XTN) PID.13 */
	if(pid['PID.13'].toString().length>0) objPatient.phoneNumberHome= convertExtendedTelecommunicationNumberToJSON(pid['PID.13']);

	/* Phone Number - Business (XTN) PID.14 */
	if(pid['PID.14'].toString().length>0) objPatient.phoneNumberBusiness= convertExtendedTelecommunicationNumberToJSON(pid['PID.14']);

	/* Patient Account Number (CX) PID.18 */
	if(pid['PID.18'].toString().length>0) objPatient.patientAccountNumber= pid['PID.18']['PID.18.1'].toString();
	// 2023-10-09 JER - Added the patient Ethnicity using the convertCodedElementToJSON using PID-22
	if(pid['PID.22'].toString().length>0) objPatient.ethnicity= convertCodedElementToJSON(pid['PID.22'])
	/* Patient Death Date and Time PID.29 */
	if(pid['PID.29'].toString().length>0) objPatient.deathDateTime= pid['PID.29']['PID.29.1'].toString();
	/* Patient Death Indicator PID.30 */
	if(pid['PID.30'].toString().length>0) objPatient.deathIndicator= pid['PID.30']['PID.30.1'].toString();

	if(debug>0) {
		logger.error(scriptName+':DEBUG:: objPatient= '+objPatient);
		logger.error(scriptName+':DEBUG:: stringify(objPatient)= '+JSON.stringify(objPatient));
	}

	channelMap.put('Patient',JSON.stringify(objPatient));
	return objPatient;

}

/**
	Desc:  this JS code template is to parse HL7v2 message PV1 segment  and return a Javascript object

	Filename: json_pv1_clinician.js
	@param {String} pv1 - This will contains the XML representation of the HL7v2 PV1 segment
	@return {String} return - Returning the Javascript object representation of the converted HL7v2 PV1 segment
	History:
	2023-01-09 JER - Modified all physicians to Doctor

*/
function json_pv1_clinician(pv1) {

	var scriptName= 'json_pv1_clinician';
	var debug= 0;
	if(debug>0) { logger.error('DEBUG::'+scriptName+' pv1= '+pv1); }

	var arrClinician= new Array();

	// Clinician object
	var objClinician= {};

	// PV1.7 - attending physician
	// PV1.7.1 - Id Number
	if(pv1['PV1.7']['PV1.7.1'].toString().length>0) objClinician.idNumber= pv1['PV1.7']['PV1.7.1'].toString();

	// PV1.7.2 - Last/Family Name
	if(pv1['PV1.7']['PV1.7.2'].toString().length>0) objClinician.lastName= pv1['PV1.7']['PV1.7.2'].toString();

	// PV1.7.3 - First/Given Name
	if(pv1['PV1.7']['PV1.7.3'].toString().length>0) objClinician.firstName= pv1['PV1.7']['PV1.7.3'].toString();

	// PV1.7.9.1 - Assigning Authority - Namespace Id
	if(pv1['PV1.7']['PV1.7.9']['PV1.7.9.1'].toString().length>0) objClinician.assigningAuthority= pv1['PV1.7']['PV1.7.9']['PV1.7.9.1'].toString();

	if(pv1['PV1.7']['PV1.7.1'].toString().length>0) {
		// 2023-01-09 JER - Modified all physicians to Doctor
		// objClinician.clinicanType= 'Attending Physician';
		objClinician.clinicanType= 'Doctor';
		arrClinician.push(objClinician);
	} // end if 

	// Clinician object
	var objClinician= {};

	// PV1.8 - referring physician
	// PV1.8.1 - Id Number
	if(pv1['PV1.8']['PV1.8.1'].toString().length>0) objClinician.idNumber= pv1['PV1.8']['PV1.8.1'].toString();

	// PV1.8.2 - Last/Family Name
	if(pv1['PV1.8']['PV1.8.2'].toString().length>0) objClinician.lastName= pv1['PV1.8']['PV1.8.2'].toString();

	// PV1.8.3 - First/Given Name
	if(pv1['PV1.8']['PV1.8.3'].toString().length>0) objClinician.firstName= pv1['PV1.8']['PV1.8.3'].toString();

	// PV1.8.9.1 - Assigning Authority - Namespace Id
	if(pv1['PV1.8']['PV1.8.9']['PV1.8.9.1'].toString().length>0) objClinician.assigningAuthority= pv1['PV1.8']['PV1.8.9']['PV1.8.9.1'].toString();

	if(pv1['PV1.8']['PV1.8.1'].toString().length>0) {
		// 2023-01-09 JER - Modified all physicians to Doctor
		// objClinician.clinicanType= 'Referring Physician';	
		objClinician.clinicanType= 'Doctor';
		arrClinician.push(objClinician);
	} // end if 

	// Clinician object
	var objClinician= {};

	// PV1.9 - consulting physician
	// PV1.9.1 - Id Number
	if(pv1['PV1.9']['PV1.9.1'].toString().length>0) objClinician.idNumber= pv1['PV1.9']['PV1.9.1'].toString();

	// PV1.9.2 - Last/Family Name
	if(pv1['PV1.9']['PV1.9.2'].toString().length>0) objClinician.lastName= pv1['PV1.9']['PV1.9.2'].toString();

	// PV1.9.3 - First/Given Name
	if(pv1['PV1.9']['PV1.9.3'].toString().length>0) objClinician.firstName= pv1['PV1.9']['PV1.9.3'].toString();

	// PV1.9.9.1 - Assigning Authority - Namespace Id
	if(pv1['PV1.9']['PV1.9.9']['PV1.9.9.1'].toString().length>0) objClinician.assigningAuthority= pv1['PV1.9']['PV1.9.9']['PV1.9.9.1'].toString();

	if(pv1['PV1.9']['PV1.9.1'].toString().length>0) {
		// 2023-01-09 JER - Modified all physicians to Doctor
		// objClinician.clinicanType= 'Consulting Physician';
		objClinician.clinicanType= 'Doctor';
		arrClinician.push(objClinician);
	} // end if 

	// Clinician object
	var objClinician= {};

	// PV1.17 - admitting physician
	for each(admittingPhysician in pv1['PV1.17']) {
		// PV1.17.1 - Id Number
		if(admittingPhysician['PV1.17.1'].toString().length>0) objClinician.idNumber= admittingPhysician['PV1.17.1'].toString();

		// PV1.17.2 - Last/Family Name
		if(admittingPhysician['PV1.17.2'].toString().length>0) objClinician.lastName= admittingPhysician['PV1.17.2'].toString();

		// PV1.17.3 - First/Given Name
		if(admittingPhysician['PV1.17.3'].toString().length>0) objClinician.firstName= admittingPhysician['PV1.17.3'].toString();

		// PV1.17.9.1 - Assigning Authority - Namespace Id
		if(admittingPhysician['PV1.17.9']['PV1.17.9.1'].toString().length>0) objClinician.assigningAuthority= admittingPhysician['PV1.17.9']['PV1.17.9.1'].toString();

		if(admittingPhysician['PV1.17.1'].toString().length>0) {
			// 2023-01-09 JER - Modified all physicians to Doctor
			// objClinician.clinicanType= 'Admitting Physician';
			objClinician.clinicanType= 'Doctor';
			arrClinician.push(objClinician);
		} // end if 
	} // end for each

	if(debug>0) {
		logger.error(scriptName+':DEBUG:: clinician= '+JSON.stringify(arrClinician));
	}

	channelMap.put('Clinicians',JSON.stringify(arrClinician));
	return arrClinician;

}

/**
	Desc:  this JS code template is to parse HL7v2 PV1 segment and return a Javascript object

	@param {String} pv1 - This will contains the XML representation of the HL7v2 PV1 segment
	@param {String} patient - This will contains the XML representation of the Javascript patient object

	@return {String} return - Returning the Javascript object representation of the conveted HL7v2 PV1 segment
	History:
	2023-02-14 JER - Evaluate to capture the encounterType from PV1.2.1
	2022-11-14 JER - Modified from the json_pv1_visit to json_pv1_encounter and removed dependency on the patient object
*/
function json_pv1_encounter(pv1) {

	var scriptName= 'json_pv1_encounter';
	var debug= 0;
	var encounter= {};

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' pv1= '+JSON.stringify(pv1));
	}

	// 2022-08-26 JER - JSON stringify the globalChannel variable - evaluate organization if defined and toString.length GT 0 to persist to visit.organization
	// "organization" : "\"QS1\"",
	var organization= JSON.stringify(channelMap.get('organization')).replace(/[^\w\s]/gi, '');
	if(debug>0) logger.error('DEBUG::'+scriptName+' typeof(organization): '+typeof(organization)+' length: '+organization.length);

	if((typeof(organization)!== 'undefined')&&(organization.toString().length>0)) {
		encounter.organization= organization;
	} // end if

	// 2023-02-14 JER - Evaluate to capture the encounterType from PV1.2.1
	if(typeof(pv1['PV1.2'])!== 'undefined') {
		if(pv1['PV1.2']['PV1.2.1'].toString().length>0) {
			var encounterTypeCode= pv1['PV1.2']['PV1.2.1'].toString();
			if(encounterTypeCode== 'E') {
				encounter.encounterType= 'Emergency';
			} else if(encounterTypeCode== 'I') {
				encounter.encounterType= 'Inpatient';  	
			} else if((encounterTypeCode== 'O')||(encounterTypeCode== 'R')) {
				encounter.encounterType= 'Outpatient';
			} else if(encounterTypeCode== 'P') {
				encounter.encounterType= 'PreAdmit';
			}
		} else {
			encounter.encounterType= '';
		}
	} // end if 

	if(debug>0) logger.error('DEBUG::'+scriptName+' typeof(pv1[PV1.3])= '+typeof(pv1['PV1.3'])+' pv1[PV1.3]= '+pv1['PV1.3']); 
	// 2022-08-26 JER - Modified from using object: patient to visit

	if(typeof(pv1['PV1.3'])!== 'undefined') {
		if(pv1['PV1.3']['PV1.3.4'].toString().length>0) {
			encounter.facility= pv1['PV1.3']['PV1.3.4'].toString();
		} else {
			encounter.facility= '';
		}
	} else {
		if((typeof(channelMap.get('organization'))!== 'undefined')&&(channelMap.get('organization').toString().length>0)) {
			encounter.facility= channelMap.get('organization');
		} else {
			encounter.facility= '';
		} // end if 
	} // end if

	if(pv1['PV1.3']['PV1.3.1'].toString()) {
		encounter.unit= 	pv1['PV1.3']['PV1.3.1'].toString();
	} else {
		encounter.unit=	'';
	}

	if(pv1['PV1.3']['PV1.3.2'].toString()) {
		encounter.room= 	pv1['PV1.3']['PV1.3.2'].toString();
	} else {
		encounter.room=	'';
	}

	if(pv1['PV1.3']['PV1.3.3'].toString()) {
		encounter.bedID= 	pv1['PV1.3']['PV1.3.3'].toString();
	} else {
		encounter.bedID=	'';
	} // end if 


	if(pv1['PV1.6']['PV1.6.1'].toString().length>0) {
		encounter.prior_unit= 	pv1['PV1.6']['PV1.6.1'].toString();
		encounter.prior_room= 	pv1['PV1.6']['PV1.6.2'].toString();
		encounter.prior_bedID=	pv1['PV1.6']['PV1.6.3'].toString();
	} else {
		encounter.prior_unit= 	'';
		encounter.prior_room= 	'';
		encounter.prior_bedID=	'';
	}

	// 2022-03-02 JER - Acquire device_Id/room to synchronize data between the Caregility Admin Portal and data table on the BJC side.
	if((typeof(pv1['PV1.3']['PV1.3.9'])!== 'undefined')&&(pv1['PV1.3']['PV1.3.9'].toString().length>0)) {
		encounter.deviceID= pv1['PV1.3']['PV1.3.9'].toString();
		channelMap.put('deviceID',encounter.deviceID);
	} // end if 

	if(pv1['PV1.19']['PV1.19.1'].toString()){
		encounter.encounter_identifier= pv1['PV1.19']['PV1.19.1'].toString();
	} else {
		encounter.encounter_identifier= '';
	} // end if  

	// 2022-03-10 PP - per Greg's email dated 03/14, Admit/Discharge in YYYYMMDD format
	if(pv1['PV1.44']['PV1.44.1'].toString()){
		encounter.admissionDate= pv1['PV1.44']['PV1.44.1'].toString().substring(0,8);
	} // end if 

	// 2022-08-26 JER - Modified from using object: patient to visit
	if(pv1['PV1.45']['PV1.45.1'].toString()){
		encounter.dischargeDate= pv1['PV1.45']['PV1.45.1'].toString().substring(0,8);
	} else {
		encounter.dischargeDate= '';
	}

	if(debug>0) { logger.error(scriptName+':DEBUG:: encounter= '+JSON.stringify(encounter)); }

	return encounter;

}

/**
	Desc:  this JS code template is to parse HL7v2 PV1 segment and return a Javascript object

	@param {String} pv1 - This will contains the XML representation of the HL7v2 PV1 segment
	@param {String} patient - This will contains the XML representation of the Javascript patient object

	@return {String} return - Returning the Javascript object representation of the conveted HL7v2 PV1 segment
	History:
	2022-08-26 JER - JSON stringify the globalChannel variable - evaluate organization if defined and toString.length GT 0 to persist to visit.organization
	2022-08-26 JER - Modified from using object: patient to visit and 
	2022-07-22 JER - Removed dependencies on pid segment
	2022-03-07 JER - Added channelMap.put('deviceID',patient.deviceID)
	2022-03-02 JER - Acquire device_Id/room to synchronize data between the Caregility Admin Portal and data table on the BJC side.
	2022-02-28 JER Modified to copy patient.organization to patient.facility if no value for PV1.3.4 element value
*/
function json_pv1_visit(pv1, patient) {

	var scriptName= 'json_pv1_visit';
	var debug= 0;

	var objVisit= {};

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' pv1= '+pv1+' patient: '+JSON.stringify(patient));
	}

	if(debug>0) { logger.error('DEBUG::'+scriptName+' typeof(pv1[PV1.3][PV1.3.4])= '+typeof(pv1['PV1.3']['PV1.3.4'])+' pv1[PV1.3][PV1.3.4]= '+pv1['PV1.3']['PV1.3.4'].toString()); }

	// 2022-08-26 JER - JSON stringify the globalChannel variable - evaluate organization if defined and toString.length GT 0 to persist to objVisit.organization
	// "organization" : "\"QS1\"",
	var organization= JSON.stringify(channelMap.get('organization')).replace(/[^\w\s]/gi, '');
	if(debug>0) { logger.error('DEBUG::'+scriptName+' typeof(organization): '+typeof(organization)+' length: '+organization.length); }

	if((typeof(organization)!== 'undefined')&&(organization.toString().length>0)) {
		objVisit.organization= organization;
	} // end if

	// 2022-08-26 JER - Modified from using object: patient to visit
	if((typeof(pv1['PV1.3']['PV1.3.4'])!== 'undefined')&&(pv1['PV1.3']['PV1.3.4'].toString().length>0)) {
		objVisit.facility= pv1['PV1.3']['PV1.3.4'].toString();
	} else {
		if((typeof(channelMap.get('organization'))!== 'undefined')&&(channelMap.get('organization').toString().length>0)) {
			objVisit.facility= channelMap.get('organization');
		} // end if 
	} // end if

	objVisit.unit= 	pv1['PV1.3']['PV1.3.1'].toString().replace('-','');
	objVisit.room= 	pv1['PV1.3']['PV1.3.2'].toString().replace('-','');
	objVisit.bedID= 	pv1['PV1.3']['PV1.3.3'].toString().replace('-','');

	/* 2022-02-27 JER - removed as not storing prior location (i.e. unit, room, bed)
	if(pv1['PV1.6']['PV1.6.1'].toString().length>0) {
		patient.prior_unit= 	pv1['PV1.6']['PV1.6.1'].toString();
		patient.prior_room= 	pv1['PV1.6']['PV1.6.2'].toString();
		patient.prior_bedID=	pv1['PV1.6']['PV1.6.3'].toString();
	}
	*/

	// 2022-03-02 JER - Acquire device_Id/room to synchronize data between the Caregility Admin Portal and data table on the BJC side.
	if((typeof(pv1['PV1.3']['PV1.3.9'])!== 'undefined')&&(pv1['PV1.3']['PV1.3.9'].toString().length>0)) {
		objVisit.deviceID= pv1['PV1.3']['PV1.3.9'].toString();
		channelMap.put('deviceID',objVisit.deviceID);
	} // end if 

	/* Attending Doctor (XCN) PV1-7 */
	if(pv1['PV1.7'].toString().length>0) objVisit.attendingDoctor= convertExtendedCompositeIDNumberAndNameToJSON(pv1['PV1.7']);

	// 2022-08-26 JER - Modified from using object: patient to visit
	if(pv1['PV1.44']['PV1.44.1'].toString()){
	//	patient.admissionDate= pv1['PV1.44']['PV1.44.1'].toString();
		objVisit.admissionDate= pv1['PV1.44']['PV1.44.1'].toString().substring(0,8);
		if(pv1['PV1.44']['PV1.44.2'].toString().length>0) objVisit.admissionTime= pv1['PV1.44']['PV1.44.2'].toString().substring(0,8);
	} // end if 

	// 2022-08-26 JER - Modified from using object: patient to visit
	if(pv1['PV1.45']['PV1.45.1'].toString()){
	//	patient.dischargeDate= pv1['PV1.45']['PV1.45.1'].toString();
		objVisit.dischargeDate= pv1['PV1.45']['PV1.45.1'].toString().substring(0,8);
		if(pv1['PV1.45']['PV1.45.2'].toString().length>0) objVisit.dischargeTime= pv1['PV1.45']['PV1.45.2'].toString().substring(0,8);
	}

	if(debug>0) { logger.error(scriptName+':DEBUG:: objVisit= '+JSON.stringify(objVisit)); }
	// 2022-08-26 JER - Modified from using object: patient to objVisit
	return objVisit;

}

/**
	Desc:  this JS code template is to parse HL7v2 message ROL segment and return a Javascript object

	Filename: json_rol_clinician.js
	@param {String} rol - This will contains the XML representation of the HL7v2 ROL segment
	@return {String} return - Returning the Javascript object representation of the converted HL7v2 ROL segment
	History:

*/
function json_rol_clinician(rol) {

	var scriptName= 'json_rol_clinician';
	var debug= 0;
	//if(debug>0) { 
		logger.error('DEBUG::'+scriptName+' rol typeof= '+typeof(rol)+' rol: '+rol); 
	//}

	// Clinician object
	var objClinician= {};

	// ROL.1 - Role Instance Identifier (EI)
	if(rol['ROL.1']['ROL.1.1'].toString().length>0) objClinician.id= rol['ROL.1']['ROL.1.1'].toString();

	// ROL.2 - Action Code (ID)
	if(rol['ROL.2']['ROL.2.1'].toString().length>0) objClinician.actionCode= rol['ROL.2']['ROL.2.1'].toString();

	// ROL.3 - Role (Identifier, Text, CodingSystemName, CodingSystemNameOid (CE)
	logger.error('DEBUG::'+scriptName+' typeof rol[ROL.3]: '+typeof(rol['ROL.3'])+' stringify: '+rol['ROL.3']);
	if(typeof(rol['ROL.3'])!== 'undefined') {
		objClinician.role= convertCodedElementToJSON(rol['ROL.3']);
	} else {
		objClinician.role= '';
	} // end if 
	// if(debug>0) 
		logger.error('DEBUG::'+scriptName+' objClinician.role: '+JSON.stringify(objClinician.role));

	// ROL.4 - Role Person (XCN)
	logger.error('DEBUG::'+scriptName+' typeof rol[ROL.4]: '+typeof(rol['ROL.4'])+' stringify: '+rol['ROL.4']);
	if(typeof(rol['ROL.4'])!== 'undefined') {
		objClinician.rolePerson= convertExtendedCompositeIDNumberAndNameToJSON(rol['ROL.4']);
	} else {
		objClinician.rolePerson= '';
	} // end if 
	// if(debug>0) 
		logger.error('DEBUG::'+scriptName+' objClinician.rolePerson: '+JSON.stringify(objClinician.rolePerson));

	// ROL.5 - Role Begin DateTime (TS)
	if(typeof(rol['ROL.5'])!== 'undefined') {
		objClinician.roleBeginDatetime= convertTimestampToJSON(rol['ROL']['ROL.5']);
	} else {
		objClinician.roleBeginDatetime= '';
	} // end if 
	if(debug>0) logger.error('DEBUG::'+scriptName+' objClinician.roleBeginDatetime: '+objClinician.roleBeginDatetime);

	// ROL.6 - Role End DateTime (TS)
	if(typeof(rol['ROL.6'])!== 'undefined') {
		objClinician.roleEndDatetime= convertTimestampToJSON(rol['ROL']['ROL.6']);
	} else {
		objClinician.roleEndDateTime= '';
	} // end if 
	if(debug>0) logger.error('DEBUG::'+scriptName+' objClinician.roleEndDateTime: '+objClinician.roleEndDateTime);

	// ROL.7 - Role Duration
	if(typeof(rol['ROL.7'])!== 'undefined') {
		objClinician.roleDuration= convertCodedElementToJSON(rol['ROL']['ROL.7']);
	} else {
		objClinician.roleDuration= '';
	} // end if 
	if(debug>0) logger.error('DEBUG::'+scriptName+' objClinician.roleDuration: '+objClinician.roleDuration);

	// ROL.8 - Role Action Reason
	if(typeof(rol['ROL.8'])!== 'undefined') {
		objClinician.roleActionReason= convertCodedElementToJSON(rol['ROL']['ROL.8']);
	} else {
		objClinician.roleActionReason= '';
	} // end if 
	if(debug>0) logger.error('DEBUG::'+scriptName+' objClinician.roleActionReason: '+objClinician.roleActionReason);

	// ROL.9 - Provider Type
	if(typeof(rol['ROL.9'])!== 'undefined') {
		objClinician.providerType= convertCodedElementToJSON(rol['ROL']['ROL.9']);
	} else {
		objClinician.providerType= '';
	} // end if 
	if(debug>0) logger.error('DEBUG::'+scriptName+' objClinician.providerType: '+objClinician.providerType);

	// ROL.10 - Organization Unit Type
	if(typeof(rol['ROL.10'])!== 'undefined') {
		objClinician.organizationUnitType= convertCodedElementToJSON(rol['ROL']['ROL.10']);
	} else {
		objClinician.organizationUnitType= '';
	} // end if 
	if(debug>0) logger.error('DEBUG::'+scriptName+' objClinician.organizationUnitType: '+objClinician.organizationUnitType);

	// ROL.11 - Office/Home Address
	if(typeof(rol['ROL.11'])!== 'undefined') {
		objClinician.officeAddress= convertExtendedAddressToJSON(rol['ROL']['ROL.11']);
	} else {
		objClinician.officeAddress= '';
	} // end if 
	if(debug>0) logger.error('DEBUG::'+scriptName+' objClinician.officeAddress: '+objClinician.officeAddress);

	// ROL.12 - Phone
	if(typeof(rol['ROL.12'])!== 'undefined') {
		objClinician.phone= convertExtendedTelecommunicationNumberToJSON(rol['ROL']['ROL.12']);
	} else {
		objClinician.phone= '';
	} // end if 
	if(debug>0) logger.error('DEBUG::'+scriptName+' objClinician.phone: '+objClinician.phone);


	if(debug>0) {
		logger.error(scriptName+':DEBUG:: clinician= '+JSON.stringify(objClinician));
	}

	channelMap.put('Clinician',JSON.stringify(objClinician));
	return objClinician;

}

/**
	Desc:  this JS code template is to parse HL7v2 Pharmacy/Treatment Dispensed Order (RXD) segment and return a Javascript object

	@param {String} orc - This will contains the XML representation of the HL7v2 RXD segment
	@return {String} return - Returning the Javascript object representation of the conveted HL7v2 RXD segment
	History:
	2022-04-15 jer - Finalized remaining properties 
	2022-04-14 jer - Created from json_rxd_timing_quantity(rxd) method to populate visit centric content
*/
function json_rxd_pharmacy_dispense_order(rxd) {

	var scriptName= 'json_rxd_pharmacy_dispense_order';
	var debug= 0;
	/*
	logger.error(scriptName+':DEBUG:: rxd= '+rxd);;
	*/
	var objPharmacyDispenseOrder= {};

	// rxd.1 - Dispense SubId Counter
	// if(rxd['RXD.1'].toString().length>0) objPharmacyDispenseOrder.quantityTiming= convertTimingQuantityToJSON(rxd['RXD.1']);
	if(rxd['RXD.1'].toString().length>0) objPharmacyDispenseOrder.dispenseIdCounter= rxd['RXD.1']['RXD.1.1'].toString();

	// rxd.2 - Dispense Give Code
	if(rxd['RXD.2'].toString().length>0) objPharmacyDispenseOrder.dispenseGiveCode= convertCodedElementToJSON(rxd['RXD.2']);

	// rxd.3 - DateTime Dispensed
	if(rxd['RXD.3'].toString().length>0) objPharmacyDispenseOrder.giveAmountMinimum= convertTimestampToJSON(rxd['RXD.3']);

	// rxd.4 - Actual Dispense Amount
	if(rxd['RXD.4'].toString().length>0) objPharmacyDispenseOrder.actualDispenseAmount= rxd['RXD.4']['RXD.4.1'].toString();

	// rxd.5 - Actual Dispense Units
	if(rxd['RXD.5'].toString().length>0) objPharmacyDispenseOrder.actualDispenseUnits= convertCodedElementToJSON(rxd['RXD.5']);

	// rxd.6 - Actual Dosage Form
	if(rxd['RXD.6'].toString().length>0) objPharmacyDispenseOrder.actualDosageForm= convertCodedElementToJSON(rxd['RXD.6']);

	// rxd.7 - Prescription Number
	if(rxd['RXD.7'].toString().length>0) objPharmacyDispenseOrder.prescriptionNumber= rxd['RXD.7']['RXD.7.1'].toString();

	// rxd.8 - Number of Refills Remaining
	if(rxd['RXD.8'].toString().length>0) objPharmacyDispenseOrder.refillsRemaining= rxd['RXD.8']['RXD.8.1'].toString();

	// rxd.9 - Dispense Notes
	if(rxd['RXD.9'].toString().length>0) objPharmacyDispenseOrder.dispenseNotes= rxd['RXD.9']['RXD.9.1'].toString();

	// rxd.10 - Dispensing Provider
	if(rxd['RXD.10'].toString().length>0) objPharmacyDispenseOrder.dispensingProvider=convertExtendedCompositeIDNumberAndNameToJSON(rxd['RXD.10']);

	// rxd.11 - Substitution Status
	if(rxd['RXD.11'].toString().length>0) objPharmacyDispenseOrder.substitutionStatus= rxd['RXD.11']['RXD.11.1'].toString();

	// rxd.12 - Total Daily Dose
	if(rxd['RXD.12'].toString().length>0) objPharmacyDispenseOrder.totalDailyDose= convertCompositeQuantityWithUnitsToJSON(rxd['RXD.12']);

	// rxd.13 - Dispense-To Location
	if(rxd['RXD.13'].toString().length>0) objPharmacyDispenseOrder.dispenseToLocation= 
	convertLocationAddressVariationToJSON(rxd['RXD.13']);

	// rxd.14 - Needs Human Review 
	if(rxd['RXD.14'].toString().length>0) objPharmacyDispenseOrder.needsHumanReview= rxd['RXD.14']['RXD.14.1'].toString();

	// rxd.15 - Pharmacy/Treatment Suppliers Special Dispensing Instructions
	if(rxd['RXD.15'].toString().length>0) objPharmacyDispenseOrder.pharmacySuppliersSpecialDispensingInstructions= convertCodedElementToJSON(rxd['RXD.15']);

	// rxd.16 - Actual Strength
	if(rxd['RXD.16'].toString().length>0) objPharmacyDispenseOrder.actualStrength= rxd['RXD.16']['RXD.16.1'].toString();

	// rxd.17 - Actual Strength Units
	if(rxd['RXD.17'].toString().length>0) objPharmacyDispenseOrder.actualStrengthUnit= convertCodedElementToJSON(rxd['RXD.17']);

	// rxd.18 - Substance Lot Number
	if(rxd['RXD.18'].toString().length>0) objPharmacyDispenseOrder.substanceLotNumber= rxd['RXD.18']['RXD.18.1'].toString();

	// rxd.19 - Substance Expiration Date
	if(rxd['RXD.19'].toString().length>0) objPharmacyDispenseOrder.substanceExpirationDate=convertTimestampToJSON(rxd['RXD.19']);

	// rxd.20 - Substance Manufacturer Name
	if(rxd['RXD.20'].toString().length>0) objPharmacyDispenseOrder.substanceManufacturerName= convertCodedElementToJSON(rxd['RXD.20']);

	// rxd.21 - Give Indication
	if(rxd['RXD.21'].toString().length>0) objPharmacyDispenseOrder.giveIndication= convertCodedElementToJSON(rxd['RXD.21']);

	// rxd.22 - Dispense Package Size
	if(rxd['RXD.22'].toString().length>0) objPharmacyDispenseOrder.dispensePackageSize= rxd['RXD.22']['RXD.22.1'].toString();

	// rxd.23 - Dispense Package Size Unit
	if(rxd['RXD.23'].toString().length>0) objPharmacyDispenseOrder.dispensePackageSizeUnit= convertCodedElementToJSON(rxd['RXD.23']);

	// rxd.24 - Dispense Package Method
	if(rxd['RXD.24'].toString().length>0) objPharmacyDispenseOrder.dispensePackageMethod= rxd['RXD.24']['RXD.24.1'].toString();

	// rxd.25 - Supplementary Code
	if(rxd['RXD.25'].toString().length>0) objPharmacyDispenseOrder.supplementaryCode= convertCodedElementToJSON(rxd['RXD.25']);

	// rxd.26 - Initiating Location
	if(rxd['RXD.26'].toString().length>0) objPharmacyDispenseOrder.initiatingLocation= convertCodedElementToJSON(rxd['RXD.26']);

	// rxd.27 - Packaging/Assembly Location
	if(rxd['RXD.27'].toString().length>0) objPharmacyDispenseOrder.packagingLocation= convertCodedElementToJSON(rxd['RXD.27']);

	// rxd.28 - Actual Drug Strength Volume
	if(rxd['RXD.28'].toString().length>0) objPharmacyDispenseOrder.actualDrugStrengthVolume= rxd['RXD.28']['RXD.28.1'].toString();

	// rxd.29 - Actual Drug Strength Volume Units
	if(rxd['RXD.29'].toString().length>0) objPharmacyDispenseOrder.actualDrugStrengthVolumeUnits= convertCodedWithExceptionsToJSON(rxd['RXD.29']);

	// rxd.30 - Dispense to Pharmacy
	if(rxd['RXD.30'].toString().length>0) objPharmacyDispenseOrder.dispenseToPharmacy= convertCodedWithExceptionsToJSON(rxd['RXD.30']);

	// rxd.31 - Dispense to Pharmacy Address
	if(rxd['RXD.31'].toString().length>0) objPharmacyDispenseOrder.dispenseToPharmacyAddress= convertExtendedAddressToJSON(rxd['RXD.31']);

	// rxd.32 - Pharmacy Order Type
	if(rxd['RXD.32'].toString().length>0) objPharmacyDispenseOrder.pharmacyOrderType= rxd['RXD.32']['RXD.32.1'].toString();

	// rxd.33 - Dispense Type
	if(rxd['RXD.33'].toString().length>0) objPharmacyDispenseOrder.dispenseType= convertCodedWithExceptionsToJSON(rxd['RXD.33']);

	if(debug>0) {
		logger.error(scriptName+':DEBUG:: objPharmacyDispenseOrder= '+objPharmacyDispenseOrder);
		logger.error(scriptName+':DEBUG:: commonOrder= '+JSON.stringify(objPharmacyDispenseOrder));
	}

	channelMap.put('PharmacyEncodedOrder',JSON.stringify(objPharmacyDispenseOrder));
	return objPharmacyDispenseOrder;

}

/**
	Desc:  this JS code template is to parse HL7v2 Pharmacy/Treatment Encoded Order (RXE) segment and return a Javascript object

	@param {String} orc - This will contains the XML representation of the HL7v2 RXE segment
	@return {String} return - Returning the Javascript object representation of the conveted HL7v2 RXE segment
	History:
	2022-04-12 jer - Added RXE-11 through RXE-44
	2022-04-11 jer - Added RXE-6 through RXE-10
	2022-04-07 jer - Created from json_rxe_timing_quantity(rxe) method to populate visit centric content
*/
function json_rxe_pharmacy_encoded_order(rxe) {

	var scriptName= 'json_rxe_pharmacy_encoded_order';
	var debug= 0;
	/*
	logger.error(scriptName+':DEBUG:: rxe= '+rxe);;
	*/
	var objPharmacyEncodedOrder= {};

	// RXE.1 - Quantity/Timing
	if(rxe['RXE.1'].toString().length>0) objPharmacyEncodedOrder.quantityTiming= convertTimingQuantityToJSON(rxe['RXE.1']);
	// if(rxe['RXE.1'].toString().length>0) objPharmacyEncodedOrder.setId= rxe['RXE.1']['RXE.1.1'].toString();

	// RXE.2 - Give Code
	if(rxe['RXE.2'].toString().length>0) objPharmacyEncodedOrder.giveCode= convertCodedElementToJSON(rxe['RXE.2']);

	// RXE.3 - Give Amount - Minimum
	if(rxe['RXE.3'].toString().length>0) objPharmacyEncodedOrder.giveAmountMinimum= rxe['RXE.3']['RXE.3.1'].toString();

	// RXE.4 - Give Amount - Maximum
	if(rxe['RXE.4'].toString().length>0) objPharmacyEncodedOrder.giveAmountMaximum= rxe['RXE.4']['RXE.4.1'].toString();

	// RXE.5 - Give Units
	if(rxe['RXE.5'].toString().length>0) objPharmacyEncodedOrder.giveUnits= convertCodedElementToJSON(rxe['RXE.5']);

	// RXE.6 - Give Dosage Form
	if(rxe['RXE.6'].toString().length>0) objPharmacyEncodedOrder.giveDosageForm= convertCodedElementToJSON(rxe['RXE.6']);

	// RXE.7 - Provider's Administration Instructions
	if(rxe['RXE.7'].toString().length>0) objPharmacyEncodedOrder.provideAdminInstructions= convertCodedElementToJSON(rxe['RXE.7']);

	// RXE.8 - Deliver-To Location
	if(rxe['RXE.8'].toString().length>0) objPharmacyEncodedOrder.deliverToLocation= 
	convertLocationAddressVariationToJSON(rxe['RXE.8']);

	// RXE.9 - Substitution Status
	if(rxe['RXE.9'].toString().length>0) objPharmacyEncodedOrder.substitutionStatus= rxe['RXE.9']['RXE.9.1'].toString();

	// RXE.10 - Dispense Amount
	if(rxe['RXE.10'].toString().length>0) objPharmacyEncodedOrder.dispenseAmount= rxe['RXE.10']['RXE.10.1'].toString();

	// RXE.11 - Dispense Units
	if(rxe['RXE.11'].toString().length>0) objPharmacyEncodedOrder.dispenseUnits= convertCodedElementToJSON(rxe['RXE.11']);

	// RXE.12 - Number of Refills
	if(rxe['RXE.12'].toString().length>0) objPharmacyEncodedOrder.substitutionStatus= rxe['RXE.12']['RXE.12.1'].toString();

	// RXE.13 - Order Provider's DEA Number
	if(rxe['RXE.13'].toString().length>0) objPharmacyEncodedOrder.orderingProviderDEANumber=convertExtendedCompositeIDNumberAndNameToJSON(rxe['RXE.13']);

	// RXE.14 - Pharamcist/Treatment Supplier Verifier Id
	if(rxe['RXE.14'].toString().length>0) objPharmacyEncodedOrder.pharmacistSupplierVerifierId=convertExtendedCompositeIDNumberAndNameToJSON(rxe['RXE.14']); 

	// RXE.15 - Prescription Number
	if(rxe['RXE.15'].toString().length>0) objPharmacyEncodedOrder.prescriptionNumber= rxe['RXE.15']['RXE.15.1'].toString();

	// RXE.16 - Number of Refills Remaining
	if(rxe['RXE.16'].toString().length>0) objPharmacyEncodedOrder.refillsRemaining= rxe['RXE.16']['RXE.16.1'].toString();

	// RXE.17 - Number of Refills/Doses Dispensed
	if(rxe['RXE.17'].toString().length>0) objPharmacyEncodedOrder.refillsDispensed= rxe['RXE.17']['RXE.17.1'].toString();

	// RXE.18 - DateTime of Most Recent Refill or Dose Dispensed
	if(rxe['RXE.18'].toString().length>0) objPharmacyEncodedOrder.mostRecentRefillOrDispensed=convertTimestampToJSON(rxe['RXE.18']);

	// RXE.19 - Total Daily Dose
	if(rxe['RXE.19'].toString().length>0) objPharmacyEncodedOrder.totalDailyDose= convertCompositeQuantityWithUnitsToJSON(rxe['RXE.19']);

	// RXE.20 - Needs Human Review
	if(rxe['RXE.20'].toString().length>0) objPharmacyEncodedOrder.refillsDispensed= rxe['RXE.20']['RXE.20.1'].toString();

	// RXE.21 - Pharmacy/Treatment Suppliers Special Dispensing Instructions
	if(rxe['RXE.21'].toString().length>0) objPharmacyEncodedOrder.pharmacySuppliersSpecialDispensingInstructions= convertCodedElementToJSON(rxe['RXE.21']);

	// RXE.22 - Give Per
	if(rxe['RXE.22'].toString().length>0) objPharmacyEncodedOrder.givePer= rxe['RXE.22']['RXE.22.1'].toString();

	// RXE.23 - Give Rate Amount
	if(rxe['RXE.23'].toString().length>0) objPharmacyEncodedOrder.giveRateAmount= rxe['RXE.23']['RXE.23.1'].toString();

	// RXE.24 - Give Rate Units
	if(rxe['RXE.24'].toString().length>0) objPharmacyEncodedOrder.giveRateUnits= convertCodedElementToJSON(rxe['RXE.24']);

	// RXE.25 - Give Strength
	if(rxe['RXE.25'].toString().length>0) objPharmacyEncodedOrder.giveStrength= rxe['RXE.25']['RXE.25.1'].toString();

	// RXE.26 - Give Strength Units
	if(rxe['RXE.26'].toString().length>0) objPharmacyEncodedOrder.giveStrengthUnits= convertCodedElementToJSON(rxe['RXE.26']);

	// RXE.27 - Give Indication
	if(rxe['RXE.27'].toString().length>0) objPharmacyEncodedOrder.giveIndication= convertCodedElementToJSON(rxe['RXE.27']);

	// RXE.28 - Dispense Package Size
	if(rxe['RXE.28'].toString().length>0) objPharmacyEncodedOrder.dispensePackageSize= rxe['RXE.28']['RXE.28.1'].toString();

	// RXE.29 - Dispense Package Size Unit
	if(rxe['RXE.29'].toString().length>0) objPharmacyEncodedOrder.dispensePackageSizeUnit= convertCodedElementToJSON(rxe['RXE.29']);

	// RXE.30 - Dispense Package Method
	if(rxe['RXE.30'].toString().length>0) objPharmacyEncodedOrder.dispensePackageMethod= rxe['RXE.30']['RXE.30.1'].toString();

	// RXE.31 - Supplementary Code
	if(rxe['RXE.31'].toString().length>0) objPharmacyEncodedOrder.supplementaryCode= convertCodedElementToJSON(rxe['RXE.31']);

	// RXE.32 - Original Order DateTime
	if(rxe['RXE.32'].toString().length>0) objPharmacyEncodedOrder.originalOrderDateTime=convertTimestampToJSON(rxe['RXE.32']);

	// RXE.33 - Give Drug Strength Volume
	if(rxe['RXE.33'].toString().length>0) objPharmacyEncodedOrder.giveDrugStengthVolume= rxe['RXE.33']['RXE.33.1'].toString();

	// RXE.34 - Give Drug Stength Volume Units
	if(rxe['RXE.34'].toString().length>0) objPharmacyEncodedOrder.giveDrugStengthVolumeUnits= convertCodedWithExceptionsToJSON(rxe['RXE.34']);

	// RXE.35 - Controlled Substance Schedule
	if(rxe['RXE.35'].toString().length>0) objPharmacyEncodedOrder.controlledSubstanceSchedule= convertCodedWithExceptionsToJSON(rxe['RXE.35']);

	// RXE.36 - Formulary Status
	if(rxe['RXE.36'].toString().length>0) objPharmacyEncodedOrder.formularyStatus= rxe['RXE.36']['RXE.36.1'].toString();

	// RXE.37 - Pharmaceutical Substance Alternative
	if(rxe['RXE.37'].toString().length>0) objPharmacyEncodedOrder.pharmaceuticalSubstanceAlternative= convertCodedWithExceptionsToJSON(rxe['RXE.37']);

	// RXE.38 - Pharmacy of Most Recent Fill
	if(rxe['RXE.38'].toString().length>0) objPharmacyEncodedOrder.pharmacyMostRecentFill= convertCodedWithExceptionsToJSON(rxe['RXE.38']);

	// RXE.39 - Initial Dispense Amount
	if(rxe['RXE.39'].toString().length>0) objPharmacyEncodedOrder.initialDispenseAmount= rxe['RXE.39']['RXE.39.1'].toString();

	// RXE.40 - Dispensing Pharmacy
	if(rxe['RXE.40'].toString().length>0) objPharmacyEncodedOrder.dispensingPharmacy= convertCodedWithExceptionsToJSON(rxe['RXE.40']);

	// RXE.41 - Dispensing Pharmacy Address
	if(rxe['RXE.41'].toString().length>0) objPharmacyEncodedOrder.dispensingPharmacyAddress= convertExtendedAddressToJSON(rxe['RXE.41']);

	// RXE.42 - Deliver to Patient Location
	if(rxe['RXE.42'].toString().length>0) objPharmacyEncodedOrder.deliverToPatientLocation= convertPersonLocationToJSON(rxe['RXE.42']);

	// RXE.43 - Deliver to Address
	if(rxe['RXE.43'].toString().length>0) objPharmacyEncodedOrder.deliverToAddress= convertExtendedAddressToJSON(rxe['RXE.43']);

	// RXE.44 - Pharmacy Order Type
	if(rxe['RXE.44'].toString().length>0) objPharmacyEncodedOrder.pharmacyOrderType= rxe['RXE.44']['RXE.44.1'].toString();

	if(debug>0) {
		logger.error(scriptName+':DEBUG:: objPharmacyEncodedOrder= '+objPharmacyEncodedOrder);
		logger.error(scriptName+':DEBUG:: commonOrder= '+JSON.stringify(objPharmacyEncodedOrder));
	}

	channelMap.put('PharmacyEncodedOrder',JSON.stringify(objPharmacyEncodedOrder));
	return objPharmacyEncodedOrder;

}

/**
	Desc:  this JS code template is to parse HL7v2 Pharmacy/Treatment Order (RXO) segment and return a Javascript object

	@param {String} orc - This will contains the XML representation of the HL7v2 rxo segment
	@return {String} return - Returning the Javascript object representation of the conveted HL7v2 rxo segment
	History:
	2022-04-12 jer - Added rxo-11 through rxo-44
	2022-04-11 jer - Added rxo-6 through rxo-10
	2022-04-07 jer - Created from json_rxo_timing_quantity(rxo) method to populate visit centric content
*/
function json_rxo_pharmacy_treatment_order(rxo) {

	var scriptName= 'json_rxo_pharmacy_treatment_order';
	var debug= 0;
	/*
	logger.error(scriptName+':DEBUG:: rxo= '+rxo);;
	*/
	var objPharmacyTreatmentOrder= {};

	// rxo.1 - Requested Give Code
	if(rxo['rxo.1'].toString().length>0) objPharmacyTreatmentOrder.requestedGiveCode= convertCodedElementToJSON(rxo['rxo.1']);

	// rxo.2 - Requested Give Amount - Minimum
	if(rxo['rxo.2'].toString().length>0) objPharmacyTreatmentOrder.requestedGiveAmountMinimum= rxo['rxo.2']['rxo.2.1'].toString();

	// rxo.3 - Requested Give Amount - Maximum
	if(rxo['rxo.3'].toString().length>0) objPharmacyTreatmentOrder.requestedGiveAmountMaximum= rxo['rxo.3']['rxo.3.1'].toString();

	// rxo.4 - Requested Give Units
	if(rxo['rxo.4'].toString().length>0) objPharmacyTreatmentOrder.requestedGiveUnits= convertCodedElementToJSON(rxo['rxo.4']);

	// rxo.5 - Requested Give Dosage Form
	if(rxo['rxo.5'].toString().length>0) objPharmacyTreatmentOrder.requestedGiveDosageForm= convertCodedElementToJSON(rxo['rxo.5']);

	// rxo.6 - Provider's Treatment Instructions
	if(rxo['rxo.6'].toString().length>0) objPharmacyTreatmentOrder.providerTreatmentInstructions= convertCodedElementToJSON(rxo['rxo.6']);

	// rxo.7 - Provider's Administration Instructions
	if(rxo['rxo.7'].toString().length>0) objPharmacyTreatmentOrder.providerAdminInstructions= convertCodedElementToJSON(rxo['rxo.7']);

	// rxo.8 - Deliver-To Location
	if(rxo['rxo.8'].toString().length>0) objPharmacyTreatmentOrder.deliverToLocation= convertLocationAddressVariationToJSON(rxo['rxo.8']);

	// rxo.9 - Allow Substitutions ID
	if(rxo['rxo.9'].toString().length>0) objPharmacyTreatmentOrder.allowSubstitutions= rxo['rxo.9']['rxo.9.1'].toString();

	// rxo.10 - Requested Dispense Code
	if(rxo['rxo.10'].toString().length>0) objPharmacyTreatmentOrder.requestedDispenseCode= rxo['rxo.10']['rxo.10.1'].toString();

	// rxo.11 - Requested Dispense Amount
	if(rxo['rxo.11'].toString().length>0) objPharmacyTreatmentOrder.requestedDispenseAmount= rxo['rxo.10']['rxo.11.1'].toString();

	// rxo.12 - Requested Dispense Units
	if(rxo['rxo.12'].toString().length>0) objPharmacyTreatmentOrder.requestedDispenseUnits= convertCodedElementToJSON(rxo['rxo.12']);

	// rxo.13 - Number of Refills
	if(rxo['rxo.13'].toString().length>0) objPharmacyTreatmentOrder.numberOfRefills= rxo['rxo.13']['rxo.13.1'].toString();

	// rxo.14 - Order Provider's DEA Number
	if(rxo['rxo.14'].toString().length>0) objPharmacyTreatmentOrder.orderingProviderDEANumber=convertExtendedCompositeIDNumberAndNameToJSON(rxo['rxo.14']);

	// rxo.15 - Pharamcist/Treatment Supplier Verifier Id
	if(rxo['rxo.15'].toString().length>0) objPharmacyTreatmentOrder.pharmacistSupplierVerifierId=convertExtendedCompositeIDNumberAndNameToJSON(rxo['rxo.15']); 

	// rxo.16 - Needs Human Review
	if(rxo['rxo.16'].toString().length>0) objPharmacyTreatmentOrder.needsHumanReview= rxo['rxo.16']['rxo.16.1'].toString();

	// rxo.17 - Requested Give Per
	if(rxo['rxo.17'].toString().length>0) objPharmacyTreatmentOrder.requestedGivePer= rxo['rxo.17']['rxo.17.1'].toString();

	// rxo.18 - Requested Give Strength
	if(rxo['rxo.18'].toString().length>0) objPharmacyTreatmentOrder.requestedGiveStrength= rxo['rxo.18']['rxo.18.1'].toString();

	// rxo.19 - Requested Give Strength Units
	if(rxo['rxo.19'].toString().length>0) objPharmacyTreatmentOrder.requestedGiveStrengthUnits= convertCodedElementToJSON(rxo['rxo.19']);

	// rxo.20 - Requested Give Indication
	if(rxo['rxo.20'].toString().length>0) objPharmacyTreatmentOrder.requestedGiveIndication= convertCodedElementToJSON(rxo['rxo.20']);

	// rxo.21 - Requested Give Rate Amount
	if(rxo['rxo.21'].toString().length>0) objPharmacyTreatmentOrder.requestedGiveRateAmount= rxo['rxo.23']['rxo.21.1'].toString();

	// rxo.22 - Requested Give Rate Units
	if(rxo['rxo.22'].toString().length>0) objPharmacyTreatmentOrder.requestedGiveRateUnits= convertCodedElementToJSON(rxo['rxo.22']);

	// rxo.23 - Total Daily Dose
	if(rxo['rxo.23'].toString().length>0) objPharmacyTreatmentOrder.totalDailyDose= convertCompositeQuantityWithUnitsToJSON(rxo['rxo.23']);

	// rxo.24 - Supplementary Code
	if(rxo['rxo.24'].toString().length>0) objPharmacyTreatmentOrder.supplementaryCode= convertCodedElementToJSON(rxo['rxo.24']);

	// rxo.25 - Requested Give Drug Strength Volume
	if(rxo['rxo.25'].toString().length>0) objPharmacyTreatmentOrder.requestedGiveDrugStengthVolume= rxo['rxo.25']['rxo.25.1'].toString();

	// rxo.26 - Give Drug Stength Volume Units
	if(rxo['rxo.26'].toString().length>0) objPharmacyTreatmentOrder.requestedGiveDrugStengthVolumeUnits= convertCodedWithExceptionsToJSON(rxo['rxo.26']);

	// rxo.27 - Pharmacy Order Type
	if(rxo['rxo.27'].toString().length>0) objPharmacyTreatmentOrder.pharmacyOrderType= rxo['rxo.27']['rxo.27.1'].toString();

	// rxo.28 - Dispensing Interval
	if(rxo['rxo.28'].toString().length>0) objPharmacyTreatmentOrder.dispensingInterval= rxo['rxo.28']['rxo.28.1'].toString();

	if(debug>0) {
		logger.error(scriptName+':DEBUG:: objPharmacyTreatmentOrder= '+objPharmacyTreatmentOrder);
		logger.error(scriptName+':DEBUG:: commonOrder= '+JSON.stringify(objPharmacyTreatmentOrder));
	}

	channelMap.put('PharmacyTreatmentOrder',JSON.stringify(objPharmacyTreatmentOrder));
	return objPharmacyTreatmentOrder;

}

/**
	Desc:  this JS code template is to parse HL7v2 Pharmacy/Treatment Route (RXR) segment and return a Javascript object

	@param {String} orc - This will contains the XML representation of the HL7v2 RXR segment
	@return {String} return - Returning the Javascript object representation of the conveted HL7v2 RXR segment
	History:
	2022-04-15 jer - Finalized the script
	2022-04-14 jer - Created from json_rxd_timing_quantity(rxr) method to populate visit centric content
*/
function json_rxr_pharmacy_treatment_route(rxr) {

	var scriptName= 'json_rxr_pharmacy_treatment_route';
	var debug= 0;
	/*
	logger.error(scriptName+':DEBUG:: rxr= '+rxr);;
	*/
	var objPharmacyTreatmentRoute= {};

	// rxr.1 - Route
	if(rxr['RXR.1'].toString().length>0) objPharmacyTreatmentRoute.route= rxr['RXR.1']['RXR.1.1'].toString();

	// rxr.2 - Administrative Site
	if(rxr['RXR.2'].toString().length>0) objPharmacyTreatmentRoute.administrativeSite= convertCodedWithExceptionsToJSON(rxr['RXR.2']);

	// rxr.3 - Adminisrative Device
	if(rxr['RXR.3'].toString().length>0) objPharmacyTreatmentRoute.administrativeDevice= convertCodedElementToJSON(rxr['RXR.3']);

	// rxr.4 - Adminisrative Method
	if(rxr['RXR.4'].toString().length>0) objPharmacyTreatmentRoute.administrativeMethod= convertCodedWithExceptionsToJSON(rxr['RXR.4']);

	// rxr.5 - Routing Instructions
	if(rxr['RXR.5'].toString().length>0) objPharmacyTreatmentRoute.routingInstructions= convertCodedElementToJSON(rxr['RXR.5']);

	// rxr.6 - Administrative Site Modifier
	if(rxr['RXR.6'].toString().length>0) objPharmacyTreatmentRoute.administrativeSiteModifier= convertCodedWithExceptionsToJSON(rxr['RXR.6']);

	if(debug>0) {
		logger.error(scriptName+':DEBUG:: objPharmacyTreatmentRoute= '+objPharmacyTreatmentRoute);
		logger.error(scriptName+':DEBUG:: Pharmacy Treatment Route= '+JSON.stringify(objPharmacyTreatmentRoute));
	}

	channelMap.put('PharmacyTreatmentRoute',JSON.stringify(objPharmacyTreatmentRoute));
	return objPharmacyTreatmentRoute;

}

/**
	Desc:  this JS code template is to parse HL7v2 TQ1 segment and return a Javascript object

	@param {String} orc - This will contains the XML representation of the HL7v2 TQ1 segment
	@return {String} return - Returning the Javascript object representation of the conveted HL7v2 TQ1 segment
	History:
	2023-12-12 JER - Modified to handle multiple values in Timing Quantity Explicit Time TQ1.4
	2022-04-07 jer - Created from json_tq1_timing_quantity(tq1) method to populate visit centric content
*/
function json_tq1_timing_quantity(tq1) {

	var scriptName= 'json_tq1_timing_quantity';
	var debug= 0;
	/*
	logger.error(scriptName+':DEBUG:: tq1= '+tq1);;
	*/
	var objTimingQuantity= {};

	if(tq1['TQ1.1'].toString().length>0) objTimingQuantity.setId= tq1['TQ1.1']['TQ1.1.1'].toString();
	// 2022-04-07 jer - Added convertCompositeQuantityWithUnitsToJSON code template
	if(tq1['TQ1.2'].toString().length>0) objTimingQuantity.quantity= convertCompositeQuantityWithUnitsToJSON(tq1['TQ1.2']);
	if(tq1['TQ1.3'].toString().length>0) objTimingQuantity.repeatPattern= convertRepeatPatternToJSON(tq1['TQ1.3']);
	// 2023-12-12 JER - Modified to handle multiple values in Timing Quantity Explicit Time TQ1.4
	if(tq1['TQ1.4'].toString().length>0) {
		if(tq1['TQ1.4']['TQ1.4.1'].length()>1) {
			var arrExplicitTime= [];
			for each(explicitTime in tq1['TQ1.4']['TQ1.4.1']) {
				var objExplicitTime= {};
				objExplicitTime.explicitTime= explicitTime.toString();
				if(debug>0) logger.error('DEBUG::objExplicitTime: '+JSON.stringify(objExplicitTime));				arrExplicitTime.push(objExplicitTime);
			} // end for each
			if(debug>0) logger.error('DEBUG::arrExplicitTime: '+JSON.stringify(arrExplicitTime));
			objTimingQuantity= arrExplicitTime;
			if(debug>0) logger.debug('DEBUG::Code Template:'+scriptName+' List of Explicit Time(s): '+JSON.stringify(arrExplicitTime));
		} else {
			objTimingQuantity.explicitTime= tq1['TQ1.4']['TQ1.4.1'].toString();
			if(debug>0) logger.debug('DEBUG::Code Template:'+scriptName+' Singular Explicit Time: '+arrExplicitTime);
		} // end if 
	} // end if 
	// if(tq1['TQ1.4'].toString().length>0) objTimingQuantity.explicitTime= tq1['TQ1.4']['TQ1.4.1'][0].toString();
	if(tq1['TQ1.5'].toString().length>0) objTimingQuantity.relativeTimeAndUnits= convertCompositeQuantityWithUnitsToJSON(tq1['TQ1.5']);
	if(tq1['TQ1.6'].toString().length>0) objTimingQuantity.serviceDuration= convertCompositeQuantityWithUnitsToJSON(tq1['TQ1.6']);
	if(tq1['TQ1.7'].toString().length>0) objTimingQuantity.startDateTime= convertTimestampToJSON(tq1['TQ1.7']);
	if(tq1['TQ1.8'].toString().length>0) objTimingQuantity.endDateTime= convertTimestampToJSON(tq1['TQ1.8']);
	if(tq1['TQ1.9'].toString().length>0) objTimingQuantity.priority= convertCodedWithExceptionsToJSON(tq1['TQ1.9']);	
	if(tq1['TQ1.10'].toString().length>0) objTimingQuantity.conditionText= tq1['TQ1.10']['TQ1.10.1'].toString();
	if(tq1['TQ1.11'].toString().length>0) objTimingQuantity.textInstruction= tq1['TQ1.11']['TQ1.11.1'].toString();
	if(tq1['TQ1.12'].toString().length>0) objTimingQuantity.conjunction= tq1['TQ1.12']['TQ1.12.1'].toString();
	if(tq1['TQ1.13'].toString().length>0) objTimingQuantity.occurenceDuration= convertCompositeQuantityWithUnitsToJSON(tq1['TQ1.13']);
	if(tq1['TQ1.14'].toString().length>0) objTimingQuantity.totalOccurrences= tq1['TQ1.14']['TQ1.14.1'].toString();

	if(debug>0) {
		logger.error(scriptName+':DEBUG:: objTimingQuantity= '+objTimingQuantity);
		logger.error(scriptName+':DEBUG:: commonOrder= '+JSON.stringify(objTimingQuantity));
	}

	channelMap.put('timingQuantity',JSON.stringify(objTimingQuantity));
	return objTimingQuantity;

}

/**
	Desc:  this JS code template is to parse Soft Writers Framework HL7 Additional Prescription Information Segment (ZPI) segment and return a Javascript object

	@param {String} zpi - This will contains the XML representation of the HL7v2 ZPI segment
	@return {String} return - Returning the Javascript object representation of the conveted HL7v2 ZPI segment
	History:
	2022-04-15 jer - Finalized remaining properties 
	2022-04-14 jer - Created from json_zpi_add_prescription_information(zpi) method to populate additional prescription information content
*/
function json_zpi_add_prescription_information(zpi) {

	var scriptName= 'json_zpi_add_prescription_information';
	var debug= 0;
	/*
	logger.error(scriptName+':DEBUG:: zpi= '+zpi);;
	*/
	var objAddPrescriptionInfo= {};

	// zpi.1 - Package Type (type: ST, len: 10)
	if(zpi['ZPI.1'].toString().length>0) objAddPrescriptionInfo.packageType= zpi['ZPI.1']['ZPI.1.1'].toString();

	// zpi.2 - Number of Labels (type: NM, len: 5)
	if(zpi['ZPI.2'].toString().length>0) objAddPrescriptionInfo.numberOfLabels= zpi['ZPI.2']['ZPI.2.1'].toString();

	// zpi.3 - Label Quantity (type: NM, len: 18)
	if(zpi['ZPI.3'].toString().length>0) objAddPrescriptionInfo.labelQuantity= zpi['ZPI.3']['ZPI.3.1'].toString();

	// zpi.4 - Zone (type: ST, len: 10)
	if(zpi['ZPI.4'].toString().length>0) objAddPrescriptionInfo.zone= zpi['ZPI.4']['ZPI.4.1'].toString();

	// zpi.5 - Prescription Bin (type: ST, len: 10)
	if(zpi['ZPI.5'].toString().length>0) objAddPrescriptionInfo.prescriptionBin= zpi['ZPI.5']['ZPI.5.1'].toString();

	// zpi.6 - Total Quantity Written (type: NM, len: 18)
	if(zpi['ZPI.6'].toString().length>0) objAddPrescriptionInfo.totalQuantityWritten= zpi['ZPI.6']['ZPI.6.1'].toString();

	// zpi.7 - Total Quantity Dispensed (type: NM, len: 18)
	if(zpi['ZPI.7'].toString().length>0) objAddPrescriptionInfo.totalQuantityDispensed= zpi['ZPI.7']['ZPI.7.1'].toString();

	// zpi.8 - Fill Quantity Written (type: NM, len: 18)
	if(zpi['ZPI.8'].toString().length>0) objAddPrescriptionInfo.fillQuantityWritten= zpi['ZPI.8']['ZPI.8.1'].toString();

	// zpi.9 - Maximum Daily Dose Allowed (type: NM, len: 18)
	if(zpi['ZPI.9'].toString().length>0) objAddPrescriptionInfo.maxDailyDoseAllowed= zpi['ZPI.9']['ZPI.9.1'].toString();

	// zpi.10 - Days Supplied (type: NM, len: 5)
	if(zpi['ZPI.10'].toString().length>0) objAddPrescriptionInfo.daysSupplied= zpi['ZPI.10']['ZPI.10.1'].toString();

	// zpi.11 - Times Per Day (type: NM, len: 5)
	if(zpi['ZPI.11'].toString().length>0) objAddPrescriptionInfo.timesPerDay= zpi['ZPI.11']['ZPI.11.1'].toString();

	// zpi.12 - Date Written (type: TS, len: 26)
	if(zpi['ZPI.11'].toString().length>0) objAddPrescriptionInfo.dateWritten= convertTimestampToJSON(zpi['ZPI.12']);

	// zpi.13 - Prepack (type: ST, len: 1)
	if(zpi['ZPI.13'].toString().length>0) objAddPrescriptionInfo.prepack= zpi['ZPI.13']['ZPI.13.1'].toString();

	// zpi.14 - Cycle Fill (type: ST, len: 1)
	if(zpi['ZPI.14'].toString().length>0) objAddPrescriptionInfo.cycleFill= zpi['ZPI.14']['ZPI.14.1'].toString();

	// zpi.15 - MAR Group Level (type: ST, len: 1)
	if(zpi['ZPI.15'].toString().length>0) objAddPrescriptionInfo.MARGroupLevel= zpi['ZPI.15']['ZPI.15.1'].toString();

	// zpi.16 - MAR Group (type: ST, len: 1= value: Y|N)
	if(zpi['ZPI.16'].toString().length>0) objAddPrescriptionInfo.MARGroup= zpi['ZPI.16']['ZPI.16.1'].toString();

	// zpi.17 - Partial Status (type: ST, len: 1)
	if(zpi['ZPI.17'].toString().length>0) objAddPrescriptionInfo.partialStatus= zpi['ZPI.17']['ZPI.17.1'].toString();

	// zpi.18 - Intended Quantity (type: NM, len: 9)
	if(zpi['ZPI.18'].toString().length>0) objAddPrescriptionInfo.intendedQuantity= zpi['ZPI.18']['ZPI.18.1'].toString();

	// zpi.19 - Intended Days Supply (type: NM, len: 5)
	if(zpi['ZPI.19'].toString().length>0) objAddPrescriptionInfo.intendedDaysSupplied= zpi['ZPI.19']['ZPI.19.1'].toString();

	// zpi.20 - Origin Code (type: ST, len: 1)
	if(zpi['ZPI.20'].toString().length>0) objAddPrescriptionInfo.originCode= zpi['ZPI.20']['ZPI.20.1'].toString();

	// zpi.21 - Rx GUID (type: ST, len: 50)
	if(zpi['ZPI.21'].toString().length>0) objAddPrescriptionInfo.rxGuid= zpi['ZPI.21']['ZPI.21.1'].toString();

	// zpi.22 - Tote ID (type: ST, len: 10 => value: Y|N)
	if(zpi['ZPI.22'].toString().length>0) objAddPrescriptionInfo.toteId= zpi['ZPI.22']['ZPI.22.1'].toString();

	// zpi.23 - Custom Field (type: CE)
	// Components: <Identifier (ST)> ^ <Text (ST)> ^ <Name of Coding System (ID)> ^ <Alternate Identifier (ST)> ^<Alternate Text (ST)> ^ <Name of Alternate Coding System(ID)>
	if(zpi['ZPI.23'].toString().length>0) objAddPrescriptionInfo.customField= convertCodedElementToJSON(zpi['ZPI.23']);

	// zpi.24 - Rx Type (type: ST, len: 1 => value: A= Acute, C= Chronic, P= PRN, S= Supply)
	if(zpi['ZPI.24'].toString().length>0) objAddPrescriptionInfo.rxType= zpi['ZPI.24']['ZPI.24.1'].toString();

	// zpi.25 - Linked Reorder Number (type: ST, len: 10)
	if(zpi['ZPI.25'].toString().length>0) objAddPrescriptionInfo.linkedReorderNumber= zpi['ZPI.25']['ZPI.25.1'].toString();

	// zpi.26 - Extra Dose Indicator (type: ST, len: 1 => value: Y|N)
	if(zpi['ZPI.26'].toString().length>0) objAddPrescriptionInfo.extraDoseIndicator= zpi['ZPI.26']['ZPI.26.1'].toString();

	// zpi.27 - Leave of Absence Indicator (type: ST, len: 1 => value: Y|N)
	if(zpi['ZPI.27'].toString().length>0) objAddPrescriptionInfo.leaveOfAbsenceIndicator= zpi['ZPI.27']['ZPI.27.1'].toString();

	// zpi.28 - Delivery ID (type: ST, len: 10)
	if(zpi['ZPI.28'].toString().length>0) objAddPrescriptionInfo.deliveryId= zpi['ZPI.28']['ZPI.28.1'].toString();

	// zpi.29 - Partial Tablet Indicator (type: ST, len: 1 => value: Y|N)
	if(zpi['ZPI.29'].toString().length>0) objAddPrescriptionInfo.partialTabletIndicator= zpi['ZPI.29']['ZPI.29.1'].toString();

	// zpi.30 - Raw Administration Times (type: ST, len: 250)
	/*
	This field will contain the raw administration times exactly as keyed by the pharmacy. Each repeat will represent a split. Multiple administration times for the same split will be delimited by a comma. Blank time boxes will be included. Leading and trailing spaces will be not trimmed.
	*/
	if(zpi['ZPI.30'].toString().length>0) objAddPrescriptionInfo.rawAdministrationTimes= zpi['ZPI.30']['ZPI.30.1'].toString();

	// zpi.31 - Product Type (type: ST, len: 1 => value: D= Drug Product, C= Compound, I= IV)
	if(zpi['ZPI.31'].toString().length>0) objAddPrescriptionInfo.productType= zpi['ZPI.31']['ZPI.31.1'].toString();

	// zpi.32 - Treatment (type: ST, len: 1 => value: Y|N)
	if(zpi['ZPI.32'].toString().length>0) objAddPrescriptionInfo.treatment= zpi['ZPI.32']['ZPI.32.1'].toString();

	// zpi.33 - PhRxExpireDate (type: TS, len: 26)
	if(zpi['ZPI.33'].toString().length>0) objAddPrescriptionInfo.phRxExpireDate= convertTimestampToJSON(zpi['ZPI.33']);

	// zpi.34 - Rx Number (type: ST, len: 20)
	if(zpi['ZPI.34'].toString().length>0) objAddPrescriptionInfo.rxNumber= zpi['ZPI.34']['ZPI.34.1'].toString();

	if(debug>0) {
		logger.error(scriptName+':DEBUG:: objAddPrescriptionInfo= '+objAddPrescriptionInfo);
		logger.error(scriptName+':DEBUG:: commonOrder= '+JSON.stringify(objAddPrescriptionInfo));
	}

	channelMap.put('PharmacyEncodedOrder',JSON.stringify(objAddPrescriptionInfo));
	return objAddPrescriptionInfo;

}

/**
	Desc:  this JS code template function is to parse HL7v2 PID-3 element and return a JSON Javascript object 
	File:  set_patient_identifier.js

	@param {String} pid3 - This will contains the XML representation of the HL7v2 PID-3 element (e.g. PID-3 or PID-4 or PID-18)
	@return {String} return - Returning the Javascript object representation of the converted JSON patient identifier element object.

	History:
	2024-01-15 JER - if pid3 contains indexOf("\\") store the original patient Identifier as alternateIdentifier
*/
function set_patient_identifier(pid3, domain) {			

	var scriptName= 'set_patient_identifier';
	var debug= 0;
	var arrPatientIdentifiers= new Array();
	// if(debug>0) { 
		logger.debug('DEBUG::'+channelName+':'+scriptName+' pid3: '+pid3);
		logger.debug('DEBUG::'+channelName+':'+scriptName+' domain: '+domain);
	// } // end if 

	for each(_pid3 in pid3) {

		var objPatient= new Object();
		// if expected to evaluate first element in pid3 for domain (aka entity)

		// PID.3
		// if element value contains \\ prefix string (e.g. "ZTEST\\F\\2847") - remove the prefix string
		if(_pid3.indexOf('3')>-1) {

			if(_pid3['PID.3.1'].toString().indexOf("\\")>-1) {

				var lstPatientId= _pid3['PID.3.1'].toString().split("\\");
				var patientId= lstPatientId[(lstPatientId.length-1)];  // 2847
				var altId= _pid3['PID.3.1'].toString();

				if(domain== 1) { 
					var entity= lstPatientId[0]; // ZTEST
				} else if(domain== 3) {
					var entity = _pid3['PID.3.3'].toString();
				} else if(domain== 4) {
					var entity = _pid3['PID.3.4'].toString();
				} else if(domain== 5) {
					var entity = _pid3['PID.3.5'].toString();
				} else {
					var entity = _pid3['PID.3.4'].toString();
				} // end if 

			} else {

				var patientId= _pid3['PID.3.1'].toString();

				if(domain== 3) {
					var entity = _pid3['PID.3.3'].toString();
				} else if(domain== 4) {
					var entity = _pid3['PID.3.4'].toString();
				} else if(domain== 5) {
					var entity = _pid3['PID.3.5'].toString();
				} else {
					var entity = _pid3['PID.3.4'].toString();
				} // end if 

			} // end if 

			// 2022-02-22 jer - verify if PID.3.5 exists if not is undefined
			if(_pid3['PID.3.5'].toString().length>0) {
				var IDCode = _pid3['PID.3.5'].toString();
			} // end if 
		} // end if 

		// PID.4
		// if element value contains \\ prefix string (e.g. "ZTEST\\F\\2847") - remove the prefix string
		if(_pid3.indexOf('4')>-1) {

			if(_pid3['PID.4.1'].toString().indexOf("\\")>-1) {

				var lstPatientId= _pid3['PID.4.1'].toString().split("\\");
				var patientId= lstPatientId[(lstPatientId.length-1)];  // 2847
				var altId= _pid3['PID.3.1'].toString();

				if(domain== 1) { 
					var entity= lstPatientId[0]; // ZTEST
				} else if(domain== 3) {
					var entity = _pid3['PID.4.3'].toString();
				} else if(domain== 4) {
					var entity = _pid3['PID.4.4'].toString();
				} else if(domain== 5) {
					var entity = _pid3['PID.4.5'].toString();
				} else {
					var entity = _pid3['PID.4.4'].toString();
				} // end if 

			} else {

				var patientId= _pid3['PID.4.1'].toString();

				if(domain== 3) {
					var entity = _pid3['PID.4.3'].toString();
				} else if(domain== 4) {
					var entity = _pid3['PID.4.4'].toString();
				} else if(domain== 5) {
					var entity = _pid3['PID.4.5'].toString();
				} else {
					var entity = _pid3['PID.4.4'].toString();
				} // end if 

			} // end if 

			// 2022-02-22 jer - verify if PID.4.5 exists if not is undefined
			if(_pid3['PID.4.5'].toString().length>0) {
				var IDCode = _pid3['PID.4.5'].toString();
			} // end if 

		} // end if 

		// PID.18
		// if element value contains \\ prefix string (e.g. "ZTEST\\F\\2847") - remove the prefix string
		if(_pid3.indexOf('18')>-1) {

			if(_pid3['PID.18.1'].toString().length>0) var patientId= _pid3['PID.18.1'].toString();
			if(_pid3['PID.18.4'].toString().length>0) var entity = 	_pid3['PID.18.4'].toString();
			if(_pid3['PID.18.5'].toString().length>0) var IDCode = 	_pid3['PID.18.5'].toString();
		} // end if 	

		// 2022-02-22 jer - verify if undefined or defined with value of MRN
		if((typeof(IDCode)== 'undefined') || (IDCode.indexOf('MRN')>-1)) {
			if(entity != null) {
				// 2022-08-25 JER - Modified from mrn and mrnDomain to identifier and assigningAuthorityName
				objPatient.identifier= patientId;
				objPatient.assigningAuthorityName= entity;

				if(typeof(altId) !== 'undefined') {
					objPatient.alternateIdentifier= altId;
					channelMap.put('alternateIdentifier',altId);
				} // end if 

				channelMap.put('identifier',patientId);
				channelMap.put('assigningAuthorityName',entity);
			} // end if 
		} else if (IDCode.indexOf("MR")>-1) {
			if(entity != null) {
				// 2022-08-25 JER - Modified from mpi and mpiDomain to mpiIdentifier and globalAssigningAuthorityName
				objPatient.mpiIdentifier= patientId;
				objPatient.globalAssigningAuthorityName= entity;

				if(typeof(altId) !== 'undefined') {
					objPatient.alternateIdentifier= altId;
					channelMap.put('alternateIdentifier',altId);
				} // end if 

				channelMap.put('mpiIdentifier',patientId);
				channelMap.put('globalAssigningAuthorityName',entity);
			} // end if 
		} else {
			if(entity != null) {
				// 2022-08-25 JER - Modified from mpi and mpiDomain to mpiIdentifier and globalAssigningAuthorityName
				objPatient.mpiIdentifier= patientId;
				objPatient.globalAssigningAuthorityName= entity;

				if(typeof(altId) !== 'undefined') {
					objPatient.alternateIdentifier= altId;
					channelMap.put('alternateIdentifier',altId);
				} // end if 

				channelMap.put('mpiIdentifier',patientId); 
				channelMap.put('globalAssigningAuthorityName',entity);
			} // end if 
		} // end if 
		arrPatientIdentifiers.push(objPatient);
	} // end for

	channelMap.put('arrPatientIdentifiers',JSON.stringify(arrPatientIdentifiers));
	return arrPatientIdentifiers;

} // end function

/**
	getEVN_v23(msg)

	- Desc: This function retrieves HL7v2 HAPI format elements and values from EVN segment stored in
	object for reference

	@param {String} msg - this contains the raw message (string)
	@return {object} objEVN - this contains the EVN segment elements composed into a single objMSH
		object.
*/
function getEVN_v23(msg) {

	// define variables
	if (typeof(msg['EVN'].toString()) !== undefined) {

		// EVN	
		var objEVN= new Object();

		if (msg['EVN'].length() > 0) {
			objEVN = [];
			var index = 0;
			for each (seg in msg.EVN) {
				objEVN[index] = {};

				// EVN.1, eventType
				if (seg['EVN.1']['EVN.1.1'].toString() != "") { objEVN[index].eventType = seg['EVN.1']['EVN.1.1']; }

				// EVN.2, recordedDateTime
				if (seg['EVN.2']['EVN.2.1'].toString() != "") { objEVN[index].recordedDateTime = seg['EVN.2']['EVN.2.1']; }

				// EVN.3, plannedEventDateTime
				if (seg['EVN.3']['EVN.3.1'].toString() != "") { objEVN[index].plannedEventDateTime = seg['EVN.3']['EVN.3.1']; }

				// EVN.4, eventReasonCode
				if (seg['EVN.4']['EVN.4.1'].toString() != "") { objEVN[index].eventReasonCode = seg['EVN.4']['EVN.4.1']; }

				// EVN.5, operatorId
				objEVN[index].operatorId = {};
				if (seg['EVN.5']['EVN.5.1'].toString() != "") { objEVN[index].operatorId.id = seg['EVN.5']['EVN.5.1']; }
				if (seg['EVN.5']['EVN.5.2'].toString() != "") { objEVN[index].operatorId.lastName = seg['EVN.5']['EVN.5.2']; }
				if (seg['EVN.5']['EVN.5.3'].toString() != "") { objEVN[index].operatorId.firstName = seg['EVN.5']['EVN.5.3']; }
				if (seg['EVN.5']['EVN.5.4'].toString() != "") { objEVN[index].operatorId.middleInitOrName = seg['EVN.5']['EVN.5.4']; }
				if (seg['EVN.5']['EVN.5.5'].toString() != "") { objEVN[index].operatorId.suffix = seg['EVN.5']['EVN.5.5']; }
				if (seg['EVN.5']['EVN.5.6'].toString() != "") { objEVN[index].operatorId.prefix = seg['EVN.5']['EVN.5.6']; }
				if (seg['EVN.5']['EVN.5.7'].toString() != "") { objEVN[index].operatorId.degree = seg['EVN.5']['EVN.5.7']; }
				if (seg['EVN.5']['EVN.5.8'].toString() != "") { objEVN[index].operatorId.sourceTable = seg['EVN.5']['EVN.5.8']; }
				objEVN[index].operatorId.assigningAuthority = {};
				if (seg['EVN.5']['EVN.5.9']['EVN.5.9.1'].toString() != "") { objEVN[index].operatorId.assigningAuthority.id = seg['EVN.5']['EVN.5.9']['EVN.5.9.1']; }
				if (seg['EVN.5']['EVN.5.9']['EVN.5.9.2'].toString() != "") { objEVN[index].operatorId.assigningAuthority.universalId = seg['EVN.5']['EVN.5.9']['EVN.5.9.2']; }
				if (seg['EVN.5']['EVN.5.9']['EVN.5.9.3'].toString() != "") { objEVN[index].operatorId.assigningAuthority.universalIdType = seg['EVN.5']['EVN.5.9']['EVN.5.9.3']; }
				if (Object.keys(objEVN[index].operatorId.assigningAuthority).length == 0) {
					if (seg['EVN.5']['EVN.5.9'].toString() != "") { objEVN[index].operatorId.assigningAuthority.id = seg['EVN.5']['EVN.5.9']; }
					else delete objEVN[index].operatorId.assigningAuthority;
				}
				if (seg['EVN.5']['EVN.5.10'].toString() != "") { objEVN[index].operatorId.nameTypeCode = seg['EVN.5']['EVN.5.10']; }
				if (seg['EVN.5']['EVN.5.11'].toString() != "") { objEVN[index].operatorId.identifierCheckDigit = seg['EVN.5']['EVN.5.11']; }
				if (seg['EVN.5']['EVN.5.12'].toString() != "") { objEVN[index].operatorId.codeIdentifyingCheckDigitScheme = seg['EVN.5']['EVN.5.12']; }
				if (seg['EVN.5']['EVN.5.13'].toString() != "") { objEVN[index].operatorId.identifierTypeCode = seg['EVN.5']['EVN.5.13']; }
				objEVN[index].operatorId.assigningFacility = {};
				if (seg['EVN.5']['EVN.5.14']['EVN.5.14.1'].toString() != "") { objEVN[index].operatorId.assigningFacility.id = seg['EVN.5']['EVN.5.14']['EVN.5.14.1']; }
				if (seg['EVN.5']['EVN.5.14']['EVN.5.14.2'].toString() != "") { objEVN[index].operatorId.assigningFacility.universalId = seg['EVN.5']['EVN.5.14']['EVN.5.14.2']; }
				if (seg['EVN.5']['EVN.5.14']['EVN.5.14.3'].toString() != "") { objEVN[index].operatorId.assigningFacility.universalIdType = seg['EVN.5']['EVN.5.14']['EVN.5.14.3']; }
				if (Object.keys(objEVN[index].operatorId.assigningFacility).length == 0) {
					if (seg['EVN.5']['EVN.5.14'].toString() != "") { objEVN[index].operatorId.assigningFacility.id = seg['EVN.5']['EVN.5.14']; }
					else delete objEVN[index].operatorId.assigningFacility;
				}
				if (Object.keys(objEVN[index].operatorId).length == 0) {
					delete objEVN[index].operatorId;
				}

				// EVN.6, eventOccurred
				if (seg['EVN.6']['EVN.6.1'].toString() != "") { objEVN[index].eventOccurred = seg['EVN.6']['EVN.6.1']; }

				// clean up EVN and increment index
				if (Object.keys(objEVN[index]).length == 0) {
					delete objEVN[index];
				} else {
					index++;
				}
			}
		}

		return objEVN;

	}
}

/**
	getGT1_v23(msg)

	- Desc: This function retrieves HL7v2 HAPI format elements and values from GT1 segment stored in
	object for reference

	@param {String} msg - this contains the raw message (string)
	@return {object} objGT1 - this contains the GT1 segment elements composed into a single objGT1
		object.
*/
function getGT1_v23(msg) {

	// define variables
	if (typeof(msg['GT1'].toString()) !== undefined) {

		// PV1	
		var objGT1= new Object();

		if (msg['GT1'].length() > 0) {
			objGT1 = [];
			var index = 0;
			for each (seg in msg['GT1']) {
				objGT1[index] = {};

				// GT1.1, setId
				if (seg['GT1.1']['GT1.1.1'].toString() != "") { objGT1[index].setId = seg['GT1.1']['GT1.1.1']; }

				// GT1.2, guarantorNumber
				objGT1[index].guarantorNumber = [];
				for each (field in seg['GT1.2']) {
					var block = {};
					if (field['GT1.2.1'].toString() != "") { block.id = field['GT1.2.1']; }
					if (field['GT1.2.2'].toString() != "") { block.identifierCheckDigit = field['GT1.2.2']; }
					if (field['GT1.2.3'].toString() != "") { block.codeIdentifyingCheckDigitScheme = field['GT1.2.3']; }
					block.assigningAuthority = {};
					if (field['GT1.2.4']['GT1.2.4.1'].toString() != "") { block.assigningAuthority.namespaceId = field['GT1.2.4']['GT1.2.4.1']; }
					if (field['GT1.2.4']['GT1.2.4.2'].toString() != "") { block.assigningAuthority.universalId = field['GT1.2.4']['GT1.2.4.2']; }
					if (field['GT1.2.4']['GT1.2.4.3'].toString() != "") { block.assigningAuthority.universalIdType = field['GT1.2.4']['GT1.2.4.3']; }
					if (Object.keys(block.assigningAuthority).length == 0) {
						if (field['GT1.2.4'].toString() != "") { block.assigningAuthority.namespaceId = field['GT1.2.4']; }
						else delete block.assigningAuthority;
					}
					if (field['GT1.2.5'].toString() != "") { block.idTypeCode = field['GT1.2.5']; }
					block.assigningFacility = {};
					if (field['GT1.2.6']['GT1.2.6.1'].toString() != "") { block.assigningFacility.namespaceId = field['GT1.2.6']['GT1.2.6.1']; }
					if (field['GT1.2.6']['GT1.2.6.2'].toString() != "") { block.assigningFacility.universalId = field['GT1.2.6']['GT1.2.6.2']; }
					if (field['GT1.2.6']['GT1.2.6.3'].toString() != "") { block.assigningFacility.universalIdType = field['GT1.2.6']['GT1.2.6.3']; }
					if (Object.keys(block.assigningFacility).length == 0) {
						if (field['GT1.2.6'].toString() != "") { block.assigningFacility.namespaceId = field['GT1.2.6']; }
						else delete block.assigningFacility;
					}
					if (Object.keys(block).length > 0) {
						objGT1[index].guarantorNumber.push(block);
					}
				}
				if (Object.keys(objGT1[index].guarantorNumber).length == 0) {
					delete objGT1[index].guarantorNumber;
				}

				// GT1.3, guarantorName
				objGT1[index].guarantorName = [];
				for each (field in seg['GT1.3']) {
					var block = {};
					if (field['GT1.3.1'].toString() != "") { block.lastName = field['GT1.3.1']; }
					if (field['GT1.3.2'].toString() != "") { block.firstName = field['GT1.3.2']; }
					if (field['GT1.3.3'].toString() != "") { block.middleInitOrName = field['GT1.3.3']; }
					if (field['GT1.3.4'].toString() != "") { block.suffix = field['GT1.3.4']; }
					if (field['GT1.3.5'].toString() != "") { block.prefix = field['GT1.3.5']; }
					if (field['GT1.3.6'].toString() != "") { block.degree = field['GT1.3.6']; }
					if (field['GT1.3.7'].toString() != "") { block.nameTypeCode = field['GT1.3.7']; }
					if (Object.keys(block).length > 0) {
						objGT1[index].guarantorName.push(block);
					}	
				}
				if (objGT1[index].guarantorName.length == 0) {
					delete objGT1[index].guarantorName;
				}

				// GT1.4, guarantorSpouseName
				objGT1[index].guarantorSpouseName = [];
				for each (field in seg['GT1.4']) {
					var block = {};
					if (field['GT1.4.1'].toString() != "") { block.lastName = field['GT1.4.1']; }
					if (field['GT1.4.2'].toString() != "") { block.firstName = field['GT1.4.2']; }
					if (field['GT1.4.3'].toString() != "") { block.middleInitOrName = field['GT1.4.3']; }
					if (field['GT1.4.4'].toString() != "") { block.suffix = field['GT1.4.4']; }
					if (field['GT1.4.5'].toString() != "") { block.prefix = field['GT1.4.5']; }
					if (field['GT1.4.6'].toString() != "") { block.degree = field['GT1.4.6']; }
					if (field['GT1.4.7'].toString() != "") { block.nameTypeCode = field['GT1.4.7']; }
					if (Object.keys(block).length > 0) {
						objGT1[index].guarantorSpouseName.push(block);
					}	
				}
				if (objGT1[index].guarantorSpouseName.length == 0) {
					delete objGT1[index].guarantorSpouseName;
				}

				// GT1.5, guarantorAddress
				objGT1[index].guarantorAddress = [];
				for each (field in seg['GT1.5']) {
					var block = {};
					if (field['GT1.5.1'].toString() != "") { block.streetAddress = field['GT1.5.1']; }
					if (field['GT1.5.2'].toString() != "") { block.otherDesignation = field['GT1.5.2']; }
					if (field['GT1.5.3'].toString() != "") { block.city = field['GT1.5.3']; }
					if (field['GT1.5.4'].toString() != "") { block.stateOrProvince = field['GT1.5.4']; }
					if (field['GT1.5.5'].toString() != "") { block.zip = field['GT1.5.5']; }
					if (field['GT1.5.6'].toString() != "") { block.country = field['GT1.5.6']; }
					if (field['GT1.5.7'].toString() != "") { block.addressType = field['GT1.5.7']; }
					if (field['GT1.5.8'].toString() != "") { block.otherGeographicDesignation = field['GT1.5.8']; }
					if (field['GT1.5.9'].toString() != "") { block.countyOrParishCode = field['GT1.5.9']; }
					if (field['GT1.5.10'].toString() != "") { block.censusTract = field['GT1.5.10']; }
					if (Object.keys(block).length > 0) {
						objGT1[index].guarantorAddress.push(block);
					}
				}
				if (objGT1[index].guarantorAddress.length == 0) {
					delete objGT1[index].guarantorAddress;
				}

				// GT1.6, guarantorPhNumHome
				objGT1[index].guarantorPhNumHome = [];
				for each (field in seg['GT1.6']) {
					var block = {};
					if (field['GT1.6.1'].toString() != "") { block.number = field['GT1.6.1']; }
					if (field['GT1.6.2'].toString() != "") { block.telecommunicationUseCode = field['GT1.6.2']; }
					if (field['GT1.6.3'].toString() != "") { block.telecommunicationEquipmentType = field['GT1.6.3']; }
					if (field['GT1.6.4'].toString() != "") { block.emailAddress = field['GT1.6.4']; }
					if (field['GT1.6.5'].toString() != "") { block.countryCode = field['GT1.6.5']; }
					if (field['GT1.6.6'].toString() != "") { block.areaCityCode = field['GT1.6.6']; }
					if (field['GT1.6.7'].toString() != "") { block.phoneNumber = field['GT1.6.7']; }
					if (field['GT1.6.8'].toString() != "") { block.extension = field['GT1.6.8']; }
					if (field['GT1.6.9'].toString() != "") { block.text = field['GT1.6.9']; }
					if (Object.keys(block).length > 0) {
						objGT1[index].guarantorPhNumHome.push(block);
					}
				}
				if (objGT1[index].guarantorPhNumHome.length == 0) {
					delete objGT1[index].guarantorPhNumHome;
				}

				// GT1.7, guarantorPhNumBusiness
				objGT1[index].guarantorPhNumBusiness = [];
				for each (field in seg['GT1.7']) {
					var block = {};
					if (field['GT1.7.1'].toString() != "") { block.number = field['GT1.7.1']; }
					if (field['GT1.7.2'].toString() != "") { block.telecommunicationUseCode = field['GT1.7.2']; }
					if (field['GT1.7.3'].toString() != "") { block.telecommunicationEquipmentType = field['GT1.7.3']; }
					if (field['GT1.7.4'].toString() != "") { block.emailAddress = field['GT1.7.4']; }
					if (field['GT1.7.5'].toString() != "") { block.countryCode = field['GT1.7.5']; }
