/**
	fileName:		csv_create_observation_json_object.js
	name:		CSV Create Observation JSON Object
	authored:		Jeffrey Ritz
	created:		2022-10-26
	input:		<array> object to import the observation array (e.g. ['obx11:1','obx21:ST','obx31:INT001', etc.)
	output:		JavaScript object to objectify the observation array
	description:	Productin Evaluate Coded Elements from the CCDA into arrObservations per SQL query to find praMatching elements
	history:
	2022-10-30 JER - Modified to not use switch but to use if condition
*/
function csv_create_observation_json_object(arrObservation) {
	var scriptName= 'csv_create_observation_json_object';
	var debug= 0;
	var objObservation= new Object();

	if(debug>0) { logger.error('DEBUG::'+scriptName+' BEGIN arrObservation object: '+JSON.stringify(arrObservation)); }

	for(var i= 0;i<arrObservation.length;i++) {

		if(i== 0) {
			if(arrObservation[i].toString().length>0) {
				objObservation.obx11= 	arrObservation[i];
			} else {
				objObservation.obx11= 	'';
			} 
		} else if(i== 1) {
			if(arrObservation[i].toString().length>0) {
				objObservation.obx21= 	arrObservation[i];
			} else {
				objObservation.obx21= 	'';
			} 
		} else if(i== 2) {
			if(arrObservation[i].toString().length>0) {
				objObservation.obx31= 	arrObservation[i];
			} else {
				objObservation.obx31= 	'';
			}		
		} else if(i== 3) {
			if(arrObservation[i].toString().length>0) {
				objObservation.obx32= 	arrObservation[i];
			} else {
				objObservation.obx32= 	'';
			} 
		} else if(i== 4) {
			if(arrObservation[i].toString().length>0) {
				objObservation.obx33= 	arrObservation[i];
			} else {
				objObservation.obx33= 	'';
			}
		} else if(i== 5) {
			if(arrObservation[i].toString().length>0) {
				objObservation.obx51= 	arrObservation[i];
			} else {
				objObservation.obx51= 	'';
			} 			
		} else if(i== 6) {
			if(arrObservation[i].toString().length>0) {
				objObservation.obx111= 	arrObservation[i];
			} else {
				objObservation.obx111= 	'';
			}
		} else if(i== 7) {
			if(arrObservation[i].toString().length>0) {
				if(arrObservation[i].toString()== 'NULL') {
					objObservation.obx141= 	get_current_date_time();
				} else {
					objObservation.obx141= 	arrObservation[i];
				} // end if 
			} else {
				objObservation.obx141= 	'';
			}
		} else if(i> 7) {
			if(arrObservation[i].toString().length>0) {
				objObservation.obx161= 	arrObservation[i];
			} else {
				objObservation.obx161= 	'';
			}
		} // end if 

	} // end for each
	if(debug>0) { logger.error('DEBUG::'+scriptName+' END objObservation object: '+JSON.stringify(objObservation)); }

	return objObservation;
} // end function

/** get_current_date_time

  	- Desc: This function returns current DateTime only YYYYMMDDHHMMSS format

	@return {string} datetime - this string object contains the current DateTime only YYYYMMDDHHMMSS format
*/
function get_current_date_time() {
	var today = new Date();
	// set Date with leading zeros if needed
	var tMonth= today.getMonth()+1;
	if(tMonth.toString().length<2) tMonth= '0'+tMonth;
	var tDay= today.getDate()
	if(tDay.toString().length<2) tDay= '0'+tDay;
	var date = today.getFullYear()+''+tMonth+''+tDay;

	// set Time with leading zeros if needed
	var tHour= today.getHours();
	if(tHour.toString().length<2) tHour= '0'+tHour;
	var tMinute= today.getMinutes();
	if(tMinute.toString().length<2) tMinute= '0'+tMinute;
	var tSecond= today.getSeconds();
	if(tSecond.toString().length<2) tSecond= '0'+tSecond;

	var time = tHour+''+tMinute+''+tSecond;
	var dateTime = date+''+time;

	return dateTime;
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function calcSizeBase64EncodedMsg(size) {

	var code_size=    ((size*4)/3)
	var padding_size= (size % 3) ? (3- (size % 3)) : 0;
	var crlfs_size=   2 + (2 * (code_size + padding_size) / 72);
	var total_size=   code_size + padding_size + crlfs_size;

	return total_size;
}

/**
	convertDate(date, outpattern)
*/
// modify function_name and parameters as you wish.
// one function per template is recommended. i.e.) create a new code template for each new function.
var patterns = [
	'yyyy-MM-dd HH:mm:ss:SSS',
	'yyyy-MM-dd HH:mm:ss.SSS',
	'yyyy-MM-dd HH:mm:ss',
	'yyyy-MM-dd HH:mm',
	'EEE MMM dd HH:mm:ss:SSS zzz yyyy',
	'EEE MMM dd HH:mm:ss.SSS zzz yyyy',
	'EEE MMM dd HH:mm:ss zzz yyyy',
	'EEE MMM dd zzz yyyy',
	'dd-MMM-yyyy HH:mm:ss:SSS',
	'dd-MMM-yyyy HH:mm:ss.SSS',
	'dd-MMM-yyyy HH:mm:ss',
	'MM/dd/yyyy',	
	'yyyy-MM-dd',
	'yyyy MM dd',
	'yyyy.MM.dd',
	'MM-dd-yyyy',
	'MM dd yyyy',
	'MM.dd.yyyy',
	'HH:mm:ss:SSS',
	'HH:mm:ss.SSS',
	'HH:mm:ss',
	'yyyyMMddHHmmssSSS',
	'yyyyMMddHHmmss',
	'yyyyMMddHHmm',
	'hh:mm aa'];

var formatters = [];
for each (pattern in patterns) {
	formatters.push(org.joda.time.format.DateTimeFormat.forPattern(pattern));
}

function convertDate(date, outpattern) {
	var instant = 0;
	if (typeof date == 'number' || date instanceof java.lang.Number)
		instant = new Number(date);
	else if (date instanceof Date || date instanceof java.util.Date)
		instant = date.getTime();
	else if (date instanceof org.joda.time.ReadableInstant)
		instant = date.getMillis();
	else {
		for each (formatter in formatters) {
			try {
				instant = formatter.parseMillis(new String(date));
				break;
			} catch(e) {}
		}
	}

	return org.joda.time.format.DateTimeFormat.forPattern(outpattern).print(instant);
}

/** currentDate_yyyyMMddhhmmss

	@return {String} return currentDate in yyyyMMddhhmmss string format

	// Modified: To include addition checks on the length of each datepart 
*/
function currentDate_yyyyMMddhhmmss() {

	var scriptName= 'currentDate_yyyyMMddhhmmss';
	var debug= 0;

	var today = new Date();
	var day = today.getDate();
	var month = today.getMonth()+1; //January is 0!
	var year = today.getFullYear();
	var hour = today.getHours();	// 0-23
	var minute = today.getMinutes();	// 0-59
	var second = today.getSeconds();  // 0-59

	if(day.toString().length<2) { day = '0'+day } 
	if(month.toString().length<2) { month = '0'+month }
	if(hour.toString().length<2) { hour = '0'+hour }
	if(minute.toString().length<2) { minute = '0'+minute }
	if(second.toString().length<2) { second = '0'+second }
	if(debug>0) { 
		logger.error('DEBUG::'+scriptName+' currentDate day: '+day+' month:' +month+' year: '+year+' hour: '+hour+' minutes: '+minute+' seconds: '+second); 
	}
	var newDate= year+''+month+''+day+''+hour+''+minute+''+second;
	if(debug>0) { logger.error("currentDate_yyyyMMddhhmmss() returns: "+newDate); }
	return newDate;

}

/**
	Desc: Perform sFTP Put Javascript to connect over SFTP and Put a file on the remote disk
	Name:  Perform sFTP Put Action (ip, port, user, pass, filename, msg)
	@param {String} ip - provide the IP address
		  {String} port - provide the port number
		  {String} user - provide the username
		  {String} pass - provide the password
		  {String} filename - provide the filename
		  {String} msg - XML presentation of the message to be sent as a file
	@return {String} return response (Success or Failure)
*/
function sftpPut(ip, port, user, pass, filename, msg){
	importPackage(com.jcraft.jsch);
	importPackage(java.io);
	importPackage(java.lang);
	importPackage(java.nio.charset);

	function toByteArray(str){
		var bytes = [];
		for (var i = 0; i < str.length; ++i) {
    		bytes.push(str.charCodeAt(i));
		}
		return bytes;
	}

	// testing vars
	var debug= 0;

	//create object.
	var jsch = new JSch();
	jsch.setConfig('StrictHostKeyChecking','no');
	var session = jsch.getSession(user,ip,port);
	session.setPassword(pass);
	session.setTimeout(10000);
	session.connect();
	var channel = session.openChannel('sftp');
	var ioexception = new Packages.java.io.IOException;
	channel.connect();
	if(debug>0) {logger.info('Connected to SFTP: ');}
	if(debug>0) {logger.info('Home Directory: ' + channel.getHome());}
	channel.cd('/'); //Folder on the SFTP server where file will be found

	var filename = filename;
	var filebody = msg;
	channel.put(filebody);

	channel.disconnect();
	session.disconnect();	
	return;
}

/**
	Desc: This script is used to evaluate a string if contains a number

	scriptName: contains_number
	@param {String} arg1 - arg1 description
	@return {boolean} return true or false depending if isNaN() - if isNan() -> return true otherwise -> return false
	e.g., write_msg_to_disk($('rawhl7'),api_error_directory,$('ark_uuid'));
	History:
*/
function contains_number(text) {

	var scriptName= 'contains_number';
	var debug= 0;

	if(debug>0) { logger.error('DEBUG::'+scriptName+' verify text: '+text); }

	if((typeof(text) !== 'undefined') && (text.toString().length>0))  {

		if((text.toString()== 'DETECTED')||(text.toString()== 'TRUE')) {
			return false;
		} else if(text.toString()== 'NOT DETECTED') {
			return false;
		} else if(text.toString()== 'INVALID') {
			return false;
		} else {
			var arrText= text.toString().split('');
			if(debug>0) { logger.error('DEBUG::'+scriptName+' arrText: '+JSON.stringify(arrText)); }
			// curObservationValue.toString() !== 'NaN')
			for each(strElement in arrText) {
				// if(debug>0) { logger.error('DEBUG::'+scriptName+' element: '+element+' isNaN(): '+element.toString().isNaN()); }
				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' typeof element: '+typeof(strElement));
					logger.error('DEBUG::'+scriptName+' toString: '+strElement.toString());
					logger.error('DEBUG::'+scriptName+' element toString() !== isNaN(): '+(strElement.toString()!== 'NaN')); 
				}
				if(strElement.toString() !== 'NaN') {
					return true;
				} // end if
			} // end for
		} // end if 
		return false;
	} // end if
}

/**
	Converts standard HL7 v2.x date format to human-readable ISO date format
*/

/*
	Author: Datica Inc.
	toISODate: Returns an ISO-formatted date string with specified timezone offset

	Arguments
	---------
		Required
		--------
			hl7DateString:		An HL7-formatted date string (ex: "yyyyMMddHHmmss"

		Optional
		--------
			timezome:			A timezone identifier string corresponding to one of the java.util.TimeZone values (ex: "US/Central")
*/

function toISODate( hl7DateString, timezone ) {
	var isoDate;
	var dateString;
	var hl7Formatter;
	var isoFormatter = new Packages.java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ssZ");

	if (timezone) {
		isoFormatter.setTimeZone(Packages.java.util.TimeZone.getTimeZone(timezone));
	}

	if ( hl7DateString != null && !isNaN(hl7DateString.substring(0,13)) && hl7DateString.length > 14 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmmssZ");
	}
	else if ( hl7DateString != null && !isNaN(hl7DateString) && hl7DateString > 0 && hl7DateString.length == 14 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmmss");
	} 
	else if ( hl7DateString != null && !isNaN(hl7DateString) && hl7DateString > 0 && hl7DateString.length == 12 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmm");
	} 
	else if ( hl7DateString != null && !isNaN(hl7DateString) && hl7DateString > 0 && hl7DateString.length == 8 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMdd");
	} 
	else dateString = hl7DateString;

	try {
		if (hl7Formatter) {
			isoDate = hl7Formatter.parse(hl7DateString);
			isoDate = isoFormatter.format(isoDate);
			dateString = new String(isoDate);
		}
	} catch (err) {
		dateString = hl7DateString;
	} finally {
		return dateString;
	}
}

/**
	Convert HL7 Ethnic Code to Ethnicity Coded ELement (CWS) data type

	@scriptName: hl7_modify_ethnic_code_pid22_to_ethnic_coded_element
	@descName:   Convert HL7 Ethnic Code to Ethnicity Coded ELement Data Type
	@authorName: Jeffrey Ritz
	Parameters:
	@param {XML} pid22 - contains the XML HL7 representation of the PID-22 element
	@return {XML} newEthnicity - returns the modified XML HL7 representation of the PID-22 element
*/
function hl7_modify_ethnic_code_pid22_to_ethnic_coded_element(pid22) {

	var scriptName= 'hl7_modify_ethnic_code_pid22_to_ethnic_coded_element'
	var debug= 0;

	// 2023-03-07 JER - Added code per Streamline Issues List - 20220306 item# 3
	// https://phinvads.cdc.gov/vads/ViewValueSet.action?id=2088950C-6EF8-E111-B875-001A4BE7FA90
	var newEthnicity= new XML('<PID.22></PID.22>');

	if(typeof(pid22)!== 'undefined') {

		for each(element in pid22) {

			if(element['PID.22.1'].toString().length>0) {
				var pid221= element['PID.22.1'].toString();
				switch (pid221) {
					case 'H': 
						var strPid22= pid221+'^Hispanic or Latino^HL70189';
						break;
					case 'N': 
						var strPid22= pid221+'^Not Hispanic or Latino^HL70189';
						break;
					case 'U':
						var strPid22= pid221+'^Unknown^HL70189';
						break;
					default: 
						var strPid22= 'U^Unknown^HL70189';
				} // end switch
			} else {
				var strPid22= '';
			} // end if 
			newEthnicity.appendChild(strPid22);
		} // end for each

	} // end if 

	return newEthnicity;

} // end function

