/**
	Converts ISO date format to HL7 v2 format
*/

/*
	Author: Datica Inc.
	toHL7Date: Returns an HL7-formatted date string with specified timezone offset

	Arguments
	---------
		Required
		--------
			isoDateString:		An ISO-formatted date string (ex: "yyyy-MM-ddTHH:mm:ssZ"

		Optional
		--------
			timezome:			A timezone identifier string corresponding to one of the java.util.TimeZone values (ex: "US/Central")
*/

function toHL7Date( isoDateString, timezone ) 
{
	var hl7Date;
	var dateString;
	var isoFormatter;
	var hl7Formatter;

	// ISO Date Format: yyyy-MM-dd'T'HH:mm:ss'Z'
	if ( isoDateString != null && isoDateString.length <= 25 && isoDateString.length >= 22) {
		isoFormatter = new Packages.java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ssZ");
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmmssZ");
		if (timezone) {
			hl7Formatter.setTimeZone(Packages.java.util.TimeZone.getTimeZone(timezone));
		}
	}
	// ISO Date Format: yyyy-MM-dd HH:mm:ss
	else if ( isoDateString != null && isoDateString.length == 19 ) {
		isoFormatter = new Packages.java.text.SimpleDateFormat("yyyy-MM-dd HH:mm:ss");
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmmss");
	}
	// ISO Date Format: yyyy-MM-dd HH:mm
	else if ( isoDateString != null && isoDateString.length == 16 ) {
		isoFormatter = new Packages.java.text.SimpleDateFormat("yyyy-MM-dd HH:mm");
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmm");	
	} 
	// ISO Date Format: yyyy-MM-dd
	else if ( isoDateString != null && isoDateString.length == 10 ) {
		isoFormatter = new Packages.java.text.SimpleDateFormat("yyyy-MM-dd");
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMdd");	
	}
	// None of the above, return original date string.
	else dateString = isoDateString;

	try {
		if (isoFormatter) {
			hl7Date = isoFormatter.parse(isoDateString);
			hl7Date = hl7Formatter.format(hl7Date);
			dateString = new String(hl7Date);
		}
	} catch (err) {
		dateString = isoDateString;
	} finally {
		return dateString;
	}
}

/**
	Evaluate the OBX-5.1 values for Detected vs Non-Detected result values to determine if needed to filter the observation results

	fileName:		filter_non_detected_observations_results.js
	name:		Filter Non Detected Observation Results
	authored:		Jeffrey Ritz
	created:		2023-02-22
	desc:		Evaluate the OBX-5.1 values for Detected vs Non-Detected result values
	specimenDetected= true  -> desired specimens detected used to permit the entire message to be sent
	specimenDetected= false -> desired specimens detected used to filter the entire message
	history:
	2024-02-24 JER - Clone of the remove_observations_from_observation_result_codes.js function
*/
function filter_non_detected_observations_results(msg) {

	var scriptName= 'filter_non_detected_observations_results';
	var debug= 1;
	var detected= false;
	var i= 1;	// used for the OBX children objects
	var j= 1;	// used for all msg children objects
	var channelName = Packages.com.mirth.connect.server.controllers.ChannelController.getInstance().getDeployedChannelById(channelId).getName();
	channelMap.put('channelName',channelName);

	// for each segment - child per msg.children()
	for each(child in msg.children()) {

		if(debug>0) logger.error('DEBUG::'+scriptName+' typeof: '+typeof(child)+' localName: '+child.localName()+' child: '+child);

		if(child.localName()== 'OBX') {

			// process each OBX
			var curObservationValue=child['OBX.5']['OBX.5.1'].toString().toUpperCase();

			// evaluate if element value is numerical exponential
			if(curObservationValue.toString().indexOf('1.00E')>-1) {
				if(debug>0) { logger.error('DEBUG::'+scriptName+' determined value contains an exponential 1.00E value: '+(curObservationValue.toString().indexOf('1.00E')>-1)); }
			}

			// evaluate if element value is number 
			if(parseInt(curObservationValue.toString() !== 'NaN')) {
				if(debug>0) { logger.error('DEBUG::'+scriptName+' determined value contains IS a number: '+parseInt(curObservationValue.toString()!== 'NaN')); }
			}

			if(debug>0) logger.error('DEBUG::'+scriptName+' ['+i+'] curObservationValue: '+curObservationValue);

			// 2022-10-17 JER - Added evaluate if NaN - only 
			if((curObservationValue== 'DETECTED')||(curObservationValue== 'TRUE')||(curObservationValue.toString().indexOf('1.00E')>-1)||parseInt(curObservationValue.toString()!== 'NaN')) {
				detected= true;
			} // end if 

			i++;
		}  // end if 
		j++;
	} // end for

	channelMap.put('specimenDetected',detected); // true, false, invalid

	if(detected== true) {
		return true;
	} else {
		return false;
	}
}

/**
	Returns an array of segments with the specified name that come after a given segment in the message.
*/
/*
	Author: Nick Rupley
	Date Modified: 2/13/2013
	getSegmentsAfter: Returns an array of segments with the specified name that come after a given segment in the message.

	Arguments
	---------
		Required
		--------
			root:			The root HL7Message node of the message, or the parent of the segment node.
			startSeg:		The segment AFTER which to start collecting segments.
			segName:		The name (String or RegExp) of the segments you want to collect.

		Optional
		--------
			consecutiveInd:	If true, indicates that the segments are expected to come directly after startSeg. 
							If false, segments are collected until another segment with the same name as startSeg is encountered.
							Defaults to false.
			stopSegNames:	An array of segment names that, when encountered, stop the collection of segments.
*/

function getSegmentsAfter(root, startSeg, segName, consecutiveInd, stopSegNames) {
	function test(str) {
		return segName instanceof RegExp ? segName.test(str) : segName === str;
	}

	// The index to start collection is the next one up from the starting segment
	var index = startSeg.childIndex()+1;
	// The return array
	var out = [];
	// Boolean placeholder to stop iteration
	var done = false;
	// Object that will contain all of the stopSegNames strings, bound to a truthy value (1)
	var stopNames = {};
	// Indicates whether we have any stop segments
	var stopNamesInd = false;
	// If stopSegNames is defined
	if (stopSegNames !== undefined && stopSegNames !== null) {
		// Set our indicator to true
		stopNamesInd = true;
		// Add each string in the array to our object
		for each (name in stopSegNames)
			stopNames[name] = 1;
	}

	// Iterate through each child in the root, starting at the segment after startSeg, and
	// ending at the final segment, or when the done flag is set to true.
	while (index < root.children().length() && !done) {
		// If a stop segment is encountered, stop iteration
		if (stopNamesInd && root.children()[index].name().toString() in stopNames)
			done = true;
		// If a segment with the same name as startSeg is encountered, stop iteration
		else if (root.children()[index].name().toString() == startSeg.name().toString() && !consecutiveInd)
			done = true;
		// If we're only collecting consecutive segments and we encounter a segment with a name other than segName, stop iteration
		else if (!test(root.children()[index].name().toString()) && consecutiveInd)
			done = true;
		// If all previous tests passed, and the current segment has a name of segName, then add it to our array
		else if (test(root.children()[index].name().toString()))
			out.push(root.children()[index]);
		// Increment our index counter
		index++;
	}

	// Return the output array
	return out;
}

/**
	Desc: This script is used to evaluate the provided messageObservation Value (OBX-5) to determine if the value should 
	be designated as DETECTED or NON-DETECTED

	@param {String} messageObservationValue - this only argument is the OBX-5 value provided to be evaluated by this script
	@return {String} return - to return the detected boolean value of detected= true or non-detected= false (true|false) 
	history
	2023-09-27 JER - Modified to include Conversion from Exponential value (1.00E10 or 9.44E1) to Number with Precision
*/
function isMessageObservationValueDetected(messageObservationValue) {

	var scriptName= 'isMessageObservationValueDetected';
	var debug= 0;
	var blnDetected= false;

	if(debug>0) logger.debug('DEBUG::'+scriptName+' evaluate messageObservationValue: '+messageObservationValue);

	if((messageObservationValue.indexOf('.')>-1) && (messageObservationValue.indexOf('E')>-1)) {
		messageObservationValue= Number(messageObservationValue).toPrecision();
		if(debug>0) logger.debug('DEBUG::'+scriptName+' messageObservationValue CONTAINS an exponential value add precision: '+messageObservationValue.toString()+' type: '+typeof(messageObservationValue));
	} // end if 

	// 2023-09-27 JER - Conversion from Exponential value (1.00E10 or 9.44E1) to Number with Precision 
	if(isNaN(messageObservationValue) == false) {
		blnDetected= true;
		if(debug>0) logger.debug('DEBUG::'+scriptName+' messageObservationValue isNAN: '+isNaN(messageObservationValue)+' value: '+messageObservationValue.toString().toUpperCase()+' blnDetected: '+blnDetected);
	// evaluate if element value is numerical exponential
	} else if(messageObservationValue.toString().indexOf('1.00E')>-1) {
		// IS AN EXPONENTIAL NUMERIC EXPRESSION (1.00E10, ETC.)
		blnDetected= true;
		if(debug>0) logger.debug('DEBUG::'+scriptName+' messageObservationValue CONTAINS an exponential 1.00E value: '+messageObservationValue.toString()+' blnDetected: '+blnDetected);
	} else if(messageObservationValue.toString().toUpperCase().indexOf('INVALID')>-1) {				
		// INVALID
		blnDetected= false;
		if(debug>0) logger.debug('DEBUG::'+scriptName+' messageObservationValue CONTAINS an INVALID text value: '+messageObservationValue.toString().toUpperCase()+' blnDetected: '+blnDetected);
	} else if(messageObservationValue.toString().toUpperCase().indexOf('NOT DETECTED')>-1) {
		// NON-DETECTED
		blnDetected= false;
		if(debug>0) logger.debug('DEBUG::'+scriptName+' messageObservationValue CONTAINS NOT DETECTED text value: '+messageObservationValue.toString().toUpperCase()+' blnDetected: '+blnDetected);
	} else if (parseInt(messageObservationValue.toString() !== 'NaN')) {
		// IS A NUMBER
		blnDetected= true;
		if(debug>0) logger.debug('DEBUG::'+scriptName+' messageObservationValue CONTAINS a number value: '+messageObservationValue.toString()+' blnDetected: '+blnDetected);
	} else if(messageObservationValue.toString().toUpperCase()== 'DETECTED') {
		// DETECTED
		blnDetected= true;
		if(debug>0) logger.debug('DEBUG::'+scriptName+' messageObservationValue CONTAINS a number value: '+messageObservationValue.toString()+' blnDetected: '+blnDetected);
	} // end if 

	if(blnDetected== true) {
		return 'DETECTED';
	} else {
		return 'NOT DETECTED';
	} // end if 

} // end function

/**
Desc:  Evaluate the OBX versus the observed result code (OBX-3.1) evaluated against Configuration Setting variable: ADPHNotifiablePathogens to determine whether to exclude the Observation Results (OBX) segments from the message and set the pathogenDetected channelMap variable.

	fileName:		remove_duplicate_segments.js
	name:		Remove Duplicate Segments
	@authored:	Jeffrey Ritz
	@created:		2023-05-29
	@description:	Remove duplicate segments

	history:
	2023-02-27 JER - Cloned the Remove Observations From Observation Result Codes
*/
function remove_duplicate_segments(msg, removeDuplicateSegmentName) {

	var scriptName= 'remove_duplicate_segments';
	var debug= 0;
	var arrRemoveDuplicateSegments= new Array();

	// 2023-04-26 JER - Modified to determine if should evaluate detectable or not

	// var tmp= new XML('<HL7Message></HL7Message>');
	var channelName = Packages.com.mirth.connect.server.controllers.ChannelController.getInstance().getDeployedChannelById(channelId).getName();
	channelMap.put('channelName',channelName);

	var lastSegmentName= '';
	var lastSegmentIndex= 0;
	var lastSegment= '';
	var arrDeleteSegments= new Array();

	var i= 0;
	for each(seg in msg.children()) {

		var segmentName= seg.name();
		if(segmentName== removeDuplicateSegmentName) {

			var getSegmentIndex= seg.childIndex();

			if(debug>0) logger.error('DEBUG::'+scriptName+' segment['+i+'] getSegmentIndex: '+getSegmentIndex+' segment: '+seg);

			if(segmentName== lastSegmentName) {

				var strRemoveDuplicateSegments= segmentName+'['+lastSegmentIndex+']: '+seg;
				arrRemoveDuplicateSegments.push(strRemoveDuplicateSegments);
				if(debug>0) logger.error('DEBUG::'+scriptName+' found seg['+i+'] segmentName: '+segmentName+' lastSegmentName: '+lastSegmentName+' IF segmentName== lastSegmentName: '+(segmentName== lastSegmentName)+' DELETE last segment: '+lastSegment);
				delete msg.children()[lastSegmentIndex]; 
			} 

			lastSegment= seg;
			lastSegmentIndex= getSegmentIndex;
			lastSegmentName= segmentName;
		} // end if 

	} // end for each 

	if(arrRemoveDuplicateSegments.length>0) channelMap.put('removeDuplicateSegments',arrRemoveDuplicateSegments);
	return msg;

}

/**
	Desc: Remove non-desired HL7 Segments from the HL7 message 
	
	@fileName:	remove_non_desired_hl7_segments.js
	@name:		Remove Non Desired HL7 Segments
	@authored:	Jeffrey Ritz
	@created:		2023-03-01
	@description:	Remove non-desired HL7 Segments from Message
	@return:		XML Object - returns the HL7 XML representation of the HL7 message excluding the non-desired segments

	history:
	2023-03-02 JER - remove the carriage return line feeds from the segmentsFiltered channelMap variable
	2023-02-27 JER - Cloned the Remove Observations From Observation Result Codes
*/
function remove_non_desired_hl7_segments(msg, removeSegmentsList) {

	var scriptName= 'remove_non_desired_hl7_segments';
	var debug= 0;
	var detected= false;
	var i= 0;
	var segmentsFiltered = new Array();
	var tmp= new XML('<HL7Message></HL7Message>');
	var channelName = Packages.com.mirth.connect.server.controllers.ChannelController.getInstance().getDeployedChannelById(channelId).getName();
	channelMap.put('channelName',channelName);

	// for each segment - child per msg.children()
	for each(child in msg.children()) {

		if(debug>0) logger.error('DEBUG::'+scriptName+' typeof: '+typeof(child)+' localName: '+child.localName()+' child: '+child);

		if(child.localName()== 'MSH') {
			var msgCtrlId= child['MSH.10']['MSH.10.1'].toString();
		}

		if(removeSegmentsList.indexOf(child.localName())<0) {

			if(debug>0) logger.error('DEBUG::'+scriptName+' ['+i+'] determined segment: '+child.localName()+' KEEP IT as is NOT CONTAINED in the removeSegments List of: '+removeSegmentsList); 
			tmp.appendChild(child);
			if(debug>0) logger.error('DEBUG::'+scriptName+' ['+i+'] ADD SEGMENT: '+child.localName());
		} else {
			// 2023-03-01 JER - Added the collective observationFiltered channelMap variable to capture the array of filtered observations
			var segmentFiltered= 'Filtered segment['+i+']: '+child.localName()+'\r\n';
			segmentsFiltered.push(segmentFiltered);
			if(debug>0) logger.error('DEBUG::'+scriptName+' determined segment['+i+']: '+child.localName()+' REMOVE IT as it IS CONTAINED in the removeSegments List of: '+removeSegmentsList); 
		} // end if 	
		i++;

	} // end for

	// 2023-03-02 JER - remove the carriage return line feeds from the segmentsFiltered channelMap variable
	var strSegmentsFiltered= segmentsFiltered;
	var segmentsFiltered= segmentsFiltered.toString().replace(/[\r][\n]/g,'');
	channelMap.put('segmentsFiltered',segmentsFiltered);
	channelMap.put('remove_segments_msg',tmp);

	if(debug>0) logger.error('DEBUG::'+scriptName+' msg: '+msgCtrlId+' non-desired REMOVED segments: '+'\r\n'+strSegmentsFiltered);

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' remove_segments_msg: '+$c('remove_segments_msg'));
		logger.error('DEBUG::'+scriptName+' tmp: '+$c('remove_segments_msg'));
	} // end if 

	return tmp;
}

/**
	Desc:  Evaluate the OBX versus the observation identifier alternative identifier code (OBX-3.4) evaluated against Configuration Setting variable: TNRemoveObservations
	to determine whether to exclude the Observation Results (OBX) segments from the message and set the pathogenDetected channelMap variable.

	fileName:		remove_observations_from_observation_alternative_code.js
	name:		Remove Observations From Observation Alternative Codes
	authored:		Jeffrey Ritz
	created:		2023-04-18
	description:	Evaluate the OBX versus the observed result code (OBX-3.4) evaluated against Configuration Setting variable: TNRemoveObservations
	to determine whether to exclude the Observation Results (OBX) segments from the message. 
	history:
	2023-04-18 JER - Cloned the pre-existing remove_observations_from_observation_result_codes 
*/
function remove_observations_from_observation_alternative_code(msg, removeObservations) {

	var scriptName= 'remove_observations_from_observation_alternative_code';
	var debug= 0;
	// allowedObservations
	var allowedObservationsCount= 0;
	var allowedObservations= new Array();
	// removedObservations
	var removedObservationsCount= 0;
	var removedObservations= new Array();

	var i= 1;
	var j= 1;

	var tmp= new XML('<HL7Message></HL7Message>');
	var channelName = Packages.com.mirth.connect.server.controllers.ChannelController.getInstance().getDeployedChannelById(channelId).getName();
	channelMap.put('channelName',channelName);

	// for each segment - child per msg.children()
	for each(child in msg.children()) {

		if(debug>0) logger.error('DEBUG::'+scriptName+' typeof: '+typeof(child)+' localName: '+child.localName()+' child: '+child);

		if(child.localName()== 'MSH') {
			var msgCtrlId= child['MSH.10']['MSH.10.1'].toString();
		}

		if(child.localName()== 'OBX') {

			// process each OBX
			// 2023-03-02 JER - Modification to evaluate differences between CWE (Coded Extended) vs ST (String) data type 
			var obx21= child['OBX.2']['OBX.2.1'].toString();
			// if(child['OBX.2']['OBX.2.1'].toString()== 'CWE') {
			var obx31= child['OBX.3']['OBX.3.1'].toString();
			var obx32= child['OBX.3']['OBX.3.2'].toString();
			var obx34= child['OBX.3']['OBX.3.4'].toString();
			var obx51= child['OBX.5']['OBX.5.2'].toString().toUpperCase();
			// } // end if 
			var curObservationValue= obx51;

			if(debug>0) logger.error('DEBUG::'+scriptName+' obx21: '+obx21+' obx34: '+obx34+' obx51: '+obx51+' observedValue: '+curObservationValue);

			if(debug>0) {
				logger.error('DEBUG::'+scriptName+' OBX['+i+'] obx34 typeof: '+typeof(obx34)+' obx34: '+obx34);
				logger.error('DEBUG::'+scriptName+' OBX['+i+'] TNRemoveObservations: '+removeObservations);
				logger.error('DEBUG::'+scriptName+' OBX['+i+'] removeObservations.indexOf(obx34): '+removeObservations.indexOf(obx34));
			}

			if(removeObservations.indexOf(obx34)<0) {		

				// 2024-02-24 JER - Modified to leverage the function input parameterized variable orderCodeList to replace hardCoded configuration setting variable
				if(debug>0) logger.error('DEBUG::'+scriptName+' ['+i+'] FOUND TN Remove Observation OBX-3.1: '+obx31+' value: '+curObservationValue+' from the list of codes: '+removeObservations+' evaluation: '+removeObservations.indexOf(obx34)+' to ADD this observation OBX Segment.'); 

				tmp.appendChild(child);
				allowedObservationsCount++;
				var strAllowedObservations= 'Allowed Observations['+allowedObservationsCount+'] Observation CODE: '+obx31+' DESCRIPTION: '+obx32;
				allowedObservations.push(obx31);

				if(debug>0) logger.error('DEBUG::'+scriptName+' removeObservations['+i+'] allowObservations:' +strAllowedObservations);

			} else {

				// 2023-03-01 JER - Added the collective observationFiltered channelMap variable to capture the array of filtered observations
				removedObservationsCount++;
				var strRemovedObservations= 'Filtered Observation['+removedObservationsCount+'] Observation CODE: '+obx31+' DESCRIPTION: '+obx32+'\r\n';
				removedObservations.push(obx31);

				if(debug>0) logger.error('DEBUG::'+scriptName+' removeObservations['+i+'] removedObservations:' +strRemovedObservations);

			}
			i++;
		} else {
			// 2023-02-24 JER - Modified to use appendChild E4X denoted XML function
			tmp.appendChild(child);
			if(debug>0) logger.error('DEBUG::'+scriptName+' ['+i+'] ADD SEGMENT: '+child.localName());
		} // end for

		if(debug>0) logger.error('DEBUG::'+scriptName+' tmp['+j+'] contains: '+tmp);
		j++;

	} // end for

	// 2023-03-02 JER - remove the carriage return line feeds from the observationsFiltered channelMap variable
	// var observationsFiltered= observationsFiltered.toString().replace(/[\r][\n]/g,'');
	channelMap.put('allowedObservations',allowedObservations);
	channelMap.put('removedObservations',removedObservations);

	if(debug>0) logger.error('DEBUG::'+scriptName+' msg: '+msgCtrlId+' allowed observations: '+$c('allowedObservations')+' and removed observations: '+$c('removedObservations'));

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' tmp: '+tmp);
	} // end if 

	return tmp;
}

/**
	Desc:  Evaluate the OBX versus the observation identifier alternative identifier code (OBX-3.4) evaluated against Configuration Setting variable: TNRemoveObservations
	to determine whether to exclude the Observation Results (OBX) segments from the message and set the pathogenDetected channelMap variable.

	fileName:		remove_observations_from_observation_alternative_code_new.js
	name:		Remove Observations From Observation Alternative Codes (NEW)
	authored:		Jeffrey Ritz
	created:		2023-04-18
	description:	Evaluate the OBX versus the observed result code (OBX-3.4) evaluated against Configuration Setting variable: TNRemoveObservations
	to determine whether to exclude the Observation Results (OBX) segments from the message. 
	history:
	2023-05-08 JER - Modified to include a removeNonDetected variable if provided with a 1 - then remove nonDetected observations
	2023-05-08 JER - Modified to evaluate observation types (CWE) differently than other coded values
	2023-04-18 JER - Cloned the pre-existing remove_observations_from_observation_result_codes 
*/
function remove_observations_from_observation_alternative_code_new(msg, removeObservations, removeNonDetected) {

	var scriptName= 'remove_observations_from_observation_alternative_code_new';
	var debug= 0;
	var detected= false;
	var pathogenDetected= false;

	// allowedObservations
	var allowedObservationsCount= 0;
	var allowedObservations= new Array();

	// removedObservations
	var removedObservationsCount= 0;
	var removedObservations= new Array();
	if(typeof(removeNonDetected)== undefined) var removeNonDetected= 0;

	var i= 1;
	var j= 1;

	if(debug>0) logger.error('DEBUG::'+channelName+':'+scriptName+' removeObservations: '+removeObservations+' removeNonDetected: '+removeNonDetected); 

	// var tmp= new XML('<HL7Message></HL7Message>');
	var channelName = Packages.com.mirth.connect.server.controllers.ChannelController.getInstance().getDeployedChannelById(channelId).getName();
	channelMap.put('channelName',channelName);


	for each(seg in msg..OBR) {

		var perSegAllowedObservationSegments= 0;

		if(debug>0) logger.error('DEBUG::'+scriptName+' typeof: '+typeof(seg)+' seg['+j+']: '+seg);

		var allowedObservationSegments= 0;
		for each(obx in getSegmentsAfter(msg, msg.children()[seg.childIndex()],'OBX',false,'OBR')) {

			// process each OBX
			var obx21= obx['OBX.2']['OBX.2.1'].toString;
			var obx31= obx['OBX.3']['OBX.3.1'].toString();
			var obx32= obx['OBX.3']['OBX.3.2'].toString();
			var obx34= obx['OBX.3']['OBX.3.4'].toString();
			var obx51= obx['OBX.5']['OBX.5.1'].toString();

			// 2023-02-27 JER - Modified from OBX.5.1 to OBX.5.2
			if(obx['OBX.2']['OBX.2.1'].toString()== 'CWE') {
				var observationCode= obx34;
				var curObservationValue=	obx['OBX.5']['OBX.5.2'].toString().toUpperCase();
			} else {
				var observationCode= obx31;
				var curObservationValue=	obx51.toString().toUpperCase();
			} // end if

			if(debug>0) logger.error('DEBUG::'+scriptName+' OBX['+j+'] observationCode: '+observationCode+' obx31: '+obx31+' obx32: '+obx32+' obx51: '+obx51+' curObservationValue: '+curObservationValue);

			if(debug>0) { 
				logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] curObservationValue: '+curObservationValue);
				logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] observationCode: '+observationCode+' removeObservations: '+removeObservations+' EVAL: '+removeObservations.indexOf(observationCode));
			} // end if 

			// 2024-02-24 JER - Modified to leverage the function input parameterized variable orderCodeList to replace hardCoded configuration setting variable
			if(removeObservations.indexOf(observationCode)>-1) {	

				// 2024-02-24 JER - Modified to leverage the function input parameterized variable orderCodeList to replace hardCoded configuration setting variable
				if(debug>0) logger.error('DEBUG::'+scriptName+' ['+i+'] FOUND Observation Code: '+observationCode+' value: '+curObservationValue+' from the list of codes: '+removeObservations+' evaluation: '+removeObservations.indexOf(observationCode)+' to REMOVE this observation OBX Segment.'); 

				removedObservationsCount++;
				var strObservationCode= observationCode+':'+curObservationValue;
				var strRemovedObservations= 'Filtered Observation['+removedObservationsCount+'] Observation CODE: '+observationCode+' DESCRIPTION: '+obx32+' obx34: '+obx34+' curObservationValue: '+curObservationValue+'\t\r\n';
				removedObservations.push(strObservationCode);

				delete msg.children()[obx.childIndex()];

				if(debug>0) logger.error('DEBUG::'+scriptName+' REMOVE removable Non-Detected observation CODE: '+observationCode+' removeObservations['+removedObservationsCount+'] removedObservations:' +strRemovedObservations);

			} else {

				pathogenDetected= true;

				perSegAllowedObservationSegments++;
				allowedObservationsCount++;
				var strObservationCode= observationCode+':'+curObservationValue;
				var strAllowedObservations= 'Allowed Observations['+allowedObservationsCount+'] Observation CODE: '+observationCode+' DESCRIPTION: '+obx32+ 'obx34: '+obx34;
				allowedObservations.push(strObservationCode);

				if(debug>0) logger.error('DEBUG::'+scriptName+' PERMITTED removable Detected observation CODE: '+observationCode+' allowedObservations['+allowedObservationsCount+'] allowObservations:' +strAllowedObservations);

			} // end if 

			j++;

		} // end for 
		i++;

		// 2024-04-26 JER - Modified to evaluate if any allowedObservations are found - if none then remove associated OBR and SPM segments
		if(perSegAllowedObservationSegments<1) {

			var getSegment= getSegmentsAfter(msg, msg.children()[seg.childIndex()],'SPM',true,'OBR');
			if(debug>0) logger.error('TEST::'+scriptName+' SPM getSegment typeof: '+typeof(getSegment)+' index: '+[seg.childIndex()+1]+' value: '+getSegment);
			delete msg.children()[seg.childIndex()+1]; 

			var getSegment= getSegmentsAfter(msg, msg.children()[seg.childIndex()],'OBR',false,'OBR');
			if(debug>0) logger.error('TEST::'+scriptName+' OBR getSegment typeof: '+typeof(getSegment)+' index: '+[seg.childIndex()]+' value: '+getSegment);
			delete msg.children()[seg.childIndex()]; 

		} // end if 

	} // end for each

	channelMap.put('altAllowedObservations',allowedObservations);
	channelMap.put('altRemovedObservations',removedObservations);

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' allowedObservations: '+allowedObservations);
		logger.error('DEBUG::'+scriptName+' removedObservations: '+removedObservations);
	} // end if 

	channelMap.put('pathogenDetected',pathogenDetected); // true, false, invalid
	channelMap.put('modified_msg',msg);

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' pathogenDetected: '+$c('pathogenDetected'));
		logger.error('DEBUG::'+scriptName+' msg: '+$c('modified_msg'));
	} // end if 

	return msg;
}

/**
	Desc:  Evaluate the OBX versus the observed result code (OBX-3.1) evaluated against Configuration Setting variable: ADPHNotifiablePathogens
	to determine whether to exclude the Observation Results (OBX) segments from the message and set the pathogenDetected channelMap variable.

	fileName:		remove_observations_from_observation_result_codes.js
	name:		Remove Observations From Observation Result Codes
	authored:		Jeffrey Ritz
	created:		2023-02-22
	description:	Evaluate the OBX versus the observed result code (OBX-3.1) evaluated against Configuration Setting variable: ADPHNotifiablePathogens
	to determine whether to exclude the Observation Results (OBX) segments from the message and set the pathogenDetected channelMap variable.
	pathogenDetected= true -> desired pathogens detected persist the observation result OBX segment in the transformed message (msg)
	pathogenDetected= false -> failed to observe the desired pathogen or Non-Detected result remove observation result from OBX segment in transformed message (msg)
	history:
	2023-04-20 JER - Modified changed from OBX.3.4 to OBX.3.1
	2023-03-06 JER - Modified to incorporate the count of the detected in OBX[OBX.1][OBX.1.1]
	2023-03-03 JER - Moved the curObservationValue was moved up to be evaluated and accompanying logger.error statement
	2023-03-02 JER - Modification to evaluate differences between CWE (Coded Extended) vs ST (String) data type
		if obx-2.1 is EQ CWE reference observation code from obx-3.4
		if obx-2.1 is NE CWE (e.g. ST) reference observation code from obx-3.1
	2023-03-02 JER - remove the carriage return line feeds from the observationsFiltered channelMap variable
	2023-03-01 JER - Added the collective observationFiltered channelMap variable to capture the array of filtered observations obx31 and obx32 values
	2023-02-27 JER - Modified from: $cfg('ADPHNotifiablePathogens') to: allowedObservationCodeList
	2024-02-24 JER - Modified to leverage the function input parameterized variable orderCodeList to replace hardCoded configuration setting variable
	2023-02-23 JER - Converted transformer to code_template 
*/
function remove_observations_from_observation_result_codes(msg, allowedObservationCodeList) {

	var scriptName= 'remove_observations_from_observation_result_codes';
	var debug= 0;
	var detected= false;
	var nonDetectedFilteredCount= 0;
	var nonDetectedFiltered= new Array();
	var permittedPathogenOrder= false;
	var i= 1;
	var j= 1;
	var observationsFiltered = new Array();
	var observationsFilteredCount= 0;
	var tmp= new XML('<HL7Message></HL7Message>');
	var channelName = Packages.com.mirth.connect.server.controllers.ChannelController.getInstance().getDeployedChannelById(channelId).getName();
	channelMap.put('channelName',channelName);

	// for each segment - child per msg.children()
	for each(child in msg.children()) {

		if(debug>0) logger.error('DEBUG::'+scriptName+' typeof: '+typeof(child)+' localName: '+child.localName()+' child: '+child);

		if(child.localName()== 'MSH') {
			var msgCtrlId= child['MSH.10']['MSH.10.1'].toString();
		}

		if(child.localName()== 'OBX') {

			// process each OBX
			// 2023-03-02 JER - Modification to evaluate differences between CWE (Coded Extended) vs ST (String) data type 
			var obx21= child['OBX.2']['OBX.2.1'].toString();
			if(child['OBX.2']['OBX.2.1'].toString()== 'CWE') {
				// 2023-04-20 JER - Modified changed from OBX.3.4 to OBX.3.1
				var obx31= child['OBX.3']['OBX.3.1'].toString();
				var obx51= child['OBX.5']['OBX.5.2'].toString().toUpperCase();
			} else if(child['OBX.2']['OBX.2.1'].toString()== 'ST') {
				var obx31= child['OBX.3']['OBX.3.1'].toString();
				var obx51= child['OBX.5']['OBX.5.1'].toString().toUpperCase();
			} else {
				var obx31= child['OBX.3']['OBX.3.1'].toString();
				var obx51= child['OBX.5']['OBX.5.1'].toString().toUpperCase();
			} // end if 
			var obx32= child['OBX.3']['OBX.3.2'].toString();
			var curObservationValue= obx51;

			if(debug>0) logger.error('DEBUG::'+scriptName+' obx21: '+obx21+' obx31: '+obx31+' obx32: '+obx32+' obx51: '+obx51+' observedValue: '+curObservationValue);

			if(debug>0) {
				logger.error('DEBUG::'+scriptName+' OBX['+i+'] typeof: '+typeof(obx31)+' obx31: '+obx31);
				logger.error('DEBUG::'+scriptName+' OBX['+i+'] ADPHNotifiablePathogens: '+$cfg('ADPHNotifiablePathogens'));
				logger.error('DEBUG::'+scriptName+' OBX['+i+'] obx31 contained in ADPHNotifiablePathogens evaluation: '+$cfg('ADPHNotifiablePathogens').indexOf(obx31));
			}

			// 2024-02-24 JER - Modified to leverage the function input parameterized variable orderCodeList to replace hardCoded configuration setting variable
			// if($cfg('ADPHNotifiablePathogens').indexOf(obx31)>-1) {
			if(allowedObservationCodeList.indexOf(obx31)>-1) {		
				permittedPathogenOrder= true;

				// 2024-02-24 JER - Modified to leverage the function input parameterized variable orderCodeList to replace hardCoded configuration setting variable
				if(debug>0) logger.error('DEBUG::'+scriptName+' ['+i+'] FOUND ADPH Notifiable Pathogens: '+obx31+' value: '+curObservationValue+' from the list of codes: '+allowedObservationCodeList+' evaluation: '+allowedObservationCodeList.indexOf(obx31)+' permitted Order Observation OBX Segment.'); 

				// evaluate if element value is numerical exponential
				if(curObservationValue.toString().indexOf('1.00E')>-1) {
					if(debug>0) logger.error('DEBUG::'+scriptName+' determined value contains an exponential 1.00E value: '+(curObservationValue.toString().indexOf('1.00E')>-1));
				}

				if(debug>0) {
					logger.error('DEBUG::'+scriptName+' OBX['+i+'].5.1 (curObservationValue== DETECTED): '+(curObservationValue== 'DETECTED'));
					logger.error('DEBUG::'+scriptName+' OBX['+i+'].5.1 (curObservationValue== TRUE): '+(curObservationValue== 'TRUE'));
					logger.error('DEBUG::'+scriptName+' OBX['+i+'].5.1 (curObservationValue.toString().indexOf(1.00E)>-1): '+(curObservationValue.toString().indexOf('1.00E')>-1));
					logger.error('DEBUG::'+scriptName+' OBX['+i+'].5.1 parseInt(curObservationValue)!== NaN): '+(parseInt(curObservationValue) !== 'NaN'));
				} // end if 

				// 2022-10-17 JER - Added evaluate if NaN - only 
				if(((curObservationValue== 'DETECTED')&&(parseInt(curObservationValue) !== 'NaN'))||(curObservationValue== 'TRUE')||(curObservationValue.toString().indexOf('1.00E')>-1)) {
					detected= true;
					// 2023-03-06 JER - Modified to incorporate the count of the detected in OBX[OBX.1][OBX.1.1]
					// child['OBX.1']['OBX.1.1']= detectedCount;
					// detectedCount++;
					// 2023-02-24 JER - Modified to use appendChild E4X denoted XML function
					tmp.appendChild(child);
					//if(debug>0) 
					if(debug>0) logger.error('DEBUG::'+scriptName+' ['+i+'] DETECTED: '+detected+' ADD SEGMENT: '+child.localName());
				} else {
					nonDetectedFilteredCount++;
					var strNonDetectedFiltered= 'Non-Detected['+nonDetectedFilteredCount+'] Observation CODE: '+obx31+' DESCRIPTION: '+obx32;
					nonDetectedFiltered.push(strNonDetectedFiltered);
					if(debug>0) logger.error('DEBUG::'+scriptName+' ['+i+'] NOT DETECTED: '+detected+' REMOVE SEGMENT: '+child.localName());
				} // end if 

			} else {
					// 2023-03-01 JER - Added the collective observationFiltered channelMap variable to capture the array of filtered observations
					observationsFilteredCount++;
					var observationFiltered= 'Observation['+observationsFilteredCount+'] CODE: '+obx31+' DESCRIPTION: '+obx32+'\r\n';
					observationsFiltered.push(observationFiltered);
					//if(debug>0)
					// 2023-03-05 JER - Modified to include description of failed to find the ADPH Notifiable Pathogen observed results 
					if(debug>0) logger.error('DEBUG::'+scriptName+' ['+i+'] DID NOT FIND ADPH Notifiable Pathogens: '+obx31+' value: '+curObservationValue+' from the list of codes: '+allowedObservationCodeList+' evaluation: '+allowedObservationCodeList.indexOf(obx31)+' rejected Order Observation OBX Segment.'); 
			}
			i++;
		} else {
			// 2023-02-24 JER - Modified to use appendChild E4X denoted XML function
			// tmp+= child;
			tmp.appendChild(child);
			if(debug>0) logger.error('DEBUG::'+scriptName+' ['+i+'] ADD SEGMENT: '+child.localName());
		} // end for
		if(debug>0) logger.error('DEBUG::'+scriptName+' tmp['+j+'] contains: '+tmp);
		j++;
	} // end for

	if(observationsFiltered.length<1) {
		observationsFiltered= 'NONE';
	} // end if 

	channelMap.put('pathogenDetected',detected); // true, false, invalid

	var strObservationsFiltered= observationsFiltered;
	// 2023-03-02 JER - remove the carriage return line feeds from the observationsFiltered channelMap variable
	var observationsFiltered= observationsFiltered.toString().replace(/[\r][\n]/g,'');
	channelMap.put('observationsFiltered',observationsFiltered);
	channelMap.put('nonDetectedFiltered',nonDetectedFiltered);

	if(debug>0) logger.error('DEBUG::'+scriptName+' msg: '+msgCtrlId+' observation required pathogens: '+$c('pathogenDetected')+' these observations were FILTERED: '+'\r\n'+strObservationsFiltered);

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' pathogenDetected: '+$c('pathogenDetected'));
		logger.error('DEBUG::'+scriptName+' tmp: '+tmp);
	} // end if 

	return tmp;
}

/**
Desc:  Evaluate the OBX versus the observed result code (OBX-3.1) evaluated against Configuration Setting variable: ADPHNotifiablePathogens to determine whether to exclude the Observation Results (OBX) segments from the message and set the pathogenDetected channelMap variable.

	fileName:		remove_observations_from_observation_result_codes_new2.js
	name:		Remove Observations From Observation Result Codes (New2)
	@authored:	Jeffrey Ritz
	@created:		2023-02-27
	@description:	Remove observations with Non-Detected Results and mark Detected to be Allowed/Filtered

	history:
	2023-05-18 JER - persist the OBR segment childIndex() and msg segment children() the OBR childIndex() remove the prior segment -> ORC segment
	2023-04-26 JER - Modified to determine if should evaluate detectable or not
	2023-04-21 JER - Consider following https://github.com/nextgenhealthcare/connect/discussions/5244
	2023-02-27 JER - Cloned the Remove Observations From Observation Result Codes
*/
function remove_observations_from_observation_result_codes_new2(msg, allowedObservationCodeList, filterDetectable) {

	var scriptName= 'remove_observations_from_observation_result_codes_new2';
	var debug= 0;
	var detected= false;
	// Allowed Observations Filtered List
	var nonDetectedFilteredCount= 0;
	var nonDetectedFiltered= new Array();
	// Disallowed Observations Filtered listStyleType
	var disallowedObservationsCount= 0;
	var disallowedObservations= new Array();

	var permittedPathogenOrder= false;
	// 2023-04-26 JER - Modified to determine if should evaluate detectable or not

	var i= 1;
	var j= 1;
	var k= 1;
	var observationsFiltered = new Array();
	var observationsFilteredCount= 0;
	// var tmp= new XML('<HL7Message></HL7Message>');
	var channelName = Packages.com.mirth.connect.server.controllers.ChannelController.getInstance().getDeployedChannelById(channelId).getName();
	channelMap.put('channelName',channelName);

	var num_orc= msg..ORC.length();

	for each(seg in msg..OBR) {	

		if(debug>0) logger.error('DEBUG::'+scriptName+' seg['+j+']: '+seg+' childIndex(): '+seg.childIndex());

		// 2023-05-18 JER - persist the OBR segment childIndex()
		var obrChildIndex= seg.childIndex(); 

		var allowedObservationSegments= 0;
		for each(obx in getSegmentsAfter(msg, msg.children()[seg.childIndex()],'OBX',false,'OBR')) {

			// process each OBX
			if(obx['OBX.2']['OBX.2.1'].toString()== 'CWE') {
				var obx31= obx['OBX.3']['OBX.3.4'].toString();
			} else {
				var obx31= obx['OBX.3']['OBX.3.1'].toString();
			} // end if 
			var obx32= obx['OBX.3']['OBX.3.2'].toString();
			var obx51= obx['OBX.5']['OBX.5.1'].toString();

			if(debug>0) logger.error('DEBUG::'+scriptName+' OBX['+j+'] obx31: '+obx31+' obx32: '+obx32+' obx51: '+obx51);

			// 2023-02-27 JER - Modified from OBX.5.1 to OBX.5.2
			if(child['OBX.5']['OBX.5.2'].toString().length<1) {
				var curObservationValue=obx['OBX.5']['OBX.5.2'].toString().toUpperCase();
			} else {
				var curObservationValue=obx51.toString().toUpperCase();
			} // end if 

			if(debug>0) {
				logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] curObservationValue: '+curObservationValue);
				logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] obx31: '+obx31+' EVAL FOUND: '+allowedObservationCodeList.indexOf(obx31)+' allowedObservationCodeList: '+allowedObservationCodeList);
			}
			// 2024-02-24 JER - Modified to leverage the function input parameterized variable orderCodeList to replace hardCoded configuration setting variable
			if(allowedObservationCodeList.indexOf(obx31)>-1) {	

				channelMap.put('pathogenDetected',true);
				// evaluate if element value is numerical exponential
				if(curObservationValue.toString().indexOf('1.00E')>-1) {
					if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] OBX.5.2 value contains an exponential 1.00E value: '+(curObservationValue.toString().indexOf('1.00E')>-1));
				} // end if 

				// evaluate if element value is number 
				if(parseInt(curObservationValue.toString() !== 'NaN')) {
					if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] OBX.5.2 value contains IS a number: '+parseInt(curObservationValue.toString()!== 'NaN'));
				} // end if 

				if(debug>0) logger.error('DEBUG::'+scriptName+' filterDetecteable: '+filterDetecteable);
				// 2023-04-26 JER - Evaluate if allowing either Detected or Non-Detected values
				if(filterDetectable=== true) {

					// 2022-10-17 JER - Added evaluate if NaN - only 
					if((curObservationValue== 'DETECTED')||(curObservationValue== 'TRUE')||(curObservationValue.toString().indexOf('1.00E')>-1)||parseInt(curObservationValue.toString()!== 'NaN')) {

						allowedObservationSegments++;
						detected= true;
						//tmp.appendChild(child);
						if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] DETECTED OBX.5.1 value: '+curObservationValue+' checkDetected: '+filterDetecteable+' DETECTED: '+detected+' KEEP SEGMENT: '+obx);
					} else {
						nonDetectedFilteredCount++;
						var strNonDetectedFiltered= 'Non-Detected['+nonDetectedFilteredCount+'] Observation CODE: '+obx31+' DESCRIPTION: '+obx32;
						nonDetectedFiltered.push(strNonDetectedFiltered);

						delete msg.children()[obx.childIndex()];

						if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] NON-DETECTED OBX.5.1 value: '+curObservationValue+' checkDetected: '+filterDetecteable+' vs NON-DETECTED: '+detected+' REMOVE SEGMENT: '+obx);

					} // end if 

				// 2023-04-26 JER - Evaluate if allowing either Detected or Non-Detected values
				} else {

					allowedObservationSegments++;
					if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] NO NON-DETECTED FILTER - ALLOW OBX.5.1 value '+curObservationValue+' checkDetected: '+filterDetecteable+' ALLOW ALL Detectable values ALLOW SEGMENT: '+obx);

				} // end if 

			} else {

				disallowedObservationsCount++;
				var strDisallowedObservations= 'Disallowed Observations['+disallowedObservationsCount+'] Observation CODE: '+obx31+' DESCRIPTION: '+obx32;
				disallowedObservations.push(strDisallowedObservations);

				delete msg.children()[obx.childIndex()];
				if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] OBX.3.1 value: '+obx31+' DISALLOWED Observation['+disallowedObservationsCount+'] REMOVE SEGMENT: '+obx);

			} // end if 
			j++;

		} // end for 
		i++;

		// 2024-04-26 JER - Modified to evaluate if any allowedObservations are found - if none then remove associated OBR and SPM segments
		if(allowedObservationSegments<1) {

			/*
			var getSegment= getSegmentsAfter(msg, msg.children()[seg.childIndex()],'SPM',true,'OBR');
			if(debug>0) logger.error('TEST::'+scriptName+' SPM getSegment typeof: '+typeof(getSegment)+' index: '+[seg.childIndex()]+' value: '+getSegment);
			delete msg.children()[seg.childIndex()]; 
			*/

			var getSegment= getSegmentsAfter(msg, msg.children()[seg.childIndex()],'OBR',false,'OBR');
			if(debug>0) logger.error('TEST::'+scriptName+' OBR getSegment typeof: '+typeof(getSegment)+' index: '+[seg.childIndex()]+' value: '+getSegment);
			delete msg.children()[seg.childIndex()]; 

			// 2023-05-18 JER - msg segment children() the OBR childIndex() remove the prior segment -> ORC segment
			delete msg.children()[obrChildIndex-1];


		} // end if 

	} // end for each

	channelMap.put('specimenDetected',detected); // true, false, invalid
	channelMap.put('modified_msg',msg);

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' specimenDetected: '+$c('specimenDetected'));
		logger.error('DEBUG::'+scriptName+' msg: '+$c('modified_msg'));
	} // end if 

	return msg;

} // end function

/**
Desc:  Evaluate the OBX versus the observed result code (OBX-3.1) evaluated against Configuration Setting variable: ADPHNotifiablePathogens to determine whether to exclude the Observation Results (OBX) segments from the message and set the pathogenDetected channelMap variable.

	fileName:		remove_observations_from_observation_result_codes_new3.js
	name:		Remove Observations From Observation Result Codes (New3)
	@authored:	Jeffrey Ritz
	@created:		2023-02-27
	@description:	Remove observations with Non-Detected Results and mark Detected to be Allowed/Filtered

	history:
	2023-05-22 JER - Modified to include the set of the curObservationValue of OBX.5.2 based on OBX.2.1 as 'CWE'
	2023-05-18 JER - persist the OBR segment childIndex() and msg segment children() the OBR childIndex() remove the prior segment -> ORC segment
	2023-04-26 JER - Modified to determine if should evaluate detectable or not
	2023-04-21 JER - Consider following https://github.com/nextgenhealthcare/connect/discussions/5244
	2023-02-27 JER - Cloned the Remove Observations From Observation Result Codes
*/
function remove_observations_from_observation_result_codes_new3(msg, allowedObservationCodeList, filterDetectable) {

	var scriptName= 'remove_observations_from_observation_result_codes_new3';
	var debug= 0;
	var detected= false;
	// Allowed Observations Filtered List
	var nonDetectedFilteredCount= 0;
	var nonDetectedFiltered= new Array();
	var lstNonDetectedFiltered= new Array();

	// Allowed Observations Filtered List
	var allowedObservationsCount= 0;
	var allowedObservations= new Array();
	var lstAllowedObservations= new Array();

	// Disallowed Observations Filtered listStyleType
	var disallowedObservationsCount= 0;
	var disallowedObservations= new Array();
	var lstDisallowedObservations= new Array();

	var permittedPathogenOrder= false;
	// 2023-04-26 JER - Modified to determine if should evaluate detectable or not

	var i= 1;
	var j= 1;
	var k= 1;
	var observationsFiltered = new Array();
	var observationsFilteredCount= 0;
	// 2023-05-18 JER - persist all OBR related segment indexes
	var arrObrChildIndex= new Array();

	// var tmp= new XML('<HL7Message></HL7Message>');
	var channelName = Packages.com.mirth.connect.server.controllers.ChannelController.getInstance().getDeployedChannelById(channelId).getName();
	channelMap.put('channelName',channelName);

	var num_orc= msg..ORC.length();

	for each(seg in msg..OBR) {	

		var allowedObservationSegments= 0;

		if(debug>0) logger.error('DEBUG::'+scriptName+' seg['+j+']: '+seg+' childIndex(): '+seg.childIndex());

		// 2023-05-18 JER - persist the OBR segment childIndex()
		var obrChildIndex= seg.childIndex(); 

		var allowedObservationSegments= 0;
		for each(obx in getSegmentsAfter(msg, msg.children()[seg.childIndex()],'OBX',false,'OBR')) {

			// process each OBX
			if(obx['OBX.2']['OBX.2.1'].toString()== 'CWE') {
				var obx31= obx['OBX.3']['OBX.3.4'].toString();
				// 2023-05-22 JER - Modified to include the set of the curObservationValue of OBX.5.2 based on OBX.2.1 as 'CWE'
				var curObservationValue=obx['OBX.5']['OBX.5.2'].toString().toUpperCase();
			} else {
				var obx31= obx['OBX.3']['OBX.3.1'].toString();
				// 2023-05-22 JER - Modified to include the set of the curObservationValue of OBX.5.2 based on OBX.2.1 as 'CWE'
				var curObservationValue=obx['OBX.5']['OBX.5.1'].toString();
			} // end if 
			var obx32= obx['OBX.3']['OBX.3.2'].toString();
			var obx51= obx['OBX.5']['OBX.5.1'].toString();

			if(debug>0) logger.error('DEBUG::'+scriptName+' OBX['+j+'] obx31: '+obx31+' obx32: '+obx32+' curObservationValue: '+curObservationValue);

			/*
			// 2023-05-22 JER - Modified to include the set of the curObservationValue of OBX.5.2 based on OBX.2.1 as 'CWE'
			// 2023-02-27 JER - Modified from OBX.5.1 to OBX.5.2
			if(child['OBX.5']['OBX.5.2'].toString().length<1) {
				var curObservationValue=obx['OBX.5']['OBX.5.2'].toString().toUpperCase();
			} else {
				var curObservationValue=obx51.toString().toUpperCase();
			} // end if 
			*/

			if(debug>0) {
				logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] obx31: '+obx31+' EVAL FOUND: '+allowedObservationCodeList.indexOf(obx31)+' allowedObservationCodeList: '+allowedObservationCodeList);
				logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] curObservationValue: '+curObservationValue);
			}

			// 2024-02-24 JER - Modified to leverage the function input parameterized variable orderCodeList to replace hardCoded configuration setting variable
			if(allowedObservationCodeList.indexOf(obx31)>-1) {	

				channelMap.put('pathogenDetected',true);
				// evaluate if element value is numerical exponential
				if(curObservationValue.toString().indexOf('1.00E')>-1) {
					if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] OBX.5.2 value contains an exponential 1.00E value: '+(curObservationValue.toString().indexOf('1.00E')>-1));
					curObservationValue= 'DETECTED';
				} // end if 

				// 8.01E+04
				if(curObservationValue.toString().indexOf('8.01E')>-1) {
					if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] OBX.5.2 value contains an exponential 8.01E value: '+(curObservationValue.toString().indexOf('8.01E')>-1));
					curObservationValue= 'DETECTED';
				} // end if 

				// evaluate if element value is number 
				if(parseInt(curObservationValue.toString() !== 'NaN')) {
					if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] OBX.5.2 value contains IS a number: '+parseInt(curObservationValue.toString()!== 'NaN'));
					curObservationValue= 'DETECTED';
				} // end if 

				if(debug>0) {
					logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] obx31: '+obx31+' filterDetectable: '+filterDetectable+' filterDetectable.indexOf(true): '+(filterDetectable.indexOf('true')));
					logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] obx31: '+obx31+' curObservationValue: '+curObservationValue.toUpperCase()+' curObservationValue NEQ NOT DETECTED: '+(curObservationValue.toUpperCase()!== 'NOT DETECTED'));
				} // end if

				// 2023-04-26 JER - Evaluate if allowing either Detected or Non-Detected values
				if(filterDetectable.indexOf('true')>-1) {

					// 2022-10-17 JER - Added evaluate if NaN - only 
					// if((curObservationValue== 'DETECTED')||(curObservationValue== 'TRUE')||(curObservationValue.toString().indexOf('1.00E')>-1)||parseInt(curObservationValue.toString()!== 'NaN')) {
					// 2023-05-22 JER - Modified to evaluate only the string 'NOT DETECTED'
					if(curObservationValue.toUpperCase()!== 'NOT DETECTED') {

						// required to evaluate to determine if require to filter ORM and SPM segments
						allowedObservationSegments++;

						allowedObservationsCount++;
						detected= true;
						var strAllowedObservations= 'OBR['+i+']OBX['+j+'] Allowed['+allowedObservationsCount+'] Observation CODE: '+obx31+' DESCRIPTION: '+obx32+' curObservationValue: '+curObservationValue;
						if(allowedObservations.indexOf(strAllowedObservations)<0) {
							allowedObservations.push(strAllowedObservations);
						} // end if 

						if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] DETECTED OBX.5.1 value: '+curObservationValue+' checkDetected: '+filterDetectable+' DETECTED: '+detected+' KEEP SEGMENT: '+obx);
					} else {

						nonDetectedFilteredCount++;
						var strNonDetectedFiltered= 'OBR['+i+']OBX['+j+'] Non-Detected['+nonDetectedFilteredCount+'] Observation CODE: '+obx31+' DESCRIPTION: '+obx32+' curObservationValue: '+curObservationValue;
						if(nonDetectedFiltered.indexOf(strNonDetectedFiltered)<0) {
							nonDetectedFiltered.push(strNonDetectedFiltered);
						} // end if 
						delete msg.children()[obx.childIndex()];

						if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] NON-DETECTED OBX.5.1 value: '+curObservationValue+' checkDetected: '+filterDetectable+' vs NON-DETECTED: '+detected+' REMOVE SEGMENT: '+obx);

					} // end if 

				// 2023-04-26 JER - Evaluate if allowing either Detected or Non-Detected values
				} else {

					detected= true;
					allowedObservationsCount++;
					var strAllowedObservations= 'OBR['+i+']OBX['+j+'] Allowed['+allowedObservationsCount+'] Observation CODE: '+obx31+' DESCRIPTION: '+obx32+' curObservationValue: '+curObservationValue;
					if(allowedObservations.indexOf(strAllowedObservations)<0) {
						allowedObservations.push(strAllowedObservations);
					} // end if 

					if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] Allowed Observations OBX.5.1 value '+curObservationValue+' checkDetected: '+filterDetectable+' ALLOW ALL Detectable values ALLOW SEGMENT: '+obx);

				} // end if 

			} else {

				disallowedObservationsCount++;
				var strDisallowedObservations= 'OBR['+i+']OBX['+j+'] Disallowed Observations['+disallowedObservationsCount+'] Observation CODE: '+obx31+' DESCRIPTION: '+obx32+' curObservationValue: '+curObservationValue;
				if(disallowedObservations.indexOf(strDisallowedObservations)<0) {
					disallowedObservations.push(strDisallowedObservations);
				} // end if 

				delete msg.children()[obx.childIndex()];

				if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] OBX.3.1 value: '+obx31+' DISALLOWED Observation['+disallowedObservationsCount+'] REMOVE SEGMENT: '+obx);

			} // end if 
			j++;

		} // end for 
		i++;

		if(debug>0) logger.error('DEBUG::'+scriptName+' OBR childIndex['+obrChildIndex+'] allowedObservationSegments: '+allowedObservationSegments); 

		// 2024-04-26 JER - Modified to evaluate if any allowedObservations are found - if none then remove associated OBR and SPM segments
		if(allowedObservationSegments<1) {

			var getSegment= getSegmentsAfter(msg, msg.children()[seg.childIndex()],'SPM',true,'OBR');

			if(debug>0) logger.error('DEBUG::'+scriptName+' delete SPM getSegment typeof: '+typeof(getSegment)+' index: '+[obrChildIndex+1]+' value: '+getSegment);
			delete msg.children()[obrChildIndex+1]; 

			var getSegment= getSegmentsAfter(msg, msg.children()[seg.childIndex()],'OBR',false,'OBR');
			if(debug>0) logger.error('DEBUG::'+scriptName+' delete OBR getSegment typeof: '+typeof(getSegment)+' index: '+[seg.childIndex()]+' value: '+getSegment);
			delete msg.children()[seg.childIndex()]; 

			// if(debug>0) 
			//logger.error('DEBUG::'+scriptName+' delete segment typeof: '+typeof(msg.children()[obrChildIndex-1])+' index: '+[obrChildIndex-1]+' value: '+msg.children()[obrChildIndex-1]);
			// 2023-05-18 JER - msg segment children() the OBR childIndex() remove the prior segment -> ORC segment
			//delete msg.children()[obrChildIndex-1];


		} // end if 

		// per OBR segment push into an overall list to be persisted in channelMap
		lstAllowedObservations.push(allowedObservations);
		lstDisallowedObservations.push(disallowedObservations);
		lstNonDetectedFiltered.push(nonDetectedFiltered);

	} // end for each


	if(lstAllowedObservations.length>0) {
		if(debug>0) logger.error('DEBUG::'+scriptName+' lstAllowedObservations: '+lstAllowedObservations); 
		channelMap.put('arrAllowedObservations',lstAllowedObservations);
	}  // end if 

	if(lstDisallowedObservations.length>0) {
		if(debug>0) logger.error('DEBUG::'+scriptName+' lstDisallowedObservations: '+lstDisallowedObservations);  
		channelMap.put('arrDisallowedObservations',lstDisallowedObservations); 
	} // end if 

	if(lstNonDetectedFiltered.length>0) {
		if(debug>0) logger.error('DEBUG::'+scriptName+' lstNonDetectedFiltered: '+lstNonDetectedFiltered); 
		channelMap.put('arrNonDetectedFiltered',lstNonDetectedFiltered); 
	} // end if 

	channelMap.put('specimenDetected',detected); // true, false, invalid
	channelMap.put('modified_msg',msg);

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' specimenDetected: '+$c('specimenDetected'));
		logger.error('DEBUG::'+scriptName+' msg: '+$c('modified_msg'));
	} // end if 

	return msg;

}

/**
Desc:  Evaluate the OBX versus the observed result code (OBX-3.1) evaluated against Configuration Setting variable: ADPHNotifiablePathogens to determine whether to exclude the Observation Results (OBX) segments from the message and set the pathogenDetected channelMap variable.

	fileName:		remove_observations_from_observation_result_codes_new.js
	name:		Remove Observations From Observation Result Codes (New)
	@authored:	Jeffrey Ritz
	@created:		2023-02-27
	@description:	Remove observations with Non-Detected Results and mark Detected to be Allowed/Filtered

	history:
	2023-08-25 JER - Modified moved the static Config Map setting to a function parameterized variable and added debug to check/verify two parameterized variables
	2023-04-26 JER - Modified to determine if should evaluate detectable or not
	2023-04-21 JER - Consider following https://github.com/nextgenhealthcare/connect/discussions/5244
	2023-02-27 JER - Cloned the Remove Observations From Observation Result Codes
*/
function remove_observations_from_observation_result_codes_new(msg, allowedObservationCodeList, filterNotifiablePathogensNonDetectable) {

	var scriptName= 'remove_observations_from_observation_result_codes_new';
	var debug= 0;
	var detected= false;
	// Allowed Observations Filtered List
	var nonDetectedFilteredCount= 0;
	var nonDetectedFiltered= new Array();
	// Disallowed Observations Filtered listStyleType
	var disallowedObservationsCount= 0;
	var disallowedObservations= new Array();

	var permittedPathogenOrder= false;
	// 2023-04-26 JER - Modified to determine if should evaluate detectable or not
	// 2023-08-25 JER - Modified moved the static Config Map setting to a function parameterized variable and added debug to check/verify two parameterized variables
	// var filterNotifiablePathogensNonDetectable= $cfg('TNNotifiablePathogensDetectable');
	if(debug>0) {
		logger.error('DEBUG::'+channelName+':'+scriptName+' allowedObservationCodeList: '+allowedObservationCodeList);
		logger.error('DEBUG::'+channelName+':'+scriptName+' filterNotifiablePathogensNonDetectable: '+filterNotifiablePathogensNonDetectable);
	} // end if 

	var i= 1;
	var j= 1;
	var observationsFiltered = new Array();
	var observationsFilteredCount= 0;
	// var tmp= new XML('<HL7Message></HL7Message>');
	var channelName = Packages.com.mirth.connect.server.controllers.ChannelController.getInstance().getDeployedChannelById(channelId).getName();
	channelMap.put('channelName',channelName);

	for each(seg in msg..OBR) {	

		logger.error('TEST::'+scriptName+' seg['+j+']: '+seg);

		var allowedObservationSegments= 0;
		for each(obx in getSegmentsAfter(msg, msg.children()[seg.childIndex()],'OBX',false,'OBR')) {

			// process each OBX
			var obx31= obx['OBX.3']['OBX.3.1'].toString();
			var obx32= obx['OBX.3']['OBX.3.2'].toString();
			var obx51= obx['OBX.5']['OBX.5.1'].toString();

			if(debug>0) logger.error('DEBUG::'+scriptName+' OBX['+j+'] obx31: '+obx31+' obx32: '+obx32+' obx51: '+obx51);

			// 2023-02-27 JER - Modified from OBX.5.1 to OBX.5.2
			if(child['OBX.5']['OBX.5.2'].toString().length<1) {
				var curObservationValue=obx['OBX.5']['OBX.5.2'].toString().toUpperCase();
			} else {
				var curObservationValue=obx51.toString().toUpperCase();
			} // end if 

			if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] curObservationValue: '+curObservationValue);

			// 2024-02-24 JER - Modified to leverage the function input parameterized variable orderCodeList to replace hardCoded configuration setting variable
			if(allowedObservationCodeList.indexOf(obx31)>-1) {	

				// evaluate if element value is numerical exponential
				if(curObservationValue.toString().indexOf('1.00E')>-1) {
					if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] OBX.5.2 value contains an exponential 1.00E value: '+(curObservationValue.toString().indexOf('1.00E')>-1));
				} // end if 

				// evaluate if element value is number 
				if(parseInt(curObservationValue.toString() !== 'NaN')) {
					if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] OBX.5.2 value contains IS a number: '+parseInt(curObservationValue.toString()!== 'NaN'));
				} // end if 

				// 2023-04-26 JER - Evaluate if allowing either Detected or Non-Detected values
				if(filterNotifiablePathogensNonDetectable== true) {

					// 2022-10-17 JER - Added evaluate if NaN - only 
					if((curObservationValue== 'DETECTED')||(curObservationValue== 'TRUE')||(curObservationValue.toString().indexOf('1.00E')>-1)||parseInt(curObservationValue.toString()!== 'NaN')) {

						allowedObservationSegments++;
						detected= true;
						//tmp.appendChild(child);
						if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] OBX.5.1 value: '+curObservationValue+' DETECTED: '+detected+' KEEP SEGMENT: '+obx);
					} else {
						nonDetectedFilteredCount++;
						var strNonDetectedFiltered= 'Non-Detected['+nonDetectedFilteredCount+'] Observation CODE: '+obx31+' DESCRIPTION: '+obx32;
						nonDetectedFiltered.push(strNonDetectedFiltered);

						delete msg.children()[obx.childIndex()];

						if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] OBX.5.1 value: '+curObservationValue+' NON-DETECTED: '+detected+' REMOVE SEGMENT: '+obx);

					} // end if 

				// 2023-04-26 JER - Evaluate if allowing either Detected or Non-Detected values
				} else {

					allowedObservationSegments++;
					if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] ALLOW OBX.5.1 value '+curObservationValue+' ALLOW ALL Detectable values ALLOW SEGMENT: '+obx);

				} // end if 

			} else {

				disallowedObservationsCount++;
				var strDisallowedObservations= 'Disallowed Observations['+disallowedObservationsCount+'] Observation CODE: '+obx31+' DESCRIPTION: '+obx32;
				disallowedObservations.push(strDisallowedObservations);

				delete msg.children()[obx.childIndex()];
				if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] OBX.3.1 value: '+obx31+' DISALLOWED Observation['+disallowedObservationsCount+'] REMOVE SEGMENT: '+obx);

			} // end if 
			j++;

		} // end for 
		i++;

		// 2024-04-26 JER - Modified to evaluate if any allowedObservations are found - if none then remove associated OBR and SPM segments
		if(allowedObservationSegments<1) {

			var getSegment= getSegmentsAfter(msg, msg.children()[seg.childIndex()],'SPM',true,'OBR');
			if(debug>0) logger.error('TEST::'+scriptName+' SPM getSegment typeof: '+typeof(getSegment)+' index: '+[seg.childIndex()+1]+' value: '+getSegment);
			delete msg.children()[seg.childIndex()+1]; 

			var getSegment= getSegmentsAfter(msg, msg.children()[seg.childIndex()],'OBR',false,'OBR');
			if(debug>0) logger.error('TEST::'+scriptName+' OBR getSegment typeof: '+typeof(getSegment)+' index: '+[seg.childIndex()]+' value: '+getSegment);
			delete msg.children()[seg.childIndex()]; 

		} // end if 

	} // end for each

	channelMap.put('disallowedObservations',disallowedObservations); 
	channelMap.put('specimenDetected',detected); // true, false, invalid
	channelMap.put('modified_msg',msg);

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' disallowedObservations: '+$c('disallowedObservations'));
		logger.error('DEBUG::'+scriptName+' specimenDetected: '+$c('specimenDetected'));
		logger.error('DEBUG::'+scriptName+' msg: '+$c('modified_msg'));
	} // end if 

	return msg;

} // end function

/**
Desc:  Evaluate the OBX versus the observed result code (OBX-3.1) evaluated against Configuration Setting variable: ADPHNotifiablePathogens to determine whether to exclude the Observation Results (OBX) segments from the message and set the pathogenDetected channelMap variable.

	fileName:		remove_observations_from_observation_result_codes_new.js
	name:			Remove Observations From Observation Result Codes (New)
	@authored:		Jeffrey Ritz
	@created:		2023-02-27
	@description:	Remove observations with Non-Detected Results and mark Detected to be Allowed/Filtered

	history:
	2023-05-22 JER - Modified to include the set of the curObservationValue of OBX.5.2 based on OBX.2.1 as 'CWE'
	2023-05-18 JER - persist the OBR segment childIndex() and msg segment children() the OBR childIndex() remove the prior segment -> ORC segment
	2023-04-26 JER - Modified to determine if should evaluate detectable or not
	2023-04-21 JER - Consider following https://github.com/nextgenhealthcare/connect/discussions/5244
	2023-02-27 JER - Cloned the Remove Observations From Observation Result Codes
*/
function remove_observations_from_observation_result_codes_new(msg, allowedObservationCodeList, filterDetectable) {

	var scriptName= 'remove_observations_from_observation_result_codes_new';
	var debug= 0;
	var detected= false;
	// Allowed Observations Filtered List
	var nonDetectedFilteredCount= 0;
	var nonDetectedFiltered= new Array();
	var lstNonDetectedFiltered= new Array();

	// Allowed Observations Filtered List
	var allowedObservationsCount= 0;
	var allowedObservations= new Array();
	var lstAllowedObservations= new Array();

	// Disallowed Observations Filtered listStyleType
	var disallowedObservationsCount= 0;
	var disallowedObservations= new Array();
	var lstDisallowedObservations= new Array();

	var permittedPathogenOrder= false;
	// 2023-04-26 JER - Modified to determine if should evaluate detectable or not

	var i= 1;
	var j= 1;
	var k= 1;
	var observationsFiltered = new Array();
	var observationsFilteredCount= 0;
	// 2023-05-18 JER - persist all OBR related segment indexes
	var arrObrChildIndex= new Array();

	// var tmp= new XML('<HL7Message></HL7Message>');
	var channelName = Packages.com.mirth.connect.server.controllers.ChannelController.getInstance().getDeployedChannelById(channelId).getName();
	channelMap.put('channelName',channelName);

	var num_orc= msg..ORC.length();

	for each(seg in msg..OBR) {	

		var allowedObservationSegments= 0;

		if(debug>0) logger.error('DEBUG::'+scriptName+' seg['+j+']: '+seg+' childIndex(): '+seg.childIndex());

		// 2023-05-18 JER - persist the OBR segment childIndex()
		var obrChildIndex= seg.childIndex(); 

		var allowedObservationSegments= 0;
		for each(obx in getSegmentsAfter(msg, msg.children()[seg.childIndex()],'OBX',false,'OBR')) {

			// process each OBX
			if(obx['OBX.2']['OBX.2.1'].toString()== 'CWE') {
				var obx31= obx['OBX.3']['OBX.3.4'].toString();
				// 2023-05-22 JER - Modified to include the set of the curObservationValue of OBX.5.2 based on OBX.2.1 as 'CWE'
				var curObservationValue=obx['OBX.5']['OBX.5.2'].toString().toUpperCase();
			} else {
				var obx31= obx['OBX.3']['OBX.3.1'].toString();
				// 2023-05-22 JER - Modified to include the set of the curObservationValue of OBX.5.2 based on OBX.2.1 as 'CWE'
				var curObservationValue=obx['OBX.5']['OBX.5.1'].toString();
			} // end if 
			var obx32= obx['OBX.3']['OBX.3.2'].toString();
			var obx51= obx['OBX.5']['OBX.5.1'].toString();

			if(debug>0) logger.error('DEBUG::'+scriptName+' OBX['+j+'] obx31: '+obx31+' obx32: '+obx32+' curObservationValue: '+curObservationValue);

			/*
			// 2023-05-22 JER - Modified to include the set of the curObservationValue of OBX.5.2 based on OBX.2.1 as 'CWE'
			// 2023-02-27 JER - Modified from OBX.5.1 to OBX.5.2
			if(child['OBX.5']['OBX.5.2'].toString().length<1) {
				var curObservationValue=obx['OBX.5']['OBX.5.2'].toString().toUpperCase();
			} else {
				var curObservationValue=obx51.toString().toUpperCase();
			} // end if 
			*/

			if(debug>0) {
				logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] obx31: '+obx31+' EVAL FOUND: '+allowedObservationCodeList.indexOf(obx31)+' allowedObservationCodeList: '+allowedObservationCodeList);
				logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] curObservationValue: '+curObservationValue);
			}

			// 2024-02-24 JER - Modified to leverage the function input parameterized variable orderCodeList to replace hardCoded configuration setting variable
			if(allowedObservationCodeList.indexOf(obx31)>-1) {	

				channelMap.put('pathogenDetected',true);
				// evaluate if element value is numerical exponential
				if(curObservationValue.toString().indexOf('1.00E')>-1) {
					if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] OBX.5.2 value contains an exponential 1.00E value: '+(curObservationValue.toString().indexOf('1.00E')>-1));
					curObservationValue= 'DETECTED';
				} // end if 

				// 8.01E+04
				if(curObservationValue.toString().indexOf('8.01E')>-1) {
					if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] OBX.5.2 value contains an exponential 8.01E value: '+(curObservationValue.toString().indexOf('8.01E')>-1));
					curObservationValue= 'DETECTED';
				} // end if 

				// evaluate if element value is number 
				if(parseInt(curObservationValue.toString() !== 'NaN')) {
					if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] OBX.5.2 value contains IS a number: '+parseInt(curObservationValue.toString()!== 'NaN'));
					curObservationValue= 'DETECTED';
				} // end if 

				if(debug>0) {
					logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] obx31: '+obx31+' filterDetectable: '+filterDetectable+' filterDetectable.indexOf(true): '+(filterDetectable.indexOf('true')));
					logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] obx31: '+obx31+' curObservationValue: '+curObservationValue.toUpperCase()+' curObservationValue NEQ NOT DETECTED: '+(curObservationValue.toUpperCase()!== 'NOT DETECTED'));
				} // end if

				// 2023-04-26 JER - Evaluate if allowing either Detected or Non-Detected values
				if(filterDetectable.indexOf('true')>-1) {

					// 2022-10-17 JER - Added evaluate if NaN - only 
					// if((curObservationValue== 'DETECTED')||(curObservationValue== 'TRUE')||(curObservationValue.toString().indexOf('1.00E')>-1)||parseInt(curObservationValue.toString()!== 'NaN')) {
					// 2023-05-22 JER - Modified to evaluate only the string 'NOT DETECTED'
					if(curObservationValue.toUpperCase()!== 'NOT DETECTED') {

						// required to evaluate to determine if require to filter ORM and SPM segments
						allowedObservationSegments++;

						allowedObservationsCount++;
						detected= true;
						var strAllowedObservations= 'OBR['+i+']OBX['+j+'] Allowed['+allowedObservationsCount+'] Observation CODE: '+obx31+' DESCRIPTION: '+obx32+' curObservationValue: '+curObservationValue;
						if(allowedObservations.indexOf(strAllowedObservations)<0) {
							allowedObservations.push(strAllowedObservations);
						} // end if 

						if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] DETECTED OBX.5.1 value: '+curObservationValue+' checkDetected: '+filterDetectable+' DETECTED: '+detected+' KEEP SEGMENT: '+obx);
					} else {

						nonDetectedFilteredCount++;
						var strNonDetectedFiltered= 'OBR['+i+']OBX['+j+'] Non-Detected['+nonDetectedFilteredCount+'] Observation CODE: '+obx31+' DESCRIPTION: '+obx32+' curObservationValue: '+curObservationValue;
						if(nonDetectedFiltered.indexOf(strNonDetectedFiltered)<0) {
							nonDetectedFiltered.push(strNonDetectedFiltered);
						} // end if 
						delete msg.children()[obx.childIndex()];

						if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] NON-DETECTED OBX.5.1 value: '+curObservationValue+' checkDetected: '+filterDetectable+' vs NON-DETECTED: '+detected+' REMOVE SEGMENT: '+obx);

					} // end if 

				// 2023-04-26 JER - Evaluate if allowing either Detected or Non-Detected values
				} else {

					detected= true;
					allowedObservationsCount++;
					var strAllowedObservations= 'OBR['+i+']OBX['+j+'] Allowed['+allowedObservationsCount+'] Observation CODE: '+obx31+' DESCRIPTION: '+obx32+' curObservationValue: '+curObservationValue;
					if(allowedObservations.indexOf(strAllowedObservations)<0) {
						allowedObservations.push(strAllowedObservations);
					} // end if 

					if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] Allowed Observations OBX.5.1 value '+curObservationValue+' checkDetected: '+filterDetectable+' ALLOW ALL Detectable values ALLOW SEGMENT: '+obx);

				} // end if 

			} else {

				disallowedObservationsCount++;
				var strDisallowedObservations= 'OBR['+i+']OBX['+j+'] Disallowed Observations['+disallowedObservationsCount+'] Observation CODE: '+obx31+' DESCRIPTION: '+obx32+' curObservationValue: '+curObservationValue;
				if(disallowedObservations.indexOf(strDisallowedObservations)<0) {
					disallowedObservations.push(strDisallowedObservations);
				} // end if 

				delete msg.children()[obx.childIndex()];

				if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] OBX.3.1 value: '+obx31+' DISALLOWED Observation['+disallowedObservationsCount+'] REMOVE SEGMENT: '+obx);

			} // end if 
			j++;

		} // end for 
		i++;

		if(debug>0) logger.error('DEBUG::'+scriptName+' OBR childIndex['+obrChildIndex+'] allowedObservationSegments: '+allowedObservationSegments); 

		// 2024-04-26 JER - Modified to evaluate if any allowedObservations are found - if none then remove associated OBR and SPM segments
		if(allowedObservationSegments<1) {

			var getSegment= getSegmentsAfter(msg, msg.children()[seg.childIndex()],'SPM',true,'OBR');

			if(debug>0) logger.error('DEBUG::'+scriptName+' delete SPM getSegment typeof: '+typeof(getSegment)+' index: '+[obrChildIndex+1]+' value: '+getSegment);
			delete msg.children()[obrChildIndex+1]; 

			var getSegment= getSegmentsAfter(msg, msg.children()[seg.childIndex()],'OBR',false,'OBR');
			if(debug>0) logger.error('DEBUG::'+scriptName+' delete OBR getSegment typeof: '+typeof(getSegment)+' index: '+[seg.childIndex()]+' value: '+getSegment);
			delete msg.children()[seg.childIndex()]; 

			// if(debug>0) 
			//logger.error('DEBUG::'+scriptName+' delete segment typeof: '+typeof(msg.children()[obrChildIndex-1])+' index: '+[obrChildIndex-1]+' value: '+msg.children()[obrChildIndex-1]);
			// 2023-05-18 JER - msg segment children() the OBR childIndex() remove the prior segment -> ORC segment
			//delete msg.children()[obrChildIndex-1];


		} // end if 

		// per OBR segment push into an overall list to be persisted in channelMap
		lstAllowedObservations.push(allowedObservations);
		lstDisallowedObservations.push(disallowedObservations);
		lstNonDetectedFiltered.push(nonDetectedFiltered);

	} // end for each


	if(lstAllowedObservations.length>0) {
		if(debug>0) logger.error('DEBUG::'+scriptName+' lstAllowedObservations: '+lstAllowedObservations); 
		channelMap.put('arrAllowedObservations',lstAllowedObservations);
	}  // end if 

	if(lstDisallowedObservations.length>0) {
		if(debug>0) logger.error('DEBUG::'+scriptName+' lstDisallowedObservations: '+lstDisallowedObservations);  
		channelMap.put('arrDisallowedObservations',lstDisallowedObservations); 
	} // end if 

	if(lstNonDetectedFiltered.length>0) {
		if(debug>0) logger.error('DEBUG::'+scriptName+' lstNonDetectedFiltered: '+lstNonDetectedFiltered); 
		channelMap.put('arrNonDetectedFiltered',lstNonDetectedFiltered); 
	} // end if 

	channelMap.put('specimenDetected',detected); // true, false, invalid
	channelMap.put('modified_msg',msg);

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' specimenDetected: '+$c('specimenDetected'));
		logger.error('DEBUG::'+scriptName+' msg: '+$c('modified_msg'));
	} // end if 

	return msg;

}

/**
	Desc: Remove observations with Non-Detected Results and mark Detected to be Allowed/Filtered  

	@fileName:	remove_observations_non_detected_results_new.js
	@name:		Remove Observations Non Detected Results (New)
	@authored:	Jeffrey Ritz
	@created:		2023-02-27
	@description:	Remove observations with Non-Detected Results and mark Detected to be Allowed/Filtered

	history:
	2023-04-21 JER - Consider following https://github.com/nextgenhealthcare/connect/discussions/5244
	2023-02-27 JER - Cloned the Remove Observations From Observation Result Codes
*/
function remove_observations_non_detected_results_new(msg) {

	var scriptName= 'remove_observations_non_detected_results_new';
	var debug= 0;
	var nonDetectedFiltered= new Array();
	var nonDetectedFilteredCount= 0;
	var detected= false;
	var i= 1;
	var j= 1;
	var tmp= new XML('<HL7Message></HL7Message>');
	var channelName = Packages.com.mirth.connect.server.controllers.ChannelController.getInstance().getDeployedChannelById(channelId).getName();
	channelMap.put('channelName',channelName);

	for each(seg in msg..OBR) {	

		for each(obx in getSegmentsAfter(msg, msg.children()[seg.childIndex()],'OBX',false,'OBR')) {

			// process each OBX
			var obx31= obx['OBX.3']['OBX.3.1'].toString();
			var obx32= obx['OBX.3']['OBX.3.2'].toString();
			var obx51= obx['OBX.5']['OBX.5.1'].toString();

			logger.error('DEBUG::'+scriptName+' child[OBX.5][OBX.5.2]: '+child['OBX.5']['OBX.5.2'].toString().toUpperCase()+' length: '+child['OBX.5']['OBX.5.2'].toString().length);

			// 2023-02-27 JER - Modified from OBX.5.1 to OBX.5.2
			if(child['OBX.5']['OBX.5.2'].toString().length<1) {
				var curObservationValue=child['OBX.5']['OBX.5.2'].toString().toUpperCase();
			} else {
				var curObservationValue=obx51.toString().toUpperCase();
			} // end if 

			if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] curObservationValue: '+curObservationValue);

			// if(debug>0) 
			logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] typeof: '+typeof(obx31)+' obx31: '+obx31+' obx32: '+obx32+' obx51: '+obx51+' curObservationValue: '+curObservationValue);

			// evaluate if element value is numerical exponential
			if(curObservationValue.toString().indexOf('1.00E')>-1) {
				if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] OBX.5.2 value contains an exponential 1.00E value: '+(curObservationValue.toString().indexOf('1.00E')>-1));
			} // end if 

			// evaluate if element value is number 
			if(parseInt(curObservationValue.toString() !== 'NaN')) {
				if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] OBX.5.2 value contains IS a number: '+parseInt(curObservationValue.toString()!== 'NaN'));
			} // end if 

			// 2022-10-17 JER - Added evaluate if NaN - only 
			if((curObservationValue== 'DETECTED')||(curObservationValue== 'TRUE')||(curObservationValue.toString().indexOf('1.00E')>-1)||parseInt(curObservationValue.toString()!== 'NaN')) {

				detected= true;
				//tmp.appendChild(child);
				// if(debug>0) 
				logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] OBX.5.1 value: '+curObservationValue+' DETECTED: '+detected+' KEEP SEGMENT: '+obx);

			} else {

				nonDetectedFilteredCount++;
				var strNonDetectedFiltered= 'Non-Detected['+nonDetectedFilteredCount+'] Observation CODE: '+obx31+' DESCRIPTION: '+obx32;
				nonDetectedFiltered.push(strNonDetectedFiltered);

				delete msg.children()[obx.childIndex()];

				// if(debug>0) 
				logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] OBX.5.1 value: '+curObservationValue+' NON-DETECTED: '+detected+' REMOVE SEGMENT: '+obx);

			} // end if 	
			j++;

		} // end for 

		if(getSegmentsAfter(msg, msg.children()[seg.childIndex()],'OBX',false,'OBR').length == 0) {
			// if(debug>0) 
			logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] REMOVED related segments DELETE segment: '+msg.children()[seg.childIndex()]);
			delete msg.children()[seg.childIndex()];

		} // end if 

		i++;
	} // end for each

	channelMap.put('specimenDetected',detected); // true, false, invalid
	channelMap.put('modified_msg',tmp);

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' specimenDetected: '+$c('specimenDetected'));
		logger.error('DEBUG::'+scriptName+' tmp: '+$c('modified_msg'));
	} // end if 

	return tmp;

} // end function

/**
	Desc:  Evaluate the OBX versus the observation (OBX) segments with removeNonDetected parameter from the message and set the pathogenDetected channelMap variable.

	fileName:		remove_observations_non_detected_observation_values.js
	name:		Remove Observations Non-Detected Observation Values
	authored:		Jeffrey Ritz
	created:		2023-05-10
	description:	Evaluate the OBX versus the observed result code (OBX-3.4) evaluated against Configuration Setting variable: TNRemoveObservations
	to determine whether to exclude the Observation Results (OBX) segments from the message. 
	history:
	2023-04-18 JER - Cloned the pre-existing remove_observations_from_observation_result_codes 
*/
function remove_observations_non_detected_observation_values(msg, removeNonDetected) {

	var scriptName= 'remove_observations_non_detected_observation_values';
	var debug= 0;
	var detected= false;
	var pathogenDetected= false;

	// allowedObservations
	var allowedObservationsCount= 0;
	var allowedObservations= new Array();

	// removedObservations
	var removedObservationsCount= 0;
	var removedObservations= new Array();
	if(typeof(removeNonDetected)== undefined) var removeNonDetected= 0;

	var i= 1;
	var j= 1;

	if(debug>0) logger.error('DEBUG::'+scriptName+' removeObservations: '+removeObservations); 

	// var tmp= new XML('<HL7Message></HL7Message>');
	var channelName = Packages.com.mirth.connect.server.controllers.ChannelController.getInstance().getDeployedChannelById(channelId).getName();
	channelMap.put('channelName',channelName);


	for each(seg in msg..OBR) {

		var perSegAllowedObservationSegments= 0;

		if(debug>0) logger.error('DEBUG::'+scriptName+' typeof: '+typeof(seg)+' seg['+j+']: '+seg);

		var allowedObservationSegments= 0;
		for each(obx in getSegmentsAfter(msg, msg.children()[seg.childIndex()],'OBX',false,'OBR')) {

			// process each OBX
			var obx21= obx['OBX.2']['OBX.2.1'].toString;
			var obx31= obx['OBX.3']['OBX.3.1'].toString();
			var obx32= obx['OBX.3']['OBX.3.2'].toString();
			var obx34= obx['OBX.3']['OBX.3.4'].toString();
			var obx51= obx['OBX.5']['OBX.5.1'].toString();

			// 2023-02-27 JER - Modified from OBX.5.1 to OBX.5.2
			if(obx['OBX.2']['OBX.2.1'].toString()== 'CWE') {
				var observationCode= obx34;
				var curObservationValue=	obx['OBX.5']['OBX.5.2'].toString().toUpperCase();
			} else {
				var observationCode= obx31;
				var curObservationValue=	obx51.toString().toUpperCase();
			} // end if

			if(debug>0) logger.error('DEBUG::'+scriptName+' OBX['+j+'] observationCode: '+observationCode+' obx31: '+obx31+' obx32: '+obx32+' obx51: '+obx51+' curObservationValue: '+curObservationValue);

			//if(debug>0) { 
				// logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] curObservationValue: '+curObservationValue);
				logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] observationCode: '+observationCode+' removeObservations: '+removeObservations+' EVAL: '+removeObservations.indexOf(observationCode));
			//}


			// 2024-02-24 JER - Modified to leverage the function input parameterized variable orderCodeList to replace hardCoded configuration setting variable
			if(debug>0) logger.error('DEBUG::'+scriptName+' ['+i+'] FOUND Observation Code: '+observationCode+' value: '+curObservationValue+' from the list of codes: '+removeObservations+' evaluation: '+removeObservations.indexOf(observationCode)+' to ADD this observation OBX Segment.'); 

			// 2023-04-26 JER - Evaluate if allowing either Detected or Non-Detected values
			if(removeNonDetected>0) {

				if(curObservationValue.indexOf('NOT DETECTED')>-1) {
					removedObservationsCount++;
					var strObservationCode= observationCode+':'+curObservationValue;
					var strRemovedObservations= 'Filtered Observation['+removedObservationsCount+'] Observation CODE: '+observationCode+' DESCRIPTION: '+obx32+' obx34: '+obx34+' curObservationValue: '+curObservationValue+'\t\r\n';
					removedObservations.push(strObservationCode);

					delete msg.children()[obx.childIndex()];

					// if(debug>0) 
					logger.error('DEBUG::'+scriptName+' REMOVE removable Non-Detected observation CODE: '+observationCode+' removeNonDetected: '+removeNonDetected+' removeObservations['+removedObservationsCount+'] removedObservations:' +strRemovedObservations);
				} else {
					pathogenDetected= true;

					perSegAllowedObservationSegments++;
					allowedObservationsCount++;
					var strObservationCode= observationCode+':'+curObservationValue;
					var strAllowedObservations= 'Allowed Observations['+allowedObservationsCount+'] Observation CODE: '+observationCode+' DESCRIPTION: '+obx32+ 'obx34: '+obx34;
					allowedObservations.push(strObservationCode);

					// if(debug>0) 
					logger.error('DEBUG::'+scriptName+' PERMITTED removable Detected observation CODE: '+observationCode+' removeNonDetected: '+removeNonDetected+' allowedObservations['+allowedObservationsCount+'] allowObservations:' +strAllowedObservations);
				} // end if 

			} // end if 
			j++;

		} // end for 
		i++;

		// 2024-04-26 JER - Modified to evaluate if any allowedObservations are found - if none then remove associated OBR and SPM segments
		if(perSegAllowedObservationSegments<1) {

			var getSegment= getSegmentsAfter(msg, msg.children()[seg.childIndex()],'SPM',true,'OBR');
			if(debug>0) logger.error('TEST::'+scriptName+' SPM getSegment typeof: '+typeof(getSegment)+' index: '+[seg.childIndex()+1]+' value: '+getSegment);
			delete msg.children()[seg.childIndex()+1]; 

			var getSegment= getSegmentsAfter(msg, msg.children()[seg.childIndex()],'OBR',false,'OBR');
			if(debug>0) logger.error('TEST::'+scriptName+' OBR getSegment typeof: '+typeof(getSegment)+' index: '+[seg.childIndex()]+' value: '+getSegment);
			delete msg.children()[seg.childIndex()]; 

		} // end if 

	} // end for each

	channelMap.put('allowedObservations',allowedObservations);
	channelMap.put('removedObservations',removedObservations);

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' allowedObservations: '+allowedObservations);
		logger.error('DEBUG::'+scriptName+' removedObservations: '+removedObservations);
	} // end if 

	channelMap.put('pathogenDetected',pathogenDetected); // true, false, invalid
	channelMap.put('modified_msg',msg);

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' pathogenDetected: '+$c('pathogenDetected'));
		logger.error('DEBUG::'+scriptName+' msg: '+$c('modified_msg'));
	} // end if 

	return msg;
}

/**
	Desc: Remove observations with Non-Detected Results and mark Detected to be Allowed/Filtered  

	@fileName:	remove_observations_non_detected_results.js
	@name:		Remove Observations Non Detected Results
	@authored:	Jeffrey Ritz
	@created:		2023-02-27
	@description:	Remove observations with Non-Detected Results and mark Detected to be Allowed/Filtered

	history:
	2023-02-27 JER - Cloned the Remove Observations From Observation Result Codes
*/
function remove_observations_non_detected_results(msg) {

	var scriptName= 'remove_observations_non_detected_results';
	var debug= 0;
	var detected= false;
	var i= 1;
	var j= 1;
	var tmp= new XML('<HL7Message></HL7Message>');
	var channelName = Packages.com.mirth.connect.server.controllers.ChannelController.getInstance().getDeployedChannelById(channelId).getName();
	channelMap.put('channelName',channelName);

	// for each segment - child per msg.children()
	for each(child in msg.children()) {

		if(debug>0) logger.error('DEBUG::'+scriptName+' typeof: '+typeof(child)+' localName: '+child.localName()+' child: '+child);

		if(child.localName()== 'OBX') {

			// process each OBX
			var obx31= child['OBX.3']['OBX.3.1'].toString();

			if(debug>0) {
				logger.error('DEBUG::'+scriptName+' OBX['+i+'] typeof: '+typeof(obx31)+' obx31: '+obx31);
			}

			// 2023-02-27 JER - Modified from OBX.5.1 to OBX.5.2
			var curObservationValue=child['OBX.5']['OBX.5.2'].toString().toUpperCase();

			// evaluate if element value is numerical exponential
			if(curObservationValue.toString().indexOf('1.00E')>-1) {
				if(debug>0) logger.error('DEBUG::'+scriptName+' determined value contains an exponential 1.00E value: '+(curObservationValue.toString().indexOf('1.00E')>-1));
			}

			// evaluate if element value is number 
			if(parseInt(curObservationValue.toString() !== 'NaN')) {
				if(debug>0) logger.error('DEBUG::'+scriptName+' determined value contains IS a number: '+parseInt(curObservationValue.toString()!== 'NaN'));
			}

			if(debug>0) logger.error('DEBUG::'+scriptName+' OBX['+i+'].5.1 curObservationValue: '+curObservationValue);

			// 2022-10-17 JER - Added evaluate if NaN - only 
			if((curObservationValue== 'DETECTED')||(curObservationValue== 'TRUE')||(curObservationValue.toString().indexOf('1.00E')>-1)||parseInt(curObservationValue.toString()!== 'NaN')) {
				detected= true;
				// tmp appended child object from msg.children()
				// 2023-02-24 JER - Modified to use appendChild E4X denoted XML function
				// tmp+= child;
				if(debug>0) logger.error('DEBUG::'+scriptName+' ['+i+'] determined value CONTAINS: '+curObservationValue+' value - adding this segment.');
				tmp.appendChild(child);
				if(debug>0) logger.error('DEBUG::'+scriptName+' ['+i+'] ADD SEGMENT: '+child.localName());
			} else {
				if(debug>0) logger.error('DEBUG::'+scriptName+' ['+i+'] determined value CONTAINS: '+curObservationValue+' value is a Non-Detected or an Un-expected value - skipping this segment.');
			} // end if 	
			i++;
		} else {
			// 2023-02-24 JER - Modified to use appendChild E4X denoted XML function
			// tmp+= child;
			tmp.appendChild(child);
			if(debug>0) logger.error('DEBUG::'+scriptName+' ['+i+'] ADD SEGMENT: '+child.localName());
		} // end for
		if(debug>0) logger.error('DEBUG::'+scriptName+' tmp['+j+'] contains: '+tmp);
		j++;
	} // end for

	channelMap.put('specimenDetected',detected); // true, false, invalid
	channelMap.put('modified_msg',tmp);

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' specimenDetected: '+$c('specimenDetected'));
		logger.error('DEBUG::'+scriptName+' tmp: '+$c('modified_msg'));
	} // end if 

	return tmp;
}

/**
Desc:  Evaluate the OBX versus the observed result code (OBX-3.1) evaluated against Configuration Setting variable: ADPHNotifiablePathogens to determine whether to exclude the Observation Results (OBX) segments from the message and set the pathogenDetected channelMap variable.

	fileName:		remove_observations_using_observation_result_codes.js
	name:			Remove Observations Using Observation Result Codes
	@authored:		Jeffrey Ritz
	@created:		2023-02-27
	@description:	Remove observations with Non-Detected Results and mark Detected to be Allowed/Filtered

	history:
	2023-07-27 JER - Modified to include isCovid boolean argument to determine how to set the observationCode and observationValue
	2023-04-26 JER - Modified to determine if should evaluate detectable or not
	2023-04-21 JER - Consider following https://github.com/nextgenhealthcare/connect/discussions/5244
	2023-02-27 JER - Cloned the Remove Observations From Observation Result Codes
*/
function remove_observations_using_observation_result_codes(msg, allowedObservationCodeList, isCovid) {

	var scriptName= 'remove_observations_using_observation_result_codes';
	var debug= 0;
	var detected= false;

	// Disallowed Observations Filtered List
	var disallowedObservationsCount= 0;
	var disallowedObservations= new Array();

	// Allowed Observations Filtered List
	var allowedObservationsCount= 0;
	var allowedObservations= new Array();

	var i= 1;
	var j= 1;
	var observationsFiltered = new Array();
	var observationsFilteredCount= 0;
	// var tmp= new XML('<HL7Message></HL7Message>');
	var channelName = Packages.com.mirth.connect.server.controllers.ChannelController.getInstance().getDeployedChannelById(channelId).getName();
	channelMap.put('channelName',channelName);

	if(debug>0) logger.error('DEBUG::'+channelName+':'+scriptName+' allowedObservationCodeList: '+allowedObservationCodeList);

	for each(seg in msg..OBR) {	

		if(debug>0) logger.error('TEST::'+scriptName+' seg['+j+']: '+seg);

		var allowedObservationSegments= 0;
		for each(obx in getSegmentsAfter(msg, msg.children()[seg.childIndex()],'OBX',false,'OBR')) {

			if(debug>0) logger.error('DEBUG::'+channelName+':'+scriptName+' obx: '+obx);

			var obx32= obx['OBX.3']['OBX.3.2'].toString();
			var obx51= obx['OBX.5']['OBX.5.1'].toString();

			if(typeof(isCovid)!== undefined) {
				if(isCovid== true) {

					var obx31= obx['OBX.3']['OBX.3.1'].toString();
					var observationCode=  obx31;
					var observationValue= obx['OBX.5']['OBX.5.2'].toString().toUpperCase();

				} else {
					// process each OBX
					if(obx['OBX.2']['OBX.2.1'].toString()== 'CWE') {
						var obx34= obx['OBX.3']['OBX.3.4'].toString();
						var observationCode=  obx34;
						var observationValue= obx['OBX.5']['OBX.5.2'].toString().toUpperCase();
					} else {
						var obx31= obx['OBX.3']['OBX.3.1'].toString();
						var observationCode=  obx31;
						var observationValue= obx51.toString().toUpperCase();
					} // end if
				} // end if 

			} else {
				// process each OBX
				if(obx['OBX.2']['OBX.2.1'].toString()== 'CWE') {
					var obx34= obx['OBX.3']['OBX.3.4'].toString();
					var observationCode=  obx34;
					var observationValue= obx['OBX.5']['OBX.5.2'].toString().toUpperCase();
				} else {
					var obx31= obx['OBX.3']['OBX.3.1'].toString();
					var observationCode=  obx31;
					var observationValue= obx51.toString().toUpperCase();
				} // end if
			} // end if 

			if(debug>0) logger.error('DEBUG::'+scriptName+' OBX['+j+'] observationCode: '+observationCode+' observationValue: '+observationValue+' Eval: '+allowedObservationCodeList.indexOf(observationCode)+' allowedObservationCodeList: '+allowedObservationCodeList);

			// 2024-02-24 JER - Modified to leverage the function input parameterized variable orderCodeList to replace hardCoded configuration setting variable
			if(allowedObservationCodeList.indexOf(observationCode)>-1) {	

				allowedObservationsCount++;
				detected= true;
				var logText= 'observationCode: '+observationCode+' observationValue: '+observationValue;
				var strAllowedObservation= 'Allowed['+allowedObservationsCount+'] '+logText;
				allowedObservations.push(strAllowedObservation);
				allowedObservationSegments++;

				if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] ALLOWED logText: '+logText+' DETECTED: '+detected+' KEEP SEGMENT: '+obx);

			} else {

				disallowedObservationsCount++;
				var logText= 'observationCode: '+observationCode+' observationValue: '+observationValue;
				var strDisallowedObservation= 'Disallowed['+disallowedObservationsCount+'] '+logText;
				disallowedObservations.push(strDisallowedObservation);

				delete msg.children()[obx.childIndex()];

				if(debug>0) logger.error('DEBUG::'+scriptName+' OBR['+i+']OBX['+j+'] DISALLOWED logText: '+logText+' DETECTED: '+detected+' REMOVE SEGMENT: '+obx);

			} // end if 
			j++;

		} // end for 
		i++;

		if(debug>0) logger.error('DEBUG::'+scriptName+' allowedObservationSegments: '+allowedObservationSegments);

		// 2024-04-26 JER - Modified to evaluate if any allowedObservations are found - if none then remove associated OBR and SPM segments
		if(allowedObservationSegments<1) {

			var getSegment= getSegmentsAfter(msg, msg.children()[seg.childIndex()],'SPM',true,'OBR');
			if(debug>0) logger.error('TEST::'+scriptName+' SPM getSegment typeof: '+typeof(getSegment)+' index: '+[seg.childIndex()+1]+' value: '+getSegment);
			delete msg.children()[seg.childIndex()+1]; 

			var getSegment= getSegmentsAfter(msg, msg.children()[seg.childIndex()],'OBR',false,'OBR');
			if(debug>0) logger.error('TEST::'+scriptName+' OBR getSegment typeof: '+typeof(getSegment)+' index: '+[seg.childIndex()]+' value: '+getSegment);
			delete msg.children()[seg.childIndex()]; 

		} // end if 

	} // end for each

	channelMap.put('specimenDetected',detected); // true, false, invalid
	channelMap.put('modified_msg',msg);

	channelMap.put('obsvDisallowedObservations',disallowedObservations);
	channelMap.put('obsvAllowedObservations',allowedObservations);


	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' specimenDetected: '+$c('specimenDetected'));
		logger.error('DEBUG::'+scriptName+' msg: '+$c('modified_msg'));
	} // end if 

	return msg;

} // end function

/**
	This script is used to write a message to a local system destination folder

	@param {String} arg1 - arg1 description
	@return {String} return description
	e.g., write_msg_to_disk($('rawhl7'),api_error_directory,$('ark_uuid'));
	History:
	2022-10-28 JER - Modified to make destinationFile String() and then modify FileUtils.write using java.lang.String, boolean, java.lang.String corresponding to 
	public static void write(java.lang.String fileName,boolean append,java.lang.String data)
	https://javadocs.mirthcorp.com/connect/3.12.0/user-api/com/mirth/connect/server/userutil/FileUtil.html
*/
function write_msg_to_disk(msg, destinationFileDirectory, uuid) {

	var scriptName= 'write_msg_to_disk';
	var debug= 0;

	try {
		var destinationFilename= uuid+'.err';
		var error_hl7_msg= msg;
		var destinationFile= destinationFileDirectory+'\\'+destinationFilename;

		if(debug>0) {
			logger.error('DEBUG::'+scriptName+' destinationFileDirectory: '+destinationFileDirectory);
			logger.error('DEBUG::'+scriptName+' destinationFilename: '+destinationFilename);
			logger.error('DEBUG::'+scriptName+' error_hl7_msg: '+error_hl7_msg);
		} // end if 

		// org.apache.commons.io.FileUtils.write(new java.io.File(destinationFileDirectory+'\\'+destinationFilename),false,error_hl7_msg);
		// org.apache.commons.io.FileUtils.write(String(destinationFile),boolean(false),String(error_hl7_msg));
		FileUtil.write(java.lang.String(destinationFile),false,java.lang.String(error_hl7_msg));

		return true;

	} catch (e) {
		logger.error('DEBUG::'+scriptName+' ERROR: '+e);
		return false;
	} // end try
}

/**
	This script is used to write a message to a destination file providing both a destination folder and the fileName

	@param {String} args: 	msg - java.lang.String
							destinationFilename - java.lang.String
							destinationFileDirectory - java.lang.stringify
							append - java.lang.Boolean
							debug - java.lang.Boolean
	@return {boolean} - 	java.lang.boolean;  FileUtil.write success (return true|false)
	Desc:   If append parameter is not provided or has no value the default action is to overwrite the provided file.
	e.g., write_msg_to_file($('raw'),destinationFilename, destinationFileDirectory);
	History:
	2022-12-06 JER - Modified to include the append and debug parameter arguments to designate whether to overwrite (append= false) or append (append= true) or required to leverage debug statements (debug= true|false)
	2022-10-28 JER - Modified to make destinationFile String() and then modify FileUtils.write using java.lang.String, boolean, java.lang.String corresponding to 
	public static void write(java.lang.String fileName,boolean append,java.lang.String data)
	http://javadocs.mirthcorp.com/connect/3.9.0/user-api/com/mirth/connect/server/userutil/FileUtil.html
*/
function write_msg_to_file(msg, destinationFilename, destinationFileDirectory, append, debug) {

	var scriptName= 'write_msg_to_file';
	if((typeof(debug)== 'undefined')||(debug.toString.length<1)) {
		var debug= false;
	}
	if((typeof(append)== 'undefined')||(append.toString().length<1)) {
		var append= false;  // default overwrite
	} // end if 

	try {
		// var destinationFilename= uuid+'.err';
		var error_msg= msg;
		var destinationFile= destinationFileDirectory+'\\'+destinationFilename;

		if(debug>0) {
			logger.error('DEBUG::'+scriptName+' destinationFileDirectory: '+destinationFileDirectory);
			logger.error('DEBUG::'+scriptName+' destinationFilename: '+destinationFilename);
			logger.error('DEBUG::'+scriptName+' error_msg: '+error_msg);
			logger.error('DEBUG::'+scriptName+' append: '+append);
		} // end if 

		// org.apache.commons.io.FileUtils.write(new java.io.File(destinationFileDirectory+'\\'+destinationFilename),false,error_msg);
		// org.apache.commons.io.FileUtils.write(String(destinationFile),boolean(false),String(error_msg));

		// 2022-12-07 JER - Modified to leverage the append parameterized variable
		// com.mirth.connect.server.userutil.FileUtil.write(java.lang.String(destinationFile),false,java.lang.String(error_msg));
		FileUtil.write(java.lang.String(destinationFile),append,java.lang.String(error_msg));
		return true;

	} catch (e) {
		logger.error('DEBUG::'+scriptName+' ERROR: '+e);
		return false;
	} // end try
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.
	@scriptName= convert_hl7_to_extended_address_object
	@description= Convert HL7 to Extended Address XAD Object
	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function convert_hl7_to_extended_address_object(hl7_xad_xml) {

	var scriptName= 'convert_hl7_to_extended_address_object';
	var debug= 0;

	if(debug>0) logger.error('DEBUG::'+channelName+':'+scriptName+' hl7_xad_xml typeof: '+typeof(hl7_xad_xml)+' value: '+hl7_xad_xml);

	var objExtendedAddress= new Object();
	var i= 0;

	for each(child in hl7_xad_xml.children()) {

		if(i== 0) {
			if(child.toString().length>0) {
				objExtendedAddress.streetAddress=			child.toString();
			} else {
				objExtendedAddress.streetAddress=			'';
			} // end if 
		} // end if 

		if(i== 1) {
			if(child.toString().length>0) {
				objExtendedAddress.otherDesignation=  		child.toString();
