	Desc:  this JS code template is to return JSON string per a field value provided as argument

	scriptName: get_4ps_plus_field_lookup.js
	@param {String}  field - This field argument is a string (e.g. LBW_PRI)
	@return {String} jsonString - This returned value is a jsonString
	History:
	2022-09-21 JER - Modified to leverage pra_equivalent_field and pra_equivalent_value variables
	2022-09-19 JER - Modified from signal element variable (field) and array of values
	2022-09-04 JER - Copy from the javascript: get_pregnancy_risk_factors_field_lookup.js
*/
function get_4ps_plus_field_lookup(arrField) {

	var scriptName= 'get_4ps_plus_field_lookup';
	// complete list of Pregnancy Risk Factors fields configured from Global Channel Map - in Deploy script
	var in4psPlus= globalChannelMap.get('pra4psPlus');
	var debug= 0;

	if(debug<0) { 
		logger.error('DEBUG::'+scriptName+' argument field: '+arrField+' typeof: '+typeof(arrField)+' length: '+arrField.length); 
	}

	if(typeof(arrField) !== 'undefined') {
		// if(arrField.length()>0) {
		if(arrField.length>0) {
			// verify in4psPlus is defined and has a valid value for evaluation
			if(debug>0) { 
				logger.error('DEBUG::'+scriptName+' argument arrField typeof= '+typeof(arrField)+' arrField= '+JSON.stringify(arrField)+' length= '+arrField.length());
				logger.error('DEBUG::'+scriptName+' argument in4psPlus typeof= '+typeof(in4psPlus)+' in4psPlus= '+in4psPlus+' toString()= '+in4psPlus.toString()+' length= '+in4psPlus.toString().length);
			}
			if((typeof(in4psPlus) !== 'undefined')&&(in4psPlus.toString().length>0)) {
				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' EVAL in4psPlus.toString.indexOf(field.toString)= '+in4psPlus.toString().indexOf(field.toString()));
				}
				// 2022-08-04 JER - Modified from in4psPlus to field in the jsonString return argument
				// 2022-09-21 JER - Check and verify the arrField array has the required element fields and values
				if((typeof(arrField) !== 'undefined')&&(arrField.length>0)) {
					if(arrField[0]) { var pra_equivalent_json_section= arrField[0].toString(); } else { var pra_equivalent_json_section= ''; } 
					if(arrField[1]) { var pra_equivalent_field= arrField[1].toString(); } else { var pra_equivalent_field= ''; } 
					if(arrField[4]) { var pra_equivalent_value= arrField[4].toString(); } else { var pra_equivalent_value= ''; } 
				}
				// 2022-09-21 JER - Modified to leverage pra_equivalent_field and pra_equivalent_value variables
				if(in4psPlus.toString().indexOf(pra_equivalent_field)>-1) {
					return "'"+pra_equivalent_field+"': '"+pra_equivalent_value+"'";
				} else {
					return "'"+pra_equivalent_field+"': ''";
				}// end if 
			} // end if
		} // end if
	} // end if 
} // end function

