	2022-09-19 JER - Modified single field to an array (arrField)
	2022-09-04 JER - Copy from the javascript: get_pregnancy_risk_factors_field_lookup.js
*/
function get_ethnicity_field_lookup(strField) {

	var scriptName= 'get_ethnicity_field_lookup';
	// complete list of Ethnicity fields configured from Global Channel Map - in Deploy script
	var inEthnicity= globalChannelMap.get('praEthnicity');
	var inEthnicity= String(inEthnicity);
	var arrField=    strField.split('|');
	var debug= 0;

	if(debug<0) { 
	 	// arrField: ethnicity|Hispanic|4|Not Hispanic or Latino|N typeof: string length: 45
		logger.error('DEBUG::'+scriptName+' A argument arrField: '+arrField+' typeof: '+typeof(arrField)+' length: '+arrField.length); 
		logger.error('DEBUG::'+scriptName+' A.1 (typeof(arrField)!== undefined): '+(typeof(arrField)!== 'undefined'));
		logger.error('DEBUG::'+scriptName+' A.2 (arrField.length>0): '+(arrField.length>0));
	} // end if 

	if((typeof(arrField)!== 'undefined')&&(arrField.length>0)) {
		// verify inEthnicity is defined and has a valid value for evaluation
		if(debug<0) { 
			logger.error('DEBUG::'+scriptName+' B argument inEthnicity typeof= '+typeof(inEthnicity)+' inEthnicity= '+inEthnicity+' toString()= '+inEthnicity.toString()+' length= '+inEthnicity.toString().length);
		} // end if 

		// 2022-09-20 JER - Check and verify the arrField array has the required element fields and values
		if(arrField[0]) { var pra_equivalent_json_section= arrField[0].toString(); } else { var pra_equivalent_json_section= ''; } 
		if(arrField[1]) { var pra_equivalent_field= arrField[1].toString(); } else { var pra_equivalent_field= ''; } 
		if(arrField[4]) { var pra_equivalent_value= arrField[4].toString(); } else { var pra_equivalent_value= ''; } 

		if(debug<0) { 
			logger.error('DEBUG::'+scriptName+' C.1 pra_equivalent_json_section: '+pra_equivalent_json_section);
			logger.error('DEBUG::'+scriptName+' C.2 pra_equivalent_field: '+pra_equivalent_field);
			logger.error('DEBUG::'+scriptName+' C.3 pra_equivalent_value: '+pra_equivalent_value);
		} // end if 

		if((typeof(inEthnicity) !== 'undefined')&&(inEthnicity.length>0)) {
			if(debug<0) { 
				logger.error('DEBUG::'+scriptName+' D.1 EVAL inEthnicity= '+inEthnicity);
				logger.error('DEBUG::'+scriptName+' D.2 EVAL pra_equivalent_field= '+pra_equivalent_field);
				logger.error('DEBUG::'+scriptName+' D.3 EVAL '+inEthnicity+'.indexOf('+pra_equivalent_field+')= '+inEthnicity.indexOf(pra_equivalent_field));
			} // end if 
			// 2022-08-04 JER - Modified from inEthnicity to field in the jsonString return argument
			if(inEthnicity.indexOf(pra_equivalent_field)>-1) {
				return "'"+pra_equivalent_field+"': '"+pra_equivalent_value+"'";
			} else {
				return "'"+pra_equivalent_field+"': ''";
			} // end if 
		} // end if
	} // end if 
} // end function

