/**
	Desc:  this JS code template is to return observation value string per requested HTML referenced in CCDA

	fileName:		get_html_referenced_value.js
	name:		Get HTML Referenced Text Value
	authored:		Jeffrey Ritz
	created:		2022-09-13
	description:	Evaluate HTML Table Referenced Text
	history: 
	2022-09-14 JER - Modified from evaluating the msg to the specified section to eliminate the multiple executions 
*/
function get_html_referenced_text(section, htmlReference) {

	var scriptName= 'get_html_referenced_text'
	var debug= 0;
	var cntSection= 0;
	var cntTable= 0;
	var cntColumn= 0;

	var strHtmlReference= htmlReference.toString().replace('#','');
	var referencedValue= '';

	if(debug>0) { 
		logger.error('DEBUG::'+scriptName+' htmlReference: '+htmlReference+' strHtmlReference: '+strHtmlReference); 
	} // end if 

	if(typeof(section)!== 'undefined') {

		if(debug>0) {
			logger.error('DEBUG::'+scriptName+' SECTION length= '+section.children().length()+' children= '+section.children());
			logger.error('DEBUG::'+scriptName+' SECTION section= '+section);
		} // end if 

		if(section.children().length()>0) {
			for each(table in section..*['table']) {

				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' section table['+cntTable+']: '+table); 
				} // end if 

				for each(column in table..*['td']) {

					if(debug>0) {
						logger.error('DEBUG::'+scriptName+' section table['+cntTable+'] column['+cntColumn+'].@ID: '+column.@ID+' column value: '+column);
					} // end if 

					if(typeof(column.ID) !== 'undefined') {

						if(debug>0) {
							logger.error('DEBUG::'+scriptName+' column.ID= '+column.@ID);
						} // end if 
						var columnID= column.@ID.toString();

						if(columnID.toString().length>0) {
							if(debug>0) {
								logger.error('DEBUG::'+scriptName+' COLUMN columnID: '+columnID+' strHtmlReference: '+strHtmlReference+' ('+columnID+'== '+strHtmlReference+'):' +(columnID== strHtmlReference));
							} // end if 
							// if(columnID.indexOf(strHtmlReference)>-1) {
							if(columnID== strHtmlReference) {
								var referencedValue= column.toString();
								if(debug>0) {
									logger.error('DEBUG::'+scriptName+' OBSERVATION SET referencedValue= '+referencedValue);
								} //end if 
							} // end if
						} // end if 

					} // end if
					cntColumn++;

				} // end for each
				cntTable++;
			} // end for each
		} // end if 
	} // end if
	return referencedValue;
} // end function

