/**
	Desc:  this JS code template is to return observation value string per a field value provided as argument

	scriptName: 	get_observation_value.js
	description:	Get Observation Value
	created:		2022-08-17 JER
	@param {String}  praObservation - This field argument is a string (e.g. 'physicalAssessment,DiastolicBloodPressure,10,Diastolic Blood Pressure,')
	@return {String} strObservation - This returned string value (e.g.  106 mm[Hg])
	History:
	2022-09-07 JER - Modified to evaluate the observation['type'] to determine (PQ, TS, etc.) if Timestamp or Observation Quantity 
	2022-08-29 JER - Modified to remove [] open/closed brackets from lstPraObservation[4] array element
	2022-08-19 JER - Modified to incorporate the evaluation of the observation/value and to reference concatenated value and unit 

	E.G. {
		"id": 67,
		"templateId": "2.16.840.1.113883.10.20.22.4.27",
		"extension": "5722403880-sysBP-Z2918157",
		"root": "1.2.840.114350.1.13.225.2.7.1.2109.1",
		...
		"text": "#diaBP_5722403880",
		"value": {
			"value": "58",
			"unit": "mm[Hg]",
			"type": "PQ"
		}

*/
function get_observation_value(praObservation) {

	var scriptName= 'get_observation_value';
	// complete list of observation fields configured from Global Channel Map - in Deploy script
	var arrObservations= channelMap.get('arrObservations');
	var debug= 0;

	if(debug<0) { 
		logger.error('DEBUG::'+scriptName+' provided argument: '+praObservation); 
	}

	// "physicalAssessment,DiastolicBloodPressure,10,Diastolic Blood Pressure,"
	var lstPraObservation= praObservation.toString().split(',');
	var pra_equivalent_section=		lstPraObservation[0];
	var pra_equivalent_field=		lstPraObservation[1];
	var pra_equivalent_section_num=	lstPraObservation[2];
	var code_display_name=			lstPraObservation[3];
	// 2022-08-29 JER - Modified to remove [] open/closed brackets from lstPraObservation[4] array element
	var code_value_description=		lstPraObservation[4].toString().replace(/\]/g,'').replace(/\[/g,'');;

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' pra_equivalent_section: '+pra_equivalent_section+' pra_equivalent_field: '+pra_equivalent_field+' pra_equivalent_section_num: '+pra_equivalent_section_num+' code_display_name: '+code_display_name+' code_value_description: '+code_value_description);
		logger.error('DEBUG::'+scriptName+' arrObservations: '+JSON.stringify(arrObservations));
	} // end if 

	var i= 0;
	for each(observation in arrObservations) {
		var strObservation= JSON.stringify(observation);
		if(debug>0) {
			logger.error('DEBUG::'+scriptName+' observation['+i+'] JSON.stringify: ' +strObservation);
			logger.error('DEBUG::'+scriptName+' observation['+i+']: code_value_description: '+code_value_description+' comparison strObservation.indexOf(code_value_description): '+strObservation.indexOf(code_value_description));
		} // end if 
		if(strObservation.indexOf(code_value_description)>-1) {
			if(debug>0) {
				logger.error('DEBUG::'+scriptName+' observation['+i+'] FOUND code_value_description: '+code_value_description);
				logger.error('DEBUG::'+scriptName+' typeof(observation[value]): '+typeof(observation['value'])+' observation[value]: '+JSON.stringify(observation['value']));
			} // end if
			// observation[value]: {"value":"106","unit":"mm[Hg]","type":"PQ"}
			var jsonObservationCode= observation['code'];
			strObservationCode= jsonObservationCode.code;
			// var jsonObservationText=  observation['text'].toString().replace(/\#/,'');
			var jsonObservationValue= observation['value'];
			strObservationValue= jsonObservationValue.value;
			var jsonObservationType= observation['type'];
			strObservationType=  jsonObservationValue.type;

			// 2022-09-07 JER - Need to evaluate the observation type (e.g PQ, TS, etc)
			if(strObservationType== 'PQ') {

				var strObservationUnit=  jsonObservationValue.unit;

				if(debug>0) {
					logger.error('DEBUG::'+scriptName+' type=PQ jsonObservationValue: '+JSON.stringify(jsonObservationValue));
					logger.error('DEBUG::'+scriptName+' type=PQ jsonObservationValue.value: '+jsonObservationValue.value); // 1096
					logger.error('DEBUG::'+scriptName+' type=PQ jsonObservationValue.unit: '+strObservationUnit);  // mm[Hg]
				}
				var strObservationValue= jsonObservationValue.value+' '+strObservationUnit;
			} else if(strObservationType== 'TS') {
				if(debug>0) {
					logger.error('DEBUG::'+scriptName+' type=TS jsonObservationValue: '+JSON.stringify(jsonObservationValue));
					logger.error('DEBUG::'+scriptName+' type=TS jsonObservationValue.value: '+jsonObservationValue.value); // 1096
				} // end if 
				var strObservationValue= jsonObservationValue.value;
			} // end if 

			if(debug>0) {
				logger.error('DEBUG::'+scriptName+' typeof(strObservationValue): '+typeof(strObservationValue)+' strObservationValue: '+strObservationValue);
			} // end if 
			// channelMap.put(strObservationCode,strObservationValue);
			return strObservationValue;
		} // end if
		i++;
	}
	return;
}

/**
	Desc:  this JS code template is to return JSON string per a field value provided as argument

	scriptName: get_perinatal_history_field_lookup.js
	@param {String}  field - This field argument is a string (e.g. LBW_PRI)
	@return {String} jsonString - This returned value is a jsonString
	History:
	2022-09-21 JER - Modified default no answer from 'N' to ''
	2022-08-04 JER - Modified from using inPerinatalHistory to field variables
*/
function get_perinatal_history_field_lookup(field) {

	var scriptName= 'get_perinatal_history_field_lookup';
	// complete list of Perinatal History fields configured from Global Channel Map - in Deploy script
	var inPerinatalHistory= globalChannelMap.get('praPerinatalHistory');
	var debug= 0;

	if(debug<0) { 
		logger.error('DEBUG::'+scriptName+' argument field: '+field); 
	}

	if(typeof(field) !== 'undefined') {
		if(field.toString().length>0) {
			// verify inPerinatalHistory is defined and has a valid value for evaluation
			if(debug<0) { 
				logger.error('DEBUG::'+scriptName+' argument field typeof= '+typeof(field)+' field= '+field+' length= '+field.length);
				logger.error('DEBUG::'+scriptName+' argument inPerinatalHistory typeof= '+typeof(inPerinatalHistory)+' inPerinatalHistory= '+inPerinatalHistory+' toString()= '+inPerinatalHistory.toString()+' length= '+inPerinatalHistory.toString().length+' EVAL inPerinatalHistory.toString.indexOf(field.toString)= '+inPerinatalHistory.toString().indexOf(field.toString()));
			}
			if((typeof(inPerinatalHistory) !== 'undefined')&&(inPerinatalHistory.toString().length>0)) {
				// 2022-08-04 JER - Modified from using inPerinatalHistory to field variables
				if(inPerinatalHistory.toString().indexOf(field.toString())>-1) {
					return jsonString= "'"+field+"': 'Y'";
				} else {
					return jsonString= "'"+field+"': ''";
				}// end if 
			} // end if
		} // end if
	} // end if 
} // end function

