/**
	Desc:  this JS code template is to return JSON string per a field value provided as argument

	scriptName: get_smoking_tobacco_use_field_lookup.js
	@param {String}  field - This field argument is a string (e.g. LBW_PRI)
	@return {String} jsonString - This returned value is a jsonString
	History:
	2022-09-21 JER - Modified default no answer from 'N' to ''
	2022-09-14 JER - Modified the comma (,) to pipe (|)
	2022-08-30 JER - Modified input argument from field to observation object (e.g. [smokingTobaccoUse,cigarettes,17,N,Never smoked tobacco (finding)])
	2022-08-03 JER - Modified from inSmokingTobaccoUse to field in the jsonString return argument
*/
function get_smoking_tobacco_use_field_lookup(observation) {

	var scriptName= 'get_smoking_tobacco_use_field_lookup';
	// complete list of Smoking Tobacco Use fields configured from Global Channel Map - in Deploy script
	var inSmokingTobaccoUse= globalChannelMap.get('praSmokingTobaccoUse');
	var debug= 0;

	if(debug<0) { 
		logger.error('DEBUG::'+scriptName+' argument field: '+field); 
	}

	// 2022-09-14 JER - Modified the comma (,) to pipe (|)
	var lstObservation= observation.split('|');
	// 2022-08-30 JER - Get the PRA equivalent field from observation
	var field= lstObservation[1].toString();
	var value= lstObservation[3].toString();

	if(typeof(field) !== 'undefined') {
		if(field.toString().length>0) {
			// verify inSmokingTobaccoUse is defined and has a valid value for evaluation
			// 2022-08-30 JER - Modify inSmokingTobaccoUse from array object to string
			var strInSmokingTobaccoUse= JSON.stringify(inSmokingTobaccoUse);
			if(debug<0) { 
				logger.error('DEBUG::'+scriptName+' argument field typeof= '+typeof(field)+' field= '+field+' length= '+field.length);
				logger.error('DEBUG::'+scriptName+' argument strInSmokingTobaccoUse typeof= '+typeof(strInSmokingTobaccoUse)+' strInSmokingTobaccoUse= '+strInSmokingTobaccoUse+' length= '+strInSmokingTobaccoUse.toString().length+' EVAL '+strInSmokingTobaccoUse+'.indexOf('+field+')= '+inSmokingTobaccoUse.toString().indexOf(field.toString()));
			} // end if 
			if((typeof(strInSmokingTobaccoUse) !== 'undefined')&&(strInSmokingTobaccoUse.length>0)) {
				// 2022-08-30 JER - Modify the if-condition to evaluate if the PRA fields inSmokingTobaccoUse exists in field
				// if(inSmokingTobaccoUse.toString().indexOf(field.toString())>-1) {
				if(strInSmokingTobaccoUse.indexOf(field)>-1) {
				// 2022-08-03 JER - Modified from inSmokingTobaccoUse to field in the jsonString return argument
					if(value.toString().length>0) {
						return jsonString= "'"+field+"': '"+value+"'";
					} else {
						return jsonString= "'"+field+"': ''";
					} // end if 
				/*
					} else {
						return jsonString= "'"+field+"': ''";
					}// end if
				*/
				} // end if 
			} // end if
		} // end if
	} // end if 
} // end function

/**
	Desc:  this JS code template is to return JSON string per a field value provided as argument
	scriptName: set_4ps_plus_field_lookup.js
	@param {object}  field - This field argument is an array object (e.g. {'LBW_PRI': 'Y','Smoking': 'Y'})
	@return {object} jsonString - This returns a resulting object using the arrJSONString
	History:
	2022-09-20 JER - Modified added to distinguish between str4psPlus element name and value
*/
function set_4ps_plus_field_lookup(arrObservations) {

	var scriptName= 'set_4ps_plus_field_lookup';
	var debug= 0;
	// complete list of Pregnancy Risk Factors configured from Global Channel Map - in Deploy script
	var in4psPlus= globalChannelMap.get('pra4psPlus');
	// output list of all Pregnancy Risk Factors for JSON
	var out4psPlus= {};

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' arrObservation typeof: '+typeof(arrObservations)+' arrObservations: '+arrObservations);
	} // end if 

	if((typeof(in4psPlus) !== 'undefined')&&(in4psPlus.length)>0) {

		if(debug<0) { 
			logger.error('DEBUG::'+scriptName+' A argument arrObservation typeof= '+typeof(arrObservations)+' arrObservations= '+arrObservations+' length= '+arrObservations.length); 
		}

		for(var i= 0;i<in4psPlus.length;i++) {

			// set the in4psPlus element toString()
			var str4psPlus= in4psPlus[i].toString();

			// set the arrObservation array into toString() to be evaluated
			var strObservations= JSON.stringify(arrObservations);
			if(debug>0) { 
				logger.error('DEBUG::'+scriptName+' B1 strObservations typeof= '+typeof(strObservations)+' length= '+strObservations.length+' strObservations= '+strObservations);
			} // end if 

			// arrObservations object contains current list of found Pregnancy Risk Factors from CCDA
			if((typeof(strObservations) !== 'undefined')&&(strObservations.length>0)) {

				// 2022-09-20 JER - Modified added to distinguish between str4psPlus element name and value
				var first4psPlusElement= strObservations.split(':')[0].replace(/[^a-z0-9]/gi, '');
				var first4psPlusValue=   strObservations.split(':')[1].trim().replace(/[^a-z0-9]/gi, '');
				if(debug>0) { 
					// logger.error('DEBUG::'+scriptName+' B.1 str4psPlus= '+str4psPlus+' length: '+str4psPlus.length);
					logger.error('DEBUG::'+scriptName+' B.1 first4psPlusElement= '+first4psPlusElement);
					logger.error('DEBUG::'+scriptName+' B.2 first4psPlusValue= '+first4psPlusValue);
				}

				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' C EVALUATE str4psPlus= '+str4psPlus.toUpperCase()+' VS strObservations= '+strObservations.toUpperCase()+' EVAL= '+strObservations.toUpperCase().indexOf(str4psPlus.toUpperCase()));
				}

				// "'HAVEYOUEVERDRUNKALCOHOL': 'N'"
				if(strObservations.toUpperCase().indexOf(str4psPlus.toUpperCase())>-1) {
					// 2022-07-27 JER - Modified to accommodate out4psPlus as Object()
					out4psPlus[first4psPlusElement]= first4psPlusValue;
					if(debug>0) { 
						logger.error('DEBUG::'+scriptName+' D1 SET out4psPlus['+str4psPlus+']= '+out4psPlus[str4psPlus]);
					}
				} else {
					out4psPlus[first4psPlusElement]= '';
					if(debug>0) { 
						logger.error('DEBUG::'+scriptName+' D2 SET out4psPlus['+str4psPlus+']= '+out4psPlus[str4psPlus]);
					}
				} // end if
			} else {
				out4psPlus[first4psPlusElement]= '';
				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' D3 SET out4psPlus['+str4psPlus+']= '+out4psPlus[str4psPlus]);
				} // end if 	
			}
		} // end for
		channelMap.put('out4psPlus',out4psPlus);
	} else {
		logger.error('DEBUG::'+scriptName+' E in4psPlus has no value or is "undefined".');
	}// end if
	if(debug>0) { 
		logger.error('DEBUG::'+scriptName+' F typeof(out4psPlus)= '+typeof(out4psPlus)+' toString= '+JSON.stringify(out4psPlus)+' length= '+JSON.stringify(out4psPlus).length); 
	}
} // end function

/**
	Desc:  this JS code template is to return JSON string per a field value provided as argument

	scriptName: set_4ps_plus_followup_questions_field_lookup.js
	@param {object}  field - This field argument is an array object (e.g. {'LBW_PRI': 'Y','Smoking': 'Y'})
	@return {object} jsonString - This returns a resulting object using the arrJSONString
	History:
*/
function set_4ps_plus_followup_questions_field_lookup(arrObservations) {

	var scriptName= 'set_4ps_plus_followup_questions_field_lookup';
	var debug= 0;
	// complete list of4ps Plus Followup Questions configured from Global Channel Map - in Deploy script
	var in4psPlusFollowupQuestions= globalChannelMap.get('pra4psPlusFollowupQuestions');
	// output list of all4ps Plus Followup Questions for JSON
	var out4psPlusFollowupQuestions= {};
	var outString;

	if(debug<0) { 
		logger.error('DEBUG::'+scriptName+' argument field typeof= '+typeof(arrObservations)+' arrObservations= '+arrObservations+' length= '+arrObservations.length); 
	}

	// arrObservations object contains current list of found4ps Plus Followup Questions from CCDA
	if(typeof(arrObservations) !== 'undefined') {
		if(arrObservations.toString().length>0) {
			for(var j= 0;j<arrObservations.length;j++) {
				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' typeof= '+typeof(arrObservations[j])+' observation= '+arrObservations[j]+' toString= '+arrObservations[j].toString()+' length= '+arrObservations[j].toString().length); 
				}
				var strObservation= arrObservations[j].toString();
				if(strObservation.length>0) {
					if(debug>0) { 
						logger.error('DEBUG::'+scriptName+' typeof(in4psPlusFollowupQuestions)= '+typeof(in4psPlusFollowupQuestions)+' toString= '+in4psPlusFollowupQuestions.toString()+' length= '+in4psPlusFollowupQuestions.toString().length); 
					}
					var lst4psPlusFollowupQuestionsElements= in4psPlusFollowupQuestions.toString().split(',');
					for(var i= 0;i<lst4psPlusFollowupQuestionsElements.length;i++) {
						var in4psPlusFollowupQuestionsElement= lst4psPlusFollowupQuestionsElements[i].toString();
						if(debug>0) { 
							logger.error('DEBUG::'+scriptName+' typeof(in4psPlusFollowupQuestionsElement['+i+'])= '+typeof(in4psPlusFollowupQuestionsElement)+' in4psPlusFollowupQuestionsElement['+i+']= '+in4psPlusFollowupQuestionsElement+' length= '+in4psPlusFollowupQuestionsElement.length+' strObservation= '+strObservation+' length= '+strObservation.legnth+' EVAL: strObservation.indexOf(in4psPlusFollowupQuestionsElement)= '+strObservation.indexOf(in4psPlusFollowupQuestionsElement));
						}
						if(strObservation.indexOf(in4psPlusFollowupQuestionsElement)<0) {
							// 2022-07-27 JER - Modified to accommodate out4psPlusFollowupQuestions as Object()
							out4psPlusFollowupQuestions[in4psPlusFollowupQuestionsElement]= 'N';
							// outString= "'"+in4psPlusFollowupQuestionsElement+"': 'N'";
							// out4psPlusFollowupQuestions.push(outString);
						} else {
							 out4psPlusFollowupQuestions[in4psPlusFollowupQuestionsElement]= 'Y';
						} // end if
					} // end for
				} else {
					// 2022-07-27 JER - Verify this modified code to handle if there is no current Medical Condition value has no length
					var lst4psPlusFollowupQuestionsElements= in4psPlusFollowupQuestions.toString().split(',');
					for(var i= 0;i<lst4psPlusFollowupQuestionsElements.length;i++) {
						var in4psPlusFollowupQuestionsElement= lst4psPlusFollowupQuestionsElements[i].toString();
						if(debug>0) { 
							logger.error('DEBUG::'+scriptName+' Defined with value - typeof(in4psPlusFollowupQuestionsElement['+i+'])= '+typeof(in4psPlusFollowupQuestionsElement)+' in4psPlusFollowupQuestionsElement['+i+']= '+in4psPlusFollowupQuestionsElement+' length= '+in4psPlusFollowupQuestionsElement.length);
						}
						// 2022-07-27 JER - Modified to accommodate out4psPlusFollowupQuestions as Object()
						out4psPlusFollowupQuestions[in4psPlusFollowupQuestionsElement]= 'N';
					} // end for
				} // end if 
			} // end for
			channelMap.put('out4psPlusFollowupQuestions',out4psPlusFollowupQuestions);
		} else {
			// 2022-07-27 JER - Verify this modified code to handle if there is no current Medical Condition value has no length
			var lst4psPlusFollowupQuestionsElements= in4psPlusFollowupQuestions.toString().split(',');
			for(var i= 0;i<lst4psPlusFollowupQuestionsElements.length;i++) {
				var in4psPlusFollowupQuestionsElement= lst4psPlusFollowupQuestionsElements[i].toString();
				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' Defined with no value - typeof(in4psPlusFollowupQuestionsElement['+i+'])= '+typeof(in4psPlusFollowupQuestionsElement)+' in4psPlusFollowupQuestionsElement['+i+']= '+in4psPlusFollowupQuestionsElement+' length= '+in4psPlusFollowupQuestionsElement.length);
				}
				// 2022-07-27 JER - Modified to accommodate out4psPlusFollowupQuestions as Object()
				out4psPlusFollowupQuestions[in4psPlusFollowupQuestionsElement]= 'N';
			} // end for
		}
		channelMap.put('out4psPlusFollowupQuestions',out4psPlusFollowupQuestions);
	} // end if
	if(debug>0) { 
		logger.error('DEBUG::'+scriptName+' typeof(out4psPlusFollowupQuestions)= '+typeof(out4psPlusFollowupQuestions)+' toString= '+out4psPlusFollowupQuestions.toString()+' length= '+out4psPlusFollowupQuestions.toString().length); 
	}
} // end function

/**
	Desc:  this JS code template is to return JSON string per a field value provided as argument

	scriptName: set_bleeding_during_current_pregnancy_field_lookup.js
	@param {object}  field - This field argument is an array object (e.g. {'LBW_PRI': 'Y','Smoking': 'Y'})
	@return {object} jsonString - This returns a resulting object using the arrJSONString
	History:
	2022-09-14 JER - Modified the section to= inBleedingDuringCurrentPregnancy and incorporated additional debug statements
	2022-08-01 JER - Modified to if D defined with no value - inBleedingDuringCurrentPregnancyElement[5]== "None" set value to 'Y'
*/
function set_bleeding_during_current_pregnancy_field_lookup(arrObservations) {

	var scriptName= 'set_bleeding_during_current_pregnancy_field_lookup';
	var debug= 0;
	// complete list of Bleeding During Current Pregnancy configured from Global Channel Map - in Deploy script
	var inBleedingDuringCurrentPregnancy= globalChannelMap.get('praBleedingDuringCurrentPregnancy');
	// output list of all Bleeding During Current Pregnancy for JSON
	var outBleedingDuringCurrentPregnancy= {};
	var outString;

	if(debug<0) { 
		logger.error('DEBUG::'+scriptName+' argument field typeof= '+typeof(arrObservations)+' arrObservations= '+arrObservations+' length= '+arrObservations.length); 
	}

	// arrObservations object contains current list of found Bleeding During Current Pregnancy from CCDA
	if(typeof(arrObservations) !== 'undefined') {
		if(arrObservations.toString().length>0) {
			for(var j= 0;j<arrObservations.length;j++) {
				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' A typeof= '+typeof(arrObservations[j])+' observation= '+arrObservations[j]+' toString= '+arrObservations[j].toString()+' length= '+arrObservations[j].toString().length); 
				}
				var strObservation= arrObservations[j].toString();
				if(strObservation.length>0) {
					if(debug>0) { 
						logger.error('DEBUG::'+scriptName+' typeof(inBleedingDuringCurrentPregnancy)= '+typeof(inBleedingDuringCurrentPregnancy)+' toString= '+inBleedingDuringCurrentPregnancy.toString()+' length= '+inBleedingDuringCurrentPregnancy.toString().length); 
					}
					var lstBleedingDuringCurrentPregnancyElements= inBleedingDuringCurrentPregnancy.toString().split(',');
					for(var i= 0;i<lstBleedingDuringCurrentPregnancyElements.length;i++) {
						var inBleedingDuringCurrentPregnancyElement= lstBleedingDuringCurrentPregnancyElements[i].toString();
						if(debug>0) { 
							logger.error('DEBUG::'+scriptName+' B typeof(inBleedingDuringCurrentPregnancyElement['+i+'])= '+typeof(inBleedingDuringCurrentPregnancyElement)+' inBleedingDuringCurrentPregnancyElement['+i+']= '+inBleedingDuringCurrentPregnancyElement+' length= '+inBleedingDuringCurrentPregnancyElement.length+' strObservation= '+strObservation+' length= '+strObservation.legnth+' EVAL: strObservation.indexOf(inBleedingDuringCurrentPregnancyElement)= '+strObservation.indexOf(inBleedingDuringCurrentPregnancyElement));
						}
						if(strObservation.indexOf(inBleedingDuringCurrentPregnancyElement)<0) {
							// 2022-07-27 JER - Modified to accommodate outBleedingDuringCurrentPregnancy as Object()
							outBleedingDuringCurrentPregnancy[inBleedingDuringCurrentPregnancyElement]= 'N';
							// outString= "'"+inBleedingDuringCurrentPregnancyElement+"': 'N'";
							// outBleedingDuringCurrentPregnancy.push(outString);
						} else {
							 outBleedingDuringCurrentPregnancy[inBleedingDuringCurrentPregnancyElement]= 'Y';
						} // end if
						if(debug>0) {
							logger.error('DEBUG::'+scriptName+' B1 inBleedingDuringCurrentPregnancyElement= '+inBleedingDuringCurrentPregnancyElement+' outBleedingDuringCurrentPregnancy= '+JSON.stringify(outBleedingDuringCurrentPregnancy));
						} // end if 
					} // end for
				} else {
					// 2022-07-27 JER - Verify this modified code to handle if there is no current Medical Condition value has no length
					var lstBleedingDuringCurrentPregnancyElements= inBleedingDuringCurrentPregnancy.toString().split(',');
					for(var i= 0;i<lstBleedingDuringCurrentPregnancyElements.length;i++) {
						var inBleedingDuringCurrentPregnancyElement= lstBleedingDuringCurrentPregnancyElements[i].toString();
						if(debug>0) { 
							logger.error('DEBUG::'+scriptName+' C Defined with value - typeof(inBleedingDuringCurrentPregnancyElement['+i+'])= '+typeof(inBleedingDuringCurrentPregnancyElement)+' inBleedingDuringCurrentPregnancyElement['+i+']= '+inBleedingDuringCurrentPregnancyElement+' length= '+inBleedingDuringCurrentPregnancyElement.length);
						}
						// 2022-07-27 JER - Modified to accommodate outBleedingDuringCurrentPregnancy as Object()
						outBleedingDuringCurrentPregnancy[inBleedingDuringCurrentPregnancyElement]= 'N';
						if(debug>0) {
							logger.error('DEBUG::'+scriptName+' C1 inBleedingDuringCurrentPregnancyElement= '+inBleedingDuringCurrentPregnancyElement+' outBleedingDuringCurrentPregnancy= '+JSON.stringify(outBleedingDuringCurrentPregnancy));
						} // end if
					} // end for
				} // end if 
			} // end for
			channelMap.put('outBleedingDuringCurrentPregnancy',outBleedingDuringCurrentPregnancy);
		} else {
			// 2022-07-27 JER - Verify this modified code to handle if there is no current Medical Condition value has no length
			var lstBleedingDuringCurrentPregnancyElements= inBleedingDuringCurrentPregnancy.toString().split(',');
			for(var i= 0;i<lstBleedingDuringCurrentPregnancyElements.length;i++) {
				var inBleedingDuringCurrentPregnancyElement= lstBleedingDuringCurrentPregnancyElements[i].toString();
				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' D Defined with no value - typeof(inBleedingDuringCurrentPregnancyElement['+i+'])= '+typeof(inBleedingDuringCurrentPregnancyElement)+' inBleedingDuringCurrentPregnancyElement['+i+']= '+inBleedingDuringCurrentPregnancyElement+' length= '+inBleedingDuringCurrentPregnancyElement.length);
				}
				// 2022-08-01 JER - Modified to if D defined with no value - inBleedingDuringCurrentPregnancyElement[5]== "None" set value to 'Y'
				if(inBleedingDuringCurrentPregnancyElement == 'None') {
					outBleedingDuringCurrentPregnancy[inBleedingDuringCurrentPregnancyElement]= 'Y';
				} else {
					outBleedingDuringCurrentPregnancy[inBleedingDuringCurrentPregnancyElement]= 'N';
				} // end if 
				if(debug>0) {
					logger.error('DEBUG::'+scriptName+' D1 inBleedingDuringCurrentPregnancyElement= '+inBleedingDuringCurrentPregnancyElement+' outBleedingDuringCurrentPregnancy= '+JSON.stringify(outBleedingDuringCurrentPregnancy));
				} // end if
			} // end for
		}
		channelMap.put('outBleedingDuringCurrentPregnancy',outBleedingDuringCurrentPregnancy);
	} // end if
	if(debug>0) { 
		logger.error('DEBUG::'+scriptName+' E EXIT typeof(outBleedingDuringCurrentPregnancy)= '+typeof(outBleedingDuringCurrentPregnancy)+' stringify= '+JSON.stringify(outBleedingDuringCurrentPregnancy)+' length= '+outBleedingDuringCurrentPregnancy.toString().length); 
	}
} // end function

/**
	Desc:  this JS code template is to return JSON string per a field value provided as argument

	scriptName: set_current_medical_conditions_field_lookup.js
	@param {object}  field - This field argument is an array object (e.g. {'LBW_PRI': 'Y','Smoking': 'Y'})
	@return {object} jsonString - This returns a resulting object using the arrJSONString
	History:
	2022-08-02 18:29 JER - Modified with latest revision of the evaluation and insert into outCurrentMedicalConditions array
	2022-08-01 20:02 JER - Modified on the evaluation between observation and inCurrentMedicalConditions array to simply use indexOf function and not interate through all of the inCurrentMedicalConditions array elements
	2022-08-01 14:39 JER - Modified to use toUpperCase() to match case sensitivity when performing indexOf function
	2022-08-01 13:09 JER - Modified configuration bug from using inPregnancyRiskFactor to inCurrentMedicalCondition
*/
function set_current_medical_conditions_field_lookup(arrObservations) {

	var scriptName= 'set_current_medical_conditions_field_lookup';
	var debug= 0;
	// complete list of Current Medical Conditions configured from Global Channel Map - in Deploy script
	var inCurrentMedicalConditions= globalChannelMap.get('praCurrentMedicalConditions');
	// output list of all Current Medical Conditions for JSON
	var outCurrentMedicalConditions= {};

	if((typeof(inCurrentMedicalConditions) !== 'undefined')&&(inCurrentMedicalConditions.length)>0) {

		if(debug<0) { 
			logger.error('DEBUG::'+scriptName+' A argument arrObservation typeof= '+typeof(arrObservations)+' arrObservations= '+arrObservations+' length= '+arrObservations.length); 
		}

		for(var i= 0;i<inCurrentMedicalConditions.length;i++) {

			// set the currentMedicalCondition element toString()
			var strCurrentMedicalCondition= inCurrentMedicalConditions[i].toString();
			// set the arrObservation array into toString() to be evaluated
			var strObservations= JSON.stringify(arrObservations);
			if(debug>0) { 
				logger.error('DEBUG::'+scriptName+' strObservations typeof= '+typeof(strObservations)+' length= '+strObservations.length+' strObservations= '+strObservations);
				logger.error('DEBUG::'+scriptName+' strCurrentMedicalCondition typeof= '+typeof(strCurrentMedicalCondition)+' length= '+strCurrentMedicalCondition.length+' strCurrentMedicalCondition= '+strCurrentMedicalCondition);
				logger.error('DEBUG::'+scriptName+' strObservations CHECK typeof= '+(typeof(strObservations) !== 'undefined')+' length= '+(strObservations.length>0)+' EVALUATION= '+(typeof(strObservations) !== 'undefined')&&(strObservations.length>0));
			}
			// arrObservations object contains current list of found Current Medical Conditions and Risks from CCDA
			if((typeof(strObservations) !== 'undefined')&&(strObservations.length>0)) {

				// 2022-08-01 JER - Modified to use toUpperCase() to match case sensitivity when performing indexOf function
				// Convert string (e.g. "HelloWorld": "Y") to array and then string to first element of array
				// var strObservations= strObservations.toString().split(":")[0].replace(/['"]+/g,'');
				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' EVALUATE strCurrentMedicalCondition= '+strCurrentMedicalCondition.toUpperCase()+' VS strObservations= '+strObservations.toUpperCase()+' EVAL= '+strObservations.toUpperCase().indexOf(strCurrentMedicalCondition.toUpperCase()));
				}
				if(strObservations.toUpperCase().indexOf(strCurrentMedicalCondition.toUpperCase())>-1) {
					// 2022-07-27 JER - Modified to accommodate outCurrentMedicalConditions as Object()
					outCurrentMedicalConditions[strCurrentMedicalCondition]= 'Y';
					if(debug>0) { 
						logger.error('DEBUG::'+scriptName+' SET outCurrentMedicalConditions['+strCurrentMedicalCondition+']= '+outCurrentMedicalConditions[strCurrentMedicalCondition]);
					}
				} else {
					// 2022-08-01 JER - Modified configuration bug from using inPregnancyRiskFactor to inCurrentMedicalCondition
					outCurrentMedicalConditions[strCurrentMedicalCondition]= 'N';
					if(debug>0) { 
						logger.error('DEBUG::'+scriptName+' SET outCurrentMedicalConditions['+strCurrentMedicalCondition+']= '+outCurrentMedicalConditions[strCurrentMedicalCondition]);
					}
				} // end if
			} else {
				// 2022-08-01 JER - Modified configuration bug from using inPregnancyRiskFactor to inCurrentMedicalCondition
				outCurrentMedicalConditions[strCurrentMedicalCondition]= 'N';
				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' SET outCurrentMedicalConditions['+strCurrentMedicalCondition+']= '+outCurrentMedicalConditions[strCurrentMedicalCondition]);
				} // end if 	
			}
		} // end for
		channelMap.put('outCurrentMedicalConditions',outCurrentMedicalConditions);
	} else {
		logger.error('DEBUG::'+scriptName+' inCurrentMedicalConditions has no value or is "undefined".');
	}// end if
	if(debug>0) { 
		logger.error('DEBUG::'+scriptName+' typeof(outCurrentMedicalConditions)= '+typeof(outCurrentMedicalConditions)+' toString= '+JSON.stringify(outCurrentMedicalConditions)+' length= '+JSON.stringify(outCurrentMedicalConditions).length); 
	}
} // end function

/**
	Desc:  this JS code template is to return JSON string per a field value provided as argument

	scriptName: set_entry_into_prenatal_care_field_lookup.js
	@param {object}  field - This field argument is an array object (e.g. {'LBW_PRI': 'Y','Smoking': 'Y'})
	@return {object} jsonString - This returns a resulting object using the arrJSONString
	History:
*/
function set_entry_into_prenatal_care_field_lookup(arrObservations) {

	var scriptName= 'set_entry_into_prenatal_care_field_lookup';
	var debug= 0;
	// complete list of Primary Language configured from Global Channel Map - in Deploy script
	var inEntryIntoPrenatalCare= globalChannelMap.get('praEntryIntoPrenatalCare');
	// build list of key: value pair strings into array before JSON
	var arrEntryIntoPrenatalCare= [];
	// output list of all Entry Into Prenatal Care for JSON
	var outEntryIntoPrenatalCare= {};
	var outString;

	if(debug<0) { 
		logger.error('DEBUG::'+scriptName+' argument field typeof= '+typeof(arrObservations)+' arrObservations= '+arrObservations+' length= '+arrObservations.length); 
	}

	// arrObservations object contains current list of found Entry Into Prenatal Care from CCDA
	// loop through the PRA EntryIntoPrenatalCare Form Elements
	var lstEntryIntoPrenatalCareElements= inEntryIntoPrenatalCare.toString().split(',');
	for(var i= 0;i<lstEntryIntoPrenatalCareElements.length;i++) {
		var inEntryIntoPrenatalCareElement= lstEntryIntoPrenatalCareElements[i].toString();
		if(debug>0) { 
			logger.error('DEBUG::'+scriptName+' typeof(inEntryIntoPrenatalCareElement['+i+'])= '+typeof(inEntryIntoPrenatalCareElement)+' inEntryIntoPrenatalCareElement['+i+']= '+inEntryIntoPrenatalCareElement+' length= '+inEntryIntoPrenatalCareElement.length+' arrObservations= '+arrObservations+' length= '+arrObservations.length+' EVAL: inEntryIntoPrenatalCareElement.indexOf(arrObservations)= '+inEntryIntoPrenatalCareElement.indexof(arrObservations));
		}

		for each(observation in arrObservations) {
			if(debug>0) { 
				logger.error('DEBUG::'+scriptName+' observation= '+observation+' typeof= '+typeof(observation)+' toString= '+observation.toString()+' inEntryIntoPrenatalCareElement= '+inEntryIntoPrenatalCareElement+' typeof= '+typeof(inEntryIntoPrenatalCareElement)+' toString= '+inEntryIntoPrenatalCareElement.toString());
				logger.error('DEBUG::'+scriptName+' observation.indexOf(inEntryIntoPrenatalCareElement) or '+observation+'.indexOf('+inEntryIntoPrenatalCareElement+')= '+observation.toString().indexOf(inEntryIntoPrenatalCareElement));
			}

			var strObservation= observation.toString().replace(/\s/g,'');
			if(strObservation.toString().indexOf(inEntryIntoPrenatalCareElement)>-1) {
				// var indexElement= 	observation.indexOf(inEntryIntoPrenatalCareElement);

				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' BEFORE typeof(strObservation): '+typeof(strObservation)+' strObservation: '+strObservation);
				}
				//var strObservation= JSON.stringify(strObservation).replace(/["\\]/gi,'');

				/*
				if(debug>0) { 
				logger.error('DEBUG::'+scriptName+' AFTER typeof(strObservation): '+typeof(strObservation)+' strObservation: '+strObservation); 
				}
				*/

				var lstObservation= observation.split(':');
				if(lstObservation[1].toString().length>0) {
					// 2022-08-19 JER - evaluate if the coded observation if 'Y' value
					/*
					if(lstObservation[1].toString()== 'Y') {
						logger.error('D');
						outEntryIntoPrenatalCare[inEntryIntoPrenatalCareElement]= 'Y';
						// 2022-08-19 JER - evaluate if observation is a discrete value (e.g. 106 mm[Hg])
					} else {
					*/
						if(debug>0) { 
							logger.error('DEBUG::'+scriptName+' discrete lstObservation[0]]= '+lstObservation[0].replace(/"/g,'')+' lstObservation[1]= '+lstObservation[1].replace(/"/g,'')); 
						}
						outEntryIntoPrenatalCare[lstObservation[0].replace(/"/g,'')]= lstObservation[1].replace(/"/g,'').trim();
						if(debug>0) { 
							logger.error('DEBUG::'+scriptName+' discrete outEntryIntoPrenatalCare= '+outEntryIntoPrenatalCare); 
						}
					//}
				} // end if 
			} // end if
		} // end for 
	} // end for

	for each(jsonPraElement in lstEntryIntoPrenatalCareElements) {

		if(debug>0) logger.error('DEBUG::'+scriptName+' JSON.stringify(outEntryIntoPrenatalCare)= '+JSON.stringify(outEntryIntoPrenatalCare)+' typeof= '+typeof(JSON.stringify(outEntryIntoPrenatalCare)));
		var strOutEntryIntoPrenatalCare= JSON.stringify(outEntryIntoPrenatalCare);
		if(strOutEntryIntoPrenatalCare.indexOf(jsonPraElement)<0) {
			outEntryIntoPrenatalCare[jsonPraElement]= '';
		} // end if 
	} // end for each

	channelMap.put('outEntryIntoPrenatalCare',outEntryIntoPrenatalCare);

	if(debug>0) { 
		logger.error('DEBUG::'+scriptName+' typeof(outEntryIntoPrenatalCare)= '+typeof(outEntryIntoPrenatalCare)+' toString= '+JSON.stringify(outEntryIntoPrenatalCare)+' length= '+JSON.stringify(outEntryIntoPrenatalCare).length); 
	}
} // end function

/**
	Desc:  this JS code template is to return JSON string per a field value provided as argument

	scriptName: set_entry_prenatal_care_field_lookup.js
	@param {object}  field - This field argument is an array object (e.g. {'LBW_PRI': 'Y','Smoking': 'Y'})
	@return {object} jsonString - This returns a resulting object using the arrJSONString
