function set_entry_prenatal_care_field_lookup(arrObservations) {

	var scriptName= 'set_entry_prenatal_care_field_lookup';
	var debug= 0;
	// complete list of Entry Prenatal Care configured from Global Channel Map - in Deploy script
	var inEntryPrenatalCare= globalChannelMap.get('praReasonForLateEntryToPrenatalCare');
	// output list of all Entry Prenatal Care for JSON
	var outEntryPrenatalCare= {};
	var outString;

	if(debug<0) { 
		logger.error('DEBUG::'+scriptName+' argument field typeof= '+typeof(arrObservations)+' arrObservations= '+arrObservations+' length= '+arrObservations.length); 
	}

	// arrObservations object contains current list of found Entry Prenatal Care from CCDA
	if(typeof(arrObservations) !== 'undefined') {
		if(arrObservations.toString().length>0) {
			for(var j= 0;j<arrObservations.length;j++) {
				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' typeof= '+typeof(arrObservations[j])+' observation= '+arrObservations[j]+' toString= '+arrObservations[j].toString()+' length= '+arrObservations[j].toString().length); 
				}
				var strObservation= arrObservations[j].toString();
				if(strObservation.length>0) {
					if(debug>0) { 
						logger.error('DEBUG::'+scriptName+' typeof(inEntryPrenatalCare)= '+typeof(inEntryPrenatalCare)+' toString= '+inEntryPrenatalCare.toString()+' length= '+inEntryPrenatalCare.toString().length); 
					}
					var lstEntryPrenatalCareElements= inEntryPrenatalCare.toString().split(',');
					for(var i= 0;i<lstEntryPrenatalCareElements.length;i++) {
						var inEntryPrenatalCareElement= lstEntryPrenatalCareElements[i].toString();
						if(debug>0) { 
							logger.error('DEBUG::'+scriptName+' typeof(inEntryPrenatalCareElement['+i+'])= '+typeof(inEntryPrenatalCareElement)+' inEntryPrenatalCareElement['+i+']= '+inEntryPrenatalCareElement+' length= '+inEntryPrenatalCareElement.length+' strObservation= '+strObservation+' length= '+strObservation.legnth+' EVAL: strObservation.indexOf(inEntryPrenatalCareElement)= '+strObservation.indexOf(inEntryPrenatalCareElement));
						}
						if(strObservation.indexOf(inEntryPrenatalCareElement)<0) {
							// 2022-07-27 JER - Modified to accommodate outEntryPrenatalCare as Object()
							outEntryPrenatalCare[inEntryPrenatalCareElement]= 'N';
							// outString= "'"+inEntryPrenatalCareElement+"': 'N'";
							// outEntryPrenatalCare.push(outString);
						} else {
							 outEntryPrenatalCare[inEntryPrenatalCareElement]= 'Y';
						} // end if
					} // end for
				} else {
					// 2022-07-27 JER - Verify this modified code to handle if there is no current Medical Condition value has no length
					var lstEntryPrenatalCareElements= inEntryPrenatalCare.toString().split(',');
					for(var i= 0;i<lstEntryPrenatalCareElements.length;i++) {
						var inEntryPrenatalCareElement= lstEntryPrenatalCareElements[i].toString();
						if(debug>0) { 
							logger.error('DEBUG::'+scriptName+' Defined with value - typeof(inEntryPrenatalCareElement['+i+'])= '+typeof(inEntryPrenatalCareElement)+' inEntryPrenatalCareElement['+i+']= '+inEntryPrenatalCareElement+' length= '+inEntryPrenatalCareElement.length);
						}
						// 2022-07-27 JER - Modified to accommodate outEntryPrenatalCare as Object()
						outEntryPrenatalCare[inEntryPrenatalCareElement]= 'N';
					} // end for
				} // end if 
			} // end for
			channelMap.put('outEntryPrenatalCare',outEntryPrenatalCare);
		} else {
			// 2022-07-27 JER - Verify this modified code to handle if there is no current Medical Condition value has no length
			var lstEntryPrenatalCareElements= inEntryPrenatalCare.toString().split(',');
			for(var i= 0;i<lstEntryPrenatalCareElements.length;i++) {
				var inEntryPrenatalCareElement= lstEntryPrenatalCareElements[i].toString();
				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' Defined with no value - typeof(inEntryPrenatalCareElement['+i+'])= '+typeof(inEntryPrenatalCareElement)+' inEntryPrenatalCareElement['+i+']= '+inEntryPrenatalCareElement+' length= '+inEntryPrenatalCareElement.length);
				}
				// 2022-07-27 JER - Modified to accommodate outEntryPrenatalCare as Object()
				outEntryPrenatalCare[inEntryPrenatalCareElement]= 'N';
			} // end for
		}
		channelMap.put('outEntryPrenatalCare',outEntryPrenatalCare);
	} // end if
	if(debug>0) { 
		logger.error('DEBUG::'+scriptName+' typeof(outEntryPrenatalCare)= '+typeof(outEntryPrenatalCare)+' toString= '+outEntryPrenatalCare.toString()+' length= '+outEntryPrenatalCare.toString().length); 
	}
} // end function

/**
	Desc:  this JS code template is to return JSON string per a field value provided as argument

	scriptName: set_ethnicity_field_lookup.js
	@param {object}  field - This field argument is an array object (e.g. {'LBW_PRI': 'Y','Smoking': 'Y'})
	@return {object} jsonString - This returns a resulting object using the arrJSONString
	History:
	2022-09-21 JER - Modified the script leveraging from the set_4ps_plus_field_lookup.js to set_ethnicity_field_lookup.js
	2022-09-20 JER - Modified added to distinguish between str4psPlus element name and value
*/
function set_ethnicity_field_lookup(arrObservations) {

	var scriptName= 'set_ethnicity_field_lookup';
	var debug= 0;
	// complete list of Ethnicity configured from Global Channel Map - in Deploy script
	var inEthnicity= globalChannelMap.get('praEthnicity');
	// output list of all Ethnicity for JSON
	var outEthnicity= {};
	// 2022-08-22 JER - Added arrEthnicity to store initial evaluation observation versus praRace array list of JSON properties
	var arrEthnicity= [];
	var outString;
	var foundEthnicity= false;

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' A arrObservation typeof: '+typeof(arrObservations)+' arrObservations: '+arrObservations);
	} // end if 

	if((typeof(inEthnicity) !== 'undefined')&&(inEthnicity.length)>0) {

		if(debug<0) { 
			logger.error('DEBUG::'+scriptName+' B argument arrObservation typeof= '+typeof(arrObservations)+' arrObservations= '+arrObservations+' length= '+arrObservations.length); 
		} // end if 

		for(var i= 0;i<inEthnicity.length;i++) {

			// set the inEthnicity element toString()
			var strInEthnicity= inEthnicity[i].toString();

			// set the arrObservation array into toString() to be evaluated
			var strObservations= JSON.stringify(arrObservations);
			if(debug>0) { 
				logger.error('DEBUG::'+scriptName+' C strObservations typeof= '+typeof(strObservations)+' length= '+strObservations.length+' strObservations= '+strObservations);
			} // end if 

			// arrObservations object contains current list of found Pregnancy Risk Factors from CCDA
			if((typeof(strObservations) !== 'undefined')&&(strObservations.length>0)) {

				// 2022-09-20 JER - Modified added to distinguish between strInEthnicity element name and value
				var firstEthnicityElement= strObservations.split(':')[0].replace(/[^a-z0-9]/gi, '');
				var firstEthnicityValue=   strObservations.split(':')[1].trim().replace(/[^a-z0-9]/gi, '');
				if(debug>0) { 
					// logger.error('DEBUG::'+scriptName+' B.1 strObservation= '+strObservation+' length: '+strObservation.length);
					logger.error('DEBUG::'+scriptName+' D.1 firstEthnicityElement= '+firstEthnicityElement);
					logger.error('DEBUG::'+scriptName+' D.2 firstEthnicityValue= '+firstEthnicityValue);
				} // end if 

				// "'Hispanic': 'N'"
				if(strObservations.toUpperCase().indexOf(strInEthnicity.toUpperCase())>-1) {
					// 2022-07-27 JER - Modified to accommodate outEthnicity as Object()
					outEthnicity[firstEthnicityElement]= firstEthnicityValue;
					if(debug>0) { 
						logger.error('DEBUG::'+scriptName+' E1 SET outEthnicity['+firstEthnicityElement+']= '+outEthnicity[firstEthnicityElement]);
					} // end if 
				} else {
					outEthnicity[firstEthnicityElement]= '';
					if(debug>0) { 
						logger.error('DEBUG::'+scriptName+' E2 SET outEthnicity['+firstEthnicityElement+']= '+outEthnicity[firstEthnicityElement]);
					} // end if
				} // end if
			} else {
				outEthnicity[firstEthnicityElement]= '';
				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' E3 SET outEthnicity['+firstEthnicityElement+']= '+outEthnicity[firstEthnicityElement]);
				} // end if 	
			} // end if
		} // end for
		channelMap.put('outEthnicity',outEthnicity);
	} else {
		logger.error('DEBUG::'+scriptName+' F inEthnicity has no value or is "undefined".');
	} // end if
	if(debug>0) { 
		logger.error('DEBUG::'+scriptName+' G typeof(outEthnicity)= '+typeof(outEthnicity)+' toString= '+JSON.stringify(outEthnicity)+' length= '+JSON.stringify(outEthnicity).length); 
	} // end if
} // end function

