*/
function set_health_insurance_field_lookup(arrObservations) {

	var scriptName= 'set_health_insurance_field_lookup';
	var debug= 0;
	// complete list of Health Insurance configured from Global Channel Map - in Deploy script
	var inHealthInsurance= globalChannelMap.get('praHealthInsurance');
	// output list of all Perinatal History for JSON
	var outHealthInsurance= {};
	var outString;

	if(debug<0) { 
		logger.error('DEBUG::'+scriptName+' argument field typeof= '+typeof(arrObservations)+' arrObservations= '+arrObservations+' length= '+arrObservations.length); 
	}

	// arrObservations object contains current list of found Health Insurance from CCDA
	if(typeof(arrObservations) !== 'undefined') {
		if(arrObservations.toString().length>0) {
			for(var j= 0;j<arrObservations.length;j++) {
				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' typeof= '+typeof(arrObservations[j])+' observation= '+arrObservations[j]+' toString= '+arrObservations[j].toString()+' length= '+arrObservations[j].toString().length); 
				}
				var strObservation= arrObservations[j].toString();
				if(strObservation.length>0) {
					if(debug>0) { 
						logger.error('DEBUG::'+scriptName+' typeof(inHealthInsurance)= '+typeof(inHealthInsurance)+' toString= '+inHealthInsurance.toString()+' length= '+inHealthInsurance.toString().length); 
					}
					var lstHealthInsuranceElements= inHealthInsurance.toString().split(',');
					for(var i= 0;i<lstHealthInsuranceElements.length;i++) {
						var inHealthInsuranceElement= lstHealthInsuranceElements[i].toString();
						if(debug>0) { 
							logger.error('DEBUG::'+scriptName+' typeof(inHealthInsuranceElement['+i+'])= '+typeof(inHealthInsuranceElement)+' inHealthInsuranceElement['+i+']= '+inHealthInsuranceElement+' length= '+inHealthInsuranceElement.length+' strObservation= '+strObservation+' length= '+strObservation.legnth+' EVAL: strObservation.indexOf(inHealthInsuranceElement)= '+strObservation.indexOf(inHealthInsuranceElement));
						}
						if(strObservation.indexOf(inHealthInsuranceElement)<0) {
							// 2022-07-27 JER - Modified to accommodate outHealthInsurance as Object()
							outHealthInsurance[inHealthInsuranceElement]= 'N';
							// outString= "'"+inHealthInsuranceElement+"': 'N'";
							// outHealthInsurance.push(outString);
						} else {
							 outHealthInsurance[inHealthInsuranceElement]= 'Y';
						} // end if
					} // end for
				} else {
					// 2022-07-27 JER - Verify this modified code to handle if there is no current Medical Condition value has no length
					var lstHealthInsuranceElements= inHealthInsurance.toString().split(',');
					for(var i= 0;i<lstHealthInsuranceElements.length;i++) {
						var inHealthInsuranceElement= lstHealthInsuranceElements[i].toString();
						if(debug>0) { 
							logger.error('DEBUG::'+scriptName+' Defined with value - typeof(inHealthInsuranceElement['+i+'])= '+typeof(inHealthInsuranceElement)+' inHealthInsuranceElement['+i+']= '+inHealthInsuranceElement+' length= '+inHealthInsuranceElement.length);
						}
						// 2022-07-27 JER - Modified to accommodate outHealthInsurance as Object()
						outHealthInsurance[inHealthInsuranceElement]= 'N';
					} // end for
				} // end if 
			} // end for
			channelMap.put('outHealthInsurance',outHealthInsurance);
		} else {
			// 2022-07-27 JER - Verify this modified code to handle if there is no current Medical Condition value has no length
			var lstHealthInsuranceElements= inHealthInsurance.toString().split(',');
			for(var i= 0;i<lstHealthInsuranceElements.length;i++) {
				var inHealthInsuranceElement= lstHealthInsuranceElements[i].toString();
				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' Defined with no value - typeof(inHealthInsuranceElement['+i+'])= '+typeof(inHealthInsuranceElement)+' inHealthInsuranceElement['+i+']= '+inHealthInsuranceElement+' length= '+inHealthInsuranceElement.length);
				}
				// 2022-07-27 JER - Modified to accommodate outHealthInsurance as Object()
				outHealthInsurance[inHealthInsuranceElement]= 'N';
			} // end for
		}
		channelMap.put('outHealthInsurance',outHealthInsurance);
	} // end if
	if(debug>0) { 
		logger.error('DEBUG::'+scriptName+' typeof(outHealthInsurance)= '+typeof(outHealthInsurance)+' toString= '+outHealthInsurance.toString()+' length= '+outHealthInsurance.toString().length); 
