/**
	Desc:  this JS code template is to return JSON string per a field value provided as argument

	scriptName: set_physical_assessment_field_lookup.js
	@param {object}  field - This field argument is an array object (e.g. {'LBW_PRI': 'Y','Smoking': 'Y'})
	@return {object} jsonString - This returns a resulting object using the arrJSONString
	History:
	2022-08-29 JER - Modifications to the handling of inserts into the outPhysicalAssessment object 
	2022-08-27 JER - Modifications post evaluation of string to outPhysicalAssessment JSON object
	2022-08-19 JER - evaluate if observation is a discrete value (e.g. 106 mm[Hg])
*/
function set_physical_assessment_field_lookup(arrObservations) {

	var scriptName= 'set_physical_assessment_field_lookup';
	var debug= 0;
	// complete list of Physical Assessment configured from Global Channel Map - in Deploy script
	var inPhysicalAssessment= globalChannelMap.get('praPhysicalAssessment');
	// build list of key: value pair strings into array before JSON
	var arrPhysicalAssessment= [];
	// output list of all Pregnancy Risk Factors for JSON
	var outPhysicalAssessment= {};
	var outString;

	if(debug<0) { 
		logger.error('DEBUG::'+scriptName+' argument field typeof= '+typeof(arrObservations)+' arrObservations= '+arrObservations+' length= '+arrObservations.length); 
	}

	// arrObservations object contains current list of found Physical Assessments from CCDA
	// loop through the PRA PhysicalAssessment Form Elements
	var lstPhysicalAssessmentElements= inPhysicalAssessment.toString().split(',');
	for(var i= 0;i<lstPhysicalAssessmentElements.length;i++) {
		var inPhysicalAssessmentElement= lstPhysicalAssessmentElements[i].toString();
		if(debug>0) { 
			logger.error('DEBUG::'+scriptName+' typeof(inPhysicalAssessmentElement['+i+'])= '+typeof(inPhysicalAssessmentElement)+' inPhysicalAssessmentElement['+i+']= '+inPhysicalAssessmentElement+' length= '+inPhysicalAssessmentElement.length+' arrObservations= '+arrObservations+' length= '+arrObservations.length+' EVAL: inPhysicalAssessmentElement.indexOf(arrObservations)= '+inPhysicalAssessmentElement.indexof(arrObservations));
		}

		for each(observation in arrObservations) {
			if(debug>0) { 
				logger.error('DEBUG::'+scriptName+' observation= '+observation+' typeof= '+typeof(observation)+' toString= '+observation.toString()+' inPhysicalAssessmentElement= '+inPhysicalAssessmentElement+' typeof= '+typeof(inPhysicalAssessmentElement)+' toString= '+inPhysicalAssessmentElement.toString());
				logger.error('DEBUG::'+scriptName+' observation.indexOf(inPhysicalAssessmentElement) or '+observation+'.indexOf('+inPhysicalAssessmentElement+')= '+observation.toString().indexOf(inPhysicalAssessmentElement));
			}

		/*
			if(observation.toString().indexOf(inPhysicalAssessmentElement)<0) {
				logger.error('A');
				// 2022-07-27 JER - Modified to accommodate outPhysicalAssessment as Object()
				outPhysicalAssessment[inPhysicalAssessmentElement]= '';
				// outString= "'"+inPhysicalAssessmentElement+"': 'N'";
				// outPhysicalAssessment.push(outString);
			} else {
		*/
			var strObservation= observation.toString().replace(/\s/g,'');
			if(strObservation.toString().indexOf(inPhysicalAssessmentElement)>-1) {
				// var indexElement= 	observation.indexOf(inPhysicalAssessmentElement);

				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' BEFORE typeof(strObservation): '+typeof(strObservation)+' strObservation: '+strObservation);
				}
				//var strObservation= JSON.stringify(strObservation).replace(/["\\]/gi,'');

				/*
				if(debug>0) { 
				logger.error('DEBUG::'+scriptName+' AFTER typeof(strObservation): '+typeof(strObservation)+' strObservation: '+strObservation); 
				}
				*/

				var lstObservation= observation.split(':');
				if(lstObservation[1].toString().length>0) {
					// 2022-08-19 JER - evaluate if the coded observation if 'Y' value
					/*
					if(lstObservation[1].toString()== 'Y') {
						logger.error('D');
						outPhysicalAssessment[inPhysicalAssessmentElement]= 'Y';
						// 2022-08-19 JER - evaluate if observation is a discrete value (e.g. 106 mm[Hg])
					} else {
					*/
						if(debug>0) { 
							logger.error('DEBUG::'+scriptName+' discrete lstObservation[0]]= '+lstObservation[0].replace(/"/g,'')+' lstObservation[1]= '+lstObservation[1].replace(/"/g,'')); 
						}
						outPhysicalAssessment[lstObservation[0].replace(/"/g,'')]= lstObservation[1].replace(/"/g,'').trim();
						if(debug>0) { 
							logger.error('DEBUG::'+scriptName+' discrete outPhysicalAssessment= '+outPhysicalAssessment); 
						}
					//}
				} // end if 
			} // end if
		} // end for 
	} // end for

	for each(jsonPraElement in lstPhysicalAssessmentElements) {

		if(debug>0) logger.error('DEBUG::'+scriptName+' JSON.stringify(outPhysicalAssessment)= '+JSON.stringify(outPhysicalAssessment)+' typeof= '+typeof(JSON.stringify(outPhysicalAssessment)));
		var strOutPhysicalAssessment= JSON.stringify(outPhysicalAssessment);
		if(strOutPhysicalAssessment.indexOf(jsonPraElement)<0) {
			outPhysicalAssessment[jsonPraElement]= '';
		} // end if 
	} // end for each

	channelMap.put('outPhysicalAssessment',outPhysicalAssessment);

	if(debug>0) { 
		logger.error('DEBUG::'+scriptName+' typeof(outPhysicalAssessment)= '+typeof(outPhysicalAssessment)+' toString= '+JSON.stringify(outPhysicalAssessment)+' length= '+JSON.stringify(outPhysicalAssessment).length); 
	}
} // end function

/**
	Desc:  this JS code template is to return JSON string per a field value provided as argument
	scriptName: set_pregnancy_risk_factors_field_lookup.js
	@param {object}  field - This field argument is an array object (e.g. {'LBW_PRI': 'Y','Smoking': 'Y'})
	@return {object} jsonString - This returns a resulting object using the arrJSONString
	History:
*/
function set_pregnancy_risk_factors_field_lookup(arrObservations) {

	var scriptName= 'set_pregnancy_risk_factors_field_lookup';
	var debug= 0;
	// complete list of Pregnancy Risk Factors configured from Global Channel Map - in Deploy script
	var inPregnancyRiskFactors= globalChannelMap.get('praPregnancyRiskFactors');
	// output list of all Pregnancy Risk Factors for JSON
	var outPregnancyRiskFactors= {};

	if((typeof(inPregnancyRiskFactors) !== 'undefined')&&(inPregnancyRiskFactors.length)>0) {

		if(debug<0) { 
			logger.error('DEBUG::'+scriptName+' A argument arrObservation typeof= '+typeof(arrObservations)+' arrObservations= '+arrObservations+' length= '+arrObservations.length); 
		}

		for(var i= 0;i<inPregnancyRiskFactors.length;i++) {

			// set the inPregnancyRiskFactors element toString()
			var strPregnancyRiskFactors= inPregnancyRiskFactors[i].toString();
			// set the arrObservation array into toString() to be evaluated
			var strObservations= JSON.stringify(arrObservations);
			if(debug>0) { 
				logger.error('DEBUG::'+scriptName+' strObservations typeof= '+typeof(strObservations)+' length= '+strObservations.length+' strObservations= '+strObservations);
				logger.error('DEBUG::'+scriptName+' strPregnancyRiskFactors typeof= '+typeof(strPregnancyRiskFactors)+' length= '+strPregnancyRiskFactors.length+' strPregnancyRiskFactors= '+strPregnancyRiskFactors);
				logger.error('DEBUG::'+scriptName+' strObservations CHECK typeof= '+(typeof(strObservations) !== 'undefined')+' length= '+(strObservations.length>0)+' EVALUATION= '+(typeof(strObservations) !== 'undefined')&&(strObservations.length>0));
			}
			// arrObservations object contains current list of found Pregnancy Risk Factors from CCDA
			if((typeof(strObservations) !== 'undefined')&&(strObservations.length>0)) {

				// 2022-08-01 JER - Modified to use toUpperCase() to match case sensitivity when performing indexOf function
				// Convert string (e.g. "HelloWorld": "Y") to array and then string to first element of array
				// var strObservations= strObservations.toString().split(":")[0].replace(/['"]+/g,'');
				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' EVALUATE strPregnancyRiskFactors= '+strPregnancyRiskFactors.toUpperCase()+' VS strObservations= '+strObservations.toUpperCase()+' EVAL= '+strObservations.toUpperCase().indexOf(strPregnancyRiskFactors.toUpperCase()));
				}
				if(strObservations.toUpperCase().indexOf(strPregnancyRiskFactors.toUpperCase())>-1) {
					// 2022-07-27 JER - Modified to accommodate outPregnancyRiskFactors as Object()
					outPregnancyRiskFactors[strPregnancyRiskFactors]= 'Y';
					if(debug>0) { 
						logger.error('DEBUG::'+scriptName+' SET outPregnancyRiskFactors['+strPregnancyRiskFactors+']= '+outPregnancyRiskFactors[strPregnancyRiskFactors]);
					}
				} else {
					outPregnancyRiskFactors[strPregnancyRiskFactors]= 'N';
					if(debug>0) { 
						logger.error('DEBUG::'+scriptName+' SET outPregnancyRiskFactors['+strPregnancyRiskFactors+']= '+outPregnancyRiskFactors[strPregnancyRiskFactors]);
					}
				} // end if
			} else {
				outPregnancyRiskFactors[strPregnancyRiskFactors]= 'N';
				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' SET outPregnancyRiskFactors['+strPregnancyRiskFactors+']= '+outPregnancyRiskFactors[strPregnancyRiskFactors]);
				} // end if 	
			}
		} // end for
		channelMap.put('outPregnancyRiskFactors',outPregnancyRiskFactors);
	} else {
		logger.error('DEBUG::'+scriptName+' inPregnancyRiskFactors has no value or is "undefined".');
	}// end if
	if(debug>0) { 
		logger.error('DEBUG::'+scriptName+' typeof(outPregnancyRiskFactors)= '+typeof(outPregnancyRiskFactors)+' toString= '+JSON.stringify(outPregnancyRiskFactors)+' length= '+JSON.stringify(outPregnancyRiskFactors).length); 
	}
} // end function

/**
	Desc:  this JS code template is to return JSON string per a field value provided as argument

	scriptName: set_primary_language_field_lookup.js
	@param {object}  field - This field argument is an array object (e.g. {'LBW_PRI': 'Y','Smoking': 'Y'})
	@return {object} jsonString - This returns a resulting object using the arrJSONString
	History:
*/
function set_primary_language_field_lookup(arrObservations) {

	var scriptName= 'set_primary_language_field_lookup';
	var debug= 0;
	// complete list of Primary Language configured from Global Channel Map - in Deploy script
	var inPrimaryLanguage= globalChannelMap.get('praPrimaryLanguage');
	// output list of all Primary Language for JSON
	var outPrimaryLanguage= {};
	var outString;

	if(debug<0) { 
		logger.error('DEBUG::'+scriptName+' argument field typeof= '+typeof(arrObservations)+' arrObservations= '+arrObservations+' length= '+arrObservations.length); 
	}

	// arrObservations object contains current list of found Primary Language from CCDA
	if(typeof(arrObservations) !== 'undefined') {
		if(arrObservations.toString().length>0) {
			for(var j= 0;j<arrObservations.length;j++) {
				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' typeof= '+typeof(arrObservations[j])+' observation= '+arrObservations[j]+' toString= '+arrObservations[j].toString()+' length= '+arrObservations[j].toString().length); 
				}
				var strObservation= arrObservations[j].toString();
				if(strObservation.length>0) {
					if(debug>0) { 
						logger.error('DEBUG::'+scriptName+' typeof(inPrimaryLanguage)= '+typeof(inPrimaryLanguage)+' toString= '+inPrimaryLanguage.toString()+' length= '+inPrimaryLanguage.toString().length); 
					}
					var lstPrimaryLanguageElements= inPrimaryLanguage.toString().split(',');
					for(var i= 0;i<lstPrimaryLanguageElements.length;i++) {
						var inPrimaryLanguageElement= lstPrimaryLanguageElements[i].toString();
						if(debug>0) { 
							logger.error('DEBUG::'+scriptName+' typeof(inPrimaryLanguageElement['+i+'])= '+typeof(inPrimaryLanguageElement)+' inPrimaryLanguageElement['+i+']= '+inPrimaryLanguageElement+' length= '+inPrimaryLanguageElement.length+' strObservation= '+strObservation+' length= '+strObservation.legnth+' EVAL: strObservation.indexOf(inPrimaryLanguageElement)= '+strObservation.indexOf(inPrimaryLanguageElement));
						}
						if(strObservation.indexOf(inPrimaryLanguageElement)<0) {
							// 2022-07-27 JER - Modified to accommodate outPrimaryLanguage as Object()
							outPrimaryLanguage[inPrimaryLanguageElement]= 'N';
							// outString= "'"+inPrimaryLanguageElement+"': 'N'";
							// outPrimaryLanguage.push(outString);
						} else {
							 outPrimaryLanguage[inPrimaryLanguageElement]= 'Y';
						} // end if
					} // end for
				} else {
					// 2022-07-27 JER - Verify this modified code to handle if there is no current Medical Condition value has no length
					var lstPrimaryLanguageElements= inPrimaryLanguage.toString().split(',');
					for(var i= 0;i<lstPrimaryLanguageElements.length;i++) {
						var inPrimaryLanguageElement= lstPrimaryLanguageElements[i].toString();
						if(debug>0) { 
							logger.error('DEBUG::'+scriptName+' Defined with value - typeof(inPrimaryLanguageElement['+i+'])= '+typeof(inPrimaryLanguageElement)+' inPrimaryLanguageElement['+i+']= '+inPrimaryLanguageElement+' length= '+inPrimaryLanguageElement.length);
						}
						// 2022-07-27 JER - Modified to accommodate outPrimaryLanguage as Object()
						outPrimaryLanguage[inPrimaryLanguageElement]= 'N';
					} // end for
				} // end if 
			} // end for
			channelMap.put('outPrimaryLanguage',outPrimaryLanguage);
		} else {
			// 2022-07-27 JER - Verify this modified code to handle if there is no current Medical Condition value has no length
			var lstPrimaryLanguageElements= inPrimaryLanguage.toString().split(',');
			for(var i= 0;i<lstPrimaryLanguageElements.length;i++) {
				var inPrimaryLanguageElement= lstPrimaryLanguageElements[i].toString();
				if(debug>0) { 
					logger.error('DEBUG::'+scriptName+' Defined with no value - typeof(inPrimaryLanguageElement['+i+'])= '+typeof(inPrimaryLanguageElement)+' inPrimaryLanguageElement['+i+']= '+inPrimaryLanguageElement+' length= '+inPrimaryLanguageElement.length);
				}
				// 2022-07-27 JER - Modified to accommodate outPrimaryLanguage as Object()
				outPrimaryLanguage[inPrimaryLanguageElement]= 'N';
			} // end for
		}
		channelMap.put('outPrimaryLanguage',outPrimaryLanguage);
	} // end if
	if(debug>0) { 
		logger.error('DEBUG::'+scriptName+' typeof(outPrimaryLanguage)= '+typeof(outPrimaryLanguage)+' toString= '+outPrimaryLanguage.toString()+' length= '+outPrimaryLanguage.toString().length); 
	}
} // end function

