function phoneToE164(phoneIn) {
	logger.info('L1 '+phoneIn);
	if(phoneIn != null ){
	//phoneIn = '+1' + phoneIn.toString(); 
	logger.info('L2 '+phoneIn);
	return '+1' + phoneIn.toString(); 
