}
}

/** createXdsExtrinsicClassification(msg)

  	- Desc: This function receives CCD msg and returns XdsExtrinsicClassification XML object
	- Modification:
	2018-10-30 jer - additional debug and namespace logic

   	@param {object} objObject 	- this contains a CCD object (i.e. author, performer, participant, etc.)
	@param {string} objType		- this contains a string to identify the type of Object (i.e. author, performer, participant, etc.)
	@return {string} strXML 		- contains the XML string to be inserted into an XML Soap Envelope
**/
function createXdsExtrinsicClassification(objObject, objType) {		

	var scriptName= 'createXdsExtrinsicClassification';

	// build Classification XML
	var tmp = new XML();
	// var tmp = new XML("<classification></classification>");

	default xml namespace= 'urn:ihe:iti:xds-b:2007';
	var rim= new Namespace('urn:oasis:names:tc:ebxml-regrep:xsd:rim:3.0');
	tmp.addNamespace(rim);

	logger.error(scriptName+'::objObject: '+JSON.stringify(objObject, null, 4)+' typeof: '+typeof(objObject));
	logger.error(scriptName+'::objType: '+objType+' typeof: '+typeof(objType)+' length: '+objType.length);

	if (objType== 'author') {
		var classificationScheme= "urn:uuid:93606bcf-9494-43ec-9b4e-a7748d1a838d";
		var classifiedObject= $('extrinsicObjectUUID');
	} // end if

	/*
	var rootElement = new XML('<'+rootElementTag+'></'+rootElementTag+'>');
	rootElement.addNamespace(new Namespace('xsi', 'http://www.w3.org/2001/XMLSchema-instance'));
	rootElement['@xsi:schemaLocation']='urn:hl7-org:v3 ../../schema/HL7V3/NE2008/multicacheschemas/PRPA_IN201305UV02.xsd';
	*/

	// tmp.addNamespace(new Namespace('rim', 'urn:oasis:names:tc:ebxml-regrep:xsd:rim:3.0'));
	// <rim:Classification id="cl01" classificationScheme="urn:uuid:93606bcf-9494-43ec-9b4e-a7748d1a838d" classifiedObject="775a43ca-adf2-445e-bd4b-936d08f033ab">
	// createSegment('rim:Classification',tmp);
	logger.error(scriptName+'::xml.toXMLString: '+tmp.toXMLString()+' typeof: '+typeof(tmp));

	tmp.rim::['classification']['@id']= UUIDGenerator.getUUID().toString();
	tmp.rim::['classification']['@classificationScheme']= 	classificationScheme;
	tmp.rim::['classification']['@classifiedObject']=		classifiedObject;
	logger.error(scriptName+'::xml.toXMLString: '+tmp.toXMLString()+' typeof: '+typeof(tmp));

	if (objType== 'Author') {
		for (var property in objObject) {
			if (objObject.hasOwnProperty(property)) {
				logger.error(property, objObject[property]);
			} // end if
		} // end for
	} // end if

/*
			createSegment('rim:Slot',msg);				
			msg['Classification']['Slot']['@name']
							<rim:Slot name="authorPerson">
                                <rim:ValueList>
                                    <rim:Value>1073533246^Kalish^Michael^^MD^^^^&amp;2.16.840.1.113883.4.6&amp;ISO</rim:Value>
                                </rim:ValueList>
                            </rim:Slot>
                            <rim:Slot name="authorInstitution">
                                <rim:ValueList>
                                    <rim:Value>Carolina Kidney Associates</rim:Value>
                                </rim:ValueList>
                            </rim:Slot>
                            <rim:Slot name="authorRole">
                                <rim:ValueList>
                                    <rim:Value>Medical Doctor</rim:Value>
                                </rim:ValueList>
                            </rim:Slot>
                            <rim:Slot name="authorSpecialty">
                                <rim:ValueList>
                                    <rim:Value>General Medicine</rim:Value>
                                </rim:ValueList>
                            </rim:Slot>
                        </rim:Classification>

*/
	var XMLmessage = SerializerFactory.getSerializer('XML').fromXML(tmp);
	return XMLmessage;
}
