/* 
Name: 	filter_validation_hl7_message.js
Desc: 	Filter Incoming HL7v2 Message - ChannelMap
Created:	2021-02-12 
Modified:
*/
var scriptName= "filter_validation_hl7_message";
var debug= 0;
var msgCtrlId= msg['MSH']['MSH.10']['MSH.10.1'].toString();
var msgDateTime= msg['MSH']['MSH.7']['MSH.7.1'].toString();
if(msgCtrlId.toString().length< 1) {
	msgCtrlId= msgDateTime;
}

if (debug>0) { logger.debug(scriptName+"::DEBUG received msg: "+msg); }
// Verify PID-3 element value
if(msg['PID']['PID.3']['PID.3.1'].toString().length > 0) {
    if (debug>0) { logger.debug(scriptName+"::DEBUG message PASSED filter successfully"); }
} else {
	var failureMessage= 'PID-3 Element: Missing Patient ID';
	logger.error(scriptName+"::DEBUG message: "+msgCtrlId+" FILTERED msg: "+failureMessage);
}
// Verify PID-18 element value
if(msg['PID']['PID.18']['PID.18.1'].toString().length > 0) {
    if (debug>0) { logger.debug(scriptName+"::DEBUG message PASSED filter successfully"); }
} else {
	var failureMessage= 'PID-18 Element: Missing Account Number';
	logger.error(scriptName+"::DEBUG message: "+msgCtrlId+" FILTERED msg: "+failureMessage);
}
// Verify PV1-19 element value
/*
if(msg['PV1']['PV1.19']['PID.19.1'].toString().length > 0) {
    if (debug>0) { logger.debug(scriptName+"::DEBUG message PASSED filter successfully"); }
} else {
	var failureMessage= 'PV1-19 Element: Missing Visit Number';
	logger.error(scriptName+"::DEBUG message: "+msgCtrlId+" FILTERED msg: "+failureMessage);
}
*/
if(failureMessage) {
	$c('rejectMessage',failureMessage);
	$c('failureMessage',failureMessage);
	return false;
} else {
    return true;
}

com.mirth.connect.plugins.javascriptstep.script:
/*
	scriptName:	Create JSON from ADT message
	desc:		This script handle the transformation of all ADT message including the addition of A17 and A40 into the desire JSON format - no JSON template
	date:		2022-02-17 
	history:
	2022-03-01 JER Modified from patientSecondary back to patient per Zhivko request
	2022-02-28 JER Modified object name property of message from secondaryPatient to patientSecondary message.secondaryPatient= patient;
	2022-02-25 JER Modified to leverage json_pid_patient, json_pid_pv1, json_mrg_patient JS Code templates
	2022-02-22 JER For A40 Moved the survivorPatient object initialization before the for each PID.3 element XMl list
	2022-02-22 JER For A17 Moved patients array outside of the evaluation of PID segment
	2022-02-22 JER For A17 Added handling of circumstances where PID-3.5 or MRG-1.5 is not provided (aka undefined) per received HL7 data different facilities
	2022-02-17 JER	Added the Patient Swap (A17) messages to handle the latest RESTful JSON payload for patient swap events
	2022-02-17 JER Added the Patient Merge (A40) message to handle the latest RESTful JSON payload for patient merge events 
*/
var scriptName= "MRN & EMPI Patient Identifier";
var message = {};

//Create JSON Object from HL7 Message.
var messageType= msg['MSH']['MSH.9']['MSH.9.1'].toString()+'-'+msg['MSH']['MSH.9']['MSH.9.2'].toString();
message.messageType= messageType;
message.eventType=   msg['MSH']['MSH.9']['MSH.9.2'].toString();
channelMap.put('organization',msg['MSH']['MSH.4']['MSH.4.1'].toString());
// message.id= UUIDGenerator.getUUID();
channelMap.put('uuid',UUIDGenerator.getUUID());
channelMap.put('msgCntrlID',msg['MSH']['MSH.10']['MSH.10.1'].toString());

var patients= [];

if(msg['MSH']['MSH.9']['MSH.9.2'].toString().indexOf('A17')>-1) {

	var primaryPatient= true;
	// var patients= [];

	for(var j=0; j< msg['PID'].length(); j++) {

		if(j> 0) {
			var primaryPatient= false;	
		}

		var patient= {};
		patient= json_pid_patient(msg['PID'][j], msg['PV1'][j]);

		patient.recordType= 'ADT';
		patient.type= 'swap';
		patients.push(patient);
	}
	message.patient= patients;

} else if(msg['MSH']['MSH.9']['MSH.9.2'].toString().indexOf('A40')>-1) {


	var survivorPatient= {};
	survivorPatient= json_pid_patient(msg['PID'], msg['PV1']);
	var firstName= msg['PID']['PID.5']['PID.5.2'].toString();
	var middleName= msg['PID']['PID.5']['PID.5.3'].toString();
	var lastName= msg['PID']['PID.5']['PID.5.1'].toString();
	survivorPatient.recordType= 'ADT';
	survivorPatient.type= 'survivor';

	// 2022-03-01 jer - Modified from patientSecondary back to patient per Zhivko request
	// message.patient= survivorPatient;
	patients.push(survivorPatient);

	var victimPatient= {};
	victimPatient= json_mrg_patient(msg['MRG']);
	victimPatient.recordType= 'ADT';
	victimPatient.type= 'victim';

	patients.push(victimPatient);
	message.patient= patients;

} else {

	var patient= {};
	patient= json_pid_patient(msg['PID'], msg['PV1']);
	patient.recordType= 'ADT';
	// patient.type= 'update';
	//

	// 2022-03-04 jer - Required to make this change to characterize this as a patient array
	patients.push(patient);
	message.patient= patients;

}

tmp= JSON.stringify(message);
channelMap.put('json_msg',tmp);
channelMap.put('msg', tmp);

// 2022-02-15 jer - Added these channelMap to populate the custom Metadata view on Mirth Dashboard
channelMap.put('messageType',messageType);
channelMap.put('msgCtrlId',msg['MSH']['MSH.10']['MSH.10.1'].toString());

