/** callGetPID(objPID)

  	- Desc: This transformation function evaluates the objPID Object

	Modified:

   	@param {object} objPID - input the objPID object instance
	@return {object} msg - return entire msg
**/

var scriptName= 'callGetPID';
var debug= new Boolean(false);

if (typeof(getPID(msg)) !== undefined) {

	var objPID= getPID(msg);
	if(debug== true) { 
		channelMap.put('objPID',JSON.stringify(objPID, null, 4)); 
	}
	// channelMap.put('patientId',objPID.internalId.id.toString());

for (var key in objPID) {
	// skip loop if the property is from prototype
	if(debug== true) { logger.error(scriptName+'::DEBUG:key= '+key); }

	if (!objPID.hasOwnProperty(key)) continue;
	var obj= objPID[key];
	if(debug== true) { logger.error(scriptName+'::DEBUG:objPID[key]= '+JSON.stringify(objPID[key])+' key= '+JSON.stringify(key)); }

    	for (var prop in obj) {

		if(debug== true) { 
			logger.error(scriptName+'::DEBUG[1]:objProp= '+obj[prop]+' typeof= '+typeof(obj[prop]));
			logger.error(scriptName+'::DEBUG[2]:prop= '+prop+' typeof= '+typeof(prop)); 
		}

		if(typeof(prop)== 'object') {
        		// skip loop if the property is from prototype
        		if(debug== true) { 
        			logger.error(scriptName+'::DEBUG[3.1]:objProp: '+JSON.stringify(prop)); 
        		}
        		if(!obj.hasOwnProperty(prop)) continue;
    		} else {
    			var objProp= prop;
    			if(debug== true) { 
    				logger.error(scriptName+'::DEBUG[3.2.1]:objProp: '+JSON.stringify(objProp)+' value: '+JSON.stringify(obj[prop]));
    				logger.error(scriptName+'::DEBUG[3.2.2]:objProp.type: '+typeof(objProp));
    				logger.error(scriptName+'::DEBUG[3.2.3]:objProp.hasOwnProperty: '+obj.hasOwnProperty(objProp));
    			}
    			// [2019-04-07 11:01:04,596]  ERROR  (transformer:?): callGetPID::DEBUG[3.2]:objProp: "internalId" value: [{"id":"3878477"}]
			if(typeof(objProp) =='object') {
				if(!obj.hasOwnProperty(objProp)) continue;
				if(debug== true) { 
					logger.error(scriptName+'DEBUG[3.3.1]::objProp: '+JSON.stringify(objProp)); 
				}
    			} else if (typeof(objProp) =='string' && objProp.toString()== 'internalId') {
				if(debug== true) {
					logger.error(scriptName+'DEBUG[3.3.2]::obj[prop] type: '+typeof(obj[prop]));
    					logger.error(scriptName+'DEBUG[3.3.3]::objProp length: '+objProp.toString().length+' string: '+objProp.toString());
				}
    				if (typeof(obj[prop]) == 'object') {
					for (var subObjProp in obj[prop]) {
						if(debug== true) {
    							logger.error(scriptName+'::DEBUG[3.3.3.1].typeof: '+typeof(subObjProp)+' value: '+subObjProp); 
    						// +JSON.stringify(objProp)+' value: '+JSON.stringify(obj[prop]));
						}
					}
    				}
    			}
			for (var propKey in objProp) {
				if(typeof(propKey)== 'object') {		    				
					if(!objProp.hasOwnProperty(propKey)) continue;
					if(debug== true) { 
						logger.error(scriptName+'::DEBUG[4]:'+propKey + "= " + objProp[propKey]+' typeof= '+typeof(objProp[propKey])); 
					}
				} // end if
			} // end for
    		} // end if
    	} // end for
} // end for

} // end if

