for(var i= 0;i<msg['OBX'].length();i++) {
	for each(element in removeFooter) {
		if(typeof(msg['OBX'][i]) !== undefined) {
			if(debug== true) {
				logger.error('');
				logger.error('DEBUG::'+scriptName+ 'msg[OBX]['+i+']: '+JSON.stringify(msg['OBX'][i]));
