// 2019-04-10 JER - Re-number the segment sequences in OBX.1
var j= 1;
for(var i= 0;i<newTmp['OBX'].length();i++) {
	newTmp['OBX'][i]['OBX.1']['OBX.1.1']= j;
	// 2019-04-08 JER - left trim to left justify each OBX.5 sequence element value
	newTmp['OBX'][i]['OBX.5']['OBX.5.1']= newTmp['OBX'][i]['OBX.5']['OBX.5.1'].toString().replace(/^\s+/,"");
	j++;
}

var outMessage= SerializerFactory.getSerializer('HL7V2').fromXML(newTmp);
// logger.error('AFTER remove arrOBXRemoveLines segments message: '+outMessage);
channelMap.put('outMessage',outMessage);

