	try
	{
		var dbConnTranslationsCh = globalChannelMap.get("dbConnTranslationsCh");

		if (dbConnTranslationsCh == null || dbConnTranslationsCh.getConnection().isClosed())
		{
			dbConnTranslationsCh = DatabaseConnectionFactory.createDatabaseConnection($('dbDriverMsSql'),$('dbUrlTranslations'),$('dbUserFhir'),$('dbPwFhir'));
			globalChannelMap.put("dbConnTranslationsCh", dbConnTranslationsCh);
		}

		if (paramList == undefined)
		{
			paramList = new Packages.java.util.ArrayList();
		}

		if (isQuery)
		{
			result = dbConnTranslationsCh.executeCachedQuery(statement, paramList);
		}
		else
		{
			result = dbConnTranslationsCh.executeUpdate(statement, paramList);
		}
	}
	catch (e)
	{
		logger.error(e);
	}
	finally
	{
		return result;
	}
}

