function sendMessageToChannelDestination(channelID, destinationID, map, data) {
	var msgMap;
	var outData = {};

	if (!map) {
		msgMap = Maps.map();
	} else {
		msgMap = Maps.map(map);
	}

	if (data == null || data == undefined) {
		data = '';
	}

	if (msgMap.get('sourceChannelId') == null) {
		msgMap.add('sourceChannelId', channelId);
	}
	if (msgMap.get('sourceChannelName') == null) {
		msgMap.add('sourceChannelName', channelName);
	}

	// Dirty way to handle connector message not being defined. Handles cases where this function 
	// is used in a source js writer.
	try {
		if (msgMap.get('sourceMessageId') == null && connectorMessage && connectorMessage.getMessageId()) {
			msgMap.add('sourceMessageId', connectorMessage.getMessageId().toString());
		}
	} catch(ex) {}

	var msgRequest = new RawMessage(data, [destinationID], msgMap);
	var msgResponse = router.routeMessageByChannelId(channelID, msgRequest);

	if (msgResponse == null) {
		// return;
		throw ChannelUtil.getChannelName(channelID) + '" does not return a valid response object'; 
	}
	if (msgResponse.getStatus() == null) {
		throw ChannelUtil.getChannelName(channelID) + '" does not return a valid response object';
	}

	if (msgResponse.getStatus() == 'ERROR') {
		throw msgResponse.getMessage();
	}

	return msgResponse.getMessage();


