				}
				if (Object.keys(objIN1[index].policyDeductible).length == 0) {
					delete objIN1[index].policyDeductible;
				}

				// IN1.38, policyLimitAmount
				objIN1[index].policyLimitAmount = {};
				objIN1[index].policyLimitAmount.price = {};
				if (seg['IN1.38']['IN1.38.1']['IN1.38.1.1'].toString() != "") { objIN1[index].policyLimitAmount.price.quantity = seg['IN1.38']['IN1.38.1']['IN1.38.1.1']; }
				if (seg['IN1.38']['IN1.38.1']['IN1.38.1.2'].toString() != "") { objIN1[index].policyLimitAmount.price.denomination = seg['IN1.38']['IN1.38.1']['IN1.38.1.2']; }
				if (Object.keys(objIN1[index].policyLimitAmount.price).length == 0) {
					if (seg['IN1.38']['IN1.38.1'].toString() != "") { objIN1[index].policyLimitAmount.price.quantity = seg['IN1.38']['IN1.38.1']; }
					else delete objIN1[index].policyLimitAmount.price;
				}
				if (seg['IN1.38']['IN1.38.2'].toString() != "") { objIN1[index].policyLimitAmount.priceType = seg['IN1.38']['IN1.38.2']; }
				if (seg['IN1.38']['IN1.38.3'].toString() != "") { objIN1[index].policyLimitAmount.fromValue = seg['IN1.38']['IN1.38.3']; }
				if (seg['IN1.38']['IN1.38.4'].toString() != "") { objIN1[index].policyLimitAmount.toValue = seg['IN1.38']['IN1.38.4']; }
				if (seg['IN1.38']['IN1.38.5'].toString() != "") { objIN1[index].policyLimitAmount.rangeUnits = seg['IN1.38']['IN1.38.5']; }
				objIN1[index].policyLimitAmount.rangeType = {};
				if (seg['IN1.38']['IN1.38.6']['IN1.38.6.1'].toString() != "") { objIN1[index].policyLimitAmount.rangeType.id = seg['IN1.38']['IN1.38.6']['IN1.38.6.1']; }
				if (seg['IN1.38']['IN1.38.6']['IN1.38.6.2'].toString() != "") { objIN1[index].policyLimitAmount.rangeType.text = seg['IN1.38']['IN1.38.6']['IN1.38.6.2']; }
				if (seg['IN1.38']['IN1.38.6']['IN1.38.6.3'].toString() != "") { objIN1[index].policyLimitAmount.rangeType.codingSystem = seg['IN1.38']['IN1.38.6']['IN1.38.6.3']; }
				if (seg['IN1.38']['IN1.38.6']['IN1.38.6.4'].toString() != "") { objIN1[index].policyLimitAmount.rangeType.altId = seg['IN1.38']['IN1.38.6']['IN1.38.6.4']; }
				if (seg['IN1.38']['IN1.38.6']['IN1.38.6.5'].toString() != "") { objIN1[index].policyLimitAmount.rangeType.altText = seg['IN1.38']['IN1.38.6']['IN1.38.6.5']; }
				if (seg['IN1.38']['IN1.38.6']['IN1.38.6.6'].toString() != "") { objIN1[index].policyLimitAmount.rangeType.altCodingSystem = seg['IN1.38']['IN1.38.6']['IN1.38.6.6']; }
				if (Object.keys(objIN1[index].policyLimitAmount.rangeType).length == 0) {
					if (seg['IN1.38']['IN1.38.6'].toString() != "") { objIN1[index].policyLimitAmount.rangeType.id = seg['IN1.38']['IN1.38.6']; }
					else delete objIN1[index].policyLimitAmount.rangeType;
				}
				if (Object.keys(objIN1[index].policyLimitAmount).length == 0) {
					delete objIN1[index].policyLimitAmount;
				}

				// IN1.39, policyLimitDays
				if (seg['IN1.39']['IN1.39.1'].toString() != "") { objIN1[index].policyLimitDays = seg['IN1.39']['IN1.39.1']; }

				// IN1.40, roomRateSemiPrivate
				objIN1[index].roomRateSemiPrivate = {};
				objIN1[index].roomRateSemiPrivate.price = {};
				if (seg['IN1.40']['IN1.40.1']['IN1.40.1.1'].toString() != "") { objIN1[index].roomRateSemiPrivate.price.quantity = seg['IN1.40']['IN1.40.1']['IN1.40.1.1']; }
				if (seg['IN1.40']['IN1.40.1']['IN1.40.1.2'].toString() != "") { objIN1[index].roomRateSemiPrivate.price.denomination = seg['IN1.40']['IN1.40.1']['IN1.40.1.2']; }
				if (Object.keys(objIN1[index].roomRateSemiPrivate.price).length == 0) {
					if (seg['IN1.40']['IN1.40.1'].toString() != "") { objIN1[index].roomRateSemiPrivate.price.quantity = seg['IN1.40']['IN1.40.1']; }
					else delete objIN1[index].roomRateSemiPrivate.price;
				}
				if (seg['IN1.40']['IN1.40.2'].toString() != "") { objIN1[index].roomRateSemiPrivate.priceType = seg['IN1.40']['IN1.40.2']; }
				if (seg['IN1.40']['IN1.40.3'].toString() != "") { objIN1[index].roomRateSemiPrivate.fromValue = seg['IN1.40']['IN1.40.3']; }
				if (seg['IN1.40']['IN1.40.4'].toString() != "") { objIN1[index].roomRateSemiPrivate.toValue = seg['IN1.40']['IN1.40.4']; }
				if (seg['IN1.40']['IN1.40.5'].toString() != "") { objIN1[index].roomRateSemiPrivate.rangeUnits = seg['IN1.40']['IN1.40.5']; }
				objIN1[index].roomRateSemiPrivate.rangeType = {};
				if (seg['IN1.40']['IN1.40.6']['IN1.40.6.1'].toString() != "") { objIN1[index].roomRateSemiPrivate.rangeType.id = seg['IN1.40']['IN1.40.6']['IN1.40.6.1']; }
				if (seg['IN1.40']['IN1.40.6']['IN1.40.6.2'].toString() != "") { objIN1[index].roomRateSemiPrivate.rangeType.text = seg['IN1.40']['IN1.40.6']['IN1.40.6.2']; }
				if (seg['IN1.40']['IN1.40.6']['IN1.40.6.3'].toString() != "") { objIN1[index].roomRateSemiPrivate.rangeType.codingSystem = seg['IN1.40']['IN1.40.6']['IN1.40.6.3']; }
				if (seg['IN1.40']['IN1.40.6']['IN1.40.6.4'].toString() != "") { objIN1[index].roomRateSemiPrivate.rangeType.altId = seg['IN1.40']['IN1.40.6']['IN1.40.6.4']; }
				if (seg['IN1.40']['IN1.40.6']['IN1.40.6.5'].toString() != "") { objIN1[index].roomRateSemiPrivate.rangeType.altText = seg['IN1.40']['IN1.40.6']['IN1.40.6.5']; }
				if (seg['IN1.40']['IN1.40.6']['IN1.40.6.6'].toString() != "") { objIN1[index].roomRateSemiPrivate.rangeType.altCodingSystem = seg['IN1.40']['IN1.40.6']['IN1.40.6.6']; }
				if (Object.keys(objIN1[index].roomRateSemiPrivate.rangeType).length == 0) {
					if (seg['IN1.40']['IN1.40.6'].toString() != "") { objIN1[index].roomRateSemiPrivate.rangeType.id = seg['IN1.40']['IN1.40.6']; }
					else delete objIN1[index].roomRateSemiPrivate.rangeType;
				}
				if (Object.keys(objIN1[index].roomRateSemiPrivate).length == 0) {
					delete objIN1[index].roomRateSemiPrivate;
				}

				// IN1.41, roomRatePrivate
				objIN1[index].roomRatePrivate = {};
				objIN1[index].roomRatePrivate.price = {};
				if (seg['IN1.41']['IN1.41.1']['IN1.41.1.1'].toString() != "") { objIN1[index].roomRatePrivate.price.quantity = seg['IN1.41']['IN1.41.1']['IN1.41.1.1']; }
				if (seg['IN1.41']['IN1.41.1']['IN1.41.1.2'].toString() != "") { objIN1[index].roomRatePrivate.price.denomination = seg['IN1.41']['IN1.41.1']['IN1.41.1.2']; }
				if (Object.keys(objIN1[index].roomRatePrivate.price).length == 0) {
					if (seg['IN1.41']['IN1.41.1'].toString() != "") { objIN1[index].roomRatePrivate.price.quantity = seg['IN1.41']['IN1.41.1']; }
					else delete objIN1[index].roomRatePrivate.price;
				}
				if (seg['IN1.41']['IN1.41.2'].toString() != "") { objIN1[index].roomRatePrivate.priceType = seg['IN1.41']['IN1.41.2']; }
				if (seg['IN1.41']['IN1.41.3'].toString() != "") { objIN1[index].roomRatePrivate.fromValue = seg['IN1.41']['IN1.41.3']; }
				if (seg['IN1.41']['IN1.41.4'].toString() != "") { objIN1[index].roomRatePrivate.toValue = seg['IN1.41']['IN1.41.4']; }
				if (seg['IN1.41']['IN1.41.5'].toString() != "") { objIN1[index].roomRatePrivate.rangeUnits = seg['IN1.41']['IN1.41.5']; }
				objIN1[index].roomRatePrivate.rangeType = {};
				if (seg['IN1.41']['IN1.41.6']['IN1.41.6.1'].toString() != "") { objIN1[index].roomRatePrivate.rangeType.id = seg['IN1.41']['IN1.41.6']['IN1.41.6.1']; }
				if (seg['IN1.41']['IN1.41.6']['IN1.41.6.2'].toString() != "") { objIN1[index].roomRatePrivate.rangeType.text = seg['IN1.41']['IN1.41.6']['IN1.41.6.2']; }
				if (seg['IN1.41']['IN1.41.6']['IN1.41.6.3'].toString() != "") { objIN1[index].roomRatePrivate.rangeType.codingSystem = seg['IN1.41']['IN1.41.6']['IN1.41.6.3']; }
				if (seg['IN1.41']['IN1.41.6']['IN1.41.6.4'].toString() != "") { objIN1[index].roomRatePrivate.rangeType.altId = seg['IN1.41']['IN1.41.6']['IN1.41.6.4']; }
				if (seg['IN1.41']['IN1.41.6']['IN1.41.6.5'].toString() != "") { objIN1[index].roomRatePrivate.rangeType.altText = seg['IN1.41']['IN1.41.6']['IN1.41.6.5']; }
				if (seg['IN1.41']['IN1.41.6']['IN1.41.6.6'].toString() != "") { objIN1[index].roomRatePrivate.rangeType.altCodingSystem = seg['IN1.41']['IN1.41.6']['IN1.41.6.6']; }
				if (Object.keys(objIN1[index].roomRatePrivate.rangeType).length == 0) {
					if (seg['IN1.41']['IN1.41.6'].toString() != "") { objIN1[index].roomRatePrivate.rangeType.id = seg['IN1.41']['IN1.41.6']; }
					else delete objIN1[index].roomRatePrivate.rangeType;
				}
				if (Object.keys(objIN1[index].roomRatePrivate).length == 0) {
					delete objIN1[index].roomRatePrivate;
				}

				// IN1.42, insuredEmploymentStatus
				objIN1[index].insuredEmploymentStatus = {};
				if (seg['IN1.42']['IN1.42.1'].toString() != "") { objIN1[index].insuredEmploymentStatus.id = seg['IN1.42']['IN1.42.1']; }
				if (seg['IN1.42']['IN1.42.2'].toString() != "") { objIN1[index].insuredEmploymentStatus.text = seg['IN1.42']['IN1.42.2']; }
				if (seg['IN1.42']['IN1.42.3'].toString() != "") { objIN1[index].insuredEmploymentStatus.codingSystem = seg['IN1.42']['IN1.42.3']; }
				if (seg['IN1.42']['IN1.42.4'].toString() != "") { objIN1[index].insuredEmploymentStatus.altId = seg['IN1.42']['IN1.42.4']; }
				if (seg['IN1.42']['IN1.42.5'].toString() != "") { objIN1[index].insuredEmploymentStatus.altText = seg['IN1.42']['IN1.42.5']; }
				if (seg['IN1.42']['IN1.42.6'].toString() != "") { objIN1[index].insuredEmploymentStatus.altCodingSystem = seg['IN1.42']['IN1.42.6']; }
				if (Object.keys(objIN1[index].insuredEmploymentStatus).length == 0) {
					delete objIN1[index].insuredEmploymentStatus;
				}

				// IN1.43, insuredSex
				if (seg['IN1.43']['IN1.43.1'].toString() != "") { objIN1[index].insuredSex = seg['IN1.43']['IN1.43.1']; }

				// IN1.44, insuredEmployerAddress
				objIN1[index].insuredEmployerAddress = [];
				for each (field in seg['IN1.44']) {
					var block = {};
					if (field['IN1.44.1'].toString() != "") { block.streetAddress = field['IN1.44.1']; }
					if (field['IN1.44.2'].toString() != "") { block.otherDesignation = field['IN1.44.2']; }
					if (field['IN1.44.3'].toString() != "") { block.city = field['IN1.44.3']; }
					if (field['IN1.44.4'].toString() != "") { block.stateOrProvince = field['IN1.44.4']; }
					if (field['IN1.44.5'].toString() != "") { block.zip = field['IN1.44.5']; }
					if (field['IN1.44.6'].toString() != "") { block.country = field['IN1.44.6']; }
					if (field['IN1.44.7'].toString() != "") { block.addressType = field['IN1.44.7']; }
					if (field['IN1.44.8'].toString() != "") { block.otherGeographicDesignation = field['IN1.44.8']; }
					if (field['IN1.44.9'].toString() != "") { block.countyOrParishCode = field['IN1.44.9']; }
					if (field['IN1.44.10'].toString() != "") { block.censusTract = field['IN1.44.10']; }
					if (Object.keys(block).length > 0) {
						objIN1[index].insuredEmployerAddress.push(block);
					}
				}
				if (objIN1[index].insuredEmployerAddress.length == 0) {
					delete objIN1[index].insuredEmployerAddress;
				}

				// IN1.45, verificationStatus
				if (seg['IN1.45']['IN1.45.1'].toString() != "") { objIN1[index].verificationStatus = seg['IN1.45']['IN1.45.1']; }

				// IN1.46, priorInsurancePlanId
				if (seg['IN1.46']['IN1.46.1'].toString() != "") { objIN1[index].priorInsurancePlanId = seg['IN1.46']['IN1.46.1']; }

				// IN1.47, coverageType
				if (seg['IN1.47']['IN1.47.1'].toString() != "") { objIN1[index].coverageType = seg['IN1.47']['IN1.47.1']; }

				// IN1.48, handicap
				if (seg['IN1.48']['IN1.48.1'].toString() != "") { objIN1[index].handicap = seg['IN1.48']['IN1.48.1']; }

				// IN1.49, insuredIdNumber
				objIN1[index].insuredIdNumber = [];
				for each (field in seg['IN1.49']) {
					var block = {};
					if (field['IN1.49.1'].toString() != "") { block.id = field['IN1.49.1']; }
					if (field['IN1.49.2'].toString() != "") { block.identifierCheckDigit = field['IN1.49.2']; }
					if (field['IN1.49.3'].toString() != "") { block.codeIdentifyingCheckDigitScheme = field['IN1.49.3']; }
					block.assigningAuthority = {};
					if (field['IN1.49.4']['IN1.49.4.1'].toString() != "") { block.assigningAuthority.namespaceId = field['IN1.49.4']['IN1.49.4.1']; }
					if (field['IN1.49.4']['IN1.49.4.2'].toString() != "") { block.assigningAuthority.universalId = field['IN1.49.4']['IN1.49.4.2']; }
					if (field['IN1.49.4']['IN1.49.4.3'].toString() != "") { block.assigningAuthority.universalIdType = field['IN1.49.4']['IN1.49.4.3']; }
					if (Object.keys(block.assigningAuthority).length == 0) {
						if (field['IN1.49.4'].toString() != "") { block.assigningAuthority.namespaceId = field['IN1.49.4']; }
						else delete block.assigningAuthority;
					}
					if (field['IN1.49.5'].toString() != "") { block.idTypeCode = field['IN1.49.5']; }
					block.assigningFacility = {};
					if (field['IN1.49.6']['IN1.49.6.1'].toString() != "") { block.assigningFacility.namespaceId = field['IN1.49.6']['IN1.49.6.1']; }
					if (field['IN1.49.6']['IN1.49.6.2'].toString() != "") { block.assigningFacility.universalId = field['IN1.49.6']['IN1.49.6.2']; }
					if (field['IN1.49.6']['IN1.49.6.3'].toString() != "") { block.assigningFacility.universalIdType = field['IN1.49.6']['IN1.49.6.3']; }
					if (Object.keys(block.assigningFacility).length == 0) {
						if (field['IN1.49.6'].toString() != "") { block.assigningFacility.namespaceId = field['IN1.49.6']; }
						else delete block.assigningFacility;
					}
					if (Object.keys(block).length > 0) {
						objIN1[index].insuredIdNumber.push(block);
					}
				}
				if (objIN1[index].insuredIdNumber.length == 0) {
					delete objIN1[index].insuredIdNumber;
				}

				// clean up IN1 and increment index
				if (Object.keys(objIN1[index]).length == 0) {
					delete objIN1[index];
				} else {
					index++;
				}
			}
		}
		return objIN1;	
	} // end if 
}

/**
	getMSH_v23(msg)

	- Desc: This function retrieves HL7v2 HAPI format elements and values from MSH segment stored in
	object for reference

	@param {String} msg - this contains the raw message (string)
	@return {object} objMSH - this contains the MSH segment elements composed into a single objMSH
		object.
*/
function getMSH_v23(msg) {

	var scriptName= 'getMSH_v23';
	var debug= 0;
	if (debug>0) { logger.error(scriptName+'::DEBUG msg.MSH: '+JSON.stringify(msg['MSH'].toString())); }

	// define variables
	if (typeof(msg['MSH'].toString()) !== undefined) {

		// MSH
		// var objMSH= new Object();
		var objMSH= {};

		// MSH.1, fieldSeparator
		if (msg['MSH']['MSH.1'].toString() != "") { objMSH.fieldSeparator = msg['MSH']['MSH.1'].toString(); }

		// objMSH.2, encodingCharacters
		if (msg['MSH']['MSH.2'].toString() != "") { objMSH.subFieldSeparator = msg['MSH']['MSH.2'].toString(); }

		// objMSH.3, sendingApplication
		if (msg['MSH']['MSH.3']['MSH.3.1'].toString() != "") { objMSH.sendingApplication = msg['MSH']['MSH.3']['MSH.3.1'].toString(); }

		// objMSH.4, sendingFacility
		if (msg['MSH']['MSH.4']['MSH.4.1'].toString() != "") { objMSH.sendingFacility= msg['MSH']['MSH.4']['MSH.4.1'].toString(); }

		// objMSH.5, receivingApplication
		if (msg['MSH']['MSH.5']['MSH.5.1'].toString() != "") { objMSH.receivingApplication = msg['MSH']['MSH.5']['MSH.5.1'].toString(); }

		// objMSH.6, receivingFacility
		if (msg['MSH']['MSH.6']['MSH.6.1'].toString() != "") { objMSH.receivingFacility = msg['MSH']['MSH.6']['MSH.6.1'].toString(); }

		// objMSH.7, dateTimeOfMessage
		if (msg['MSH']['MSH.7']['MSH.7.1'].toString() != "") { objMSH.messageDateTime = msg['MSH']['MSH.7']['MSH.7.1'].toString(); }

		// objMSH.8, security
		if (msg['MSH']['MSH.8']['MSH.8.1'].toString() != "") { objMSH.security = msg['MSH']['MSH.8']['MSH.8.1'].toString(); }

		// objMSH.9, messageType
		if (msg['MSH']['MSH.9'].toString() != "") {
			objMSH.messageType = msg['MSH']['MSH.9']['MSH.9.1'].toString();
			objMSH.eventType   = msg['MSH']['MSH.9']['MSH.9.2'].toString();
			// logger.error(scriptName+'::DEBUG objMSH_messageType: '+objMSH.messageType+' objMSH_eventType: '+objMSH.eventType);
		}

		// objMSH.10, messageControlID
		if (msg['MSH']['MSH.10']['MSH.10.1'].toString() != "") { 
			objMSH.messageControlID = msg['MSH']['MSH.10']['MSH.10.1'].toString(); 
		}

		// objMSH.11, processingID
		if (msg['MSH']['MSH.11']['MSH.11.1'].toString() != "") {
			objMSH.processingID = msg['MSH']['MSH.11']['MSH.11.1'].toString(); 
		}

		// objMSH.12, versionID
		if (msg['MSH']['MSH.12']['MSH.12.1'].toString() != "") { objMSH.hl7version = msg['MSH']['MSH.12']['MSH.12.1'].toString(); }

		// objMSH.13, sequenceNumber
		if (msg['MSH']['MSH.13']['MSH.13.1'].toString() != "") { objMSH.sequenceNumber = msg['MSH']['MSH.13']['MSH.13.1'].toString(); }

		// objMSH.14, continuationPointer
		if (msg['MSH']['MSH.14']['MSH.14.1'].toString() != "") { objMSH.continuationPointer = msg['MSH']['MSH.14']['MSH.14.1'].toString(); }

		// objMSH.15, acceptAcknowledgmentType
		if (msg['MSH']['MSH.15']['MSH.15.1'].toString() != "") { objMSH.acceptAcknowledgmentType = msg['MSH']['MSH.15']['MSH.15.1'].toString(); }

		// objMSH.16, applicationAcknowledgmentType
		if (msg['MSH']['MSH.16']['MSH.16.1'].toString() != "") { objMSH.applicationAcknowledgmentType = msg['MSH']['MSH.16']['MSH.16.1'].toString(); }

		// objMSH.17, countryCode
		if (msg['MSH']['MSH.17']['MSH.17.1'].toString() != "") { objMSH.countryCode = msg['MSH']['MSH.17']['MSH.17.1'].toString(); }

		// objMSH.18, characterSet
		if (msg['MSH']['MSH.18']['MSH.18.1'].toString() != "") { objMSH.characterSet= msg['MSH']['MSH.18']['MSH.18.1'].toString(); }

		// objMSH.19, principalLanguageOfMessage
		if (msg['MSH']['MSH.19']['MSH.19.1'].toString() != "") { objMSH.principalLanguageOfMessage = msg['MSH']['MSH.19']['MSH.19.1'].toString(); }

		if (debug>0) { 
			logger.error(scriptName+'::DEBUG objMSH: '+JSON.stringify(objMSH)); 
		}

		return objMSH;
	}
}

/**
	getPD1_v23(msg)

	- Desc: This function retrieves HL7v2 HAPI format elements and values from PD1 segment stored in
	object for reference

	@param {String} msg - this contains the raw message (string)
	@return {object} objPD1 - this contains the PD1 segment elements composed into a single objPD1 object.
*/
function getPD1_v23(msg) {

	// define variables
	if (typeof(msg['PD1']) !== undefined) {

		// PD1	
		var objPD1= new Object();

		if (msg['PD1'].length() > 0) {
			var index = 0;
			for each (seg in msg.PD1) {

				// PD1.1, Living Dependency
				if (seg['PD1.1'].toString() != '') { objPD1.livingDependency= seg['PD1.1'].toString(); }

				// PD1.2, Living Arrangement
				if (seg['PD1.2'].toString() != '') { objPD1.livingArrangement= seg['PD1.2'].toString(); }

				// PD1.3, Patient Primary Facility
				if(typeof(seg['PD1.3']) !== 'undefined') {

					var objPatientPrimaryFacility= new Object();
						if (seg['PD1.3']['PD1.3.1'].toString() != "") { objPatientPrimaryFacility.organizationName= seg['PD1.3']['PD1.3.1'].toString(); }
						if (seg['PD1.3']['PD1.3.2'].toString() != "") { objPatientPrimaryFacility.organizationNameTypeCode= seg['PD1.3']['PD1.3.2'].toString(); }
						if (seg['PD1.3']['PD1.3.3'].toString() != "") { objPatientPrimaryFacility.id= seg['PD1.3']['PD1.3.3'].toString(); }
						if (seg['PD1.3']['PD1.3.4'].toString() != "") { objPatientPrimaryFacility.checkDigit= seg['PD1.3']['PD1.3.4'].toString(); }
						if (seg['PD1.3']['PD1.3.5'].toString() != "") { objPatientPrimaryFacility.checkDigitScheme= seg['PD1.3']['PD1.3.5'].toString(); }

					var objAssigningAuthority= new Object();
						if (seg['PD1.3']['PD1.3.6']['PD1.3.6.1'].toString() != "") { objAssigningAuthority.namespaceId= seg['PD1.3']['PD1.3.6']['PD1.3.6.1'].toString(); }
						if (seg['PD1.3']['PD1.3.6']['PD1.3.6.2'].toString() != "") { objAssigningAuthority.universalId= seg['PD1.3']['PD1.3.6']['PD1.3.6.2'].toString(); }
						if (seg['PD1.3']['PD1.3.6']['PD1.3.6.3'].toString() != "") { objAssigningAuthority.universalIdType= seg['PD1.3']['PD1.3.6']['PD1.3.6.3'].toString(); }
						objPatientPrimaryFacility.assigningAuthority= objAssigningAuthority;

					if (seg['PD1.3']['PD1.3.7'].toString() != "") { objPatientPrimaryFacility.identifierTypeCode= seg['PD1.3']['PD1.3.7'].toString(); }

					var objAssigningFacility= new Object();
						if (seg['PD1.3']['PD1.3.8']['PD1.3.8.1'].toString() != "") { objAssigningFacility.namespaceId= seg['PD1.3']['PD1.3.8']['PD1.3.8.1'].toString(); }
						if (seg['PD1.3']['PD1.3.8']['PD1.3.8.2'].toString() != "") { objAssigningFacility.universalId= seg['PD1.3']['PD1.3.8']['PD1.3.8.2'].toString(); }
						if (seg['PD1.3']['PD1.3.8']['PD1.3.8.3'].toString() != "") { objAssigningFacility.universalIdType= seg['PD1.3']['PD1.3.8']['PD1.3.8.3'].toString(); }
						objPatientPrimaryFacility.assigningFacility= objAssigningFacility;

					objPD1.patientPrimaryFacility= objPatientPrimaryFacility;
				}					

				// PD1.4, Primary Care Provider Name &amp; ID
				if(typeof(seg['PD1.4']) !== 'undefined') {
					var objPrimaryCareProviderName= new Object();
					if (seg['PD1.4']['PD1.4.1'].toString() != "") { objPrimaryCareProviderName.id= seg['PD1.4']['PD1.4.1'].toString(); }
					if (seg['PD1.4']['PD1.4.2'].toString() != "") { objPrimaryCareProviderName.familyName= seg['PD1.4']['PD1.4.2'].toString(); }
					if (seg['PD1.4']['PD1.4.3'].toString() != "") { objPrimaryCareProviderName.givenName= seg['PD1.4']['PD1.4.3'].toString(); }
					if (seg['PD1.4']['PD1.4.4'].toString() != "") { objPrimaryCareProviderName.middleName= seg['PD1.4']['PD1.4.4'].toString(); }
					if (seg['PD1.4']['PD1.4.5'].toString() != "") { objPrimaryCareProviderName.suffix= seg['PD1.4']['PD1.4.5'].toString(); }
					if (seg['PD1.4']['PD1.4.6'].toString() != "") { objPrimaryCareProviderName.prefix= seg['PD1.4']['PD1.4.6'].toString(); }
					if (seg['PD1.4']['PD1.4.7'].toString() != "") { objPrimaryCareProviderName.degree= seg['PD1.4']['PD1.4.7'].toString(); }
					if (seg['PD1.4']['PD1.4.8'].toString() != "") { objPrimaryCareProviderName.sourceTable= seg['PD1.4']['PD1.4.8'].toString(); }

					var objAssigningFacility= new Object();
						if (seg['PD1.4']['PD1.4.9']['PD1.4.9.1'].toString() != "") { objAssigningFacility.namespaceId= seg['PD1.4']['PD1.4.9']['PD1.4.9.1'].toString(); }
						if (seg['PD1.4']['PD1.4.9']['PD1.4.9.2'].toString() != "") { objAssigningFacility.universalId= seg['PD1.4']['PD1.4.9']['PD1.4.9.2'].toString(); }
						if (seg['PD1.4']['PD1.4.9']['PD1.4.9.3'].toString() != "") { objAssigningFacility.universalIdType= seg['PD1.4']['PD1.4.9']['PD1.4.9.3'].toString(); }
						objPrimaryCareProviderName.assigningAuthority= objAssigningFacility;

					objPD1.primaryCareProviderName= objPrimaryCareProviderName;
				}

				/*
				// clean up SCH and increment index
				if (Object.keys(objSCH[index]).length == 0) {
					delete objSCH[index];
				} else {
					index++;
				}
				*/

			}
		}

		return objPD1;
	}
}

/**
	getPID_v23(msg)

	- Desc: This function retrieves HL7v2 HAPI format elements and values from PID segment stored in
	object for reference

	@param {String} msg - this contains the raw message (string)
	@return {object} objPID - this contains the PID segment elements composed into a single objMSH
		object.
*/
function getPID_v23(msg) {

	var scriptName= 'getPID_v23';
	var debug= 0;
	if (debug>0) { logger.error(scriptName+'::DEBUG msg.PID: '+JSON.stringify(msg['PID'].toString())); }

	if (debug>0) { logger.error(scriptName+'::DEBUG msg[PID] not undefined= '+(typeof(msg['PID'].toString()) !== undefined)); }

	// define variables
	if (typeof(msg['PID'].toString()) !== undefined) {

		// PID	
		var objPID= {};

		// PID.1, setId
		if (msg['PID']['PID.1']['PID.1.1'].toString() != "") {objPID.setId = msg['PID']['PID.1']['PID.1.1'].toString(); }

		// PID.2, externalPatientId
		if (msg['PID']['PID.2']['PID.2.1'].toString() != "") { objPID.externalPatientId= msg['PID']['PID.2']['PID.2.1'].toString(); }

		// PID.3, patientId
		if (msg['PID']['PID.3']['PID.3.1'].toString() != "") { objPID.patientId= msg['PID']['PID.3']['PID.3.1'].toString(); }

		// PID.4, alternatePatientId
		if (msg['PID']['PID.4']['PID.4.1'].toString() != "") { objPID.alternatePatientId= msg['PID']['PID.4']['PID.4.1'].toString(); }

		// PID.5, patientName
		var objPatientName= {};
		if (msg['PID']['PID.5'].toString().length> 0) {

			if (msg['PID']['PID.5']['PID.5.1'].toString() != "") { objPatientName.lastName = msg['PID']['PID.5']['PID.5.1'].toString(); }
			if (msg['PID']['PID.5']['PID.5.2'].toString() != "") { objPatientName.firstName = msg['PID']['PID.5']['PID.5.2'].toString(); }
			if (msg['PID']['PID.5']['PID.5.3'].toString() != "") { objPatientName.middleName = msg['PID']['PID.5']['PID.5.3'].toString(); }
			if (msg['PID']['PID.5']['PID.5.4'].toString() != "") { objPatientName.suffix = msg['PID']['PID.5']['PID.5.4'].toString(); }
			if (msg['PID']['PID.5']['PID.5.5'].toString() != "") { objPatientName.prefix = msg['PID']['PID.5']['PID.5.5'].toString(); }
			if (msg['PID']['PID.5']['PID.5.6'].toString() != "") { objPatientName.degree = msg['PID']['PID.5']['PID.5.6'].toString(); }
			if (msg['PID']['PID.5']['PID.5.7'].toString() != "") { objPatientName.nameTypeCode = msg['PID']['PID.5']['PID.5.7'].toString(); }

			objPID.patientName= objPatientName;
			channelMap.put('objPID.patientName',objPatientName);
		}	

		// PID.6, mothersMaidenName
		var objMotherMaidenName= {};
		for each (field in msg['PID']['PID.6'].children()) {
			if (field['PID.6.1'].toString() != "") { objMothersMaidenName.lastName = field['PID.6.1'].toString(); }
			if (field['PID.6.2'].toString() != "") { objMothersMaidenName.firstName = field['PID.6.2'].toString(); }
			if (field['PID.6.3'].toString() != "") { objMothersMaidenName.middleName =field['PID.6.3'].toString(); }
			if (field['PID.6.4'].toString() != "") { objMothersMaidenName.suffix = field['PID.6.4'].toString(); }
			if (field['PID.6.5'].toString() != "") { objMothersMaidenName.prefix = field['PID.6.5'].toString(); }
			if (field['PID.6.6'].toString() != "") { objMothersMaidenName.degree = field['PID.6.6'].toString(); }
			if (field['PID.6.7'].toString() != "") { objMothersMaidenName.nameTypeCode = field['PID.6.7'].toString(); }
		}

		if (objMotherMaidenName.length > 0) {
			objPID.motherMaidenName= objMotherMaidenName;
			channelMap.put('objPID.motherMaidenName',objMotherMaidenName);
		}

		// PID.7, dateTimeBirth
		if (msg['PID']['PID.7']['PID.7.1'].toString() != "") {objPID.dateTimeBirth = msg['PID']['PID.7']['PID.7.1'].toString(); }

		// PID.8, gender
		if (msg['PID']['PID.8']['PID.8.1'].toString() != "") {objPID.gender = msg['PID']['PID.8']['PID.8.1'].toString(); }

		// PID.9, alias
		var objPatientAlias= {};
		for each (field in msg['PID']['PID.9'].children()) {
			if (field['PID.9.1'].toString() != "") { objPatientAlias.lastName = field['PID.9.1'].toString(); }
			if (field['PID.9.2'].toString() != "") { objPatientAlias.firstName = field['PID.9.2'].toString(); }
			if (field['PID.9.3'].toString() != "") { objPatientAlias.middleName = field['PID.9.3'].toString(); }
			if (field['PID.9.4'].toString() != "") { objPatientAlias.suffix = field['PID.9.4'].toString(); }
			if (field['PID.9.5'].toString() != "") { objPatientAlias.prefix = field['PID.9.5'].toString(); }
			if (field['PID.9.6'].toString() != "") { objPatientAlias.degree = field['PID.9.6'].toString(); }
			if (field['PID.9.7'].toString() != "") { objPatientAlias.nameTypeCode = field['PID.9.7'].toString(); }
		}
		if (objPatientAlias.length > 0) {
			objPID.patientAlias= objPatientAlias;
			channelMap.put('objPID.patientAlias',objPatientAlias);
		}

		// PID.10, race
		if (msg['PID']['PID.10']['PID.10.1'].toString() != "") {objPID.race = msg['PID']['PID.10']['PID.10.1'].toString(); }

		// PID.11, address
		var objPatientAddress= {};
		if (msg['PID']['PID.11'].toString().length> 0) {

			if (msg['PID']['PID.11']['PID.11.1'].toString().length> 0) { objPatientAddress.streetAddress = msg['PID']['PID.11']['PID.11.1'].toString(); }
			if (msg['PID']['PID.11']['PID.11.2'].toString().length> 0) { objPatientAddress.otherDesignation = msg['PID']['PID.11']['PID.11.2'].toString(); }
			if (msg['PID']['PID.11']['PID.11.3'].toString().length> 0) { objPatientAddress.city = msg['PID']['PID.11']['PID.11.3'].toString(); }
			if (msg['PID']['PID.11']['PID.11.4'].toString().length> 0) { objPatientAddress.stateOrProvince = msg['PID']['PID.11']['PID.11.4'].toString(); }
			if (msg['PID']['PID.11']['PID.11.5'].toString().length> 0) { objPatientAddress.zip = msg['PID']['PID.11']['PID.11.5'].toString(); }
			if (msg['PID']['PID.11']['PID.11.6'].toString().length> 0) { objPatientAddress.country = msg['PID']['PID.11']['PID.11.6'].toString(); }
			if (msg['PID']['PID.11']['PID.11.7'].toString().length> 0) { objPatientAddress.addressType = msg['PID']['PID.11']['PID.11.7'].toString(); }
			if (msg['PID']['PID.11']['PID.11.8'].toString().length> 0) { objPatientAddress.otherGeographicDesignation = msg['PID']['PID.11']['PID.11.8'].toString(); }
			if (msg['PID']['PID.11']['PID.11.9'].toString().length> 0) { objPatientAddress.countyOrParishCode = msg['PID']['PID.11']['PID.11.9'].toString(); }
			if (msg['PID']['PID.11']['PID.11.10'].toString().length> 0) { objPatientAddress.censusTract = msg['PID']['PID.11']['PID.11.10'].toString(); }

			objPID.patientAddress= objPatientAddress;
			channelMap.put('objPID.patientAddress',objPatientAddress);
		}

		// PID.12, countyCode
		if (msg['PID']['PID.12']['PID.12.1'].toString() != "") {objPID.countyCode = msg['PID']['PID.12']['PID.12.1'].toString(); }

		// PID.13, homePhone
		var objHomePhone = {};
		for each (field in msg['PID']['PID.13'].children()) {
			if (field['PID.13.1'].toString() != "") { objHomePhone.number = field['PID.13.1'].toString(); }
			if (field['PID.13.2'].toString() != "") { objHomePhone.telecommunicationUseCode = field['PID.13.2'].toString(); }
			if (field['PID.13.3'].toString() != "") { objHomePhone.telecommunicationEquipmentType = field['PID.13.3'].toString(); }
			if (field['PID.13.4'].toString() != "") { objHomePhone.emailAddress = field['PID.13.4'].toString(); }
			if (field['PID.13.5'].toString() != "") { objHomePhone.countryCode = field['PID.13.5'].toString(); }
			if (field['PID.13.6'].toString() != "") { objHomePhone.areaCityCode = field['PID.13.6'].toString(); }
			if (field['PID.13.7'].toString() != "") { objHomePhone.phoneNumber = field['PID.13.7'].toString(); }
			if (field['PID.13.8'].toString() != "") { objHomePhone.extension = field['PID.13.8'].toString(); }
			if (field['PID.13.9'].toString() != "") { objHomePhone.text = field['PID.13.9'].toString(); }
		}
		if (objHomePhone.length > 0) {
			objPID.homePhone =objHomePhone;
			channelMap.put('objPID.homePhone',objHomePhone);
		}

		// PID.14, businessPhone
		var objBusinessPhone = {};
		for each (field in msg['PID']['PID.14']) {
			if (field['PID.14.1'].toString() != "") { objBusinessPhone.number = field['PID.14.1'].toString(); }
			if (field['PID.14.2'].toString() != "") { objBusinessPhone.telecommunicationUseCode = field['PID.14.2'].toString(); }
			if (field['PID.14.3'].toString() != "") { objBusinessPhone.telecommunicationEquipmentType = field['PID.14.3'].toString(); }
			if (field['PID.14.4'].toString() != "") { objBusinessPhone.emailAddress = field['PID.14.4'].toString(); }
			if (field['PID.14.5'].toString() != "") { objBusinessPhone.countryCode = field['PID.14.5'].toString(); }
			if (field['PID.14.6'].toString() != "") { objBusinessPhone.areaCityCode = field['PID.14.6'].toString(); }
			if (field['PID.14.7'].toString() != "") { objBusinessPhone.phoneNumber = field['PID.14.7'].toString(); }
			if (field['PID.14.8'].toString() != "") { objBusinessPhone.extension = field['PID.14.8'].toString(); }
			if (field['PID.14.9'].toString() != "") { objBusinessPhone.text = field['PID.14.9'].toString(); }
		}
		if (objBusinessPhone.length > 0) {
			objPID.businessPhone= objBusinessPhone;
			channelMap.put('objPID.businessPhone',objBusinessPhone);
		}

		// PID.15, primaryLanguage
		if (msg['PID']['PID.15']['PID.15.1'].toString() != "") { objPID.primaryLanguage = msg['PID']['PID.15']['PID.15.1'].toString(); }

		// PID.16, maritalStatus
		if (msg['PID']['PID.16']['PID.16.1'].toString() != "") { objPID.maritalStatus = msg['PID']['PID.16']['PID.16.1'].toString(); }

		// PID.17, religion
		if (msg['PID']['PID.17']['PID.17.1'].toString() != "") { objPID.religion = msg['PID']['PID.17']['PID.17.1'].toString(); }

		// PID.18, accountNumber
		if (msg['PID']['PID.18']['PID.18.1'].toString() != "") { objPID.accountNumber = msg['PID']['PID.18']['PID.18.1'].toString(); }

		// PID.19, ssn
		if (msg['PID']['PID.19']['PID.19.1'].toString() != "") {objPID.ssn = msg['PID']['PID.19']['PID.19.1'].toString(); }

		// PID.20, driversLicenseNumber
		if (msg['PID']['PID.20']['PID.20.1'].toString() != "") {objPID.driversLicenseNumber = msg['PID']['PID.20']['PID.20.1'].toString(); }

		// PID.21, mothersId
		if (msg['PID']['PID.21']['PID.21.1'].toString() != "") {objPID.mothersId = msg['PID']['PID.21']['PID.21.1'].toString(); }

		// PID.22, ethnicGroup
		if (msg['PID']['PID.22']['PID.22.1'].toString() != "") {objPID.ethnicGroup = msg['PID']['PID.22']['PID.22.1'].toString(); }

		// PID.23, birthPlace
		if (msg['PID']['PID.23']['PID.23.1'].toString() != "") {objPID.birthPlace = msg['PID']['PID.23']['PID.23.1'].toString(); }

		// PID.24, multipleBirthIndicator
		if (msg['PID']['PID.24']['PID.24.1'].toString() != "") {objPID.multipleBirthIndicator = msg['PID']['PID.24']['PID.24.1'].toString(); }

	}
	if (debug>0) { logger.error(scriptName+'::DEBUG objPID: '+JSON.stringify(objPID)); }

	return objPID;
}

/**
	getPV1_v23(msg)

	- Desc: This function retrieves HL7v2 HAPI format elements and values from PV1 segment stored in
	object for reference

	@param {String} msg - this contains the raw message (string)
	@return {object} objPV1 - this contains the PV1 segment elements composed into a single objMSH
		object.
*/
function getPV1_v23(msg) {

	var scriptName= "getPV1_v23";
	var debug= 0;

	// define variables
	if (typeof(msg['PV1']) !== undefined) {

		// PV1	
		var pv1;
		var objPV1= {};

		if (msg['PV1'].toString().length > 0) {

			var index = 0;
			for each (seg in msg.PV1.children()) {

				if(debug> 0) logger.error(scriptName+'::DEBUG seg["+index+"]: '+seg.toString());

				// PV1.1, setId
				if (seg['PV1.1']['PV1.1.1'].toString() != "") {objPV1.setId = seg['PV1.1']['PV1.1.1'].toString(); }

				// PV1.2, externalId
				if (seg['PV1.2']['PV1.2.1'].toString() != "") { objPV1.externalId = seg['PV1.2']['PV1.2.1'].toString(); }

				// PV1.3, assignedPatientLocation
				objPV1.assignedPatientLocation = {};
				if (seg['PV1.3']['PV1.3.1'].toString() != "") { objPV1.assignedPatientLocation.pointOfCare = seg['PV1.3']['PV1.3.1'].toString(); }
				if (seg['PV1.3']['PV1.3.2'].toString() != "") { objPV1.assignedPatientLocation.room = seg['PV1.3']['PV1.3.2'].toString(); }
				if (seg['PV1.3']['PV1.3.3'].toString() != "") { objPV1.assignedPatientLocation.bed = seg['PV1.3']['PV1.3.3'].toString(); }

				objPV1.facility = {};
				if (seg['PV1.3']['PV1.3.4']['PV1.3.4.1'].toString() != "") { objPV1.facility.nameSpaceId =  seg['PV1.3']['PV1.3.4']['PV1.3.4.1'].toString(); }
				if (seg['PV1.3']['PV1.3.4']['PV1.3.4.2'].toString() != "") { objPV1.facility.universalId =  seg['PV1.3']['PV1.3.4']['PV1.3.4.2'].toString(); }
				if (seg['PV1.3']['PV1.3.4']['PV1.3.4.3'].toString() != "") { objPV1.facility.universalIdType =  seg['PV1.3']['PV1.3.4']['PV1.3.4.3'].toString(); }

				if (seg['PV1.3']['PV1.3.5'].toString() != "") { objPV1.assignedPatientLocation.locationStatus = seg['PV1.3']['PV1.3.5'].toString(); }
				if (seg['PV1.3']['PV1.3.6'].toString() != "") { objPV1.assignedPatientLocation.personLocationType = seg['PV1.3']['PV1.3.6'].toString(); }
				if (seg['PV1.3']['PV1.3.7'].toString() != "") { objPV1.assignedPatientLocation.building = seg['PV1.3']['PV1.3.7'].toString(); }
				if (seg['PV1.3']['PV1.3.8'].toString() != "") { objPV1.assignedPatientLocation.floor = seg['PV1.3']['PV1.3.8'].toString(); }
				if (seg['PV1.3']['PV1.3.9'].toString() != "") { objPV1.assignedPatientLocation.locationDescription = seg['PV1.3']['PV1.3.9'].toString(); }

				// PV1.4, admissionType
				if (seg['PV1.4']['PV1.4.1'].toString() != "") { objPV1.admissionType = seg['PV1.4']['PV1.4.1'].toString(); }

				// PV1.5, preadmitNumber
				objPV1.preadmitNumber = {};
				if (seg['PV1.5']['PV1.5.1'].toString() != "") { objPV1.preadmitNumber.id = seg['PV1.5']['PV1.5.1'].toString(); }
				if (seg['PV1.5']['PV1.5.2'].toString() != "") { objPV1.preadmitNumber.identifierCheckDigit = seg['PV1.5']['PV1.5.2'].toString(); }
				if (seg['PV1.5']['PV1.5.3'].toString() != "") { objPV1.preadmitNumber.codeIdentifyingCheckDigitScheme = seg['PV1.5']['PV1.5.3'].toString(); }

				objPV1.preadmitNumber.assigningAuthority = {};
				if (seg['PV1.5']['PV1.5.4']['PV1.5.4.1'].toString() != "") { objPV1.preadmitNumber.assigningAuthority.namespaceId = seg['PV1.5']['PV1.5.4']['PV1.5.4.1'].toString(); }
				if (seg['PV1.5']['PV1.5.4']['PV1.5.4.2'].toString() != "") { objPV1.preadmitNumber.assigningAuthority.universalId = seg['PV1.5']['PV1.5.4']['PV1.5.4.2'].toString(); }
				if (seg['PV1.5']['PV1.5.4']['PV1.5.4.3'].toString() != "") { objPV1.preadmitNumber.assigningAuthority.universalIdType = seg['PV1.5']['PV1.5.4']['PV1.5.4.3'].toString(); }
/*
				if (seg['PV1.5']['PV1.5.5'].toString() != "") { pv1[index].preadmitNumber.idTypeCode = seg['PV1.5']['PV1.5.5']; }
				pv1[index].preadmitNumber.assigningFacility = {};
				if (seg['PV1.5']['PV1.5.6']['PV1.5.6.1'].toString() != "") { pv1[index].preadmitNumber.assigningFacility.namespaceId = seg['PV1.5']['PV1.5.6']['PV1.5.6.1']; }
				if (seg['PV1.5']['PV1.5.6']['PV1.5.6.2'].toString() != "") { pv1[index].preadmitNumber.assigningFacility.universalId = seg['PV1.5']['PV1.5.6']['PV1.5.6.2']; }
				if (seg['PV1.5']['PV1.5.6']['PV1.5.6.3'].toString() != "") { pv1[index].preadmitNumber.assigningFacility.universalIdType = seg['PV1.5']['PV1.5.6']['PV1.5.6.3']; }
				if (Object.keys(pv1[index].preadmitNumber.assigningFacility).length == 0) {
					if (seg['PV1.5']['PV1.5.6'].toString() != "") { pv1[index].preadmitNumber.assigningFacility.namespaceId = seg['PV1.5']['PV1.5.6']; }
					else delete pv1[index].preadmitNumber.assigningFacility;
				}
				if (Object.keys(pv1[index].preadmitNumber).length == 0) {
					delete pv1[index].preadmitNumber;
				}

				// PV1.6, priorPatientLocation
				pv1[index].priorPatientLocation = {};
				if (seg['PV1.6']['PV1.6.1'].toString() != "") { pv1[index].priorPatientLocation.pointOfCare = seg['PV1.6']['PV1.6.1']; }
				if (seg['PV1.6']['PV1.6.2'].toString() != "") { pv1[index].priorPatientLocation.room = seg['PV1.6']['PV1.6.2']; }
				if (seg['PV1.6']['PV1.6.3'].toString() != "") { pv1[index].priorPatientLocation.bed = seg['PV1.6']['PV1.6.3']; }
				pv1[index].priorPatientLocation.facility = {};
				if (seg['PV1.6']['PV1.6.4']['PV1.6.4.1'].toString() != "") { pv1[index].priorPatientLocation.facility.nameSpaceId =  seg['PV1.6']['PV1.6.4']['PV1.6.4.1']; }
				if (seg['PV1.6']['PV1.6.4']['PV1.6.4.2'].toString() != "") { pv1[index].priorPatientLocation.facility.universalId =  seg['PV1.6']['PV1.6.4']['PV1.6.4.2']; }
				if (seg['PV1.6']['PV1.6.4']['PV1.6.4.3'].toString() != "") { pv1[index].priorPatientLocation.facility.universalIdType =  seg['PV1.6']['PV1.6.4']['PV1.6.4.3']; }
				if (Object.keys(pv1[index].priorPatientLocation.facility).length == 0) {
					if (seg['PV1.6']['PV1.6.4'].toString() != "") { pv1[index].priorPatientLocation.facility.nameSpaceId = seg['PV1.6']['PV1.6.4']; }
					else delete pv1[index].priorPatientLocation.facility;
				}
				if (seg['PV1.6']['PV1.6.5'].toString() != "") { pv1[index].priorPatientLocation.locationStatus = seg['PV1.6']['PV1.6.5']; }
				if (seg['PV1.6']['PV1.6.6'].toString() != "") { pv1[index].priorPatientLocation.personLocationType = seg['PV1.6']['PV1.6.6']; }
				if (seg['PV1.6']['PV1.6.7'].toString() != "") { pv1[index].priorPatientLocation.building = seg['PV1.6']['PV1.6.7']; }
