	if(typeof objEncounter !== 'undefined') {
		// use Encounter startTime
		if((typeof objEncounter.startTime !== 'undefined')||(objEncounter.startTime !== '')) {
			if(debug>0) { logger.error('objEncounter.startTime: '+objEncounter.startTime); }
			if(objEncounter.startTime.toString().length<9) {
				channelMap.put('serviceStartTime',objEncounter.startTime.toString()+'0000');
			} else {
				channelMap.put('serviceStartTime',objEncounter.startTime);
			} // end if 
			if(debug>0) { logger.error('serviceStartTime: '+objEncounter.startTime.toString()); }
		} else {
		// use current datetime
			channelMap.put('serviceStartTime', currentDate_yyyyMMddhhmmss()); 
			if(debug>0) { logger.error('serviceStartTime: '+currentDate_yyyyMMddhhmmss().toString()); }	
		} // end if 

		// use Encounter stopTime
		if((typeof objEncounter.stopTime !== 'undefined')||(objEncounter.stopTime !== '')) {
			if(objEncounter.stopTime.toString().length<9) {
				channelMap.put('serviceStopTime',objEncounter.stopTime.toString()+'0000');
			} else {
				channelMap.put('serviceStopTime',objEncounter.stopTime);
			} // end if 
			if(debug>0) { logger.error('serviceStopTime: '+objEncounter.stopTime.toString()); }
		}  else {
			channelMap.put('serviceStopTime', currentDate_yyyyMMddhhmmss()); 
			if(debug>0) { logger.error('serviceStopTime: '+currentDate_yyyyMMddhhmmss().toString()); }	
		} // end if 
	} // end if 
	// } // end for each
} else if(typeof(arrServiceEvent) !== 'undefined') {
	for each(objServiceEvent in arrServiceEvent) {
		// use ServiceEvent startTime
		if((typeof objServiceEvent.startTime !== 'undefined')||(objServiceEvent.startTime !== '')) {
			if(objServiceEvent.startTime.toString().length<9) {
				channelMap.put('serviceStartTime',objServiceEvent.startTime.toString()+'0000');
			} else {
				channelMap.put('serviceStartTime',objServiceEvent.startTime);
			} // end if 
			if(debug>0) { logger.error('serviceStartTime: '+objServiceEvent.startTime); }
		} else {
		// use current datetime
			channelMap.put('serviceStartTime', currentDate_yyyyMMddhhmmss()); 
			if(debug>0) { logger.error('serviceStartTime: '+currentDate_yyyyMMddhhmmss().toString()); }	
		} // end if 

		// use ServiceEvent stopTime
		if((typeof objServiceEvent.stopTime !== 'undefined')||(objServiceEvent.stopTime !== '')) {
			if(objServiceEvent.stopTime.toString().length<9) {
				channelMap.put('serviceStopTime',objServiceEvent.stopTime.toString()+'0000');
			} else {
				channelMap.put('serviceStopTime',objServiceEvent.stopTime);
			} // end if 
			if(debug>0) { logger.error('serviceStopTime: '+objServiceEvent.stopTime); }
		}  else {
			channelMap.put('serviceStopTime', currentDate_yyyyMMddhhmmss()); 
			if(debug>0) { logger.error('serviceStopTime: '+currentDate_yyyyMMddhhmmss().toString()); }	
		} // end if
	} // end foreach
} else {
	// use current datetime
	channelMap.put('serviceStartTime', currentDate_yyyyMMddhhmmss()); 
	if(debug>0) { logger.error('serviceStartTime: '+currentDate_yyyyMMddhhmmss().toString()); }	

	channelMap.put('serviceStopTime', currentDate_yyyyMMddhhmmss()); 
	if(debug>0) { logger.error('serviceStopTime: '+currentDate_yyyyMMddhhmmss().toString()); }	
} // end if

