				logger.error('objAuthor.authorInstitution: '+objAuthor.authorInstitution.toString()); 
			} // end if 
		} // end if
	} // end if

	// get array of Performer Objects
	var objPerformers= getPerformer(msg);		
	channelMap.put('objPerformers',objPerformers);

	if(debug>0) { logger.error('objPerformers: '+objPerformers.toSource()+' type: '+typeof objPerformers); }

	// second try from objPerformer - objPerformer.assignedEntity_representedOrganization to populate authorInstitution
	var strAuthorInstitution= channelMap.get('authorInstitution');
	if((typeof strAuthorInstitution == 'undefined')||(strAuthorInstitution == null)) {
