/*   ScriptName:  setDocumentSource_PatientLocalAssigningAuthorityOID
	Description: This script is used to normalize the Source Document and Patient Local AssigningAuthority OIDs per CHSWyoming HIE requirements
	Created:     2018-12-17 

	Modified
	2018-12-18 - Modified by storing separate local assigning authorities one for original MedHost and latest MobileMD for patientId
	2018-12-17 - Modified additional debugging to verify sourceOid population
*/

var scriptName= 'setDocumentSource_PatientLocalAssigningAuthorityOID';
var debug= 0;
var environment= globalMap.get('environment');

// evaluate Document Source OID
if(debug>0) { logger.error('DEBUG::'+scriptName+' sourceOid: '+$('sourceOid')+' check either contains: '+$('BerwickRootOID')+' OR: '+$('TylerRootOID')); }
if(($('sourceOid').indexOf($('BerwickRootOID'))>-1)||($('sourceOid').indexOf($('TylerRootOID'))>-1)) {
	if(environment.toString().toUpperCase()== 'TEST') {
		channelMap.put('sourceOid',globalMap.get('TestSourceOID'));
	} else {
		channelMap.put('sourceOid',globalMap.get('ProdSourceOID'));
	} // end if 
