if(($('patientIdRootOid').indexOf($('BerwickRootOID'))>-1)||($('patientIdRootOid').indexOf($('TylerRootOID'))>-1)) {
	// check Test environment
	if(environment.toString().toUpperCase()== 'TEST') {
		// check if patient Root OID is Berwick Root OID
		if($('patientIdRootOid').indexOf($('BerwickRootOID'))>-1) {
			channelMap.put('patientIdRootOid', globalMap.get('TestBerwickMRNOID'));
		// check if patient Root OID is Tyler Root OID
		} else if($('patientIdRootOid').indexOf($('TylerRootOID'))>-1) {
			channelMap.put('patientIdRootOid', globalMap.get('TestTylerMRNOID'));
		} // end if 
	// check Prod environment
	} else if(environment.toString().toUpperCase()== 'PROD') {
		// check if patient Root OID is Berwick Root OID
		if($('patientIdRootOid').indexOf($('BerwickRootOID'))>-1) {
			channelMap.put('patientIdRootOid', globalMap.get('ProdBerwickMRNOID'));
		// check if patient Root OID is Tyler Root OID
		} else if($('patientIdRootOid').indexOf($('TylerRootOID'))>-1) {
			channelMap.put('patientIdRootOid', globalMap.get('ProdTylerMRNOID'));
		} // end if 
	} // end if 
}  // end if

logger.error('DEBUG::'+scriptName+' MedHost patientId: '+$('patientId')+' MobileMD patientIdRootOid: '+$('patientIdRootOid'));
channelMap.put('MobileMDPatientId',$('patientId')+'^^^&amp;'+$('patientIdRootOid')+'&amp;ISO');

