properties.code:
/**  getAddress(address) - Desc: This function receives CCD/CCDA msg and returns from Address Object

   	@param {String} msg - this contains the raw message (string)
	@return {object} objAddress - this contains the patientRole elements composed into a single objPatientRole object.

	History
	2022-07-28 JER - Modify the streetAddressLine if multiple streetAddressLine element objects
	2021-09-20 JER - Modified to incorporate evaluation of multiple patientRole Id(s) in the case there are multiple patient identifiers
	2021-09-20 JER - Modified to patient.name incorporate evaluation of multiple patient name child elements in the case there are multiple patient identifiers
*/
function getAddress(address) {

	// define variables
	var debug= 0;
	var scriptName= 'getAddress';
	var arrAddresses= new Array();

	if(debug>0) { logger.error(scriptName+' msg= '+msg); }
	if (typeof(address) !== 'undefined') {

		if(debug>0) { logger.error('DEBUG::'+scriptName+' address.length= '+address.length()+' address= '+address); }			

		if(address.length()>1) {
			// for each addr element
			var j= 0;
			for(var i= 0;i<1;i++) {

				var objAddress= new Object();
				objAddress.id= j;
				if(address[i]['@use']) {
					objAddress.use= address[i]['@use'].toString();
				} // end if address element @use property is not null

				if(debug>0) { logger.error('DEBUG::'+scriptName+' address['+i+'].children()= '+address[i].children()); }

				// 2022-07-28 JER - Modify the streetAddressLine if multiple streetAddressLine element objects
				var strStreetAddressLine= ''; var lenStreetAddressLine= address[i].streetAddressLine.length();
				for(var j= 0;j<address[i].streetAddressLine.length();j++) {
					if(lenStreetAddressLine>1) {
						strStreetAddressLine+= address[i].streetAddressLine[j].toString()+' ';
					} else {
						strStreetAddressLine=  address[i].streetAddressLine[j].toString();
					} // end if 
				} // end for 
				objAddress.streetAddressLine= strStreetAddressLine;

				/*
				if(address[i].streetAddressLine.toString().length>0) {
					objAddress.streetAddressLine=	address[i].streetAddressLine.toString();
				} else {
					objAddress.streetAddressLine=	""; 
				} // end if streetAddressLine
				*/	

					if(address[i].county.toString().length>0) {
						objAddress.county=			address[i].county.toString()
					} else {
						objAddress.county=	""; 
					} // end if county element

					if(address[i].city.toString().length>0) {
						objAddress.city=			address[i].city.toString()
					} else {
						objAddress.city=	""; 
					} // end if city element

					if(address[i].state.toString().length>0) {
						objAddress.state=			address[i].state.toString()
					} else {
						objAddress.state= "";
					} // end if state element

					if(address[i].postalCode.toString().length>0) {
						objAddress.postalCode=		address[i].postalCode.toString()
					} else {
						objAddress.postalCode= "";
					} // end if postalCode element

					if(address[i].useablePeriod) {
						if(address[i].useablePeriod.children().length()>0) {
							for each(child in address[i].useablePeriod.children()) {
								if(child.localName()== 'low') {
									objAddress.beginDate=	child['@low'].toString();
								}
								if(child.localName()== 'high') {
									objAddress.endDate=		child['@high'].toString();
								}
							} // end for each
						} // end if 
					} // end if useablePeriod element
					arrAddresses.push(objAddress);
				//} // for each child element
				if(debug>0) { logger.error('DEBUG::'+scriptName+'function arrAddress: '+JSON.stringify(arrAddresses)); }
				j++;
			} // for each address element 
		} else {
			if(debug>0) { logger.error('DEBUG::'+scriptName+' address.children()= '+JSON.stringify(address.children())); }
			var objAddress= new Object();
			objAddress.id= i;
			if(address['@use']) {
				objAddress.use= address['@use'].toString();
			} // end if address element @use property is not null

			if(debug>0) { logger.error('DEBUG::'+scriptName+' address.children()= '+address.children()); }

				// for each(child in address.children()) {

					if(address.streetAddressLine.toString().length>0) {
						objAddress.streetAddressLine=	address.streetAddressLine.toString();
					} else {
						objAddress.streetAddressLine=	""; 
					} // end if streetAddressLine

					if(address.county.toString().length>0) {
						objAddress.county=			address.county.toString()
					} else {
						objAddress.county=	""; 
					} // end if county element

					if(address.city.toString().length>0) {
						objAddress.city=			address.city.toString()
					} else {
						objAddress.city=	""; 
					} // end if city element

					if(address.state.toString().length>0) {
						objAddress.state=			address.state.toString()
					} else {
						objAddress.state= "";
					} // end if state element

					if(address.postalCode.toString().length>0) {
						objAddress.postalCode=		address.postalCode.toString()
					} else {
						objAddress.postalCode= "";
					} // end if postalCode element

					if(address.useablePeriod) {
						if(address.useablePeriod.children().length()>0) {
							for each(child in address.useablePeriod.children()) {
								if(child.localName()== 'low') {
									objAddress.beginDate=	child['@low'].toString();
								}
								if(child.localName()== 'high') {
									objAddress.endDate=		child['@high'].toString();
								}
							} // end for each
						} // end if 
					} // end if useablePeriod element
					arrAddresses.push(objAddress);
				// } // for each child element

				if(debug>0) { logger.error('DEBUG::'+scriptName+'function arrAddress: '+JSON.stringify(arrAddresses)); }

			arrAddresses= objAddress;
			if(debug>0) { logger.error('DEBUG::'+scriptName+'function arrAddress: '+JSON.stringify(arrAddresses)); }
		} // end if number of addr elements

		channelMap.put('arrAddresses',JSON.stringify(arrAddresses));
		return arrAddresses;
	}
}

/** getAssignedAuthor(msg)

  	- Desc: This function receives CCD assignedAuthor XML Object and returns JavaScript object
  	- Modified 
  	2018-02-24 04:18 CT JER - Modified the msg['assignedAuthor']['representedOrganization'].toString() evaluation !== undefined to length>0

   	@param {XML} msg - this contains the raw message (string)
	@return {object} getAssignedAuthor - contains getAssignedAuthor JS object and related properties.
*/

function getAssignedAuthor(msg) {

	var debug= 0;
	var objAssignedAuthor= new Object;

	if(debug>0) { 
		logger.error('getAssignedAuthor msg: '+msg);
		logger.error('getAssignedAuthor msg[assignedAuthor]: '+JSON.stringify(msg['assignedAuthor'])+
		' typeof: '+msg['assignedAuthor']+
		' length: '+msg['assignedAuthor'].toString().length); 
	} // end if 

	if(typeof msg['assignedAuthor'] !== 'undefined') {
		if(msg['assignedAuthor'].children().length()>0) {

			// <assignedAuthor classCode="ASSIGNED">
			// set assignedAuthor classCode
			if(typeof msg['assignedAuthor']['@classCode'] !== 'undefined') {
				if(debug>0) { logger.error('msg[assignedAuthor][@classCode]: '+msg['assignedAuthor']['@classCode'].toString()); }
				objAssignedAuthor.classCode= msg['assignedAuthor']['@classCode'].toString();
			} // end if

			// <id assigningAuthorityName="INLP_FIN" extension="INLP" root="2.16.840.1.113883.3.2139.200"/>
			// set assignedAuthor Id
			if(typeof msg['assignedAuthor']['id'] !== 'undefined') {
				// set assignedAuthor Id assigningAuthorityName
				if(typeof msg['assignedAuthor']['id']['@assigningAuthorityName'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedAuthor][id][@assigningAuthorityName]: '+msg['assignedAuthor']['id']['@assigningAuthorityName'].toString()); }
					objAssignedAuthor.assigningAuthorityName= msg['assignedAuthor']['id']['@assigningAuthorityName'].toString();
				} // end if
				// set assignedAuthor Id root
				if(typeof msg['assignedAuthor']['id']['@root'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedAuthor][id][@root]: '+msg['assignedAuthor']['id']['@root'].toString()); }
					objAssignedAuthor.root= msg['assignedAuthor']['id']['@root'].toString();
				} // end if
				// set assignedAuthor Id extension
				if(typeof msg['assignedAuthor']['id']['@extension'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedAuthor][id][@extension]: '+msg['assignedAuthor']['id']['@extension'].toString()); }
					objAssignedAuthor.extension= msg['assignedAuthor']['id']['@extension'].toString();
				} // end if
			} // end if

			// set assignedAuthor Code
			if(typeof msg['assignedAuthor']['code'] !== 'undefined') {
				// set assignedAuthor code code
				if(typeof msg['assignedAuthor']['code']['@code'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedAuthor][code][@code]: '+msg['assignedAuthor']['code']['@code'].toString()); }
					objAssignedAuthor.code= msg['assignedAuthor']['code']['@code'].toString();
				} // end if
				// set assignedAuthor code codeSystemOid
				if(typeof msg['assignedAuthor']['code']['@codeSystem'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedAuthor][code][@codeSystem]: '+msg['assignedAuthor']['code']['@codeSystem'].toString()); }
					objAssignedAuthor.codeSystem= msg['assignedAuthor']['code']['@codeSystem'].toString();
				} // end if
				// set assignedAuthor code codeSystemName
				if(typeof msg['assignedAuthor']['code']['@codeSystemName'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedAuthor][code][@codeSystemName]: '+msg['assignedAuthor']['code']['@codeSystemName'].toString()); }
					objAssignedAuthor.codeSystemName= msg['assignedAuthor']['code']['@codeSystemName'].toString();
				} // end if
				// set assignedAuthor code displayName
				if(typeof msg['assignedAuthor']['code']['@displayName'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedAuthor][code][@displayName]: '+msg['assignedAuthor']['code']['@displayName'].toString()); }
					objAssignedAuthor.codeDisplayName= msg['assignedAuthor']['code']['@displayName'].toString();
				} // end if
			} // end if


			// set assignedAuthor assignedAuthoringDevice
			/* <assignedAuthoringDevice><manufacturerModelName>Cerner Corporation</manufacturerModelName><softwareName>Millennium Clinical Document Generator</softwareName></assignedAuthoringDevice> */
			if(typeof msg['assignedAuthor']['assignedAuthoringDevice'] !== 'undefined') {
				// get manufacturer
				if(typeof msg['assignedAuthor']['assignedAuthoringDevice']['manufacturerModelName'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedAuthor][assignedAuthoringDevice][manufacturerModelName]: '+msg['assignedAuthor']['assignedAuthoringDevice']['manufacturerModelName'].toString()); }
					objAssignedAuthor.assignedAuthorizingDevice_manufacturer= msg['assignedAuthor']['assignedAuthoringDevice']['manufacturerModelName'].toString();
				} // end if 
				// get softwareName
				if(typeof msg['assignedAuthor']['assignedAuthoringDevice']['softwareName'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedAuthor][assignedAuthoringDevice][softwareName]: '+msg['assignedAuthor']['assignedAuthoringDevice']['softwareName'].toString()); }
					objAssignedAuthor.assignedAuthorizingDevice_softwareName= msg['assignedAuthor']['assignedAuthoringDevice']['softwareName'].toString();
				} // end if 
			} // end if 

			// set assignedAuthor representedOrganization
			/*  <representedOrganization classCode="ORG" determinerCode="INSTANCE"><id assigningAuthorityName="INLP_FIN" extension="INLP" root="2.16.840.1.113883.3.2139.200"/><id root="2.16.840.1.113883.3.2139"/><name>La Porte Hospital</name>
			*/

			if(debug>0) { 
				logger.error('msg[assignedAuthor][representedOrganization]: '+msg['assignedAuthor']['representedOrganization']+
					' typeof: '+typeof msg['assignedAuthor']['representedOrganization']+
					' toString: '+msg['assignedAuthor']['representedOrganization'].toString()+
					' length: '+msg['assignedAuthor']['representedOrganization'].toString().length); 
			}

			if(msg['assignedAuthor']['representedOrganization'].toString().length>0) {
				if(typeof msg['assignedAuthor']['representedOrganization']['id'] !== 'undefined') {
					// set representedOrganization Id assigningAuthorityName
					if(typeof msg['assignedAuthor']['representedOrganization']['id']['@assigningAuthorityName'] !== 'undefined') {
						if(debug>0) { logger.error('msg[assignedAuthor][representedOrganization][id][@assigningAuthorityName]: '+msg['assignedAuthor']['representedOrganization']['id']['@assigningAuthorityName'].toString()); }
						objAssignedAuthor.representedOrganization_assigningAuthorityName= msg['assignedAuthor']['representedOrganization']['id']['@assigningAuthorityName'].toString();
					} // end if
					// set representedOrganization Id extension
					if(typeof msg['assignedAuthor']['representedOrganization']['id'][0]['@extension'] !== 'undefined') {
						if(debug>0) { logger.error('msg[assignedAuthor][representedOrganization][id][@extension]: '+msg['assignedAuthor']['representedOrganization']['id'][0]['@extension'].toString()); }
						objAssignedAuthor.representedOrganization_extension= msg['assignedAuthor']['representedOrganization']['id'][0]['@extension'].toString();
					} // end if
					// set representedOrganization Id root
					if(typeof msg['assignedAuthor']['representedOrganization']['id'][0]['@root'] !== 'undefined') {
						if(debug>0) { logger.error('msg[assignedAuthor][representedOrganization][id][@root]: '+msg['assignedAuthor']['representedOrganization']['id'][0]['@root'].toString()); }
						objAssignedAuthor.representedOrganization_root= msg['assignedAuthor']['representedOrganization']['id'][0]['@root'].toString();
					} // end if
				} // end if 
			} // end if

			// check for assignedAuthor representedOrganization name child element
			if(typeof msg['assignedAuthor']['representedOrganization']['name'] !== 'undefined') {
				if(msg['assignedAuthor']['representedOrganization']['name'].toString().length>0) {
					if(debug>0) { logger.error('msg[assignedAuthor][representedOrganization][name]: '+msg['assignedAuthor']['representedOrganization']['name'].toString()); }
					objAssignedAuthor.representedOrganizationName= msg['assignedAuthor']['representedOrganization']['name'].toString();	// Walgreens Drug Store 12562
				} else if (msg['assignedAuthor']['assignedPerson']['name'].toString().length>0) {
					if(debug>0) { logger.error('msg[assignedAuthor][assignedPerson][name]: '+msg['assignedAuthor']['assignedPerson']['name'].toString()); }
					objAssignedAuthor.representedOrganizationName= msg['assignedAuthor']['assignedPerson']['name'].toString();	// Walgreens Drug Store 12562
				} // end if
			} // end if 
		} // end if
	} // end if 
	return objAssignedAuthor;
}

/** setAssignedAuthorAddress(msg)

  	- Desc: This setAssignedAuthorAddress function receives message and returns objAssignedAuthorAddress toXMLString
  	- Modified:
  	2019-11-20 22:20 CST - Under development

   	@param {msg} - this contains the Javascript message document 
	@return {XML} objAssignedAuthorAddress - contains objAssignedAuthorAddress in XML Object string.

	Reference: https://gazelle.ihe.net/XDStarClient/rimihe-templates/rimihe-Author_XDSDocumentEntry.html

*/

function getAssignedAuthorAddress(msg) {

	var debug= 0;
	var arrParticipant= new Array();
	var strFunctionName= 'getAssignedAuthorAddress';

	if(debug>0) {
		logger.error('DEBUG::'+strFunctionName+' msg[author][assignedAuthor]: '+msg['author']['assignedAuthor'].toXMLString());
		logger.error('DEBUG::'+strFunctionName+' msg[author][assignedAuthor][addr]: '+msg['author']['assignedAuthor']['addr'].toXMLString());
	} // end if 

	var objAssignedAuthorAddress= new Object;
	if(msg.author.assignedAuthor.children().length()>0) {

		if(debug>0) { 
			logger.error('DEBUG::'+strFunctionName+' AssignedAuthor.children().length: '+msg.author.assignedAuthor.children().length()+' children names: '+msg.author.assignedAuthor.children());
		} // end if 
		if((msg['author']['assignedAuthor']['addr'] !== 'undefined')&&(msg['author']['assignedAuthor']['addr'].toString().length>0)) {

			for each (child in msg.author.assignedAuthor.children()) {

				if(debug>0) { 
					logger.error('DEBUG::'+strFunctionName+' child.fullName: '+child.name()+' child.text: '+child.name+' child.localName(): '+child.localName());
				} // end if 
				if(child.localName()== 'addr') {
					if((child.toString() !== 'undefined')&&(child.toString().length>0)) {
						if(debug>0) { 
							logger.error('DEBUG::'+strFunctionName+' addr.toXMLString: '+child.toXMLString());
						} // end if 
						objAssignedAuthorAddress.addr= child.toXMLString();
					} // end if 
				} // end if 
			} // end for each
		} // end if
		channelMap.put('objAssignedAuthorAddress',objAssignedAuthorAddress);
	} // end if

} // end proc

/** getAssignedEntity(msg)

  	- Desc: This function receives CCD assignedEntity XML Object and returns JavaScript object
  	- Modified 	
  	2020-01-29 19:25 CT JER - Modified to comment out the debug statement to echo the toXMLString msg[assignedEntity][assignedPerson][name][given]
	2018-04-22 16:43 CT JER - Modified to include the storing of the id.root element.attribute for reference purposes
	2018-04-15 23:42 CT JER - Modified to include additional if no value or sizeable value handling (length()>1, length= 1 and the else) for givenName element value.
	2018-02-16 17:48 CT JER - Added the assignedPerson Name suffix and the representedOrganization Name

   	@param {XML} msg - this contains the raw message (string)
	@return {object} objAssignedEntity - contains objAssignedEntity JS object and related properties.
*/

function getAssignedEntity(msg) {

	var debug= 0;
	var objAssignedEntity= new Object;
	default xml namespace = 'urn:hl7-org:v3';
	// <given xmlns="urn:hl7-org:v3">Julius</given><given xmlns="urn:hl7-org:v3">R</given>

	if(typeof msg['assignedEntity'] !== 'undefined') {
		if(msg['assignedEntity'].children().length()>0) {

			// set assignedEntity classCode
			if(typeof msg['assignedEntity']['@classCode'] !== 'undefined') {
				if(debug>0) { logger.error('msg[assignedEntity][@classCode]: '+msg['assignedEntity']['@classCode'].toString()); }
				objAssignedEntity.classCode= msg['assignedEntity']['@classCode'].toString();
			} // end if

			// set assignedEntity Id
			if(typeof msg['assignedEntity']['id'][0] !== 'undefined') {
				// set assignedEntity Id assigningAuthorityName
				if(typeof msg['assignedEntity']['id'][0]['@assigningAuthorityName'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedEntity][id][0][@assigningAuthorityName]: '+msg['assignedEntity']['id'][0]['@assigningAuthorityName'].toString()); }
					objAssignedEntity.assigningAuthorityName= msg['assignedEntity']['id'][0]['@assigningAuthorityName'].toString();
				} // end if
				// set assignedEntity Id root
				if(typeof msg['assignedEntity']['id'][0]['@root'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedEntity][id][0][@root]: '+msg['assignedEntity']['id'][0]['@root'].toString()); }
					objAssignedEntity.root= msg['assignedEntity']['id'][0]['@root'].toString();
				} // end if
				// set assignedEntity Id extension
				if(typeof msg['assignedEntity']['id'][0]['@extension'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedEntity][id][0][@extension]: '+msg['assignedEntity']['id'][0]['@extension'].toString()); }
					objAssignedEntity.extension= msg['assignedEntity']['id'][0]['@extension'].toString();
				} // end if
			} // end if

			// set assignedEntity Code
			if(typeof msg['assignedEntity']['code'] !== 'undefined') {
				// set assignedEntity code code
				if(typeof msg['assignedEntity']['code']['@code'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedEntity][code][@code]: '+msg['assignedEntity']['code']['@code'].toString()); }
					objAssignedEntity.code= msg['assignedEntity']['code']['@code'].toString();
				} // end if
				// set assignedEntity code codeSystemOid
				if(typeof msg['assignedEntity']['code']['@codeSystem'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedEntity][code][@codeSystem]: '+msg['assignedEntity']['code']['@codeSystem'].toString()); }
					objAssignedEntity.codeSystem= msg['assignedEntity']['code']['@codeSystem'].toString();
				} // end if
				// set assignedEntity code codeSystemName
				if(typeof msg['assignedEntity']['code']['@codeSystemName'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedEntity][code][@codeSystemName]: '+msg['assignedEntity']['code']['@codeSystemName'].toString()); }
					objAssignedEntity.codeSystemName= msg['assignedEntity']['code']['@codeSystemName'].toString();
				} // end if
				// set assignedEntity code displayName
				if(typeof msg['assignedEntity']['code']['@displayName'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedEntity][code][@displayName]: '+msg['assignedEntity']['code']['@displayName'].toString()); }
					objAssignedEntity.codeDisplayName= msg['assignedEntity']['code']['@displayName'].toString();
				} // end if
			} // end if


			// set assignedEntity assignedPerson name
			if(typeof msg['assignedEntity']['assignedPerson'] !== 'undefined') {
				if(debug>0) { 
					logger.error('toXMLString msg[assignedEntity][assignedPerson][name][given]: '+msg['assignedEntity']['assignedPerson']['name']['given'].toXMLString()+
					'msg[assignedEntity][assignedPerson][name][given]: '+msg['assignedEntity']['assignedPerson']['name']['given'].toString()+
					' typeof: '+typeof msg['assignedEntity']['assignedPerson']['name']['given']+
					' length: '+msg['assignedEntity']['assignedPerson']['name']['given'].length()
					);
				}
				if(typeof msg['assignedEntity']['assignedPerson']['name'] !== 'undefined') {
					// get assignedEntity Given name	
					if((typeof msg['assignedEntity']['assignedPerson']['name']['given'] !== 'undefined')||(msg['assignedEntity']['assignedPerson']['name']['given'].toString().length>0)) {	
						if(debug>0) { 
							logger.error('msg[assignedEntity][assignedPerson][name][given]: '+msg['assignedEntity']['assignedPerson']['name']['given'][0].toString()); 
						} // end if 
						if(msg['assignedEntity']['assignedPerson']['name']['given'].length()>1) {
							objAssignedEntity.givenName= msg['assignedEntity']['assignedPerson']['name']['given'][0].toString();
						} else if(msg['assignedEntity']['assignedPerson']['name']['given'].length()== 1) {
							objAssignedEntity.givenName= msg['assignedEntity']['assignedPerson']['name']['given'].toString();
						} else {
							objAssignedEntity.givenName= '';
						} // end if 
					} // end if 
					// get assignedEntity Family name	
					if((typeof msg['assignedEntity']['assignedPerson']['name']['family'] !== 'undefined')||(msg['assignedEntity']['assignedPerson']['name']['family'].length>0)) {	
						if(debug>0) { logger.error('msg[assignedEntity][assignedPerson][name][family]: '+msg['assignedEntity']['assignedPerson']['name']['family'].toString().replace(/xmlns=\"(.*?)\"/g, '')); }
						objAssignedEntity.familyName= msg['assignedEntity']['assignedPerson']['name']['family'].toString().replace(/xmlns=\"(.*?)\"/g, '');
					} // end if
					// get assignedEntity Suffix
					if(debug>0) { logger.error('getAssignedEntity msg[assignedEntity][assignedPerson][name][suffix]: '+msg['assignedEntity']['assignedPerson']['name']['suffix']+
						' typeof: '+typeof msg['assignedEntity']['assignedPerson']['name']['suffix']+
						' length: '+msg['assignedEntity']['assignedPerson']['name']['suffix'].toString().length);
					} // end if logger.error
					if((typeof msg['assignedEntity']['assignedPerson']['name']['suffix'] !== 'undefined')||(msg['assignedEntity']['assignedPerson']['name']['suffix'].toString().length>0)) {	
						if(debug>0) { logger.error('msg[assignedEntity][assignedPerson][name][suffix]: '+msg['assignedEntity']['assignedPerson']['name']['suffix'].toString()); }
						objAssignedEntity.suffix= msg['assignedEntity']['assignedPerson']['name']['suffix'].toString();
					} // end if 
				} // end if 
			} // end if 

			// set assignedEntity representedOrganization
			if(typeof msg['assignedEntity']['representedOrganization'] !== 'undefined') {
				// check for assignedEntity representedOrganization name child element
				if(typeof msg['assignedEntity']['representedOrganization']['name'] !== 'undefined') {
					if(debug>0) { logger.error('msg[assignedEntity][representedOrganization][name]: '+msg['assignedEntity']['representedOrganization']['name'].toString()); }
					objAssignedEntity.representedOrganizationName= msg['assignedEntity']['representedOrganization']['name'].toString();	// Walgreens Drug Store 12562
				} // end if

				if(typeof msg['assignedEntity']['representedOrganization']['id'] !== 'undefined') {
					if(typeof msg['assignedEntity']['representedOrganization']['id']['root'] !== 'undefined') {
						if(debug>0) { logger.error('msg[assignedEntity][representedOrganization][id][root]: '+msg['assignedEntity']['representedOrganization']['id']['root'].toString()); }
						objAssignedEntity.representedOrganizationIdRoot= msg['assignedEntity']['representedOrganization']['id']['root'].toString();	// 12562
					} // end if 
				} // end if
			} // end if
		} // end if
	} // end if 

	if(objAssignedEntity.toString().length>0) {
		channelMap.put('assignedEntity',objAssignedEntity);
	}

	return objAssignedEntity;
}

/** getAuthorPerson(object)

  	- Desc: This function receives Javascript Object and returns AuthorPerson string
  	- Modified:
	2018-02-18 10:12 CT JER - Modified to accommodate the changes to the objAssignedEntity
  	2018-02-16 10:40 CT JER - Removed the objAuthor instances reference and left the references specific to JS Rhino XPath
  	2018-02-15 16:01 CT JER - Modified included additional typeof and for each object

   	@param {object} objAuthor - this contains the Javascript Object (object)
	@return {string} strAuthorPerson - contains authorPerson string containing the required HL7.Composite.XCN datatype and related properties.

	[2018-02-18 10:11:47,629]  ERROR (transformer:?): objAuthor= ({assigningAuthorityName:"INLP_FIN", extension:"3100004118", root:"2.16.840.1.113883.3.2139.200", startTime:"20180208041500", stopTime:"20180208075000", dispositionCode:"", dispositionCodeSystemOid:"", dispositionCodeSystemName:"", dispositionDisplayName:"Home or Self Care", encounterParticipant_code:"ATND", assignedEntity_root:"2.16.840.1.113883.3.2139.300", assignedEntity_extension:"8284", assignedEntity_assigningAuthorityName:"LPS_ORG_DOC", assignedEntity_code:"", assignedEntity_codeSystem:"", assignedEntity_codeSystemName:"", assignedEntity_codeDisplayName:"", assignedEntity_givenName:"Robert", assignedEntity_familyName:"Riggs"})

*/

function getAuthorPerson(objAuthor) {

	var debug= 0;
	var cntObject= 0;
	var strAuthorPerson= '';
	if(debug>0) { 
		logger.error("getAuthorPerson objAuthor= "+objAuthor.toSource()); 
		// logger.error("objAuthor JSON= "+JSON.stringify(objAuthor, null, 4));
	}

	if(typeof objAuthor !== 'undefined') {	

		// replaced the assignedId with assignedEntity_extension
		if(typeof objAuthor.assignedEntity_extension !== 'undefined') {
			if(objAuthor.assignedEntity_extension.toString().length>0) {
				if(debug>0) { logger.error('objAuthor.assignedEntity_extension: '+objAuthor.assignedEntity_extension.toString()); }
				strAuthorPerson += objAuthor.assignedEntity_extension.toString();
			} // end if 
		} // end if 


		// replaced the assignedPersonLastName with assignedEntity_familyName
		if(typeof objAuthor.assignedEntity_familyName !== 'undefined') {
			if(debug>0) { logger.error('objAuthor.assignedEntity_familyName: '+objAuthor.assignedEntity_familyName.toString()); }
			if(objAuthor.assignedEntity_familyName.toString().length>0) {
				strAuthorPerson += "^"+objAuthor.assignedEntity_familyName.toString();
			} else {
				strAuthorPerson += "^";
			} // end if 
		} // end if 
		// replaced the assignedPersonFirstName with assignedEntity_givenName
		if(typeof objAuthor.assignedEntity_givenName !== 'undefined') {
			// replace(/xmlns=\"(.*?)\"/g, '') to remove namespaces
			// objAuthor.assignedEntity_givenName.toString().replace(/xmlns=\"(.*?)\"/g, '')); }
			if(debug>0) { 
				logger.error('objAuthor.assignedEntity_givenName: '+objAuthor.assignedEntity_givenName.toString());
			} // end if 
			if(objAuthor.assignedEntity_givenName.toString().length>0) {
				// strAuthorPerson += "^"+objAuthor.assignedEntity_givenName.toString().replace(/xmlns=\"(.*?)\"/g, '');
				strAuthorPerson += "^"+objAuthor.assignedEntity_givenName;
			} else {
				strAuthorPerson += "^";
			} // end if 
		} // end if


		// replace the assignedPersonSuffix with assignedEntity_suffix
		if(typeof objAuthor.assignedEntity_suffix !== 'undefined') {
			if(debug>0) { logger.error('objAuthor.assignedEntity_suffix: '+objAuthor.assignedEntity_suffix.toString()); }
			if(objAuthor.assignedEntity_suffix.toString().length>0) {
				strAuthorPerson += "^^"+objAuthor.assignedEntity_suffix.toString();
			} else {
				strAuthorPerson += "^^";
			} // end if 
		} // end if 
		// replace the assignedRoot with assignedEntity_root
		if(typeof objAuthor.assignedEntity_root !== 'undefined') {
			if(debug>0) { logger.error('objAuthor.assignedEntity_root: '+objAuthor.assignedEntity_root.toString()); }
			if (objAuthor.assignedEntity_root.toString().length>0) {
				strAuthorPerson += "^^^^^&amp;"+objAuthor.assignedEntity_root.toString()+"&amp;ISO";
			} else {
				strAuthorPerson += "^^^^^";
			} // end if
		} // end if
	} // end if 

	if(debug>0) { logger.error("getAuthorPerson.strAuthorPerson: "+strAuthorPerson); }
	return strAuthorPerson;
}

/** getAuthors(msg)

	- Desc: This function receives CCD msg and returns an array of Author objects 
	- Modified
	2018-02-18 13:38 CT JER - Modified to accommodate the changes to call the objAssignedAuthor function
	2018-02-10 11:32 CT JER - Verified per the HL7 Rim model 1..* assignedAuthors elements (1..* more authors) list objects persist to arrAuthors.
	2018-02-08 03:58 CT JER - Modified for additional handling verify typeof !== 'undefined' and two conditionals to set authorInstitution property

	@param {String} msg - Raw msg
	@return {Array} return array of Author objects
*/
function getAuthors(msg) {

	var debug= 0;
	// uncertain if more than one assignedAuthor per ClinicalDocument
	if(debug>0) { logger.error('getAuthor of msg[author]: '+msg['author']); }

	var objAuthor= new Object;			
	var objAssignedAuthor= getAssignedAuthor(msg['author']);

	if(typeof objAssignedAuthor !== 'undefined') {

		// get assigningAuthorityName
		if((typeof objAssignedAuthor.assigningAuthorityName !== 'undefined')&&(objAssignedAuthor.assigningAuthorityName.toString().length>0)) {
			if(debug>0) { logger.error('msg[author][assignedAuthor][id][@assigningAuthorityName]: '+objAssignedAuthor.assigningAuthorityName.toString()); }
			objAuthor.IdAssigningAuthorityName= objAssignedAuthor.assigningAuthorityName.toString();
		} // end if
		// get root
		if((typeof objAssignedAuthor.root !== 'undefined')&&(objAssignedAuthor.root.toString().length>0)) {
			if(debug>0) { logger.error('msg[author][assignedAuthor][id][@root]: '+objAssignedAuthor.root.toString()); }
			objAuthor.Idroot= objAssignedAuthor.root.toString();
		} // end if
		// get extension
		if((typeof objAssignedAuthor.extension !== 'undefined')&&(objAssignedAuthor.extension.toString().length>0)) {
			if(debug>0) { logger.error('msg[author][assignedAuthor][id][@extension]: '+objAssignedAuthor.extension.toString()); }
			objAuthor.Idextension= objAssignedAuthor.extension.toString();
		} // end if			

		// get assignedAuthorizingDevice manufacturer -> set manufacturer
		if((typeof objAssignedAuthor.assignedAuthorizingDevice_manufacturer !== 'undefined')&&(objAssignedAuthor.assignedAuthorizingDevice_manufacturer.toString().length>0)) {
			if(debug>0) { logger.error('msg[author][assignedAuthor][assignedAuthoringDevice][manufacturerModelName]: '+objAssignedAuthor.assignedAuthorizingDevice_manufacturer.toString()); }
			objAuthor.authorizingDevice= objAssignedAuthor.assignedAuthorizingDevice_manufacturer.toString();
		} // end if			

		// get assignedAuthorizingDevice softwareName -> set softwareName
		if((typeof objAssignedAuthor.assignedAuthorizingDevice_softwareName !== 'undefined')&&(objAssignedAuthor.assignedAuthorizingDevice_softwareName.toString().length>0)) {
			if(debug>0) { logger.error('msg[author][assignedAuthor][assignedAuthoringDevice][softwareName]: '+objAssignedAuthor.assignedAuthorizingDevice_softwareName.toString()); }
			objAuthor.softwareName= objAssignedAuthor.assignedAuthorizingDevice_softwareName.toString();
		} // end if	

		// get objAssignedAuthor representedOrganization_assigningAuthorityName -> set assigningAuthorityName
		if((typeof objAssignedAuthor.representedOrganization_assigningAuthorityName !== 'undefined')&&(objAssignedAuthor.representedOrganization_assigningAuthorityName.toString().length>0)) {
			if(debug>0) { logger.error('msg[author][assignedAuthor][representedOrganization][id][assigingAuthorityName]: '+objAssignedAuthor.representedOrganization_assigningAuthorityName.toString()); }
			objAuthor.representedOrganization_assigingAuthorityName= objAssignedAuthor.representedOrganization_assigningAuthorityName.toString();
		} // end if				

		// get objAssignedAuthor representedOrganization_extension -> set extension
		if((typeof objAssignedAuthor.representedOrganization_extension !== 'undefined')&&(objAssignedAuthor.representedOrganization_extension.toString().length>0)) {
			if(debug>0) { logger.error('msg[author][assignedAuthor][representedOrganization][id][extension]: '+objAssignedAuthor.representedOrganization_extension.toString()); }
			objAuthor.representedOrganization_extension= objAssignedAuthor.representedOrganization_extension.toString();
		} // end if	

		// get objAssignedAuthor representedOrganization_root -> set root
		if((typeof objAssignedAuthor.representedOrganization_root !== 'undefined')&&(objAssignedAuthor.representedOrganization_root.toString().length>0)) {
			if(debug>0) { logger.error('msg[author][assignedAuthor][representedOrganization][id][root]: '+objAssignedAuthor.representedOrganization_root.toString()); }
			objAuthor.representedOrganization_root= objAssignedAuthor.representedOrganization_root.toString();
		} // end if

		// get objAssignedAuthor.representedOrganizationName -> set representedOrganizationName
		if((typeof objAssignedAuthor.representedOrganizationName !== 'undefined')&&(objAssignedAuthor.representedOrganizationName.toString().length>0)) {
			if(debug>0) { logger.error('msg[author][assignedAuthor][representedOrganization][name]: '+objAssignedAuthor.representedOrganizationName.toString()); }
			objAuthor.authorInstitution= objAssignedAuthor.representedOrganizationName.toString();
		} // end if
	} // end if 

	return objAuthor;
}

/** getCCDASectionMap(code)

  	- Desc: This function receives the templateId code and returns the mapped CCDA Section titlename

   	@param {String} msg - this contains the raw message (string)
	@return {object} objPatientRole - this contains the patientRole elements composed into a single objPatientRole object.

	History
*/
function getCCDASectionMap(code) {

	var debug= 0;
	var scriptName= 'getCCDASectionMap';

	if(code== "2.16.840.1.113883.10.20.22.2.22") return 'Encounter Details';
	if(code== "2.16.840.1.113883.10.20.22.2.6")  return 'Allergies';
	if(code== "2.16.840.1.113883.10.20.22.2.1")  return 'Medications';
	if(code== "2.16.840.1.113883.10.20.22.2.5")  return 'Active Problems';
	if(code== "2.16.840.1.113883.10.20.22.2.2")  return 'Immunizations';
	if(code== "2.16.840.1.113883.10.20.22.2.17") return 'Social History';
	if(code== "2.16.840.1.113883.10.20.22.2.4")  return 'Vital Signs';
	if(code== "1.3.6.1.4.1.19376.1.5.3.1.3.4")   return 'Progress Notes';
	if(code== "2.16.840.1.113883.10.20.22.2.65") return 'Miscellaneous Notes';
	if(code== "2.16.840.1.113883.10.20.22.2.10") return 'Plan of Treatment'; 
	if(code== "2.16.840.1.113883.10.20.22.2.7")  return 'Procedures';
	if(code== "2.16.840.1.113883.10.20.22.2.3")  return 'Results';
	if(code== "2.16.840.1.113883.10.20.22.2.8")  return 'Visit Diagnoses';
	if(code== "2.16.840.1.113883.10.20.22.2.18") return 'Insurance';
}

/** getClinicalDocumentInfo(msg)

  	- Desc: This function receives CCD msg and returns clinicalDocumentInfo object
  	- Modified:
 	2018-02-10 11:20 CT JER - Modified to include capturing the SetId and versionNumber attributes

   	@param {String} msg - this contains the raw message (string)
	@return {object} objClinDocInfo - this contains a clinical document info element specific values composed into a single objClinDocInfo object.
	History 
	2022-08-23 JER - Added channelMap to capture the CCDA Document Header Information
	2021-09-20 JER - Modified to incorporate logger.error prior to returning function value
*/
function getClinicalDocumentInfo(msg) {
	var scriptName= 'getClinicalDocumentInfo';
	var debug= 0;
	if(debug>0) { logger.error('DEBUG::'+scriptName+' function BEGIN msg: '+msg); }

		var objClinDocInfo= new Object();

		objClinDocInfo.extension=				msg['id']['@extension'].toString();
		objClinDocInfo.root=					msg['id']['@root'].toString();	
		objClinDocInfo.assigningAuthorityName=		msg['id']['@assigningAuthorityName'].toString();
		objClinDocInfo.code=					msg['code']['@code'].toString();
		objClinDocInfo.codeSystemOid=				msg['code']['@codeSystem'].toString();
		objClinDocInfo.codeSystemName=			msg['code']['@codeSystemName'].toString();
		objClinDocInfo.displayName=				msg['code']['@displayName'].toString();
		objClinDocInfo.title=					msg['title'].toString();
		objClinDocInfo.creationTime=				msg['effectiveTime']['@value'].toString().substring(0,14);
		objClinDocInfo.confCode=					msg['confidentialityCode']['@code'].toString();
		objClinDocInfo.confCodeSystemOid=			msg['confidentialityCode']['@codeSystem'].toString();
		objClinDocInfo.languageCode=				msg['languageCode']['@code'].toString();
		objClinDocInfo.setIdExtension=			msg['setId']['@extension'].toString();
		objClinDocInfo.setIdRoot=				msg['setId']['@root'].toString();	
		objClinDocInfo.setIdAssigningAuthorityName=	msg['setId']['@assigningAuthorityName'].toString();
		objClinDocInfo.documentVersion=			msg['versionNumber']['@value'].toString();

		// 2022-08-23 JER - Added channelMap to capture the CCDA Document Header Information
		if(debug>0) { logger.error('DEBUG::'+scriptName+' function objClinDocInfo: '+JSON.stringify(objClinDocInfo)); }
		channelMap.put('ClinDocInfo',JSON.stringify(objClinDocInfo));

		return objClinDocInfo;

}

/** getCustodian(msg)

	- Desc: This function receives CCD msg and returns an array of custodian objects 
	- Modified
	2018-02-11 17:49 CT JER - Newly created to store the custodian objects

	@param {String} msg - Raw msg
	@return {Array} return array of custodian objects
*/
function getCustodian(msg) {

	var debug= 0;
	var objCounter= 1;
	var arrCustodian= new Array();

	// uncertain if more than one assignedCustodians per ClinicalDocument
	for each(custodian in msg['custodian'][0]) {
		var objCustodian= new Object;
		objCustodian.id= objCounter;
		if(custodian.children().length()>0) {
			if(debug>0) { logger.error('custodian children()= '+custodian.children()); }
			for each(child in custodian.children()) {
				// each assignedCustodian
				if(child.localName()== 'assignedCustodian') {
					// <id assigningAuthorityName="HMS HOSPITAL NUMBER" extension="134" root="2.16.840.1.113883.3.1579.834848495152.134.500"/>
					if(typeof child.representedCustodianOrganization.id !== 'undefined') {
						objCustodian.representedOrganization_AssignAuthorityName=	child.representedCustodianOrganization.id.@assigningAuthorityName.toString();
						objCustodian.representedOrganization_Extension=  			child.representedCustodianOrganization.id.@extension.toString();
						objCustodian.representedOrganization_Root=  				child.representedCustodianOrganization.id.@root.toString();
					} // end if
					// e.g <name>HILL REGIONAL HOSPITAL</name>
					if(typeof child.representedOrganziation.name !== 'undefined') {
						objCustodian.representedOrganization=					child.representedCustodianOrganization.name.toString();
					}
					// <telecom use="WP" value="tel:(254)580-8500"/>
					if(typeof child.representedOrganziation.telecom !== 'undefined') {
						objCustodian.representedOrganization_telecom=			child.representedCustodianOrganization.telecom.@value.toString();
					}
					// <addr use="WP"><streetAddressLine>101 CIRCLE DRIVE</streetAddressLine><city>HILLSBORO</city><state>TX</state><postalCode>766450000</postalCode></addr>
					if(typeof child.representedOrganziation.addr !== 'undefined') {
						objCustodian.representedOrganization_streetAddress=				child.representedCustodianOrganization.addr.streetAddressLine.toString();
						objCustodian.representedOrganization_city=						child.representedCustodianOrganization.addr.city.toString();
						objCustodian.representedOrganization_state=						child.representedCustodianOrganization.addr.state.toString();
						objCustodian.representedOrganization_postalCode=					child.representedCustodianOrganization.addr.postalCode.toString();
					} // end if
				}
			} // end for each
		} // end if 
		objCounter++;
	} // end for each
	arrCustodian.push(objCustodian);

	return arrCustodian;
}

/** getEncounter(msg)

  	- Desc: This function receives CCD msg and returns Encounter object
  	- Modified
  	2018-02-18 20:23 CT JER - Modified to include the addition of healthcareFacility Location Name - use for HealthcareFacilityTypeCode displayName
  	2018-02-16 17:33 CT JER - Added association to the getAssignedEntity to retrieve the assignedEntity related elements and attributes
  	2018-02-16 15:55 CT JER - Added handling for DischargeDisposition elements and attributes
  	2018-02-11 17:34 CT JER - Modified startTime and stopTime by applying the substring(0,14) function to remove the datetime offset
  	2018-02-10 11:27 CT JER - Corrected the setting of the objEncounter extension, root, startTime and stopTime


   	@param {String} msg - this contains the raw message (string)
	@return {object} objEncounter - contains objEncounter object and related properties.
*/

function getEncounter(msg) {

var debug= 0;

	if(typeof msg['componentOf']['encompassingEncounter'] !== 'undefined') {
		if(msg['componentOf']['encompassingEncounter'].children().length()>0) {
			var objEncounter= new Object;

			// <id assigningAuthorityName="HMS PATIENT ACCOUNT NUMBER" extension="3480487" root="2.16.840.1.113883.3.1579.834848495152.134.300"/>
			if(typeof msg['componentOf']['encompassingEncounter']['id'] !== 'undefined') {
				if(typeof msg['componentOf']['encompassingEncounter']['id']['@assigningAuthorityName'] !== 'undefined') {
					if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][id][@assigningAuthorityName]: '+msg['componentOf']['encompassingEncounter']['id']['@assigningAuthorityName'].toString()); }
					objEncounter.assigningAuthorityName= msg['componentOf']['encompassingEncounter']['id']['@assigningAuthorityName'].toString();
				} // end if
				if(typeof msg['componentOf']['encompassingEncounter']['id']['@extension'] !== 'undefined') {
					if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][id][@extension]: '+msg['componentOf']['encompassingEncounter']['id']['@extension'].toString()); }
					objEncounter.extension= msg['componentOf']['encompassingEncounter']['id']['@extension'].toString();
				} // end if
				if(typeof msg['componentOf']['encompassingEncounter']['id']['@root'] !== 'undefined') {
					if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][id][@root]: '+msg['componentOf']['encompassingEncounter']['id']['@root'].toString()); }
					objEncounter.root= msg['componentOf']['encompassingEncounter']['id']['@root'].toString();
				} // end if
			} // end if

			// <effectiveTime><low value="20180203075200-0600"/><high value="20180203075200-0600"/></effectiveTime>
			if(typeof msg['componentOf']['encompassingEncounter']['effectiveTime'] !== 'undefined') {
				if(typeof msg['componentOf']['encompassingEncounter']['effectiveTime']['low']['@value'] !== 'undefined') {
					if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][effectiveTime][low][@value]: '+msg['componentOf']['encompassingEncounter']['effectiveTime']['low']['@value'].toString()); }
					objEncounter.startTime= msg['componentOf']['encompassingEncounter']['effectiveTime']['low']['@value'].toString().substring(0,14);
				} // end if
				if(typeof msg['componentOf']['encompassingEncounter']['effectiveTime']['high']['@value'] !== 'undefined') {
					if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][effectiveTime][high][@value]: '+msg['componentOf']['encompassingEncounter']['effectiveTime']['high']['@value'].toString()); }
					objEncounter.stopTime= msg['componentOf']['encompassingEncounter']['effectiveTime']['high']['@value'].toString().substring(0,14);
				} // end if
			} // end if

			//  <dischargeDispositionCode code="1" codeSystem="2.16.840.1.113883.12.112" codeSystemName="HL7 Discharge Disposition" displayName="DISCHARGED HOME/SELF CARE"/>
			if(typeof msg['componentOf']['encompassingEncounter']['dischargeDispositionCode'] !== 'undefined') {
				if(typeof msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@code'] !== 'undefined') {
					// get DischargeDisposition Code
					if(typeof msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@code'] !== 'undefined') {						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][dischargeDispositionCode][@code]: '+msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@code'].toString()); }
						objEncounter.dispositionCode= msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@code'].toString();
					} // end if
					// get DischargeDisposition CodeSystemOid
					if(typeof msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@codeSystem'] !== 'undefined') {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][dischargeDispositionCode][@codeSystem]: '+msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@codeSystem'].toString()); }
						objEncounter.dispositionCodeSystemOid= msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@codeSystem'].toString();
					} // end if
					// get DischargeDisposition CodeSystemName
					if(typeof msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@codeSystemName'] !== 'undefined') {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][dischargeDispositionCode][@codeSystemName]: '+msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@codeSystemName'].toString()); }						
						objEncounter.dispositionCodeSystemName= msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@codeSystemName'].toString();
					} // end if
					// get DischargeDisposition DisplayName
					if(typeof msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@displayName'] !== 'undefined') {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][dischargeDispositionCode][@displayName]: '+msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@displayName'].toString()); }
						objEncounter.dispositionDisplayName= msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['@displayName'].toString();
					} // end if
				} // end if 
				// get DischargeDisposition originalText
				if(typeof msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['originalText'] !== 'undefined') {
					if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][dischargeDispositionCode][originalText]: '+msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['originalText'].toString()); }
					objEncounter.dispositionDisplayName= msg['componentOf']['encompassingEncounter']['dischargeDispositionCode']['originalText'].toString();
				} // end if 
			} // end if 	

			// get encounterParticipant assignedEntity
			if(typeof msg['componentOf']['encompassingEncounter']['encounterParticipant'] !== 'undefined') {

				if(typeof msg['componentOf']['encompassingEncounter']['encounterParticipant']['@typeCode'] !== 'undefined') {
					if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][encounterParticipant][@typeCode]: '+msg['componentOf']['encompassingEncounter']['encounterParticipant']['@typeCode'].toString()); }
					objEncounter.encounterParticipant_code= msg['componentOf']['encompassingEncounter']['encounterParticipant']['@typeCode'].toString();
				} // end if

				// call getAssignedEntity
				var objAssignedEntity= getAssignedEntity(msg['componentOf']['encompassingEncounter']['encounterParticipant']);
				if(objAssignedEntity !== 'undefined') {

					// get assignedEntity Root
					if(objAssignedEntity.root != null) {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][encounterParticipant][assignedEntity][id][@root]: '+objAssignedEntity.root.toString()); }
						objEncounter.assignedEntity_root= objAssignedEntity.root.toString();
					} // end if
					// get assignedEntity Extension
					if(objAssignedEntity.extension != null) {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][encounterParticipant][assignedEntity][id][@extension]: '+objAssignedEntity.extension.toString()); }
						objEncounter.assignedEntity_extension= objAssignedEntity.extension.toString();
					} // end if
					// get assignedEntity AssigningAuthorityName
					if(objAssignedEntity.assigningAuthorityName != null) {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][encounterParticipant][assignedEntity][id][@assigningAuthorityName]: '+objAssignedEntity.assigningAuthorityName.toString()); }
						objEncounter.assignedEntity_assigningAuthorityName= objAssignedEntity.assigningAuthorityName.toString();
					} // end if

					// get assignedEntity code
					if(objAssignedEntity.code != null) {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][encounterParticipant][assignedEntity][code][@code]: '+objAssignedEntity.code.toString()); }
						objEncounter.assignedEntity_code= objAssignedEntity.code.toString();
					} // end if
					// get assignedEntity codeSystem
					if(objAssignedEntity.codeSystem != null) {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][encounterParticipant][assignedEntity][code][@codeSystem]: '+objAssignedEntity.codeSystem.toString()); }
						objEncounter.assignedEntity_codeSystem= objAssignedEntity.codeSystem.toString();
					} // end if
					// get assignedEntity codeSystemName
					if(objAssignedEntity.codeSystemName != null) {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][encounterParticipant][assignedEntity][code][@codeSystemName]: '+objAssignedEntity.codeSystemName.toString()); }
						objEncounter.assignedEntity_codeSystemName= objAssignedEntity.codeSystemName.toString();
					} // end if
					// get assignedEntity displayName
					if(objAssignedEntity.codeDisplayName != null) {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][encounterParticipant][assignedEntity][code][@displayName]: '+objAssignedEntity.codeDisplayName.toString()); }
						objEncounter.assignedEntity_codeDisplayName= objAssignedEntity.codeDisplayName.toString();
					} // end if

					// get assignedEntity Author GivenName
					if(objAssignedEntity.givenName != null) {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][encounterParticipant][assignedEntity][assignedPerson][name][@given]: '+objAssignedEntity.givenName.toString()); }
						objEncounter.assignedEntity_givenName= objAssignedEntity.givenName.toString();
					} // end if
					// get assignedEntity Author FamilyName
					if(objAssignedEntity.familyName != null) {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][encounterParticipant][assignedEntity][assignedPerson][name][@family]: '+objAssignedEntity.familyName.toString()); }
						objEncounter.assignedEntity_familyName= objAssignedEntity.familyName.toString();
					} // end if					
				} // end if 
			} // end if 

			// get HealthcareFacilityTypeCode
			if(typeof msg['componentOf']['encompassingEncounter']['location']['healthCareFacility'] !== 'undefined') {

				// set healthcareFacility classCode
				if(typeof msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['@classCode'] !== 'undefined') {
					if((typeof msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['@classCode'] != null)&&(msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['@classCode'].toString().length>0)) {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][location][healthcareFacility][@classCode].toString(): '+msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['@classCode'].toString()); }
						objEncounter.healthcareFacility_classCode= msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['@classCode'].toString();
					} // end if 
				} // end if

				// set healthcareFacility Location 
				if(typeof msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['location'] !== 'undefined') {

					// set healthcareFacility Location classCode
					if((typeof msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['location']['@classCode'] != null)&&(msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['location']['@classCode'].toString().length>0)) {
						if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][location][healthcareFacility][location][@classCode].toString(): '+msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['location']['@classCode'].toString()); }
						objEncounter.healthcareFacilityLocation_classCode= msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['location']['@classCode'].toString();
					} // end if 

					// set healthcareFacility Location Name - use for HealthcareFacilityTypeCode displayName
					if(typeof msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['location']['name'] !== 'undefined') {
						if((typeof msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['location']['name'] != null)&&(msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['location']['name'].toString().length>0)) {
							if(debug>0) { logger.error('msg[componentOf][encompassingEncounter][location][healthcareFacility][location][name].toString(): '+msg.componentOf.encompassingEncounter.location.healthcareFacility.location.name.toString()); }
							objEncounter.healthcareFacilityLocation_name= msg['componentOf']['encompassingEncounter']['location']['healthcareFacility']['location']['name'].toString();
							channelMap.put(healthcareFacilityDisplayName,objEncounter.healthcareFacilityLocation_name);
						} // end if 
					} // end if
				} // end if 
			} // end if 
		} // end if 
	} // end if

	return objEncounter;
}

/**  getEncounterDetails(object) - Desc: This function evaluates the Encounter Details Section of the CCDA to create arrObservations array of objObservations objects 

   	@param {String} msg - this contains the raw message (string)
	@return {object} objPatientRole - this contains the patientRole elements composed into a single objPatientRole object.

	History
	2021-09-20 JER - Modified to incorporate evaluation of multiple patientRole Id(s) in the case there are multiple patient identifiers
	2021-09-20 JER - Modified to patient.name incorporate evaluation of multiple patient name child elements in the case there are multiple patient identifiers
*/
function getEncounterDetails(section) {

	// define variables
	var debug= 0;
	var scriptName= 'getEncounterDetails';
	var arrObservations= [];

	// if(debug>0) { 
		logger.error('DEBUG::'+scriptName+' typeof(section)= '+typeof(section)+' section= '+section); 
	// }

	logger.error('DEBUG::'+scriptName+' A'+'\n');

	if (typeof(section['entry']['encounter']['entryRelationship']['act'] !== 'undefined')) {

		logger.error('DEBUG::'+scriptName+' B act.children()= '+section['entry']['encounter']['entryRelationship']['act'].children()+'\n');

		var cnt= 0;
		for(child in section['entry']['encounter']['entryRelationship']['act'].children()) {

			logger.error('DEBUG::'+scriptName+' C'+'\n');

			if(child.localName()== 'entryRelationship') {
				if (child['@typeCode'].toString() == 'SUBJ') {

					logger.error('DEBUG::'+scriptName+' D'+'\n');
					var objObservation= new Object();
					if(debug>0) logger.error('DEBUG::'+scriptName+': entryRelationship.act= '+entryRelationship['@typeCode'].toString()+' of entryRelationship= '+entryRelationship);
					objObservation.id= cnt;
					objObservation.typeCode= 'SUBJ';

					cnt++;
					logger.error('DEBUG::'+scriptName+': objObservation= '+objObservation);
					arrObservations.push(objObservation);
				}
			}
		}
	}

	channelMap.put('arrObservations',arrObservations);
	return arrObservations;
}

/**  getEthnicGroupCode(ethnicity) - Desc: This function receives CCD/CCDA msg and returns from getEthnicGroupCode Object

   	@param {String} msg - this contains the raw message (string)
	@return {object} objEthnicity - this contains the elements composed into a single objEthnicity object.

	History
	2022-08-21 JER - Handling of nullFlavor property with UNK value
	2022-08-17 JER - Modifying arrEthnicity from array to objEthnicity as a singular object
	2021-09-20 JER - leveraging the getTelecom for getEthnicGroupCode script
*/
function getEthnicGroupCode(ethnicity) {

	// define variables
	var debug= 0;
	var scriptName= 'getEthnicGroupCode';
	// var arrEthnicity= new Array();

	if(debug>0) { logger.error(scriptName+' ethnicity= '+ethnicity); }
	if (typeof(ethnicity) !== 'undefined') {

		if(debug>0) { 
			logger.error('DEBUG::'+scriptName+' object.length= '+ethnicity.length+' value= '+ethnicity);
		}

		var j= 0;
		for(var i= 0;i<ethnicity.length();i++) {

			if(debug>0) { 
				logger.error('DEBUG::'+scriptName+' typeof(ethnicity['+i+'][@code])= '+typeof(ethnicity[i]['@code'])+' ethnicity[@code]= '+ethnicity[i]['@code']);
			}

			var objEthnicity= new Object();
			// 2022-08-19 JER - Added evaluation of code if undefined or has toString gt zero (0) 
			if((typeof(ethnicity[i]['@code'])!== 'undefined')&&(ethnicity[i]['@code'].toString().length>0)) {

				if(debug> 0) { logger.error('DEBUG::'+scriptName+' ethnicity['+i+'][@code]: '+ethnicity[i]['@code'].toString()); }

				objEthnicity.id= j;
				objEthnicity.code= ethnicity[i]['@code'].toString();
				objEthnicity.codeSystem= ethnicity[i]['@codeSystem'].toString();
				objEthnicity.codeSystemName= ethnicity[i]['@codeSystemName'].toString();
				objEthnicity.displayName= ethnicity[i]['@displayName'].toString();

			} else {
				logger.error('B');
				if(debug> 0) { logger.error('DEBUG::'+scriptName+' ethnicity['+i+'][@code]: '+ethnicity[i]['@code'].toString()); }

				objEthnicity.id= j;
				objEthnicity.code= 'LA4489-6';
				objEthnicity.codeSystem= '1.3.6.1.4.1.12009.10.1.1817';
				objEthnicity.codeSystemName= 'LOINC UCUM Codes for Electronic Messaging'
				objEthnicity.displayName= 'Unknown'

				if(debug>0) {
					logger.error('DEBUG::'+scriptName+' function objEthnicity: '+JSON.stringify(objEthnicity));
				}
			} // end if 

			if(debug>0) { 
				logger.error('DEBUG::'+scriptName+' function objEthnicity: '+JSON.stringify(objEthnicity)); 
			}
			j++;
		} // for each address element 

		//2022-08-17 JER - Modifying arrEthnicity from array to objEthnicity as a singular object
		// channelMap.put('arrEthnicity',JSON.stringify(arrEthnicity));
		// return arrEthnicity;
		if(objEthnicity) {
			return objEthnicity;
		} // end if 
	} else {
		return;
	} // end if 
}

/**  getLanguageCommunication(language) - Desc: This function receives CCD/CCDA msg and returns from getLanguageCommunication Object

