/** getXdsSourcePatientInfo(object)

  	- Desc: This function receives patientRole JavaScript Object and returns a XML SourcePatientInfo JavaScript object

	@param {object} objPatientRole - the objPatientRole object passed input argument
	@return {object} return object containing the newly devised string for XML containing the sourcePatientInfo
*/
// @return {XML} return stringXML containing the newly devised XML containing the sourcePatientInfo
function getXdsSourcePatientInfo(objPatientRole) {

	var objSourcePatientInfo= new Object;
	var rim = new Namespace('urn:oasis:names:tc:ebxml-regrep:xsd:rim:3.0');
	var stringXML= new XML('<ValueList/>');

	var pid_3=  "PID-3|"+objPatientRole.patientId+"^^^"+objPatientRole.assigningAuthorityName+"&amp;"+objPatientRole.assigningAuthorityOid+"&amp;ISO"
	var stringXML= stringXML.appendChild('<Value>'+pid_3+'</Value>');
	objSourcePatientInfo.pid3= pid_3;

	var pid_5=  "PID-5|"+objPatientRole.lastName+"^"+objPatientRole.firstName+"^^^^"
	var stringXML= stringXML.appendChild('<Value>'+pid_5+'</Value>');
	objSourcePatientInfo.pid5= pid_5;

	var pid_7=  "PID-7|"+objPatientRole.dateOfBirth
	var stringXML= stringXML.appendChild('<Value>'+pid_7+'</Value>');
	objSourcePatientInfo.pid7= pid_7;

	var pid_8=  "PID-8|"+objPatientRole.gender
	var stringXML= stringXML.appendChild('<Value>'+pid_8+'</Value>');
	objSourcePatientInfo.pid8= pid_8;

	var pid_11= "PID-11|"+objPatientRole.streetAddress+"^^"+objPatientRole.city+"^"+objPatientRole.state+"^"+objPatientRole.postalCode+"^"+objPatientRole.country
	var stringXML= stringXML.appendChild('<Value>'+pid_11+'</Value>');
	objSourcePatientInfo.pid11= pid_11;

	var pid_13= "PID-13|"+objPatientRole.phoneNumber
	var stringXML= stringXML.appendChild('<Value>'+pid_13+'</Value>');
	objSourcePatientInfo.pid13= pid_13;

	return objSourcePatientInfo;
}

/** setXdsMetadataAuthor(object)

  	- Desc: This setXdsAuthor function receives Javascript Array of Objects and author XML string
  	- Modified:
  	2018-02-26 19:20 CST - Under development
	2018-02-25 12:40PM CST - Replacement of the Call getPerformer, getAuthor and usage of objEncounter transformation function.

   	@param {object} arrPerformer - this contains the Javascript Object (object) - could pass in 
	@return {XML} authorXML - contains authorXML XML Object string to build out the Template : XDSDocumentEntry.author (urn:uuid:93606bcf-9494-43ec-9b4e-a7748d1a838d) XML scheme.

	Reference: https://gazelle.ihe.net/XDStarClient/rimihe-templates/rimihe-Author_XDSDocumentEntry.html

*/

function setXdsMetadataAuthor(arrAuthors) {

	var debug= 1;
	var i= 0;

	if(debug>0) { logger.error('setXdsAuthor arrAuthors: '+JSON.stringify(arrAuthors)); } 

	if(typeof arrAuthor !== 'undefined') {
		if(arrAuthor.length>1) {
			for each(objAuthor in arrAuthors) {
				if(debug>0) { logger.error('objAuthor['+i+']: '+JSON.stringify(objAuthor)+
					' typeof: '+typeof objAuthor+
					' length: '+objAuthor.length());
				} // end if 

				var authorXML = new XML('<rim:Classification id="${uuid}" classificationScheme="urn:uuid:93606bcf-9494-43ec-9b4e-a7748d1a838d" classifiedObject="${extrinsicObjectUUID}"></rim:Classification>');

				// need to confirm the objAuthor contains the representedOrganization
				// create Author Person - using either objAuthor.assignedEntity_familyName OR objEncounter.assignedEntity_familyName call the getAuthorPerson function
				if((typeof objAuthor.assignedEntity_familyName !== 'undefined')&&(objAuthor.assignedEntity_familyName.toString() != null)) { 
					var xmlAuthor= new XML('<rim:Slot name="authorPerson"><rim:ValueList><rim:Value></rim:Value></rim:ValueList></rim:Slot>');
					xmlAuthor['rim:Slot']['rim:ValueList']['rim:Value']= getAuthorPerson(objAuthor);
				} else {
					var xmlAuthor= new XML('<rim:Slot name="authorPerson"><rim:ValueList><rim:Value></rim:Value></rim:ValueList></rim:Slot>');
				} // end if 

				// create Author Institution - objAuthor.authorInstitution
				if((typeof objAuthor.authorInstitution !== 'undefined')&&(objAuthor.authorInstitution.toString() != null)) {
					var xmlAuthor= new XML('<rim:Slot name="authorInstitution"><rim:ValueList><rim:Value></rim:Value></rim:ValueList></rim:Slot>');
					xmlAuthor['rim:Slot']['rim:ValueList']['rim:Value']= objAuthor.authorInstitution.toString();
				} else {
					var xmlAuthor= new XML('<rim:Slot name="authorInstitution"><rim:ValueList><rim:Value></rim:Value></rim:ValueList></rim:Slot>');
				} // end if

				// create Author Role 
				// objPerformers.functionDisplayName
				if((typeof objAuthor.functionDisplayName !== 'undefined')&&(objAuthor.functionDisplayName.toString() != null)) {
					if(objAuthor.functionDisplayName.toString().length>0) {
						var xmlAuthor= new XML('<rim:Slot name="authorRole"><rim:ValueList><rim:Value></rim:Value></rim:ValueList></rim:Slot>');
						xmlAuthor['rim:Slot']['rim:ValueList']['rim:Value']= objAuthor.functionDisplayName.toString();
					} // end if 
					// objEncounter.encounterParticipant_code
				} else if((typeof objAuthor.encounterParticipant_code !== 'undefined')&&(objAuthor.encounterParticipant_code.toString() != null)) {
					if(objAuthor.encounterParticipant_code.toString().length>0) {
						var xmlAuthor= new XML('<rim:Slot name="authorRole"><rim:ValueList><rim:Value></rim:Value></rim:ValueList></rim:Slot>');
						xmlAuthor['rim:Slot']['rim:ValueList']['rim:Value']= objAuthor.functionDisplayName.toString();
					} // end if 
				// objEncounter.assignedEntity_codeDisplayName
				} else if((typeof objAuthor.assignedEntity_codeDisplayName !== 'undefined')&&(objAuthor.assignedEntity_codeDisplayName.toString() != null)) {
					if(objAuthor.assignedEntity_codeDisplayName.toString().length>0) {
						var xmlAuthor= new XML('<rim:Slot name="authorRole"><rim:ValueList><rim:Value></rim:Value></rim:ValueList></rim:Slot>');
						xmlAuthor['rim:Slot']['rim:ValueList']['rim:Value']= objAuthor.assignedEntity_codeDisplayName.toString();
					} // end if 
				} else {
					var xmlAuthor= new XML('<rim:Slot name="authorRole"><rim:ValueList><rim:Value>Medical Doctor</rim:Value></rim:ValueList></rim:Slot>');
				} // end if 

				// create Author Specialty
				// objPerformers.assignedDisplayName
				if((typeof objAuthor.assignedDisplayName !== 'undefined')&&(objAuthor.assignedDisplayName.toString() != null)) {
					if(objAuthor.assignedDisplayName.toString().length>0) {
						var xmlAuthor= new XML('<rim:Slot name="authorSpecialty"><rim:ValueList><rim:Value></rim:Value></rim:ValueList></rim:Slot>');
						xmlAuthor['rim:Slot']['rim:ValueList']['rim:Value']= objAuthor.assignedDisplayName.toString();
					} // end if 
				// objEncounter.assignedEntity_codeDisplayName
				} else if((typeof objAuthor.assignedEntity_codeDisplayName !== 'undefined')&&(objAuthor.assignedEntity_codeDisplayName.toString() != null)) {
					if(objAuthor.assignedEntity_codeDisplayName.toString().length>0) {
						var xmlAuthor= new XML('<rim:Slot name="authorSpecialty"><rim:ValueList><rim:Value></rim:Value></rim:ValueList></rim:Slot>');
						xmlAuthor['rim:Slot']['rim:ValueList']['rim:Value']= objAuthor.assignedEntity_codeDisplayName.toString();
					} // end if 
				} else {
					var xmlAuthor= new XML('<rim:Slot name="authorSpecialty"><rim:ValueList><rim:Value>General Medicine</rim:Value></rim:ValueList></rim:Slot>');
				} // end if 
			} // end for each
		} // end if 
	} else {		
		var classificationXML=     ['rim:Classification']['@id']="1234"; 
		classificationXML+=        ['rim:Classification']['@ClassificationScheme']= "urn:uuid:93606bcf-9494-43ec-9b4e-a7748d1a838d";
		classificationXML+=        ['rim:Classification']['@classifiedObject']= "";
		var authorXML = new XML(classificationXML);

		var authorPersonXML=    	new XML('<rim:Slot name="authorPerson"></rim:Slot>');
		authorPersonXML+= 		new XML('<rim:ValueList></rim:ValueList>');
		authorPersonXML+=	 	new XML('<rim:Value></rim:Value>');
		authorXML+= authorPersonXML; 

		var authorInstitutionXML=    new XML('<rim:Slot name="authorInstitution"></rim:Slot>');
		authorInstitutionXML+= 		new XML('<rim:ValueList></rim:ValueList>');
		authorInstitutionXML+=		new XML('<rim:Value></rim:Value>');
		authorXML+= authorInstitutionXML;

		var authorRoleXML=  new XML('<rim:Slot name="authorRole"></rim:Slot>');
		authorRoleXML+= 	new XML('<rim:ValueList></rim:ValueList>');
		authorRoleXML+=	new XML('<rim:Value></rim:Value>');
		authorXML+= autorRoleXML;

		var authorSpecialtyXML =		new XML('<rim:Slot name="authorSpecialty"></rim:Slot>');
		authorSpecialtyXML+= new XML('<rim:ValueList></rim:ValueList>');
		authorSpecialtyXML+= new XML('<rim:Value></rim:Value>');
		authorXML+= authorSpecialtyXML;

	} // end if 	

	return authorXML;
} // end function

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} csv - CSV string with column headers
	@return {String} return JSON object using column headers and field keys
*/
function convertCsvToJson(csv) {
	var textXML = SerializerFactory.getSerializer('DELIMITED').toXML(csv);
	var xml = new XML(textXML);
	var dataItems = [];
	for (var i = 1; i <= xml['row'].length() - 1; i++) {
		var jsonData = {};
		for (var j = 1; j <= xml['row'][0].children().length(); j++) {
			var column = 'column' + j.toString();
			var header = xml['row'][0][column].toString().replace(/\s/g, '_').replace(/-/g, '_').toLowerCase();
			var value = xml['row'][i][column].toString();

			jsonData[header] = value;

		}
		dataItems.push(jsonData);
	}

	return JSON.stringify(dataItems);
}

/**
	Converts JSON to CSV.
**/
/*	@param {String} jsonData - JSON string with [0] index key as headers for csv
	@return {String} return csv
*/	

function convertJsonToCsv(data) {
	try{
		var json = JSON.parse(data);	
		var fields = Object.keys(json[0])
		var replacer = function(key, value) { return value === null ? '' : value } 
		var csv = json.map(function(row){
		  return fields.map(function(fieldName){
		    return JSON.stringify(row[fieldName], replacer)
		  }).join(',')
		})
		csv.unshift(fields.join(',')) // add header column
		csv = csv.join('\r\n');
		return csv;
	}catch(error){
		return error;
	}
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function sendMessageToChannelDestination(channelID, destinationID, map, data) {
	var msgMap;
	var outData = {};

	if (!map) {
		msgMap = Maps.map();
	} else {
		msgMap = Maps.map(map);
	}

	if (data == null || data == undefined) {
		data = '';
	}

	if (msgMap.get('sourceChannelId') == null) {
		msgMap.add('sourceChannelId', channelId);
	}
	if (msgMap.get('sourceChannelName') == null) {
		msgMap.add('sourceChannelName', channelName);
	}

	// Dirty way to handle connector message not being defined. Handles cases where this function 
	// is used in a source js writer.
	try {
		if (msgMap.get('sourceMessageId') == null && connectorMessage && connectorMessage.getMessageId()) {
			msgMap.add('sourceMessageId', connectorMessage.getMessageId().toString());
		}
	} catch(ex) {}

	var msgRequest = new RawMessage(data, [destinationID], msgMap);
	var msgResponse = router.routeMessageByChannelId(channelID, msgRequest);

	if (msgResponse == null) {
		// return;
		throw ChannelUtil.getChannelName(channelID) + '" does not return a valid response object'; 
	}
	if (msgResponse.getStatus() == null) {
		throw ChannelUtil.getChannelName(channelID) + '" does not return a valid response object';
	}

	if (msgResponse.getStatus() == 'ERROR') {
		throw msgResponse.getMessage();
	}

	return msgResponse.getMessage();


	// var msgResponse;

	/*
	try {
		msgResponse = router.routeMessageByChannelId(channelID, msgRequest);
		outData.status = 'success';
		outData.status_message = 'message successfully sent to channel';
	} catch (ex) {
		outData.status = 'error';
		outData.status_message = ex.toString();
		return outData;
	}

	if (msgResponse.getStatus() == null) {
		outData.response_status = 'unknown';
		logger.error('Code Template "Send message to channel by id": channel "' + ChannelUtil.getChannelName(channelID) + '" does not return a valid response object');
	}

	switch (String(msgResponse.getStatus())) {
		case 'ERROR':
			outData.response_status = 'error';
			break;
		case 'FILTERED':
			outData.response_status = 'filtered';
			break;
		case 'QUEUED':
			outData.response_status = 'queued';
			break;
		case 'RECEIVED':
			outData.response_status = 'received';
			break;
		case 'SENT':
			outData.response_status = 'sent';
			break;
	}

	outData.response_message = msgResponse.getMessage();

	return outData;
	*/
}

/**
	Get a new bots database connection. Be sure to wrap in try/finally block to close database connection.

	@param {String} environment - prod, staging, dev, local
	@return {String} return database connection
*/
function GetBotsDBConnection() {
	var username = configurationMap.get('bots-db-user');
	var password = configurationMap.get('bots-db-pass');
	var uri = configurationMap.get('bots-db-uri');	
	return DatabaseConnectionFactory.createDatabaseConnection('org.postgresql.Driver', uri, username, password);
}

/**
	Get a new database connection. 

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function GetDBConnection(environment) {
	if (environment != 'prod' && environment != 'staging' && environment != 'dev' && environment != 'local') {
		return null;
	}

	var username = configurationMap.get('docsink-db-user-' + environment);
	var password = configurationMap.get('docsink-db-pass-' + environment);
	var uri = configurationMap.get('docsink-db-uri-' + environment);

	return DatabaseConnectionFactory.createDatabaseConnection('com.mysql.cj.jdbc.Driver', uri, username, password);
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function generateChannelResponse() {
	// This script executes once after a message has been processed
	// Responses returned from here will be stored as "Postprocessor" in the response map

	var destinations = sourceMap.get('destinationSet');
	var metadataIds = destinations.toArray();

	if (metadataIds.length == 0) {
		return ResponseFactory.getErrorResponse('invalid request');
	}

	var smsg = message.getConnectorMessages().get(0);
	if (smsg.getStatus() == 'ERROR') {
		if (smsg.getProcessingError()) {
			return ResponseFactory.getErrorResponse(smsg.getProcessingError());
		}
		return ResponseFactory.getErrorResponse(smsg.getResponseData().getMessage());
	}

	var metadataID = metadataIds[0];
	var cmsg = message.getConnectorMessages().get(metadataID);

	if (cmsg.getStatus() == 'ERROR') {
		if (cmsg.getProcessingError()) {
			return ResponseFactory.getErrorResponse(cmsg.getProcessingError());
		}
		return ResponseFactory.getErrorResponse(cmsg.getResponseData().getMessage());
	} else if (cmsg.getStatus() == 'SENT') {
		return ResponseFactory.getSentResponse(cmsg.getResponseData().getMessage());
	} else if (cmsg.getStatus() == 'FILTERED') {
		return ResponseFactory.getFilteredResponse(cmsg.getResponseData().getMessage());
	} else if (cmsg.getStatus() == 'QUEUED') {
		return ResponseFactory.getQueuedResponse(cmsg.getResponseData().getMessage());
	} else if (cmsg.getStatus() == 'RECEIVED') {
		return ResponseFactory.getSentResponse(cmsg.getResponseData().getMessage());
	} else {
		return cmsg;
	}
}

/** getHL7v3AdministrativeGender(code)

  	- Desc: This function receives a code system code (string) and returns adminGender array contains both the code and displayName

   	@param {String} code - this contains a code system code (string)
	@return {array} adminGender - this array contains the Gender and DisplayName (key,value) pair in the adminGender array.
*/

function getHL7v3AdministrativeGender(code) {

	// define local variables
	var adminGender= {};
	adminGender.codeSystem= "2.16.840.1.133883.5.1"
	adminGender.codeSystemName= "HL7 AdministrativeGender";

	if("M" == code) {
		adminGender.code= "M";
		adminGender.displayName= "Male";
	} else if ("F" == code) {
		adminGender.code= "F";
		adminGender.displayName= "Female";
	} else {
		adminGender.code= "UN";
		adminGender.displayName= "Undifferentiated";
	}

	return adminGender;

}

/** getHL7v3Confidentiality(code)

  	- Desc: This function receives a code system code (string) and returns confidentiality array contains both the code and displayName

   	@param {String} code - this contains a code system code (string)
	@return {array} confidentiality - this array contains the Confidentiality Code and DisplayName (key,value) pair in the confidentialityCode array.
*/

function getHL7v3Confidentiality(code) {

	// define local variables
	var confidentiality= {};
	confidentiality.codeSystem= "2.16.840.1.113883.5.25"
	confidentiality.codeSystemName= "HL7 Confidentiality";

	if("N" == code) {
		confidentiality.code= "N";
		confidentiality.displayName= "Normal";
	} else if ("R" == code) {
		confidentiality.code= "R";
		confidentiality.displayName= "Restricted";
	} else {
		confidentiality.code= "V";
		confidentiality.displayName= "Very Restricted";
	}

	return confidentiality;

}

