properties.code:
/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} csv - CSV string with column headers
	@return {String} return JSON object using column headers and field keys
*/
function convertCsvToJson(csv) {
	var textXML = SerializerFactory.getSerializer('DELIMITED').toXML(csv);
	var xml = new XML(textXML);
	var dataItems = [];
	for (var i = 1; i <= xml['row'].length() - 1; i++) {
		var jsonData = {};
		for (var j = 1; j <= xml['row'][0].children().length(); j++) {
			var column = 'column' + j.toString();
			var header = xml['row'][0][column].toString().replace(/\s/g, '_').replace(/-/g, '_').toLowerCase();
			var value = xml['row'][i][column].toString();

			jsonData[header] = value;

		}
		dataItems.push(jsonData);
	}

	return JSON.stringify(dataItems);
}

/**
	Converts JSON to CSV.
**/
/*	@param {String} jsonData - JSON string with [0] index key as headers for csv
	@return {String} return csv
*/	

function convertJsonToCsv(data) {
	try{
		var json = JSON.parse(data);	
		var fields = Object.keys(json[0])
		var replacer = function(key, value) { return value === null ? '' : value } 
		var csv = json.map(function(row){
		  return fields.map(function(fieldName){
		    return JSON.stringify(row[fieldName], replacer)
		  }).join(',')
		})
		csv.unshift(fields.join(',')) // add header column
		csv = csv.join('\r\n');
		return csv;
	}catch(error){
		return error;
	}
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function sendMessageToChannelDestination(channelID, destinationID, map, data) {
	var msgMap;
	var outData = {};

	if (!map) {
		msgMap = Maps.map();
	} else {
		msgMap = Maps.map(map);
	}

	if (data == null || data == undefined) {
		data = '';
	}

	if (msgMap.get('sourceChannelId') == null) {
		msgMap.add('sourceChannelId', channelId);
	}
	if (msgMap.get('sourceChannelName') == null) {
		msgMap.add('sourceChannelName', channelName);
	}

	// Dirty way to handle connector message not being defined. Handles cases where this function 
	// is used in a source js writer.
	try {
		if (msgMap.get('sourceMessageId') == null && connectorMessage && connectorMessage.getMessageId()) {
			msgMap.add('sourceMessageId', connectorMessage.getMessageId().toString());
		}
	} catch(ex) {}

	var msgRequest = new RawMessage(data, [destinationID], msgMap);
	var msgResponse = router.routeMessageByChannelId(channelID, msgRequest);

	if (msgResponse == null) {
		// return;
		throw ChannelUtil.getChannelName(channelID) + '" does not return a valid response object'; 
	}
	if (msgResponse.getStatus() == null) {
		throw ChannelUtil.getChannelName(channelID) + '" does not return a valid response object';
	}

	if (msgResponse.getStatus() == 'ERROR') {
		throw msgResponse.getMessage();
	}

	return msgResponse.getMessage();


	// var msgResponse;

	/*
	try {
		msgResponse = router.routeMessageByChannelId(channelID, msgRequest);
		outData.status = 'success';
		outData.status_message = 'message successfully sent to channel';
	} catch (ex) {
		outData.status = 'error';
		outData.status_message = ex.toString();
		return outData;
	}

	if (msgResponse.getStatus() == null) {
		outData.response_status = 'unknown';
		logger.error('Code Template "Send message to channel by id": channel "' + ChannelUtil.getChannelName(channelID) + '" does not return a valid response object');
	}

	switch (String(msgResponse.getStatus())) {
		case 'ERROR':
			outData.response_status = 'error';
			break;
		case 'FILTERED':
			outData.response_status = 'filtered';
			break;
		case 'QUEUED':
			outData.response_status = 'queued';
			break;
		case 'RECEIVED':
			outData.response_status = 'received';
			break;
		case 'SENT':
			outData.response_status = 'sent';
			break;
	}

	outData.response_message = msgResponse.getMessage();

	return outData;
	*/
}

/**
	Get a new bots database connection. Be sure to wrap in try/finally block to close database connection.

	@param {String} environment - prod, staging, dev, local
	@return {String} return database connection
*/
function GetBotsDBConnection() {
	var username = configurationMap.get('bots-db-user');
	var password = configurationMap.get('bots-db-pass');
	var uri = configurationMap.get('bots-db-uri');	
	return DatabaseConnectionFactory.createDatabaseConnection('org.postgresql.Driver', uri, username, password);
}

/**
	Get a new database connection. 

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function GetDBConnection(environment) {
	if (environment != 'prod' && environment != 'staging' && environment != 'dev' && environment != 'local') {
		return null;
	}

	var username = configurationMap.get('docsink-db-user-' + environment);
	var password = configurationMap.get('docsink-db-pass-' + environment);
	var uri = configurationMap.get('docsink-db-uri-' + environment);

	return DatabaseConnectionFactory.createDatabaseConnection('com.mysql.cj.jdbc.Driver', uri, username, password);
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function generateChannelResponse() {
	// This script executes once after a message has been processed
	// Responses returned from here will be stored as "Postprocessor" in the response map

	var destinations = sourceMap.get('destinationSet');
	var metadataIds = destinations.toArray();

	if (metadataIds.length == 0) {
		return ResponseFactory.getErrorResponse('invalid request');
	}

	var smsg = message.getConnectorMessages().get(0);
	if (smsg.getStatus() == 'ERROR') {
		if (smsg.getProcessingError()) {
			return ResponseFactory.getErrorResponse(smsg.getProcessingError());
		}
		return ResponseFactory.getErrorResponse(smsg.getResponseData().getMessage());
	}

	var metadataID = metadataIds[0];
	var cmsg = message.getConnectorMessages().get(metadataID);

	if (cmsg.getStatus() == 'ERROR') {
		if (cmsg.getProcessingError()) {
			return ResponseFactory.getErrorResponse(cmsg.getProcessingError());
		}
		return ResponseFactory.getErrorResponse(cmsg.getResponseData().getMessage());
	} else if (cmsg.getStatus() == 'SENT') {
		return ResponseFactory.getSentResponse(cmsg.getResponseData().getMessage());
	} else if (cmsg.getStatus() == 'FILTERED') {
		return ResponseFactory.getFilteredResponse(cmsg.getResponseData().getMessage());
	} else if (cmsg.getStatus() == 'QUEUED') {
		return ResponseFactory.getQueuedResponse(cmsg.getResponseData().getMessage());
	} else if (cmsg.getStatus() == 'RECEIVED') {
		return ResponseFactory.getSentResponse(cmsg.getResponseData().getMessage());
	} else {
		return cmsg;
	}
}

/** This script handles the transformation to update HL7 message header during anonymization

	@param {String} msg - provide the hl7 message to have the message header modified to reflect anonmymization
	@return {String} return description

	scriptName:	anonymize_hl7_message_header.js
	desc:		This script handles the modification message header modified to reflect anonmymization
	date:		2022-05-36 
	history:
	2022-08-12 19:50 JER - Modified to MSH-4 statically always set to QS1
	2022-07-18 19:07 JER - Modified to evaluate the segments length() to determine if children nodes
*/
function anonymize_hl7_message_header(msg) {

	var scriptName= "anonymize_hl7_message_header";
	var debug= 0;
	var currentHeader= new Object();

	if(debug>0) { logger.error('DEBUG::'+scriptName+' msg[MSH] length: '+msg['MSH'].length()); }

	if(msg['MSH'].length()>0) {

		// get MSH segment data
		var curSendingApplication= msg['MSH']['MSH.3']['MSH.3.1'].toString();
		// 2022-08-12 JER - Modified to MSH-4 statically always set to QS1		
		// var curSendingFacility= 'QS1';
		var curSendingFacility= msg['MSH']['MSH.4']['MSH.4.1'].toString();
		globalMap.put('gSendingFacility',curSendingFacility);
		var curReceivingApplication= msg['MSH']['MSH.5']['MSH.5.1'].toString();
		var curReceivingFacility= msg['MSH']['MSH.6']['MSH.6.1'].toString();
		var messageDateTime= msg['MSH']['MSH.7']['MSH.7.1'].toString();
		var curMessageType= msg['MSH']['MSH.9']['MSH.9.1'].toString()+'^'+msg['MSH']['MSH.9']['MSH.9.2'].toString();
		var messageControlId= msg['MSH']['MSH.10']['MSH.10.1'].toString();

		if(debug>0) { logger.error('DEBUG::'+scriptName+': sendingApplication= '+curSendingApplication+' sendingFacility= '+curSendingFacility+' receivingApplication= '+curReceivingApplication+' receivingFacility= '+curReceivingFacility+' messageType= '+curMessageType+' messageDateTime= '+messageDateTime+' messageControlId= '+messageControlId); }

		// collect the currentHeader Object Properties
		if(evaluate_defined_variable(curSendingApplication,'msh-3')) 	{ currentHeader.sendingApplication= curSendingApplication; }
		if(evaluate_defined_variable(curSendingFacility,'msh-4')) 		{ currentHeader.sendingFacility= curSendingFacility; }
		if(evaluate_defined_variable(curReceivingApplication,'msh-5')) 	{ currentHeader.receivingApplication= curReceivingApplication; }
		if(evaluate_defined_variable(curReceivingFacility,'msh-6')) 	{ currentHeader.receivingFacility= curReceivingFacility; }
		if(evaluate_defined_variable(curMessageType,'msh-7')) 			{ currentHeader.messageType= curMessageType; }
		if(evaluate_defined_variable(messageDateTime,'msh-9')) 		{ currentHeader.messageDateTime= messageDateTime; }
		if(evaluate_defined_variable(messageControlId,'msh-10')) 		{ currentHeader.messageControlId= messageControlId; }

		channelMap.put('currentHeader',currentHeader);
		if(debug>0) { logger.error('DEBUG::'+scriptName+' currentHeader: '+JSON.stringify(currentHeader)); }
	} // end if 

	if(debug>0) { logger.error('DEBUG::'+scriptName+' msg[EVN] length: '+msg['EVN'].length()); }

	if(msg['EVN'].length()>0) {

		// get EVN segment data
		var curEventTypeCode= msg['EVN']['EVN.1'].toString();
		var eventDateTime= msg['EVN']['EVN.2']['EVN.2.1'].toString();

		if(debug>0) { logger.error('DEBUG::'+scriptName+': eventTypeCode= '+curEventTypeCode+' eventDateTime= '+eventDateTime); }

		// collect the currentHeader Object Properties
		if(evaluate_defined_variable(curEventTypeCode,'evn-1'))	{ currentHeader.eventTypeCode= curEventTypeCode; }
		if(evaluate_defined_variable(eventDateTime,'evn-2')) 		{ currentHeader.eventDateTime= eventDateTime; }

		channelMap.put('currentHeader',currentHeader);
		if(debug>0) { logger.error('DEBUG::'+scriptName+' currentHeader: '+JSON.stringify(currentHeader)); }
	} // end if 

	// new Header
	var newHeader= new Object();

	// global channel Map configuration specific to this channel referenced across all messages
	// pull the global channel map variable: gcSendingApplication
	if(evaluate_defined_variable($gc('gcSendingApplication'),'gcSendingApplication')) { 
		var sendingApplication= $gc('gcSendingApplication');
	} else {
		var sendingApplication= curSendingApplication;
	}
	// pull the global channel map variable: gcSendingFacility
	if(evaluate_defined_variable($gc('gcSendingFacility'),'gcSendingFacility')) { 
		var sendingFacility= $gc('gcSendingFacility');
	} else {
		var sendingFacility= curSendingFacility;
	}
	// pull the global channel map variable: gcReceivingApplication
	if(evaluate_defined_variable($gc('gcReceivingApplication'),'gcReceivingApplication')) { 
		var receivingApplication= $gc('gcReceivingApplication');
	} else {
		var receivingApplication= curReceivingApplication;
	}
	// pull the global channel map variable: gcReceivingFacility
	if(evaluate_defined_variable($gc('gcReceivingFacility'),'gcReceivingFacility')) { 
		var receivingFacility= $gc('gcReceivingFacility');
	} else {
		var receivingFacility= curReceivingFacility;
	}
	// pull the global channel map variable: gcMessageType
	if(evaluate_defined_variable($gc('gcMessageType'),'gcMessageType')) { 
		var messageType= $gc('gcMessageType');
	} else {
		var messageType= curMessageType;
	}
	var messageDateTime=  current_date_get_hl7_formatted_datetime('datetime');
	var messageControlId= UUIDGenerator.getUUID();
	var eventMessageType= messageType.toString().split('^')[1];
	var eventDateTime=    messageDateTime;

	if(msg['MSH'].length()>0) {
		// set newPatient Object
		newHeader.sendingApplication= 	sendingApplication;
		newHeader.sendingFacility=    	sendingFacility;
		newHeader.receivingApplication= 	receivingApplication;
		newHeader.receivingFacility= 		receivingFacility;
		newHeader.messageType=    		messageType;
		newHeader.messageDateTime= 		messageDateTime;
		newHeader.messageControlId=		messageControlId;
	}

	if(msg['EVN'].length()>0) { 
		newHeader.eventMessageType=		eventMessageType;
		newHeader.eventDateTime=			eventDateTime;
	}

	channelMap.put('newHeader',newHeader);
	if(debug>0) { logger.error('DEBUG::'+scriptName+' newHeader: '+JSON.stringify(newHeader)); }

	// Populate tmp outbound message
	var tmp= msg;

	if(msg['MSH'].length()>0) {
		// Modify MSH segment values 
		tmp['MSH']['MSH.3']['MSH.3.1']=   sendingApplication;
		tmp['MSH']['MSH.4']['MSH.4.1']=   sendingFacility;
		tmp['MSH']['MSH.5']['MSH.5.1']=   receivingApplication;
		tmp['MSH']['MSH.6']['MSH.6.1']=   receivingFacility;
		tmp['MSH']['MSH.7']['MSH.7.1']=   messageDateTime;
		tmp['MSH']['MSH.9']=   messageType;
		tmp['MSH']['MSH.10']['MSH.10.1']= messageControlId;
	}

	if(msg['EVN'].length()>0) {
		// Modify EVN segment values 
		tmp['EVN']['EVN.1']= eventMessageType;
		tmp['EVN']['EVN.2']['EVN.2.1']= eventDateTime;	
	}

	channelMap.put('anonymizedHeader',tmp);

	return tmp;
}

/** This script handles the transformation to anonymize HL7 message patient demographics

	@param {String} msg - provide the hl7 message to have the patient demographics anonmymized
	@return {String} return description

	scriptName:	anonymize_hl7_patient_demographics.js
	desc:		This script handles the transformation to anonymize HL7 message patient demographics
	date:		2022-05-36 
	history:
	2023-01-03 JER - Modified from patient.encounter to patient.account
	2022-08-12 JER - Added the Alternate Patient Id PID-4
	2022-07-13 JER - Modified this script as a code_template function
	2022-04-15 JER - created from the convert_RDS_HL7_to_JSON script
*/
function anonymize_hl7_patient_demographics(msg) {

	var scriptName= "anonymize_hl7_patient_demographics";
	var debug= 0;
	var currentPatient= new Object();
	var newPatient= new Object();

	var patientId= msg['PID']['PID.3'][0]['PID.3.1'].toString();
	// 2022-08-12 JER - Added the Alternate Patient Id PID-4
	var altPatientId= msg['PID']['PID.4']['PID.4.1'].toString();
	var lastName= msg['PID']['PID.5']['PID.5.1'].toString();
	var firstName= msg['PID']['PID.5']['PID.5.2'].toString();
	var dateOfBirth= msg['PID']['PID.7']['PID.7.1'].toString();
	var gender= msg['PID']['PID.8']['PID.8.1'].toString();
	var account= msg['PID']['PID.18']['PID.18.1'].toString();

	if(debug>0) { logger.error('DEBUG::'+scriptName+': patientId= '+patientId+' lastName= '+lastName+' firstName= '+firstName+' dateOfBirth= '+dateOfBirth+' gender='+gender+' account= '+account); }

	// collect the currentPatient Object Properties
	currentPatient.patientId= patientId;
	// 2022-08-12 JER - Added the Alternate Patient Id PID-4
	currentPatient.altPatientId= altPatientId;
	currentPatient.lastName= lastName;
	currentPatient.firstName= firstName;
	currentPatient.dateOfBirth= dateOfBirth;
	currentPatient.gender= gender;
	currentPatient.account= account;
	channelMap.put('currentPatient',currentPatient);
	if(debug>0) { logger.error('DEBUG::'+scriptName+' currentPatient: '+JSON.stringify(currentPatient)); }

	// global channel Map configuration specific to this channel referenced across all messages
	var patientLastName= 	channelMap.get('lPatientLastName').toString();
	var patientFirstName=	channelMap.get('lPatientFirstName').toString();
	var patientDateOfBirth=	channelMap.get('lPatientDateOfBirth').toString();

	var patientId= 		channelMap.get('lPatientIdPrefix').toString()+random_generator(4);
	// 2022-08-12 JER - Added the Alternate Patient Id PID-4
	var altPatientId= 		patientId;
	var patientAccount= 	channelMap.get('lPatientIdPrefix').toString()+random_generator(8);
	var patientLastName= 	patientLastName+''+random_generator(4);
	var patientFirstName= 	patientFirstName+''+random_generator(4);

	// determine FullYear, Month and Day
	var strFullYear=		patientDateOfBirth.substring(0,4);
	var strMonth=			patientDateOfBirth.substring(4,6);
	var strDay=			patientDateOfBirth.substring(6,8);
	// randomDateAfterDate(new Date(2021, 4, 5), -365)
	var patientDateOfBirth= random_date_after_date(new Date (strFullYear, strMonth, strDay), 3650, scriptName);

	var patientGender= Math.random();
	if(patientGender<0.5) { patientGender= 'M'; } else { patientGender= 'F'; }

	// set newPatient Object
	newPatient.patientId= patientId;
	// 2022-08-12 JER - Added the Alternate Patient Id PID-4
	newPatient.altPatientId= altPatientId;
	newPatient.lastName=  patientLastName;
	newPatient.firstName= patientFirstName;
	newPatient.dateOfBirth= patientDateOfBirth;
	newPatient.gender=    patientGender;
	newPatient.account= patientAccount;
	channelMap.put('newPatient',newPatient);
	if(debug>0) { logger.error('DEBUG::'+scriptName+' newPatient: '+JSON.stringify(newPatient)); }

	var tmp= msg;

	// Modify PID segment values - Patient Id (PID.3.1)
	tmp['PID']['PID.3'][0]['PID.3.1']= patientId;
	// 2022-08-12 JER - Added the Alternate Patient Id PID-4
	tmp['PID']['PID.4']['PID.4.1']= altPatientId;
	tmp['PID']['PID.5']['PID.5.1']= patientLastName;
	tmp['PID']['PID.5']['PID.5.2']= patientFirstName;
	tmp['PID']['PID.7']['PID.7.1']= patientDateOfBirth;
	tmp['PID']['PID.8']['PID.8.1']= patientGender;
	tmp['PID']['PID.18']['PID.18.1']= patientAccount;
	tmp['PID']['PID.5']['PID.5.9']['PID.5.9.2']='ANONYMIZED';

	channelMap.put('anonymizedMessage',tmp);

	return tmp;
}

/** This script handles the transformation to anonymize HL7 message patient encounter visit information
	@param {String} msg - provide the hl7 message to have the patient encounter anonmymized
	@return {String} return description

	scriptName:	anonymize_hl7_patient_encounter.js
	desc:		This script handles the transformation to anonymize HL7 message encounter visit information
	date:		2022-05-36 
	history:
	2023-01-03 JER - Modified to include the random_generator function for the encounter number
	2022-08-12 JER - Created from the anonmymize_hl7_patient_demographics function
*/
function anonymize_hl7_patient_encounter(msg) {

	var scriptName= "anonymize_hl7_patient_encounter";
	var debug= 0;
	var currentEncounter= new Object();
	var newEncounter= new Object();

	for each(child in msg) {
		if(typeof(msg['encounter'])!== 'undefined') {
			currentEncounter= msg['encounter'];
		} // end if 
	} // end for each

	// get GlobalMap gLocationUnits (array)
	// gLocationUnits= ['ABC','BCD','CDE','DEF','EFG','FGH','GHI','HIJ','IJK','JKL'];
	var arrLocationUnits=  			$g('gLocationUnits');
	// if(debug>0) {
		logger.error('DEBUG::'+scriptName+' arrLocationUnits typeof:'+typeof(arrLocationUnits)+' length: '+arrLocationUnits.length+' valueOf: '+arrLocationUnits.toString()+' json: '+JSON.stringify(arrLocationUnits)); 
	// } // end if 
 	var encounterCurrentUnit= 		arrLocationUnits[Math.floor(Math.random()*arrLocationUnits.length)];
	newEncounter.unit= encounterCurrentUnit;

	var encounterCurrentRoom= 		encounterCurrentUnit+''+random_generator(4);
	newEncounter.room= 				encounterCurrentRoom;
	var encounterCurrentBed=   		random_generator(2);
	newEncounter.bed=  				encounterCurrentBed;
	var encounterCurrentFacility= 	$g('gSendingFacility');
	newEncounter.facility=			encounterCurrentFacility;

	if(msg['PV1']['PV1.6']) {
		if(msg['PV1']['PV1.6']['PV1.6.1']) { 
			var encounterPriorUnit= 		msg['PV1']['PV1.6']['PV1.6.1'].toString(); 
			newEncounter.prior_unit=		encounterPriorUnit;
		}
		if(msg['PV1']['PV1.6']['PV1.6.2']) { 
			var encounterPriorRoom= 		msg['PV1']['PV1.6']['PV1.6.2'].toString(); 
			newEncounter.prior_room=		encounterPriorRoom;
		}
		if(msg['PV1']['PV1.6']['PV1.6.3']) { 
			var encounterPriorBed= 			msg['PV1']['PV1.6']['PV1.6.3'].toString();
			newEncounter.prior_bed=			encounterPriorBed;
		}
		if(msg['PV1']['PV1.6']['PV1.6.4']) { 
			var encounterPriorFacility= 	msg['PV1']['PV1.6']['PV1.6.4'].toString(); 
			newEncounter.prior_facility=	encounterPriorFacility;
		}
	} // end if 

	// 2023-01-03 JER - Modified to include the random_generator function for the encounter number
	if(msg['PV1']['PV1.19']['PV1.19.1']) { 
		var encounterVisitNumber= 		msg['PV1']['PV1.19']['PV1.19.1'].toString().substring(0,5)+''+random_generator(4); 
		newEncounter.encounter_identifier=	encounterVisitNumber;
	}

	if(msg['PV1']['PV1.44']['PV1.44.1']) { 
		var encounterAdmissionDate= 	msg['PV1']['PV1.44']['PV1.44.1'].toString();
		// determine FullYear, Month and Day
		var strFullYear=				encounterAdmissionDate.substring(0,4);
		var strMonth=					encounterAdmissionDate.substring(4,6);
		var strDay=					encounterAdmissionDate.substring(6,8);
		// randomDateAfterDate(new Date(2021, 4, 5), -365)
		var encounterAdmissionDate= 		random_date_after_date(new Date (strFullYear, strMonth, strDay), 20, scriptName);
		newEncounter.admissionDate=		encounterAdmissionDate;
	} // end if

	channelMap.put('anonymizedVisit',newEncounter);

	var tmp= msg;

	// Modify PV1 segment values
	// Current Location PV1-3
	if(msg['PV1']['PV1.3']['PV1.3.1']!== tmp['PV1']['PV1.3']['PV1.3.1']) { tmp['PV1']['PV1.3']['PV1.3.1']= encounterCurrentUnit; }
	if(msg['PV1']['PV1.3']['PV1.3.2']!== tmp['PV1']['PV1.3']['PV1.3.1']) { tmp['PV1']['PV1.3']['PV1.3.2']= encounterCurrentRoom; }
	if(msg['PV1']['PV1.3']['PV1.3.3']!== tmp['PV1']['PV1.3']['PV1.3.3']) { tmp['PV1']['PV1.3']['PV1.3.3']= encounterCurrentBed;  }
	if(msg['PV1']['PV1.3']['PV1.3.4']!== tmp['PV1']['PV1.3']['PV1.3.4']) { tmp['PV1']['PV1.3']['PV1.3.4']= encounterCurrentFacility; }

	// Prior Location PV1-6
	if(msg['PV1']['PV1.6']['PV1.6.1']!== tmp['PV1']['PV1.6']['PV1.6.1']) { tmp['PV1']['PV1.6']['PV1.6.1']= encounterPriorUnit; }
	if(msg['PV1']['PV1.6']['PV1.6.2']!== tmp['PV1']['PV1.6']['PV1.6.1']) { tmp['PV1']['PV1.6']['PV1.6.2']= encounterPriorRoom; }
	if(msg['PV1']['PV1.6']['PV1.6.3']!== tmp['PV1']['PV1.6']['PV1.6.3']) { tmp['PV1']['PV1.6']['PV1.6.3']= encounterPriorBed;  }
	if(msg['PV1']['PV1.6']['PV1.6.4']!== tmp['PV1']['PV1.6']['PV1.6.4']) { tmp['PV1']['PV1.6']['PV1.6.4']= encounterPriorFacility; }

	// Encounter/Visit Number  PV1-19
	if(msg['PV1']['PV1.19']['PV1.19.1']!== tmp['PV1']['PV1.19']['PV1.19.1']) { tmp['PV1']['PV1.19']['PV1.19.1']= encounterVisitNumber; }

	// Admission Date  PV1-44
	if(msg['PV1']['PV1.44']['PV1.44.1']!== tmp['PV1']['PV1.44']['PV1.44.1']) { tmp['PV1']['PV1.44']['PV1.44.1']= encounterAdmissionDate; }

	channelMap.put('anonymizedEncounter',tmp);

	return tmp;
}

/** This script handles the transformation to anonymize HL7 message patient orders

	@param {String} msg - provide the hl7 message to have the patient RDE orders anonmymized
	@return {String} return description

	scriptName:	anonymize_hl7_patient_orders.js
	desc:		This script handles the transformation to anonymize HL7 message patient orders
	date:		2022-05-36 
	history:
	2022-07-13 JER - Modified this script as a code_template function
	2022-04-15 JER - created from the convert_RDS_HL7_to_JSON script
*/
function anonymize_hl7_patient_orders(msg) {

	var scriptName= "anonymize_hl7_patient_orders";
	var debug= 0;
	var currentOrder= new Object();
	var newOrder= new Object();

	// get newPatient Object
	var objPatient= channelMap.get('newPatient');
	var patientId= objPatient.patientId.toString();
	var patientName= objPatient.firstName.toString()+' '+objPatient.lastName.toString();

	// ORC
	if(msg['ORC']) { 
		var orderFillerNumber= random_generator(9);
		// var orderFillerNumber= msg['ORC']['ORC.3']['ORC.3.1'].toString();
		var orderTransactionDateTime= (new Date()).toISOString().replace(/[^0-9]/g,'').slice(0,-3);
		var orderEffectiveDateTime= orderTransactionDateTime;
		//var orderTransactionDateTime= msg['ORC']['ORC.9']['ORC.9.1'].toString();
		//var orderEffectiveDateTime= msg['ORC']['ORC.15']['ORC.15.1'].toString();
	}
	// RXO
	if(msg['RXO']) {
		var requestTreatmentOrderIdentifier= random_generator(11);
		// var requestTreatmentOrderIdentifier= msg['RXO']['RXO.1']['RXO.1.1'].toString();
	}
	// RXE
	if(msg['RXE']) { 
		// var encodedTreatmentOrderIdentifier= msg['RXE']['RXE.2']['RXE.2.1'].toString();
		var encodedTreatmentOrderIdentifier= requestTreatmentOrderIdentifier;
		// var encodedAlternateTreatmentOrderIdentifier= msg['RXE']['RXE.2']['RXE.2.4'].toString();
		var encodedAlternateTreatmentOrderIdentifier= requestTreatmentOrderIdentifier;
		// ((new Date())+1).toISOString().replace(/[^0-9]/g,'').slice(0,-3);
		// var doseDispensedDateTime= msg['RXE']['RXE.18']['RXE.18.1'].toString();
		var doseDispensedDateTime= ((new Date())+1).toISOString().replace(/[^0-9]/g,'').slice(0,-3);
		// var originalOrderedDateTime= msg['RXE']['RXE.32']['RXE.32.1'].toString();
		var originalOrderedDateTime= orderTransactionDateTime
	}
	// TQ1
	if(msg['TQ1'].length()>1) {
		var arrTQ1Segment= new Array();
		for each(tq1 in msg['TQ1']) {
			arrTQ1Segment.push(tq1['TQ1.8']['TQ1.8.1'].toString());
		}
		var orderTimingEndDate= arrTQ1Segment;
	} else if(msg['TQ1']) { 
		var orderTimingEndDate= msg['TQ1']['TQ1.8']['TQ1.8.1'].toString()
	}

	if(debug>0) { 
		logger.error('DEBUG::'+scriptName+': patientName: '+patientName+' patientId: '+patientId+' orderFillerNumber= '+orderFillerNumber+' orderTransactionDateTime= '+orderTransactionDateTime+' requestTreatmentOrderIdentifier= '+requestTreatmentOrderIdentifier); 
	}

	// collect the currentPatient Object Properties
	currentOrder.orderFillerNumber= orderFillerNumber;
	currentOrder.requestTreatmentOrderIdentifier= requestTreatmentOrderIdentifier;
	currentOrder.encodedTreatmentOrderIdentifier= encodedTreatmentOrderIdentifier;
	currentOrder.encodedAlternateTreatmentOrderIdentifier= encodedAlternateTreatmentOrderIdentifier;
	//
	currentOrder.orderTransactionDateTime= orderTransactionDateTime;
	currentOrder.orderEffectiveDateTime= orderEffectiveDateTime;
	currentOrder.doseDispensedDateTime= doseDispensedDateTime;
	currentOrder.originalOrderedDateTime= originalOrderedDateTime;
	currentOrder.orderTimingEndDate= orderTimingEndDate;

	channelMap.put('currentOrder',currentOrder);
	if(debug>0) { logger.error('DEBUG::'+scriptName+' currentOrder: '+JSON.stringify(currentOrder)); }

	// var newDate= new Date(new Date() - Math.random()*(1e+12));
	// determine FullYear, Month and Day
	var newDate= 			new Date();
	var strFullYear=		newDate.getFullYear();
	var strMonth=			newDate.getMonth();
	var strDay=			newDate.getDate();
	// randomDateAfterDate(new Date(2021, 4, 5), -365)
	var newRandomDate= random_date_after_date(new Date (strFullYear, strMonth, strDay), 3650, scriptName);

	var newOrderFillerNumber= 					newRandomDate.substring(0,8).toString()+random_generator(5);
	var newRequestTreatmentOrderIdentifier= 		random_generator(5)+newRandomDate.substring(0,8).toString();
	var newEncodedTreatmentOrderIdentifier= 		newRequestTreatmentOrderIdentifier;
	var newEncodedAlternateTreatmentOrderIdentifier= 	newRequestTreatmentOrderIdentifier;

	var newOrderTransactionDateTime= newRandomDate;
	var newOrderEffectiveDateTime= newRandomDate;
	var newDoseDispensedDateTime= newRandomDate;
	var newOriginalOrderedDateTime= newRandomDate;
	var newOrderTimingEndDate= newRandomDate;

	// set newPatient Object
	// newOrder.orderFillerNumber= newOrderFillerNumber;
	newOrder.orderFillerNumber= orderFillerNumber;
	//newOrder.requestTreatmentOrderIdentifier=  newRequestTreatmentOrderIdentifier;
	//newOrder.encodedTreatmentOrderIdentifier= newEncodedTreatmentOrderIdentifier;
	//newOrder.encodedAlternateTreatmentOrderIdentifier= newEncodedAlternateTreatmentOrderIdentifier;
	newOrder.requestTreatmentOrderIdentifier=  requestTreatmentOrderIdentifier;
	newOrder.encodedTreatmentOrderIdentifier= encodedTreatmentOrderIdentifier;
	newOrder.encodedAlternateTreatmentOrderIdentifier= encodedAlternateTreatmentOrderIdentifier;

	newOrder.orderTransactionDateTime= newOrderTransactionDateTime;
	newOrder.orderEffectiveDateTime= newOrderEffectiveDateTime;
	newOrder.doseDispensedDateTime= newDoseDispensedDateTime;
	newOrder.originalOrderedDateTime= newOriginalOrderedDateTime;
	newOrder.orderTimingEndDate= newOrderTimingEndDate;

	channelMap.put('newOrder',newOrder);
	if(debug>0) { logger.error('DEBUG::'+scriptName+' newOrder: '+JSON.stringify(newOrder)); }

	var tmp= msg;

	// Modify PID segment values - Patient Id (PID.3.1)
	tmp['ORC']['ORC.3']['ORC.3.1']= newOrderFillerNumber;
	tmp['ORC']['ORC.9']['ORC.9.1']= newOrderTransactionDateTime;
	tmp['ORC']['ORC.15']['ORC.15.1']= newOrderEffectiveDateTime= 
	// RXO
	tmp['RXO']['RXO.1']['RXO.1.1']= newRequestTreatmentOrderIdentifier;
	// RXE
	tmp['RXE']['RXE.2']['RXE.2.1']= newEncodedTreatmentOrderIdentifier;
	tmp['RXE']['RXE.2']['RXE.2.4']= newEncodedAlternateTreatmentOrderIdentifier; 
	tmp['RXE']['RXE.18']['RXE.18.1']= newDoseDispensedDateTime;
	tmp['RXE']['RXE.32']['RXE.32.1']= newOriginalOrderedDateTime;
	// TQ1
	// 2022-07-19 JER - handling multiple TQ1 segments
	if(typeof(orderTimingEndDate)== object) {
		if(orderTimingEndDate.length()>1) {
			for each(var i= 0;i<orderTimingEndDate.length();i++) {
				tmp['TQ1']['TQ1.8']['TQ1.8.1']= newOrderTimingEndDate;
			}
		}
	}
	tmp['TQ1']['TQ1.8']['TQ1.8.1']= newOrderTimingEndDate;
	tmp['ORC']['ORC.2']['ORC.2.1']='ANONYMIZED';

	channelMap.put('anonymizedOrder',tmp);

	return tmp;
}

/**
	This function when called with the desired datetime type of either: DATE (YYYYMMDD), TIME (HHMMSS) or 
	DATETIME (YYYYMMDDHHMMSS) from the current Date() function
	scriptName - current_date_get_hl7_formatted_datetime.js

	@param {type} string - provided the desired type 
	@return {string} - return the new Date() string per the provided argument type
*/
function current_date_get_hl7_formatted_datetime(type) {
	// TODO: Enter code here

	if(typeof(type) !== 'undefined') {
		if(type.toString().toUpperCase()== 'DATE') {
			return new Date().toISOString().split('T')[0].replace(/-/g,"");
		} else if (type.toString().toUpperCase()== 'TIME') {
			return new Date().toISOString().split('T')[1].substring(0,8).replace(/:/g,"");
		} else if (type.toString().toUpperCase()== 'DATETIME') {
			return new Date().toISOString().split('T')[0].replace(/-/g,"")+''+new Date().toISOString().split('T')[1].substring(0,8).replace(/:/g,"");;
		} else {
			return new Date().toISOString().split('T')[0].replace(/-/g,"")+''+new Date().toISOString().split('T')[1].substring(0,8).replace(/:/g,"");;
		}
	} else {
		return new Date().toISOString().split('T')[0].replace(/-/g,"")+''+new Date().toISOString().split('T')[1].substring(0,8).replace(/:/g,"");;
	}

}

/** This script handles the evaluate a variable to determine if defined or undefined for an Javascript object

	@param {String} msg - provide the hl7 message to have the patient demographics anonmymized
	@return {String} return description

	scriptName:	evaluate_defined_variable.js
	desc:		This script handles the evaluation of variables to determine the typeof and toString().length GT 0
	date:		2022-05-36 
	history:
	2022-07-13 JER - Modified this script as a code_template function
	2022-04-15 JER - created from the convert_RDS_HL7_to_JSON script
*/
function evaluate_defined_variable(element, elementName) {
	var scriptName= 'evaluate_defined_variable';
	var debug= 0;
	var definedResult= true;
	var debugStatement= 'DEBUG::'+scriptName+' evaluate of element '+elementName+' for typeof(element): '+typeof(element); 
	if(debug>0) { logger.info('DEBUG::'+scriptName+' evaluate element: '+element); }


	if(typeof(element) !== 'undefined') {
		if(debug>0) { logger.error(debugStatement); }
		if(typeof(element)== 'object') {

			if(debug>0) { logger.error(debugStatement+' A evaluate the current element value: '+element+' JSON: '+JSON.stringify(element)+' EQ null: '+element=== null); }

			if(JSON.stringify(element).toString().length<1) {
				if(debug>0) { logger.error(debugStatement+' B FAILS as JSON.stringify.length LT 1 '+JSON.stringify(element).length); }
				definedResult= false;
			} else if(element=== null) {
				if(debug>0) { logger.error(debugStatement+' B1 FAILS as JSON.stringify EQ NULL '+JSON.stringify(element).length); }
				definedResult= false;
			} else {
				if(debug>0) { logger.error(debugStatement+' C SUCCESS as JSON.stringify.length GT 0 '+JSON.stringify(element).length); }
			}
		} else if(element.toString().length<1) {
			if(debug>0) { logger.error(debugStatement+' D FAILS as toString.length LT 1 '+element.toString().length); }
			definedResult= false;
		} else if(element=== null) {
			if(debug>0) { logger.error(debugStatement+' D1 FAILS as toString EQ NULL '+element.toString()); }
			definedResult= false;
		} else {
			if(debug>0) { logger.error(debugStatement+' E SUCCESS as toString.length GT 0 '+element.toString().length); }
		}
	} else {
		if(debug>0) { logger.error(debugStatement+' F FAILS with undefined value.'); }
		definedResult= false;
	}
	if(debug>0) { logger.error('DEBUG::'+scriptName+' G definedResult for '+elementName+'= '+definedResult); }

	return definedResult;
}

/**
	This function will produce a random date based on a provided date and number of days argument.

	@param {String} start - enter the starting date ('YYYY', 'MM', 'DD') (e.g. '2021', '09', '13')
	@param {String} days -  number of days (positive or negative) (e.g. 365, 3000, -365)
	@return {String} return description
*/
function random_date_after_date(start, days, inScriptName) {

	// randomDateAfterDate(new Date(2021, 4, 5), -365) 
	var scriptName= 'random_date_after_date';
	var debug= 0;

	if(debug>0) { logger.error('DEBUG::'+scriptName+' originating from: '+inScriptName+' typeof(start)= '+typeof(start)+' start= '+start+' number of days= '+days+ ' typeof(days)= '+typeof(days)); }

	var startDate= new Date(start);
	if(debug>0) { logger.error('DEBUG::'+scriptName+' typeof(startDate)= '+typeof(startDate)+' startDate= '+startDate); }

	var strRandom= Math.random()*days*24*60*60*1000;
	var newDate= new Date(start.getTime() + strRandom);
	// Fri Jun 26 2020 08:59:35 GMT-0600 (GMT-06:00)
	var strDate= newDate.getFullYear() + "" + addLeadingZeros(newDate.getMonth() + 1) + "" + addLeadingZeros(newDate.getDate());

	if(debug>0) { logger.error('DEBUG::'+scriptName+' random date generated: '+strDate); }

	return strDate;
}

function addLeadingZeros(n) {

  if (n <= 9) {
    return "0" + n;
  }

  return n;
}

/**
	This function will produce a random number based on the number of characters provided into the numOfChars argument.

	@param {String} numOfChars - enter the number of random characters to be returned
	@return {String} return description
*/
function random_generator(numOfChars) {
	var scriptName= 'random_generator';
	var debug= 0;

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' evaluate number of characters: '+numOfChars);
	}

	var strNumOfChars= ''+numOfChars+'';
	var cntNumOfChars= strNumOfChars.padEnd(numOfChars,0);
	var strRandom= (Math.random()*cntNumOfChars).toFixed();

	if(debug>0) {
		logger.error('DEBUG::'+scriptName+' random number generated: '+strRandom);
	}

	return strRandom;
}

/**
	To verify if one string (source) contains another string (target).

	@param {String} arg1 - target string to lookup in the source string
	@return {String} return true if the source string contains the target string
*/
String.prototype.contains = function(arg1) {

	return this.indexOf(arg1) > -1;

};

/**
	To verify if the given string contains HTTP status code.

	@param - no parameter
	@return {String} return true if the string contains the HTTP status code, false otherwise
*/

function isHttpCode2xx(arg1) {
	var code2xx = ['200','201','202','203','204','205','206','207','208'];	
	var length = code2xx.length;

	while(length--) {
   		if (arg1.indexOf(code2xx[length]) > -1) {
       		return true;
   		}   		
	}
	return false;
}

function isHttpCode4xx(arg1) {
	var code4xx = ['400','401','402','403','404','405','406','407','408','409','410','411','412','413','414','415','416','417','429','431','451'];	
	var length = code4xx.length;

	while(length--) {
   		if (arg1.indexOf(code4xx[length]) > -1) {
       		return true;
   		}   		
	}
	return false;
}

function isHttpCode5xx(arg1) {
	var code5xx = ['500','501','502','503','504','505','506','510','511'];	
	var length = code5xx.length;

	while(length--) {
   		if (arg1.indexOf(code5xx[length]) > -1) {
       		return true;
   		}   		
	}
	return false;
}

