/* 
Modified: 
2023-03-06 JER - Modified to populate patient_id from lastname + first initial firstname if PID-3.1 has no value
*/
var scriptName= 'patient_id';
var debug= 0;

var patientId= '';
if(typeof(msg['PID']['PID.3'])!== 'undefined') {
	if(msg['PID']['PID.3'].length()>1) {
		// 2023-02-28 JER - handling more than one patient identifier, determine for each pid evaluate if length LT 1 or NOT use lastName+firstNamefirstInitial
		for each (pid3 in msg['PID']['PID.3']) {
			// 2023-02-28 JER - Instances no PID-3.1 use concatenated value of lastName & firstNamefirstInitial (ritzj) lowercase
			if(debug>0) logger.error('DEBUG::'+scriptName+' pid3 typeof: '+typeof(pid3)+' length: '+pid3.length+' msg[PID][PID.3]: '+pid3.toString());
			// evaluate if PID.3.1 string length LT 1 get the PID-5.1 and PID-5.2 concatenated values as the patientId
			if(pid3['PID.3.1'].toString().length<1) {
				var tmp_pid3= msg['PID']['PID.5']['PID.5.1'].toString().toLowerCase()+msg['PID']['PID.5']['PID.5.2'].toString().toLowerCase();
				if(patientId.indexOf(tmp_pid3)<0) {
					patientId= tmp_pid3;
				} // end if 
			// evaluate if PID.3.1 string length GT 0 get the PID-5.1 and PID-5.2 concatenated values as the patientId
			} else {
				if(pid3['PID.3.5'].toString()== 'MR') {
					var pid31= pid3['PID.3.1'].toString();
					if(patientId.indexOf(pid31)<0) {
						patientId= pid31;
					} // end if 
				} // end if
			} // end if 
		} // end for each
	// only one patient identifier - evaluate if length LT 1 or NOT
	} else {
		if(msg['PID']['PID.3']['PID.3.1'].toString().length<1) {
			var patientId= msg['PID']['PID.5']['PID.5.1'].toString().toLowerCase()+ msg['PID']['PID.5']['PID.5.2'].toString().toLowerCase().charAt(0);
		} else {
			var patientId= msg['PID']['PID.3']['PID.3.1'].toString();
		} // end if 
	} // end if 
} // end if 

channelMap.put('patient_id',patientId);

