	if(debug>0) logger.error('DEBUG::'+scriptName+' E OBR.4.1 str_order_type: '+str_order_type);
	arrOrderType.push(str_order_type);
	// handling of str_order_desc
	if(debug>0) logger.error('DEBUG::'+scriptName+' F OBR.4.2 str_order_desc: '+str_order_desc);
