var filterCOVIDNonDetectable= new Boolean($cfg('CAFilterCOVIDNonDetectable'));

// filterCOVIDNonCodedWithExceptions is to determine if filter COVID observation with a non-Coded with Exceptions (CWE) observation 
var filterCOVIDNonCodedWithExceptions= new Boolean($cfg('CAFilterCOVIDNonCodedWithExceptions'));

// detected is to identify if an expected observation qualifies for the result to be distributed (true: yes, false: no, default: false: no)
var detected= false;

// array of Observation (OBR) ResultSets array containing individual Observation (OBR) ResultSet array of observationCodes and ObservationValues (e.g. {{Trelloc:Non-Detected,Pressuti:Detected},{COVID:Detected}} )
