	var allowedObservationSegments= 0;
	// 2023-09-18 JER - persist the OBR segment childIndex()
	var obrChildIndex= seg.childIndex(); 

	if(debug>0) logger.debug('DEBUG::'+channelName+':'+scriptName+' segmentIndex: '+obrChildIndex+' OBR.4.1: '+seg['OBR.4']['OBR.4.1'].toString()+' obr.4: '+seg['OBR.4']+' seg: '+seg);

