					disallowedObservationsCount++;
					delete msg.children()[obx.childIndex()];

					if(debug>0) logger.debug('DEBUG::'+channelName+':'+scriptName+' COVID evaluate: filterCOVIDNonCodedWithExceptions & filterCOVIDNonDetectable - DELETE Observation Disallowed with Code: '+messageObservationCode+' Value: '+messageObservationValue+' increment counter: '+disallowedObservationsCount+' segIndex: '+seg.childIndex());		
				} else { 

					if((filterCOVIDNonDetectable== true)||(filterCOVIDNonDetectable== 'true')) {

						if(messageObservationValue== 'NOT DETECTED') {
						// NOT DETECTED
							arrNonDetectedFiltered.push(messageObservationCode+':'+messageObservationValue);
							nonDetectedFilteredCount++;
							delete msg.children()[obx.childIndex()];

							if(debug>0) logger.debug('DEBUG::'+channelName+':'+scriptName+' COVID evaluate: filterCOVIDNonCodedWithExceptions & filterCOVIDNonDetectable - DELETE Observation NOT DETECTED with Code: '+messageObservationCode+' Value: '+messageObservationValue+' increment counter: '+nonDetectedFilteredCount);

						}  else {
						// DETECTED
							detected= true;
							arrDetectedNotFiltered.push(messageObservationCode+':'+messageObservationValue);
							detectedNotFilteredCount++;
							allowedObservationSegments++;

							if(debug>0) logger.debug('DEBUG::'+channelName+':'+scriptName+' COVID evaluate: filterCOVIDNonCodedWithExceptions & filterCOVIDNonDetectable - Observation DETECTED with Code: '+messageObservationCode+' Value: '+messageObservationValue+' increment counter: '+detectedNotFilteredCount);
						} // end if 
					} else {
					// ALLOWED
						detected= true;
						arrAllowedObservations.push(messageObservationCode+':'+messageObservationValue);
						allowedObservationCount++;
						allowedObservationSegments++;

						if(debug>0) logger.debug('DEBUG::'+channelName+':'+scriptName+' COVID evaluate: filterCOVIDNonCodedWithExceptions & filterCOVIDNonDetectable - Observation NO FILTER APPLIED with Code: '+messageObservationCode+' Value: '+messageObservationValue+' increment counter: '+allowedObservationCount);					
					} // end if

				} // end if

			} else {

				if((filterCOVIDNonDetectable== true)||(filterCOVIDNonDetectable== 'true')) {

					if(messageObservationValue== 'NOT DETECTED') {
					// NOT DETECTED
						arrNonDetectedFiltered.push(messageObservationCode+':'+messageObservationValue);
						nonDetectedFilteredCount++;
						delete msg.children()[obx.childIndex()];

						if(debug>0) logger.debug('DEBUG::'+channelName+':'+scriptName+' COVID Only evaluate: filterCOVIDNonDetectable - DELETE Observation NOT DETECTED with Code: '+messageObservationCode+' Value: '+messageObservationValue+' increment counter: '+nonDetectedFilteredCount);

					}  else {
					// DETECTED
						detected= true;
						arrDetectedNotFiltered.push(messageObservationCode+':'+messageObservationValue);
						detectedNotFilteredCount++;
						allowedObservationSegments++;

						if(debug>0) logger.debug('DEBUG::'+channelName+':'+scriptName+' COVID Only evaluate: filterCOVIDNonDetectable - Observation DETECTED with Code: '+messageObservationCode+' Value: '+messageObservationValue+' increment counter: '+detectedNotFilteredCount);
					} // end if 

				} else {
				// ALLOWED
					detected= true;
					arrAllowedObservations.push(messageObservationCode+':'+messageObservationValue);
					allowedObservationCount++;
					allowedObservationSegments++;

					if(debug>0) logger.debug('DEBUG::'+channelName+':'+scriptName+' COVID Observation NO FILTER APPLIED - Code: '+messageObservationCode+' Value: '+messageObservationValue+' increment counter: '+allowedObservationCount);					
				} // end if 

			} // end if 
			j++;
		} // end for each

	} // end if  

	// 2024-04-26 JER - Modified to evaluate if any allowedObservations are found - if none then remove associated OBR and SPM segments
	if(allowedObservationSegments<1) {

		var getSegment= getSegmentsAfter(msg, msg.children()[seg.childIndex()],'SPM',true,'OBR');

		if(debug>0) logger.debug('DEBUG::'+scriptName+' childIndex: '+seg.childIndex()+' index getSegment: '+getSegmentsAfter(msg, msg.children()[seg.childIndex()],'SPM',true,'OBR'));
		if(debug>0) logger.debug('DEBUG::'+scriptName+' delete SPM getSegment typeof: '+typeof(getSegment)+' index: '+[obrChildIndex+1]+' value: '+getSegmentsAfter(msg, msg.children()[seg.childIndex()],'SPM',true,'OBR'));
		delete msg.children()[obrChildIndex+1]; 

		var getSegment= getSegmentsAfter(msg, msg.children()[(seg.childIndex()-1)],'OBR',false,'OBR');

		if(debug>0) logger.debug('DEBUG::'+scriptName+' childIndex: '+(seg.childIndex()-1)+' index getSegment: '+getSegmentsAfter(msg, msg.children()[(seg.childIndex()-1)],'OBR',true,'OBR'));
		if(debug>0) logger.debug('DEBUG::'+scriptName+' delete OBR getSegment typeof: '+typeof(getSegment)+' index: '+[seg.childIndex()-1]+' value: '+getSegmentsAfter(msg, msg.children()[(seg.childIndex()-1)],'OBR',false,'OBR'));
		delete msg.children()[(seg.childIndex())]; 

	} // end if

	arrObservationResultSets.push(arrObservationResultSet);
	i++;

} // end for each
channelMap.put('_DetectedNotFiltered','COUNT:'+detectedNotFilteredCount+' LIST:'+arrDetectedNotFiltered);
channelMap.put('_NonDetectedFiltered','COUNT:'+nonDetectedFilteredCount+' LIST:'+arrNonDetectedFiltered);
channelMap.put('_AllowedObservation','COUNT:'+allowedObservationCount+' LIST:'+arrAllowedObservations);
channelMap.put('_DisallowedObservation','COUNT:'+disallowedObservationsCount+' LIST:'+arrDisallowedObservations);

channelMap.put('arrObservationResultSets',arrObservationResultSets);
channelMap.put('detected',detected);
// show the Filters
// channelMap.put('filterNotifiablePathogensNonDetectable',filterNotifiablePathogensNonDetectable);
channelMap.put('CADetectedOnlyNotifiablePathogens',detectedOnlyNotifiablePathogens);
channelMap.put('CADetectALLNotifiablePathogens',detectALLNotifiablePathogens);
channelMap.put('CAFilterCOVIDNonDetectable',filterCOVIDNonDetectable);
channelMap.put('CAFilterCOVIDNonCodedWithExceptions',filterCOVIDNonCodedWithExceptions);

