var obr_count= 0;
for each(seg in msg..OBR) {	

	if(debug>0) {
		logger.error('DEBUG::'+channelName+':'+scriptName+' EVALUATE SPM Segment Index to MATCH the OBR Segment Index');
		logger.error('DEBUG::'+channelName+':'+scriptName+' OBR.1 toString(): '+seg['OBR.1'].toString()+' seg: '+seg);
	} // end if 

	if(seg['OBR.1']['OBR.1.1'].toString().length>0) {

		var obr1= seg['OBR.1']['OBR.1.1'].toString();
		var spm_count= 0;
		for each(spm in getSegmentsAfter(msg, msg.children()[seg.childIndex()],'SPM',false,'OBR')) {

			if(debug>0) logger.error('DEBUG::'+channelName+':'+scriptName+' SPM segment: '+spm);
			// get for each SPM
			var spm1=  spm['SPM.1']['SPM.1.1'].toString();

			if(spm1!== obr1) {
				spm['SPM.1']['SPM.1.1']= obr1;
				if(debug>0) logger.error('DEBUG::'+channelName+':'+scriptName+' MODIFY FROM SPM['+spm_count+'].1: '+spm1+' -> OBR['+obr_count+'] TO: '+spm['SPM.1']['SPM.1.1']);
			} // end if 
		} // end for each

	} // end if 

} // end for each

var scriptName= 'Renumber OBX Segment in Sequence';
var debug= 0;
var parentSegmentName= 'OBR';
var childSegmentName= 'OBX';

if(debug>0) {
	logger.error('DEBUG::'+channelName+':'+scriptName+' Renumber Segment Sequence for segment: '+childSegmentName+' for parent segmernt: '+parentSegmentName);
} // end if 

for each(seg in msg..OBR) {	

	if(debug>0) {
		logger.error('DEBUG::'+channelName+':'+scriptName+' Evaluating from the OBR Segment current seg: '+seg);
	} // end if 

	var newSegmentIndex= 1;
	for each(childSegment in getSegmentsAfter(msg, msg.children()[seg.childIndex()],childSegmentName,false,parentSegmentName)) {

		var segmentIndexParent= childSegmentName+'.1';
		var segmentIndexChild=  segmentIndexParent+'.1';
		var curSegmentIndex= childSegment[segmentIndexParent][segmentIndexChild].toString();
		if(curSegmentIndex!== newSegmentIndex) {
			childSegment[segmentIndexParent][segmentIndexChild]= newSegmentIndex;
			if(debug>0) logger.error('DEBUG::'+channelName+':'+scriptName+' ReIndex Segment Index FROM: '+curSegmentIndex+' TO: '+newSegmentIndex+' FOR: '+childSegment[segmentIndexParent][segmentIndexChild].toString());

		} else {
			if(debug>0) logger.error('DEBUG::'+channelName+':'+scriptName+' ReIndex Segment Index NOT REQUIRED FROM: '+curSegmentIndex+' EQ TO: '+newSegmentIndex);
		}
		// increment index
		newSegmentIndex++;

	} // end for each

} // end for each

