}

if(responseStatusLine== internalServerError) {
	responseStatus= ERROR;
} else if (responseStatusLine== conflictError) {
	responseStatus= ERROR;
} else if (responseStatusLine== gatewayTimeout) {
	if (responseStatus == QUEUED && connectorMessage.getSendAttempts()>= 3) {
		responseStatus = ERROR;
	} else {
		responseStatus= QUEUED;
	} // end if 
} else if (responseStatusLine== badGateway) {
	if (responseStatus == QUEUED && connectorMessage.getSendAttempts()>= 3) {
		responseStatus = ERROR;
	} else {
		responseStatus= QUEUED;
	} // end if 
} else if (responseStatus== ERROR) {
	responseStatus= ERROR;
}  // end if

